(window.webpackJsonp=window.webpackJsonp||[]).push([[1879],{2234:function(n,h,i){"use strict";i.r(h);var t=i(44),g=Object(t.a)({},(function(){var n=this,h=n.$createElement,i=n._self._c||h;return i("ContentSlotsDistributor",{attrs:{"slot-key":n.$parent.slotKey}},[i("h1",{attrs:{id:"chuong-613-luc-tieu-thu-đanh-cuoc-【-canh-3-】"}},[i("a",{staticClass:"header-anchor",attrs:{href:"#chuong-613-luc-tieu-thu-đanh-cuoc-【-canh-3-】"}},[n._v("#")]),n._v(" Chương 613: Lục tiểu thư đánh cược 【 Canh [3] 】")]),n._v(" "),i("hr"),n._v(" "),i("p",[i("br"),i("br"),n._v("Vị này Lục tiểu thư, theo cột đá bên kia vừa đi tới, cũng không biết thanh niên cùng Cam Nhất Bình trò chuyện."),i("br"),i("br"),n._v("Mới vừa nhìn một lần, phát hiện đối với mấy cái này cột đá, không có một điểm biện pháp nào."),i("br"),i("br"),n._v("Đường đường học thức uyên bác thiên chi kiêu nữ đều không có bất kỳ thủ đoạn nào, kẻ trước mắt này, thế mà khẩu xuất cuồng ngôn, nói có thể toàn bộ phá giải... Lập tức cảm thấy không vui, mở miệng phản bác."),i("br"),i("br"),n._v("“Ngươi cái nào rễ hành? Bản quản gia nói chuyện với ngươi?”"),i("br"),i("br"),n._v("Con mắt thoáng nhìn, Tôn Cường đầy người ngạo nghễ."),i("br"),i("br"),n._v("“Ngươi...”"),i("br"),i("br"),n._v("Lục tiểu thư sững sờ."),i("br"),i("br"),n._v("“Cái gì ngươi nha, ta nha!”"),i("br"),i("br"),n._v("Tôn Cường hất lên ống tay áo, đầu lâu giơ lên: “Ta đang cùng lâu chủ trao đổi, còn chưa tới phiên như ngươi loại này, liền một cái cột đá phá giải không được gia hỏa xen vào!”"),i("br"),i("br"),n._v("“Ngươi nói cái gì?”"),i("br"),i("br"),n._v("Lục tiểu thư lúc này mới kịp phản ứng, thiếu chút nữa ngất đi, ngũ tinh danh sư, Hóa Phàm lục trọng cường giả, thế mà bị một cái chỉ có Hóa Phàm nhị trọng gia hỏa sỉ nhục, tức giận đến ngực nhấp nhô, suýt nổ tung."),i("br"),i("br"),n._v("“Láo xược!” Nghe được theo đuổi nữ thần bị người quát lớn, thanh niên cũng khuôn mặt không vui."),i("br"),i("br"),n._v("“Láo xược? Ngươi còn không biết xấu hổ nói ta? Ta không nói ngươi, ngươi còn tăng thể diện? Một cái cột đá đều không có phá giải, một cái điểm tích lũy đều không có, cái rắm bản lĩnh không có, giống như này nói khoác không biết ngượng phá hoại quy củ... Cho là có hai cái tiền bẩn liền ghê gớm!”"),i("br"),i("br"),n._v("Hừ một tiếng, Tôn Cường tràn đầy khinh thường."),i("br"),i("br"),n._v("Đều là người nào nha, nhìn quần áo giả vờ giả vịt, xông bia không thành công, liền dùng tiền mua sắm, thậm chí không tiếc lấy ra thân phận uy hiếp... Thật sự là mất mặt!"),i("br"),i("br"),n._v("Ngũ tinh danh sư mà thôi, lão gia nhà chúng ta trước mặt, Hồng Viễn đế quốc Mạc Cao Viễn đường chủ, lục tinh đỉnh phong, cũng không ngoan ngoãn, không dám nói nhảm?"),i("br"),i("br"),n._v("“Ngươi...”"),i("br"),i("br"),n._v("Nhìn thấy hắn tràn ngập khinh bỉ ánh mắt, thanh niên cùng Lục tiểu thư liếc mắt nhìn nhau, đều muốn điên rồi."),i("br"),i("br"),n._v("Bọn họ đều là rồng phượng trong loài người, đi đến bất kỳ địa phương nào đều là nhân vật có tiếng tăm lừng lẫy, giờ phút này thế mà bị một cái nho nhỏ quản gia xem thường, hơn nữa triệt để như vậy, nếu không phải hàm dưỡng cao, khẳng định đã sớm nổ."),i("br"),i("br"),n._v("“Đã ngươi nói ngươi thiếu gia có thể phá giải, hắn là ai? Ở nơi nào, có thể hay không mời tới thử một chút?”"),i("br"),i("br"),n._v("Hất lên ống tay áo, thanh niên khẽ nói: “Ta ngược lại muốn xem xem, phải chăng như như lời ngươi nói, có thể hay không phá giải sạch sẽ!”"),i("br"),i("br"),n._v("Hắn một cái không có phá giải, cái tên này lại nói nhà bọn hắn thiếu gia, có thể phá giải sạch sẽ, nói đùa cái gì?"),i("br"),i("br"),n._v("“Cái này...”"),i("br"),i("br"),n._v("Lời còn chưa dứt, liền nghe đến một cái tràn đầy bất đắc dĩ thanh âm từ một bên vang lên."),i("br"),i("br"),n._v("“Trong miệng hắn nói người thiếu gia kia... Hình như là ta!”"),i("br"),i("br"),n._v("Ngay sau đó, một thanh niên mặt mũi lúng túng đi tới."),i("br"),i("br"),n._v("Nghe được lâu chủ đáp ứng muốn đem đồ vật bán ra, Trương Huyền cũng nghĩ qua đi nói một tiếng, có điều, thấy Tôn Cường đi trước, đành phải như vậy coi như không có gì."),i("br"),i("br"),n._v("Vốn nghĩ cái tên này gần nhất đều rất đáng tin cậy, đi qua nói một chút cũng không sao, không nghĩ tới, mới hai câu nói, liền đem bản thân đỡ đến trên lửa..."),i("br"),i("br"),n._v("Cái này gọi chuyện gì."),i("br"),i("br"),n._v("Nói như ngươi vậy, ta còn thế nào khiêm tốn?"),i("br"),i("br"),n._v("“Liền ngươi... Có thể đem cột đá phá giải sạch sẽ?”"),i("br"),i("br"),n._v("Lục tiểu thư hừ lạnh một tiếng: “Cuồng vọng!”"),i("br"),i("br"),n._v("Còn tưởng rằng đối phương trong miệng thiếu gia, là thần thánh phương nào, nhân vật lợi hại gì, thế mà chỉ là cái không đủ hai mươi thanh niên, nhịn không được có chút phản cảm."),i("br"),i("br"),n._v("Thân là công chúa, mỹ nữ, đi đến bất kỳ địa phương nào, đều muốn đối mặt một đám theo đuổi người, loại này tự cho mình siêu phàm gia hỏa, đã gặp nhiều, cái gì cũng đều không hiểu, còn muốn trang lợi hại, thật sự là không biết trời cao đất rộng."),i("br"),i("br"),n._v("“Cái này... Hạ nhân tùy ý miệng nói bậy, chư vị không cần thiết quả thật!”"),i("br"),i("br"),n._v("Thấy mình một câu không nói, trước bị người khác chán ghét lên, Trương Huyền có chút ngượng ngùng khoát tay áo."),i("br"),i("br"),n._v("“Cái gì nói bậy, thiếu gia, một ít cột đá mà thôi, ngươi phá giải cho bọn hắn nhìn xem, có cái gì ghê gớm. Cũng làm cho bọn họ những này chưa thấy qua việc đời người nhìn xem, cái gì gọi là thực lực!”"),i("br"),i("br"),n._v("Thấy thiếu gia khiêm tốn, Tôn Cường không làm nữa, vội vàng nói."),i("br"),i("br"),n._v("“Tốt, không nên nói bậy!”"),i("br"),i("br"),n._v("Trương Huyền sầm mặt lại, khoát tay áo: “Ngươi chẳng lẽ không nghe thấy ư? Vừa rồi lâu chủ mới nói, những này cột đá rất khó phá giải, liền xem như hắn, năm mươi năm cũng chỉ giải quyết ba cây, há có thể dễ dàng như vậy giải quyết?”"),i("br"),i("br"),n._v("“Thiếu gia...”"),i("br"),i("br"),n._v("“Đủ rồi!” Cắt ngang đối phương muốn nói tiếp mà nói, Trương Huyền nhìn về phía trước mắt ba người: “Thực sự thật không tiện, là ta quản giáo thuộc hạ vô phương, làm các ngươi cười cho rồi!”"),i("br"),i("br"),n._v("“A!”"),i("br"),i("br"),n._v("Thấy hắn thừa nhận sai lầm, thanh niên cùng Lục tiểu thư sắc mặt khá hơn một chút, đầu lâu giơ lên."),i("br"),i("br"),n._v("“Hạ nhân bội phục chủ nhân, vì chủ tranh luận, không có gì!”"),i("br"),i("br"),n._v("Cam Nhất Bình gật đầu."),i("br"),i("br"),n._v("Làm hạ nhân sùng bái chủ nhân, cho rằng chủ nhân không hướng vô cùng, cũng coi như bình thường."),i("br"),i("br"),n._v("“Đa tạ lâu chủ lý giải!”"),i("br"),i("br"),n._v("Thấy hóa giải xấu hổ, Trương Huyền lúc này mới thở phào nhẹ nhõm, nhịn không được nhìn về phía trước mắt Cam Nhất Bình: “Cái này lâu chủ... Ta muốn phía dưới Lạc Xà nội đan, Đỗ Ưu thảo, còn có Băng Vũ kiếm, ngươi nhìn cần phá giải mấy cái cột đá vừa vặn?”"),i("br"),i("br"),n._v("“...” Thanh niên, Lục tiểu thư đồng thời một cái lảo đảo."),i("br"),i("br"),n._v("Mới vừa quát lớn xong hạ nhân tùy ý miệng nói bậy, bản thân liền hỏi phá giải mấy cái cột đá vừa vặn... Nghe khẩu khí, muốn phá giải mấy cái liền phá giải mấy cái giống nhau..."),i("br"),i("br"),n._v("Muốn hay không giả bộ như vậy?"),i("br"),i("br"),n._v("Ngươi cho rằng những này cột đá, là nhà ngươi, muốn phá bao nhiêu liền phá bao nhiêu?"),i("br"),i("br"),n._v("“Phá giải mấy cái cột đá, còn... Vừa vặn?”"),i("br"),i("br"),n._v("Cam Nhất Bình cũng lông mày nhảy một cái, mặc dù không vui, vẫn là giải thích một chút: “Lạc Xà nội đan, mười điểm, Đỗ Ưu thảo bảy điểm, Băng Vũ kiếm sáu điểm, cộng lại là 23 điểm. Phá giải cột đá tiêu chuẩn là, cái thứ nhất một điểm, cái thứ hai hai điểm, cái thứ ba ba điểm... Cứ thế mà suy ra. Chỉ có phá giải bảy cái cột đá, điểm tích lũy mới tính đầy đủ!”"),i("br"),i("br"),n._v("“Bảy cái cột đá?”"),i("br"),i("br"),n._v("Nghe đối phương nói cùng Tôn Cường trước đó giới thiệu giống nhau, Trương Huyền thở phào, gãi gãi đầu: “Cái kia... Ta có thể đi thử một chút ư?”"),i("br"),i("br"),n._v("“Có thể!”"),i("br"),i("br"),n._v("Cam Nhất Bình gật đầu."),i("br"),n._v(" “Còn có một việc, không tốt lắm ý tứ...”"),i("br"),i("br"),n._v("Thấy đối phương đáp ứng sảng khoái như vậy, Trương Huyền càng thêm thật không tiện: “Ta nghe nói, chỉ cần phá giải cột đá, nộp lên mười cái linh thạch liền sẽ lui về đến, nói cách khác, có thể phá giải cột đá, cũng không cần tiền, ngươi nhìn... Ta không có tiền, có thể hay không đi trước phá giải? Cùng lắm thì, một lát nữa, nhiều phá giải mấy cái, xem như bồi thường...”"),i("br"),i("br"),n._v("“...”"),i("br"),i("br"),n._v("Cam Nhất Bình cùng Lục tiểu thư, thanh niên đám người liếc mắt nhìn nhau, kém chút không có thổ huyết."),i("br"),i("br"),n._v("Mới vừa nghe hắn quát lớn mập mạp, nói nghĩa chính ngôn từ, cảm thấy cái này tiểu tử mặc dù tuổi không lớn lắm, vẫn còn thật khiêm nhường, rất hiểu quy củ, ai ngờ thời gian nháy mắt, liền nói lời này..."),i("br"),i("br"),n._v("Phá giải mấy cái vừa vặn, cùng lắm thì nhiều phá mấy cái bồi thường..."),i("br"),i("br"),n._v("Em gái ngươi ah, ngươi cho rằng phá giải những này cột đá, là đùa giỡn? Muốn phá thì phá?"),i("br"),i("br"),n._v("Trước đó còn cảm thấy cái tên mập mạp kia liền đủ cuồng vọng, không nghĩ tới cái tên này cuồng hơn..."),i("br"),i("br"),n._v("“Nói khoác không biết ngượng!”"),i("br"),i("br"),n._v("Lục tiểu thư gương mặt xinh đẹp lộ ra tức giận: “Nghe ngươi khẩu khí, muốn phá giải mấy cái, liền phá giải mấy cái?”"),i("br"),i("br"),n._v("“Cố gắng mà thôi...” Không nghĩ tới thuận miệng nói, đối phương tức giận như vậy, Trương Huyền lắc đầu."),i("br"),i("br"),n._v("“Tốt, đã ngươi tự tin như vậy, không bằng chúng ta đánh cược!”"),i("br"),i("br"),n._v("Thấy nói như thế rõ ràng, cái tên này còn không tốt biết bao cảm giác, Lục tiểu thư cắn răng."),i("br"),i("br"),n._v("“Đánh cược?”"),i("br"),i("br"),n._v("Trương Huyền nháy con mắt."),i("br"),i("br"),n._v("Bản thân xông cột đá, mắc mớ gì đến nàng? Đánh cái gì cược?"),i("br"),i("br"),n._v("“Không sai, ngươi mười cái linh thạch, ta giúp ngươi ra, ngươi chỉ cần có thể phá giải một cái cột đá, ta cho ngươi một trăm viên linh thạch trung phẩm... Nếu như phá giải không được, ta cũng không cần ngươi hướng ta bồi thường, chỉ cần hướng ta quỳ xuống nhận sai là được! Dám vẫn là không dám?”"),i("br"),i("br"),n._v("Lục tiểu thư khẽ nói."),i("br"),i("br"),n._v("Cái gì cũng đều không hiểu, liền tin miệng thư hoàng, quả thực thật ngông cuồng!"),i("br"),i("br"),n._v("Không hảo hảo giết giết ngươi uy phong, chỉ sợ ngay cả mình là ai, cũng không biết!"),i("br"),i("br"),n._v("“Phá giải một cái cột đá, cho ta một trăm viên linh thạch trung phẩm?”"),i("br"),i("br"),n._v("Quay đầu nhìn về phía trước mắt hai hàng xấp xỉ hơn 100 cây cột đá, Trương Huyền hô hấp dồn dập: “Ngươi xác định?”"),i("br"),i("br"),n._v("Một cái một trăm viên, hơn 100 cây nếu là đều có thể phá giải, chẳng phải hơn vạn viên linh thạch trung phẩm?"),i("br"),i("br"),n._v("Thật muốn có thể được đến nhiều như vậy, phía sau tu luyện, liền rốt cuộc không cần buồn..."),i("br"),i("br"),n._v("“Đương nhiên! Làm sao, không dám? Không dám cũng đừng nói nhảm!”"),i("br"),i("br"),n._v("Lục tiểu thư bàn tay như ngọc trắng vẫy một cái."),i("br"),i("br"),n._v("“Ta không phải không dám, là sợ ngươi một lát nữa quỵt nợ...” Do dự một chút, Trương Huyền nói."),i("br"),i("br"),n._v("Hơn một vạn viên linh thạch trung phẩm, hắn không phải tin tưởng đối phương có thể lấy ra, đầu tiên nói trước lại nói."),i("br"),i("br"),n._v("“Ngươi...”"),i("br"),i("br"),n._v("Lục tiểu thư khí con mắt thấu đỏ."),i("br"),i("br"),n._v("Nàng đường đường ngũ tinh danh sư, lại thêm vô cùng tôn quý thân phận, làm sao có thể quỵt nợ?"),i("br"),i("br"),n._v("Răng cắn chặt: “Ngươi yên tâm, ta Ngọc Phi Nhi, nói lời giữ lời, đã dám cá với ngươi, đương nhiên sẽ không quỵt nợ, ngược lại là ngươi, chờ một hồi, quỳ xuống thời điểm, hi vọng cũng không cần do dự!”"),i("br"),i("br"),n._v("“Sẽ không quỵt nợ là tốt rồi...”"),i("br"),i("br"),n._v("Nhẹ nhàng thở ra, Trương Huyền hai mắt sáng lên nhìn về phía cách đó không xa cột đá, đối Cam Nhất Bình liền ôm quyền: “Chỉ cần nhận ra trong trụ đá bảo vật, thứ này liền sẽ nổ tung đúng không?”"),i("br"),i("br"),n._v("“Vâng!”"),i("br"),i("br"),n._v("Cam Nhất Bình gật gật đầu."),i("br"),i("br"),n._v("“Vậy là tốt rồi!”"),i("br"),i("br"),n._v("Lên tiếng, Trương Huyền nhấc chân đi tới trước mặt."),i("br"),i("br"),n._v("Tới gần cột đá, hắn mới biết được vì cái gì thanh niên cùng vị này Lục tiểu thư sắc mặt vì cái gì đều trở nên khó coi."),i("br"),i("br"),n._v("Phía trên văn tự, căn bản cũng không phải là gợi ý, mà là một loại không biết tên ngôn ngữ, như là đặc thù hoa văn, để cho người ta xem không hiểu."),i("br"),i("br"),n._v("Một loại xem không hiểu ngôn ngữ, tự nhiên cũng là đoán không ra ý nghĩa cùng nội dung... Loại tình huống này, có thể đoán được trong trụ đá đồ vật là cái gì mới là lạ."),i("br"),i("br"),n._v("“Thật là một cái cuồng vọng vô tri gia hỏa!”"),i("br"),i("br"),n._v("Thấy cái tên này lại dám cùng Lục tiểu thư đánh cược, thanh niên cũng cười lạnh."),i("br"),i("br"),n._v("Hắn tự mình thể hội cột đá độ khó, biết đáng sợ, liền ngũ tinh, lục tinh giám bảo sư đều không giải quyết được, nhận không ra, một cái hai mươi tuổi tiểu gia hỏa, làm sao có thể phá giải?"),i("br"),i("br"),n._v("Cho Lục tiểu thư quỳ xuống, không cần nghĩ, đã thành kết cục đã định!"),i("br"),i("br"),n._v("“Cố làm ra vẻ, tuổi còn trẻ có một chút bản lĩnh, liền cuồng ngạo không dứt, nhưng lại không biết, trời cao bao nhiêu, đất dày bao nhiêu!”"),i("br"),i("br"),n._v("Lục tiểu thư cũng hừ một tiếng, một đôi đôi mắt thanh tú, nhìn chằm chằm phía trước thanh niên, đang muốn nói tiếp, chỉ thấy trước mặt gia hỏa, mặt mỉm cười nhìn lại."),i("br"),i("br"),n._v("“Có thể bắt đầu chưa?”"),i("br"),i("br"),n._v("“Có thể!”"),i("br"),i("br"),n._v("Nhen lửa một cái nhang lớn, Cam Nhất Bình gật gật đầu."),i("br"),i("br"),n._v("“Cái kia tốt...”"),i("br"),i("br"),n._v("Trương Huyền hít sâu một hơi, ngón tay đối cái thứ nhất cột đá nhấn tới: “Đây là Thất Xảo Kim Tâm Toa!”"),i("br"),i("br"),n._v("Răng rắc!"),i("br"),i("br"),n._v("Tiếng nói kết thúc, trước mắt cột đá xuất hiện vết rách, trong nháy mắt biến thành bột phấn, ngay sau đó một cái kim sắc toa hình dáng vũ khí xuất hiện ở trước mắt mọi người."),i("br"),i("br"),n._v("“Đây là Tử Hà lưu tinh chùy...”"),i("br"),i("br"),n._v("“Đây là Thiết Cốt quyền ấn...”"),i("br"),i("br"),n._v("“Đây là Đồng Tâm bát...”"),i("br"),i("br"),n._v("Một bên tiến lên, một bên hướng cột đá điểm tới, Trương Huyền mỗi điểm một cái, cột đá liền rầm rầm nổ tung, chỗ đến, đá vụn tung bay."),i("br"),i("br"),n._v("Convert by: ThấtDạ"),i("br"),i("br"),i("br"),i("br")]),n._v(" "),i("hr"),n._v(" "),i("p",[i("a",{attrs:{href:"./chap-621"}},[n._v("Chương trước")]),n._v("  |  "),i("a",{attrs:{href:"./chap-623"}},[n._v("Chương sau")])])])}),[],!1,null,null,null);h.default=g.exports}}]);