(window.webpackJsonp=window.webpackJsonp||[]).push([[1883],{2239:function(n,h,t){"use strict";t.r(h);var c=t(44),i=Object(c.a)({},(function(){var n=this,h=n.$createElement,t=n._self._c||h;return t("ContentSlotsDistributor",{attrs:{"slot-key":n.$parent.slotKey}},[t("h1",{attrs:{id:"chuong-617-cai-thu-nhat-thieu-hut"}},[t("a",{staticClass:"header-anchor",attrs:{href:"#chuong-617-cai-thu-nhat-thieu-hut"}},[n._v("#")]),n._v(" Chương 617: Cái thứ nhất thiếu hụt")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("br"),t("br"),n._v("“Cái gì?”"),t("br"),t("br"),n._v("“Nói Lạc đan sư không phải luyện đan?”"),t("br"),t("br"),n._v("“Quá mức đi? Lạc đan sư thế nhưng là tổng bộ tới ngũ tinh luyện đan sư, chúng ta cũng nhìn, thủ pháp luyện đan, động tác, hoàn mỹ không một tì vết, không có kẽ hở, nói nàng không gọi luyện đan? Là ai vô lễ như thế?”"),t("br"),t("br"),n._v("“Loè thiên hạ!”"),t("br"),t("br"),n._v("Đại sảnh xôn xao, như là thiêu đốt, từng cái nổi giận đùng đùng hướng thanh âm vang lên địa phương nhìn lại."),t("br"),t("br"),n._v("Lập tức nhìn thấy một người mặc thường phục thanh niên, hai mươi tuổi bộ dáng, trước ngực luyện đan sư huy chương bên trên, ba sao lấp lánh."),t("br"),t("br"),n._v("“Một cái nho nhỏ tam tinh luyện đan sư, liền dám ăn nói bừa bãi?”"),t("br"),t("br"),n._v("“Vô tri!”"),t("br"),t("br"),n._v("Thấy rõ ràng bộ dáng của hắn, không ít người cười lạnh, nhịn không được hét lớn."),t("br"),t("br"),n._v("Lạc đan sư mặc dù thoạt nhìn không lớn, có chút tuổi trẻ, nhưng đối đan dược lý giải cùng luyện đan năng lực, là ở đây hầu như chín mươi chín phần trăm trở lên người, đều không thể so sánh."),t("br"),t("br"),n._v("Quá trình luyện đan, xuân phong hóa vũ, không có một chút dư thừa trình tự, không có một giây khác biệt, xem ở nhãn lực quả thực chính là xinh đẹp hưởng thụ, như thế năng lực luyện đan, nếu như đều không gọi luyện đan nói, cái gì gọi là?"),t("br"),t("br"),n._v("Lại nói, cho dù có người chỉ trích, cũng là ngũ tinh danh sư mới có thể làm, ngươi một cái nho nhỏ tam tinh, cái gì đều xem không hiểu, liền nói khoác không biết ngượng, không phải loè thiên hạ là làm gì?"),t("br"),t("br"),n._v("Lạc đan sư cũng nhướng mày."),t("br"),t("br"),n._v("Nàng muốn nghe chính là ý kiến, mà không phải nghi ngờ."),t("br"),t("br"),n._v("“Vị này luyện đan sư, không biết ta vừa rồi luyện đan có gì chỗ không ổn, để cho ngươi miệng ra lời ấy?”"),t("br"),t("br"),n._v("“Muốn biết?” Nói chuyện thanh niên, cũng không đứng dậy, mí mắt vừa nhấc."),t("br"),t("br"),n._v("“Tự nhiên!” Lạc đan sư lông mày giương lên."),t("br"),t("br"),n._v("“Ngươi vừa rồi luyện đan, tổng cộng một trăm hai mươi bảy chỗ sai lầm, ta sợ nói rồi... Ngươi sẽ lòng tin sụp đổ, lại không cách nào luyện đan!” Thanh niên thản nhiên nói."),t("br"),t("br"),n._v("“Một trăm hai mươi bảy chỗ?”"),t("br"),t("br"),n._v("Hừ lạnh một tiếng, Lạc đan sư hất lên ống tay áo: “Nếu như ngươi thật có thể nói ra nhiều như vậy, ta không chịu nổi, lòng tin sụp đổ, cũng là tự rước lấy nhục!”"),t("br"),t("br"),n._v("Muốn nói mới vừa rồi còn nghi hoặc, đối phương có phải là thật hay không nhìn ra cái gì, nghe được câu này, lập tức phủ định."),t("br"),t("br"),n._v("Thuật luyện đan của nàng, là Hồng Viễn danh sư học viện, rất nhiều tiền bối tôi luyện mà ra kinh nghiệm tổng kết, hoàn chỉnh học xuống, cơ bản không sai, muốn nói nhìn ra một hai nơi chỗ không đúng, miễn cưỡng có thể tiếp nhận, có hơn một trăm chỗ... Nói đùa cái gì?"),t("br"),t("br"),n._v("Nhiều như vậy sai lầm, đừng nói đan dược không luyện được, chỉ sợ lửa đều không có đốt lên, liền nổ tung đi!"),t("br"),t("br"),n._v("Loè thiên hạ, cũng phải có cái hạn độ, như vậy không điểm mấu chốt, quả thực vô sỉ cực điểm!"),t("br"),t("br"),n._v("“Ngươi rõ ràng chính xác nhất định phải nghe?”"),t("br"),t("br"),n._v("Thanh niên nhẹ nhàng cười một tiếng."),t("br"),t("br"),n._v("“Không sai!” Lạc đan sư nói."),t("br"),t("br"),n._v("“Ôi! Đã ngươi muốn biết, ta không nói, ngược lại sẽ bị cho rằng cố ý tìm ngươi phiền phức!”"),t("br"),t("br"),n._v("Lắc đầu đứng lên, thanh niên mấy bước đi tới phía trước trên sân khấu, tại đứng sừng sững nữ hài bên người lò đan bên trên tìm ra một chút, dạo qua một vòng, thần sắc lạnh nhạt nhìn qua: “Đang nói trước đó, ta muốn hỏi một chút, ngươi cũng đã biết cái này miệng lô đỉnh lai lịch?”"),t("br"),t("br"),n._v("“Quen thuộc lò đan, biết lai lịch, là luyện đan sư cơ sở nhất sự tình, ta tự nhiên biết rõ!”"),t("br"),t("br"),n._v("Lạc đan sư đôi mi thanh tú nhăn lại, nói tiếp: “Đây là Ngũ phẩm luyện khí đại sư Ngô Phong tác phẩm, mười hai năm trước rèn đúc thành công, là đáy biển hàn thiết cùng thanh ngân đồng dung hợp luyện chế, chịu nổi nhiệt độ cao, chịu băng hàn, là Linh cấp trung phẩm đỉnh phong cấp bậc pháp bảo...”"),t("br"),t("br"),n._v("“Giải thích không sai! Nhưng ngươi cũng đã biết, cái này lô đỉnh, từng nổ qua một lần lô?”"),t("br"),t("br"),n._v("Nghe nàng nói xong, giải thích không có sai lầm chút nào, thanh niên tiếp tục nói."),t("br"),t("br"),n._v("Người thanh niên này dĩ nhiên chính là Trương Huyền."),t("br"),t("br"),n._v("Muốn làm cho đối phương cam tâm tình nguyện nghe lời hỗ trợ luyện chế dược dịch, tự nhiên muốn trước hết để cho hắn vui lòng phục tùng."),t("br"),t("br"),n._v("Đang rầu làm sao làm được, liền nghe đến nàng muốn nghe ý kiến mà nói, đã như vậy, cũng là không có gì khách khí."),t("br"),t("br"),n._v("Mặc dù hắn hiện tại đối luyện đan nắm giữ, còn không đạt tới ngũ tinh, nhưng có Thiên Đạo thư viện, đối phương luyện đan bên trong thiếu hụt còn có thể một cái xem thấu."),t("br"),t("br"),n._v("“Biết, là nổ qua một lần, nhưng là, lần kia chỉ là dược vật thiêu đốt, lô đỉnh bản thân, không có bất cứ vấn đề gì, cũng không có vết rách, không ảnh hưởng sau này luyện đan!”"),t("br"),t("br"),n._v("Lạc đan sư sững sờ, nói."),t("br"),t("br"),n._v("Cái này lô đỉnh, là gia tộc trưởng bối ban cho, dùng đến hiện tại đã ròng rã ba năm, quả thực nổ qua một lần lô, chỉ là... Nàng biết ngược lại cũng thôi, đối phương làm sao biết, hơn nữa một ngụm nói ra?"),t("br"),t("br"),n._v("“Lô đỉnh quả thực không có tổn hại!” Trương Huyền ngón tay xoa tại lô đỉnh mặt ngoài: “Cũng không có vết rách, nhưng là, miệng đỉnh nhan sắc hiện tro, mang theo hun khói nhan sắc, cẩn thận ngửi lên, còn có rỉ đồng xanh hương vị, nếu như không nhìn lầm, lần trước nổ lô, hẳn là tại luyện chế ngũ tinh  đi!”"),t("br"),t("br"),n._v("“Đúng!” Lạc đan sư cau mày, gật gật đầu."),t("br"),t("br"),n._v("Lần trước nổ lô, quả thực là tại luyện chế Liệt Hỏa đan, mặc dù không phải nàng tự mình luyện chế, nhưng cũng tham dự trong đó, biết tình huống lúc đó."),t("br"),t("br"),n._v("Nhìn một chút lô đỉnh miệng đỉnh nhan sắc, ngửi một chút, liền biết nổ lô, hơn nữa còn biết bởi vì luyện chế cái gì nổ tung, phần này nhãn lực quá lợi hại đi!"),t("br"),t("br"),n._v("Nếu như nói trước đó cảm thấy cái tên này là ăn nói bừa bãi, cố ý hấp dẫn người khác chú ý, mà bây giờ đã cảm thấy hắn chỉ sợ có chút bản lĩnh."),t("br"),t("br"),n._v("“Thật nổ qua?”"),t("br"),t("br"),n._v("“Nhìn thoáng qua, liền nhìn ra những này, coi như ngũ tinh luyện đan sư cũng làm không được đi!”"),t("br"),t("br"),n._v("“Đúng vậy a, cái tên này, khả năng thật đúng là có thể nói ra thứ gì...”"),t("br"),t("br"),n._v("Dưới đài mọi người, nghe Lạc đan sư thừa nhận, cũng từng cái lộ ra kinh ngạc."),t("br"),t("br"),n._v("Kiểm tra lô đỉnh, biết lai lịch cùng đỉnh bản thân tình huống, là mỗi cái luyện đan sư phải làm, nhưng là... Không cần giới thiệu, chỉ nhìn một cái, liền rõ ràng những này, nhãn lực không khỏi quá mức mạnh mẽ!"),t("br"),n._v(" Chỉ sợ đồng dạng ngũ tinh luyện đan sư, đều chưa hẳn có thể làm được."),t("br"),t("br"),n._v("Trước đó, mọi người và Lạc đan sư tìm cách giống nhau, cũng cảm thấy cái tên này cố ý gây chuyện, nghe được một cái lò đan phân tích, liền biết không đơn giản."),t("br"),t("br"),n._v("“Coi như nổ Liệt Hỏa đan lại như thế nào? Cùng nổ những đan dược khác khác nhau ở chỗ nào?”"),t("br"),t("br"),n._v("Lạc đan sư nói."),t("br"),t("br"),n._v("Liệt Hỏa đan là dương thuộc tính đan dược, cuồng bạo cực nóng, nổ lô bình thường, hẳn là không vấn đề gì đi."),t("br"),t("br"),n._v("“Đương nhiên là có khác nhau!”"),t("br"),t("br"),n._v("Trương Huyền lắc đầu: “Nếu như ngươi tiếp tục luyện chế Liệt Hỏa đan, quả thực không ảnh hưởng, nhưng ngươi luyện chế là Thanh Thần đan! Loại đan dược này, có thể ngưng thần tĩnh tức, sau khi phục dụng, có thể để người ta hồn lực, lấy được ích lợi, là cực kỳ trân quý dược vật. Thuốc này, kiêng kỵ nhất táo bạo, ngươi nhưng dùng nổ tung qua Liệt Hỏa đan lô đỉnh luyện chế, ngươi nói có thể hay không chịu ảnh hưởng?”"),t("br"),t("br"),n._v("“Cái này...” Lạc đan sư chần chờ."),t("br"),t("br"),n._v("Có chút đối linh hồn có tác dụng đan dược, quả thực yêu cầu cực cao, chỉ là... Cho dù có ảnh hưởng, cũng cực kỳ bé nhỏ, không tính là gì đi."),t("br"),t("br"),n._v("“Là không phải cảm thấy, cho dù có ảnh hưởng, cũng cực kỳ bé nhỏ, ảnh hưởng không lớn?”"),t("br"),t("br"),n._v("Dường như nhìn ra ý nghĩ của nàng, Trương Huyền khóe miệng giơ lên: “Bình thường luyện chế, quả thực ảnh hưởng không lớn, nhưng ngươi luyện dược trình tự không đúng, ảnh hưởng liền rất rõ ràng. Vừa rồi ta nghe ngươi nói, Thanh Linh thảo trực tiếp bỏ vào lò đan sẽ cùng Bách Mộc hoa xung đột, cần tại Cam Ngư trấp dịch bên trong ngâm, nhưng có việc này?”"),t("br"),t("br"),n._v("“Đúng!” Lạc đan sư gật đầu."),t("br"),t("br"),n._v("Vừa rồi nàng đích xác nói qua lời này, cũng là làm như vậy."),t("br"),t("br"),n._v("“Thanh Linh thảo tính liệt, Bách Mộc hoa tính ấm, trực tiếp bỏ vào, quả thực sẽ xung đột, dùng Cam Ngư trấp dịch ngâm, là cách làm chính xác, nhưng... Ngươi có bao giờ nghĩ tới, Cam Ngư trấp dịch cùng Liệt Hỏa đan chủ dược vật liệu Thanh Căn mộc, sẽ sinh ra kịch liệt phản ứng?”"),t("br"),t("br"),n._v("Trương Huyền nói."),t("br"),t("br"),n._v("“Là sẽ sinh ra phản ứng, nhưng Liệt Hỏa đan đã luyện chế nhiều năm, trong lúc đó cũng luyện chế qua những dược vật khác, cho dù có sót lại, cũng không có gì chứ?” Lạc đan sư nói."),t("br"),t("br"),n._v("Thanh Căn mộc, cùng Cam Ngư trấp dịch quả thực sẽ phản ứng, thậm chí cùng hỏa diễm cùng nước giống nhau rất khó cùng tồn tại, chỉ là... Cái này đều tốt mấy năm trước chuyện, có thể có cái gì?"),t("br"),t("br"),n._v("“Không có gì?” Trương Huyền lắc đầu, ngay sau đó nhìn về phía trên đài cách đó không xa vị kia nghi tự hội trưởng lão giả: “Vị này luyện đan sư, có thể hay không làm phiền ngươi một việc!”"),t("br"),t("br"),n._v("“Mời nói!” Lão giả đi tới trước mặt."),t("br"),t("br"),n._v("“Làm phiền ngươi đem cái này lô đỉnh trên miệng màu xám đen đồ vật cạo xuống một chút đến!”"),t("br"),t("br"),n._v("Trương Huyền nói."),t("br"),t("br"),n._v("“Tốt!” Mặc dù không rõ đối phương ý tứ, lão giả vẫn là lấy ra vũ khí, đem màu xám đen dấu vết, cạo tại một cái bát ngọc bên trong."),t("br"),t("br"),n._v("“Còn làm phiền ngươi lấy chút Cam Ngư trấp dịch, bỏ vào trong đó!”"),t("br"),t("br"),n._v("Trương Huyền cũng không tiếp xúc bát ngọc, nói tiếp."),t("br"),t("br"),n._v("“Ừm!”"),t("br"),t("br"),n._v("Lão giả gật đầu phân phó một tiếng, một cái luyện đan sư vội vàng chạy ra ngoài, thời gian không dài, lấy ra một cái hồ lô, đem bên trong màu lam nhạt dược dịch đổ vào trong đó."),t("br"),t("br"),n._v("Xì xì xì xì...!"),t("br"),t("br"),n._v("Dược dịch cùng bát ngọc bên trong màu xám phấn cặn bã tiếp xúc, lập tức toát ra rất nhiều bọt khí, như là suýt sôi trào đồng dạng."),t("br"),t("br"),n._v("“Cái này...”"),t("br"),t("br"),n._v("Dưới đài tất cả mọi người đồng thời xôn xao."),t("br"),t("br"),n._v("Người ngu đi nữa, cũng có thể nhìn ra, đây là xuất hiện kịch liệt phản ứng."),t("br"),t("br"),n._v("Nếu như loại phản ứng này tại trong lò luyện đan tiến hành, rất dễ dàng xuất hiện nổ tung, để sở hữu dược vật đều hủy hoại chỉ trong chốc lát."),t("br"),t("br"),n._v("“Tại sao có thể như vậy?”"),t("br"),t("br"),n._v("Lạc đan sư cũng giật nảy mình, gương mặt xinh đẹp tái đi."),t("br"),t("br"),n._v("Hiển nhiên, loại tình huống này, nàng cũng không dám tin tưởng."),t("br"),t("br"),n._v("“Liệt Hỏa đan nổ lô, quả thực không tính là gì, nhưng ngươi quên nó chủ dược vật liệu Thanh Căn mộc, có cực mạnh tính ăn mòn, một khi sau khi nổ tung, có thể bám vào trên vách đỉnh, khó mà loại trừ, luyện chế những đan dược khác, không có tác dụng quá lớn, nhưng gặp được Cam Ngư trấp dịch liền không đồng dạng.”"),t("br"),t("br"),n._v("Trương Huyền nói."),t("br"),t("br"),n._v("“Nhưng có phương pháp giải quyết...”"),t("br"),t("br"),n._v("Lạc đan sư nhoáng một cái."),t("br"),t("br"),n._v("“Tự nhiên có! Thanh Căn mộc, cùng Trọc Liên hoa, thuộc tính tương khắc, tại luyện chế Thanh Thần đan trước đó, nếu như ngươi có thể cầm hai lạng Trọc Liên hoa bỏ vào trong đó, liền có thể nhẹ nhõm khu trừ, còn sót lại Thanh Căn mộc dược tính, để lô đỉnh nhiệt độ cũng có chút hạ thấp...”"),t("br"),t("br"),n._v("“Làm như vậy, đằng sau thêm Mộc Đàn hoa thơm cũng không cần phối hợp Tử Anh thủy, tại Liệt Dương xuống chiếu rọi năm ngày nhiều, tiết kiệm một cái phiền toái nhất trình tự.”"),t("br"),t("br"),n._v("Trương Huyền nói."),t("br"),t("br"),n._v("“Cái này...”"),t("br"),t("br"),n._v("Lạc đan sư nói không ra lời."),t("br"),t("br"),n._v("Làm ngũ tinh luyện đan sư, quen thuộc dược tính, tự nhiên biết đối phương nói sự tình là thật là giả, cẩn thận phân tích một lần, thật làm như vậy, không có chút nào sơ hở không nói, còn có thể nhượng bộ đột nhiên tiết kiệm một mảng lớn, không đến mức phiền phức như vậy."),t("br"),t("br"),n._v("“Luyện đan, mặc dù có trình tự, có phương pháp, nhưng cũng không phải đã hình thành thì không thay đổi, thật giống như lái xe kỹ xảo tương đồng, đường xá không giống, chẳng lẽ còn dùng tương đồng thủ đoạn?”"),t("br"),t("br"),n._v("“Bởi vậy, không chỉ muốn có trình tự, còn cần mượn nhờ lô đỉnh bản thân vấn đề, tiến hành thích hợp sửa chữa. Nếu không, dần dần từng bước, sẽ chỉ làm dược vật càng luyện chế càng phiền phức!”"),t("br"),t("br"),n._v("Hai tay chắp sau lưng, Trương Huyền thần sắc lạnh nhạt nhìn qua, như là một cái để cho người ta kính ngưỡng cao cấp luyện đan sư."),t("br"),t("br"),n._v("“Là cái này... Ta muốn cùng ngươi nói cái thứ nhất thiếu hụt!”"),t("br"),t("br"),n._v("Convert by: ThấtDạ"),t("br"),t("br"),t("br"),t("br")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("a",{attrs:{href:"./chap-625"}},[n._v("Chương trước")]),n._v("  |  "),t("a",{attrs:{href:"./chap-627"}},[n._v("Chương sau")])])])}),[],!1,null,null,null);h.default=i.exports}}]);