(window.webpackJsonp=window.webpackJsonp||[]).push([[1884],{2238:function(n,h,t){"use strict";t.r(h);var c=t(44),i=Object(c.a)({},(function(){var n=this,h=n.$createElement,t=n._self._c||h;return t("ContentSlotsDistributor",{attrs:{"slot-key":n.$parent.slotKey}},[t("h1",{attrs:{id:"chuong-618-ta-muon-bai-nguoi-lam-thay"}},[t("a",{staticClass:"header-anchor",attrs:{href:"#chuong-618-ta-muon-bai-nguoi-lam-thay"}},[n._v("#")]),n._v(" Chương 618: Ta muốn bái ngươi làm thầy!")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("br"),t("br"),n._v("Chương 618: Ta muốn bái ngươi làm thầy!"),t("br"),t("br"),n._v("“Cái này...”"),t("br"),t("br"),n._v("Lạc đan sư nói không ra lời, cũng không biết làm sao đi tiếp."),t("br"),t("br"),n._v("Đối phương nói có lý có căn cứ, để nàng căn bản là không có cách phản bác."),t("br"),t("br"),n._v("Không chỉ nàng bộ biểu tình này, gian phòng bên trong cũng là lặng ngắt như tờ."),t("br"),t("br"),n._v("Cái này... Dạng cũng được?"),t("br"),t("br"),n._v("Cái này cần đối dược vật, đối đan phương, lò đan, sâu bao nhiêu hiểu rõ, mới có thể làm đến?"),t("br"),t("br"),n._v("Tất cả mọi người lần nữa nhìn về phía trước thanh niên, giống như là nhìn thấy một đầu quái vật."),t("br"),t("br"),n._v("“Vừa rồi ngươi cùng những người khác vấn đáp, ta nghe, hồn lực đi vào lò đan quả thực sẽ bị đốt bị thương, bỏ vào có linh tính Cam Tâm thảo, có thể trung hoà, nhưng... Ngươi có nghĩ tới không, Cam Tâm thảo linh tính, rất dễ dàng cùng lúc trước bỏ vào Chi Khải mộc dung hợp, từ đó hạ thấp hiệu quả?”"),t("br"),t("br"),n._v("“Cái này...”"),t("br"),t("br"),n._v("Lạc đan sư lần nữa sửng sốt."),t("br"),t("br"),n._v("Chi Khải mộc đối linh tính có hấp thụ tác dụng, Cam Tâm thảo linh tính bỏ vào, rất dễ dàng bị thôn phệ, từ đó hiệu quả yếu bớt."),t("br"),t("br"),n._v("“Đây là cái thứ hai thiếu hụt.”"),t("br"),t("br"),n._v("Trương Huyền nói tiếp: “Ngươi luyện đan thời điểm, không nhìn lầm, có lẽ dùng  đi! Cái này thủ pháp có thể bảo hộ thuốc cùng linh tính, nhưng là... Nhưng tăng lên luyện chế Ngưng Thần Đan trình tự, trong đó bảy cây dược liệu có lẽ một mạch mà thành, trực tiếp ném vào, dùng loại thủ pháp này, nhưng kéo dài ba mươi hai cái hô hấp, cuối cùng dẫn đến, dược vật dược hiệu xói mòn... Thủ pháp cùng dược vật không xứng, cái thứ ba thiếu hụt!”"),t("br"),t("br"),n._v("“Luyện chế đan dược thời điểm, ngươi dùng Thanh Hàn công, chống cự hỏa diễm cực nóng, mặc dù bảo vệ mình, lại làm cho lò lửa nhiệt độ đánh mất một bộ phận, không cách nào tốt hơn dung hợp giọt kia linh thú tinh huyết... Cái thứ tư thiếu hụt!”"),t("br"),t("br"),n._v("“... Cái thứ năm thiếu hụt!”"),t("br"),t("br"),n._v("“... Cái thứ sáu thiếu hụt!”"),t("br"),t("br"),n._v("..."),t("br"),t("br"),n._v("“... Đây là thứ hai mươi bảy cái thiếu hụt!”"),t("br"),t("br"),n._v("“Luyện chế một cái nho nhỏ Ngũ phẩm đan dược, đối lô không rõ, đối thuốc không rõ, đối pháp không quen, đối công không khống, truyền thụ không tường tận, phân tích không thấu, Thần không một lòng, lực không tùy tâm...”"),t("br"),t("br"),n._v("Trương Huyền ánh mắt như điện: “Nhiều vấn đề như vậy, cũng xứng gọi là luyện đan?”"),t("br"),t("br"),n._v("“Ta...”"),t("br"),t("br"),n._v("Lùi về phía sau mấy bước, Lạc đan sư vẻ mặt trắng bệch, thân thể mềm mại không ngừng run rẩy."),t("br"),t("br"),n._v("Thanh âm của đối phương như là cơ quan pháo giống nhau, cấp tốc truyền đến, lại thêm chính xác không sai, ép nàng thở không nổi."),t("br"),t("br"),n._v("Vốn cho rằng ngũ tinh luyện đan sư bên trong, đã trở thành người nổi bật, không ai bằng, nằm mơ đều không có nghĩ đến, tại chính thức cường giả trước mặt, như là ngớ ngẩn đồng dạng buồn cười!"),t("br"),t("br"),n._v("“Còn muốn hay không ta đem còn lại một trăm chỗ thiếu hụt, cũng nói đi ra?” Trương Huyền tiếp tục nói."),t("br"),t("br"),n._v("Cổ co rụt lại, Lạc đan sư cơ thể khẽ run."),t("br"),t("br"),n._v("Đối phương liên tục nói rồi hai mươi bảy chỗ thiếu sót, đều là nàng vừa rồi luyện đan bên trong xuất hiện, có lý có chứng cứ, để cho người ta không thể nào phản bác."),t("br"),t("br"),n._v("Chỉ nghe nhiều như vậy, đã cảm thấy lại luyện đan có chút bó tay bó chân, không biết như thế nào cho phải, chỉ sợ nói tiếp, thật sẽ tin tâm sụp đổ, lại khó chữa trị!"),t("br"),t("br"),n._v("“Không, không cần...”"),t("br"),t("br"),n._v("Liền ôm quyền, khom người, lần nữa nhìn về phía thanh niên trước mắt, không phải khinh thị, mà là kính trọng cùng bội phục."),t("br"),t("br"),n._v("Một cái nhìn ra luyện đan bên trong nhiều như vậy thiếu hụt, có lý có chứng cứ, trước mắt vị này, đối đan dược hiểu rõ, rốt cuộc đạt đến cái gì trình độ?"),t("br"),t("br"),n._v("E là cho dù nàng luyện đan lão sư, cũng rất khó so sánh với đi."),t("br"),t("br"),n._v("“Tốt, cũng không cần có cái gì gánh nặng trong lòng, luyện đan không phải vẽ tranh, nhất bút nhất hoạ, nghiêm ngặt dựa theo quy định đến, liền có thể làm lục thực cảnh tinh mỹ tác phẩm. Cái này cần không biết bao lâu ma luyện, cùng đối dược vật thuộc tính lĩnh ngộ, mới có thể thành công.”"),t("br"),t("br"),n._v("Thấy nữ tử trước mắt vẻ mặt trắng bệch, biết bị đả kích hung ác, Trương Huyền an ủi: “Ngươi có thể tại bằng chừng ấy tuổi, liền luyện chế ra loại cấp bậc này đan dược, đã coi như là rất tốt, đừng có gánh nặng trong lòng... Như vậy đi, ngươi dựa theo ta mới vừa nói, sửa lại một chút, lại luyện chế một lần Thanh Thần đan thử một chút, cam đoan có không đồng dạng thu hoạch!”"),t("br"),t("br"),n._v("Thanh âm bên trong, xen lẫn sư ngôn thiên bẩm, để nàng tự nhiên tin phục."),t("br"),t("br"),n._v("Trước mắt vị này, mặc dù là ngũ tinh danh sư, cũng chỉ là sơ kỳ, tâm cảnh khắc độ tại 15. 0 trái phải, mà hắn hiện tại tâm cảnh, đã đạt tới 16. 0, sư ngôn thiên bẩm để hắn tin tưởng, dễ như trở bàn tay."),t("br"),t("br"),n._v("Quả nhiên, nghe nói như thế, Lạc đan sư nguyên bản dao động tự tin, một lần nữa ngưng tụ, nhắm mắt lại, nhớ lại một chút, trước mắt vị thanh niên này nói tới mấu chốt, một cắn răng: “Tốt!”"),t("br"),t("br"),n._v("Mấy bước đi tới lò đan trước mặt."),t("br"),t("br"),n._v("Bình thường luyện đan, đều chuẩn bị ba phần dược liệu, nàng trước đó chỉ dùng một phần, bởi vậy, trước mặt trên mặt bàn còn thừa lại hai phần, cũng không cần vì dược vật phát sầu."),t("br"),t("br"),n._v("Chân khí trong cơ thể phun trào, đốt lửa, nóng đỉnh, chắt lọc dược dịch, một mạch mà thành, các loại dược liệu như là giọt mưa giống nhau tính vào đỉnh lô, linh khí bắn ra bốn phía, mùi thuốc xao động."),t("br"),t("br"),n._v("Sau hai mươi phút, một ngụm trọc khí phun ra, Lạc đan sư ngừng lại."),t("br"),t("br"),n._v("Vốn có dùng nửa canh giờ mới luyện chế thành công Thanh Thần đan, thế mà trước thời hạn còn nhiều gấp đôi!"),t("br"),t("br"),n._v("Bàn tay hướng về phía trước đẩy một cái."),t("br"),t("br"),n._v("Kẹt kẹt!"),t("br"),t("br"),n._v("Lô đỉnh mở ra, một luồng mùi thơm nồng nặc bay thẳng mà ra, tựa như một đoàn trắng noãn đám mây."),t("br"),t("br"),n._v("Đám mây phía trên, tròn vo viên thuốc, lơ lửng trong đó, một đạo kim sắc đan văn, khảm nạm ở phía trên, lấp lánh chói mắt."),t("br"),t("br"),n._v("“Đây là... Đan văn cảnh đan dược?”"),t("br"),t("br"),n._v("“Làm sao có thể?”"),t("br"),n._v("“Đan văn cảnh cùng hoàn mỹ cảnh, mặc dù chỉ kém một cái cấp bậc, nhưng lại có không thể nhảy vọt khoảng cách, coi như lục tinh luyện đan sư tới, đều là không có khả năng hoàn thành!”"),t("br"),t("br"),n._v("“Đúng vậy a, đan văn cảnh đan dược, đã có linh tính, không riêng gì đan dược, còn có thể nói đã giao phó sinh mệnh!”"),t("br"),t("br"),n._v("“Vừa mới là hoàn mỹ cảnh, vị này luyện đan sư, chỉ nói giải một hồi, liền để Lạc đan sư, luyện chế ra đan văn cảnh... Đây cũng quá lợi hại chứ?”"),t("br"),t("br"),n._v("..."),t("br"),t("br"),n._v("Thấy rõ ràng lô đỉnh bên trong bay ra đan dược, tất cả mọi người đều dọa đến vẻ mặt trắng bệch."),t("br"),t("br"),n._v("Thành đan, sung mãn, hoàn mỹ, đan văn, đan dược bốn cái cấp bậc, đạt tới hoàn mỹ, cơ bản liền xem như cực hạn, chỉ có đối luyện đan chân chính khống chế đến tuyệt đỉnh nhân vật thiên tài, mới có thể luyện chế ra đan văn cảnh giới dược vật."),t("br"),t("br"),n._v("Không nói cái khác, Huyễn Vũ đế quốc luyện đan sư công hội, thành lập không biết bao nhiêu năm, cũng xuất hiện qua rất nhiều kinh tài tuyệt diễm luyện đan sư, nhưng... Muốn nói đan văn đan dược, không có xuất hiện qua một cái!"),t("br"),t("br"),n._v("Nghe nói, chỉ có Hồng Viễn danh sư học viện đan viện xuất hiện qua mấy viên, mỗi một cái đều oanh động khắp nơi, để rất nhiều luyện đan sư triều bái, khiếp sợ không thôi."),t("br"),t("br"),n._v("Vốn cho rằng, loại cấp bậc này đan dược, chỉ ở trong truyền thuyết, ai cũng không cách nào tận mắt nhìn thấy, nằm mơ đều không có nghĩ đến... Vị này Lạc đan sư vậy mà tự tay luyện chế ra đến rồi!"),t("br"),t("br"),n._v("Đáng sợ!"),t("br"),t("br"),n._v("Lạc đan sư lợi hại, nhưng... Chỉ điểm nàng thành công người thanh niên này, chẳng phải càng thêm sâu không lường được?"),t("br"),t("br"),n._v("Trong nháy mắt, mọi người lần nữa nhìn về phía trên đài thanh niên, ánh mắt bên trong tất cả đều lộ ra nồng đậm kính sợ."),t("br"),t("br"),n._v("Đây là đối cường giả bội phục."),t("br"),t("br"),n._v("Coi như thực lực của hắn, khả năng không bằng một số người, nhưng ở luyện đan bên trên đã hiểu, chỉ sợ hội trưởng cũng không sánh nổi!"),t("br"),t("br"),n._v("Mọi người khiếp sợ, Lạc đan sư cũng theo đặc thù trong cảm giác tỉnh táo lại, nhìn trước mắt mang theo kim sắc đan văn dược vật, kích động thân thể mềm mại run rẩy, tiến về phía trước một bước, quỳ mọp xuống đất: “Ta muốn lạy đại nhân vi sư, kính xin thu ta vì học sinh, chỉ điểm ta luyện đan!”"),t("br"),t("br"),n._v("Nàng có thể rõ ràng cảm nhận được, trước đó giam cầm nàng luyện đan ràng buộc, đã đánh vỡ, nói cách khác, đối với luyện đan, có lĩnh ngộ mới, chỉ cần tu vi đạt tới, xung kích lục tinh luyện đan sư, cũng không thành vấn đề."),t("br"),t("br"),n._v("Mà hết thảy này... Đều là trước mắt vị này thoạt nhìn so với nàng tuổi tác còn nhỏ thanh niên ban cho."),t("br"),t("br"),n._v("Tại đối phương chỉ điểm, đánh vỡ ràng buộc, đã có nửa sư tình nghĩa, nếu như có thể lạy làm sư, thường xuyên lắng nghe, tại luyện đan thuật đạt thành tựu cao, khẳng định sẽ càng ngày càng cao!"),t("br"),t("br"),n._v("“Bái sư?”"),t("br"),t("br"),n._v("Trương Huyền sững sờ."),t("br"),t("br"),n._v("Mục đích của hắn chỉ là muốn làm cho đối phương tin phục, đằng sau càng làm tốt hơn bản thân luyện chế dược dịch, không nghĩ tới nữ tử này, trực tiếp mở miệng bái sư, để hắn ngược lại có chút ngượng ngùng."),t("br"),t("br"),n._v("“Mong rằng lão sư đáp ứng!”"),t("br"),t("br"),n._v("Lạc đan sư vội nói."),t("br"),t("br"),n._v("“Được rồi, ta có thể làm ngươi luyện đan lão sư, có điều, ta không có quá nhiều thời gian chỉ điểm...”"),t("br"),t("br"),n._v("Chần chờ một chút, Trương Huyền nói: “Như vậy đi, ta cái này có cái đặc thù dược dịch phương pháp luyện chế, có thể đo lường ngươi kiến thức cơ bản cùng tâm tính, càng có thể đo lường ngươi muốn bái sư thành ý. Sớm nói cho ngươi, cái này dược dịch, chỉ cần nghiêm ngặt dựa theo lời của ta đi làm, liền có thể luyện chế thành công... Thành công, ta thu ngươi làm giảng bài học sinh, không được, nói rõ giữa chúng ta không có duyên phận!”"),t("br"),t("br"),n._v("“Nhất định sẽ thành công!”"),t("br"),t("br"),n._v("Lạc đan sư liền vội vàng gật đầu, kích động vẻ mặt đỏ lên."),t("br"),t("br"),n._v("“Ừm!” Thấy đối phương thành công bị dao động, Trương Huyền nhẹ nhàng thở ra, cổ tay khẽ đảo, Lạc Xà nội đan cùng Đỗ Ưu thảo xuất hiện ở trước mặt mọi người."),t("br"),t("br"),n._v("“Hai thứ này là chủ dược vật liệu, còn cần Long Tu hoa hai lạng, nhất tinh trần một lạng, Mộc Nghiệp bì ba tiền...”"),t("br"),t("br"),n._v("Trương Huyền thuận miệng nói ra mười mấy dạng dược liệu, đều là phối trí dược dịch cần dùng đến."),t("br"),t("br"),n._v("“Hội trưởng, làm phiền ngươi một chút, có thể hay không đem những dược liệu này đều lấy một phần tới?”"),t("br"),t("br"),n._v("Ghi nhớ những dược vật này cùng trọng lượng, Lạc đan sư nhìn về phía cách đó không xa lão giả."),t("br"),t("br"),n._v("“Cái này đơn giản, ta lập tức để cho người ta chuẩn bị!” Lão giả cười cười."),t("br"),t("br"),n._v("Những dược liệu này, không tính là đặc biệt trân quý, công hội toàn bộ có thể tìm tới, không tính là gì đại sự, dặn dò một tiếng, chỉ chốc lát, một vị luyện đan sư liền đem những vật này toàn bộ cầm tới."),t("br"),t("br"),n._v("Nhìn dược liệu đầy đủ, hơn nữa đều là niên đại cao nhất, Trương Huyền hài lòng gật gật đầu."),t("br"),t("br"),n._v("Chạy đến nơi đây dao động, thật đúng là làm đúng, nếu không, nhiều như vậy cao năm dược vật, hầu như đều bị trân tàng lên, tốn nhiều tiền không nói, đều chưa hẳn có thể mua được."),t("br"),t("br"),n._v("“Lão sư, làm sao luyện chế?”"),t("br"),t("br"),n._v("Kiểm tra một lần dược vật, Lạc đan sư cũng hết sức hài lòng, lần nữa nhìn về phía cách đó không xa thanh niên."),t("br"),t("br"),n._v("“Nghe ta phân phó luyện chế, không có chút nào có thể sai!”"),t("br"),t("br"),n._v("Trương Huyền gật gật đầu, nhướng mày, bắt đầu phân phó: “Nóng đỉnh bốn mươi lăm cái hô hấp phía sau, bỏ vào nhất tinh trần, bảy cái hô hấp phía sau, bỏ vào Long húc hoa, sau đó chân khí thôi động hỏa diễm ba cái hô hấp...”"),t("br"),t("br"),n._v("Giúp Viên Đào mở ra thể chất dược dịch, là hắn cân nhắc không biết bao lâu mới thiết kế tốt, đồng thời để Thiên Đạo thư viện kiểm nghiệm qua, biết cụ thể luyện chế trình tự cùng phương pháp."),t("br"),t("br"),n._v("Hắn nói mười điểm kỹ càng, Lạc đan sư luyện chế cũng là đơn giản không ít."),t("br"),t("br"),n._v("Hỏa diễm hừng hực, nhiệt khí ngập trời."),t("br"),t("br"),n._v("Nương theo ném như lò đan dược vật càng ngày càng nhiều, linh khí chung quanh cũng càng ngày càng dày đặc, lò lửa bên trong lực lượng cũng càng ngày càng cuồng bạo, tựa như lúc nào cũng sẽ không kiên trì nổi, tại chỗ nổ tung."),t("br"),t("br"),n._v("“Là Lạc Xà nội đan...”"),t("br"),t("br"),n._v("“Thứ này là Hóa Phàm thất trọng linh thú toàn thân tinh hoa, lực lượng cuồng bạo, lại thêm lò đan lên men, coi như Hóa Phàm bát trọng cường giả, đều rất khó khống chế, để Lạc đan sư luyện chế... Không khỏi quá cường nhân chỗ khó khăn chứ?”"),t("br"),t("br"),n._v("Một bên hội trưởng cùng rất nhiều ngũ tinh danh sư, thấy cảnh này, tất cả đều nhướng mày."),t("br"),t("br"),n._v("Convert by: ThấtDạ"),t("br"),t("br"),t("br"),t("br")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("a",{attrs:{href:"./chap-626"}},[n._v("Chương trước")]),n._v("  |  "),t("a",{attrs:{href:"./chap-628"}},[n._v("Chương sau")])])])}),[],!1,null,null,null);h.default=i.exports}}]);