(window.webpackJsonp=window.webpackJsonp||[]).push([[1886],{2241:function(n,h,c){"use strict";c.r(h);var t=c(44),i=Object(t.a)({},(function(){var n=this,h=n.$createElement,c=n._self._c||h;return c("ContentSlotsDistributor",{attrs:{"slot-key":n.$parent.slotKey}},[c("h1",{attrs:{id:"chuong-620-vi-su-truong-huyen"}},[c("a",{staticClass:"header-anchor",attrs:{href:"#chuong-620-vi-su-truong-huyen"}},[n._v("#")]),n._v(" Chương 620: Vi sư, Trương Huyền!")]),n._v(" "),c("hr"),n._v(" "),c("p",[c("br"),c("br"),n._v("Truyền thuyết, lục phẩm đan dược, toả ra hương khí, cùng danh sư sư ngôn thiên bẩm giống nhau, có thể hội tụ linh khí, để phẩm chất tiến thêm một bước, có thể so với linh thạch."),c("br"),c("br"),n._v("Hội trưởng sớm đã đạt tới luyện đan sư ngũ tinh đỉnh phong, cho tới nay, đều nếm thử luyện chế cấp bậc cao hơn dược vật, đáng tiếc, bản thân tu vi không đủ, lại thêm không có lợi hại lão sư chỉ điểm, chưa hề thành công qua."),c("br"),c("br"),n._v("Vốn cho rằng, chỉ cần tu vi không đủ, liền không khả năng luyện chế ra loại cấp bậc này dược vật, nhìn thấy một màn này, giờ mới hiểu được, căn bản không phải chuyện như vậy."),c("br"),c("br"),n._v("Lạc đan sư thực lực so với hắn thấp hai cái đại cấp bậc, coi như theo Hồng Viễn đế quốc mà đến, kiến thức bất phàm, dù sao còn quá trẻ."),c("br"),c("br"),n._v("Muốn nói ai có thể trước hết nhất luyện chế ra lục phẩm đan dược, cũng cảm thấy bản thân càng hơn một bậc..."),c("br"),c("br"),n._v("Có thể hiện thực cứ như vậy phá vỡ!"),c("br"),c("br"),n._v("Đối phương luyện chế ra cái này linh dịch, mùi thơm nồng nặc hội tụ linh khí, để phẩm chất trong nháy mắt tăng lên, đã đạt đến lục phẩm cấp bậc."),c("br"),c("br"),n._v("“Là hắn...”"),c("br"),c("br"),n._v("Con ngươi co lại, hội trưởng cơ thể kích động run rẩy."),c("br"),c("br"),n._v("Vừa rồi luyện đan, hắn nhìn, Lạc đan sư nghiêm ngặt dựa theo đối phương phân phó tới làm, sở dĩ có thể luyện chế ra lục phẩm đan dược thành công, rất rõ ràng là người thanh niên này công lao."),c("br"),c("br"),n._v("Chỉ bằng vào chỉ điểm, để một cái chỉ có Hóa Phàm lục trọng ngũ tinh luyện đan sư luyện chế ra lục phẩm đan dược... Đây cũng quá lợi hại chứ?"),c("br"),c("br"),n._v("Rốt cuộc làm sao làm được?"),c("br"),c("br"),n._v("“Lục phẩm?”"),c("br"),c("br"),n._v("Những người khác nghe được la lên, cũng tất cả đều bờ môi run rẩy."),c("br"),c("br"),n._v("Đan dược cấp bậc càng cao, càng khó luyện chế, nghe qua nhất tinh luyện đan sư, thiên phú tuyệt hảo, luyện chế ra nhị phẩm đan dược, có thể... Ngũ tinh luyện đan sư luyện chế ra lục phẩm đan dược... Nằm mơ đều không có nghĩ tới."),c("br"),c("br"),n._v("“Luyện chế một lần, bất kể lòng tin vẫn là đối dược vật khống chế, đều sẽ tiến thêm một bước, vị này Lạc đan sư... Tiền đồ vô lượng!”"),c("br"),c("br"),n._v("“Đúng vậy a, ta làm sao ngốc như vậy, vừa rồi muốn vọt thẳng qua bái sư, cơ hội khẳng định là của ta!”"),c("br"),c("br"),n._v("“Hối hận ah, các ngươi nói, hiện tại lại bái sư còn kịp ư?”"),c("br"),c("br"),n._v("“Chỉ sợ đã chậm...”"),c("br"),c("br"),n._v("..."),c("br"),c("br"),n._v("Không ít người lộ ra hối hận vẻ."),c("br"),c("br"),n._v("Nhờ vào ngũ tinh thân phận, luyện chế ra lục phẩm đan dược, chỉ lần này trải qua, liền có thể để ánh mắt, kiến thức đều có cực lớn bay vọt, về sau đột phá lục tinh, cũng là đơn giản."),c("br"),c("br"),n._v("Vừa rồi nếu là không đợi Lạc đan sư mở miệng, bọn họ liền giành trước bái sư, cơ hội chỉ sợ sẽ là chính mình... Mà không phải giờ phút này, lực bất tòng tâm, tăng thêm làm sao."),c("br"),c("br"),n._v("Hô!"),c("br"),c("br"),n._v("Bên này cảm khái cảm khái, khiếp sợ khiếp sợ, xem như người trong cuộc Lạc đan sư nhẹ nhàng thở ra, nhìn trước mắt yên tĩnh lơ lửng dược dịch, kích động vẻ mặt đỏ lên, thân thể mềm mại lay động không dứt."),c("br"),c("br"),n._v("Cùng người khác nói giống nhau, đi qua lần này, nàng đối luyện đan nắm giữ lần nữa tăng lên một cái trình độ, có thể đoán được, luyện đan sẽ trở thành nàng phụ trợ chức nghiệp bên trong, huy hoàng nhất một cái."),c("br"),c("br"),n._v("Trở lại Hồng Viễn đế quốc, cũng sẽ để rất nhiều người vì đó khâm phục."),c("br"),c("br"),n._v("“Lợi hại!”"),c("br"),c("br"),n._v("Lần nữa nhìn về phía cách đó không xa thanh niên, càng thêm bội phục."),c("br"),c("br"),n._v("Chỉ bằng vào chỉ điểm trình tự, liền để bản thân luyện chế ra cao cấp như vậy những dược vật khác, phần này nhãn lực cùng kiến thức, chỉ sợ mệnh danh Hồng Viễn đệ nhất luyện đan sư đan viện viện trưởng, đều khó mà bằng được."),c("br"),c("br"),n._v("“Lão sư!”"),c("br"),c("br"),n._v("Đem dùng bình ngọc sắp xếp gọn, cung kính đi tới thanh niên trước mặt, đưa tới."),c("br"),c("br"),n._v("“Ừm!” Tiếp nhận bình ngọc, Trương Huyền trong mắt cũng lộ ra vẻ kích động."),c("br"),c("br"),n._v("Vốn cho rằng có thể luyện chế ra Ngũ phẩm cấp bậc dược dịch là tốt lắm rồi, không nghĩ tới trung hoà dược liệu niên đại đầy đủ, lại thêm không có chút nào sai lầm, thế mà một lần đạt tới lục phẩm!"),c("br"),c("br"),n._v("Loại cấp bậc này đồ vật, cho Viên Đào sử dụng, Long tê huyết mạch nhất định có thể mở ra rất lớn một đoạn, thực lực sẽ có chất tăng lên cùng bay vọt."),c("br"),c("br"),n._v("“Có thể luyện chế thành công, nói rõ ngươi rất có thiên phú, bái sư chi tâm, cũng mười phần kiên định, tốt, từ hôm nay trở đi, ngươi chính là của ta đan dược giảng bài học sinh!”"),c("br"),c("br"),n._v("Nhịn xuống hưng phấn, Trương Huyền trên mặt lần nữa bình thản như nước, dường như luyện chế ra lục phẩm dược vật, cùng ăn cơm uống nước giống nhau đơn giản."),c("br"),c("br"),n._v("Lò đan bất cứ lúc nào cũng sẽ nổ tung, đối phương còn có thể tin tưởng mình, dần dần từng bước bỏ vào dược liệu, không có chút nào bối rối, chỉ phần này tín nhiệm, liền biết, là thật tâm muốn bái sư mà không phải qua loa cho xong."),c("br"),c("br"),n._v("“Đa tạ lão sư thành toàn!”"),c("br"),c("br"),n._v("Nghe được trước mắt “Cao nhân” đáp ứng, Lạc đan sư hưng phấn mà nắm đấm xiết chặt, vội vàng quỳ gối."),c("br"),c("br"),n._v("“Ừm, ngươi tên là gì?”"),c("br"),c("br"),n._v("Biết đối phương thật tâm thật ý bái sư, Trương Huyền cũng mãn ý nhìn lại."),c("br"),c("br"),n._v("“Học sinh Lạc Thất Thất, lão sư cũng có thể gọi ta Thất Thất!” Lạc đan sư vội nói."),c("br"),c("br"),n._v("“Thất Thất? Ừm, ta về sau gọi ngươi tiểu Thất...” Trương Huyền gật đầu."),c("br"),c("br"),n._v("“Rõ!”"),c("br"),c("br"),n._v("Lạc Thất Thất mặt mũi mừng rỡ."),c("br"),c("br"),n._v("“Tốt, vi sư còn có sự tình khác đi làm, liền đi trước!”"),c("br"),c("br"),n._v("Kỳ Trân lâu xông bia đá, luyện đan công hội luyện chế dược dịch, chơi đùa đi, bốn, năm canh giờ đi qua, cái gọi là thái tử tiệc tối cũng hẳn là lập tức bắt đầu, liên quan Hóa Thanh trì danh ngạch, chậm trễ nữa không được."),c("br"),c("br"),n._v("“Vâng...”"),c("br"),c("br"),n._v("Thấy hắn muốn đi, Lạc Thất Thất đầy vẻ không muốn."),c("br"),c("br"),n._v("“Luyện đan, không phải một sớm một chiều học, hôm nay dạy ngươi, chậm rãi cảm ngộ, liền có thể có rất lớn tiến bộ!” Nhìn ra nàng ý tứ, Trương Huyền nói."),c("br"),c("br"),n._v("Lạc Thất Thất lên tiếng, Trương Huyền không nói thêm lời, thuận tay lấy ra mấy chục viên linh thạch trung phẩm, đưa cho cách đó không xa hội trưởng: “Đây là dược liệu tiền!”"),c("br"),c("br"),n._v("Nếu như không phải vị hội trưởng này lấy ra những cái kia dược liệu quý giá, cũng không có khả năng để linh dịch một lần đạt tới lục phẩm cấp bậc, hắn cũng không chiếm người khác tiện nghi, dựa theo bình thường giá cả thanh toán mà thôi."),c("br"),c("br"),n._v("Đến luyện đan sư công hội trên đường, Tôn Cường đã đem vị kia Lục tiểu thư thanh toán 700 viên linh thạch trung phẩm cho hắn."),c("br"),c("br"),n._v("“Cái này... Có thể tại sinh thời nhìn thấy như thế cấp bậc dược vật bị ở trước mặt luyện chế ra đến, là vinh hạnh của ta, sao có thể lấy tiền...” Hội trưởng liên tục vẫy tay."),c("br"),c("br"),n._v("“Nhận lấy!” Trương Huyền thuận tay đem linh thạch quăng ra."),c("br"),n._v("Đối phương không thu, chính là nợ người ân tình, hắn không muốn nợ tình, càng không muốn đằng sau phiền phức, còn không bằng đem nợ thanh toán, tiết kiệm về sau kẹp quấn không rõ."),c("br"),c("br"),n._v("“Được rồi!”"),c("br"),c("br"),n._v("Thấy đối phương khăng khăng muốn cho, hội trưởng biết đã mất đi một lần kết giao cơ hội, nhịn không được lắc đầu."),c("br"),c("br"),n._v("“Cáo từ!”"),c("br"),c("br"),n._v("Đem linh thạch cho ra, Trương Huyền nhấc chân đi ra ngoài."),c("br"),c("br"),n._v("“Lão sư... Ta đi nơi nào tìm ngươi?”"),c("br"),c("br"),n._v("Thấy hắn đi xa, Lạc Thất Thất vội vàng hô."),c("br"),c("br"),n._v("Hai tay chắp sau lưng, Trương Huyền dừng bước, thanh âm nhàn nhạt vang lên: “Vi sư, Trương Huyền!”"),c("br"),c("br"),n._v("..."),c("br"),c("br"),n._v("“Thiếu gia làm sao đi tới lâu như vậy, còn chưa có đi ra? Thái tử tiệc tối đều sắp bắt đầu đi!”"),c("br"),c("br"),n._v("Đại sảnh bên ngoài, Tôn Cường tràn đầy lo lắng."),c("br"),c("br"),n._v("“Đúng vậy a!” Viên Đào cũng không nhịn được duỗi cổ nhìn vào bên trong, chẳng qua đại sảnh đại môn đóng chặt, cái gì cũng nhìn không được."),c("br"),c("br"),n._v("Trương Huyền đi vào, liền chờ ở chỗ này, hiện tại cũng qua hơn hai canh giờ, một điểm động tĩnh đều không có, khó tránh khỏi gấp gáp."),c("br"),c("br"),n._v("Lại nói thái tử tiệc tối chẳng mấy chốc sẽ bắt đầu, mục đích đúng là vì tranh đoạt danh ngạch, hắn vị này Hồng Phong đế quốc các nước chư hầu quán quân không có kịp thời xuất hiện, Hồng sư còn không sống công việc khóc chết."),c("br"),c("br"),n._v("Lúc trước danh sư thi đấu, liền để vị này ngũ tinh danh sư khóc đủ thảm rồi, lại bởi vì cái này, để hắn nổi điên, thật không biết vị lão giả này, có thể hay không chịu được."),c("br"),c("br"),n._v("“Tiểu cô nương, có thể hay không làm phiền ngươi đi vào một chuyến, đem chúng ta nhà thiếu gia kêu đi ra!”"),c("br"),c("br"),n._v("Lại đợi một hồi, vẫn là không có động tĩnh, Tôn Cường nhịn không được nhìn về phía cách đó không xa nữ tử."),c("br"),c("br"),n._v("Chính là tiếp đãi bọn hắn trước đài phục vụ viên."),c("br"),c("br"),n._v("“Cái này... Không được, bên trong đều là luyện đan sư, hơn nữa cao đạt đến ngũ tinh cấp bậc, ta không gặp được cho phép tùy tiện đi vào, khẳng định sẽ bị chỉ trích, giáo huấn, làm không cẩn thận đều sẽ bị khai trừ...”"),c("br"),c("br"),n._v("Nữ hài vội vàng lắc đầu."),c("br"),c("br"),n._v("Chính thức luyện đan sư mới có thể tiến nhập đại hội, nàng một cái nhân viên phục vụ, nào có tư cách!"),c("br"),c("br"),n._v("“Cái này... Đều là luyện đan sư công hội người, ngươi lại chỉ là tìm người, hẳn là sẽ không chứ?”"),c("br"),c("br"),n._v("Tôn Cường cau mày."),c("br"),c("br"),n._v("“Biết!”"),c("br"),c("br"),n._v("Nữ hài ánh mắt lộ ra vẻ kính sợ: “Luyện đan sư là thượng cửu lưu chức nghiệp, địa vị tôn sùng, đối quy củ nhìn mười phần nghiêm trọng, xưa nay không làm vi phạm quy tắc cùng có sai lầm lễ nghi sự tình, ngươi đúng đúng chưa thấy qua, bọn họ mỗi cái, đều người mặc chuyên môn trang phục, phong thái phi phàm...”"),c("br"),c("br"),n._v("Ngay tại giải thích, muốn nói vài câu luyện đan sư nhiều quy củ, liền nghe đến trước mắt đại môn “Kẹt kẹt!” Một tiếng mở ra."),c("br"),c("br"),n._v("Lập tức liền thấy một đám tam tinh, tứ tinh luyện đan sư, chó nhà có tang giống nhau chạy ra, có mũ chạy mất, có quần áo kéo rách, có quá mức gấp gáp đụng đầu vào cách đó không xa trên cây cột, nằm trên mặt đất như là lợn chết..."),c("br"),c("br"),n._v("Cùng một đám nạn dân có chút giống như, đâu còn có nửa điểm luyện đan sư uy nghiêm."),c("br"),c("br"),n._v("“Cái này... Đây chính là ngươi nói phong thái phi phàm?”"),c("br"),c("br"),n._v("Viên Đào, Tôn Cường con mắt trợn tròn."),c("br"),c("br"),n._v("“...” Nữ hài mắt tối sầm lại."),c("br"),c("br"),n._v("Làm trước đài, nàng thường xuyên cùng những này luyện đan sư liên hệ ah, mỗi một cái đều rất có khí chất, mang theo người lạ chớ quấy rầy hương vị, làm sao... Hiện tại cũng cùng chiến bại quân ô hợp giống nhau, quăng mũ cởi giáp, không nói ra được bối rối?"),c("br"),c("br"),n._v("Không phải ở bên trong nghe giảng bài ư?"),c("br"),c("br"),n._v("Làm sao nghe được nghe sợ đến như vậy?"),c("br"),c("br"),n._v("Rốt cuộc nói cái gì?"),c("br"),c("br"),n._v("“Không phải là lão sư... Lại tại bên trong kiếm chuyện chứ?”"),c("br"),c("br"),n._v("Thấy chạy đến luyện đan sư càng ngày càng nhiều, từng cái hốt hoảng thần sắc, Viên Đào nhớ tới cái gì, nuốt ngụm nước bọt."),c("br"),c("br"),n._v("“Cái này...”"),c("br"),c("br"),n._v("Tôn Cường sờ lên cằm, nhịn không được gật đầu: “Thật là có khả năng!”"),c("br"),c("br"),n._v("Thiếu gia cũng không phải là sống yên ổn được chủ, một cái danh sư thi đấu, làm sở hữu người dự thi đều điên rồi, hiện tại chạy vào đi tìm ngũ tinh luyện đan sư hỗ trợ thay mặt luyện, có thể hay không cũng làm ra cái gì yêu thiêu thân?"),c("br"),c("br"),n._v("Đổi lại những người khác, không dám nói, thiếu gia... Vẫn đúng là không nhất định!"),c("br"),c("br"),n._v("“Thiếu gia các ngươi? Làm sao có thể! Yên tâm đi, hắn chỉ là cái tam tinh luyện đan sư, coi như cho mười cái lá gan, cũng không làm được cái gì phát!”"),c("br"),c("br"),n._v("Theo trong lúc khiếp sợ khôi phục lại, nghe được hai người trò chuyện, nữ hài lắc đầu."),c("br"),c("br"),n._v("Đối phương lão sư cùng thiếu gia, nàng cũng không phải chưa thấy qua, một cái hai mươi tuổi tam tinh luyện đan sư mà thôi."),c("br"),c("br"),n._v("Loại cấp bậc này luyện đan sư, toàn bộ công hội tùy tiện có thể cào ra một nắm lớn, không lộ ra trước mắt người đời, coi như kiếm chuyện, lại có thể làm ra cái gì?"),c("br"),c("br"),n._v("“Cái này không phải chắc chắn...” Tôn Cường lắc đầu."),c("br"),c("br"),n._v("“Các ngươi suy nghĩ nhiều, chúng ta công hội thế nhưng là có rất nhiều ngũ tinh luyện đan sư, tam tinh luyện đan sư, tại cái khác địa phương coi như rất lợi hại, tại trước mặt bọn hắn, cái gì cũng không tính, thở mạnh cũng không dám...”"),c("br"),c("br"),n._v("Nữ hài lắc đầu, muốn nói tiếp, chỉ thấy trước mắt đại môn lần nữa “Kẹt kẹt” mở ra, trước đó đã gặp vị kia “Thiếu gia” uể oải đi ra."),c("br"),c("br"),n._v("“Cung tiễn Trương đan sư!”"),c("br"),c("br"),n._v("“Hi vọng Trương đan sư về sau có rảnh đến cho chúng ta giảng bài, chúng ta công hội đem vinh hạnh cực kỳ, vô cùng cảm kích!”"),c("br"),c("br"),n._v("Ngay sau đó liền thấy hội trưởng cùng rất nhiều công hội trưởng già, từng cái khiêm tốn đứng ở phía sau, tiễn hắn rời đi, trong mắt mang theo cung kính."),c("br"),c("br"),n._v("“...” Nữ hài thật khóc."),c("br"),c("br"),n._v("Không phải không dám thở mạnh ư? Không phải cái gì cũng không tính ư?"),c("br"),c("br"),n._v("Cái này mẹ nó rốt cuộc chuyện gì xảy ra?"),c("br"),c("br"),n._v("Convert by: ThấtDạ"),c("br"),c("br"),c("br"),c("br")]),n._v(" "),c("hr"),n._v(" "),c("p",[c("a",{attrs:{href:"./chap-628"}},[n._v("Chương trước")]),n._v("  |  "),c("a",{attrs:{href:"./chap-630"}},[n._v("Chương sau")])])])}),[],!1,null,null,null);h.default=i.exports}}]);