(window.webpackJsonp=window.webpackJsonp||[]).push([[1887],{2242:function(n,h,t){"use strict";t.r(h);var i=t(44),c=Object(i.a)({},(function(){var n=this,h=n.$createElement,t=n._self._c||h;return t("ContentSlotsDistributor",{attrs:{"slot-key":n.$parent.slotKey}},[t("h1",{attrs:{id:"chuong-54-hong-kim-chau-qua"}},[t("a",{staticClass:"header-anchor",attrs:{href:"#chuong-54-hong-kim-chau-qua"}},[n._v("#")]),n._v(" Chương 54: Hồng Kim Châu Quả")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("br"),t("br"),n._v(" Vị này chính là ai?"),t("br"),t("br"),n._v(" Xưng Thiên Huyền vương quốc đệ nhất quyền thần Trấn Nam Vương con trai độc nhất!"),t("br"),t("br"),n._v(" Lại khóc trời đập đất gọi Trương Huyền gia gia? Ta không nghe lầm chứ..."),t("br"),t("br"),n._v(" Thành bá chỉ cảm thấy trước mắt thiên lôi từng trận, sắp té xỉu trên đất."),t("br"),t("br"),n._v(" Liền đi cầm cái đá đo lực trụ... Xảy ra chuyện gì? Ai có thể nói cho ta biết một hồi..."),t("br"),t("br"),n._v(" “Khụ khụ, tốt, Trương tiểu huynh đệ, sau đó có cơ hội luận bàn, Bạch Tốn bị thương, cũng cần điều chỉnh một chút! Đá đo lực trụ đem ra, ta nhìn ngươi vẫn là thử một chút sức mạnh đi!” "),t("br"),t("br"),n._v(" Thấy tình cảnh lúng túng, Lục Trầm đại sư vội vã đánh gãy."),t("br"),t("br"),n._v(" “Được rồi!” Thấy Bạch Tốn cũng chưa muốn so tài, Trương Huyền có chút ít tiếc nuối lắc đầu một cái, nhìn về phía vừa đem ra bị xen vào rãnh đá đo lực trụ, vài bước đi tới trước mặt, khẽ mỉm cười: “Kỳ thực, ngày hôm qua ta mới đo quá, cũng là bốn đỉnh tả hữu sức mạnh, ngày hôm nay đo bất trắc đều được, nên khác biệt không lớn...” "),t("br"),t("br"),n._v(" Đem Diêu Hàn đánh thành đầu heo, là ngày hôm nay rạng sáng sự tình, trước lúc này mới đo quá sức mạnh, tám đỉnh!"),t("br"),t("br"),n._v(" Ngày đó bận bịu rối tinh rối mù, hơn nữa không có công pháp, huyệt đạo không mở ra một cái, sức mạnh hẳn là sẽ không tăng trưởng, còn tuy rằng tu luyện Thiên Đạo Kim Thân, nhưng theo Trương Huyền, thân thể cho dù có tiến bộ, cũng không thể so với Chân Khí mạnh hơn đi!"),t("br"),t("br"),n._v(" Cho tới vì sao nói bốn đỉnh, không nói thật, rất đơn giản, Đỉnh Lực cảnh đỉnh cao đều là cái này sức mạnh, hắn không muốn bại lộ quá nhiều, quá mức một lúc nữa khảo nghiệm thời điểm, lưu một nửa khí lực là tốt rồi."),t("br"),t("br"),n._v(" “Nếu đều đã lấy tới, thử một chút đi!” Lục Trầm đại sư nói."),t("br"),t("br"),n._v(" “Hảo!” Không chối từ nữa, Trương Huyền nhẹ nhàng nở nụ cười, quyết định chỉ dùng một nửa khí lực dự định, một quyền với trước mắt trụ đá nện cho đi qua."),t("br"),t("br"),n._v(" Quyền phong gào thét, sức mạnh như sấm."),t("br"),t("br"),n._v(" Oành!"),t("br"),t("br"),n._v(" Hào quang lóe lên, chữ viết chậm rãi hiện lên."),t("br"),t("br"),n._v(" “Ngươi nhìn liền bốn đỉnh đi...” Nhìn thấy phía trên hiện lên chữ viết, Trương Huyền cười xua tay, quay đầu cười nói, bất quá lời còn chưa nói hết, liền thấy Bạch Tốn, Lục Trầm đại sư, Hoàng Ngữ đám người giống nhìn quái vật nhìn về phía hắn."),t("br"),t("br"),n._v(" Từng cái từng cái con ngươi đều sắp rơi trên mặt đất."),t("br"),t("br"),n._v(" “Thế nào?” Trương Huyền cau mày."),t("br"),t("br"),n._v(" Cúi đầu nhìn một chút tự mình, không cái gì không đúng a?"),t("br"),t("br"),n._v(" “Ngươi nhìn lại một chút... Đá đo lực trụ!” Hoàng Ngữ không nhịn được nói."),t("br"),t("br"),n._v(" “Đá đo lực trụ thế nào? Không phải liền là bốn đỉnh sao? Cho tới kinh ngạc như vậy...” "),t("br"),t("br"),n._v(" Quay đầu nhìn sang, vừa nhìn phía dưới, Trương Huyền thân thể loáng một cái, kém chút không ngã xuống đất, trong lúc đó vừa nãy nhìn “Bốn” mặt sau chẳng biết lúc nào hiện lên hai chữ “Mười chín”."),t("br"),t("br"),n._v(" Liền lên chính là... Bốn mươi chín!"),t("br"),t("br"),n._v(" “Ta vừa nãy một quyền đánh ra... Bốn mươi chín đỉnh?” "),t("br"),t("br"),n._v(" Trương Huyền chính mình cũng sợ hết hồn."),t("br"),t("br"),n._v(" Lăng Thần mới đo tám đỉnh lực lượng, vừa nãy lại cố ý để lại một nửa sức mạnh, làm sao có khả năng đạt đến bốn mươi chín đỉnh sức mạnh?"),t("br"),t("br"),n._v(" Nếu như không lưu một nửa sức mạnh, chẳng phải biểu thị một quyền xuống dĩ nhiên tiếp cận trăm đỉnh cự lực?"),t("br"),t("br"),n._v(" Võ giả năm tầng Đỉnh Lực cảnh đỉnh cao là bốn Đỉnh Lực lượng, viên mãn cũng sẽ không vượt qua tám mươi đỉnh, một quyền trăm đỉnh, chẳng phải biểu thị dĩ nhiên có thể cùng võ giả sáu tầng Ích Huyệt cảnh thực lực sánh ngang, thậm chí còn vượt qua?"),t("br"),t("br"),n._v(" Chuyện này..."),t("br"),t("br"),n._v(" “Có phải là... Cùng cái này đá đo lực trụ hỏng rồi?” "),t("br"),t("br"),n._v(" Một ý nghĩ xông ra, Trương Huyền không nhịn được nói."),t("br"),t("br"),n._v(" “Trụ đá không xấu, ngươi vừa rồi tại thư phòng không phải đột phá sao? Tu vi đột phá, sức mạnh tăng mạnh, mấu chốt nhất điểm chính là, ngươi không thích ứng loại sức mạnh này, không hề cảm kích, cho nên mới đem... Bạch Tốn đánh thảm như vậy!” "),t("br"),t("br"),n._v(" Lục Trầm đại sư thấy ý nghĩ của chính mình nhận được xác minh, giải thích."),t("br"),t("br"),n._v(" Nguyên bản muốn nói tự mình cũng bị đánh thảm như vậy, thời khắc mấu chốt ngừng lại, bất kể nói thế nào, chính mình cũng là đại sư, phải chú ý danh tiếng..."),t("br"),t("br"),n._v(" “Tu vi đột phá? Từ bốn đỉnh cao lên tới bốn mươi chín đỉnh? Lẽ nào một hồi mở ra bốn mươi lăm cái huyệt đạo?” "),t("br"),t("br"),n._v(" Không nghe giải thích còn tốt, vừa nghe giải thích, Bạch Tốn, Hoàng Ngữ kém chút không ngất đi tại chỗ."),t("br"),t("br"),n._v(" Không có nói đùa chớ?"),t("br"),n._v("  Võ giả sáu tầng Ích Huyệt cảnh, mở ra thân thể huyệt đạo, tăng cường tu vi, mỗi mở ra một chỗ, tăng cường một đỉnh lực lượng."),t("br"),t("br"),n._v(" Truyền thuyết, thân thể tổng cộng 108 nơi huyệt đạo, nhưng có thể mở ra, bất quá 72 viên, hơn nữa những này huyệt đạo làm sao mở ra là có trình tự, mỗi người thể chất không giống, cần không giống trình tự mới có thể đạt đến hiệu quả tốt nhất, không tìm được phương pháp, cả một đời, cũng chỉ có thể mở ra ba, bốn mươi cái huyệt đạo, liền lại không cách nào tiến bộ."),t("br"),t("br"),n._v(" Bình thường mở ra, đều là từng viên từng viên xông ra, sức mạnh cũng là tiến lên dần dần một lừng lẫy tăng cường, một hồi tăng lên dữ dội 45 đỉnh..."),t("br"),t("br"),n._v(" Đại ca, ngươi đây là muốn nghịch thiên a!"),t("br"),t("br"),n._v(" Gặp đột phá, chưa từng thấy... Như thế đột phá đi!"),t("br"),t("br"),n._v(" Kỳ thực không riêng mọi người rung động choáng váng đầu hoa mắt, khó có thể tin tưởng được, liền ngay cả Trương Huyền cũng không biết xảy ra chuyện gì."),t("br"),t("br"),n._v(" Liền tu luyện cái Thiên Đạo Kim Thân, liền có như thế tiến bộ? Để sức mạnh chợt tăng chín mươi đỉnh?"),t("br"),t("br"),n._v(" Dựa theo tình huống bình thường, vừa nãy tiện tay đem Lục Trầm đại sư vứt bay, đem Bạch Tốn đánh bại, hắn nên rõ ràng có rất tiến nhanh bước, sức mạnh tăng lên, mà không phải không biết gì cả."),t("br"),t("br"),n._v(" Nhưng hắn làm như người xuyên việt, tu luyện cũng mới một hai ngày sự tình, có thể khống chế hảo tu vi phát huy bao nhiêu sức mạnh cũng rất không tệ, để hắn khống chế tỉ mỉ, đối với sức mạnh nắm giữ viên mãn như ý, cũng là ở quá làm người khác khó chịu!"),t("br"),t("br"),n._v(" Vậy thì như là một người bình thường, trực tiếp nắm giữ siêu nhân sức mạnh, vừa mới bắt đầu khẳng định cũng không thích ứng, thậm chí đều không làm rõ được."),t("br"),t("br"),n._v(" Trương Huyền hiện tại chính là tình huống như thế, nguyên bản chỉ có tám đỉnh lực lượng, một hồi tăng lên dữ dội 90 đỉnh, gia tăng rồi gấp mười lần còn nhiều, then chốt vẫn là thân thể, không cách nào cảm thấy... Có thể sớm rõ ràng mới là lạ!"),t("br"),t("br"),n._v(" Không tại chỗ đem Bạch Tốn đánh thành bánh thịt, cũng rất không tệ."),t("br"),t("br"),n._v(" “Trương tiểu huynh đệ, ngươi không biết vừa nãy thật... Mở ra bốn mươi lăm nơi huyệt đạo chứ?” "),t("br"),t("br"),n._v(" Thấy hắn đờ ra, Lục Trầm đại sư không nhịn được nói."),t("br"),t("br"),n._v(" Một hồi mở ra bốn mươi lăm nơi huyệt đạo, này chưa từng nghe nói a... Có thể không phải như vậy, làm sao một hồi chợt tăng sức mạnh lớn như vậy?"),t("br"),t("br"),n._v(" “Ta... Vẫn là Đỉnh Lực cảnh đỉnh cao, tu vi vẫn chưa tiến bộ, chỉ là... Sức mạnh thân thể gia tăng rồi một ít!” Trương Huyền nói."),t("br"),t("br"),n._v(" “Thân thể?” Lục Trầm đại sư kinh ngạc, tràn đầy không thể tin được: “Ta nghe nói, trong thiên địa có một ít thiên tài địa bảo, chỉ cần dùng, có thể để cho thân thể sánh ngang sắt thép, nắm giữ có thể so với Ích Huyệt cảnh đỉnh cao sức mạnh, lẽ nào... Tu luyện cũng có thể?” "),t("br"),t("br"),n._v(" “Ây... Ta ngày hôm nay thật giống ăn một cái không biết tên trái cây, vừa mới bắt đầu cảm thấy toàn thân toả nhiệt, vẫn chưa để ý, có thể là khi đó sức mạnh thân thể đột nhiên tăng lên dữ dội a!” Trương Huyền nói."),t("br"),t("br"),n._v(" Làm như người hiện đại, đương nhiên biết thất phu vô tội hoài bích kỳ tội sự tình, nếu quả thật nói mình là tu luyện một hồi, liền đạt đến mức độ này, nhất định sẽ có vô số nhân đỏ mắt tìm tự mình yêu cầu công pháp!"),t("br"),t("br"),n._v(" Thậm chí còn có thể có người bắt tự mình nghiên cứu."),t("br"),t("br"),n._v(" Nếu Lục Trầm đại sư nói có thiên tài địa bảo có thể để người ta thân thể tiến nhanh, vừa vặn liền sườn núi xuống lừa."),t("br"),t("br"),n._v(" “Cơ duyên a, đây thực sự là cơ duyên to lớn!” Lục Trầm đại sư không nghi ngờ gì."),t("br"),t("br"),n._v(" Kỳ thực không phải hắn không nghi ngờ, mà là coi như hoài nghi, cũng phải tin tưởng mới được a!"),t("br"),t("br"),n._v(" Tu luyện một canh giờ liền có thể để thân thể tăng lên dữ dội mấy chục đỉnh cự lực?"),t("br"),t("br"),n._v(" Đây không phải làm mộng ban ngày sao?"),t("br"),t("br"),n._v(" Cùng nam nhân sinh con như thế buồn cười!"),t("br"),t("br"),n._v(" Cùng với tin tưởng cái này, còn không bằng tin tưởng ăn một cái nào đó thiên tài địa bảo."),t("br"),t("br"),n._v(" “Không biết tiểu huynh đệ ăn cái kia trái cây dáng dấp ra sao?” Lục Trầm đại sư hỏi tiếp."),t("br"),t("br"),n._v(" “Cái gì dáng dấp?” Trương Huyền vò đầu, hắn tin khẩu nói bậy, nào có biết hình dáng gì? Bất quá, nếu bức đến nước này, không nói, nhất định sẽ bị hoài nghi, liền nói ngay: “Là cái trái cây màu đỏ...” "),t("br"),t("br"),n._v(" Trước ở học viện Tàng Thư Các đọc sách thời điểm, có vẻ như cũng xem qua tương tự ghi chép, bất quá là cái du ký truyền thuyết, chính là ăn một loại trái cây màu đỏ, đột nhiên sức mạnh tăng mạnh, giờ khắc này cũng chỉ đành mèo mù chạm chuột chết, thử vận khí một chút."),t("br"),t("br"),n._v(" “Màu đỏ? Lẽ nào là màu đỏ loét? Quả nhiên là vật kia!” "),t("br"),t("br"),n._v(" Lục Trầm đại sư giống là nghĩ đến cái gì, hô hấp dồn dập."),t("br"),t("br"),n._v(" “Đại sư biết?” "),t("br"),t("br"),n._v(" “Là Hồng Kim Châu Quả! Truyền thuyết chỉ cần ăn, có thể trực tiếp để thân xác con người sức mạnh thân thể tăng nhiều, Ích Huyệt cảnh đỉnh cao, đều khó mà chống lại!” Lục Trầm đại sư hưng phấn nói."),t("br"),t("br"),n._v(" “Hồng Kim Châu Quả?” "),t("br"),t("br"),n._v(" Trương Huyền một mặt quái lạ."),t("br"),t("br"),n._v(" Lẽ nào... Thật là có vật này?"),t("br"),t("br"),n._v("Convert by: Hoang Chau"),t("br"),t("br"),t("br"),t("br")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("a",{attrs:{href:"./chap-62"}},[n._v("Chương trước")]),n._v("  |  "),t("a",{attrs:{href:"./chap-64"}},[n._v("Chương sau")])])])}),[],!1,null,null,null);h.default=c.exports}}]);