(window.webpackJsonp=window.webpackJsonp||[]).push([[1888],{2243:function(n,h,t){"use strict";t.r(h);var i=t(44),c=Object(i.a)({},(function(){var n=this,h=n.$createElement,t=n._self._c||h;return t("ContentSlotsDistributor",{attrs:{"slot-key":n.$parent.slotKey}},[t("h1",{attrs:{id:"chuong-621-phu-thai-tu"}},[t("a",{staticClass:"header-anchor",attrs:{href:"#chuong-621-phu-thai-tu"}},[n._v("#")]),n._v(" Chương 621: Phủ thái tử")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("br"),t("br"),n._v("“Đi thôi!”"),t("br"),t("br"),n._v("Không để ý tới cung kính tiễn đưa hội trưởng đám người, cùng một bên khóc như mưa nữ hài, Trương Huyền gọi Viên Đào cùng Tôn Cường, bước nhanh ra ngoài đi đến."),t("br"),t("br"),n._v("Nếu có thời gian, khẳng định đem cái này công hội thư tịch đều xem một lần, không có thời gian, chỉ có thể coi như không có gì, có cơ hội lại nói."),t("br"),t("br"),n._v("Đi ra luyện đan sư công hội, trở lại xe ngựa, Trương Huyền đem bình ngọc lấy ra, cho Viên Đào đưa tới."),t("br"),t("br"),n._v("“Mỗi lần bôi lên một bộ phận tại trên da, đợi hấp thu xong xong, lại bôi lên lần tiếp theo... Cố gắng tu luyện, có thể để ngươi thực lực trong thời gian ngắn gia tăng không ít!”"),t("br"),t("br"),n._v("“Đa tạ lão sư!”"),t("br"),t("br"),n._v("Tiếp nhận bình ngọc, Viên Đào tràn đầy kích động."),t("br"),t("br"),n._v("Dược dịch trân quý, đi theo một ngày, biết đến hết sức rõ ràng, lão sư vì thứ này tốn hao nhiều như vậy tâm huyết, ân tình bao lớn, không thể báo đáp."),t("br"),t("br"),n._v("“Những linh thạch này cầm, lúc tu luyện khẳng định sẽ dùng!”"),t("br"),t("br"),n._v("Ngay sau đó lại đưa qua mấy chục viên linh thạch trung phẩm."),t("br"),t("br"),n._v("Dược dịch, chỉ có thể kích hoạt thể chất, muốn đột phá, còn cần có đại lượng linh lực duy trì, dù sao mới vừa hắc vị kia Lục tiểu thư một khoản, cũng không thiếu hụt."),t("br"),t("br"),n._v("Biết lão sư ý tứ, Viên Đào tiếp tục tiếp nhận."),t("br"),t("br"),n._v("“Tôn Cường, những linh thạch này, ngươi cầm, một khi Hóa Thanh trì mở ra, ta có thể muốn đi tu luyện vài ngày, không có rảnh chiếu cố... Ngươi cầm linh thạch, ngươi, Trịnh Dương, Vương Dĩnh, Lưu Dương ai lúc tu luyện cần, liền phân cho ai một bộ phận, đừng chậm trễ!”"),t("br"),t("br"),n._v("Tiếp lấy Trương Huyền lại lấy ra hai trăm viên khoảng chừng linh thạch cho Tôn Cường đưa tới."),t("br"),t("br"),n._v("Hóa Thanh trì một khi mở ra, tất nhiên muốn ở trong đó tu luyện, những linh thạch này, cho mọi người sử dụng, để bọn hắn nhanh chóng tiến bộ."),t("br"),t("br"),n._v("“Yên tâm đi, thiếu gia, chúng ta nhất định sẽ cố gắng tu luyện!” Tôn Cường liền vội vàng gật đầu."),t("br"),t("br"),n._v("“Người khác đến không có gì, mấu chốt nhất là ngươi...”"),t("br"),t("br"),n._v("Nghe hắn tự tin như vậy, Trương Huyền lắc đầu."),t("br"),t("br"),n._v("Trịnh Dương đám người, nói thật, hắn đến không lo lắng, mấu chốt là cái tên này, liền ưa thích lười biếng, một cái nhìn không được liền không muốn luyện, muốn cho hắn nhanh chóng tăng thực lực lên, cũng thực sự đủ nhức đầu."),t("br"),t("br"),n._v("“Ây...”"),t("br"),t("br"),n._v("Biết thiếu gia ý tứ chỉ cái gì, Tôn Cường tràn đầy xấu hổ."),t("br"),t("br"),n._v("“Tốt, đây là Kỳ Trân lâu lấy được bảo vật, ngươi cho mọi người phân một chút, thực sự vô dụng, tìm người giúp ta bán đi, hối đoái thành linh thạch!”"),t("br"),t("br"),n._v("Cổ tay khẽ đảo, đem Cam Nhất Bình cho hắn cái kia trữ vật giới chỉ đưa tới."),t("br"),t("br"),n._v("Kỳ Trân lâu đồ vật, hắn liền lưu lại  cái này có thể tấn thăng pháp bảo, cái khác có cũng được mà không có cũng không sao, còn không bằng bán đi, nhiều hối đoái chút linh thạch."),t("br"),t("br"),n._v("“Rõ!” Tiếp nhận bảo vật, Tôn Cường đáp ứng một tiếng."),t("br"),t("br"),n._v("Những chuyện này, giao cho hắn đi làm, không có gì có thể lo lắng, Trương Huyền không nói thêm lời."),t("br"),t("br"),n._v("Biết không có thời gian, xe ngựa lái thật nhanh, hơn nửa canh giờ phía sau, liền trở lại chỗ ở."),t("br"),t("br"),n._v("Mới đi đến trước cửa, chỉ thấy Hồng sư đã mặt mũi gấp gáp chờ, Tất Giang Hải cùng La Tuyền cũng theo ở phía sau."),t("br"),t("br"),n._v("Hóa Thanh trì, mỗi cái các nước chư hầu có ba cái danh ngạch, danh sư thi đấu ba hạng đầu mới có tư cách tiến vào."),t("br"),t("br"),n._v("Đã tối hôm nay tiệc tối, là vì một lần nữa quy hoạch danh ngạch, hai người này tự nhiên cũng có tư cách đi qua, nhìn một chút đối phương rốt cuộc lấy phương pháp gì, quyết định vận mệnh của bọn hắn."),t("br"),t("br"),n._v("“Trương sư, ngươi rốt cuộc đã đến, không có xảy ra chuyện gì chứ? Thật muốn có việc... Nhất định muốn sớm nói, ta dễ tìm lão sư hỗ trợ giải quyết...”"),t("br"),t("br"),n._v("Thấy hắn rốt cục trở về, Hồng sư kích động không có kém chút khóc lên."),t("br"),t("br"),n._v("Theo La Chiêu, Phùng Vũ nơi đó trở về, liền đến tìm Trương sư, kết quả... Đợi ròng rã ba, bốn canh giờ, Quỷ Ảnh đều không có thấy."),t("br"),t("br"),n._v("Thật lo lắng hắn lại chạy đến địa phương nào kiếm chuyện, tới không được."),t("br"),t("br"),n._v("Nơi này không thể so với 28 thực lực quốc gia lực, cũng không phải vạn quốc liên minh, nhờ vào thực lực của hắn, liền có thể nhẹ nhõm giải quyết... Huyễn Vũ đế quốc Đế đô, vẫn là khiêm tốn một ít tốt, nếu không, chọc ra cái sọt, cũng chỉ có thể tìm lão sư Vệ Giang đường chủ giải quyết."),t("br"),t("br"),n._v("“Yên tâm đi, chỉ là đi luyện đan sư công hội luyện đan dược, không có việc gì!”"),t("br"),t("br"),n._v("Thấy hắn thế mà lo lắng cái này, Trương Huyền tràn đầy im lặng giải thích một chút."),t("br"),t("br"),n._v("Cái này gọi chuyện gì... Ngươi cũng là cái gì ánh mắt."),t("br"),t("br"),n._v("Ta chính là như thế ưa thích người gây chuyện sao?"),t("br"),t("br"),n._v("Bất kể nói thế nào, ta cũng là danh sư, tự mình thân phận, người khác không chọc ta tình huống dưới, làm sao có thể khắp nơi làm loạn, nhìn ngươi nói gần nói xa nói ta cùng phá hoại chi vương giống như, đi đến địa phương nào đều không an lòng..."),t("br"),t("br"),n._v("Nếu như cho Hồng sư biết hắn tìm cách, khẳng định sẽ rưng rưng gật đầu."),t("br"),t("br"),n._v("Ngươi không phải phá hoại chi vương là cái gì? Ta Quỷ Vực huyễn thành, ta Thiên Tinh cờ, ta Đào Hà Chi Thư, ta thật vất vả bắt Dị Linh tộc nhân..."),t("br"),t("br"),n._v("đọ"),t("br"),n._v("c ngantruyen.com/ Không nghĩ, còn muốn trái tim của ta đoán chừng cũng xong rồi..."),t("br"),t("br"),n._v("“Không có việc gì là tốt rồi, tiệc tối lập tức liền muốn bắt đầu, chúng ta nhanh lên một chút đi đi!”"),t("br"),t("br"),n._v("Biết không có rảnh nhiều lời, Hồng sư lại không xoắn xuýt, nói một tiếng, bốn người hướng cách đó không xa phủ thái tử để đi đến."),t("br"),t("br"),n._v("Quá lá mầm trước ở lại đông cung, cách nơi này chẳng qua vài trăm mét mà thôi, rất mau tới đến trước mặt, đem thư mời lấy ra ngoài."),t("br"),t("br"),n._v("“Mấy vị, mời vào bên trong!”"),t("br"),t("br"),n._v("Nhìn thấy thư mời, phụ trách thủ vệ binh sĩ, ở phía trước dẫn đường."),t("br"),t("br"),n._v("“Vị tiểu ca này, ta muốn hỏi một chút, Phong Nguyên đế quốc Phùng sư, Tiềm Phong đế quốc La sư cùng Trục Nguyệt đế quốc trần sư đều tới rồi sao?”"),t("br"),t("br"),n._v("Theo sau lưng, Hồng sư hỏi."),t("br"),t("br"),n._v("Tiềm Phong đế quốc La sư chính là trước đó bị hắn vơ vét cái kia La Chiêu, đến mức Trục Nguyệt đế quốc trần sư, thì là trước đó cùng La Chiêu cùng nhau lão giả."),t("br"),t("br"),n._v("Hai cái này cộng thêm Phùng Vũ, đều là tứ đại các nước chư hầu Danh Sư đường người phụ trách, giống như hắn, là riêng phần mình phạm vi bên trong danh sư thi đấu người chủ trì."),t("br"),t("br"),n._v("“Bọn họ đã đến nửa canh giờ...”"),t("br"),n._v(" Binh sĩ gật đầu."),t("br"),t("br"),n._v("“Nửa canh giờ?”"),t("br"),t("br"),n._v("Hồng sư khóe miệng giật một cái."),t("br"),t("br"),n._v("Còn tưởng rằng đối phương không tới, về thời gian có thể giành được tiên cơ, không nghĩ tới đều đến nửa canh giờ."),t("br"),t("br"),n._v("Lâu như vậy, sẽ không đã đem danh ngạch xác định tới đi!"),t("br"),t("br"),n._v("“Tiệc tối... Chẳng lẽ đã bắt đầu, ta không tới chậm chứ? Nếu như đã chậm, quấy rầy thái tử sẽ không tốt...” Chần chờ một chút, Hồng sư mang theo lúng túng nói."),t("br"),t("br"),n._v("“Tiệc tối còn chưa bắt đầu, thái tử đi mời Lục công chúa, muốn để bọn hắn cũng tham gia tiệc, đến bây giờ còn không có trở về!”"),t("br"),t("br"),n._v("Binh sĩ lắc đầu."),t("br"),t("br"),n._v("“Còn chưa bắt đầu? Vậy là tốt rồi!”"),t("br"),t("br"),n._v("Nhẹ nhàng thở ra, Hồng sư cổ tay khẽ đảo, chuyển tới một cái linh thạch: “Đa tạ tiểu ca!”"),t("br"),t("br"),n._v("“Khách khí!”"),t("br"),t("br"),n._v("Thấy hắn hào phóng như vậy, trực tiếp đưa một cái linh thạch trung phẩm, binh sĩ ánh mắt sáng lên, vội vàng sắp xếp gọn, lần nữa nhìn về phía mấy người thái độ cũng lành rất nhiều, hạ giọng sát lại."),t("br"),t("br"),n._v("“Lần này tiệc tối, ta nghe nói, là vì xác nhận Hóa Thanh trì danh ngạch...”"),t("br"),t("br"),n._v("“Cũng không biết thái tử quyết định quy củ là cái gì, chúng ta có thể hay không lấy được...” Hồng sư cười khổ lắc đầu."),t("br"),t("br"),n._v("“Cụ thể cái gì quy củ ta cũng không biết, bất quá... Ta nghe nói, buổi dạ tiệc này mục đích chủ yếu, là cho Hồng Viễn danh sư học viện tới thiên tài đón tiếp, thuận tiện để các nước chư hầu thiên tài, cũng thấy một chút học trưởng.”"),t("br"),t("br"),n._v("“Đón tiếp? Ngươi ý tứ... Cái kia bốn vị cũng tới?”"),t("br"),t("br"),n._v("Hồng sư sững sờ."),t("br"),t("br"),n._v("Trước đó nghe La Chiêu nói rồi, thái tử muốn đi bốn cái danh ngạch, chính là cho danh sư học viện tới mấy cái thiên tài lưu."),t("br"),t("br"),n._v("Vốn cho rằng buổi dạ tiệc này, chỉ là giải quyết nhóm người mình vấn đề nội bộ, không nghĩ tới, đem bốn vị này cũng xin mời."),t("br"),t("br"),n._v("“Chẳng lẽ... Lựa chọn danh ngạch phương pháp, cùng bọn hắn có quan hệ?”"),t("br"),t("br"),n._v("Cơ thể chấn động, Hồng sư đột nhiên nghĩ đến cái gì."),t("br"),t("br"),n._v("“Ta không biết, có điều, ta đoán bọn họ chỉ cần mở miệng, thái tử khẳng định không dám cự tuyệt... Trước mặt La sư đám người, liền chuẩn bị không ít đồ vật, xem như lễ vật, sớm kết giao, ta nhìn các ngươi, cũng hơi chuẩn bị một chút, lo trước khỏi hoạ...”"),t("br"),t("br"),n._v("Binh sĩ liếc mắt nhìn hai phía, hạ giọng."),t("br"),t("br"),n._v("“Lễ vật?” Hồng sư sắc mặt trắng nhợt."),t("br"),t("br"),n._v("Hắn toàn bộ thân gia, đều tại danh sư thi đấu, bị phía sau cái này hai hàng hủy gần đủ rồi, coi như muốn cầm, cũng không bỏ ra nổi cái gì a!"),t("br"),t("br"),n._v("Dù sao đối phương là Hồng Viễn đế quốc tới, tầm mắt cực cao, bình thường bảo vật, là không thể nào để ý."),t("br"),t("br"),n._v("Không thấy được sắc mặt của hắn, binh sĩ cảm khái gật đầu: “Đúng vậy a, nhiều lễ thì không bị trách à, chuẩn bị chút bảo vật, nhất định có thể gia tăng hảo cảm, đến lúc đó danh ngạch cũng là đơn giản... Tốt, yến hội sảnh ngay ở phía trước, ta liền đưa đến nơi này!”"),t("br"),t("br"),n._v("“Đa tạ!”"),t("br"),t("br"),n._v("Biết là vừa rồi viên kia linh thạch phát huy tác dụng, mới cùng mình bọn người nói nhiều như vậy, Hồng sư vội vàng ôm quyền."),t("br"),t("br"),n._v("Bất cứ lúc nào chỗ nào, tiền không có hoa oan uổng."),t("br"),t("br"),n._v("Biết những tin tức này, dù sao cũng so cái gì cũng không biết, đần độn liền vọt vào đi tốt hơn nhiều."),t("br"),t("br"),n._v("Chỉ là... Biết nguyên do, mới phiền não lại tới."),t("br"),t("br"),n._v("“Lễ vật? Tìm cái gì mới có thể để cho người khác hai mắt tỏa sáng? Nếu như Đào Hà Chi Thư còn tại liền tốt... Dị Linh tộc nhân cũng được ah...”"),t("br"),t("br"),n._v("Xoa xoa mi tâm, Hồng sư mặt mũi mướp đắng."),t("br"),t("br"),n._v("Nếu như trước đó cái kia mấy thứ bảo vật không có hủy đi, tùy tiện lấy ra một cái, coi như đối phương là Hồng Viễn đế quốc người, hắn cũng có thể cam đoan để hắn ánh mắt sáng lên, nhưng bây giờ..."),t("br"),t("br"),n._v("“Hồng sư, làm sao vậy?”"),t("br"),t("br"),n._v("Thấy hắn vẻ mặt khó coi, Trương Huyền đi tới."),t("br"),t("br"),n._v("“Không có... Cái gì!” Vội vàng lắc đầu."),t("br"),t("br"),n._v("Đồ vật đã hủy, muốn nhiều hơn nữa cũng vô dụng, việc cấp bách là thế nào cùng binh sĩ nói giống nhau, cùng cái kia bốn vị học trưởng giữ gìn mối quan hệ."),t("br"),t("br"),n._v("“Có phải hay không lo lắng lễ vật?” Một bên La Tuyền hỏi."),t("br"),t("br"),n._v("“Đúng vậy a!” Hồng sư gật đầu."),t("br"),t("br"),n._v("“Lễ vật? Sớm biết liền không đem đồ chơi kia cho Tôn Cường...”"),t("br"),t("br"),n._v("Thấy hắn lo lắng cái này, Trương Huyền nhịn không được lắc đầu."),t("br"),t("br"),n._v("Còn tưởng rằng là cái gì, lễ vật, mới vừa quét sạch cái Kỳ Trân lâu, vẫn đúng là không thiếu, chỉ là... Vừa mới đem đồ vật cho Tôn Cường, lại đi muốn, cũng không biết kịp không kịp."),t("br"),t("br"),n._v("“Kỳ thật cũng không cần thiết như thế để tâm vào chuyện vụn vặt đi, tuyển bạt danh ngạch, khẳng định cùng danh sư năng lực có quan hệ, nếu như lễ vật có tác dụng, khẳng định người người đều bè lũ xu nịnh, rốt cuộc không cần học tập!”"),t("br"),t("br"),n._v("Có thể đi vào Hóa Thanh trì, khẳng định đều là muốn đi danh sư học viện, đã muốn phán quyết ra ba cái danh ngạch đến, tất nhiên liên quan danh sư nội dung, cái gọi là lễ vật, chỉ là dệt hoa trên gấm mà thôi, có lẽ ảnh hưởng không lớn!"),t("br"),t("br"),n._v("“Chỉ mong đi!” Hồng sư cũng biết những này, vẫn là có chút không yên lòng."),t("br"),t("br"),n._v("“Đúng rồi, Hồng sư, ngươi đi tìm Phùng sư bọn họ phiền phức, được cái gì hữu dụng tin tức không?” Đột nhiên nhớ tới, trước đó cùng đối phương thương nghị sự tình, Trương Huyền nhịn không được hỏi."),t("br"),t("br"),n._v("“A, lấy được một chút tin tức, là liên quan tới danh sư học viện tới bốn vị này thiên tài...”"),t("br"),t("br"),n._v("Gật gật đầu, Hồng sư đang muốn đem Lục công chúa đám người sự tình nói một chút, liền nghe đến một cái tiếng bước chân truyền đến, ngay sau đó thanh âm vang lên."),t("br"),t("br"),n._v("“Hồng Thiển, đây chính là ngươi tuyển ra thiên tài? Thoạt nhìn cũng chả có gì đặc biệt!”"),t("br"),t("br"),n._v("Ngay sau đó một thân ảnh từ phía trước đại sảnh đi ra, chính là Tiềm Phong đế quốc La Chiêu danh sư."),t("br"),t("br"),n._v("(PS: Hôm nay công chúng người ban bố “Phi Nhi công chúa” nhân vật đồ, mọi người VX lục soát “hstysds” hoặc là “Hoành Tảo Thiên Nhai” chú ý Hoành Tảo Thiên Nhai Wechat công chúng người, trực tiếp gửi đi “Phi Nhi công chúa” hoặc là “Lục tiểu thư” liền có thể kiểm tra. Mặt khác, đầu tháng, ném nguyệt phiếu!!)"),t("br"),t("br"),n._v("Convert by: ThấtDạ"),t("br"),t("br"),t("br"),t("br")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("a",{attrs:{href:"./chap-629"}},[n._v("Chương trước")]),n._v("  |  "),t("a",{attrs:{href:"./chap-631"}},[n._v("Chương sau")])])])}),[],!1,null,null,null);h.default=c.exports}}]);