(window.webpackJsonp=window.webpackJsonp||[]).push([[189],{541:function(n,h,i){"use strict";i.r(h);var t=i(44),c=Object(t.a)({},(function(){var n=this,h=n.$createElement,i=n._self._c||h;return i("ContentSlotsDistributor",{attrs:{"slot-key":n.$parent.slotKey}},[i("h1",{attrs:{id:"chuong-1147-đien-thanh"}},[i("a",{staticClass:"header-anchor",attrs:{href:"#chuong-1147-đien-thanh"}},[n._v("#")]),n._v(" Chương 1147: Điền Thanh")]),n._v(" "),i("hr"),n._v(" "),i("p",[n._v("Chương 1147: Điền Thanh"),i("br"),i("br"),n._v("Biết đối phương muốn đối phó bản thân, lại không nghĩ rằng, tàn nhẫn như vậy, trực tiếp cho Tôn Cường bên trên cực hình."),i("br"),i("br"),n._v("Tục ngữ nói, đánh chó cũng phải nhìn chủ nhân, Tôn Cường là quản gia của hắn, liên thông biết đều không có báo tin, trực tiếp bắt tới, như vậy hình pháp, rõ ràng không đem hắn để vào mắt."),i("br"),i("br"),n._v("“Có ý tứ gì?”"),i("br"),i("br"),n._v("Trung Thanh Vương hừ lạnh: “Để Minh phó điện chủ đi vào!”"),i("br"),i("br"),n._v("Rầm rầm!"),i("br"),i("br"),n._v("Nương theo lời nói, hai cái hộ vệ, mang theo một bóng người đi đến."),i("br"),i("br"),n._v("Thấy rõ ràng bộ dáng của đối phương, Trương Huyền khuôn mặt trầm xuống."),i("br"),i("br"),n._v("Hắn nhận biết, chính là Tĩnh Viễn thành Độc điện phó điện chủ, Minh Chân!"),i("br"),i("br"),n._v("Cái tên này lúc trước vì học sinh ra mặt, tìm bản thân phiền phức, huyên náo toàn bộ Độc điện biến thành một vùng phế tích, tội ác tày trời... Không phải là bị điện chủ Nhược Thanh Viễn chỗ lấy độc chết chi hình sao?"),i("br"),i("br"),n._v("Làm sao còn sống?"),i("br"),i("br"),n._v("Hơn nữa nhìn bộ dáng, không có chút nào tổn hại, thực lực dường như tiến thêm một bước?"),i("br"),i("br"),n._v("Chẳng lẽ Độc điện xảy ra biến cố?"),i("br"),i("br"),n._v("Mấu chốt nhất là, Tôn Cường trên thân mang theo nặng nề xiềng xích, đối phương lại không có cái gì, hình như Trung Thanh Vương khách quý, để sắc mặt hắn có chút âm trầm."),i("br"),i("br"),n._v("“Gặp qua vương gia!”"),i("br"),i("br"),n._v("Minh Chân ôm quyền."),i("br"),i("br"),n._v("“Ừm, đưa ngươi biết đến nói đến!”"),i("br"),i("br"),n._v("Trung Thanh Vương nói."),i("br"),i("br"),n._v("“Ta là Hồng Viễn đế quốc, Tĩnh Viễn thành Độc điện phó điện chủ, Minh Chân. Vị này Tôn Cường hơn hai tháng trước, thay đổi bộ dáng, chuyên đi qua Độc điện, tự xưng khai phái tổ sư truyền nhân... Là một vị hết sức lợi hại độc sư, ta dùng độc đều kém xa tít tắp...”"),i("br"),i("br"),n._v("Minh phó điện chủ đem trước Độc điện chuyện phát sinh, toàn bộ nói ra."),i("br"),i("br"),n._v("“... Về sau, hắn đem khai phái tổ sư, giao cho tới đây Bạch độc sư cùng Hải độc sư, liền rời đi, ta vốn bị chỗ lấy độc chết chi hình, may mắn cho ta uống thuốc độc vị kia từng là học sinh của ta, lúc này mới nhận được cơ hội chạy trốn...”"),i("br"),i("br"),n._v("“Độc điện khai phái tổ sư truyền nhân? Hộ tống tổ sư... Không phải chính tai nghe nói, cũng không dám tin tưởng là thật. Trương sư, vị này Tôn Cường thân là quản gia của ngươi, lại là một vị lợi hại như thế độc sư... Ngươi còn có lời gì nói?”"),i("br"),i("br"),n._v("Cười lạnh liên tục, Trung Thanh Vương nhìn lại."),i("br"),i("br"),n._v("“Chỉ bằng cái này, nói ta cấu kết độc sư? Không xứng làm danh sư?”"),i("br"),i("br"),n._v("Còn tưởng rằng đối phương nắm giữ chứng cớ gì, nghe được cái này, Trương Huyền lắc đầu: “Không nói trước cái này Minh Chân, thân phận đáng tin không đáng tin, chỉ nói thân là Độc điện người, Thanh Nguyên người đế quốc người kêu đánh, làm sao sẽ xuất hiện ở đây? Chẳng lẽ... Trung Thanh Vương, ngươi cũng cùng Độc điện có cấu kết? Có lẽ, thật cùng những người khác nói đồng dạng, bản thân ngươi liền là một vị độc sư?”"),i("br"),i("br"),n._v("“Làm càn!”"),i("br"),i("br"),n._v("Trung Thanh Vương hét lớn."),i("br"),i("br"),n._v("“Làm càn hay không, không phải ngươi nói tính toán!”"),i("br"),i("br"),n._v("Trương Huyền khoát tay áo, đánh gãy hắn, một mặt không quan trọng: “Thiên hạ kêu Tôn Cường nhiều người đi, một câu, liền nói là quản gia của ta... Có chứng cứ gì? Nếu như ta bắt một con chó, phong hắn làm Trung Thanh Vương, chó đi ra ngoài ăn cứt, có thể hay không nói, ngươi thích ăn cứt? Lại nói, quản gia của ta, muốn thật sự là cái gì khai phái tổ sư truyền nhân, các ngươi đối với hắn như vậy, khẳng định sớm đã bị độc chết đi!”"),i("br"),i("br"),n._v("“Ngươi...” Không nghĩ tới kẻ trước mắt này, nói chuyện như vậy làm người tức giận, Trung Thanh Vương suýt chút nữa hộc máu, sắc mặt tái xanh: “Biết thân phận của hắn, chúng ta liền trực tiếp khống chế, căn bản không để hắn hạ độc cơ hội! Vị này Minh Chân, thân phận vô cùng xác thực chắc chắn, hơn nữa đã rời khỏi Độc điện, bỏ gian tà theo chính nghĩa, dẫn đầu chúng ta người, tiêu diệt Độc điện cái u ác tính này!”"),i("br"),i("br"),n._v("“Tiêu diệt Độc điện?”"),i("br"),i("br"),n._v("Trương Huyền trong lòng “Lộp bộp!” Một lần."),i("br"),i("br"),n._v("Tĩnh Viễn thành Độc điện, mặc dù rất lợi hại, nhưng mà nếu như Thanh Nguyên đế quốc ra tay, lại có nội ứng chi viện lời nói, quả thực rất khó chống đỡ được."),i("br"),i("br"),n._v("Độc điện bị hủy diệt, không biết Hứa Du trưởng lão thế nào."),i("br"),i("br"),n._v("Không nghĩ tới ngắn ngủi hơn hai tháng thời gian, phát sinh nhiều chuyện như vậy."),i("br"),i("br"),n._v("“Không sai! Mặc kệ hắn trước kia làm sao, chỉ bằng vào cái này, cũng đủ để nói rõ lập trường, không có khả năng tùy ý nói dối. Hắn báo cáo quản gia của ngươi Tôn Cường, nói có lý có theo, ta cũng chuyên môn thẩm vấn qua Độc điện mọi người, lấy được thuyết pháp không khác chút nào! Đã đã chứng minh chuyện tính chân thực.”"),i("br"),i("br"),n._v("Đứng dậy, Trung Thanh Vương bàn tay hất lên: “Thân là chủ nhân, đem như vậy hung hãn tàn bạo độc sư, giữ ở bên người, coi thường Danh Sư đường quy củ... Vì Đế đô an toàn, ta tự nhiên có quyền lợi đưa ngươi trói lại!”"),i("br"),i("br"),n._v("“Vì Đế đô an toàn? Ta Trương Huyền, là đường đường chính chính, Danh Sư đường khảo hạch danh sư, cho dù có vấn đề gì, cũng có danh sư đường phán quyết, ngươi? Ta nhớ được, Thanh Nguyên đế quốc Danh Sư đường, không về ngươi chưởng quản đi!”"),i("br"),i("br"),n._v("Danh sư phạm sai lầm, chỉ có Danh Sư đường phán quyết, một cái đế quốc vương gia mà thôi, có tư cách ư?"),i("br"),i("br"),n._v("“Ta biết không có tư cách, cho nên, đặc biệt đem chuyện này, bẩm báo Danh Sư đường Điền Thanh Phó đường chủ, để hắn tự mình làm ra quyết định!” Trung Thanh Vương ôm quyền, ánh mắt lại nhìn về phía một bên vị lão giả kia."),i("br"),i("br"),n._v("Chính là để Trương Huyền vừa vào cửa, liền cảm thấy có chút kiêng kỵ vị kia."),i("br"),i("br"),n._v("“Điền Thanh?”"),i("br"),i("br"),n._v("Nghe được tên, nhớ tới một việc, Trương Huyền con mắt lập tức híp lại."),i("br"),i("br"),n._v("Soạt!"),i("br"),n._v(" Chưa kịp nói chuyện, lão giả liền đứng dậy, mọi người lúc này mới chú ý, trước ngực hắn huy chương, bảy viên sao lấp lánh ra ánh sáng."),i("br"),i("br"),n._v("Thất tinh danh sư!"),i("br"),i("br"),n._v("“Phó đường chủ?”"),i("br"),i("br"),n._v("Lạc Thất Thất, Ngọc Phi Nhi, đều lộ ra cảnh giác chi ý, nhìn Trương Huyền một cái."),i("br"),i("br"),n._v("Không có danh sư, còn nói được, đối phương thế mà liền Thanh Nguyên đế quốc Danh Sư đường Phó đường chủ đều mời tới, cũng có chút khó làm."),i("br"),i("br"),n._v("“Quả nhiên là có chuẩn bị mà đến!”"),i("br"),i("br"),n._v("Rất nhanh khôi phục lại bình tĩnh, Trương Huyền trong lòng suy tính."),i("br"),i("br"),n._v("Trước đó hắn nghe Ngô sư giới thiệu Thanh Nguyên đế quốc Danh Sư đường, biết qua một chút tin tức."),i("br"),i("br"),n._v("Đường chủ cẩu Thiên Trạch, thực lực đã đạt tới Xuất Khiếu cảnh sơ kỳ, nhiều năm trước liền không hỏi thế sự, mặc kệ trong đường sự tình."),i("br"),i("br"),n._v("Hiện tại, hầu như tất cả mọi chuyện đều do vị này Điền phó đường chủ tiến hành phán quyết."),i("br"),i("br"),n._v("Nguyên nhân chính là như vậy, trước mắt vị này, mặc dù chỉ là Phó đường chủ, trên thực tế lại sử dụng lấy đường chủ quyền lực, địa vị vô cùng kính trọng."),i("br"),i("br"),n._v("Còn tưởng rằng cái này Trung Thanh Vương là trò đùa trẻ con, cố ý để cho mình khó xử, cho dù muốn tìm phiền phức, cũng mang không đến tính thực chất tổn thương, không nghĩ tới thế mà đem vị này mời tới."),i("br"),i("br"),n._v("Người này nắm giữ một phương Danh Sư đường, nắm giữ câu thông tổng bộ quyền lợi, một khi xác nhận bản thân cấu kết Độc điện, chỉ sợ nói nhiều hơn nữa, cũng rửa không sạch."),i("br"),i("br"),n._v("Đương nhiên, Độc điện mặc dù danh tiếng không tốt, chỉ cần không làm ra quá nhiều chuyện ác, vẫn còn không tính là cái gì, nhiều nhất danh dự bị hao tổn thôi."),i("br"),i("br"),n._v("“Trương sư, ta nghe nói qua ngươi rất nhiều chuyện, biết thiên phú của ngươi cùng năng lực!”"),i("br"),i("br"),n._v("Nhìn lại, Điền Thanh phó điện chủ mỉm cười: “Trung Thanh Vương chủ quản Thanh Nguyên thành phòng vệ, tra được Tôn Cường là độc sư, tự nhiên muốn cẩn thận truy xét, cũng không phải là cố ý tìm ngươi phiền phức! Ngày hôm nay sở dĩ hẹn ở đây, mà không phải Danh Sư đường, liền là không muốn để cho chuyện làm lớn chuyện, có hại danh sư học viện danh tiếng.”"),i("br"),i("br"),n._v("Nói đến đây, nhìn một cái Minh Chân, tiếp tục nói: “Vị này Minh Chân độc sư thân phận, ta từ đầu tới đuôi đều kiểm tra đối chiếu sự thật qua, cũng chuyên môn đo lường, không có nói sai. Bất quá, ngươi yên tâm, mặc dù Tôn Cường là độc sư, ngươi trước thời hạn không biết rõ tình hình lời nói, Danh Sư đường cũng sẽ xét xử lý, sẽ không lấy điểm xây mặt, một gậy liền đem một vị thiên tài viện trưởng, trực tiếp đánh chết!”"),i("br"),i("br"),n._v("“Mặc dù là độc sư?”"),i("br"),i("br"),n._v("Nghe được đối phương như vậy chắc chắn lời nói, Trương Huyền mày nhăn lại: “Đã Điền phó đường chủ từ đầu tới đuôi nhìn qua, Tôn Cường có phải hay không độc sư? Ngươi hẳn là rất rõ ràng a?”"),i("br"),i("br"),n._v("Bất kể nói thế nào, đối phương đều là thất tinh đỉnh phong nhất cấp bậc danh sư, Tôn Cường có hay không độc sư năng lực, có phải hay không độc sư, chỉ cần hơi quan sát liền có thể dò xét rõ ràng, giờ phút này thế mà nói thẳng hắn chính là, rõ ràng tại giúp đối phương nói chuyện."),i("br"),i("br"),n._v("Độc sư, quanh năm uống thuốc độc, luyện độc, theo ở bề ngoài xem mặc dù cùng thường nhân không khác, nhưng cẩn thận quan sát, tình huống trong cơ thể, cùng bình thường tu luyện giả là hoàn toàn khác biệt."),i("br"),i("br"),n._v("Lợi hại độc sư, một giọt máu, có thể đủ độc chết một tòa thành thị."),i("br"),i("br"),n._v("Tôn Cường tu luyện chính là đường đường chính chính Thiên Đạo bản rút gọn công pháp, mặc dù không dụng công, nhưng cũng chân khí tinh thuần, cùng “Độc sư” nửa xu quan hệ đều không có, hắn không tin đối phương nhìn không ra."),i("br"),i("br"),n._v("“Vị này Tôn Cường, cho dù không có lục tinh độc sư thực lực, nhưng mà đem Độc điện khai phái tổ sư đưa về, liền phạm vào sai lầm cực kỳ lớn nhỡ! Độc điện khai phái tổ sư được xưng là tai nạn chi thể, một thân kịch độc, hủy diệt khắp nơi, đem như vậy người, đưa về tổng bộ, bất kể từ chỗ nào một cái phương diện tới nói, đều khó mà tha thứ. Mấy vạn năm trước nhân tộc cùng Dị Linh tộc đại chiến, nhất đẳng đế quốc công hội khả năng không có ghi chép, ta từng tại liên minh đế quốc công hội trông được qua, Dị Linh tộc nhân độc chết qua không biết bao nhiêu danh sư, sinh linh đồ thán, vị này khai phái tổ sư, tội ác tày trời...”"),i("br"),i("br"),n._v("Điền phó đường chủ nghĩa chính ngôn từ, giữa hai lông mày mang theo trách trời thương dân nặng nề."),i("br"),i("br"),n._v("Nhân tộc, Dị Linh tộc đối chiến, thương vong nặng, không biết bao nhiêu danh sư, chết tại kịch độc phía dưới, nếu như không có vị này Độc điện tổ sư, liền không có độc sư cái nghề nghiệp này, cũng không có như vậy huống."),i("br"),i("br"),n._v("Có thể nói, vị tổ sư này, là nhân tộc kẻ địch."),i("br"),i("br"),n._v("“Thân là danh sư, bản thân hạ nhân hộ tống Độc điện tổ sư, lại không thêm quản thúc, đem Danh Sư đường ban tặng thân phận của ngươi đặt chỗ nào? Lại đem danh sư thủ hộ nhân loại trách nhiệm đặt chỗ nào?”"),i("br"),i("br"),n._v("Điền phó đường chủ nói tiếp: “Đương nhiên, ngươi chỉ cần thừa nhận chuyện này, cũng không hiểu rõ tình hình, Danh Sư đường tất nhiên sẽ theo nhẹ xử phạt.”"),i("br"),i("br"),n._v("“Độc điện khai phái tổ sư? Loại chuyện hoang đường này, Điền phó đường chủ cũng có thể tin tưởng?”"),i("br"),i("br"),n._v("Trương Huyền lắc đầu: “Nếu như ta nhớ không lầm, vị này khai phái tổ sư, có lẽ cùng Khổng sư là nhân vật cùng một thời đại đi! Mấy vạn năm trước nhân vật, liền Khổng sư đều không có lưu lại bất cứ dấu vết gì, ngươi chẳng lẽ cảm thấy vị này, có thể kinh nghiệm thời gian hạo kiếp, sống đến bây giờ?”"),i("br"),i("br"),n._v("“Ta là không tin, nhưng mà đối phương không cần thiết nói dối, cũng không có lý do nói dối. Theo ta được biết, trời nhận danh sư đều xuất hiện, Độc điện khai phái tổ sư phục sinh, có lẽ không phải không có lửa thì sao có khói.”"),i("br"),i("br"),n._v("Điền phó đường chủ nói."),i("br"),i("br"),n._v("“Trời nhận danh sư?” Trương Huyền cười nhạo: “Cái này sao có thể, nếu quả thật có, khẳng định sớm đã danh dương thiên hạ, làm sao có thể, ta thân là lục tinh danh sư, nghe đều không có nghe qua?”"),i("br"),i("br"),n._v("“Ta cũng là căn cứ Phong Sư điện xuất hiện tình huống, đoán ra được, hiện nay, tất cả tin tức đều bị nói năng thận trọng, cho dù Thanh Nguyên phân bộ, người biết cũng không nhiều.”"),i("br"),i("br"),n._v("Điền phó đường chủ nhẹ gật đầu: “Mặc dù nghe rất phóng đãng, nhưng mà thân là danh sư, quan hệ hàng tỉ sinh mệnh, vẫn là muốn cẩn thận xử lý.”"),i("br"),i("br"),n._v("“Phó đường chủ có ý tứ là...”"),i("br"),i("br"),n._v("“Rất đơn giản, vị này Tôn Cường, tội ác tày trời, tại chỗ tru sát! Trương sư thân là chủ nhân, nhưng không có phát giác, mặc dù tội danh không nặng, nhưng cũng không thể khinh thường, trục xuất Địa quật ba tháng, lập công chuộc tội.”"),i("br"),i("br"),n._v("Điền sư nói."),i("br"),i("br"),n._v("“Tại chỗ tru sát? Trục xuất Địa quật ba tháng?”"),i("br"),i("br"),n._v("Nghe được đối phương quyết định, Trương Huyền giống như là nghe được chê cười, mí mắt vừa nhấc, phủi mông một cái đứng lên: “Nếu như...”"),i("br"),i("br"),n._v("“Ta không muốn đây này?”"),i("br"),i("br"),n._v("(Mọi người nói, lão nhai vì cái gì không bạo phát, thật ra thì, bạo phát dễ dàng, trạng thái được rồi, hoàn toàn có thể bạo phát đi ra. Có thể ổn định đổi mới, mỗi ngày hai chương, định thời gian xác định vị trí upload khó! Dù sao, sáng tác có tạm ngừng thời điểm, trong nhà có nhiều việc, cũng có hay không thời gian sáng tác thời điểm. Lão nhai thật rất cố gắng. Dù sao hai đứa bé, còn muốn cam đoan mỗi ngày ít nhất sáu ngàn chữ... Còn xin mọi người nhiều hơn lý giải, nhiều hơn tìm đến nguyệt phiếu ủng hộ! Cám ơn!)"),i("br"),i("br"),i("br"),i("br")]),n._v(" "),i("hr"),n._v(" "),i("p",[i("a",{attrs:{href:"./chap-1155"}},[n._v("Chương trước")]),n._v("  |  "),i("a",{attrs:{href:"./chap-1157"}},[n._v("Chương sau")])])])}),[],!1,null,null,null);h.default=c.exports}}]);