(window.webpackJsonp=window.webpackJsonp||[]).push([[1890],{2245:function(n,h,i){"use strict";i.r(h);var t=i(44),g=Object(t.a)({},(function(){var n=this,h=n.$createElement,i=n._self._c||h;return i("ContentSlotsDistributor",{attrs:{"slot-key":n.$parent.slotKey}},[i("h1",{attrs:{id:"chuong-623-hai-loai-đanh-gia"}},[i("a",{staticClass:"header-anchor",attrs:{href:"#chuong-623-hai-loai-đanh-gia"}},[n._v("#")]),n._v(" Chương 623: Hai loại đánh giá")]),n._v(" "),i("hr"),n._v(" "),i("p",[i("br"),i("br"),n._v("Chương 623: Hai loại đánh giá"),i("br"),i("br"),n._v("“Ừm?”"),i("br"),i("br"),n._v("Nghe nói như thế, tất cả mọi người sững sờ, tất cả đều âm thầm gọi tốt."),i("br"),i("br"),n._v("Trần Việt danh sư tra hỏi, rất rõ ràng tại tru tâm, trả lời cái gì đều sai, nhưng là khó như vậy lấy trả lời lời nói, Hồng sư một câu liền phản kích tới."),i("br"),i("br"),n._v("Được đó, ngươi nói ta đến trễ, thái tử cũng chưa tới, có đúng hay không tới càng muộn? Ngươi dám nói thái tử đến trễ, có ý tứ gì?"),i("br"),i("br"),n._v("Tiệc mà thôi, ngươi cố ý chụp mũ, rắp tâm ở đâu?"),i("br"),i("br"),n._v("Lời này một màn, không chỉ trả lời đối phương, còn dựa thế phản kích, để Trần Việt trả lời cũng không được, không trả lời cũng không được, cả người vẻ mặt lúc thì đỏ, lúc thì trắng, buồn bực suýt thổ huyết."),i("br"),i("br"),n._v("“Ồ? Mấy ngày không thấy, Hồng sư tài ăn nói so trước kia tốt hơn nhiều!”"),i("br"),i("br"),n._v("Thấy đồng minh Trần sư nói không ra lời, La Chiêu mở miệng."),i("br"),i("br"),n._v("“Không chỉ tài ăn nói tốt, thân thủ cũng tốt, La sư có muốn thử một chút hay không?” Hồng sư đổ đầy một chén rượu ngon, cười khanh khách nhìn về phía một chỗ: “Ngươi nói, có đúng hay không ah Phùng sư?”"),i("br"),i("br"),n._v("“Ngươi... Phốc!”"),i("br"),i("br"),n._v("Phùng Vũ cơ thể nhoáng một cái, dẫn động thương thế, một ngụm máu tươi phun ra."),i("br"),i("br"),n._v("Không phải không nói chuyện này ư? Tại sao lại dẫn? Lại nói, hai người bọn họ tìm ngươi phiền phức, liên quan ta cái rắm, lặng lẽ trốn ở một bên, ngay cả lời cũng không dám nói, liền nằm trúng đạn..."),i("br"),i("br"),n._v("“Phùng sư ra ngoài đi săn, bị linh thú vây công gây thương tích... Hai ngày này một mực trốn trong xó ít ra ngoài, làm sao có thể biết thân thủ của ngươi có được hay không!” La Chiêu cũng khóe miệng giật một cái, khẽ nói."),i("br"),i("br"),n._v("Hắn cũng chế nhạo, nói Phùng sư bị linh thú đả thương, tương đương với gián tiếp đang nói, Hồng sư là linh thú, không hiểu nhân tình."),i("br"),i("br"),n._v("“Nguyên lai là linh thú tổn thương, thật sự là đáng tiếc!”"),i("br"),i("br"),n._v("Nghe được lời nói của đối phương bên trong mang theo nhục mạ, Hồng sư không chút phật lòng, nhàn nhạt ngẩng đầu: “Trên mặt hắn vết thương, không rõ ràng vết nứt, ngừng ngắt máu ứ đọng, ửng hồng biến thành màu đen, đây là chân khí rót vào trong đó mới có thể hình thành; Xương mũi hơi trũng xuống, cùng cái trán hình thành một đường thẳng, rất hiển nhiên là bị đạp một cước, mới có thể lưu lại; Lỗ tai sưng đỏ, bờ môi phát tím, đây là bàn tay quất lên mới có thể lưu lại ấn ký...”"),i("br"),i("br"),n._v("“Rõ ràng như vậy nhân loại công kích vết thương, ngươi lại nói là linh thú...”"),i("br"),i("br"),n._v("Nói đến đây, Hồng sư mí mắt vừa nhấc, ánh mắt như điện: “Xin hỏi, La sư, ngươi là mắt mù ư? Vẫn là đã học được mở mắt nói lời bịa đặt?”"),i("br"),i("br"),n._v("“Ngươi...” Hơi đỏ mặt, La Chiêu thiếu chút nữa ngất đi."),i("br"),i("br"),n._v("Danh sư nhãn lực vô cùng tốt, nhân loại đả thương cùng linh thú công kích, bản thân liền có rất lớn khác nhau, vừa rồi nói như vậy, chỉ là cố ý nói sang chuyện khác, tránh khỏi xấu hổ mà thôi, ai ngờ... Hắn trực tiếp làm rõ, ngược lại thành bản thân mở mắt nói lời bịa đặt..."),i("br"),i("br"),n._v("La Chiêu tức giận toàn thân run rẩy, nhưng một câu cũng nói không nên lời."),i("br"),i("br"),n._v("Nếu như nói là cái tên này đánh, vòng vây sự tình khẳng định sẽ tiết lộ ra ngoài..."),i("br"),i("br"),n._v("Mẹ nó, hắn không phải luôn luôn miệng lưỡi vụng về sao? Làm sao một chút trở nên như thế nhanh mồm nhanh miệng?"),i("br"),i("br"),n._v("đăng n"),i("br"),n._v("hập http://ngantruyen.com/ để đọc truyệｎ  Những người khác cũng hai mặt nhìn nhau."),i("br"),i("br"),n._v("Hồng sư cũng quá lợi hại đi!"),i("br"),i("br"),n._v("Trần sư, La sư hai người liên thủ giáp công, dùng ngôn ngữ trêu chọc muốn cho hắn mắc câu, kết quả, người ta vừa đập vừa cào, không chỉ phản kích thành công, còn để một thứ câu nói đều nói không được... Làm sao làm được?"),i("br"),i("br"),n._v("“Ta làm sao vậy? Chẳng lẽ nói sai? Lại hoặc là... La sư chính là trong miệng mình đầu kia linh thú, Phùng sư tổn thương, chính là ngươi đánh, bây giờ được ta chỉ ra, có chút xấu hổ?” Hồng sư thản nhiên nói."),i("br"),i("br"),n._v("“A!” La Chiêu nắm đấm xiết chặt, muốn trả lời, lại không biết nói thế nào."),i("br"),i("br"),n._v("“Tốt, ngươi cũng đã nói, đây là mọi người gặp nhau vui sướng tiệc, làm gì làm giương cung bạt kiếm!” Thấy bạn cũ suýt tức điên, nhưng cái gì đều nói không được, Trần Việt đành phải mở miệng hoà giải."),i("br"),i("br"),n._v("“Cũng đúng, tiệc mà thôi, không cần thiết làm như vậy!”"),i("br"),i("br"),n._v("Đem chén rượu bưng lên uống vào, Hồng sư không nói thêm gì nữa."),i("br"),i("br"),n._v("“Đáng giận...”"),i("br"),i("br"),n._v("Nhìn thấy hắn cái bộ dáng này, La sư, Trần sư đều cảm thấy khuôn mặt khó coi, như là táo bón giống nhau."),i("br"),i("br"),n._v("Vốn có bị cái tên này đi qua làm loạn, làm đầy bụng tức giận, muốn tại tiệc bắt đầu trước, cho đối phương cái ra oai phủ đầu, thuận tiện giẫm hô mấy cước, nằm mơ đều không có nghĩ đến, chân còn không có đưa tới, liền bị đối phương giẫm hô."),i("br"),i("br"),n._v("Hơn nữa còn là ở trên mặt cuồng đạp đến mấy lần cái chủng loại kia."),i("br"),i("br"),n._v("Thật sự là càng nghĩ càng buồn bực."),i("br"),i("br"),n._v("Nhẹ nhàng ngẩng đầu nhìn lại, muốn nhìn một chút có đúng hay không tại vị kia thiên tài trong mắt lưu lại ấn tượng xấu, đã thấy hắn vẫn như cũ uống rượu, mặt Thượng Cổ giếng không gợn sóng, hình như vừa rồi cãi lộn, căn bản không nghe thấy."),i("br"),i("br"),n._v("“Như vậy cũng tốt...”"),i("br"),i("br"),n._v("Thấy đối phương không có để ý, hai người nhìn nhau, riêng phần mình nhẹ nhàng thở ra."),i("br"),i("br"),n._v("“Cái tên này không biết chuyện gì xảy ra, miệng lưỡi so trước kia lợi hại hơn nhiều, cùng hắn cãi lộn không có cái gì chỗ tốt, trước chờ thái tử tới rồi nói sau!”"),i("br"),i("br"),n._v("Biết tiếp tục ầm ĩ đi xuống, không có bất kỳ cái gì chỗ tốt, La Chiêu, Trần Việt, Phùng Vũ ba người truyền âm cho nhau."),i("br"),i("br"),n._v("Trong lúc nhất thời, gian phòng lần nữa an tĩnh lại."),i("br"),i("br"),n._v("..."),i("br"),i("br"),n._v("Bên này lẫn nhau không chịu thua, mỗi người có suy nghĩ riêng, thái tử hành cung một cái sân, một cái thân mặc lộng lẫy thanh niên đứng tại trong đó, xem bộ dáng là đang chờ người nào, hơn nữa không biết đợi bao lâu."),i("br"),i("br"),n._v("Dù vậy, trên mặt đều không có không chút nào kiên nhẫn, ngược lại mang theo mỉm cười."),i("br"),i("br"),n._v("Nếu như Hồng sư tại cái này, khẳng định có thể nhận ra, vị này, chính là Huyễn Vũ đế quốc thái tử, Diệp Tiền!"),i("br"),i("br"),n._v("“Đáng giận, đáng giận, đều do tên kia, nếu không phải hắn cướp đi ta Đỗ Căn thảo, làm sao đến mức giống bây giờ cũng mua không được... Hình Viễn, ngươi điều tra ra người kia là ai chưa?”"),i("br"),i("br"),n._v("Rít lên một tiếng, ngay sau đó một cái xinh đẹp thân ảnh, theo bên ngoài viện đi đến."),i("br"),i("br"),n._v("“Cái này... Một lát nữa, ta liền để Diệp Tiền tìm người đi thăm dò...”"),i("br"),i("br"),n._v("Ngay sau đó một cái mang theo giọng áy náy vang lên."),i("br"),i("br"),n._v("Một thanh niên theo sát ở phía sau."),i("br"),i("br"),n._v("Chính là Trương Huyền ban đầu ở Kỳ Trân lâu gặp phải Lục công chúa cùng thanh niên Hình Viễn."),i("br"),i("br"),n._v("Rời đi Kỳ Trân lâu phía sau, vị này Lục công chúa lại tìm kiếm khắp nơi, nhìn có thể hay không tìm tới Đỗ Ưu thảo, kết quả tìm thật nhiều địa phương, cũng không thấy, một mực chơi đùa đến bây giờ, không thu hoạch được gì."),i("br"),n._v(" Càng nghĩ càng giận, vừa vào cửa liền không nhịn được gào thét."),i("br"),i("br"),n._v("“Lục công chúa, Hình Viễn học trưởng!”"),i("br"),i("br"),n._v("Thái tử Diệp Tiền, tiến lên đón, ôm quyền khom người."),i("br"),i("br"),n._v("“Ồ? Ngươi tại cái này vừa vặn, vừa vặn có chuyện để cho ngươi hỗ trợ!” Hình Viễn nhàn nhạt nhìn qua."),i("br"),i("br"),n._v("“Học trưởng có gì phân phó cứ việc nói là được!”"),i("br"),i("br"),n._v("Diệp Tiền vội nói."),i("br"),i("br"),n._v("“Lúc ban ngày, ta cùng Lục công chúa tại Kỳ Trân lâu gặp một thanh niên hai mươi tuổi bộ dáng, mang theo một cái mập mạp quản gia, ngươi giúp ta tra một chút thân phận của hắn, mau chóng hồi bẩm tới.”"),i("br"),i("br"),n._v("Hình Viễn khoát tay áo."),i("br"),i("br"),n._v("“Tốt!” Diệp Tiền gật đầu."),i("br"),i("br"),n._v("Mặc dù đối phương nói rất mơ hồ, nhưng đây là Huyễn Vũ Đế đô, nhờ vào hắn thái tử lực lượng, tra ra một người, vẫn là rất đơn giản."),i("br"),i("br"),n._v("“Lục công chúa, Hình Viễn học trưởng, chuyện này ta lập tức để cho người ta đi làm, đêm nay ta chuẩn bị cái đón tiếp yến, mời các ngươi đi qua, thuận tiện cũng nhận thức một chút chúng ta Huyễn Vũ đế quốc, lần này đưa đi danh sư học viện tân sinh...”"),i("br"),i("br"),n._v("“Không hứng thú!”"),i("br"),i("br"),n._v("Lục công chúa khoát tay áo."),i("br"),i("br"),n._v("Đi ra thời điểm, vẫn là cao cao tại thượng công chúa, trở về liền biến thành người khác nô tỳ, tức giận đều tức giận đã no đầy đủ, đâu còn có tâm tư đi tham gia cái gì tiệc tối!"),i("br"),i("br"),n._v("“Cái này...” Không nghĩ tới vị này học tỷ, trực tiếp cự tuyệt, Diệp Tiền mặt mũi lúng túng nhìn về phía cách đó không xa Hình Viễn: “Học trưởng, ngươi...”"),i("br"),i("br"),n._v("“A, ngươi về trước đi, chờ một hồi, ta sẽ đi qua!”"),i("br"),i("br"),n._v("Hình Viễn khoát tay áo."),i("br"),i("br"),n._v("“Được rồi!”"),i("br"),i("br"),n._v("Diệp Tiền cũng nhìn ra vị này Lục công chúa ngay tại đang tức giận, không nói thêm lời, cúi mình vái chào đi ra ngoài."),i("br"),i("br"),n._v("Hắn mặc dù là Huyễn Vũ đế quốc thái tử, nhưng cùng Hồng Viễn đế quốc công chúa so ra, vẫn là kém nhiều lắm, căn bản không tại một cái cấp bậc."),i("br"),i("br"),n._v("Đối phương bằng lòng, ngược lại cũng thôi, không nguyện ý, tuyệt không dám cưỡng cầu."),i("br"),i("br"),n._v("Mới vừa đi mấy bước, còn không có rời đi sân nhỏ, liền nghe đến Hình Viễn truyền âm ở bên tai vang lên: “Lục công chúa ngay tại nổi nóng, ta khuyên một chút, một lát nữa cùng một chỗ tới!”"),i("br"),i("br"),n._v("“Đa tạ học trưởng!”"),i("br"),i("br"),n._v("Nghe được hứa hẹn, Diệp Tiền ánh mắt sáng lên, đi ra ngoài."),i("br"),i("br"),n._v("Lần này tiệc mục đích chủ yếu là vì cho Lục công chúa đám người đón tiếp, các nàng thật không đi, liền mất mặt."),i("br"),i("br"),n._v("“Lục công chúa, không nên tức giận, chờ Diệp Tiền tra ra đối phương cụ thể thân phận, thực sự không được, ta tốn hao đánh đổi đi đem Đỗ Ưu thảo mua được...”"),i("br"),i("br"),n._v("Thấy hắn rời đi, Hình Viễn an ủi."),i("br"),i("br"),n._v("“Không cần ngươi quan tâm!” Lục công chúa thở phì phò ngồi xuống."),i("br"),i("br"),n._v("Hình Viễn đứng tại chỗ tràn đầy xấu hổ, đi cũng không được, không đi cũng không được."),i("br"),i("br"),n._v("Vị này Lục công chúa tính tình cổ quái, để hắn theo đuổi cũng mười phần vất vả."),i("br"),i("br"),n._v("Trước đó còn rất tốt, thay đổi bất thường, để cho người ta không hiểu rõ nổi."),i("br"),i("br"),n._v("Nói thật, tiểu tử kia, thu ngươi làm tỳ nữ, là ngươi đáp ứng, ta cũng khuyên can qua, kết quả không nghe... Thậm chí càng giết chết hắn, ngươi cũng không đồng ý..."),i("br"),i("br"),n._v("Bây giờ lại chạy đến nơi đây đối ta cùng Diệp Tiền nổi giận, tính là gì?"),i("br"),i("br"),n._v("Càng nghĩ càng thấy đến buồn bực."),i("br"),i("br"),n._v("“Ngọc Phi Nhi, ngươi thế nào?”"),i("br"),i("br"),n._v("Đang không biết làm sao khuyên can, liền nghe đến chân bước tiếng vang lên, một nữ tử tràn đầy hưng phấn mà đi tới, vừa đi vừa đeo lấy mỉm cười, dường như muốn nhảy dựng lên."),i("br"),i("br"),n._v("“Thất Thất tiểu thư, ngươi...”"),i("br"),i("br"),n._v("Nhìn thấy đối phương bộ dáng này, Hình Viễn ngẩn ngơ."),i("br"),i("br"),n._v("Trước mắt vị này Lạc Thất Thất, luôn luôn lạnh lùng như tuyết, lúc nào nói chuyện, đều bị cự tuyệt mặt, làm sao hôm nay tràn đầy nụ cười?"),i("br"),i("br"),n._v("Nếu như cho học viện người biết, băng sơn mỹ nhân, thế mà cũng sẽ cười, có thể hay không tại chỗ nổi điên?"),i("br"),i("br"),n._v("“Không có gì!”"),i("br"),i("br"),n._v("Nghe được hắn, Lạc Thất Thất mới chú ý tới mình biểu lộ có vấn đề, sầm mặt lại, lần nữa khôi phục lạnh lẽo như băng trạng thái, đi tới Lục công chúa trước mặt: “Làm sao vậy, tức thành như vậy?”"),i("br"),i("br"),n._v("“Ta...”"),i("br"),i("br"),n._v("Phi Nhi công chúa đang muốn nói, tú mỹ cũng là nhíu một cái, nhìn về phía trước mắt Lạc Thất Thất: “Đúng rồi, ngươi chuyện gì xảy ra, cao hứng như vậy?”"),i("br"),i("br"),n._v("Nàng cùng Hình Viễn tìm cách giống nhau, Lạc Thất Thất luôn luôn hỉ nộ không lộ, không phải nói hôm nay ra ngoài giảng bài luyện đan sao? Làm sao trở về cao hứng như vậy?"),i("br"),i("br"),n._v("Không phù hợp tính cách của nàng ah!"),i("br"),i("br"),n._v("“Ta? Gặp được một việc mà thôi, không có gì...”"),i("br"),i("br"),n._v("Nghĩ đến Trương sư phong độ, Lạc Thất Thất ánh mắt lộ ra tôn kính tâm ý, khóe miệng lần nữa giơ lên, tiếp tục xem tới: “Ngươi vẫn là trước tiên nói một chút ngươi đi, rốt cuộc người nào có thể đem chúng ta Lục công chúa tức thành như vậy!”"),i("br"),i("br"),n._v("“Còn không phải cái kia vô sỉ...”"),i("br"),i("br"),n._v("Nói phân nửa, Phi Nhi công chúa khóe miệng giương lên, lắc đầu: “Không đúng, dựa vào cái gì ta trước nói? Ngươi nói trước đi rốt cuộc ai bảo chúng ta vị này hỉ nộ không lộ, Thất Thất tiểu thư cao hứng như thế?”"),i("br"),i("br"),n._v("“Ta...” Lạc Thất Thất chần chờ một chút: “Như vậy đi, chúng ta cùng một chỗ nói, rốt cuộc chuyện gì xảy ra! Ai cũng không cần giấu diếm ai!”"),i("br"),i("br"),n._v("“Tốt!” Phi Nhi công chúa gật gật đầu: “Bắt đầu đi!”"),i("br"),i("br"),n._v("“Là cái hèn hạ vô sỉ, nhất biết hố người người trẻ tuổi, vô tri lỗ mãng, để cho người ta tức giận!” Phi Nhi công chúa cắn răng, khóe mắt tràn đầy hận ý."),i("br"),i("br"),n._v("“Là cái ôn tồn lễ độ, hòa nhã rộng lượng thanh niên tuấn kiệt, học thức uyên bác, khiến cho người kính ngưỡng!” Lạc Thất Thất tràn đầy sùng bái nói."),i("br"),i("br"),n._v("Convert by: ThấtDạ"),i("br"),i("br"),i("br"),i("br")]),n._v(" "),i("hr"),n._v(" "),i("p",[i("a",{attrs:{href:"./chap-631"}},[n._v("Chương trước")]),n._v("  |  "),i("a",{attrs:{href:"./chap-633"}},[n._v("Chương sau")])])])}),[],!1,null,null,null);h.default=g.exports}}]);