(window.webpackJsonp=window.webpackJsonp||[]).push([[1892],{2246:function(n,h,g){"use strict";g.r(h);var i=g(44),t=Object(i.a)({},(function(){var n=this,h=n.$createElement,g=n._self._c||h;return g("ContentSlotsDistributor",{attrs:{"slot-key":n.$parent.slotKey}},[g("h1",{attrs:{id:"chuong-625-truong-huyen-bi-phan"}},[g("a",{staticClass:"header-anchor",attrs:{href:"#chuong-625-truong-huyen-bi-phan"}},[n._v("#")]),n._v(" Chương 625: Trương Huyền bi phẫn")]),n._v(" "),g("hr"),n._v(" "),g("p",[g("br"),g("br"),n._v("“Ta...”"),g("br"),g("br"),n._v("Hồng sư sắc mặt tái xanh."),g("br"),g("br"),n._v("Không nghĩ tới đối phương tàn nhẫn như vậy, trước đem đồ vật đưa ra ngoài, xoay tay một cái liền đến cái tướng quân, úp hắn nói không ra lời."),g("br"),g("br"),n._v("Ba người khác đều đưa ra cấp bậc cực cao lễ vật, mà hắn, không có sớm chuẩn bị, muốn cầm cũng không bỏ ra nổi đến ah!"),g("br"),g("br"),n._v("Mặc dù không cầm, đối phía sau danh ngạch phân phối, không thể lên tác dụng mang tính chất quyết định, nhưng người người đều tặng quà, liền bản thân không chuẩn bị, không chỉ riêng này vị Ngô Chấn mất hứng, thái tử mặt mũi cũng sẽ cảm thấy khó coi!"),g("br"),g("br"),n._v("Huống chi, đối phương dùng ngôn ngữ lẫn nhau kích, đã giết tới trước mặt, cũng không thể rụt cổ lại, để cho người ta chế giễu chứ?"),g("br"),g("br"),n._v("“Làm sao? Ngươi sẽ không phải không chuẩn bị chứ?”"),g("br"),g("br"),n._v("“Thật hay giả? Giữa trưa ta liền nói với ngươi, Ngô sư mấy người cũng sẽ tham gia tiệc, vốn cho rằng hôm nay đến như vậy muộn, muốn đi chuẩn bị, chẳng lẽ không phải?”"),g("br"),g("br"),n._v("“Lần thứ nhất gặp mặt, làm chủ nhà, thế mà cái gì đều không có cầm, ngươi đây là cố ý a?”"),g("br"),g("br"),n._v("La Chiêu, Trần Việt, Phùng Vũ đám người thấy hắn khuôn mặt khó coi, tất cả đều cười trên nỗi đau của người khác mà nói."),g("br"),g("br"),n._v("Bọn họ ban ngày đích xác cùng Hồng sư nói rồi, có bốn người này, lại không nói qua tiệc đón tiếp sự tình, người sau đương nhiên cũng là không biết đi chuẩn bị lễ vật."),g("br"),g("br"),n._v("“Ừm?”"),g("br"),g("br"),n._v("Quả nhiên, nương theo ba người ồn ào, Diệp Tiền thái tử cũng nhướng mày."),g("br"),g("br"),n._v("Mặc dù hắn đối lễ vật chuyện này cũng không thèm để ý, nhưng những người khác đưa, liền Hồng Phong đế quốc không tiễn, cũng khó tránh khỏi để trên mặt hắn không nhịn được."),g("br"),g("br"),n._v("“Ta hôm nay vừa tới, đồ vật còn không có...”"),g("br"),g("br"),n._v("Thấy mọi người ánh mắt càng ngày càng cực nóng, đối phương tra hỏi, càng ngày càng chói tai, Hồng sư vẻ mặt đỏ lên, đang muốn nói lễ vật còn không có chuẩn bị, liền nghe đến bên người một cái thanh âm nhàn nhạt vang lên."),g("br"),g("br"),n._v("“Hồng sư lễ vật, đương nhiên đã chuẩn bị xong...”"),g("br"),g("br"),n._v("Vội vàng nhìn lại, nói chuyện chính là bên người ngồi Trương Huyền."),g("br"),g("br"),n._v("“Trương sư, ta cái gì đều không có chuẩn bị...”"),g("br"),g("br"),n._v("Thấy hắn mở miệng, đồng thời nói chuẩn bị xong, Hồng Thiển da mặt giật giật, vội vàng truyền âm."),g("br"),g("br"),n._v("Không chuẩn bị liền không chuẩn bị, cùng lắm thì chịu đến người khác chế giễu, thừa nhận mang theo, nhưng không bỏ ra nổi đến, chỉ có thể càng thêm mất mặt!"),g("br"),g("br"),n._v("Lại nói, bình thường bảo vật, ta là có một đống lớn, có thể giá trị không bằng đối phương, lấy ra còn không càng mất mặt?"),g("br"),g("br"),n._v("“Ồ? Đã chuẩn bị kỹ càng lấy ra là đúng!”"),g("br"),g("br"),n._v("Thấy cái này tu vi thấp nhất gia hỏa, thế mà mở miệng, La Chiêu hừ một tiếng, khoát tay chặn lại."),g("br"),g("br"),n._v("“Lấy ra?” Trương Huyền lắc đầu: “Chỉ sợ hiện tại không bỏ ra nổi tới đi?”"),g("br"),g("br"),n._v("“Không bỏ ra nổi đến? Ngươi có ý tứ gì? Chẳng lẽ không có đồ vật, cố ý trêu đùa thái tử cùng Ngô sư?” Nghe hắn nói không bỏ ra nổi đến, La Chiêu cười lạnh."),g("br"),g("br"),n._v("Một bên Hồng sư cũng cơ thể nhoáng một cái, mắt tối sầm lại, thiếu chút nữa ngất đi."),g("br"),g("br"),n._v("Nói có lễ vật, còn nói không bỏ ra nổi đến, đại ca, cái này nên làm gì kết thúc?"),g("br"),g("br"),n._v("Một khi thật không bỏ ra nổi đến, không chỉ Ngô sư trách tội, thái tử khẳng định cũng đắc tội, đằng sau phân phối danh ngạch, ngu nữa cũng biết kết quả."),g("br"),g("br"),n._v("Thái tử cùng Ngô sư liếc mắt nhìn nhau, cũng riêng phần mình cau mày."),g("br"),g("br"),n._v("Một cái chừng hai mươi tuổi gia hỏa, trở thành ba hạng đầu, liền rất để bọn hắn nghi ngờ, giờ phút này lại nói có đồ vật không bỏ ra nổi không đến, lập tức tràn đầy kỳ lạ, không biết hắn trong hồ lô rốt cuộc bán thuốc gì."),g("br"),g("br"),n._v("Ngay cả Tất Giang Hải cùng La Tuyền cũng không hiểu ra sao."),g("br"),g("br"),n._v("Xế chiều hôm nay bọn họ liền Hồng sư cùng một chỗ, tìm không có tìm bảo vật, biết đến rõ rõ ràng ràng, nói có, thật không bỏ ra nổi đến, chẳng phải nguy rồi?"),g("br"),g("br"),n._v("“Ta chỉ là cái tứ tinh danh sư, lại từ vạn quốc liên minh loại kia địa phương nhỏ tới, coi như cho mười cái lá gan cũng không dám trêu đùa thái tử cùng Ngô sư!”"),g("br"),g("br"),n._v("Không để ý tới mọi người ánh mắt kỳ quái, Trương Huyền nhẹ nhàng cười một tiếng, mí mắt vừa nhấc nhìn về phía La Chiêu: “Ngược lại là La sư, Hồng sư nhọc nhằn khổ sở chuẩn bị bảo vật, ngươi nhìn cũng nhìn, cầm cũng cầm, có phải hay không cái kia trả lại?”"),g("br"),g("br"),n._v("“A?”"),g("br"),g("br"),n._v("La Chiêu sững sờ, sầm mặt lại: “Ngươi nói cái gì? Cái gì có nhìn hay không? Ta lúc nào cầm Hồng sư đồ vật?”"),g("br"),g("br"),n._v("“Ôi, liền biết ngươi sẽ không thừa nhận...”"),g("br"),g("br"),n._v("Than thở một tiếng, Trương Huyền lắc đầu, hai tay đối ở giữa thái tử cùng Ngô sư ôm quyền, đứng dậy: “Đã thái tử cùng Ngô sư ở đây, đương nhiên lấy các ngươi vi tôn, ta muốn kính xin hai vị cho chúng ta Hồng sư chủ trì công đạo, không biết có thể!”"),g("br"),g("br"),n._v("“Chủ trì công đạo? Tốt, chỉ cần là công đạo, chúng ta đương nhiên cam tâm tình nguyện!”"),g("br"),g("br"),n._v("Thái tử không nói chuyện, Ngô sư cười cười, nói."),g("br"),g("br"),n._v("“Đa tạ...”"),g("br"),g("br"),n._v("Trương Huyền gật gật đầu, nhìn về phía cách đó không xa La Chiêu: “Ngô sư đã đáp ứng, muốn chủ trì công đạo, cái kia... La sư, ngươi có dám hay không cùng ta đối chất?”"),g("br"),g("br"),n._v("“Đối chất? Có gì không dám?”"),g("br"),g("br"),n._v("Thấy cái tên này lời thề son sắt, La Chiêu mặt mộng bức, lơ ngơ."),g("br"),g("br"),n._v("Hắn cùng Hồng sư mặc dù nhận biết, nhưng riêng phần mình trấn thủ một phương, hầu như không chút trao đổi qua, chớ nói chi là cầm đối phương đồ vật."),g("br"),g("br"),n._v("Cái tên này tự tin như vậy, lại để cho thái tử Ngô sư làm chứng, rốt cuộc muốn làm gì?"),g("br"),g("br"),n._v("Nói thật, không riêng gì hắn, một bên Hồng sư cũng cảm thấy có chút phát điên."),g("br"),g("br"),n._v("Ta lúc nào có đồ vật bị La Chiêu cầm đi? Ta cũng không biết, làm sao ngươi biết?"),g("br"),g("br"),n._v("“Dám là tốt rồi?”"),g("br"),g("br"),n._v("Trương Huyền cười cười, ngẩng đầu nhìn tới: “Hôm nay Hồng sư tới ngươi chỗ ở đi tìm ngươi, nhưng có việc này?”"),g("br"),g("br"),n._v("“Không sai!”"),g("br"),g("br"),n._v("La Chiêu gật đầu."),g("br"),g("br"),n._v("Hồng sư nghênh ngang đi tìm bọn họ, chuyện này chỉ cần nghe ngóng liền có thể biết, coi như muốn giấu diếm, cũng không ẩn giấu được, không cần thiết che che, lại nói, chỉ là tìm hắn, cũng không liên quan bảo vật gì đi!"),g("br"),g("br"),n._v("“Giữa các ngươi, từng định tiến hành danh sư khiêu chiến... Nhưng có việc này?”"),g("br"),g("br"),n._v("Ngay tại nghi hoặc, liền nghe đến đối phương tra hỏi tiếp tục vang lên."),g("br"),g("br"),n._v("“Cái này... Có!”"),g("br"),g("br"),n._v("Chần chờ một chút, La Chiêu gật đầu."),g("br"),g("br"),n._v("Hắn nhìn Hồng sư cuồng đánh Phùng sư, trong lòng không cam lòng, từng đưa ra qua danh sư khiêu chiến."),g("br"),g("br"),n._v("Danh sư khiêu chiến, dùng máu tươi kích hoạt huy chương, tổng bộ nhận được tin tức, không khó thẩm tra, đồng dạng không cần thiết giấu diếm."),g("br"),n._v("“Thừa nhận là tốt rồi, cũng là miễn cho ta tiếp tục giải thích!”"),g("br"),g("br"),n._v("Trương Huyền gật gật đầu, xoay người nhìn về phía thái tử cùng Ngô Chấn, lần nữa ôm quyền: “Hai vị cũng đều thấy được, hắn đã thừa nhận, sự tình cũng không cần ta nói tiếp đi!”"),g("br"),g("br"),n._v("“Không cần phải nói cái gì? Ngươi đem lời nói nói cho ta rõ...”"),g("br"),g("br"),n._v("La Chiêu vò đầu, mặt mũi phát điên."),g("br"),g("br"),n._v("Cái tên này rốt cuộc làm cái quỷ gì? Tra hỏi hỏi một nửa, nói chuyện cũng đã nói một nửa, cố làm ra vẻ huyền bí..."),g("br"),g("br"),n._v("Cái gì ta thừa nhận, không cần nói, rốt cuộc chuyện gì?"),g("br"),g("br"),n._v("Ta thừa nhận cái gì ta..."),g("br"),g("br"),n._v("Hồng sư càng là không hiểu ra sao, Trương sư não mạch kín cũng quá lớn đi, làm sao ta cũng nghe không hiểu đâu?"),g("br"),g("br"),n._v("“Ngươi vẫn là đem nói chuyện rõ ràng đi!”"),g("br"),g("br"),n._v("Diệp Tiền, Ngô Chấn cũng đối liếc mắt một cái, nhịn không được ho khan một tiếng."),g("br"),g("br"),n._v("“Tốt, đã hai vị để cho ta nói rõ ràng, vậy ta không coi là phạm thượng, lấy tứ tinh danh sư thân phận, mạo phạm ngũ tinh!”"),g("br"),g("br"),n._v("Trương Huyền nói đến đây, trong mắt tràn đầy bi phẫn cùng lửa giận, trên mặt không nói ra được oan ức cùng thương tâm: “Kỳ thật chuyện này, Hồng sư không cho ta nói, ta cũng là thực sự không cam lòng, mới muốn nói ra...”"),g("br"),g("br"),n._v("“Ah... Hả?” Hồng sư con mắt trợn tròn."),g("br"),g("br"),n._v("Ta không cho ngươi nói?"),g("br"),g("br"),n._v("Ta không cho ngươi nói cái gì?"),g("br"),g("br"),n._v("“Hồng sư trạch tâm nhân hậu, không giỏi ngôn từ, vì chú ý, Huyễn Vũ đế quốc mặt mũi, không muốn nhắc tới, nhưng làm hậu bối, ta là bây giờ nhìn không nổi nữa...”"),g("br"),g("br"),n._v("Trương Huyền thanh âm tiếp tục vang lên."),g("br"),g("br"),n._v("Nghe nói như thế, Diệp Tiền, Ngô Chấn da mặt co lại."),g("br"),g("br"),n._v("Hồng sư trạch tâm nhân không nhân hậu, chúng ta không biết, nhưng... Không giỏi ngôn từ, đây là rõ ràng mở mắt nói lời bịa đặt đi, mới vừa nói La Chiêu, Trần Việt đám người kém chút không có ngất đi cảnh tượng, chúng ta cũng không phải không thấy được."),g("br"),g("br"),n._v("“Ngươi bớt ở chỗ này đông tán dóc tây tán dóc, rốt cuộc chuyện gì? Mau nói!”"),g("br"),g("br"),n._v("Thấy hắn càng nói càng nhiều, La Chiêu thực sự nhịn không được."),g("br"),g("br"),n._v("“Vậy thì tốt, ta đã nói...”"),g("br"),g("br"),n._v("Dường như làm ra quyết định gì giống nhau, Trương Huyền một cắn răng, tràn đầy bi phẫn: “Buổi trưa hôm nay, Hồng sư đi qua tìm bọn hắn ba vị thương nghị sự tình, kết quả bọn hắn ba vị danh sư, không chỉ liên thủ đem Hồng sư đánh cho một trận, còn... Đem hắn sớm đã chuẩn bị xong bảo vật, cướp đi!”"),g("br"),g("br"),n._v("“Ta... Móa! Ngươi nói cái gì? Đánh Hồng sư một trận?”"),g("br"),g("br"),n._v("Một cái lảo đảo, La Chiêu kém chút không có thổ huyết."),g("br"),g("br"),n._v("Ta đánh ngươi muội ah, là Hồng sư không đánh với ta có được hay không? Lại nói, chúng ta lúc nào, liên thủ đánh hắn? Còn cướp bảo bối, cướp cái cọng lông ah!"),g("br"),g("br"),n._v("Coi như mở mắt nói lời bịa đặt, chúng ta cũng nói đáng tin cậy chút, đây cũng quá không đáng tin cậy đi!"),g("br"),g("br"),n._v("Ba vị danh sư, trong sân, đem một cái khác danh sư đánh... Nói thế nào lên, theo bọn lưu manh giống như?"),g("br"),g("br"),n._v("“Ngươi bây giờ khẳng định là không thừa nhận, vậy ta tiếp tục hỏi ngươi, buổi trưa hôm nay, không chỉ ngươi cùng Hồng sư tiến hành qua danh sư khiêu chiến, Phùng sư có phải hay không cũng đồng dạng tiến hành qua khiêu chiến?”"),g("br"),g("br"),n._v("Không chờ hắn giải thích, Trương Huyền tiếp tục hỏi."),g("br"),g("br"),n._v("“Cái này...” La Chiêu sững sờ: “Đúng tiến hành qua, chỉ bất quá...”"),g("br"),g("br"),n._v("Phùng sư quả thực cũng cùng Hồng sư tiến hành qua danh sư khiêu chiến, nhưng... Cái kia là bị đánh có được hay không?"),g("br"),g("br"),n._v("“Chỉ bất quá cái gì? Đã khiêu chiến là tốt rồi!”"),g("br"),g("br"),n._v("Trương Huyền tràn đầy lửa giận, nói không nên lời tức giận: “Hồng sư cùng Phùng sư đều là ngũ tinh sơ kỳ danh sư, Hóa Phàm lục trọng sơ kỳ cường giả, thực lực tương đương... Điểm ấy mọi người hẳn là không quá lớn dị nghị đi!”"),g("br"),g("br"),n._v("Mọi người gật đầu."),g("br"),g("br"),n._v("Tất cả mọi người là danh sư, cấp bậc lại tương đồng, đương nhiên sức chiến đấu không kém nhiều."),g("br"),g("br"),n._v("“Thực lực tương đương, tiến hành danh sư khiêu chiến, Phùng sư đều bị đánh thành như vậy, các ngươi cảm thấy Hồng sư có thể tốt hơn chỗ nào?”"),g("br"),g("br"),n._v("Trương sư nói tiếp."),g("br"),g("br"),n._v("Thái tử cùng Ngô Chấn đồng thời sững sờ."),g("br"),g("br"),n._v("“La sư, ta hỏi lại ngươi, Hồng sư cùng Phùng sư khiêu chiến kết thúc, ngươi có phải hay không lập tức xin danh sư khiêu chiến, mà Hồng sư cũng đã nói, nguyên khí đại thương, trong vòng mười năm không thể chiến đấu các loại lời nói?”"),g("br"),g("br"),n._v("Trương Huyền lần nữa nói."),g("br"),g("br"),n._v("“Đúng nói qua...”"),g("br"),g("br"),n._v("La Chiêu lần nữa cứng đờ, vừa định giải thích, lần nữa bị cắt đứt: “Làm ngũ tinh danh sư, Hóa Phàm lục trọng cường giả, muốn đả thương tới trình độ nào, mới có thể trong vòng mười năm không thể cùng người chiến đấu?”"),g("br"),g("br"),n._v("Trương Huyền càng nói càng kích động: “Dù vậy, cái này là La sư, còn được lý không nhường người, nhất định phải khiêu chiến, nhất định phải cùng hắn chiến đấu... Thử nghĩ một lần, Hồng sư làm sao có thể là đối thủ! Sớm đã chuẩn bị xong bảo vật, đương nhiên cũng là bị hắn cướp đi...”"),g("br"),g("br"),n._v("Nói đến đây, đã tức giận khó mà ngăn chặn, nhìn tình huống, nói tiếp, nước mắt đều có thể chảy ra."),g("br"),g("br"),n._v("“Ta xxx ngươi muội... Ta lúc nào đánh qua hắn, lúc nào đoạt lấy đồ vật, ngươi nói cho ta rõ...”"),g("br"),g("br"),n._v("La Chiêu cơ thể nhoáng một cái, kém chút không có khóc tại chỗ."),g("br"),g("br"),n._v("Rõ ràng là Hồng sư chạy tới, đánh Phùng sư một trận, làm sao thời gian nháy mắt, liền biến thành cùng Phùng sư lưỡng bại câu thương, ta thừa cơ đánh lén?"),g("br"),g("br"),n._v("Hơn nữa còn không riêng gì đánh lén, còn giật đồ..."),g("br"),g("br"),n._v("Ngươi có thể hay không nói chuyện cẩn thận?"),g("br"),g("br"),n._v("Như thế mở mắt nói lời bịa đặt, ngươi thật là một cái danh sư?"),g("br"),g("br"),n._v("Mặt đâu?"),g("br"),g("br"),n._v("(Đại ái Chiến Lang, đột nhiên nhớ tới, già nhai lúc còn trẻ, bởi vì cùng Ngô kinh mọc tương đối giống, ngoại hiệu liền kêu Ngô kinh."),g("br"),g("br"),n._v("Mấy ngày nay lần nữa nhìn thấy đồng học, hắn mặt hưng phấn mà vỗ bờ vai của ta: Ngô..."),g("br"),g("br"),n._v("Ta hớn hở ra mặt: Có phải hay không còn có năm đó phong phạm, cùng Ngô kinh rất giống."),g("br"),g("br"),n._v("Đồng học: Mạnh đạt!"),g("br"),g("br"),n._v("Ta: Em gái ngươi!"),g("br"),g("br"),n._v("Ô ô, khóc một ngày, cầu nguyệt phiếu cùng phiếu đề cử an ủi..)"),g("br"),g("br"),n._v("Convert by: ThấtDạ"),g("br"),g("br"),g("br"),g("br")]),n._v(" "),g("hr"),n._v(" "),g("p",[g("a",{attrs:{href:"./chap-633"}},[n._v("Chương trước")]),n._v("  |  "),g("a",{attrs:{href:"./chap-635"}},[n._v("Chương sau")])])])}),[],!1,null,null,null);h.default=t.exports}}]);