(window.webpackJsonp=window.webpackJsonp||[]).push([[1893],{2248:function(n,h,t){"use strict";t.r(h);var i=t(44),g=Object(i.a)({},(function(){var n=this,h=n.$createElement,t=n._self._c||h;return t("ContentSlotsDistributor",{attrs:{"slot-key":n.$parent.slotKey}},[t("h1",{attrs:{id:"chuong-626-bi-lam-bi-đien-la-chieu"}},[t("a",{staticClass:"header-anchor",attrs:{href:"#chuong-626-bi-lam-bi-đien-la-chieu"}},[n._v("#")]),n._v(" Chương 626: Bị làm bị điên La Chiêu")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("br"),t("br"),n._v("Chương 626: Bị làm bị điên La Chiêu"),t("br"),t("br"),n._v("Không riêng gì La Chiêu, Trần Việt, Phùng Vũ hai người cũng đồng thời khóe miệng giật một cái."),t("br"),t("br"),n._v("Lúc đó cảnh tượng, chỉ có ba người biết chuyện gì xảy ra, bọn họ đương nhiên cũng đều rõ ràng, cùng Phùng sư chiến đấu... Khi đó Phùng sư đã ngã sắp ngỏm rồi, xác chết giống nhau thẳng tắp chờ lấy bị đánh được không?"),t("br"),t("br"),n._v("Lúc nào lưỡng bại câu thương?"),t("br"),t("br"),n._v("Còn đánh lén, Hồng sư nói hắn nguyên khí tổn hao nhiều, La sư căn bản không động tới tay..."),t("br"),t("br"),n._v("Một bên Hồng sư, thấy Trương Huyền càng nói càng tức phẫn, cả người cũng bắt đầu hoảng hốt... Chẳng lẽ ta thật bị La sư đánh? Ta thế nào một chút ấn tượng cũng không có chứ?"),t("br"),t("br"),n._v("“Ngươi nói bậy, chúng ta có thể làm chứng, tình cảnh lúc ấy không phải như vậy, Phùng sư lúc ấy đã bị thương...”"),t("br"),t("br"),n._v("Trần Việt cũng nhịn không được nữa, vội vàng nói."),t("br"),t("br"),n._v("“Phùng sư đã bị thương? Vậy thì tốt, ta hỏi ngươi, Phùng sư bởi vì cái gì bị thương? Ai đem hắn đả thương? Đường đường ngũ tinh danh sư, bị người kích thương, không có khả năng một điểm động tĩnh đều không có chứ?”"),t("br"),t("br"),n._v("Dường như sớm biết đối phương có thể như vậy nói, Trương Huyền hất lên ống tay áo."),t("br"),t("br"),n._v("“Cái này...” Trần Việt trên đầu đổ mồ hôi: “Cũng có thể là ta nhớ lầm...”"),t("br"),t("br"),n._v("Phùng sư ngăn cản Hồng sư đám người, từ không trung rơi xuống kém chút ngã chết, vốn là mất mặt, lại thêm vi phạm đạo nghĩa... Chuyện này là khẳng định không thể tiết lộ."),t("br"),t("br"),n._v("Nếu không, truyền đi Phùng sư cũng không cần sống."),t("br"),t("br"),n._v("Đã chuyện này không thể nói, vậy như thế nào bị thương?"),t("br"),t("br"),n._v("Phải biết buổi sáng thời điểm, còn mới vừa cùng thái tử đã gặp mặt, sinh long hoạt hổ, buổi chiều liền biến thành một bãi lạn nê... Căn bản không có cách nào giải thích ah!"),t("br"),t("br"),n._v("“Thế nào, bị ta nói ra sự thật, lại lật lọng?”"),t("br"),t("br"),n._v("Trương Huyền lửa giận cuồn cuộn, giống như là muốn đem bốn phía thiêu đốt: “Coi như ngươi không lật lọng, vừa rồi các ngươi liên hợp lại, ép buộc Hồng sư cảnh tượng, Ngô sư tận mắt nhìn thấy, lúc này giúp hắn nói chuyện, cũng không tính là gì chứ? Loại này mang theo rõ ràng mang theo tình cảm sắc thái lời nói, sao có thể đáng giá người tin tưởng?”"),t("br"),t("br"),n._v("“Cái này...”"),t("br"),t("br"),n._v("Trần Việt da mặt lay động."),t("br"),t("br"),n._v("Vừa rồi liên hợp La Chiêu, ép buộc Hồng sư, Ngô sư mặc dù đang uống rượu, nhưng khẳng định đều thấy được, lúc này, muốn lấp liếm, khẳng định cũng lấp liếm không được..."),t("br"),t("br"),n._v("“Tốt, ngươi bây giờ cùng Phùng sư tốt nhất cũng không cần nói lời nói, bởi vì, các ngươi căn bản chính là cái liên minh...” Trương Huyền khoát tay chặn lại."),t("br"),t("br"),n._v("Trần Việt một cắn răng, đang muốn mở miệng, liền nghe đến thái tử thanh âm vang lên: “Tốt, các ngươi trước không cần nói, để vị tiểu huynh đệ này nói hết lời!”"),t("br"),t("br"),n._v("Kỳ thật không chỉ Ngô sư nhìn thấy, liền vừa rồi tặng lễ, ba người cố ý ép buộc Hồng sư cảnh tượng, hắn cũng nhìn ở trong mắt."),t("br"),t("br"),n._v("Rất rõ ràng, ba người đã liên thủ, đã như vậy, bọn họ nói lời, đương nhiên cũng là không thể tin."),t("br"),t("br"),n._v("“Đa tạ thái tử!”"),t("br"),t("br"),n._v("Trương Huyền gật đầu: “Vừa rồi thái tử còn chưa tới, Hồng sư bởi vì bị bọn họ đả thương, chữa thương tới chậm một hồi, liền bị bọn họ trắng trợn ép buộc, điểm ấy ngươi có thể hỏi Ngô sư, ta không có chút nào nói dối!”"),t("br"),t("br"),n._v("“Hồng sư là tới chậm một hồi!” Ngô sư gật đầu."),t("br"),t("br"),n._v("“Đa tạ Ngô sư bênh vực lẽ phải.”"),t("br"),t("br"),n._v("Trương Huyền lần nữa ôm quyền: “Bọn họ đả thương Hồng sư, cướp đi bảo vật, lại cố ý ở đây khiêu khích, mục đích đúng là muốn cho chúng ta Hồng Phong đế quốc, mất đi danh ngạch, hiện tại chứng cứ vô cùng xác thực, kính xin thái tử quyết định!”"),t("br"),t("br"),n._v("“Quyết định đơn giản... Ngươi nói cũng có chút đạo lý, nhưng... Ngươi nói La sư cướp đi Hồng sư bảo vật, vậy ta muốn hỏi một chút, rốt cuộc là loại nào bảo vật? Ngươi có thể hay không nói rõ chi tiết một lần?”"),t("br"),t("br"),n._v("Thái tử nhìn qua."),t("br"),t("br"),n._v("Có thể trở thành thái tử, đương nhiên không phải ngu ngốc hạng người, trước mắt vị thanh niên này nói, mặc dù không có mảy may sơ hở, đạo lý rõ ràng, không ít La Chiêu cũng thừa nhận, nhưng hắn vẫn là chưa tin, ba đại ngũ tinh danh sư, có thể làm được cướp đoạt sự tình tới."),t("br"),t("br"),n._v("“Đúng, ngươi nói ta cướp, ta đoạt Hồng Thiển bảo vật gì? Nếu là nói không nên lời, chính là ngươi vu hãm cao hơn một cấp cái khác danh sư, kẻ nhẹ hủy bỏ danh sư tư cách, kẻ nặng tại chỗ giết chết...”"),t("br"),t("br"),n._v("Nghe nói như thế, La Chiêu ánh mắt sáng lên, vội vàng hét."),t("br"),t("br"),n._v("Tróc gian bắt song, bắt trộm bắt bẩn, ngươi nói ta đoạt Hồng sư đồ vật, vậy thì tốt, ngươi nói ta rốt cuộc đoạt cái gì, nếu như nói không được... Nhìn ngươi làm sao bây giờ!"),t("br"),t("br"),n._v("Thân là tứ tinh danh sư, phỉ báng ngũ tinh, chỉ điều này, liền có thể để ngươi vĩnh viễn không vươn mình lên được!"),t("br"),t("br"),n._v("“Gặp qua vô sỉ, chưa thấy qua vô sỉ như vậy! Đoạt người khác đồ vật, không thừa nhận ngược lại cũng thôi, thế mà còn đại nghĩa như vậy lăng nhiên... Đã như vậy, ta liền sẽ để ngươi, tâm phục khẩu phục!”"),t("br"),t("br"),n._v("Trương Huyền hừ lạnh, quay đầu nhìn về phía một bên Hồng sư: “Khả năng còn muốn phiền phức Hồng sư một lần!”"),t("br"),t("br"),n._v("“Phiền phức ta?”"),t("br"),t("br"),n._v("Hồng sư run run một cái: “Trương sư cứ việc nói chính là...”"),t("br"),t("br"),n._v("Hắn hiện tại là đối kẻ trước mắt này, phục sát đất."),t("br"),t("br"),n._v("Cái miệng này quả thực chết có thể nói thành sống, sống có thể nói thành Thần Tiên... Hôm nay tới chậm, rõ ràng là bởi vì chờ hắn, kết quả bị nói, biến thành bản thân chữa thương..."),t("br"),t("br"),n._v("Kết hợp với trước đó hồ ngôn loạn ngữ, hết thảy thuận lý thành chương, thế mà không có chút nào không hài hòa!"),t("br"),t("br"),n._v("Ngay cả hắn người trong cuộc này, đều cảm thấy có lý có căn cứ, có thứ tự có thể theo, La Chiêu, Phùng Vũ đám người có phải hay không đoạt đồ vật của mình..."),t("br"),t("br"),n._v("Có thể chuẩn bị tốt như vậy, nói không chê vào đâu được, chẳng lẽ, trước đó để cho mình đi tìm La sư, Phùng sư đám người phiền phức, liền nghĩ đến điểm ấy?"),t("br"),t("br"),n._v("Nếu thật là như vậy... Cũng không tránh khỏi quá kinh khủng đi!"),t("br"),t("br"),n._v("“Rất đơn giản!”"),t("br"),t("br"),n._v("Trương Huyền đổi thành truyền âm."),t("br"),t("br"),n._v("“Ngươi để cho ta...”"),t("br"),t("br"),n._v("Nghe nói như thế, Hồng sư con mắt một lần trợn tròn, con ngươi co rụt lại."),t("br"),t("br"),n._v("“Ừm, đi thôi, có thể hay không vạch trần đối phương bộ mặt thật, liền dựa vào ngươi!”"),t("br"),t("br"),n._v("Trương Huyền nắm đấm xiết chặt."),t("br"),t("br"),n._v("“Đi? Đi cái quỷ a...”"),t("br"),t("br"),n._v("Nhớ tới đối phương ở bên tai mình nói lời, cơ thể nhoáng một cái, Hồng sư kém chút khóc."),t("br"),t("br"),n._v("Ngươi đây không phải là vạch trần bộ mặt của hắn, là muốn giết chết ta đi..."),t("br"),t("br"),n._v("“Yên tâm, suy nghĩ một chút Nhược Hoan công tử lúc trước cùng Tần Lỗi giao đấu, ngươi liền hiểu!”"),t("br"),t("br"),n._v("Nhìn ra hắn xoắn xuýt, Trương Huyền an ủi một câu."),t("br"),n._v(" “Nhược Hoan công tử cùng Tần Lỗi?”"),t("br"),t("br"),n._v("Hồng sư sững sờ."),t("br"),t("br"),n._v("Dựa theo tình huống bình thường, Nhược Hoan công tử loại thực lực này, là không thể nào đi vào trước tám, lúc ấy đối chiến chính là Tần Lỗi, ngay cả bản thân, đều cảm thấy cái trước không có phần thắng chút nào."),t("br"),t("br"),n._v("Kết quả... Cái tên này cầm một bát canh thịt hắt đến Tần Lỗi trên mặt, Nhược Hoan công tử liền hoàn thành nghịch tập..."),t("br"),t("br"),n._v("Suy nghĩ một chút đều cảm thấy thần kỳ, lúc này xem ra, tất nhiên là cái tên này phương pháp không thể nghi ngờ."),t("br"),t("br"),n._v("Bất quá, không thể không nói, hắn hiện tại để cho mình làm được, quả thực theo lúc trước Nhược Hoan công tử làm được có chút giống."),t("br"),t("br"),n._v("“Được rồi!”"),t("br"),t("br"),n._v("Biết đối phương cũng là vì bản thân tốt, Hồng sư chần chờ một chút, một cắn răng, đứng dậy, nhanh chân hướng đối diện La sư đi tới."),t("br"),t("br"),n._v("“Ngươi lại làm cái quỷ gì?”"),t("br"),t("br"),n._v("Thấy cái tên này, không nói bản thân đoạt bảo bối gì, ngược lại làm cho Hồng sư đi tới trước mặt, La Chiêu tràn đầy nghi hoặc."),t("br"),t("br"),n._v("“Có chuyện muốn phiền phức La sư, mong rằng phối hợp!”"),t("br"),t("br"),n._v("Trương Huyền bưng chén rượu lên, tự mình uống rượu, đối với hắn tra hỏi, không thèm để ý, mà Hồng sư thì đứng tại trước mặt, ôm quyền nói, thái độ thành khẩn."),t("br"),t("br"),n._v("“Phối hợp, ta vì sao phải phối hợp?” La Chiêu cắn răng."),t("br"),t("br"),n._v("Các ngươi đều đem đen nói thành trắng, ta vì sao còn phải phối hợp các ngươi, chờ lấy bị vu oan ư?"),t("br"),t("br"),n._v("“Nếu như không phối hợp, chính là chột dạ, phía sau cũng không cần tiếp tục. Yên tâm đi, thật không thể giả, giả không thể thật!” Hồng sư nói."),t("br"),t("br"),n._v("“Tốt, ta phối hợp, ngươi để cho ta làm sao phối hợp?”"),t("br"),t("br"),n._v("Nghe được đối phương lời này, biết thật muốn không phối hợp, chính là chột dạ, không dám đối mặt, đến lúc đó, thật sự là trăm miệng khó cãi, La Chiêu một cắn răng, khẽ nói."),t("br"),t("br"),n._v("Phối hợp liền phối hợp, ta liền nhìn xem ngươi có thể chơi ra hoa gì tới."),t("br"),t("br"),n._v("Dù sao chỉ cần ngươi nói không nên lời ta đoạt bảo bối gì, không quản lý do nhiều hơn nữa, ta đều nắm vững thắng lợi."),t("br"),t("br"),n._v("“Đứng lên, hai tay chắp sau lưng, không nên động!”"),t("br"),t("br"),n._v("Hồng sư vẫy vẫy tay."),t("br"),t("br"),n._v("“A!” La Chiêu mặc dù khí suýt nổ tung, vẫn đứng lên, hai tay chắp sau lưng."),t("br"),t("br"),n._v("“Ôi, đúng, liền cái tư thế này, duy trì tốt, đừng nhúc nhích...”"),t("br"),t("br"),n._v("Hồng sư lại uốn nắn một lần đối phương dáng dấp, tìm cái bản thân tư thế thoải mái nhất, đột nhiên bàn tay nâng lên, một bàn tay quất tới."),t("br"),t("br"),n._v("Đùng!"),t("br"),t("br"),n._v("Quất vào trên mặt, thanh thúy vang dội."),t("br"),t("br"),n._v("“Ự... C?”"),t("br"),t("br"),n._v("Thái tử mộng."),t("br"),t("br"),n._v("Ngô sư choáng váng."),t("br"),t("br"),n._v("Toàn bộ gian phòng người đều ngốc tại chỗ."),t("br"),t("br"),n._v("Là cái này... Ngươi nói phối hợp?"),t("br"),t("br"),n._v("Duy trì tư thế, để ngươi đánh?"),t("br"),t("br"),n._v("“Thao mẹ ngươi, Hồng Thiển, ta muốn giết ngươi...” La Chiêu sửng sốt một chút, rốt cục kịp phản ứng."),t("br"),t("br"),n._v("Bị một bàn tay đánh đầu choáng váng, răng mất hai ba cái, lại không phát điên, thật sự choáng váng."),t("br"),t("br"),n._v("Để cho ta đứng vững, duy trì tư thế, còn tưởng rằng là muốn làm gì, kết quả chính là vì chuẩn bị kỹ càng, quất ta một bạt tai..."),t("br"),t("br"),n._v("Hồng Thiển, ta La Chiêu không đội trời chung với ngươi..."),t("br"),t("br"),n._v("La Chiêu hoàn toàn điên rồi, cũng mặc kệ trên đài thái tử cùng Ngô sư, bàn tay giơ lên, đối Hồng Thiển liền bổ tới."),t("br"),t("br"),n._v("Phần phật!"),t("br"),t("br"),n._v("Chân khí như đao, tiếng gió phần phật, còn chưa tới đến trước mặt, liền đem không khí xé rách bóp méo."),t("br"),t("br"),n._v("Hóa Phàm lục trọng danh sư, bất kể kiến thức hay là thực lực, đều đạt đến cảnh giới cực cao, toàn lực ra tay, một tòa núi lớn đều có thể thời gian nháy mắt đánh thành bột mịn, huống chi người."),t("br"),t("br"),n._v("Hô!"),t("br"),t("br"),n._v("Một chiêu công kích, đánh vào không trung, định thần nhìn lại, chỉ thấy vừa rồi quất hắn một bạt tai Hồng sư, chẳng biết lúc nào đã về tới chỗ ngồi của mình, bưng lên một chén rượu ngon, mặt mũi khoan thai, thật giống như chuyện vừa rồi, không có phát sinh giống nhau."),t("br"),t("br"),n._v("“Ngươi chết...”"),t("br"),t("br"),n._v("Con mắt đỏ lên, La Chiêu gào thét."),t("br"),t("br"),n._v("Từ khi trở thành danh sư đến nay, lúc nào nhận qua lớn như thế vũ nhục, trước mặt mọi người bị người dọn xong tư thế kề bên đánh..."),t("br"),t("br"),n._v("Còn có so đây càng hố sự tình ư?"),t("br"),t("br"),n._v("Cơ thể nhoáng một cái, liền muốn tiến lên, cùng vị này vũ nhục hắn Hồng Thiển, quyết chiến sinh tử, liền nghe đến lời mới vừa nói thanh niên, nghĩa chính ngôn từ tiếng quát vang lên."),t("br"),t("br"),n._v("“Đủ rồi, La sư! Nơi này là thái tử hành cung, là yến hội sảnh, ngươi lại muốn động thủ, lại muốn giết người, là ý gì?”"),t("br"),t("br"),n._v("La Chiêu kém chút không có khóc lên."),t("br"),t("br"),n._v("Ngươi cũng biết đây là thái tử hành cung?"),t("br"),t("br"),n._v("Biết còn để Hồng Thiển tiện nhân này, quất ta một bàn tay? Đánh ta răng mất đầy đất?"),t("br"),t("br"),n._v("Rõ ràng bị thương chính là ta, ngươi nói như thế lẽ thẳng khí hùng... Nghĩa chính ngôn từ, thật giống như đánh người chính là ngươi giống nhau..."),t("br"),t("br"),n._v("Khí suýt nổ tung, La Chiêu quay đầu nhìn về phía Ngô sư cùng thái tử: “Hồng Thiển vũ nhục ta, kính xin hai vị cho phép, để cho ta cùng hắn quyết nhất tử chiến...”"),t("br"),t("br"),n._v("Lời còn chưa dứt, lần nữa bị cách đó không xa thanh niên cắt ngang."),t("br"),t("br"),n._v("“Thái tử, Ngô sư, các ngươi không phải mới vừa phải biết, hắn cướp đi Hồng sư bảo vật gì ư? Vì để tránh cho xuất hiện phiền toái không cần thiết, để người khác cho là ta ăn nói bừa bãi, hiện tại liền đem nó viết ra!”"),t("br"),t("br"),n._v("Nói xong cũng không nói nhảm, cổ tay khẽ đảo, trước mặt thêm ra văn phòng tứ bảo (bút, mực, giấy, nghiên), dính vào mực nước, rồng bay phượng múa viết."),t("br"),t("br"),n._v("Convert by: ThấtDạ"),t("br"),t("br"),t("br"),t("br")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("a",{attrs:{href:"./chap-634"}},[n._v("Chương trước")]),n._v("  |  "),t("a",{attrs:{href:"./chap-636"}},[n._v("Chương sau")])])])}),[],!1,null,null,null);h.default=g.exports}}]);