(window.webpackJsonp=window.webpackJsonp||[]).push([[1895],{2251:function(n,h,t){"use strict";t.r(h);var c=t(44),i=Object(c.a)({},(function(){var n=this,h=n.$createElement,t=n._self._c||h;return t("ContentSlotsDistributor",{attrs:{"slot-key":n.$parent.slotKey}},[t("h1",{attrs:{id:"chuong-628-danh-ngach-phan-phoi-quy-tac"}},[t("a",{staticClass:"header-anchor",attrs:{href:"#chuong-628-danh-ngach-phan-phoi-quy-tac"}},[n._v("#")]),n._v(" Chương 628: Danh ngạch phân phối quy tắc")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("br"),t("br"),n._v("“Ta... Không lời nào để nói!”"),t("br"),t("br"),n._v("Nghĩ nửa ngày, La Chiêu than thở một tiếng."),t("br"),t("br"),n._v("Hắn là thực sự nghĩ không ra đến cùng chuyện gì xảy ra."),t("br"),t("br"),n._v("Trong hồ lô, là trước sáng sớm để người tại Bồ Phương trai mua , thế nào cũng không nghĩ đến, biến thành thánh dược chữa thương!"),t("br"),t("br"),n._v("Có thể nói, hiện tại là hết đường chối cãi, thế nào đều giải thích không rõ."),t("br"),t("br"),n._v("Nếu như nói không phải đoạt đồ của người ta, vì cái gì đối phương, biết mình trữ vật giới chỉ bên trong sẽ có cái này có thể cho người ta chữa thương rượu thuốc?"),t("br"),t("br"),n._v("Thừa nhận đoạt... Lại cảm thấy tràn đầy oan ức."),t("br"),t("br"),n._v("Phải biết, danh sư ở giữa chú ý đến thân phận, hắn nhiều nhất nói ép buộc hai lần, liền Hồng sư động đều không động tới... Lúc nào đoạt lấy đồ vật ah!"),t("br"),t("br"),n._v("Hơn nữa còn là trân quý như thế rượu thuốc!"),t("br"),t("br"),n._v("“Ngươi cũng không cần cảm thấy oan ức, nếu như hoài nghi chúng ta vu ngươi, hôm nay giúp Hồng sư trị liệu xong thương thế, còn thừa lại một chén rượu thuốc, hiện tại có thể biểu hiện ra cho ngươi xem một chút!”"),t("br"),t("br"),n._v("Biết hắn suy nghĩ trong lòng, Trương Huyền nhàn nhạt nói một tiếng, cổ tay khẽ đảo, lòng bàn tay thêm ra một một ly rượu, trong đó rượu mùi thơm khắp nơi, bốn phía phiêu tán."),t("br"),t("br"),n._v("“Ai nguyện ý thử một chút chén rượu này hiệu quả?”"),t("br"),t("br"),n._v("Nhìn về phía chung quanh."),t("br"),t("br"),n._v("“Ta tới...” Phùng Vũ đứng dậy, vẻ mặt kiên định."),t("br"),t("br"),n._v("“A, Tất sư, vẫn là ngươi đi!” Trương Huyền cúi đầu nói."),t("br"),t("br"),n._v("“...” Phùng Vũ."),t("br"),t("br"),n._v("“Khụ khụ!”"),t("br"),t("br"),n._v("Đang uống rượu Tất Giang Hải, kém chút bị rượu sặc chết, đành phải đứng dậy, vẻ mặt bất đắc dĩ: “Ta tự mình tới đi!”"),t("br"),t("br"),n._v("Nói xong, chập ngón tay làm kiếm, một đạo chân khí bắn ra, trên cánh tay trong nháy mắt vạch ra một đạo vết thương, máu tươi chảy xuôi."),t("br"),t("br"),n._v("Nhẹ nhàng cười một tiếng, Trương Huyền đem rượu trong ly đổ tới."),t("br"),t("br"),n._v("Xì xì xì!"),t("br"),t("br"),n._v("Mấy hơi thở sau đó, vết thương hoàn hảo như lúc ban đầu, không có một chút dấu vết, cùng trước đó trên bàn cái kia bình thuốc rượu hiệu quả hoàn toàn tương đồng."),t("br"),t("br"),n._v("“Thái tử điện hạ, Ngô sư, cái này bọn họ có thể đã chứng minh đi!” Làm xong những này, Trương Huyền lần nữa ôm quyền."),t("br"),t("br"),n._v("“Có thể chứng minh!”"),t("br"),t("br"),n._v("Diệp Tiền gật đầu."),t("br"),t("br"),n._v("Chuẩn xác biết đối phương trữ vật giới chỉ bên trong đồ vật, còn lấy ra tương đồng công hiệu, đã chứng cứ vô cùng xác thực."),t("br"),t("br"),n._v("“Thân là ngũ tinh danh sư, cướp đoạt người khác bảo vật, chiếm làm của riêng, quả thực tội không thể tha!”"),t("br"),t("br"),n._v("Hất lên ống tay áo, Diệp Tiền lần nữa nhìn về phía La Chiêu, trong mắt tràn đầy tức giận cùng chán ghét."),t("br"),t("br"),n._v("Trước đó đối vị này các nước chư hầu danh sư, còn có chút hảo cảm, không nghĩ tới vậy mà làm ra loại sự tình này, để cho người ta trơ trẽn."),t("br"),t("br"),n._v("“Hồng sư, Trương sư, Huyễn Vũ đế quốc xuất hiện dạng này danh sư, là trách nhiệm của ta, không biết... Các ngươi muốn xử lý như thế nào?”"),t("br"),t("br"),n._v("Thông qua vừa rồi Hồng sư xưng hô, cũng biết người thanh niên này họ Trương."),t("br"),t("br"),n._v("“Mặc dù hắn cướp đi ta đồ vật, nhưng hôm nay là điện hạ vì quý khách đón tiếp, như thế vui mừng thời điểm, ta cũng không muốn gây rối. Chỉ cần cái kia hồ lô kia rượu thuốc giao ra, liền lại đi truy cứu!”"),t("br"),t("br"),n._v("Hồng sư ôm quyền."),t("br"),t("br"),n._v("“Ta cùng Hồng sư ý tưởng đồng dạng!” Trương Huyền cũng gật đầu."),t("br"),t("br"),n._v("“Không truy cứu?”"),t("br"),t("br"),n._v("Mọi người tất cả đều ngẩn ngơ."),t("br"),t("br"),n._v("Vốn cho rằng, La Chiêu như thế bức bách, hiện tại có thái tử làm chủ, Hồng sư khẳng định sẽ để cho hắn khó xử, thậm chí vứt bỏ danh sư tư cách, nằm mơ đều không nghĩ tới, chỉ cần đưa trước rượu thuốc, liền không truy cứu..."),t("br"),t("br"),n._v("Đây là cái gì độ lượng?"),t("br"),t("br"),n._v("Nhìn xem, đều là các nước chư hầu ngũ tinh danh sư, ngươi mỗi ngày muốn hại chết nhân gia, để người ta mất mặt, mà nhân gia rõ ràng chiếm lý, nhưng không kiêu ngạo không tự ti, ung dung rộng lượng!"),t("br"),t("br"),n._v("Ngay cả thái tử, Ngô sư, lần nữa nhìn về phía Hồng sư, Trương Huyền, không khỏi coi trọng mấy phần."),t("br"),t("br"),n._v("“La sư, ngươi đã nghe chưa? Bình thuốc này rượu, ngươi là trả lại, vẫn là tiếp tục bản thân giữ lại?”"),t("br"),t("br"),n._v("Thái tử nhìn qua."),t("br"),t("br"),n._v("“Ta... Trả lại!” La Chiêu cũng có chút ngẩn người."),t("br"),t("br"),n._v("Vốn cho rằng đối phương vu hãm thành công, khẳng định sẽ thừa thắng xông lên, để hắn thân bại danh liệt, nằm mơ đều không nghĩ tới, nhân gia căn bản không có ý tứ kia."),t("br"),t("br"),n._v("“Đã ngươi đem bình thuốc này dịch trả lại, Ngô sư, thứ này liền xem như chúng ta Hồng Phong đế quốc bên này lễ gặp mặt!”"),t("br"),t("br"),n._v("Hồng sư liền ôm quyền."),t("br"),t("br"),n._v("“Cái này...”"),t("br"),t("br"),n._v("Ngô chấn chần chờ một chút, lập tức trong mắt lóe lên vẻ kích động, nhẹ gật đầu: “Tại hạ liền thay Lục công chúa, đa tạ Hồng sư!”"),t("br"),t("br"),n._v("Đây chính là thánh dược chữa thương, bất kỳ cái gì vết thương da thịt, chỉ cần nhỏ lên một chút, liền có thể hoàn hảo không chút tổn hại, có thứ này, coi như về sau bị thương, cũng không cần phải sợ!"),t("br"),t("br"),n._v("“Không có gì!”"),t("br"),t("br"),n._v("Hồng Thiển ôm quyền ngồi xuống, lần nữa quay đầu nhìn về phía cách đó không xa thanh niên, trong mắt ý tò mò càng ngày càng nồng đậm."),t("br"),t("br"),n._v("Vừa rồi La Chiêu, Trần Việt, Phùng Vũ ba người liên thủ bức bách, hắn đã làm tốt mất mặt xấu hổ dự định, nằm mơ đều không nghĩ tới, Trương sư vừa ra tay, lập tức ngăn cơn sóng dữ."),t("br"),t("br"),n._v("Không chỉ có như thế, còn đem La Chiêu làm kém chút sụp đổ."),t("br"),t("br"),n._v("La Chiêu đoạt không có đoạt hắn đồ vật, hắn biết đến rõ rõ ràng ràng, thế nào... Nói nói, sự thật vô cùng xác thực, đối phương không cách nào cãi lại?"),t("br"),t("br"),n._v("“Trương sư, chuyện vừa rồi...”"),t("br"),n._v("Thực sự nhịn không được, truyền âm qua."),t("br"),t("br"),n._v("“A, một chút che giấu tai mắt người trò vặt mà thôi, lão sư dạy, không đủ nhất sái!” Trương Huyền gật đầu."),t("br"),t("br"),n._v("Chuyện vừa rồi, rất rõ ràng là la, trần, Phùng Tam người liên thủ làm cục, muốn cho Hồng sư nhảy vào trong đó, mất mặt xấu hổ, thật muốn làm như vậy, bọn hắn Hóa Thanh trì danh ngạch, cơ bản vô duyên."),t("br"),t("br"),n._v("Cho nên, mới nhịn không được ra tay."),t("br"),t("br"),n._v("Còn cái kia có thể trị thương rượu thuốc, Hồng sư dẫn đến La Chiêu thi triển võ kỹ, Thiên Đạo thư viện nhìn ra hắn rượu ngon như mạng thiếu hụt, phỏng đoán trữ vật giới chỉ bên trong chứa đựng rượu ngon, cũng là trở nên mười phần đơn giản."),t("br"),t("br"),n._v("Kỳ thật, hồ lô kia bên trong, quả thực là bình thường rượu, có thể chữa thương, là bởi vì hắn lay động bầu rượu thời điểm, rót vào mấy đạo chân khí."),t("br"),t("br"),n._v("Thiên Đạo chân khí vô hình vô chất, trà trộn vào gay mũi trong rượu, lục tinh danh sư đều không phát hiện được, chớ nói chi là mọi người."),t("br"),t("br"),n._v("Đằng sau Hồng sư bỏ qua cho đối phương, tự nhiên cũng là hắn ý tứ."),t("br"),t("br"),n._v("Khai trừ một cái ngũ tinh danh sư, can hệ trọng đại, thật muốn truy cứu, tất nhiên dẫn tổng bộ người tới, vừa rồi lời giải thích, lừa gạt một cái coi như cũng được, kỹ càng loại bỏ lời nói, rất dễ dàng lộ ra chân ngựa."),t("br"),t("br"),n._v("Nhất là dược dịch nguồn gốc, cũng không thể lại để cho “Dương sư” ra đi?"),t("br"),t("br"),n._v("Còn không bằng không đi truy cứu, thuận tiện cho thái tử đám người lưu cái ấn tượng tốt."),t("br"),t("br"),n._v("Vật cực tất phản, thịnh cực tất suy, đã chiếm tiện nghi, nhớ lấy giặc cùng đường chớ đuổi."),t("br"),t("br"),n._v("Biết là Dương sư dạy, Hồng sư gãi gãi đầu không hỏi thêm nữa."),t("br"),t("br"),n._v("Liên lụy đến Dương sư loại này cấp bậc cao danh sư, là hắn không thể nào hiểu được, cùng hắn xoắn xuýt, còn không bằng không nghĩ nhiều nữa."),t("br"),t("br"),n._v("..."),t("br"),t("br"),n._v("“Cảm tạ chư vị lễ vật, hôm nay loại trừ cho Ngô sư đám người đón tiếp, còn có một việc, chắc hẳn không cần ta nhiều lời, mọi người cũng đều hiểu!”"),t("br"),t("br"),n._v("Biết chuyện này truy cứu tiếp, sẽ tại học trưởng trước mặt mất mặt mũi, Diệp Tiền thái tử nhìn quanh một tuần, nói sang chuyện khác."),t("br"),t("br"),n._v("“Rõ ràng!”"),t("br"),t("br"),n._v("Nghe được màn kịch quan trọng tới, mọi người tất cả đều ngồi nghiêm chỉnh, vẻ mặt nghiêm túc."),t("br"),t("br"),n._v("Đêm nay tiệc tối, đến một lần đón tiếp, để mọi người và mấy vị danh sư học viện đám học trưởng bọn họ quen biết một chút; Thứ hai, xác định danh ngạch một lần nữa phân phối."),t("br"),t("br"),n._v("Đón tiếp... Chỉ Ngô sư một cái, lại náo ra nhiều chuyện như vậy, khẳng định tiến hành không nổi nữa, còn không bằng trực tiếp phân chia danh ngạch, có lẽ còn có thể vãn hồi chút mặt mũi."),t("br"),t("br"),n._v("“Hóa Thanh trì, là ta Huyễn Vũ đế quốc kiến quốc cơ sở, đối Hợp Linh cảnh cực kỳ trở lên cường giả, có cực lớn hiệu quả. Chư vị trải qua tầng tầng khảo hạch, đi tới Đế đô, dĩ nhiên chính là vì có thể tiến vào bên trong tiềm tu, từ đó tu vi tiến nhanh!”"),t("br"),t("br"),n._v("Diệp Tiền cười cười: “Chỉ bất quá, năm nay cùng những năm qua không giống, cần từ chư vị nơi này xuất ra ba cái danh ngạch!”"),t("br"),t("br"),n._v("“Ta biết những này danh ngạch, đối với chư vị mười phần quan trọng, cho nên xin các ngươi tới, thương nghị một chút, định cái dạng gì quy tắc, tại không thương tổn ôn hòa tình huống dưới, thuận lợi tuyển ra, cũng không chậm trễ ngày mai Hóa Thanh trì mở ra.”"),t("br"),t("br"),n._v("“Hết thảy nghe theo điện hạ phán quyết!” Mọi người đồng loạt ôm quyền."),t("br"),t("br"),n._v("“Việc quan hệ mọi người danh ngạch, ta cũng không dám làm chuyên, vẫn là cùng một chỗ suy nghĩ một chút, có gì tốt biện pháp, nói ra, cùng một chỗ thương thảo!” Diệp Tiền nói."),t("br"),t("br"),n._v("“Thái tử điện hạ, Hồng Phong, Phong Nguyên, Tiềm Phong, Trục Nguyệt bốn cái các nước chư hầu, gánh vác ba cái danh ngạch, khẳng định có người có thể chia lên, có người phân không lên, có chút không công bằng. Ta có cái đề nghị, không biết có nên nói hay không!”"),t("br"),t("br"),n._v("Trầm mặc một lát, Trần Việt đứng dậy, khi mở miệng trước."),t("br"),t("br"),n._v("“Nói nghe một chút!” Diệp Tiền nhìn qua."),t("br"),t("br"),n._v("“Rất đơn giản, đã mỗi cái các nước chư hầu đều chỉ có ba cái danh ngạch, ta nhìn không bằng lấy các nước chư hầu làm đơn vị, bốn quốc chi ở giữa, đến cái tỷ thí, bài danh sau cùng... Trực tiếp mất đi tư cách!”"),t("br"),t("br"),n._v("Trần Việt nói ra ý nghĩ của mình: “Còn như thế nào tranh tài, vậy liền đơn giản, hôm nay thi đấu ba hạng đầu đều đến, liền để giữa bọn hắn tỷ thí một chút, ba cục hai thắng! Nói cách khác, mỗi cái các nước chư hầu, đem riêng phần mình ba hạng đầu phái ra tỷ thí, mỗi người so một hồi, thắng liền hai trận, coi như các nước chư hầu chiến thắng... Thua, chỉ có thể nhận thua!”"),t("br"),t("br"),n._v("“Trần sư phương pháp, ta đồng ý! Danh ngạch dắt dắt bọn họ bản thân lợi ích, tỷ thí, tự nhiên càng thêm ra sức, hơn nữa, coi như thua, cũng chỉ là bản thân tài nghệ không bằng người, oán không được riêng phần mình các nước chư hầu!”"),t("br"),t("br"),n._v("Phùng Vũ cũng mở miệng."),t("br"),t("br"),n._v("“Ta cũng đồng ý!”"),t("br"),t("br"),n._v("La Chiêu gật đầu phụ họa."),t("br"),t("br"),n._v("“Dùng cố gắng của mình tranh đoạt danh ngạch, phù hợp Danh Sư đường không ngừng vươn lên tinh thần!” Ngô chấn gật đầu."),t("br"),t("br"),n._v("“Cái kia tốt!”"),t("br"),t("br"),n._v("Suy nghĩ một chút, Diệp Tiền cũng gật đầu đồng ý."),t("br"),t("br"),n._v("Đối phương đề nghị này, nói thật, cũng không tệ lắm."),t("br"),t("br"),n._v("Lấy các nước chư hầu làm đơn vị, lại thêm có thể kích thích người tu luyện đoàn kết cùng một chỗ, cùng chung mối thù tinh thần, để hắn tốt hơn cố gắng."),t("br"),t("br"),n._v("“Bất quá, đã tỷ thí, cũng nên có nội dung, không biết tại tỷ thí nội dung bên trên, chư vị có ý nghĩ gì?”"),t("br"),t("br"),n._v("Diệp Tiền tiếp tục hỏi."),t("br"),t("br"),n._v("“Cái này ta cũng đã làm cân nhắc!”"),t("br"),t("br"),n._v("Trần Việt cười cười, tiếp tục nói: “Danh sư ở giữa tỷ thí, thi đấu thích hợp nhất. Bất quá, loại này tỷ thí, liên quan các phương mỗi bên mặt, vô số nội dung, một cái đơn giản tranh đoạt danh ngạch mà thôi, không cần phức tạp như vậy. Không bằng, liền tỷ thí tu vi đi! Hóa Thanh trì đối thực lực có trợ giúp, muốn quyết định bởi danh ngạch, tự nhiên cần mạnh nhất võ lực, trực tiếp nhất, cũng đơn giản nhất!”"),t("br"),t("br"),n._v("“Đúng vậy a, luận võ tốt nhất!”"),t("br"),t("br"),n._v("“Cũng tiết kiệm phiền phức.”"),t("br"),t("br"),n._v("Phùng Vũ, La Chiêu thuận miệng nghênh hợp."),t("br"),t("br"),n._v("“A!”"),t("br"),t("br"),n._v("Nghe nói như thế, Hồng sư cùng Trương Huyền thì liếc mắt nhìn nhau, giờ mới hiểu được mục đích của bọn hắn."),t("br"),t("br"),n._v("Đối phương ba cái các nước chư hầu ba hạng đầu, đều phục dụng Hợp Linh đan, tu vi tiến nhanh, thực lực yếu nhất, đều so la tuyền đám người muốn một cái cấp bậc, thật muốn tỷ võ, làm sao có thể là đối thủ?"),t("br"),t("br"),n._v("Coi như Trương Huyền quái thai này không sợ, nhưng mỗi người chỉ có thể so một hồi, ba cục hai thắng... La tuyền cùng Tất Giang Hải thua, đồng dạng không có cách nào."),t("br"),t("br"),n._v("Xem ra đối mới biết chặn đường không được, suy nghĩ biện pháp này."),t("br"),t("br"),n._v("Càng là vô sỉ!"),t("br"),t("br"),n._v("Convert by: ThấtDạ"),t("br"),t("br"),t("br"),t("br")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("a",{attrs:{href:"./chap-636"}},[n._v("Chương trước")]),n._v("  |  "),t("a",{attrs:{href:"./chap-638"}},[n._v("Chương sau")])])])}),[],!1,null,null,null);h.default=i.exports}}]);