(window.webpackJsonp=window.webpackJsonp||[]).push([[1896],{2250:function(n,h,t){"use strict";t.r(h);var g=t(44),i=Object(g.a)({},(function(){var n=this,h=n.$createElement,t=n._self._c||h;return t("ContentSlotsDistributor",{attrs:{"slot-key":n.$parent.slotKey}},[t("h1",{attrs:{id:"chuong-629-đon-phuong-bi-đanh"}},[t("a",{staticClass:"header-anchor",attrs:{href:"#chuong-629-đon-phuong-bi-đanh"}},[n._v("#")]),n._v(" Chương 629: Đơn phương bị đánh")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("br"),t("br"),n._v("“Hóa Thanh trì, đối hồn lực có cực trợ giúp lớn, có thể tăng thêm tu vi, luận võ tuyển bạt lời nói, quả thực thích hợp nhất!”"),t("br"),t("br"),n._v("Suy tư một lát, Diệp Tiền gật gật đầu: “Tốt, liền tỷ thí tu vi, lấy các nước chư hầu làm đơn vị, ba cục hai thắng! Bất quá, chúng ta luận bàn, dĩ hòa vi quý, điểm đến là dừng, chỉ cần một phương nhận thua, liền như vậy coi như không có gì, không cần thiết phân ra sinh tử...”"),t("br"),t("br"),n._v("“Thái tử điện hạ, phương pháp này, ta không đồng ý!”"),t("br"),t("br"),n._v("Hồng sư đứng dậy."),t("br"),t("br"),n._v("Đối phương tam đại thế lực, rất rõ ràng nghĩ kỹ biện pháp, tại ép buộc bọn hắn, thật muốn đáp ứng, chẳng phải nhất định thua?"),t("br"),t("br"),n._v("Mặc dù vừa rồi buông tha La Chiêu, không có tiếp tục truy cứu, xem như lưu lại điểm ân tình."),t("br"),t("br"),n._v("Nhưng đối với đối phương liên minh tới nói, căn bản không quản dùng, dù sao liên quan danh sư thi đấu phúc lợi, ai đều khó có khả năng bởi vì tình cảm riêng tư, làm cho cả các nước chư hầu gánh vác bị thua thanh danh."),t("br"),t("br"),n._v("Lại nói, La Chiêu lại không ngốc, biết rất rõ ràng bản thân không có đoạt lấy đồ vật, là bị đối phương vu hãm, đừng nói cảm ơn, khẳng định còn suy nghĩ thế nào trả thù lại... Cơ hội như vậy, tự nhiên sẽ không bỏ qua."),t("br"),t("br"),n._v("Phùng Vũ lại càng không cần phải nói... Bị đánh hoàn toàn thay đổi, tâm lý đã sớm kìm nén thở ra một hơi."),t("br"),t("br"),n._v("Bọn hắn đều mài đao xoèn xoẹt, Trần Việt làm một cái duy nhất không bị tổn thương, làm sao có thể lùi bước, tự nhiên gánh vác lên quyền lên tiếng, đoán chừng những này, là trước khi đến liền thương nghị tốt."),t("br"),t("br"),n._v("“Ừm?” Diệp Tiền nhìn qua."),t("br"),t("br"),n._v("“Điện hạ cũng nhìn thấy, bọn hắn đều sử dụng Hợp Linh đan, tu vi tăng nhiều, mà chúng ta vừa tới Đế đô, không có cơ hội mua sắm, tu vi so với bọn hắn muốn thấp, tỷ võ lời nói, Hồng Phong đế quốc bên này, chỉ sợ chiến thắng tỷ lệ rất nhỏ!”"),t("br"),t("br"),n._v("Hồng sư cũng không kiêng kị, nói thẳng."),t("br"),t("br"),n._v("Cái này cũng không có gì có thể kiêng kỵ, đối phương có Hợp Linh đan trợ giúp, phía bên mình không có, so ra kém cũng không tính là gì."),t("br"),t("br"),n._v("“Danh sư, cùng những nghề nghiệp khác người không giống, tu vi mặc dù không là trọng yếu nhất, nhưng cũng là nhất định, tu vi không đủ, hết thảy đều là lâu đài trên không! Các ngươi tới chậm, không tìm được Hợp Linh đan, không trách được chúng ta đi!”"),t("br"),t("br"),n._v("Trần Việt khẽ nói."),t("br"),t("br"),n._v("“Hóa Thanh trì, đối tu vi có ích lợi, tu vi thấp, không phải lấy cớ!”"),t("br"),t("br"),n._v("Phùng Vũ chen vào nói."),t("br"),t("br"),n._v("“Hồng sư, buổi trưa hôm nay, đến cùng chuyện gì xảy ra, chúng ta trong lòng hai người đều rõ ràng, chuyện này không nói cũng được, cái này ân oán, chúng ta có thể về sau lại tính toán. Bất quá... Để bọn hậu bối tỷ thí tu vi, quyết đoán danh ngạch, là công bình nhất, thích hợp, nếu như cảm thấy không thắng được, ta nhìn các ngươi liền trực tiếp nhận thua được rồi!”"),t("br"),t("br"),n._v("La Chiêu cũng lạnh lùng nhìn qua."),t("br"),t("br"),n._v("Ba người cùng chung mối thù, lập tức đem Hồng sư bức đến tuyệt cảnh."),t("br"),t("br"),n._v("“Nhận thua?”"),t("br"),t("br"),n._v("Ngay tại Hồng sư không biết trả lời như thế nào thời điểm, Trương Huyền mở miệng lần nữa: “Điều đó không có khả năng! Muốn nhận thua, cũng là các ngươi!”"),t("br"),t("br"),n._v("“Không sai, cũng là các ngươi nhận thua!”"),t("br"),t("br"),n._v("Biết người thanh niên này có qua trí tuệ con người cùng ý tưởng, so với hắn đều có chủ kiến, Hồng sư gật đầu ứng hòa."),t("br"),t("br"),n._v("“Chúng ta nhận thua? Dựa vào cái gì, quy tắc chúng ta nói, ngươi hệ so sánh cũng không dám so, không nhận thua còn muốn làm cái gì?” Trần Việt cười lạnh."),t("br"),t("br"),n._v("“Quy tắc các ngươi nói cái gì liền cái gì?” Trương Huyền mí mắt vừa nhấc."),t("br"),t("br"),n._v("“Đúng, quy tắc, lại không phải là các ngươi nhà!” Hồng sư cũng kịp phản ứng, tiếp tục đi theo hát đệm."),t("br"),t("br"),n._v("“Quy tắc không phải nhà chúng ta, cảm thấy không thích hợp, ngươi cũng có thể nói, bất quá... Chỉ cần có thể đến đến mọi người đồng ý là được!”"),t("br"),t("br"),n._v("Trần Việt khóe miệng vung lên."),t("br"),t("br"),n._v("Hắn ý tứ rất đơn giản, ba nhà chúng ta đã liên minh, ngươi nói quy tắc khá hơn nữa, chúng ta không đồng ý, cũng là nói nhảm."),t("br"),t("br"),n._v("Hồng sư nghe được ý tứ, quay đầu nhìn về phía thanh niên, nhìn hắn trả lời như thế nào."),t("br"),t("br"),n._v("“Ồ? Biết không phải là nhà các ngươi là tốt rồi, ta vừa vặn có cái mới quy tắc, muốn nói một chút, mọi người một khối thương nghị!” Trương Huyền cười cười."),t("br"),t("br"),n._v("“Rửa tai lắng nghe!” Trần Việt nói: “Bất quá, có đồng ý hay không, liền không nhất định...”"),t("br"),t("br"),n._v("“Ta tin tưởng các ngươi nhất định sẽ đồng ý!”"),t("br"),t("br"),n._v("Trương Huyền nhàn nhạt nhìn qua: “Nếu là theo các nước chư hầu giao đấu, vì sao nhất định phải ba hạng đầu? Vạn nhất bị thương, ngày mai Hóa Thanh trì mở ra, vào hay là không vào?”"),t("br"),t("br"),n._v("“Đúng a?” Hồng sư ánh mắt sáng lên, lần nữa giúp đỡ."),t("br"),t("br"),n._v("“Vậy ngươi nói làm sao bây giờ?” Trần Việt nhướng mày."),t("br"),t("br"),n._v("“Ha ha!” Trương Huyền nhẹ nhàng cười một tiếng: “Quy tắc của ta rất đơn giản, chính là... Các ngươi những này dẫn đội ngũ tinh danh sư tiến hành luận võ.”"),t("br"),t("br"),n._v("“Đúng, quy tắc rất đơn giản... Hả?”"),t("br"),t("br"),n._v("Tiếp tục giúp đỡ, Hồng sư lời nói nói phân nửa, đột nhiên câm ở trong miệng, con mắt trừng lớn nhìn qua: “Chúng ta... Luận võ?”"),t("br"),t("br"),n._v("“Không sai, không chỉ có như thế, vẫn là Hồng sư một người đồng thời đối chiến ba người các ngươi!” Trương Huyền tiếp tục nói."),t("br"),t("br"),n._v("“Đối ba cái...”"),t("br"),t("br"),n._v("Thân thể nhoáng một cái, Hồng sư mắt tối sầm lại."),t("br"),t("br"),n._v("Đại ca, ta còn tưởng rằng ngươi có thể có biện pháp gì tốt, náo loạn nửa ngày liền cái này?"),t("br"),t("br"),n._v("Ngươi đây là muốn ta mạng già ah, còn là muốn giết ta?"),t("br"),t("br"),n._v("Không nói cái khác, liền La Chiêu một cái, ta đều chưa hẳn có thể đánh qua, đối chiến ba cái..."),t("br"),t("br"),n._v("“Ngươi để một mình hắn đối chiến ba người chúng ta?”"),t("br"),t("br"),n._v("Không chỉ Hồng sư ngất, La Chiêu, Trần Việt, Phùng Vũ tất cả đều sững sờ, giống như là nhìn hai đồ đần dạng nhìn qua."),t("br"),t("br"),n._v("Thật hay giả?"),t("br"),t("br"),n._v("Vẫn nghĩ thế nào đánh cái tên này một chầu, vậy thì chủ động đưa tới cửa... Còn có cái này chuyện tốt?"),t("br"),t("br"),n._v("“Thế nào? Ta quy tắc định xuống, các ngươi dám vẫn là không dám?”"),t("br"),t("br"),n._v("Trương Huyền mỉm cười."),t("br"),t("br"),n._v("“Hỏi chúng ta có dám hay không?” La Chiêu nhịn không được nhìn về phía cách đó không xa Hồng sư: “Ngươi nghiêm túc!”"),t("br"),t("br"),n._v("Lập tức nhìn thấy Hồng sư, lập tức sẽ khóc, vẻ mặt táo bón biểu lộ, nhẹ gật đầu."),t("br"),t("br"),n._v("Mặc dù không biết Trương sư vì cái gì muốn làm như thế, nhưng có trước mặt trải qua, biết không làm chuyện không có nắm chắc, đã nói như vậy, liền nhất định là có chủ ý của mình."),t("br"),t("br"),n._v("“Thế nào, không dám? Coi như không dám, không muốn tán dóc những thứ vô dụng kia.”"),t("br"),t("br"),n._v("Khoát tay áo, Trương Huyền không nói thêm lời."),t("br"),t("br"),n._v("“Vì cái gì không dám? Liền sợ một lát nữa, hắn sẽ quỳ xuống đất cầu xin tha thứ!”"),t("br"),n._v(" Trần Việt, La Chiêu đám người nhìn nhau, xác nhận đối phương là nghiêm túc, tất cả đều cười ha ha một tiếng."),t("br"),t("br"),n._v("“Thái tử điện hạ, tình cảnh mới vừa rồi ngươi cũng thấy đấy, không phải chúng ta muốn khi dễ người, là Hồng sư tự nguyện khiêu chiến ba người chúng ta, đến quyết định danh ngạch vấn đề, đã hắn đượm tình khẩn thiết, khăng khăng muốn nhờ, mong rằng điện hạ thành toàn!”"),t("br"),t("br"),n._v("Sợ đổi ý, Trần Việt vội vàng ôm quyền."),t("br"),t("br"),n._v("“Cái này...”"),t("br"),t("br"),n._v("Không nghĩ tới lại biến thành như vậy, Diệp Tiền mày nhăn lại, quay đầu nhìn về phía Hồng sư: “Các ngươi đều là ngũ tinh sơ kỳ danh sư, Hóa Phàm lục trọng sơ kỳ, thực lực tương đương, ngươi khẳng định muốn cùng ba người đồng thời tỷ thí?”"),t("br"),t("br"),n._v("Thực lực tương đồng, một đối ba, rõ ràng là tự tìm cái chết, nói thật, hắn không có chút nào xem trọng đối phương."),t("br"),t("br"),n._v("“Xác định!”"),t("br"),t("br"),n._v("Hồng sư vẫn chưa trả lời, Trương Huyền liền thay hắn đáp ứng."),t("br"),t("br"),n._v("“Cái kia tốt...”"),t("br"),t("br"),n._v("Đã hai bên thương nghị quyết định ra phương pháp, Diệp Tiền cũng không nói thêm lời, nhìn quanh một tuần: “Tốt, ta hiện tại tuyên bố tỷ thí quy tắc, Hồng sư một người đối chiến Phùng sư, La sư, trần sư ba người, lấy thắng thua quyết định danh ngạch.”"),t("br"),t("br"),n._v("Nói xong vẫy tay một cái, ngoài cửa tới một đám hộ vệ, đem phòng khách đồ vật thu thập một chút, đưa ra một cái rộng có thể đủ địa phương chiến đấu."),t("br"),t("br"),n._v("“Điểm đến là dừng, không cần thiết tổn thương hòa khí!”"),t("br"),t("br"),n._v("Thấy bốn người đi vào sân bãi, Diệp Tiền bàn giao."),t("br"),t("br"),n._v("“Yên tâm đi, chúng ta nhất định sẽ ...”"),t("br"),t("br"),n._v("Phùng sư cười lạnh một tiếng, cố ý tăng thêm “Điểm đến là dừng” bốn chữ, rất hiển nhiên, hắn ý tứ, tuyệt không chỉ là điểm đến là dừng đơn giản như vậy."),t("br"),t("br"),n._v("“Không sai, chúng ta cùng Hồng sư đều là nhiều năm bạn cũ, sẽ không quá mức phận...” La Chiêu cũng bật cười."),t("br"),t("br"),n._v("Hai người đều tại Hồng sư tay bên trên bị thua thiệt, lúc này đối phương quang minh chính đại tự tìm cái chết, sao có thể buông tha."),t("br"),t("br"),n._v("“Trương sư, Hồng sư đối chiến ba người, không có gì phần thắng đi...”"),t("br"),t("br"),n._v("Nhìn thấy ở giữa tỷ thí hết sức căng thẳng, la tuyền, Tất Giang Hải đều xoa xoa mồ hôi lạnh trên đầu, tràn đầy sốt ruột."),t("br"),t("br"),n._v("Trương sư thoạt nhìn thật thông minh, làm sao lại làm ra như thế quyết định ngu xuẩn?"),t("br"),t("br"),n._v("Một đối ba..."),t("br"),t("br"),n._v("Không là muốn chết ư?"),t("br"),t("br"),n._v("“Không cho Hồng sư lên, các ngươi có nắm chắc vượt qua bọn hắn?” Trương Huyền nhìn qua."),t("br"),t("br"),n._v("“Cái này...”"),t("br"),t("br"),n._v("Hai người co lại, đồng thời lắc đầu."),t("br"),t("br"),n._v("Bọn hắn mặc dù là thiên tài, đối phương có thể tuyển ra, khẳng định cũng không đơn giản, đơn thuần tỷ võ lời nói, nhờ vào đẳng cấp bên trên nghiền ép, muốn vượt qua, chỉ sợ không có khả năng!"),t("br"),t("br"),n._v("Nói cách khác, bọn hắn tham gia tỷ thí lời nói, vượt qua tỷ lệ hầu như là không."),t("br"),t("br"),n._v("“Có thể Hồng sư hắn...”"),t("br"),t("br"),n._v("Mặc dù mình không cách nào thắng được, nhưng Hồng sư đi khiêu chiến, thắng được khả năng cũng không lớn ah."),t("br"),t("br"),n._v("“Nhìn xem là được!” Cũng không giải thích, Trương Huyền cười cười."),t("br"),t("br"),n._v("“Được rồi...”"),t("br"),t("br"),n._v("Không biết đối phương từ thư ở đâu, nhưng chuyện cho tới bây giờ cũng mất những biện pháp khác, hai người đành phải trên mặt lo lắng tiếp tục xem tiếp."),t("br"),t("br"),n._v("La tuyền, Tất Giang Hải lo lắng, ở giữa nhất thái tử, cũng mày nhíu lại thành u cục, thực sự không hiểu rõ vì cái gì Hồng sư sẽ đưa ra như thế tính chất tự sát yêu cầu."),t("br"),t("br"),n._v("“Học trưởng, ngươi cảm giác đến bọn hắn hai bên ai sẽ thắng?”"),t("br"),t("br"),n._v("Thực sự nhịn không được, nhìn về phía cách đó không xa Ngô Chấn."),t("br"),t("br"),n._v("Vị này chính là ngũ tinh danh sư, bất kể thực lực vẫn là hiểu biết đều so với hắn cao hơn bên trên không ít, có lẽ có thể nhìn ra thứ gì."),t("br"),t("br"),n._v("“Dựa theo tình huống bình thường, Hồng sư gần như không có khả năng chiến thắng, có điều, đã đưa ra yêu cầu này, khẳng định có chỗ ỷ lại...”"),t("br"),t("br"),n._v("Kỳ thật không chỉ thái tử không hiểu, Ngô Chấn đến bây giờ cũng không hiểu ra sao."),t("br"),t("br"),n._v("Lấy nhãn lực của hắn, có thể rõ ràng nhìn ra bốn người thực lực."),t("br"),t("br"),n._v("Hồng sư tại trong mấy người cũng không tính là kiệt xuất, đơn đả độc đấu đều rất khó, một chọi ba, rất rõ ràng là tất thua không thể nghi ngờ cục diện... Loại tình huống này còn không phải muốn kiên trì tỷ thí, đến cùng nghĩ như thế nào?"),t("br"),t("br"),n._v("“Xem tiếp đi đi, có lẽ... Có thể có không đồng dạng có thể xoay chuyển!”"),t("br"),t("br"),n._v("Nghĩ một lát, cũng nghĩ không thông, Ngô Chấn lắc đầu."),t("br"),t("br"),n._v("“Ừm!”"),t("br"),t("br"),n._v("Thái tử gật gật đầu, suy nghĩ hồi lâu cũng nghĩ không thông, đành phải tiếp tục hướng phía trước nhìn lại: “Hồng sư luôn luôn làm việc ổn thỏa, có lẽ thật có những biện pháp khác... Mau nhìn, đã bắt đầu!”"),t("br"),t("br"),n._v("Đối thoại quá trình bên trong, trên trận bốn người đã bắt đầu động thủ."),t("br"),t("br"),n._v("Hô hô hô!"),t("br"),t("br"),n._v("La Chiêu, Trần Việt, Phùng Vũ ba đại Hóa Phàm lục trọng cường giả đồng thời ra tay, cuồng bạo áp lực phong tỏa không gian."),t("br"),t("br"),n._v("Dường như cũng lo lắng Hồng sư có gì dựa dẫm, ba người không có trực tiếp ra tay, mà là phong tỏa chung quanh, tiến hành thăm dò."),t("br"),t("br"),n._v("Thử hai chiêu, càng ngày càng kỳ quái, Phùng Vũ đột nhiên một quyền đánh ra."),t("br"),t("br"),n._v("Bành!"),t("br"),t("br"),n._v("Chính giữa Hồng sư trên mặt, lập tức máu mũi chảy dài."),t("br"),t("br"),n._v("“A?”"),t("br"),t("br"),n._v("Không nghĩ tới đánh lén một quyền có thể thành công, Phùng Vũ con mắt trợn tròn, kích động thân thể run rẩy."),t("br"),t("br"),n._v("Bởi vì quá mức hưng phấn, xé rách vết thương một hồi nhe răng nhếch miệng."),t("br"),t("br"),n._v("“Đây là làm việc ổn thỏa? Có chỗ dựa dẫm?”"),t("br"),t("br"),n._v("Nhìn thấy Phùng Vũ cái này trọng thương sắp quải điệu gia hỏa, tại La Chiêu, Trần Việt trợ giúp bên dưới, đều có thể dễ như trở bàn tay đánh Hồng sư máu me đầy mặt, Ngô sư vẻ mặt mộng bức."),t("br"),t("br"),n._v("“Thế nào... Nhìn xem giống như là đơn phương bị đánh đâu?”"),t("br"),t("br"),n._v("(Các ngươi quá phận, hôm qua tại công chúng số phát ta cùng Ngô kinh ảnh chụp, kết quả không đến một ngày, nhắn lại tiếp cận 300, tất cả đều xưng hô lão nhai vì lão Tất! Quá phận, lão Tất là ta thời đại học ngoại hiệu có được hay không? Có điều, cao trung lúc ảnh chụp không tìm được, cái kia là đại học tốt nghiệp công việc sau ảnh chụp. Ai, gọi lão Tất nhiều không lễ phép a? Quá trớn! Liền không thể kêu một tiếng “Ông ngoại” ? Ô ô. Không gì sánh được tâm tắc, cầu phiếu đề cử, nguyệt phiếu ủng hộ! Đúng, hiện tại hình như có thể miễn phí dẫn chúng ta hội fan hâm mộ nhai quân hiệu chương, còn không có dẫn, nhanh lên dẫn ah!)"),t("br"),t("br"),n._v("Convert by: ThấtDạ"),t("br"),t("br"),t("br"),t("br")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("a",{attrs:{href:"./chap-637"}},[n._v("Chương trước")]),n._v("  |  "),t("a",{attrs:{href:"./chap-639"}},[n._v("Chương sau")])])])}),[],!1,null,null,null);h.default=i.exports}}]);