(window.webpackJsonp=window.webpackJsonp||[]).push([[1899],{2254:function(n,h,t){"use strict";t.r(h);var i=t(44),g=Object(i.a)({},(function(){var n=this,h=n.$createElement,t=n._self._c||h;return t("ContentSlotsDistributor",{attrs:{"slot-key":n.$parent.slotKey}},[t("h1",{attrs:{id:"chuong-631-tri-nhan"}},[t("a",{staticClass:"header-anchor",attrs:{href:"#chuong-631-tri-nhan"}},[n._v("#")]),n._v(" Chương 631: Trì Nhãn")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("br"),t("br"),n._v("Chương 631: Trì Nhãn"),t("br"),t("br"),n._v("Thấy Hồng sư sắp gánh không được, Trương Huyền không để ý một bên đùa nghịch tiện Ngô Chấn, tiếp tục truyền âm."),t("br"),t("br"),n._v("Ngô Chấn xác nhận trong lòng đoán sự thật, cũng không tại tìm hắn để gây sự, an tĩnh nhìn sang."),t("br"),t("br"),n._v("Mấy phút đồng hồ sau."),t("br"),t("br"),n._v("Phùng Vũ, La Chiêu, Trần Việt từng cái mặt sưng phù như là đầu heo, toàn thân là tổn thương nằm trên mặt đất, chủ động nhận thua."),t("br"),t("br"),n._v("Không nhận thua cũng không được, vị này Hồng sư mỗi lần đều có thể liệu địch tiên cơ, đánh bọn hắn không hề có lực hoàn thủ, tiếp tục nữa, đừng nói chiến thắng, có thể giữ được hay không tính mệnh đều còn khó nói, còn không bằng nhận thua quên đi."),t("br"),t("br"),n._v("Ba người cực kỳ thảm thiết, tổn thương cha mẹ đều nhận không ra, có điều, Hồng sư cũng không khá hơn chút nào, vẻ mặt sưng tấy, khóe mắt sưng đỏ, thân thể lay động, vẻ mặt máu tươi... Nếu không phải một luồng ý niệm chống đỡ, khẳng định đã sớm không kiên trì nổi."),t("br"),t("br"),n._v("“Tốt, đã Phùng sư, La sư, Trần sư nhận thua, ba cái danh ngạch, liền có các ngươi ba phe thế lực ra!”"),t("br"),t("br"),n._v("Thấy Hồng sư quả thật chiến thắng, Diệp Tiền tràn đầy kinh ngạc."),t("br"),t("br"),n._v("Hắn không có Ngô Chấn nhãn lực, không nhìn ra bí quyết, còn tưởng rằng Hồng sư đơn bằng thực lực của mình, vượt qua ba người, nhịn không được một hồi bội phục."),t("br"),t("br"),n._v("“Ngô sư, Hồng sư bị thương, có thể hay không hướng lấy một chén chữa thương rượu thuốc?”"),t("br"),t("br"),n._v("Thấy Hồng sư hoàn toàn thay đổi đi tới, Trương Huyền hổ thẹn trong lòng, đứng dậy đi tới Ngô sư trước mặt."),t("br"),t("br"),n._v("Nếu không phải cái này hai hàng thẳng gọi mình, Hồng sư mặc dù bị thương, cũng tại trong phạm vi khống chế, căn bản không có khả năng giống như bây giờ, đều sắp ngỏm rồi."),t("br"),t("br"),n._v("“Ừm!”"),t("br"),t("br"),n._v("Ngô sư cũng cảm thấy có chút xấu hổ, lấy ra vừa rồi rượu thuốc, rót một chén."),t("br"),t("br"),n._v("Tiện tay một đạo chân khí rót vào chén rượu, Trương Huyền để Hồng sư uống vào."),t("br"),t("br"),n._v("Chân khí trên cơ thể người bên trong có thể lâu dài bảo tồn, nhưng ở trong rượu, không bao lâu liền sẽ tiêu tán, nói cách khác, Ngô sư trong tay hồ lô kia rượu, bây giờ còn có thể có hiệu quả nhất định, nương theo thời gian trôi qua, liền lại biến thành bình thường rượu đế, không có nổi chút tác dụng nào."),t("br"),t("br"),n._v("Chỉ chốc lát, Hồng sư thương thế trên người, phục hồi từ từ."),t("br"),t("br"),n._v("Thiên Đạo chân khí dung nhập rượu, dọc theo kinh mạch chảy xuôi, coi như uống vào trong bụng đồng dạng có thể tạo được tác dụng."),t("br"),t("br"),n._v("“Đa tạ Ngô sư, Trương sư!”"),t("br"),t("br"),n._v("Thương thế khôi phục, Hồng sư một hồi sảng khoái tinh thần, vội vàng ôm quyền."),t("br"),t("br"),n._v("Vừa rồi thật sự cho rằng sống không nổi nữa, không nghĩ tới Trương sư chỉ điểm lợi hại như thế, nhẹ nhõm đem ba người đánh bại."),t("br"),t("br"),n._v("Đây chính là cùng hắn lẫn nhau cùng cấp bậc danh sư... Đơn đả độc đấu đều chưa hẳn có thể thắng được, một đối ba đều thắng, dù là bị thương lại lần nữa, cũng đáng giá!"),t("br"),t("br"),n._v("Tứ đại các nước chư hầu cạnh tranh với nhau, chỉ bằng vào lần này, Hồng Phong đế quốc liền có thể mở mày mở mặt!"),t("br"),t("br"),n._v("“Khách khí...”"),t("br"),t("br"),n._v("Ngô sư vẫy tay, nhìn về phía trước mắt Trương Huyền, tràn đầy hiếu kỳ."),t("br"),t("br"),n._v("Chẳng qua cũng biết bây giờ không phải là hỏi thăm thời điểm, không nói thêm lời."),t("br"),t("br"),n._v("“Ngô sư, chúng ta cũng có thể hay không lấy một chén rượu thuốc?”"),t("br"),t("br"),n._v("Trương Huyền hai người trở lại chỗ ngồi, Phùng sư, La sư đám người đi tới."),t("br"),t("br"),n._v("Hồng sư uống vào rượu thuốc lập tức thay đổi tốt tình cảnh, bọn hắn nhìn ở trong mắt, hâm mộ đến cực điểm, giờ phút này đành phải đồng dạng tới đòi hỏi."),t("br"),t("br"),n._v("“Tốt!”"),t("br"),t("br"),n._v("Lông mày nhảy một cái, mặc dù không quá nguyện ý, Ngô sư vẫn là đem hồ lô rượu lấy ra, cho ba người đều rót một chén."),t("br"),t("br"),n._v("“Đa tạ Ngô sư!”"),t("br"),t("br"),n._v("Tiếp nhận rượu thuốc, ba người vội vàng uống vào, chỉ cảm thấy một luồng cay độc xông lên cổ họng."),t("br"),t("br"),n._v("Chờ một hồi, phát hiện thương thế trên người, còn cùng trước kia biến hóa không lớn, nhịn không được nhìn qua: “Ngô sư, ngươi có phải hay không đem hồ lô rượu cầm nhầm...”"),t("br"),t("br"),n._v("“Cầm nhầm, vừa rồi chính là cái này...”"),t("br"),t("br"),n._v("Ngô sư nhướng mày: “Có lẽ thương thế của các ngươi càng nặng, cần thoa ngoài da!”"),t("br"),t("br"),n._v("Nói xong lại cho mấy người riêng phần mình rót một chén."),t("br"),t("br"),n._v("“Cũng đúng!”"),t("br"),t("br"),n._v("Trước đó Trương sư đâm xuyên cái kia Tất sư cánh tay, dùng chính là thoa ngoài da, có lẽ thương thế của bọn hắn so Hồng sư muốn nặng, uống thuốc đã hiệu quả không lớn."),t("br"),t("br"),n._v("Nghĩ đến điểm này, lần nữa đem rượu bưng lên, đều đặn bôi lên tại bị thương vị trí."),t("br"),t("br"),n._v("Xì xì xì!"),t("br"),t("br"),n._v("Rượu mang theo thiêu đốt chi ý, để bọn hắn đau nhe răng trợn mắt không nói, chờ nửa ngày, vết thương vẫn không có chuyển biến tốt đẹp, ngược lại có bại mủ xu thế, Phùng Vũ, La Chiêu, Trần Việt từng cái vẻ mặt choáng váng."),t("br"),t("br"),n._v("Đây không phải thánh dược chữa thương, một bôi lên đi lên, liền lập tức khôi phục như lúc ban đầu ư?"),t("br"),t("br"),n._v("Thế nào không những không có tác dụng, miệng vết thương ngược lại mang theo run rẩy đau đớn? Còn kèm theo thỉnh thoảng ngứa?"),t("br"),t("br"),n._v("Vừa rồi rõ ràng nhìn thấy Hồng sư uống liền tốt? Có phải hay không là Ngô sư bất công, cho thiếu đi?"),t("br"),t("br"),n._v("“Ngô sư, có lẽ rượu thuốc còn có chút ít, có thể hay không cho thêm mấy chén...”"),t("br"),t("br"),n._v("Tràn đầy nghi hoặc, La Chiêu nhịn không được mở miệng lần nữa."),t("br"),t("br"),n._v("“Cút!”"),t("br"),t("br"),n._v("Ngô sư lông mày giương lên."),t("br"),t("br"),n._v("Cái gì ngoạn ý, nhân gia Hồng sư liền cho ta như thế chút thuốc rượu, các ngươi một chén lại một chén, lại là uống, lại là bôi, không dứt đúng không?"),t("br"),t("br"),n._v("Mắng xong trong lòng cũng kỳ quái, chẳng lẽ là mấy người này thương thế càng nặng, hiệu quả yếu một chút?"),t("br"),t("br"),n._v("Nhất định là!"),t("br"),t("br"),n._v("Bất kể nói thế nào, mấy người trạng thái tinh thần so mới vừa mới tốt hơn nhiều."),t("br"),t("br"),n._v("Kỳ thật, hắn không biết, trước đó Trương Huyền tại toàn bộ trong hồ lô đều rót vào chân khí, mặc dù tán phát đại bộ phận, vẫn có một ít hiệu quả."),t("br"),t("br"),n._v("La Chiêu đám người mỗi người hai chén, mặc dù không giống Hồng sư đồng dạng hoàn hảo như lúc ban đầu, nhưng cũng khôi phục hơn phân nửa, máu ngừng lại, đi lại không có bất kỳ vấn đề gì."),t("br"),n._v(" “...” Thấy hắn nổi giận, La Chiêu đám người không dám nhiều lời, vội vàng đi trở về."),t("br"),t("br"),n._v("..."),t("br"),t("br"),n._v("“Danh ngạch quyết định đi ra, hiện tại phân phối khu vực!”"),t("br"),t("br"),n._v("Thấy tỷ thí kết quả đi ra, danh ngạch sự tình xác định, Diệp Tiền nhìn quanh một tuần, nói: “Hóa Thanh trì, hội tụ xanh tắc sơn cùng Long lân sông linh khí, có thể tẩm bổ hồn phách, để cho người ta hồn lực đại tăng. Bởi vậy, cái này linh trì, mệnh danh là Huyễn Vũ đế quốc Thánh địa, coi như hoàng thất người, cũng không thể tùy ý đi vào. Chỉ có thể y theo mười năm một lần mở ra quy tắc.”"),t("br"),t("br"),n._v("“Những này các ngươi đều biết, chắc hẳn cũng không cần ta nhiều lời!”"),t("br"),t("br"),n._v("Hồng sư đám người gật đầu."),t("br"),t("br"),n._v("Chuyện này người người đều biết, không phải bí mật gì."),t("br"),t("br"),n._v("“Hóa Thanh trì, là tu luyện Thánh địa, đối hồn phách có cực lớn tẩm bổ không giả, nhưng là các ngươi không biết, đây cũng là đại hung chỗ, hơi không cẩn thận, liền sẽ thân tử đạo vẫn, ai cũng không giải cứu được!”"),t("br"),t("br"),n._v("Diệp Tiền sắc mặt nghiêm túc."),t("br"),t("br"),n._v("“Đại hung chỗ? Thân tử đạo vẫn?”"),t("br"),t("br"),n._v("“Không nói là linh khí đầy đủ linh trì ư?”"),t("br"),t("br"),n._v("“Đúng vậy a, tại sao có thể có nguy hiểm?”"),t("br"),t("br"),n._v("..."),t("br"),t("br"),n._v("Mọi người tất cả đều sững sờ."),t("br"),t("br"),n._v("Ngay cả La Chiêu, Phùng Vũ mấy người cũng nhíu mày."),t("br"),t("br"),n._v("Tất cả mọi người nghe qua, Hóa Thanh trì tẩm bổ linh hồn, gia tăng tu vi, nhưng chưa từng nghe nói qua, nơi này mười phần nguy hiểm."),t("br"),t("br"),n._v("Một cái tràn ngập linh dịch bể tắm, sẽ có nguy hiểm gì?"),t("br"),t("br"),n._v("“Hóa Thanh trì là linh trì không giả, nhưng ao nước cuồng bạo không chịu nổi, Hóa Phàm cửu trọng người, tiến vào bên trong, đều sẽ bị tuỳ tiện giảo sát! Năm đó, Tiên Tổ phát hiện nơi đây, từng phái người tiến vào bên trong dò xét, kết quả không một may mắn còn sống sót.”"),t("br"),t("br"),n._v("Diệp Tiền vẻ mặt nghiêm túc."),t("br"),t("br"),n._v("“Hóa Phàm cửu trọng đều sẽ bị giảo sát?”"),t("br"),t("br"),n._v("Mọi người giật mình: “Vậy chúng ta...”"),t("br"),t("br"),n._v("Liền Hóa Phàm cửu trọng đều gánh không được, để bọn hắn đi vào không là muốn chết?"),t("br"),t("br"),n._v("Đã như vậy, lại vì cái gì nói là tu luyện Thánh địa?"),t("br"),t("br"),n._v("“Mọi người không cần phải gấp, nghe ta nói hết lời!”"),t("br"),t("br"),n._v("Thấy mọi người bộ dáng này, Diệp Tiền cười cười: “Hóa Thanh trì mười phần nguy hiểm, ta cũng không phải là nói chuyện giật gân, mà là chân thật. Bất quá, cũng không phải là không có biện pháp, Tiên Tổ tìm kiếm nhiều năm, không biết hao phí bao nhiêu sinh mệnh làm đánh đổi, rốt cục phá giải ao nước một chút bí ẩn, tìm được một chút an toàn khu vực, chỉ cần tại cái phạm vi này, vô luận như thế nào tu luyện, đều sẽ không nhận ảnh hưởng!”"),t("br"),t("br"),n._v("“Nơi này, bị chúng ta gọi là Trì Nhãn! Trì Nhãn là linh khí giao hội chỗ, hết sức an toàn, có điều, cách mỗi mười năm, mới có thể xuất hiện một lần.”"),t("br"),t("br"),n._v("“Trì Nhãn?”"),t("br"),t("br"),n._v("“Mười năm xuất hiện một lần? Chẳng lẽ, Hóa Thanh trì mười năm mở ra một lần, là bởi vì cái này?”"),t("br"),t("br"),n._v("“Khẳng định là...”"),t("br"),t("br"),n._v("Nghe được giải thích, mọi người bừng tỉnh đại ngộ."),t("br"),t("br"),n._v("Hóa Thanh trì mười năm mới mở ra một lần, thẳng làm cho tất cả mọi người không hiểu, vốn cho rằng là huyễn Vũ Hoàng phòng vì bảo hộ cái này ao nước, hiện tại xem ra, căn bản không phải, mà là... Phòng ngừa nguy hiểm."),t("br"),t("br"),n._v("“Trì Nhãn kích thước không lớn, chỉ có đường kính chừng hai mét, hơn nữa, trong đó có thể tăng tiến linh hồn linh khí, cũng là có số lượng nhất định, một khi hấp thu xong tất, liền sẽ sụp đổ! Nói cách khác, tiến vào bên trong người, phát giác nhanh hấp thu xong linh khí trong đó, liền muốn rời khỏi, nếu không, một khi sụp đổ, liền lại không cách nào đi ra.”"),t("br"),t("br"),n._v("Diệp Tiền đạo."),t("br"),t("br"),n._v("“Đường kính hai mét? Còn có hạn chế? Cái kia...”"),t("br"),t("br"),n._v("Mọi người tất cả đều tràn đầy nghi hoặc."),t("br"),t("br"),n._v("Có thể tiến vào, tổng cộng mười lăm cái danh ngạch, đường kính hai mét Trì Nhãn, không có khả năng ngồi bên dưới ah!"),t("br"),t("br"),n._v("Hơn nữa, còn có hạn chế, ai đi vào trước, ai người chậm tiến đi, có phải hay không cũng khá là phiền toái?"),t("br"),t("br"),n._v("“Mọi người biết, linh hồn bị giam cầm ở thân thể bên trong, thật giống như có chiếc lồng bao phủ, là không thể nào không hạn chế gia tăng!” Không có giải thích mọi người nghi hoặc, Diệp Tiền tiếp tục nói."),t("br"),t("br"),n._v("Trương Huyền gật đầu."),t("br"),t("br"),n._v("Bình thường người tu luyện, thân thể sẽ cùng lồng giam đồng dạng, đem hồn phách chặt khóa chặt, không cách nào tránh thoát."),t("br"),t("br"),n._v("Coi như lấy được phương pháp tu luyện linh hồn, hồn phách cũng chỉ có thể giam cầm tại trong nhục thể."),t("br"),t("br"),n._v("Liền cùng chậu hoa loại hoa, căn lại lớn, cũng chỉ có thể giam cầm tại trong chậu."),t("br"),t("br"),n._v("“Trải qua tính toán, bất kể Hợp Linh cảnh vẫn là cầu Thiên Cảnh, ba ngày là cái cực hạn, coi như đợi đến thời gian càng dài, cũng sẽ trở nên không có có hiệu quả! Hơn nữa, ba ngày cũng là Trì Nhãn có thể duy trì lớn nhất thời gian. Bởi vậy, đế quốc xác định ba ngày kỳ hạn.”"),t("br"),t("br"),n._v("Diệp Tiền nói: “Đế quốc cùng các nước chư hầu tên thứ nhất, có thể chờ đủ ba ngày, tên thứ hai, hai ngày, tên thứ ba một ngày!”"),t("br"),t("br"),n._v("Mọi người lên tiếng."),t("br"),t("br"),n._v("Trước đó danh sư thi đấu trước đã nói rồi đấy ban thưởng chính là như thế."),t("br"),t("br"),n._v("Tên thứ nhất có thể tại Hóa Thanh trì tu luyện ba ngày, tên thứ hai, tên thứ ba theo thứ tự là hai ngày, một ngày, theo thứ tự giảm dần."),t("br"),t("br"),n._v("“Trước đó mọi người khẳng định kỳ quái, một cái chỉ có đường kính hai mét ao nước, làm sao có thể để mười lăm cái đồng thời tu luyện... Kỳ thật, mỗi lần Hóa Thanh trì mở ra, cũng không phải là chỉ có một cái Trì Nhãn, mà là đồng thời xuất hiện mười cái!”"),t("br"),t("br"),n._v("“Mười cái?”"),t("br"),t("br"),n._v("“Không sai, là mười cái! Dựa theo trước đó quy củ, năm tên thứ nhất, riêng phần mình chiếm cứ một cái Trì Nhãn, tu luyện đầy ba ngày. Còn lại thứ hai, ba tên, hai người một cái, một cái hai ngày, một cái một ngày. Mười cái Trì Nhãn, vừa vặn đều có thể dùng hết, mười lăm người, cũng đều có thể được đến tu luyện!”"),t("br"),t("br"),n._v("Diệp Tiền nói tiếp."),t("br"),t("br"),n._v("Năm cái thứ nhất, chiếm cứ năm cái Trì Nhãn, còn lại năm cái, mười vị hai, ba tên đi phân, số trời khác biệt, công bằng công chính."),t("br"),t("br"),n._v("“Bất quá... Năm nay khác biệt!”"),t("br"),t("br"),n._v("Nói đến đây, trước mắt dừng lại một chút, Diệp Tiền lắc đầu: “Ngô học trưởng, Lục công chúa đám người, mỗi người khẳng định phải đơn độc một cái Trì Nhãn, ta cũng muốn đơn độc một cái, vậy thì phân đi năm cái... Cho nên, các ngươi chỉ có thể đi phân còn lại năm cái!”"),t("br"),t("br"),n._v("(Có QQ đọc bạn bè phản ứng, nói chương tiết loạn mã, quyển sách là điểm xuất phát xuất ra đầu tiên, là đồng bộ xảy ra vấn đề. Cho nên, mọi người tốt nhất đến điểm xuất phát đặt mua quan sát, nếu như xuất hiện vấn đề này, đem quyển sách từ giá sách bên trong xóa bỏ, một lần nữa download là đủ. Mặt khác, quả táo điện thoại di động người sử dụng, hy vọng có thể thiết trí tự động đặt mua, quả táo thu lấy ba mươi phần trăm thủ tục phí, dùng di động đặt mua sẽ đắt rất nhiều. Thiết trí tự động đặt mua về sau, một khi đổi mới sẽ từ trang web chụp đặt mua phí tổn, tiện nghi không ít. Một chương liền tiện nghi sáu điểm tiền. Rất có lời.)"),t("br"),t("br"),n._v("Convert by: ThấtDạ"),t("br"),t("br"),t("br"),t("br")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("a",{attrs:{href:"./chap-639"}},[n._v("Chương trước")]),n._v("  |  "),t("a",{attrs:{href:"./chap-641"}},[n._v("Chương sau")])])])}),[],!1,null,null,null);h.default=g.exports}}]);