(window.webpackJsonp=window.webpackJsonp||[]).push([[19],{374:function(n,h,t){"use strict";t.r(h);var i=t(44),g=Object(i.a)({},(function(){var n=this,h=n.$createElement,t=n._self._c||h;return t("ContentSlotsDistributor",{attrs:{"slot-key":n.$parent.slotKey}},[t("h1",{attrs:{id:"chuong-993-đi-đoc-đien"}},[t("a",{staticClass:"header-anchor",attrs:{href:"#chuong-993-đi-đoc-đien"}},[n._v("#")]),n._v(" Chương 993: Đi Độc điện")]),n._v(" "),t("hr"),n._v(" "),t("p",[n._v("Chương 993: Đi Độc điện"),t("br"),t("br"),n._v("Lại hỏi thăm một hồi, đối phương đối Hỏa Nguyên thành chỗ kia di tích không hiểu nhiều, biết có hạn, thấy theo trong miệng hắn cũng không cách nào biết được càng nhiều tin tức, Trương Huyền lắc đầu."),t("br"),t("br"),n._v("Lúc này, Ngoan Nhân cũng theo Thủy Diệp vương nguyên thần bên trong thẩm vấn ra tin tức, cùng đối phương nói tới không kém nhiều, chỉ là đối cái này cái gọi là địa đồ, hiểu rõ kỹ lưỡng hơn một chút."),t("br"),t("br"),n._v("Nghe nói hơn hai ngàn năm trước, chỗ kia di tích chủ động xuất hiện, mọi cách cướp đoạt, cuối cùng đã rơi vào Độc điện trong tay."),t("br"),t("br"),n._v("Chỉ là, không biết nguyên nhân gì, Độc điện cũng không phái người tiến đến tìm kiếm, lại thêm bọn họ từ trước tới nay đóng cửa tự phong, dần dà, đối với tin tức này, rất nhiều người cũng không biết."),t("br"),t("br"),n._v("Dị Linh tộc cũng là mọi cách tìm hiểu, mới biết tin tức, trải qua trằn trọc, lúc này mới tìm đúng cơ hội, phái ra hai đại vương giả, không nghĩ tới hai người này, liền Độc điện cái bóng cũng không đánh nghe được, liền ngã quỵ trong tay mình."),t("br"),t("br"),n._v("“Kim Diệp Vương, Thanh Diệp Vương mục đích là cái gì?”"),t("br"),t("br"),n._v("Đã mục đích của bọn hắn là địa đồ, kim, thanh hai vị vương giả, tự nhiên cũng có nó mục đích, chỉ là còn chưa bắt đầu, liền bị bản thân giết chết, đến bây giờ cũng không biết bọn họ muốn làm gì."),t("br"),t("br"),n._v("“Mục đích của bọn hắn là... Để chém giết danh sư học viện viện trưởng, cũng chính là... Thiếu chủ ngươi, chế tạo náo động, để cho người ta không rảnh quan tâm chuyện khác...” Tử Diệp vương hơi đỏ mặt, nói."),t("br"),t("br"),n._v("“Giết ta?” Trương Huyền ngẩn ngơ."),t("br"),t("br"),n._v("Trước đó một mực nghi ngờ, không nghĩ tới là vì đối phó bản thân, xem ra may mắn trước thời hạn phát hiện trước đem hắn giết chết, nếu không, bị hai đại Thánh vực tứ trọng vương giả đánh lén, làm không cẩn thận thật đúng là lại bởi vậy vẫn lạc."),t("br"),t("br"),n._v("Có thể tưởng tượng, hơn hai trăm đầu Thánh vực cấp bậc Dị Linh tộc nhân phối hợp hai cái Thánh vực tứ trọng vương giả, một khi xuất hiện tại Hồng Viễn thành, tất nhiên gây ra vô cùng động tĩnh lớn, Thanh Nguyên phong hào đế quốc Danh Sư đường, cũng có thể cuốn vào trong đó, khả năng thật không có người lại để ý tới di tích sự tình, để hắn dễ dàng được như ý."),t("br"),t("br"),n._v("“Vâng...”"),t("br"),t("br"),n._v("Thấy thiếu chủ một mặt ngớ ra, Tử Diệp vương trong lòng bất đắc dĩ."),t("br"),t("br"),n._v("Khả năng kim, thanh hai vị, là Thanh Điền mười đại vương giả bên trong chết uất ức nhất."),t("br"),t("br"),n._v("Tiến đến giết người, người bị giết, liền biết cũng không biết liền đem hắn giết chết, suy nghĩ một chút cũng đủ khổ cực."),t("br"),t("br"),n._v("“Tốt a, bọn họ cũng coi như chết hắn chỗ...”"),t("br"),t("br"),n._v("Muốn giết bản thân, nhưng tử ở trong tay chính mình, cũng coi như trừng phạt đúng tội."),t("br"),t("br"),n._v("“Ta có cái không gian gấp, ngươi đi vào trước!”"),t("br"),t("br"),n._v("Nên hỏi thì hỏi xong, Trương Huyền không nói thêm lời, vẫy tay một cái, đem Tử Diệp vương thu vào Thiên Nghĩ Phong sào."),t("br"),t("br"),n._v("Thật vất vả để Hứa trưởng lão tin tưởng mình là độc sư, lại đem cái tên này mang theo trên người, đoán chừng nói cái gì đối phương cũng sẽ không tin tưởng."),t("br"),t("br"),n._v("Vẫn là trước đem hắn thu lại cho thỏa đáng."),t("br"),t("br"),n._v("Vù vù!"),t("br"),t("br"),n._v("Lấy đi Tử Diệp vương, Trương Huyền bàn tay một trảo, triệt bỏ Thất Tinh mê trận, Hồ Vân Sinh đám người còn chưa đi xa, an bài đối phương đem những hộ vệ kia bắt lại, viết phong thư, phái người đưa đến Hồng Viễn hoàng thành cùng Danh Sư đường, lúc này mới hướng tĩnh thất phương hướng đi tới."),t("br"),t("br"),n._v("Tĩnh Viễn thành thành chủ cấu kết Dị Linh tộc nhân sự tình, do Ngọc Thần Thanh đau đầu là được, còn là đổi đi vẫn là lần nữa ủy nhiệm, liền mặc kệ chuyện của hắn."),t("br"),t("br"),n._v("Có thể đoán trước, chỉ cần giấy viết thư đưa đến, vị này ngang ngược càn rỡ thành chủ, tất nhiên sẽ trở thành lịch sử."),t("br"),t("br"),n._v("Trở lại tĩnh thất, chỉ thấy Hứa trưởng lão thương thế trên người đã gần như hoàn toàn khôi phục, cả người tinh, khí, thần cũng biến thành càng thêm mượt mà, chẳng qua vẻ mặt vẫn còn có chút trắng bệch, dường như hồn phách chịu tổn thương rất nặng."),t("br"),t("br"),n._v("Tinh thần khẽ động, một cái giáp trùng xuất hiện tại lòng bàn tay đưa tới: “Vừa rồi ta lại đi một chuyến phủ thành chủ, lặng lẽ đem cái này lấy trở về!”"),t("br"),t("br"),n._v("Cái này giáp trùng là đối phương lấy tinh huyết nuôi nấng, một khi mất đi, tất nhiên bị hao tổn cực lớn, thu phục Tử Diệp vương về sau, lấy trở về."),t("br"),t("br"),n._v("Còn người sau có thể xác nhận bọn họ trốn ở Mặc Vân hiên, khẳng định cũng cùng thứ này liên quan đến, nếu không, Tĩnh Viễn thành như thế lớn, nơi nào đều không đi, nhưng ở đây điều tra."),t("br"),t("br"),n._v("“Cái này...” Ánh mắt sáng lên, Hứa trưởng lão lộ ra vẻ kích động, vội vàng nhận lấy: “Đa tạ!”"),t("br"),t("br"),n._v("Đây là hắn bản mệnh giáp trùng, một khi chết, coi như không chết cũng phải lột lớp da, vốn cho rằng, rơi vào trong tay đối phương, lại khó lấy lại, không nghĩ tới, trước mắt vị này, mạo hiểm hỗ trợ lấy tới."),t("br"),t("br"),n._v("Phần ân tình này, để cảm giác khác động."),t("br"),t("br"),n._v("“Ngươi... Không có bị thương chứ?” Đem giáp trùng thu hồi, Hứa trưởng lão tràn đầy quan tâm nhìn lại."),t("br"),t("br"),n._v("Vị cường giả kia đáng sợ, hắn nhưng là tận mắt nhìn thấy, theo trong tay đối phương đem thứ này thu hồi, độ khó bao lớn, khó có thể tưởng tượng."),t("br"),t("br"),n._v("  “Yên tâm đi, ta không sao...” Khẽ cười một cái, Trương Huyền đột nhiên thân thể nhoáng một cái, sắc mặt trắng nhợt: “Phốc!”"),t("br"),t("br"),n._v("Một ngụm máu tươi phun tới, dường như thân thể thương thế quá nặng, có chút không kiên trì nổi, ngã trên mặt đất."),t("br"),t("br"),n._v("“Vị bằng hữu này...” Hứa trưởng lão giật mình, vội vàng đem hắn đỡ dậy, trong mắt tràn đầy lo âu và cảm kích."),t("br"),t("br"),n._v("Để giúp mình thu hồi giáp trùng, bất kể nguy hiểm, loại người này phẩm làm người ta bái phục."),t("br"),t("br"),n._v("Mấu chốt nhất là, thương thế nặng như vậy, vì sợ bản thân lo lắng, lại còn nói không có có thụ thương... Vị này độc sư thật là có thể kết giao, đáng giá dùng tính mạng phó thác người tốt!"),t("br"),t("br"),n._v("Vội vàng từ trong ngực lấy ra một chút chữa thương độc dược, cho trước mắt vị này ăn vào, thấy thần sắc hắn chậm chạp chuyển biến tốt đẹp, lúc này mới thở phào nhẹ nhõm."),t("br"),t("br"),n._v("“Nơi này chúng ta là không ở nổi nữa, nắm chặt thời gian rời khỏi đi!”"),t("br"),t("br"),n._v("Điều tức một hồi, Trương Huyền thở ra một hơi."),t("br"),t("br"),n._v("Hứa trưởng lão nhẹ gật đầu."),t("br"),t("br"),n._v("Nếu như không đi lấy giáp trùng lời nói, đối phương sẽ tưởng là hai người đã trốn xa, hướng bốn phương tám hướng tìm kiếm."),t("br"),t("br"),n._v("Mà bây giờ... Khẳng định đã biết bọn họ còn tại Tĩnh Viễn thành, co lại phạm vi nhỏ tìm tìm liền đơn giản hơn nhiều."),t("br"),t("br"),n._v("“Ngươi bây giờ cảm giác thế nào? Có cần hay không lại nghỉ ngơi một đoạn thời gian?”"),t("br"),t("br"),n._v("Dừng lại một hồi, Hứa trưởng lão nhìn qua."),t("br"),n._v("Mặc dù chạy trốn trọng yếu hơn, nhưng là thương thế của đối phương nguyên nhân quan trọng này thêm nặng, hắn khẳng định trong hội day dứt cả một đời."),t("br"),t("br"),n._v("“Không sao, thương thế của ta còn có thể chống đỡ, rời khỏi nơi này trước lại nói, đến Độc điện, sẽ chậm chậm điều trị không muộn!”"),t("br"),t("br"),n._v("Che ngực, Trương Huyền một mặt đại nghĩa lẫm nhiên nói."),t("br"),t("br"),n._v("“Vậy thì tốt, chúng ta chuẩn bị xuất phát!”"),t("br"),t("br"),n._v("Thương nghị cố định, hai người lặng lẽ đi ra tĩnh thất, hướng ngoài thành chạy đi, thời gian không dài, liền ra khỏi cửa thành."),t("br"),t("br"),n._v("Dọc theo ngoài thành núi rừng đi một hồi, đi tới một cái khe núi, lập tức nhìn thấy một đầu cực lớn Thánh thú xuất hiện tại cách đó không xa."),t("br"),t("br"),n._v("“Đây là chúng ta Độc điện Thánh thú, ta cùng Tiết trưởng lão lúc đến ngồi...”"),t("br"),t("br"),n._v("Nói đến đây, Hứa trưởng lão dừng lại một chút, vẻ mặt có chút đau thương."),t("br"),t("br"),n._v("Trong miệng hắn Tiết trưởng lão, chính là cái kia mang theo nhỏ gầy gia hỏa, tại phủ thành chủ, vì cứu hắn bị Tử Diệp vương tại chỗ đánh chết."),t("br"),t("br"),n._v("“Tiết trưởng lão là vì cứu ngươi, chỉ cần chúng ta có thể bình yên trở lại Độc điện, coi như không có phụ lòng một phen tâm huyết của hắn!” Trương Huyền an ủi một câu."),t("br"),t("br"),n._v("“Ừm!”"),t("br"),t("br"),n._v("Biết hắn ý tứ, Hứa trưởng lão nhẹ gật đầu, không nói thêm lời."),t("br"),t("br"),n._v("Hai người ngồi lên Thánh thú, trực tiếp hướng Long Thương hải phương hướng bay đi."),t("br"),t("br"),n._v("Long Thương hải là Hồng Viễn đế quốc lớn nhất lục địa biển, phạm vi bao phủ mười mấy vạn cây số, trong đó sinh tồn linh thú mạnh đều đạt đến Thánh vực ba, bốn nặng, mười phần đáng sợ, luôn luôn là cấm địa của người tu luyện."),t("br"),t("br"),n._v("Bọn họ ngồi cái này Thánh thú, là một đầu Thánh vực nhất trọng trung kỳ thiết giáp Dực Long thú, phương diện tốc độ cũng không am hiểu, nhưng mà sức chịu đựng tuyệt đối vô cùng tốt."),t("br"),t("br"),n._v("Phi hành cả buổi, liền thấy mênh mông hải dương xuất hiện ở trước mắt, bầu trời xanh thẳm cùng hơi hơi trôi nổi xanh biếc nước biển, ở phía xa dựa vào, giống như đi vào một cái thiên thủy nối liền bức tranh."),t("br"),t("br"),n._v("Xem thời gian lâu dài, sẽ cho người lòng dạ mở rộng, khí khái Vân Sinh."),t("br"),t("br"),n._v("“Đi Độc điện trên đường, tổng cộng bố trí 27 chỗ độc trận, coi như Thánh vực nhị trọng, tam trọng cường giả tùy tiện đi vào, đều sẽ bị dễ dàng độc chết.”"),t("br"),t("br"),n._v("Đứng đang phi hành Thánh thú bên trong nhà gỗ, Hứa trưởng lão nhìn trước mắt nước biển, nói."),t("br"),t("br"),n._v("“27 chỗ độc trận?” Trương Huyền líu lưỡi."),t("br"),t("br"),n._v("“Ừm, độc trận chung quanh, là Thánh vực tam trọng Thánh thú sào huyệt, một khi trốn độc trận, liền sẽ rơi vào bọn chúng công kích... Tìm kiếm Độc điện tu luyện giả, hầu như đều đã chết, hàng năm số lượng đều không dưới mấy trăm.”"),t("br"),t("br"),n._v("Nói đến đây, Hứa trưởng lão lắc đầu."),t("br"),t("br"),n._v("“Hàng năm... Không dưới mấy trăm?”"),t("br"),t("br"),n._v("Trương Huyền giật nảy mình."),t("br"),t("br"),n._v("Dám đi tìm Độc điện, thực lực khẳng định cũng sẽ không kém quá nhiều, ít nhất đều đạt đến Thánh vực, nhân vật như vậy, một năm chết mấy trăm, mặc dù không phải độc sư tự mình hạ thủ, nhưng cùng bọn hắn có cực lớn quan hệ."),t("br"),t("br"),n._v("Khó trách cái nghề nghiệp này để nhiều người như vậy sợ hãi cùng kính sợ, quả thực làm người ta kinh khủng."),t("br"),t("br"),n._v("“Độc điện giấu bí ẩn như vậy, truyền thừa như thế nào kéo dài?”"),t("br"),t("br"),n._v("Nhíu mày, Trương Huyền nhịn không được hỏi."),t("br"),t("br"),n._v("Mỗi một cái chức nghiệp đều cần hậu bối nhân tài bổ sung, Độc điện giấu sâu như vậy, rất nhiều người tìm cũng không tìm tới, như thế nào bái sư truyền thừa, để hắn tiếp tục kéo dài."),t("br"),t("br"),n._v("“Truyền thừa đệ tử, trên cơ bản đều là chúng ta Độc điện người đi ra ngoài tìm tìm, phàm là nhìn thấy có thiên phú đều sẽ thu làm môn hạ. Đương nhiên cũng có một phần là nhận nuôi rất nhiều cô nhi, để bọn hắn từ nhỏ tiếp xúc kịch độc, có thể sống sót, mới có tư cách bái sư!”"),t("br"),t("br"),n._v("Hứa trưởng lão giải thích."),t("br"),t("br"),n._v("Trương Huyền cau mày."),t("br"),t("br"),n._v("Làm như vậy cùng nuôi cổ có chút tương tự, đem tất cả cổ trùng đặt chung một chỗ, để bọn hắn tự giết lẫn nhau, cuối cùng thắng được là vương giả."),t("br"),t("br"),n._v("Một đám trước đến giờ không tiếp xúc qua độc cô nhi, đột nhiên tiếp xúc, thương vong nhất định không thể tránh được, xem ra để bồi dưỡng một cái độc sư, không biết cần bao nhiêu tính mạng tới chôn vùi."),t("br"),t("br"),n._v("“Ngươi không được đến truyền thừa, lại là như thế nào đi vào độc sư cái nghề nghiệp này?”"),t("br"),t("br"),n._v("Giải thích xong, Hứa trưởng lão nhìn lại."),t("br"),t("br"),n._v("Coi như nhận được độc sư truyền thừa, cũng chưa chắc có thể làm được bách độc bất xâm, trước mắt vị này trước đến giờ không có đi qua Độc điện, nhưng có thể làm được điểm ấy, dùng độc so với hắn đều không kém chút nào, đến cùng là làm sao làm được?"),t("br"),t("br"),n._v("“Thật ra thì... Ta tại Độc điện dạo qua, có điều, không phải nơi này, mà là tại Hiên Viên vương quốc cấp dưới một cái địa phương nhỏ, gọi là Hồng Liên sơn mạch... Về sau rời khỏi nơi đó, tự mình xông xáo, dưới cơ duyên xảo hợp, nhận được một bản độc đạo bí tịch, từ từ cũng nghiên cứu, mới tới bây giờ tình trạng...”"),t("br"),t("br"),n._v("Trương Huyền thuận miệng nói."),t("br"),t("br"),n._v("Nếu như nói trước đến giờ không có tiếp thụ qua độc, liền đạt tới loại trình độ này, đổi lại sẽ không ai tin tưởng cả, cho nên trong lời nói, có thật có giả, làm cho đối phương khó mà phân biệt."),t("br"),t("br"),n._v("“Dùng độc bí tịch? Có thể để ngươi đạt tới thực lực như thế, xem ra cái này bí tịch không đơn giản!”"),t("br"),t("br"),n._v("Khen ngợi một tiếng, Hứa trưởng lão nhịn không được nhìn lại: “Không biết phải chăng là thuận tiện, đem quyển bí tịch này cho ta mượn quan sát một ít, yên tâm, ta có thể không mở ra, chỉ là có chút tò mò, muốn nhìn một chút xuất từ cái kia vị đại sư tay!”"),t("br"),t("br"),n._v("“Cái này...”"),t("br"),t("br"),n._v("Trương Huyền khóe miệng giật một cái."),t("br"),t("br"),n._v("Hắn chỉ là tin miệng nói bậy, nơi nào có cái gì bí tịch?"),t("br"),t("br"),n._v("Chẳng lẽ... Đối phương lần nữa đối với hắn sinh ra hoài nghi?"),t("br"),t("br"),n._v("(Ngày mai ba canh, có nguyệt phiếu trước đưa tới ah! Mặt khác, công chúng số gần nhất sẽ đem quyển sách xuất hiện mỹ nữ, lấy một cái mỹ nữ bảng, mời chuyên môn hoạ sĩ vẽ ra tới, hôm nay đem in đi thứ mười hai, chuyên gia có thể chú ý nhìn một chút. Wechat lục soát “Hoành Tảo Thiên Nhai” tăng thêm chú ý là đủ.)"),t("br"),t("br"),t("br"),t("br")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("a",{attrs:{href:"./chap-1001"}},[n._v("Chương trước")]),n._v("  |  "),t("a",{attrs:{href:"./chap-1003"}},[n._v("Chương sau")])])])}),[],!1,null,null,null);h.default=g.exports}}]);