(window.webpackJsonp=window.webpackJsonp||[]).push([[190],{543:function(n,h,t){"use strict";t.r(h);var g=t(44),c=Object(g.a)({},(function(){var n=this,h=n.$createElement,t=n._self._c||h;return t("ContentSlotsDistributor",{attrs:{"slot-key":n.$parent.slotKey}},[t("h1",{attrs:{id:"chuong-1148-đai-nao-vuong-phu"}},[t("a",{staticClass:"header-anchor",attrs:{href:"#chuong-1148-đai-nao-vuong-phu"}},[n._v("#")]),n._v(" Chương 1148: Đại náo vương phủ")]),n._v(" "),t("hr"),n._v(" "),t("p",[n._v("Chương 1148: Đại náo vương phủ"),t("br"),t("br"),n._v("Đi vào Địa quật ba tháng, không dính dáng danh sư chức nghiệp, mặc dù không tính là cực nặng trừng phạt, cũng tuyệt đối không nhẹ."),t("br"),t("br"),n._v("Địa quật là danh sư đại lục cùng vực ngoại chiến trường tụ hợp nơi chốn, kích động nồng đậm sát lục chi khí, thỉnh thoảng còn sẽ có Dị Linh tộc nhân xuất hiện, nguy hiểm cực lớn, rơi vào trong đó, có thể nói cửu tử nhất sinh!"),t("br"),t("br"),n._v("Bình thường đều là phạm vào sai lầm cực kỳ lớn nhỡ danh sư, tự nhận là tội ác khó chuộc, tự động chờ lệnh trong đó, tranh thủ dùng cuộc đời còn lại của mình vì nhân tộc phát huy một điểm cuối cùng thêm nhiệt."),t("br"),t("br"),n._v("Tựa như lúc trước Lục Phong viện trưởng."),t("br"),t("br"),n._v("Không nói trước, Tôn Cường không phải độc sư, liền xem như, không có làm ra cái gì chuyện thương thiên hại lý, liền không thể nói là tội ác tày trời, cho dù là danh sư, cũng không có tư cách đánh chết, mà bản thân thân là chủ nhân, càng sẽ không bị liên lụy!"),t("br"),t("br"),n._v("Đường đường Danh Sư đường Phó đường chủ, chủ quản một phương phân bộ, thế mà cho ra như vậy phán quyết... Quả thực liền là sai lầm nghiêm trọng!"),t("br"),t("br"),n._v("“Điền phó đường chủ, ngoài vòng pháp luật khai ân, cấp cho ngươi như vậy khoan dung độ lượng đãi ngộ, không cảm kích ngược lại cũng thôi, thế mà còn muốn cự tuyệt?”"),t("br"),t("br"),n._v("Lông mày giương lên, Trung Thanh Vương lên tiếng hét lớn."),t("br"),t("br"),n._v("“Loại này khoan dung độ lượng đãi ngộ, chính ngươi giữ đi!” Không thèm để ý đối phương, Trương Huyền mấy bước đi tới Tôn Cường trước mặt."),t("br"),t("br"),n._v("Soạt!"),t("br"),t("br"),n._v("Thấy hắn đi tới trước mặt, áp Tôn Cường hai cái hộ vệ, đồng loạt rút ra vũ khí, một mặt cảnh giác."),t("br"),t("br"),n._v("Bất quá, trường kiếm trong tay, vừa mới ra khỏi vỏ, không có bất kỳ cái gì động tác, liền một tiếng vang giòn, cắt thành hai đoạn, mũi kiếm không biết bay đến đi nơi nào, ngay sau đó cũng không thấy được Trương Huyền có động tác gì, hai vị hộ vệ, rên lên một tiếng, bay ngược mà ra, tầng tầng dán tại đại điện phía ngoài trên cây cột, mắt tối sầm lại ngất đi."),t("br"),t("br"),n._v("“Để ngươi chịu oan ức...”"),t("br"),t("br"),n._v("Than thở một tiếng, Tôn Cường trên người kim hồn tỏa lập tức bay ra ngoài, rơi trên mặt đất, Trương Huyền cong ngón búng ra."),t("br"),t("br"),n._v("Xì xì xì xì...!"),t("br"),t("br"),n._v("Người sau chịu trọng thương, liền mắt trần có thể thấy khôi phục."),t("br"),t("br"),n._v("Tôn Cường thực lực, so với hắn thấp quá nhiều, dùng Thiên Đạo chân khí khôi phục thương thế, không cần tốn nhiều sức."),t("br"),t("br"),n._v("“Làm càn! Trương Huyền, nơi này là Thanh Nguyên thành, không phải là các ngươi Hồng Viễn đế quốc! Không tuân theo sùng Danh Sư đường răn dạy, tự mình làm việc, mắt vô lễ pháp, tùy ý làm bậy, thật sự là thật to gan, người tới, đem hắn bắt lại cho ta!”"),t("br"),t("br"),n._v("Trung Thanh Vương lên tiếng hét lớn."),t("br"),t("br"),n._v("Rầm rầm!"),t("br"),t("br"),n._v("Nương theo lời của hắn, một đống người lập tức theo ngoài cửa vọt vào."),t("br"),t("br"),n._v("“Thiếu gia...”"),t("br"),t("br"),n._v("Nhìn thấy thiếu gia vì hắn, cùng Thanh Nguyên phong hào đế quốc đệ nhất vương gia mạnh mẽ chống đỡ, Tôn Cường vẻ mặt trắng bệch, trong lòng một dòng nước ấm chảy xuôi."),t("br"),t("br"),n._v("Cùng Danh Sư đường Phó đường chủ xé rách, vô cùng có khả năng bị mất tương lai, thiếu gia không có chút gì do dự, chỉ vì cứu hắn một cái hạ nhân... Phần ân tình này, so thiên địa đều muốn cao xa."),t("br"),t("br"),n._v("Lạc Thất Thất, Ngọc Phi Nhi cũng một mặt cảnh giác đứng tại hai bên."),t("br"),t("br"),n._v("“Bắt ta? Trung Thanh Vương, ngươi là thật không sợ chết, vẫn cảm thấy, ta không dám giết ngươi?”"),t("br"),t("br"),n._v("Không nhìn vọt tới tất cả hộ vệ, Trương Huyền xoay người mặt hướng gian phòng chính giữa Trung Thanh Vương."),t("br"),t("br"),n._v("“Giết ta? Ha ha, một cái nhất đẳng đế quốc tới lục tinh danh sư, cơ duyên xảo hợp có chút thực lực, liền nói khoác không biết ngượng! Ngươi không nghe theo Điền đường chủ lời nói, chẳng khác nào vi phạm Danh Sư đường quy định... Ta hiện tại liền thay Danh Sư đường thanh lý môn hộ!”"),t("br"),t("br"),n._v("Cười lạnh một tiếng, Trung Thanh Vương khí tức trên thân, bỗng nhiên dâng lên, thời gian nháy mắt, liền tích góp tới được đỉnh phong."),t("br"),t("br"),n._v("Không khí chung quanh một hồi vặn vẹo, phảng phất tại hắn nguyên thần khống chế bên dưới, được xếp lên."),t("br"),t("br"),n._v("Thánh vực tứ trọng, Nguyên Thần cảnh đỉnh phong!"),t("br"),t("br"),n._v("Cùng lúc trước mới vừa tiến vào Khưu Ngô cung Ngô sư, không kém nhiều, sức chiến đấu thậm chí vượt qua trận pháp sư công hội Hàn hội trưởng."),t("br"),t("br"),n._v("“Trung Thanh Vương, bất kể nói thế nào, Trương sư đều là danh sư, mặc dù có sai, cũng muốn mang về Danh Sư đường thẩm vấn, bắt lấy là được, không nên giết...” Thấy hắn khí tức bạo tăng, Điền Thanh Phó đường chủ dặn dò một câu."),t("br"),t("br"),n._v("“Phó đường chủ yên tâm, ta có chừng mực...”"),t("br"),t("br"),n._v("Cười lạnh một tiếng, Trung Thanh Vương bàn tay bỗng nhiên một trảo, toàn bộ đại điện lập tức như bị mây đen bao phủ, như sóng biển lực lượng, phun mạnh mà xuống."),t("br"),t("br"),n._v("Vị này đệ nhất vương gia, tu luyện không biết là pháp quyết gì, không chỉ lực lượng hùng hồn, chân khí vọt tới, còn cho người một loại rơi vào sa trường cảm giác, tựa như bốn phía tất cả đều là huyết dịch, trước mắt một mảnh huyết hồng, khó mà tự kềm chế."),t("br"),t("br"),n._v("“Đây là... Đại Huyết Bào bí pháp!”"),t("br"),t("br"),n._v("Nhìn thấy chiêu này, Lạc Thất Thất đôi mắt thanh tú lập tức nhíu lại, mang theo lăng lệ."),t("br"),t("br"),n._v("“Đại Huyết Bào bí pháp?”"),t("br"),t("br"),n._v("“Là Thanh Nguyên đế quốc, sát lục khí tức nặng nhất công pháp, tu luyện này công, cần huyết dịch tẩm bổ mới có thể tiến bộ, một khi thi triển, thấy máu mới về... Cực kỳ quỷ dị! Liền xem như cùng cấp bậc Khuynh Hải công, muốn vượt qua, đều rất khó!”"),t("br"),t("br"),n._v("Lạc Thất Thất vội nói: “Chỉ là chiêu này, có tổn thương thiên hợp, danh sư cùng Chiến sư, đều không có người đi học... Không nghĩ tới hắn đường đường một cái vương gia, thế mà học... Lão sư, cẩn thận!”"),t("br"),t("br"),n._v("“Tôm tép nhãi nhép mà thôi!”"),t("br"),t("br"),n._v("Thấy nữ hài mặt mũi lo lắng, Trương Huyền trên mặt không khẩn trương chút nào, ngược lại nhẹ nhàng cười một tiếng, ngón tay búng một cái."),t("br"),t("br"),n._v("Sưu!"),t("br"),t("br"),n._v("Trên đất kim hồn tỏa lập tức bay ra ngoài, tốc độ nhanh như là sấm sét, thời gian nháy mắt liền đến đến Trung Thanh Vương trước mặt."),t("br"),t("br"),n._v("Hùng hồn chưởng lực, đối đầu nhanh chóng vọt tới kim hồn tỏa, thế mà không chặn lại chút nào, một cái nháy mắt, tựu xuyên thấu chưởng lực, xuất hiện tại vị này đệ nhất vương gia trước mặt."),t("br"),t("br"),n._v("Bành!"),t("br"),t("br"),n._v("Kim hồn tỏa đánh vào ngực, hơi đỏ mặt, Trung Thanh Vương một ngụm máu tươi phun tới."),t("br"),t("br"),n._v("Lộc cộc lộc cộc lộc cộc lộc cộc!"),t("br"),t("br"),n._v("Liên tục lui về sau bảy, tám bước, sống lưng đâm vào sau lưng trên ghế ngồi, hoa mộc điêu thành trân quý ghế tựa, không chịu nổi đụng đến mà đến lực lượng, lập tức nổ vỡ nát."),t("br"),t("br"),n._v("Chân khí trong cơ thể sôi trào, phát ra như thủy triều gào thét, Trung Thanh Vương lúc này mới ổn định thân hình, chỉ cảm thấy khí huyết sôi trào, vẻ mặt trắng bệch, nhìn về phía cách đó không xa thanh niên, không dám tin vào hai mắt của mình."),t("br"),t("br"),n._v("Hắn là Thánh vực tứ trọng Nguyên Thần cảnh cường giả đỉnh phong, đối phương chẳng qua Thánh vực nhị trọng Thần Thức cảnh đỉnh phong... Kém trọn hai cái đại cấp bậc."),t("br"),n._v("Dựa theo đạo lý, căn bản không có khả năng vượt qua, mà đối phương... Nhẹ nhõm một đòn, liền rách hắn chưởng lực, không chỉ có như vậy, còn đem hắn nhẹ nhõm đẩy lùi..."),t("br"),t("br"),n._v("Có phần thật là đáng sợ đi!"),t("br"),t("br"),n._v("Không riêng gì hắn, ngay cả một bên Điền Thanh Phó đường chủ cũng kìm lòng không được sửng sốt."),t("br"),t("br"),n._v("Hiển nhiên, Trương Huyền sức chiến đấu, đã vượt ra khỏi tưởng tượng ra, liền xem như hắn, đều bất ngờ."),t("br"),t("br"),n._v("“Khó trách dám lớn lối như vậy, nguyên lai có dựa dẫm, bất quá... Liền chút thực lực ấy lời nói, vẫn là ở lại đây đi!”"),t("br"),t("br"),n._v("Ra hiệu Điền phó đường chủ không cần ra tay, Trung Thanh Vương hét dài một tiếng, năm ngón tay liên tục bắn ra, cùng tay lúc lật bàn tay một cái, như là đem một cái lá sen, móc ngược xuống."),t("br"),t("br"),n._v("“Tam Thu chưởng!”"),t("br"),t("br"),n._v("Trương Huyền nhận ra được."),t("br"),t("br"),n._v("Chiến sư đường võ kỹ điện liên quan tới võ kỹ sách, đều nhìn qua một lần, cũng đem hắn dung hợp lại cùng nhau, trước mắt chiêu này, sớm đã học thuộc làu, quen thuộc đến mức không thể quen thuộc hơn biết."),t("br"),t("br"),n._v("Tam thu hoa quế, mười dặm hoa sen!"),t("br"),t("br"),n._v("Chiêu này uy lực so với Đại Tinh La Chỉ Lực, đều không kém chút nào, tại Chiến sư đường đều có chút danh tiếng, không ít Chiến sư, tiêu tốn mấy chục năm công phu mới có thể có thành tựu, không nghĩ tới trước mắt vị này Trung Thanh Vương, đã đạt đến đại thành cấp bậc."),t("br"),t("br"),n._v("Lợi hại như thế võ kỹ phối hợp thêm Đại Huyết Bào bí pháp, toàn bộ đại điện giống như một lần lâm vào tràn đầy đầm lầy hồ nước, huyết hồng sắc hoa sen, từng đoá từng đoá nở rộ, giống như bất cứ lúc nào cũng sẽ thu gặt lấy linh hồn của con người cùng sinh mệnh."),t("br"),t("br"),n._v("“Chiêu này gặp được cái khác Chiến sư, quả thực rất khó giải quyết, liền xem như Liêu đường chủ đám người, chỉ sợ muốn tạm tránh mũi nhọn... Đáng tiếc, ngươi dùng sai đối tượng!”"),t("br"),t("br"),n._v("Lắc đầu, Trương Huyền không lùi mà tiến tới, tiến về phía trước một bước, bàn chân đạp tại một đóa huyết hồng sắc hoa sen phía trên, ngón tay nâng lên, nhấn tới."),t("br"),t("br"),n._v("Ông!"),t("br"),t("br"),n._v("Đầu ngón tay tinh thần lấp lánh, đem nóc phòng dạ minh châu quang minh, đều hạ thấp xuống."),t("br"),t("br"),n._v("Kiếm mang theo đầu ngón tay phun ra, thời gian nháy mắt liền đem Tam Thu chưởng vỡ ra đến, ngay sau đó liền thấy Trung Thanh Vương máu tươi phun mạnh mà ra, lần nữa lùi lại mấy bước, phía sau lưng đâm vào trên tường."),t("br"),t("br"),n._v("Rầm rầm!"),t("br"),t("br"),n._v("Trên đầu bụi đất tung bay, đá vụn bay loạn."),t("br"),t("br"),n._v("“Ngươi biết... Tam Thu chưởng thiếu hụt?”"),t("br"),t("br"),n._v("Trung Thanh Vương khó tin."),t("br"),t("br"),n._v("Dựa theo tình huống bình thường, Đại Tinh La Chỉ Lực cùng Tam Thu chưởng, cấp bậc tương đồng, uy lực tương đương, cho dù đối đầu, cũng không có khả năng dễ dàng như thế liền thua, nhưng mà... Vị này Trương Huyền, không biết làm sao biết hắn chiêu này bên trong thiếu hụt, một chỉ điểm tại trong đó, lúc này mới dẫn đến chân khí của hắn kém, liền vẫy đều không có chịu được, liền bị trọng thương."),t("br"),t("br"),n._v("Lười nhác trả lời đối phương hỏi thăm, hai tay chắp sau lưng, Trương Huyền hình như không kinh nghiệm chiến đấu, mây trôi nước chảy nhìn về phía cách đó không xa Tôn Cường."),t("br"),t("br"),n._v("“Hắn làm hại ngươi như thế, xử trí như thế nào?”"),t("br"),t("br"),n._v("“Xuyên ta xương bả vai, nếu có cơ hội, cũng muốn đâm thủng hắn... Để hắn tiếp nhận loại thống khổ này!” Tôn Cường cắn răng."),t("br"),t("br"),n._v("“Đơn giản!”"),t("br"),t("br"),n._v("Nhẹ nhàng cười một tiếng, Trương Huyền bàn tay lần nữa vẫy."),t("br"),t("br"),n._v("Phần phật!"),t("br"),t("br"),n._v("Trên đất kim hồn tỏa, lập tức bay lên, trường xà đồng dạng mang theo vẻ dữ tợn."),t("br"),t("br"),n._v("Không nghĩ tới trước mắt vị này, thế mà cùng một cái hạ nhân thương nghị, xử trí như thế nào hắn, Trung Thanh Vương khí sắc mặt đỏ lên, hét lớn một tiếng: “Còn chưa động thủ!”"),t("br"),t("br"),n._v("Soạt!"),t("br"),t("br"),n._v("Nương theo phân phó của hắn, vừa rồi xông tới tất cả hộ vệ, lập tức gầm thét vọt tới."),t("br"),t("br"),n._v("“A!”"),t("br"),t("br"),n._v("Lười nhác xoay người, Trương Huyền bàn chân trên mặt đất đạp mạnh."),t("br"),t("br"),n._v("Soạt!"),t("br"),t("br"),n._v("Một đạo gợn sóng theo dưới chân lấp lánh mà ra, thời gian nháy mắt, bên trong đại sảnh trận pháp giống như là bị kích hoạt, đem tất cả hộ vệ ngăn tại bên ngoài, vô luận như thế nào chạy nước rút, đều tới không đến trước mặt."),t("br"),t("br"),n._v("“Ngươi trong mắt ta cũng không tính là cái gì, thật sự cho rằng một chút a miêu a cẩu cũng có thể ngăn cản được ta?”"),t("br"),t("br"),n._v("Nhìn về phía trước mắt Trung Thanh Vương, Trương Huyền mí mắt vừa nhấc."),t("br"),t("br"),n._v("Đem Chiến sư đường tất cả điện đường toàn bộ học một lần, thực lực của hắn, đã đạt đến Nguyên Thần cảnh vô địch tình trạng, đối phương cho dù không yếu, cùng hắn so ra, cũng không tính được cái gì."),t("br"),t("br"),n._v("Soạt!"),t("br"),t("br"),n._v("Lời nói kết thúc, kim hồn tỏa lần nữa thẳng tắp dựng thẳng lên, tựa như một con rồng lớn, bất cứ lúc nào cũng sẽ đem người thôn phệ."),t("br"),t("br"),n._v("“Phó đường chủ cứu ta...”"),t("br"),t("br"),n._v("Biết không phải là đối thủ, trước mắt vị thanh niên này, mặc dù tuổi không lớn lắm, thực lực lại vượt quá tưởng tượng ra, Điền Thanh cũng lại không để ý tới mặt mũi, lên tiếng hét lớn."),t("br"),t("br"),n._v("“Dừng tay đi, nếu không, ta liền hạ danh sư bắt lệnh, đưa ngươi trói lại...”"),t("br"),t("br"),n._v("Điền Thanh cũng nhìn ra Trung Thanh Vương không phải là đối thủ, lắc đầu, tiến về phía trước một bước, bàn tay dựng thẳng lên, đang nghĩ đánh gãy Trương Huyền công kích, liền nghe “Hô!” Một tiếng, một cái to lớn lô đỉnh không biết từ chỗ nào bay tới."),t("br"),t("br"),n._v("Phù phù!"),t("br"),t("br"),n._v("Trực tiếp nện ở trên đầu, chỉ một chút, liền nằm trên mặt đất, biến thành một cái “Đại” chữ."),t("br"),t("br"),n._v("“Chủ nhân, ngươi muốn ta đánh gia hỏa ở đâu? Nhìn ta không đem hắn luyện thành xỉ than!”"),t("br"),t("br"),n._v("“...” Trương Huyền."),t("br"),t("br"),n._v("“...” Điền Thanh Phó đường chủ."),t("br"),t("br"),n._v("P/S: Đỉnh đỉnh trở lại a~"),t("br"),t("br"),t("br"),t("br")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("a",{attrs:{href:"./chap-1156"}},[n._v("Chương trước")]),n._v("  |  "),t("a",{attrs:{href:"./chap-1158"}},[n._v("Chương sau")])])])}),[],!1,null,null,null);h.default=c.exports}}]);