(window.webpackJsonp=window.webpackJsonp||[]).push([[1902],{2256:function(n,h,i){"use strict";i.r(h);var t=i(44),c=Object(t.a)({},(function(){var n=this,h=n.$createElement,i=n._self._c||h;return i("ContentSlotsDistributor",{attrs:{"slot-key":n.$parent.slotKey}},[i("h1",{attrs:{id:"chuong-634-chien-su"}},[i("a",{staticClass:"header-anchor",attrs:{href:"#chuong-634-chien-su"}},[n._v("#")]),n._v(" Chương 634: Chiến sư")]),n._v(" "),i("hr"),n._v(" "),i("p",[i("br"),i("br"),n._v("Nhắm mắt lại đánh?"),i("br"),i("br"),n._v("Diệp Vấn Thiên, Vệ Giang đồng thời ngẩn ngơ."),i("br"),i("br"),n._v("Đây là cái chiêu số gì? Chẳng lẽ... Là bí pháp nào đó?"),i("br"),i("br"),n._v("“Đúng! Hắn con mắt nhắm, vẻ mặt khó xử, hình như cùng mọi người luận võ, quá mức đơn giản, mười phần không tình nguyện, sau đó... Vừa ra tay, chín cái Hợp Linh cảnh, liền toàn bộ nằm trên đất...”"),i("br"),i("br"),n._v("Hộ vệ đem mới vừa mới nhìn đến cảnh tượng, một năm một mười nói ra."),i("br"),i("br"),n._v("Diệp Vấn Thiên, Vệ Giang, hai mặt nhìn nhau..."),i("br"),i("br"),n._v("Còn có loại này thao tác?"),i("br"),i("br"),n._v("Trọc Thanh cảnh đỉnh phong, đem chín vị Hợp Linh cảnh gia hỏa, nhắm mắt lại từng cái đánh ngã, vẻ mặt nhẹ nhõm không nói, cũng bởi vì đối phương quá yếu mà cảm thấy khó xử..."),i("br"),i("br"),n._v("Mẹ nó, không phải là đang nói truyện cười chứ?"),i("br"),i("br"),n._v("“Thật sự...” Hộ vệ vẻ mặt đỏ lên, hắn biết nói ra khẳng định không có người tin tưởng, nhưng chính là sự thật."),i("br"),i("br"),n._v("“Vượt cấp khiêu chiến, không có bất kỳ cái gì áp lực, chẳng lẽ...” Vệ Giang con ngươi co rụt lại."),i("br"),i("br"),n._v("“Vệ sư nghĩ tới điều gì?”"),i("br"),i("br"),n._v("Diệp Vấn Thiên nhịn không được nói."),i("br"),i("br"),n._v("“Bệ hạ có thể từng nghe nói qua ?” Chần chờ một chút, Vệ Giang chậm rãi nói."),i("br"),i("br"),n._v("“Ngươi thế nhưng là nói, danh sư bên trong ti chiến Chiến sư?”"),i("br"),i("br"),n._v("Diệp Vấn Thiên sững sờ."),i("br"),i("br"),n._v("“Không sai!”"),i("br"),i("br"),n._v("Vệ Giang nhẹ gật đầu: “Danh sư mặc dù mệnh danh cùng cấp bậc vô địch, nhưng chủ yếu nhất chức trách, là giáo thư dục nhân, giáo dục ra vô số cường giả, để nhân tộc càng ngày càng cường đại. Nhưng là... Muốn cùng Dị Linh tộc chiến đấu, bảo hộ nhân loại, không có sức chiến đấu mạnh nhất, cùng cường hãn nhất công kích, là không thể nào, Chiến sư liền vì vậy mà sinh!”"),i("br"),i("br"),n._v("“Chiến sư là ti chiến danh sư, cẩn thận nói đến, lại cùng danh sư khác biệt! Bình thường danh sư, mỗi cao một cái cấp bậc, cần đồng dạng phụ trợ chức nghiệp, bọn hắn không cần, chỉ cần thông qua sức chiến đấu khảo hạch, liền có thể tấn cấp cấp bậc cao hơn... Đương nhiên, loại này sức chiến đấu khảo hạch cũng rất khó! Một vạn cái danh sư, đều chưa hẳn có thể thông qua một người, hơn nữa cơ bản đều là thể chất đặc thù, hoặc là đặc thù huyết mạch, vừa ra đời liền nắm giữ vượt cấp khiêu chiến năng lực...”"),i("br"),i("br"),n._v("“Vị này Trương sư, nhờ vào Trọc Thanh cảnh liền có thể chiến thắng chín vị Hợp Linh cảnh, sẽ không sẽ... Nắm giữ Chiến sư thiên phú?”"),i("br"),i("br"),n._v("Vệ Giang con mắt tỏa ánh sáng."),i("br"),i("br"),n._v("Nếu như nói danh sư vạn người không được một, Chiến sư, thì là danh sư bên trong ngàn dặm mới tìm được một tồn tại, mỗi một cái, đều cùng cấp bậc chân chính vô địch, thậm chí có thể vượt cấp chiến đấu."),i("br"),i("br"),n._v("Hồng Viễn danh sư học viện thành lập mấy ngàn năm, đến bây giờ, tuyển bạt ra Chiến sư số lượng, chỉ đếm được trên đầu ngón tay."),i("br"),i("br"),n._v("Nếu như... Tại Huyễn Vũ đế quốc xuất hiện một vị, toàn bộ đế quốc đều lại bởi vì hắn, mà lên như diều gặp gió."),i("br"),i("br"),n._v("“Vừa nói như vậy, ngược lại... Thật là có khả năng!” Diệp Vấn Thiên sững sờ, cũng là con mắt trợn tròn."),i("br"),i("br"),n._v("“Đi qua nhìn một chút, bất kể có phải hay không là, đối với chúng ta Huyễn Vũ đế quốc tới nói, đều chỉ có thể là chuyện tốt!”"),i("br"),i("br"),n._v("Vệ Giang đứng dậy, vội vã hướng Thái tử cung điện đi đến."),i("br"),i("br"),n._v("Diệp Vấn Thiên cũng chặt theo sau."),i("br"),i("br"),n._v("..."),i("br"),i("br"),n._v("Một ngụm trọc khí phun ra, Trương Huyền vỗ tay một cái, nhịn không được lắc đầu."),i("br"),i("br"),n._v("Phong nguyên, lặn gió, từng tháng ba đại đế quốc chín vị danh sư, lúc này tất cả đều nằm trên mặt đất, không ngừng rên rỉ."),i("br"),i("br"),n._v("Kỳ thật không cần thư viện, nhờ vào hắn thực lực bản thân, chín người liên hợp cũng khẳng định không phải là đối thủ, có điều, có thiếu hụt có thể mượn dùng, đương nhiên sẽ không keo kiệt."),i("br"),i("br"),n._v("“Các ngươi thua...”"),i("br"),i("br"),n._v("Không tiếp tục để ý bị thương mọi người, Trương Huyền ngẩng đầu nhìn về phía La Chiêu đám người: “Hiện tại năm cái Trì Nhãn, đều thuộc về chúng ta Hồng Phong đế quốc tất cả!”"),i("br"),i("br"),n._v("“...” Thân thể nhoáng một cái, La Chiêu, Phùng Vũ, Trần Việt tất cả đều cảm giác đến sắp điên rồi."),i("br"),i("br"),n._v("Đây chính là bọn hắn ba hạng đầu!"),i("br"),i("br"),n._v("Vì trợ giúp tấn cấp, còn chuyên môn mua sắm Hợp Linh đan... Kết quả, chín người liên thủ, liền một cái Trọc Thanh cảnh đều đánh không lại!"),i("br"),i("br"),n._v("Nắm vững thắng lợi tỷ thí, liền nhân gia quần áo đều không có dính vào một điểm..."),i("br"),i("br"),n._v("Ba đại các nước chư hầu liên thủ, vốn nghĩ có thể ép Hồng Phong đế quốc thiệt thòi lớn, cuối cùng, bọn hắn liền một cái Trì Nhãn đều không được đến..."),i("br"),i("br"),n._v("Cái này gọi chuyện gì!"),i("br"),i("br"),n._v("Càng nghĩ càng thấy đến buồn bực."),i("br"),i("br"),n._v("Sớm biết liền không tìm phiền toái, không uổng phí những chuyện này, chỉ bằng vào phân phối, một cái các nước chư hầu, một cái Trì Nhãn tổng điểm đi!"),i("br"),i("br"),n._v("Ăn trộm gà bất thành còn mất nắm gạo không nói, hiện tại từ trên xuống dưới, từ già đến trẻ, đều bị người đánh một trận, mặt mũi bầm dập, hoàn toàn thay đổi, trở về làm sao cùng những người khác bàn giao?"),i("br"),i("br"),n._v("“Khụ khụ, Trương sư, La sư bọn hắn, cũng không phải có ý làm khó, chỉ là vì bản thân hậu bối, có thể tranh thủ thêm mấy cái danh ngạch mà thôi. Lại nói, Hóa Thanh trì mười năm mở một lần, cũng có lợi cho Huyễn Vũ đế đô cùng cái khác các nước chư hầu đoàn kết... Ngươi nhìn, Trì Nhãn có thể hay không để cho ra hai cái! Cũng tốt cho bọn hắn có cái bàn giao.”"),i("br"),i("br"),n._v("Diệp Tiền vẻ mặt cây đu đủ bộ dáng, nhẫn nhịn không biết bao lâu, đạo."),i("br"),i("br"),n._v("Chuyên môn chạy tới phân phối danh ngạch, kết quả để ba đại các nước chư hầu, thất bại tan tác mà quay trở về, một cái danh ngạch cũng không chiếm được, thực sự không thể nào nói nổi."),i("br"),i("br"),n._v("Phụ vương, Vệ đường chủ nơi đó đều không có cách nào bàn giao."),i("br"),i("br"),n._v("Nhưng... Nhân gia là tỷ thí công bình, tỷ thí trước, không nói gì, hiện tại cũng kết thúc ra định luận, nhưng mở miệng thuyết phục, xấu hổ đến cực điểm."),i("br"),i("br"),n._v("“Cho hai cái? Cũng tốt, liền hai cái, Đế đô cái kia danh sư cũng muốn cùng bọn hắn cùng hưởng, chúng ta Hồng Phong đế quốc ba người, muốn ba cái Trì Nhãn!” Biết thật đem Trì Nhãn toàn bộ cầm đi, vị này thái tử cũng không tiện làm, Trương Huyền gật đầu đồng ý."),i("br"),i("br"),n._v("Bọn hắn ba hạng đầu, ba cái Trì Nhãn liền đủ, nhiều cũng là lãng phí, quả thực không cần thiết che chở quá nhiều."),i("br"),i("br"),n._v("“Đa tạ!” Không nghĩ tới đối phương tốt như vậy nói chuyện, Diệp Tiền nhẹ gật đầu, vẫy tay một cái: “Đi đem ta món kia kim lân giáp cho lấy tới!”"),i("br"),i("br"),n._v("“Vâng!”"),i("br"),i("br"),n._v("Một tên hộ vệ xoay người đi ra ngoài, thời gian không dài, đem một bộ khôi giáp cầm tới."),i("br"),i("br"),n._v("“Đây là Linh cấp trung phẩm đỉnh phong khôi giáp, do Hóa Phàm lục trọng Bạo Tuyết Kim Lân thú da thú chế tạo thành, ta cùng Trương sư mới quen đã thân, liền đưa cho ngươi!”"),i("br"),i("br"),n._v("Vẫy tay một cái, hộ vệ đem khôi giáp đưa tới."),i("br"),n._v(" “Cái này... Làm sao có ý tứ!” Trương Huyền sững sờ."),i("br"),i("br"),n._v("“Không cần khách khí!”"),i("br"),i("br"),n._v("Diệp Tiền vẫy tay: “Nếu như ngươi không muốn, ngược lại là ta thật không tiện!”"),i("br"),i("br"),n._v("“Đa tạ thái tử!” Biết đối phương là bởi vì hai cái Trì Nhãn, cho mình bồi thường, chối từ phản ngược lại không tiện, lúc này gật gật đầu thu nhập nhẫn."),i("br"),i("br"),n._v("“Trương sư, ngươi nguyện ý phân ra Trì Nhãn, chúng ta vô cùng cảm kích, cái này có hai cái Hợp Linh đan, nguyện ý làm làm thù lao đưa tặng, mong rằng vui vẻ nhận!”"),i("br"),i("br"),n._v("La Chiêu cũng đi tới, đưa đến một cái bình ngọc."),i("br"),i("br"),n._v("Thái tử đều có chỗ bày tỏ, bọn hắn lấy được ân huệ, lại không biết tốt xấu, không cần sống."),i("br"),i("br"),n._v("Ba đại các nước chư hầu tổng cộng mua mười một viên Hợp Linh đan, tăng lên mọi người về sau, còn thừa lại hai cái."),i("br"),i("br"),n._v("“Ừm!”"),i("br"),i("br"),n._v("Tiếp nhận đan dược, Trương Huyền tiện tay trang."),i("br"),i("br"),n._v("Có chơi có chịu, thua chính là thua, coi như đối phương lại không tình nguyện, cũng môi giới pháp."),i("br"),i("br"),n._v("Đưa cho bọn họ hai cái Trì Nhãn, có thể nói là rất đại ân huệ, thu hai cái Hợp Linh đan làm thù lao, không tính là gì."),i("br"),i("br"),n._v("Lại nói, thu đồ vật đại biểu ân oán thanh toán xong, đối phương cũng không cần nợ nhân tình, nhất cử lưỡng tiện."),i("br"),i("br"),n._v("“Trì Nhãn cùng danh ngạch đều phân phối hết, tiệc tối chính thức bắt đầu, hi vọng mọi người có thể thỏa thích sướng vui mừng, chơi vui vẻ!”"),i("br"),i("br"),n._v("Diệp Tiền vung tay lên, lập tức có không ít dáng người uyển chuyển cung nữ, bưng lên vô số mỹ thực."),i("br"),i("br"),n._v("Vị này thái tử điện hạ quả thực ưa thích hưởng thụ, bên trên mỹ thực, mỗi một cái đều có giá trị không nhỏ, ăn một bữa xuống, rất nhiều phú hào đều có thể táng gia bại sản."),i("br"),i("br"),n._v("“La Tuyền, Tất Giang Hải, cái này hai cái Hợp Linh đan, các ngươi một người một cái, đêm nay trở về liền dùng đi!”"),i("br"),i("br"),n._v("Cổ tay khẽ đảo, Trương Huyền đem vừa rồi La sư đám người cho hai cái Hợp Linh đan đưa tới."),i("br"),i("br"),n._v("Thứ này mặc dù có thể để cho Hợp Linh cảnh cường giả vô điều kiện tấn thăng một cái tiểu cấp đừng, nhưng đối với hắn mà nói... Hiệu quả không lớn!"),i("br"),i("br"),n._v("Chỉ cần sưu tập chỉnh tề Hợp Linh cảnh Thiên Đạo công pháp, đột phá đối với hắn mà nói cùng uống nước không có khác nhau quá nhiều, bản thân dùng cũng là lãng phí."),i("br"),i("br"),n._v("“Cho chúng ta?”"),i("br"),i("br"),n._v("La Tuyền, Tất Giang Hải đồng thời sững sờ."),i("br"),i("br"),n._v("Đan dược này trân quý, bọn hắn không phải không biết, đối phương một cái đều không cần, trực tiếp đưa cho mình hai người, cái này..."),i("br"),i("br"),n._v("“Ừm, nhanh lên tăng thực lực lên, đi vào Hóa Thanh trì, cũng không lãng phí ta thật vất vả cho các ngươi tranh thủ danh ngạch!” Trương Huyền thản nhiên nói."),i("br"),i("br"),n._v("“Đa tạ Trương sư!”"),i("br"),i("br"),n._v("Hốc mắt đỏ lên, hai người đồng loạt khom người."),i("br"),i("br"),n._v("Cái khác ba hạng đầu, vì phòng ngừa cạnh tranh, riêng phần mình thi triển thủ đoạn, mà Trương sư, rất mực khiêm tốn, thẳng thắn đối xử mọi người... Để cho người ta từ ở sâu trong nội tâm sinh ra cảm kích."),i("br"),i("br"),n._v("Trước đó La Tuyền, Tất Giang Hải, còn muốn lấy đi vào danh sư học viện về sau, tới phân cao thấp, bây giờ mới biết, không chỉ tu vi hiểu biết, ánh sáng lòng dạ liền so với đối phương kém nhiều lắm, hoàn toàn không có khả năng so sánh."),i("br"),i("br"),n._v("Nếu như hai người ý tưởng cho đối diện La sư các loại người biết được, khẳng định sẽ làm trận khóc ra thành tiếng."),i("br"),i("br"),n._v("Liền cái này hai hàng, còn rất mực khiêm tốn, thẳng thắn đối xử mọi người?"),i("br"),i("br"),n._v("Thẳng thắn em gái ngươi ah, không thấy được chúng ta bị chỉnh cỡ nào thảm?"),i("br"),i("br"),n._v("Lấy được nguyên bản thuộc về mình danh ngạch cùng Trì Nhãn, còn muốn cam tâm tình nguyện, đầy cõi lòng cảm kích... Có thể như thế vô sỉ hèn hạ, chỉ sợ cũng chỉ có cái này một cái..."),i("br"),i("br"),n._v("..."),i("br"),i("br"),n._v("Thái tử ngoài phủ đệ."),i("br"),i("br"),n._v("“Bệ hạ, vệ sư!”"),i("br"),i("br"),n._v("Nhìn thấy trước mắt hai bóng người, hộ vệ giật nảy mình."),i("br"),i("br"),n._v("“Ta vậy thì đi bẩm báo điện hạ để hắn ra nghênh tiếp...”"),i("br"),i("br"),n._v("Huyễn Vũ đế quốc rất có địa vị hai người, đã trễ thế như vậy đồng thời tới, đổi lại ai cũng sẽ hoảng sợ không dứt."),i("br"),i("br"),n._v("“Không cần, ngươi tiếp tục trông coi, ta cùng vệ sư vào xem!”"),i("br"),i("br"),n._v("Diệp Vấn Thiên khoát tay chặn lại, cùng Vệ Giang nhanh chân đi vào trong."),i("br"),i("br"),n._v("“Nếu như... Vị này Trương sư, thật nắm giữ Chiến sư thiên phú, làm sao bây giờ?”"),i("br"),i("br"),n._v("Đi vào sân nhỏ, vừa đi, Diệp Vấn Thiên bên cạnh nhịn không được hỏi."),i("br"),i("br"),n._v("“Còn có thể làm sao? Lập tức bẩm báo danh sư học viện, chuyên môn bồi dưỡng! Người tài giỏi như thế, đối Danh Sư đường trình độ trọng yếu, không cần ta nói, ngươi cũng hẳn phải biết!” Vệ Giang vẻ mặt nghiêm túc mà nói."),i("br"),i("br"),n._v("“Ừm!” Diệp Vấn Thiên gật đầu."),i("br"),i("br"),n._v("Mặc dù hắn không phải danh sư, nhưng thân là một nước chi chủ, đối chiến sư vẫn là biết một chút."),i("br"),i("br"),n._v("Mệnh danh danh sư bên trong chiến đấu chi vương, một khi xác nhận, coi như Hồng Viễn danh sư học viện viện trưởng, địa vị đều chưa hẳn so ra mà vượt!"),i("br"),i("br"),n._v("“Chỉ là... Chiến sư cần tuyển bạt, cùng một loạt thủ đoạn, chúng ta cứ như thế trôi qua, như thế nào xác nhận?”"),i("br"),i("br"),n._v("Diệp Vấn Thiên nhíu mày."),i("br"),i("br"),n._v("Chiến sư cần phải đặc biệt thủ đoạn xác nhận, dị thường phiền phức, không phải dễ dàng như vậy liền có thể hoàn thành."),i("br"),i("br"),n._v("“Cái này... Trước thấy được lại nói...” Vệ Giang chần chờ một chút, đang muốn nói tiếp, liền nghe đến phía trước tiếng bước chân truyền đến, ba bóng người đi tới cách đó không xa."),i("br"),i("br"),n._v("Thấy rõ bộ dáng, Diệp Vấn Thiên vội vàng tiến về phía trước một bước: “Diệp Vấn Thiên gặp qua sáu công chúa điện hạ! Lạc sư, hình sư cũng tại ah!”"),i("br"),i("br"),n._v("Không là người khác, chính là thay quần áo xong, tới tham gia yến hội Lục công chúa Ngọc Phi Nhi, Lạc Thất Thất cùng hình xa."),i("br"),i("br"),n._v("“Nguyên lai là vấn thiên bệ hạ, ta vừa vặn có việc muốn hỏi ngươi.”"),i("br"),i("br"),n._v("Nhìn thấy là hắn, Phi nhi công chúa lông mày giương lên: “Cái này Huyễn Vũ đế quốc, đến cùng làm sao quản lý? Nếu như không muốn làm, có thể nói một tiếng...”"),i("br"),i("br"),n._v("Convert by: ThấtDạ"),i("br"),i("br"),i("br"),i("br")]),n._v(" "),i("hr"),n._v(" "),i("p",[i("a",{attrs:{href:"./chap-642"}},[n._v("Chương trước")]),n._v("  |  "),i("a",{attrs:{href:"./chap-644"}},[n._v("Chương sau")])])])}),[],!1,null,null,null);h.default=c.exports}}]);