(window.webpackJsonp=window.webpackJsonp||[]).push([[1907],{2262:function(n,h,t){"use strict";t.r(h);var g=t(44),i=Object(g.a)({},(function(){var n=this,h=n.$createElement,t=n._self._c||h;return t("ContentSlotsDistributor",{attrs:{"slot-key":n.$parent.slotKey}},[t("h1",{attrs:{id:"chuong-639-xui-xeo-ngo-chan-ha"}},[t("a",{staticClass:"header-anchor",attrs:{href:"#chuong-639-xui-xeo-ngo-chan-ha"}},[n._v("#")]),n._v(" Chương 639: Xui xẻo Ngô Chấn (hạ)")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("br"),t("br"),n._v("Chương 639: Xui xẻo Ngô Chấn (hạ)"),t("br"),t("br"),n._v("“Uống?” Ngô Chấn sững sờ: “Ngươi thuốc này không phải thoa ngoài da sao?”"),t("br"),t("br"),n._v("Ngày hôm qua vị Tất sư, bị một chủy thủ đâm xuyên cánh tay, thoa ngoài da liền lên hiệu quả, làm sao đến tự mình biến thành uống thuốc?"),t("br"),t("br"),n._v("“Cái này dược căn cứ thương thế khác biệt, phân chia uống thuốc cùng thoa ngoài da, một khi sử dụng sai, chẳng những không có hiệu quả, thậm chí còn có thể để thương thế tăng thêm!” Trương Huyền thuận miệng nói."),t("br"),t("br"),n._v("Dù sao cái này hồ lô rượu bên trong “Rượu thuốc” cũng không nhiều, trước lừa gạt lại nói, chờ hắn chân chính muốn tra thời điểm, rượu thuốc cũng bị mất, tự nhiên cũng không tra ra."),t("br"),t("br"),n._v("“Còn có loại thuyết pháp này?” Ngô Chấn có chút không tin."),t("br"),t("br"),n._v("“Ta hôm qua sử dụng, ngươi cũng không phải không thấy được, hiệu quả cực giai... Không có có hiệu quả, khẳng định là dùng sai phương pháp!”"),t("br"),t("br"),n._v("Trương Huyền mặt đều không mang theo đỏ: “Ngươi vừa rồi cũng đã nói, không phải ngươi tại dùng, khẳng định là dùng người kia, đem thoa ngoài da cùng uống thuốc, tính sai, mới không có hiệu quả!”"),t("br"),t("br"),n._v("“Thật sao?” Ngô Chấn còn có chút chần chờ."),t("br"),t("br"),n._v("“Uống một ngụm, uống một ngụm!” Trương Huyền cười nói."),t("br"),t("br"),n._v("“Tốt, nếu như không dùng được, xem ta như thế nào thu thập ngươi!”"),t("br"),t("br"),n._v("Hừ một tiếng, Ngô Chấn giơ lên hồ lô rượu, uống một ngụm."),t("br"),t("br"),n._v("đọc ngantruyen.com/"),t("br"),n._v("Rượu vào cổ họng, lập tức cảm thấy một dòng nước nóng dọc theo bụng dưới chảy qua toàn thân, một lát sau, liền cảm thấy trên người trước đó đau đớn, máu ứ đọng địa phương, hoàn hảo không chút tổn hại, không có một điểm thương thế."),t("br"),t("br"),n._v("“Cái này...”"),t("br"),t("br"),n._v("Con mắt trợn tròn, Ngô Chấn tràn đầy không thể tưởng tượng nổi."),t("br"),t("br"),n._v("Tự thể nghiệm, mới biết được cái này thánh dược chữa thương đáng sợ."),t("br"),t("br"),n._v("Hôm qua bị đánh thê thảm vô cùng, uống thuốc thoa ngoài da dược vật đều dùng, kết quả không có nửa điểm hiệu quả, hôm nay vẫn là chịu lấy vành mắt đỏ tới, vốn cho rằng, Trương sư cái này Thánh dược là giả, không nghĩ tới, lợi hại như thế."),t("br"),t("br"),n._v("Một cái liền khôi phục như lúc ban đầu, thương thế toàn bộ tốt... Đây cũng quá nghịch thiên đi!"),t("br"),t("br"),n._v("“Ta nói không sai chứ!”"),t("br"),t("br"),n._v("Thấy thương thế của hắn hoàn hảo, Trương Huyền gật gật đầu: “Khẳng định là chữa thương người kia tính sai phương pháp, nếu không như vậy, ngươi đi hỏi một chút nàng tổn thương tới nơi nào, là bôi lên ở bên ngoài vẫn là uống thuốc, ta giúp ngươi phân tích một chút.”"),t("br"),t("br"),n._v("“Tốt!” Chần chờ một chút, Ngô Chấn gật gật đầu."),t("br"),t("br"),n._v("Việc quan hệ danh dự của hắn, bất kể nói thế nào, đều muốn biết rõ ràng, nếu không trắng lưng một cái lừa gạt xưng hào."),t("br"),t("br"),n._v("Đem hồ lô rượu cầm lấy, xoay người hướng Lục công chúa phương hướng đi đến, liền ôm quyền."),t("br"),t("br"),n._v("“Lục công chúa, ngươi trách oan ta, bình thuốc này rượu cũng không phải là vô dụng, mà là ngươi dùng sai phương pháp!”"),t("br"),t("br"),n._v("Lấy được Trương sư chỉ điểm, Ngô Chấn tràn đầy tự tin, mọi cử động mang theo phong độ."),t("br"),t("br"),n._v("“Dùng sai phương pháp?” Lục công chúa thấy cái tên này cùng Trương sư hàn huyên một hồi, liền tự tin như vậy, đôi mi thanh tú nhăn lại."),t("br"),t("br"),n._v("“Không sai, ngươi liền nói cho ta biết, ngươi tổn thương tới nơi nào đi, yên tâm, ta chỉ cần nhìn lên một cái, xác định bệnh căn, liền có thể biết là thoa ngoài da vẫn là uống thuốc, cam đoan ngươi lập tức là tốt rồi!”"),t("br"),t("br"),n._v("Ngẩng đầu lên sọ, Ngô Chấn cảm thấy mình lần nữa thắng được tín dự."),t("br"),t("br"),n._v("“Làm bị thương đây? Còn nhìn lên một cái?”"),t("br"),t("br"),n._v("Lục công chúa vẻ mặt như là nhỏ máu."),t("br"),t("br"),n._v("Ngươi - hắn - mẹ cố ý đúng không hả?"),t("br"),t("br"),n._v("Thương thế của ta là Trương sư đánh, ngươi cùng hắn hàn huyên nửa ngày, liền chạy tới hỏi thăm, còn phải xem một cái..."),t("br"),t("br"),n._v("Nói cách khác, ngươi muốn nhìn ta... Cái mông?"),t("br"),t("br"),n._v("“Không sai, chỉ cần xác định vị trí cùng tổn thương tình huống, ta liền có thể xác định là thoa ngoài da vẫn là uống thuốc, ngươi hôm qua khẳng định là dùng sai phương pháp, mới không có hiệu quả...”"),t("br"),t("br"),n._v("Cười cười, Ngô Chấn chính muốn tiếp tục đem lời nói đùa, làm cho đối phương biết, chuyện ngày hôm qua là oan uổng hắn, liền cảm thấy mắt tối sầm lại, một cái bàn tay thẳng tắp đánh tới."),t("br"),t("br"),n._v("Ba!"),t("br"),t("br"),n._v("Vang dội bạt tai, đánh tại nguyên chỗ chuyển hai vòng, vẻ mặt choáng váng."),t("br"),t("br"),n._v("“Ngươi muốn nhìn, ta hôm nay liền đánh chết ngươi...”"),t("br"),t("br"),n._v("Khuôn mặt xanh xám, đối lấy tên trước mắt, Lục công chúa một cước đạp tới."),t("br"),t("br"),n._v("Bành!"),t("br"),t("br"),n._v("Còn không có kịp phản ứng, Ngô Chấn bay ngược mà ra, thẳng tắp quẳng xuống đất."),t("br"),t("br"),n._v("“Lục công chúa, ngươi tại sao đánh ta? Ta làm sao vậy...” Ngô Chấn khóc."),t("br"),t("br"),n._v("Hảo ý đem chính mình cũng luyến tiếc dùng rượu thuốc, đưa cho trước mắt vị công chúa này, vốn cho rằng có thể chiếm được vui lòng, kết quả hôm qua bị đánh một trận, hôm nay thật vất vả biết rõ ràng nguyên do, lại tự mình thí nghiệm, lại bị đánh một trận..."),t("br"),t("br"),n._v("Đến cùng chuyện gì xảy ra?"),t("br"),t("br"),n._v("Ta thật là một mảnh hảo tâm, thế nào liền xui xẻo như vậy!"),t("br"),t("br"),n._v("“Lạc Thất Thất, mau giúp ta cầu xin...”"),t("br"),t("br"),n._v("Giãy dụa lấy đứng lên, thấy Lục công chúa còn nổi giận đùng đùng muốn vọt qua đến, tiếp tục đánh, Ngô Chấn giật nảy mình, vội vàng xin giúp đỡ."),t("br"),t("br"),n._v("“Liền Lục công chúa cũng dám đùa giỡn, đáng đời!” Lạc Thất Thất khẽ nói."),t("br"),t("br"),n._v("Vừa rồi đối thoại, nàng cũng nghe đến, kẻ trước mắt này, thế mà muốn nhìn Lục công chúa cái mông, đừng nói chỉ là bị đánh, bị tại chỗ đánh chết đều là nhẹ."),t("br"),t("br"),n._v("“Đùa giỡn Lục công chúa, Ngô Chấn, ngươi tự tìm cái chết!”"),t("br"),t("br"),n._v("Một bên Hình Viễn giờ mới hiểu được, kẻ trước mắt này đã làm gì, rít lên một tiếng, ma quyền sát chưởng liền muốn động thủ."),t("br"),t("br"),n._v("Hắn nhưng là Lục công chúa trung thành cầm giữ độn... Một mực như hình với bóng, ngươi cái tên này thế mà liền nàng đều dám đùa giỡn, là không có đem ta để vào mắt ah!"),t("br"),t("br"),n._v("“...” Ngô Chấn khóc."),t("br"),t("br"),n._v("Ta... Thật chỉ là muốn làm việc tốt..."),t("br"),t("br"),n._v("“Cái này...”"),t("br"),t("br"),n._v("Nhìn thấy Ngô Chấn đi qua, bị một chầu cuồng ẩu, Trương Huyền không hiểu rõ nổi, không hiểu ra sao."),t("br"),t("br"),n._v("Không hổ là danh sư học viện tới thiên tài, quả nhiên hỉ nộ vô thường, không thể dùng lẽ thường phỏng đoán."),t("br"),t("br"),n._v("..."),t("br"),t("br"),n._v("“Khụ khụ, chư vị như là đã đến đông đủ, chúng ta liền đi Hóa Thanh trì!”"),t("br"),t("br"),n._v("Cuối cùng, Diệp Vấn Thiên bệ hạ bây giờ nhìn không nổi nữa, cắt ngang mấy vị thiên tài."),t("br"),n._v(" “Đi thôi!”"),t("br"),t("br"),n._v("Biết thời gian không còn sớm, mọi người không nói thêm lời, đi theo sát."),t("br"),t("br"),n._v("Hóa Thanh trì không tại hoàng cung, thậm chí đều không tại Huyễn Vũ đế đô bên trong, mà là tại Thanh Tắc sơn cùng Long Lân hà giáp giới địa phương, khoảng cách Đế đô không xa, có huyễn Vũ Hoàng phòng trọng binh trấn giữ."),t("br"),t("br"),n._v("Mọi người đạp vào sớm liền chuẩn bị xong linh thú phi hành, phi hành mấy chục phút, liền thấy một chỗ linh khí hội tụ chi xuất hiện ở trước mắt."),t("br"),t("br"),n._v("Thanh Tắc sơn như là chiếm cứ tại mặt đất cự long, Long Lân hà thì như một đạo ngân hà, khảm nạm trên mặt đất."),t("br"),t("br"),n._v("Rồng uống trường hà, tinh bố thiên hạ."),t("br"),t("br"),n._v("Nhìn thoáng qua, liền biết nơi đây là cái tự nhiên Tụ Linh trận, hơn nữa, rồng mang theo hành vân bố vũ năng lực, bất cứ lúc nào cũng sẽ đằng không mà lên, bởi vậy, nơi này linh khí càng thêm mềm nhẹ, đối linh hồn, có càng lớn trợ giúp."),t("br"),t("br"),n._v("“Lợi hại!”"),t("br"),t("br"),n._v("Nhìn một hồi, Trương Huyền con mắt tỏa sáng."),t("br"),t("br"),n._v("Khó trách có thể trở thành một cái nhị đẳng đế quốc Đế đô chỗ trên mặt đất, cái này Hóa Thanh trì, quả nhiên không tầm thường."),t("br"),t("br"),n._v("So với hắn làm cái kia Tụ Linh trận, mạnh hơn lớn hơn nhiều lắm."),t("br"),t("br"),n._v("Chậm rãi hạ xuống."),t("br"),t("br"),n._v("“Phía trước chính là Hóa Thanh trì, tiến trước khi đi, ta sẽ căn cứ trước đó định thật danh ngạch cùng thời gian, cấp cho chư vị Thanh Thần hương!”"),t("br"),t("br"),n._v("Diệp Vấn Thiên giải thích một câu."),t("br"),t("br"),n._v("Nương theo lời của hắn, một tên hộ vệ đi tới, mang ròng rã ba mươi cây Thanh Thần hương."),t("br"),t("br"),n._v("Thứ này cùng bình thường hương liệu khác biệt, từ xa nhìn lại ngược lại giống như một loại kim loại, có thể lơ lửng ở trong nước, tự chủ thiêu đốt, xác định trì trong mắt linh khí nhiều ít."),t("br"),t("br"),n._v("Hồng Phong đế quốc lấy được ba cái Trì Nhãn, tổng cộng có chín cái Thanh Thần hương, Trương Huyền lấy ra ba cây."),t("br"),t("br"),n._v("Một cái Trì Nhãn chỉ có thể đợi ba ngày, đối với hắn mà nói, ba cây như vậy đủ rồi."),t("br"),t("br"),n._v("Đem Thanh Thần hương chia xong, đã cách giữa trưa tới gần."),t("br"),t("br"),n._v("“Trương sư, ta có việc muốn cùng ngươi tâm sự!”"),t("br"),t("br"),n._v("Thấy Hóa Thanh trì lập tức mở ra, Trương Huyền chính muốn cùng Diệp Tiền đám người đi qua, liền nghe đến một cái nhàn nhạt truyền âm vang ở bên tai, quay đầu nhìn lại, chỉ thấy Hình Viễn chẳng biết lúc nào đi tới cái trước mặt."),t("br"),t("br"),n._v("Trước đó Ngô Chấn tìm, hiện tại gia hỏa này lại tới, đến cùng muốn làm gì?"),t("br"),t("br"),n._v("Bất quá, đã đối phương có việc, cũng không tiện chối từ."),t("br"),t("br"),n._v("Hai người tới mọi người không thấy được một cái góc."),t("br"),t("br"),n._v("Mới vừa đứng vững, Hình Viễn liền nói thẳng: “Ta biết ngươi cùng Lục công chúa đánh cược!”"),t("br"),t("br"),n._v("“Đúng!” Trương Huyền cũng không có gì có thể phủ nhận."),t("br"),t("br"),n._v("Lần thứ nhất đánh cược thời điểm, cái tên này liền ở bên cạnh, tận mắt thấy mình thích Lục công chúa biến thành tự mình tỳ nữ."),t("br"),t("br"),n._v("“Tự mình nhận thua! Không nên bị Lục công chúa nhìn ra!”"),t("br"),t("br"),n._v("Hình Viễn nhìn qua."),t("br"),t("br"),n._v("“Nhận thua?” Trương Huyền lông mày nhíu một cái."),t("br"),t("br"),n._v("Còn tưởng rằng đối phương tìm tự mình làm gì, không nghĩ tới là muốn cho tự mình nhận thua."),t("br"),t("br"),n._v("“Không sai, chỉ cần nhận thua, điều kiện tùy ngươi mở!”"),t("br"),t("br"),n._v("Bàn tay lớn vẫy một cái, Hình Viễn khẽ nói."),t("br"),t("br"),n._v("“Theo ta mở?” Ánh mắt sáng lên."),t("br"),t("br"),n._v("“Không sai!”"),t("br"),t("br"),n._v("Hình Viễn gật đầu."),t("br"),t("br"),n._v("“Vậy thì tốt, ta cùng Lục công chúa đánh cược, đánh cược là một vạn linh thạch trung phẩm, cái này chắc hẳn ngươi cũng biết, lần này cá cược, cũng là như vậy... Như vậy đi, nếu như ngươi có thể cho ta một cái linh thạch thượng phẩm, nhận thua cũng không sao!”"),t("br"),t("br"),n._v("Trương Huyền thản nhiên nói."),t("br"),t("br"),n._v("Đối với hắn mà nói, hấp thu Hóa Thanh trì bên trong linh khí nhanh chậm, có thể tự mình khống chế, nếu như có thể bởi vậy lần nữa đến một cái linh thạch thượng phẩm, thật sự kiếm lời."),t("br"),t("br"),n._v("Mặc dù một cái linh thạch thượng phẩm, cũng giá trị một vạn viên trung phẩm, nhưng... Thật muốn mua, có tiền mà không mua được, muốn mua cũng mua không được."),t("br"),t("br"),n._v("“Một cái linh thạch thượng phẩm?”"),t("br"),t("br"),n._v("Hình Viễn khóe miệng giật một cái."),t("br"),t("br"),n._v("Thứ này quá mức trân quý, liền xem như gia tộc bọn họ, muốn muốn xuất ra, cũng không quá có thể."),t("br"),t("br"),n._v("“Không đồng ý liền thôi... Dù sao Lục công chúa thua, cũng sẽ đền ta một vạn viên linh thạch trung phẩm...”"),t("br"),t("br"),n._v("Trương Huyền đạo."),t("br"),t("br"),n._v("Mặc dù một vạn viên linh thạch trung phẩm so ra kém một cái thượng phẩm, nhưng là chí ít có thể bảo chứng tự mình không thua, đối phương không bỏ ra nổi đến, cũng không quan trọng."),t("br"),t("br"),n._v("“Tốt, ta đồng ý!”"),t("br"),t("br"),n._v("Hình Viễn cắn răng một cái: “Lần luyện tập này, chỉ phải hoàn thành nhiệm vụ, trở lại học viện, ta sẽ có một cái linh thạch thượng phẩm ban thưởng, cái này viên linh thạch có thể cho ngươi, bất quá... Ngươi phải giữ bí mật cho ta, không thể đem chuyện này nói ra!”"),t("br"),t("br"),n._v("“Thành giao!”"),t("br"),t("br"),n._v("Trương Huyền gật đầu."),t("br"),t("br"),n._v("Cùng lắm thì lãng phí ba ngày thời gian mà thôi, có thể được đến một cái linh thạch thượng phẩm, tuyệt đối kiếm bộn rồi."),t("br"),t("br"),n._v("Huống chi, làm như vậy còn không phải tội vị này Hình Viễn, càng có thể để cho vị kia Lục công chúa nguôi giận."),t("br"),t("br"),n._v("Hai người mới vừa thương nghị hết, liền nghe đến một hồi kịch liệt vang lên ầm ầm, ngay sau đó một luồng linh khí nồng nặc, thẳng tắp tán dật ra, giống như là một cái cự đại Tụ Linh trận, đột nhiên mở ra."),t("br"),t("br"),n._v("“Mau nhìn!”"),t("br"),t("br"),n._v("Trong đám người không biết ai hô lên."),t("br"),t("br"),n._v("Vội vàng hướng về phía trước nhìn lại, liền thấy một cái linh khí nồng nặc cột, bay thẳng lăng tiêu, một luồng để cho người ta thần thanh khí sảng khí tức, đập vào mặt, để cho người ta hút vào một ngụm, liền cảm thấy linh hồn không ngừng run rẩy, bất cứ lúc nào cũng sẽ đột phá."),t("br"),t("br"),n._v("Nơi xa mênh mông địa phương, một cái ao nước to lớn, chậm rãi xuất hiện, trong đó linh khí sôi trào, như là tạo thành từng đầu cự long, phát ra cực lớn gào thét, xem ra bất cứ lúc nào cũng sẽ phá không mà ra."),t("br"),t("br"),n._v("Ao nước chung quanh, xuất hiện mười cái lớn nhỏ tương đồng vòng xoáy, mỗi một cái, đều có đường kính chừng hai mét, an tĩnh khảm nạm tại ao nước chung quanh, như là vương miện bên trên khảm nạm bảo thạch."),t("br"),t("br"),n._v("“Hóa Thanh trì... Mở!”"),t("br"),t("br"),n._v("Thấy cảnh này, tất cả mọi người đồng thời nắm đấm xiết chặt."),t("br"),t("br"),n._v("Convert by: ThấtDạ"),t("br"),t("br"),t("br"),t("br")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("a",{attrs:{href:"./chap-647"}},[n._v("Chương trước")]),n._v("  |  "),t("a",{attrs:{href:"./chap-649"}},[n._v("Chương sau")])])])}),[],!1,null,null,null);h.default=i.exports}}]);