(window.webpackJsonp=window.webpackJsonp||[]).push([[1911],{2266:function(n,h,t){"use strict";t.r(h);var i=t(44),c=Object(i.a)({},(function(){var n=this,h=n.$createElement,t=n._self._c||h;return t("ContentSlotsDistributor",{attrs:{"slot-key":n.$parent.slotKey}},[t("h1",{attrs:{id:"chuong-642-truong-huyen-chet-roi"}},[t("a",{staticClass:"header-anchor",attrs:{href:"#chuong-642-truong-huyen-chet-roi"}},[n._v("#")]),n._v(" Chương 642: Trương Huyền chết rồi?")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("br"),t("br"),n._v("“Còn không có Lộ Xung lớn, xem ra còn kém xa lắm!”"),t("br"),t("br"),n._v("Nhớ tới Lộ Xung giống như núi nhỏ linh thể, Trương Huyền lắc đầu."),t("br"),t("br"),n._v("Năm, sáu mét đối đồng dạng vu hồn sư tới nói, là quái vật khổng lồ, nhưng cùng lúc trước Lộ Xung so, còn kém xa lắm."),t("br"),t("br"),n._v("“Tiếp tục hấp thu!”"),t("br"),t("br"),n._v("Lông mày giương lên, tiếp tục điên cuồng hấp thu."),t("br"),t("br"),n._v("Hắn bên này gia tốc, cách đó không xa phân thân, cũng bắt đầu gia tăng tốc độ."),t("br"),t("br"),n._v("Lại qua hai canh giờ, toàn bộ Hóa Thanh trì linh khí, mắt trần có thể thấy suy giảm, lại không có trước đó hung mãnh cùng xao động."),t("br"),t("br"),n._v("“Linh khí giảm bớt, khoảng cách mười mét liền kém một chút...”"),t("br"),t("br"),n._v("Nhìn thấy linh khí yếu bớt, hình như bất cứ lúc nào cũng sẽ bị hấp thu sạch sẽ, Trương Huyền tăng thêm tốc độ tốc độ."),t("br"),t("br"),n._v("Hắn hiện tại hồn thể khoảng cách mười mét, chỉ thiếu một chút, một khi đột phá cái này ràng buộc, khẳng định càng thêm cường đại, coi như không thể so với Lộ Xung, cũng không kém nhiều."),t("br"),t("br"),n._v("Dù sao, hắn đây là Thiên Đạo công pháp ngưng tụ hồn thể, bất kể đối với linh khí yêu cầu cùng bản thân tinh thuần lần, đều vượt xa quá đối phương."),t("br"),t("br"),n._v("Ầm ầm!"),t("br"),t("br"),n._v("Chính đang hấp thu, chỉ thấy một bên phân thân nhẹ nhàng nhoáng một cái, đột phá mười mét ràng buộc."),t("br"),t("br"),n._v("Thế mà so với hắn cái này bản tôn hấp thu còn nhanh hơn."),t("br"),t("br"),n._v("Trong lòng kỳ quái, đồng dạng linh hồn phân hai, tại sao lại còn nhanh hơn chính mình, cúi đầu xem xét, không còn gì để nói."),t("br"),t("br"),n._v("Chỉ thấy cái tên này, để Cửu Thiên Liên Thai ngưng tụ thân thể cũng đang không ngừng hấp thu."),t("br"),t("br"),n._v("Liên thai là thần vật, không e ngại ao nước cuồng bạo, coi như hình thể không lớn, hấp thu so hồn thể chậm, góp gió thành bão, cũng làm cho cái tên này so bản tôn nhanh hơn không ít."),t("br"),t("br"),n._v("Đi đến mười mét về sau, phân thân hồn thể, rõ ràng mạnh mẽ hơn không ít, cho người ta một loại kịch liệt áp bách, hấp thu linh khí tốc độ cũng nhanh hơn."),t("br"),t("br"),n._v("“Ta cũng tăng nhanh!”"),t("br"),t("br"),n._v("Biết thời gian không đợi người, Trương Huyền nào còn dám do dự, càng thêm điên cuồng cắn nuốt."),t("br"),t("br"),n._v("Bất quá, vô luận như thế nào tu luyện, cảm giác tốc độ đều so vừa rồi chậm không ít, ngẩng đầu nhìn lại, chỉ thấy phân thân chu vi tạo thành một vòng xoáy khổng lồ, bên cạnh mình linh khí, cơ bản đều bị cái tên này cắn nuốt sạch sẽ, khó trách tiến lên không rõ ràng."),t("br"),t("br"),n._v("Mười mét cùng không có đi đến mười mét, là cái cự đại đường ranh giới, đối phương hấp thu tốc độ, so hắn hiện tại, nhanh chí ít gấp ba trở lên."),t("br"),t("br"),n._v("Thấy cái tên này càng hấp thu càng nhanh, bất cứ lúc nào cũng sẽ đem trọn cái Hóa Thanh trì linh khí đều hút sạch, Trương Huyền càng nghĩ càng giận, một bàn tay quất vào trên đầu: “Lấy cái gì gấp? Chừa chút cho ta!”"),t("br"),t("br"),n._v("Cái gì ngoạn ý đây!"),t("br"),t("br"),n._v("Ngươi biết rõ ràng, ngươi là phân thân của ta!"),t("br"),t("br"),n._v("Trang cùng lão sói vẫy đuôi giống như, một quyền đem bản tôn đánh tìm không thấy phương hướng ngược lại cũng thôi, hấp thu cái linh khí, cũng đoạt!"),t("br"),t("br"),n._v("Quá không biết xấu hổ đi!"),t("br"),t("br"),n._v("Ta một cái chính trực danh sư, tại sao có thể có ngươi không biết xấu hổ như vậy phân thân!"),t("br"),t("br"),n._v("“Ta...” Bị quất một cái tát, phân thân vẻ mặt ủy khuất ngừng lại."),t("br"),t("br"),n._v("Trương Huyền để hắn trở lại thân thể, một lần nữa thu vào trữ vật giới chỉ, lúc này mới thở phào nhẹ nhõm."),t("br"),t("br"),n._v("“Hắc hắc, không có cạnh tranh, bản tôn hẳn là cũng có thể đột phá mười mét ràng buộc!”"),t("br"),t("br"),n._v("Nhẹ nhàng cười một tiếng, Trương Huyền hồn thể, toàn thân rất nhiều khiếu huyệt đột nhiên mở ra."),t("br"),t("br"),n._v("Trước đó phân thân một mực cùng hắn tranh đoạt, hiện tại gia hỏa này không tại, có thể biểu hiện ra tốc độ nhanh nhất hấp thu."),t("br"),t("br"),n._v("“Bắt đầu!”"),t("br"),t("br"),n._v("Một tiếng thấp giọng hô, khiếu huyệt điên cuồng hấp thu."),t("br"),t("br"),n._v("Ầm ầm!"),t("br"),t("br"),n._v("Linh khí mới vừa tiến vào hồn thể, chuyển hóa thành lực lượng, liền nghe đến một hồi kịch liệt oanh minh, dưới thân Hóa Thanh trì, đột nhiên lắc lư, đột nhiên nổ tung."),t("br"),t("br"),n._v("Rầm rầm!"),t("br"),t("br"),n._v("Ao nước bốn phía chảy xuôi, giống như là vỡ đê giang hà đồng dạng."),t("br"),t("br"),n._v("“Làm sao vậy?”"),t("br"),t("br"),n._v("Dừng lại hấp thu, vội vàng hướng chung quanh nhìn lại, chỉ thấy tất cả người tu luyện, đều bị bừng tỉnh, đồng loạt mở mắt."),t("br"),t("br"),n._v("Từng cái lơ ngơ, cũng không biết xảy ra chuyện gì."),t("br"),t("br"),n._v("“Nguy rồi, ao nước một khi chảy xuôi sạch sẽ, ta như thế lớn thân thể, tất nhiên có thể bị phát hiện, mau đi trở về!”"),t("br"),t("br"),n._v("Linh hồn giấu ở trong nước hồ, có linh khí vờn quanh, còn để cho người ta nhìn không ra, một khi nước trôi hết, còn muốn che lấp dấu vết, khẳng định liền không làm được, nhất định phải thừa dịp bây giờ trở lại thân thể mới được."),t("br"),t("br"),n._v("Lại nói, hồn phách ở đây, thân thể chính là cái xác không, người khác đều mở mắt, liền hắn đóng chặt lại “Tu luyện”, người ngu đi nữa cũng nhất định có thể nhìn xảy ra vấn đề."),t("br"),t("br"),n._v("..."),t("br"),t("br"),n._v("Hóa Thanh trì bên ngoài."),t("br"),t("br"),n._v("Diệp Vấn Thiên bệ hạ cùng Vệ Giang đường chủ, đang đứng tại cách đó không xa nhìn trước mắt cự ao nước lớn."),t("br"),t("br"),n._v("“Hóa Thanh trì, Đông Lâm Thanh Tắc sơn, phương tây tiếp Long Lân hà, hội tụ sơn hà chi khí, ngưng tụ địa mạch chi lực, có thể trong này tu luyện ba ngày, linh hồn tất nhiên gia tăng một cái cấp bậc, trở nên cứng cáp hơn.”"),t("br"),t("br"),n._v("Vuốt râu, Diệp Vấn Thiên vẻ mặt vui vẻ."),t("br"),t("br"),n._v("Huyễn Vũ đế quốc trấn quốc chí bảo, tự nhiên không có khả năng chỉ là để cho người ta rèn luyện linh hồn đơn giản như vậy, càng quan trọng hơn là, trong đó trải qua địa mạch rèn luyện qua linh khí, có thể để người ta linh hồn cứng cáp hơn, đối tu luyện về sau, có cực trợ giúp lớn!"),t("br"),t("br"),n._v("Nhất là đối đi vào Hóa Phàm bát trọng, Đạp Hư cảnh, có tác dụng cực lớn."),t("br"),t("br"),n._v("Đạp hư, đạp phá hư không, lấy hư không làm thềm bậc thang, hồn phách có thể mượn nhờ chung quanh sức mạnh tự nhiên, để thân thể trong thời gian ngắn, lơ lửng không trung."),t("br"),t("br"),n._v("Linh hồn không cường đại lời nói, đừng nói lơ lửng, khả năng căn bản là không cách nào đột phá quy nhất."),t("br"),n._v(" Hóa Thanh trì trì trong mắt linh khí, mặc dù đã suôn sẻ không ít, nhưng trải qua ao nước xao động, tính bền dẻo mười phần, hấp thu loại này linh khí, có thể để cho hồn phách, càng thêm cường đại, so hấp thu đơn thuần linh thạch, tốt hơn không biết bao nhiêu lần."),t("br"),t("br"),n._v("Chính vì vậy, cái này ao nước mới như thế nổi danh."),t("br"),t("br"),n._v("“Đúng vậy a!” Vệ sư gật gật đầu: “Hóa Thanh trì phía dưới nhưng thật ra là cái cự đại núi lửa, hội tụ thiên địa linh khí, lại trải qua núi lửa rèn luyện, linh khí phẩm chất tự nhiên khác biệt! Hấp thu sau đó, không chỉ có thể để linh hồn trở nên chắc chắn, càng quan trọng hơn, còn có thể để cho tràn ngập dương hỏa thuộc tính, không e ngại âm u lực lượng!”"),t("br"),t("br"),n._v("“Ừm!” Diệp Vấn Thiên nhẹ nhàng cười một tiếng: “Ngươi nhìn linh khí này sôi trào bao nhiêu lợi hại, như là nấu nước sôi đồng dạng, thiên chuy bách luyện mới đến chân kim, không trải qua như thế ma luyện, làm sao có thể để phẩm chất tiến thêm một bước... Hả? Cái này, cái này... Làm sao cái tình huống?”"),t("br"),t("br"),n._v("Ngay tại cảm khái Hóa Thanh trì mạnh mẽ, đột nhiên sửng sốt một cái, thanh âm phát run."),t("br"),t("br"),n._v("Nghe được tiếng nói khác thường, Vệ sư vội vàng nhìn lại, cũng là kìm lòng không được ngây người."),t("br"),t("br"),n._v("Chỉ thấy mới vừa rồi còn sôi trào ao nước, giờ phút này giống như là bị người rót một cái thìa lớn nước lạnh, khôi phục bình tĩnh, không có chút rung động nào."),t("br"),t("br"),n._v("“Có thể là... Sôi trào lâu, muốn bình yên tĩnh một chút?” Nuốt ngụm nước bọt, Vệ sư nhịn không được nói."),t("br"),t("br"),n._v("Sống hơn một trăm năm, Hóa Thanh trì mở ra, hắn cũng trải qua không ít lần, thậm chí lúc trước đã từng trong này tu luyện qua... Dựa theo tình huống trước kia, ao nước chịu đến linh khí xung kích cùng địa hỏa xung kích, hẳn là một mực sôi trào, làm sao... Đột nhiên trở nên cùng nước giếng đồng dạng?"),t("br"),t("br"),n._v("“Không có khả năng!” Diệp Vấn Thiên vẻ mặt trắng bệch: “Ao nước chung quanh do Tiên Tổ bố trí trận pháp, một khi khôi phục bình ổn, nói rõ linh khí không đủ, phản tác dụng bên dưới, trận pháp liền sẽ tùy thời sụp đổ...”"),t("br"),t("br"),n._v("Ầm ầm!"),t("br"),t("br"),n._v("Lời còn chưa dứt, nơi đó một hồi lắc lư, lập tức liền thấy Hóa Thanh trì xuất hiện một đạo khe nứt to lớn, ao nước điên cuồng trào ra ngoài."),t("br"),t("br"),n._v("“Thật sập...”"),t("br"),t("br"),n._v("Vệ sư cũng cảm thấy mắt tối sầm lại."),t("br"),t("br"),n._v("Đây chính là Huyễn Vũ đế quốc trấn quốc chí bảo, một khi sụp đổ, cao tầng cường giả sẽ giảm mạnh, toàn bộ đế quốc chỉ sợ đều sẽ lòng người bàng hoàng!"),t("br"),t("br"),n._v("Tại sao sẽ là như vậy?"),t("br"),t("br"),n._v("Chẳng qua bình thường mở ra... Người tu luyện tại Trì Nhãn bên trong tu luyện mà thôi, làm sao lại linh khí giảm mạnh, tạo thành trận pháp không ổn định, trực tiếp sụp đổ?"),t("br"),t("br"),n._v("“Ta đi để Lục công chúa bọn hắn đi ra, một khi ao nước chảy khô... Áp chế không nổi phía dưới núi lửa, liền xong rồi! Vệ sư, ngươi nắm chắc thời gian mời trận pháp sư công hội Ngô hội trưởng bọn hắn, qua đến giúp đỡ tu bổ trận pháp!”"),t("br"),t("br"),n._v("Nhìn thấy ao nước như vỡ đê tuôn ra, Diệp Vấn Thiên bệ hạ vội vàng hô."),t("br"),t("br"),n._v("Cái này Hóa Thanh trì do cực lớn trận pháp khống chế, cùng Thanh Tắc sơn, Long Lân hà cùng địa mạch, duy trì một cái đối lập cân bằng, hiện tại không chịu nổi vỡ tan, phía dưới địa mạch nhúc nhích, rất dễ dàng tạo thành núi lửa bộc phát."),t("br"),t("br"),n._v("Một khi nổ tung, đừng nói là hắn, tất cả mọi người ở đây chỉ sợ đều sẽ hôi phi yên diệt."),t("br"),t("br"),n._v("Núi lửa cực nóng, chỉ cần không biết phi hành, muốn chạy trốn đều không trốn thoát được, bị thôn phệ trong đó, Bán Thánh cũng không sống nổi!"),t("br"),t("br"),n._v("Thiên uy vô địch, chân chính thiên nhiên trước mặt, chỉ có trở thành chân chính Thánh giả, còn có thể kéo dài hơi tàn, cái khác, đều không tính là cái gì."),t("br"),t("br"),n._v("Núi lửa nổ tung, hắn gặp được nguy hiểm không có gì, Lục công chúa đám người xảy ra chuyện... Có thể gặp phải, toàn bộ Huyễn Vũ đế quốc, đều muốn bởi vậy diệt vong!"),t("br"),t("br"),n._v("Vốn cho rằng để công chúa qua tới tu luyện, có thể thừa cơ lấy lòng, để Huyễn Vũ đế quốc địa vị tăng lên một chút, nằm mơ đều không nghĩ tới, một mực rất vững chắc Hóa Thanh trì, đột nhiên nổ!"),t("br"),t("br"),n._v("Chuyện này là sao!"),t("br"),t("br"),n._v("Thật tốt Hóa Thanh trì nói thế nào nổ liền nổ?"),t("br"),t("br"),n._v("Nếu như cho hắn biết, sớm bảo vị này Trương sư đến, cái đồ chơi này đã sớm nổ, khẳng định hiểu ý nhét nói không ra lời."),t("br"),t("br"),n._v("“Tốt! Ta vậy thì đi mời người!”"),t("br"),t("br"),n._v("Vệ sư cũng biết chuyện nghiêm trọng, một tiếng hí dài, một đầu phi hành man thú lập tức bay tới, nhảy lên lưng thú, trực tiếp hướng Đế đô phóng đi."),t("br"),t("br"),n._v("Nhất định phải lập tức để trận pháp khôi phục, khống chế lại cục diện, nếu không, địa mạch trùng kích vào, núi lửa bộc phát, phương viên mấy trăm cây số, chỉ sợ đều sẽ bị san thành bình địa, vô số sinh mệnh đều gặp phải tử vong!"),t("br"),t("br"),n._v("“Ừm!”"),t("br"),t("br"),n._v("Thấy hắn rời khỏi, Diệp Vấn Thiên bệ hạ không dám do dự, vội vàng hướng Lục công chúa vị trí Trì Nhãn vọt tới."),t("br"),t("br"),n._v("..."),t("br"),t("br"),n._v("Lúc này Phi nhi công chúa, đã từ trong tu luyện tỉnh táo lại, đang cùng Lạc Thất Thất, đồng loạt chằm chằm lấy thanh niên trước mắt, hai mặt nhìn nhau."),t("br"),t("br"),n._v("Động tĩnh lớn như vậy, toàn bộ Hóa Thanh trì đều không khác mấy muốn nổ, cái này Trương sư, lại còn đang nhắm mắt tu luyện... Hắn làm sao bình tĩnh lại?"),t("br"),t("br"),n._v("Chân tu luyện ngược lại cũng thôi... Trước mặt lơ lửng Thanh Thần hương, còn là trước kia dài như vậy, nói cách khác... Đều đi qua bốn canh giờ, cái tên này một điểm linh khí không có hấp thu!"),t("br"),t("br"),n._v("Không có hấp thu linh khí, tu luyện cái gì?"),t("br"),t("br"),n._v("“Trương lão sư...”"),t("br"),t("br"),n._v("Bây giờ nhìn không nổi nữa, Lạc Thất Thất nhịn không được hô."),t("br"),t("br"),n._v("Đối diện không có động tĩnh chút nào."),t("br"),t("br"),n._v("“Hóa Thanh trì đột nhiên xảy ra chuyện, ta cũng đuổi không kịp ngươi, coi như ngươi thắng...”"),t("br"),t("br"),n._v("Phi nhi công chúa khẽ nói."),t("br"),t("br"),n._v("Đối phương đều tiêu hao năm cái nhiều, nàng bốn canh giờ không có chút nào dám ngừng, cũng chỉ dùng không đến một cái, kém một mảng lớn, đánh cược khẳng định là thua."),t("br"),t("br"),n._v("Ta đều thua, cũng chớ giả bộ, nắm chặt thời gian đi!"),t("br"),t("br"),n._v("Nói xong, đối phương mắt vẫn nhắm như cũ, không có động tĩnh."),t("br"),t("br"),n._v("“Hắn làm sao vậy?”"),t("br"),t("br"),n._v("Ngu nữa, hai người cũng nhìn ra không được bình thường."),t("br"),t("br"),n._v("“Ta xem một chút!”"),t("br"),t("br"),n._v("Tràn đầy sốt ruột, Lạc Thất Thất vừa tung người, nhảy vào Trương Huyền Trì Nhãn, kéo một cái, phát hiện không có động tĩnh, nhịn không được sờ về phía cái mũi, sờ một cái trên người, lập tức giật nảy mình."),t("br"),t("br"),n._v("Chỉ thấy thanh niên toàn thân cứng ngắc, hô hấp đoạn tuyệt."),t("br"),t("br"),n._v("“Hắn... Hắn... Chết rồi!”"),t("br"),t("br"),n._v("Bờ môi phát run, Lạc Thất Thất vẻ mặt trắng bệch."),t("br"),t("br"),n._v("Convert by: ThấtDạ"),t("br"),t("br"),t("br"),t("br")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("a",{attrs:{href:"./chap-650"}},[n._v("Chương trước")]),n._v("  |  "),t("a",{attrs:{href:"./chap-652"}},[n._v("Chương sau")])])])}),[],!1,null,null,null);h.default=c.exports}}]);