(window.webpackJsonp=window.webpackJsonp||[]).push([[1913],{2268:function(n,h,t){"use strict";t.r(h);var i=t(44),g=Object(i.a)({},(function(){var n=this,h=n.$createElement,t=n._self._c||h;return t("ContentSlotsDistributor",{attrs:{"slot-key":n.$parent.slotKey}},[t("h1",{attrs:{id:"chuong-644-bo-tien-vao"}},[t("a",{staticClass:"header-anchor",attrs:{href:"#chuong-644-bo-tien-vao"}},[n._v("#")]),n._v(" Chương 644: Bò tiến vào")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("br"),t("br"),n._v("Chương 644: Bò tiến vào"),t("br"),t("br"),n._v("“Hồn phách ly thể?”"),t("br"),t("br"),n._v("Phi nhi công chúa, Lạc Thất Thất đám người tất cả đều sững sờ."),t("br"),t("br"),n._v("Hồn phách ly thể, cũng gọi mất hồn, là võ giả rất chuyện kiêng kỵ, một khi không cách nào quay về, thân thể liền lại biến thành xác rỗng, trực tiếp tử vong."),t("br"),t("br"),n._v("Bất quá, muốn hồn phách ly thể, đầu tiên hồn thể đủ cường đại, kém cỏi nhất cũng muốn đạt tới Hợp Linh cảnh đỉnh phong, thậm chí Kiều Thiên cảnh, một cái Trọc Thanh cảnh, làm sao lại xuất hiện loại bệnh trạng này?"),t("br"),t("br"),n._v("“Hồn phách ly thể do nhiều loại nguyên nhân tạo thành, tu luyện linh hồn thời điểm phạm sai lầm, bị đặc thù trận pháp hấp dẫn, áp chế không nổi tâm ma... Có thể là Trương sư tu vi không đủ, mạnh mẽ dùng bí pháp nào đó, tu luyện linh hồn, cái này mới xuất hiện loại tình huống này... Không nắm chặt thời gian để hắn quy vị, ta sợ không bao lâu, liền sẽ thật chết đi!”"),t("br"),t("br"),n._v("Diệp Vấn Thiên vẻ mặt nghiêm túc."),t("br"),t("br"),n._v("Hồn phách ly thể, cũng không phải là chỉ có vu hồn sư mới có thể làm đến, một chút lợi hại trận pháp, mê huyễn chi pháp, sở dĩ có thể làm cho không người nào có thể tự kềm chế, chính yếu nhất chính là câu dẫn linh hồn."),t("br"),t("br"),n._v("“Có thể có phương pháp cứu chữa...” Lạc Thất Thất mang theo lo lắng hỏi."),t("br"),t("br"),n._v("Vị này chính là đối nàng có ân đan dược lão sư, thật muốn bởi vì hồn phách ly thể mà tử vong, liền nguy rồi."),t("br"),t("br"),n._v("“Có là có, chẳng qua rất phiền phức, chỉ sợ thời gian không còn kịp nữa...”"),t("br"),t("br"),n._v("Quay đầu nhìn thoáng qua không ngừng chảy xuôi Hóa Thanh trì, Diệp Vấn Thiên mày nhíu lại thành u cục."),t("br"),t("br"),n._v("Để hồn phách ly thể người thanh tỉnh, trình tự cực kỳ phức tạp phiền phức, không có có mấy canh giờ, thậm chí mười mấy canh giờ không có khả năng hoàn thành, hiện tại ao nước chảy xuôi sạch sẽ, núi lửa liền có khả năng bộc phát, về thời gian rõ ràng không kịp."),t("br"),t("br"),n._v("“Vậy làm sao bây giờ?”"),t("br"),t("br"),n._v("“Lên trước bờ lại nói!”"),t("br"),t("br"),n._v("Biết nơi này không phải chỗ nói chuyện, Diệp Vấn Thiên khoát tay áo, ôm Trương Huyền đi đầu nhảy lên bờ đi."),t("br"),t("br"),n._v("“Ai, ai...”"),t("br"),t("br"),n._v("Trong ao Trương Huyền cực lớn hồn thể, nhìn thấy thân thể trực tiếp bị ôm đi, khóc không ra nước mắt."),t("br"),t("br"),n._v("Các ngươi đây là muốn cứu ta, vẫn là muốn giết ta à?"),t("br"),t("br"),n._v("Đem thân thể ở lại chỗ này, hồn phách coi như rất khó đi vào, nương theo thời gian trôi qua, cũng có thể thành công, trực tiếp ôm đi, ta thật muốn đi qua, chỉ sợ còn chưa tới đến trước mặt, liền sẽ bị Diệp Vấn Thiên xem như âm hồn đánh tan..."),t("br"),t("br"),n._v("“Không được, nhất định phải chờ cơ hội đem thân thể đoạt lại...”"),t("br"),t("br"),n._v("Chần chờ một chút, hồn thể cuối cùng vẫn không có đuổi theo."),t("br"),t("br"),n._v("Thiên Đạo công pháp hình thành Vu hồn, mặc dù không có âm khí, để cho người ta rất khó phát giác, nhưng thật phải chạy đến trước mặt đối phương, vẫn là rất dễ dàng xem thấu."),t("br"),t("br"),n._v("Nhất là vị này Diệp Vấn Thiên, tu vi đi đến Hóa Phàm bát trọng đỉnh phong, cả người như là cực nóng thái dương, tùy tiện chạy tới, chỉ sợ còn không có cho thấy thân phận, liền sẽ bị tại chỗ đánh chết."),t("br"),t("br"),n._v("Lại nói, coi như cho thấy thân phận, đối phương cũng phải tin tưởng mới được."),t("br"),t("br"),n._v("Một cái Trọc Thanh cảnh người, có gần tới cao mười mét hồn thể, còn tự động ly thể tu luyện... Những này, nên giải thích thế nào?"),t("br"),t("br"),n._v("Cho nên, kế trước mắt, hay là không thể để người khác phát hiện cái này cái cự đại hồn thể."),t("br"),t("br"),n._v("Chỉ cần tìm cơ hội thích hợp, chui vào thân thể, sự tình liền có thể giải quyết tốt đẹp, không cần giải thích rất nhiều thứ, nhiều nhất liền nói tu luyện quá nhanh, chỉ vì cái trước mắt, xuất hiện sai lầm."),t("br"),t("br"),n._v("..."),t("br"),t("br"),n._v("Trương Huyền hồn thể, giấu ở trong nước hồ tùy thời mà động, thân thể đã Diệp Vấn Thiên dẫn tới trước đó sân nhỏ."),t("br"),t("br"),n._v("“Trương sư làm sao vậy?”"),t("br"),t("br"),n._v("Hồng sư, La Tuyền đám người thấy hắn cứng ngắc bất động, tất cả đều tràn đầy gấp gáp tiến lên đón."),t("br"),t("br"),n._v("“Hẳn là hồn phách ly thể!”"),t("br"),t("br"),n._v("Diệp Vấn Thiên hạ giọng."),t("br"),t("br"),n._v("“Hồn phách ly thể?” Hồng sư sắc mặt trắng nhợt, thân thể nhoáng một cái: “Tại sao có thể như vậy?”"),t("br"),t("br"),n._v("Vị này chính là Trương sư, lão sư nhưng mà hư hư thực thực bát tinh siêu cường tồn tại, làm hắn hồn phách ly thể... Một khi bị lão sư hắn biết, đừng nói Huyễn Vũ đế quốc, toàn bộ Hồng Viễn đế quốc chỉ sợ đều muốn xong đời!"),t("br"),t("br"),n._v("“Nếu ta đoán không lầm, hẳn là hắn mới vừa mới lúc tu luyện, Hóa Thanh trì đột nhiên phát sinh xao động, đánh tan linh hồn, để nhục thể của hắn biến thành xác không!”"),t("br"),t("br"),n._v("Diệp Vấn Thiên nói."),t("br"),t("br"),n._v("Hiện tại chỉ có cái này một lời giải thích."),t("br"),t("br"),n._v("Nếu không, làm sao người khác đều vô sự, hắn biến thành như vậy?"),t("br"),t("br"),n._v("Khẳng định là nhận lấy ao nước biến cố ảnh hưởng, kích đống hồn phách!"),t("br"),t("br"),n._v("Loại tình huống này, linh hồn nếu như là hoàn chỉnh, còn tốt tìm kiếm, một khi tán loạn, sợ sợ rằng muốn cứu sống, rất khó."),t("br"),t("br"),n._v("“Cái này...” Sắc mặt trắng nhợt, Hồng sư đang muốn hỏi thăm nên làm sao chữa, liền cảm giác tới mặt đất một hồi lắc lư, một luồng nóng rực khí tức, từ trong ao hướng lên bốc lên."),t("br"),t("br"),n._v("“Nguy rồi, lập tức sẽ bạo phát!”"),t("br"),t("br"),n._v("Khuôn mặt biến đổi, Diệp Vấn Thiên lại không để ý tới Trương Huyền, đem hắn “Thi thể” tìm cái bàn đá thả xuống, vội vã nhìn sang."),t("br"),t("br"),n._v("Hóa Thanh trì đột nhiên sinh ra biến cố, cũng đưa tới chú ý của những người khác, tất cả mọi người đem ánh mắt tập trung qua."),t("br"),t("br"),n._v("Chỉ thấy lúc này ao nước, lần nữa giống như đun lên, không ngừng tản ra nhiệt khí, hơi nóng hầm hập, bốn phía chảy xuôi, nơi đó không ngừng lắc lư, hình như toàn bộ sân nhỏ phía dưới, phong tỏa một cái cự đại man thú, bất cứ lúc nào cũng sẽ nhảy ra đến, đem bọn hắn tàn sát sạch sẽ."),t("br"),t("br"),n._v("“Đây là thế nào?”"),t("br"),t("br"),n._v("Kiến giải mặt lắc lư càng ngày càng lợi hại, tất cả mọi người đều nhíu mày."),t("br"),t("br"),n._v("“Hẳn là lập tức liền sắp không kiên trì được nữa...”"),t("br"),t("br"),n._v("Nắm đấm xiết chặt, Diệp Vấn Thiên vẻ mặt càng ngày càng trắng."),t("br"),t("br"),n._v("Còn tưởng rằng coi như trận pháp tổn hại, Hóa Thanh trì nhiều năm tích lũy, vẫn như cũ có thể trấn áp mấy canh giờ, không nghĩ tới tốc độ nhanh như vậy!"),t("br"),t("br"),n._v("Tiếp tục như vậy, hẳn là không dùng đến mười phút đồng hồ, lại áp chế không nổi phía dưới địa mạch cùng núi lửa, cuồng bạo hung mãnh lực lượng, trực tiếp tuôn ra!"),t("br"),t("br"),n._v("“Diệp Tiền, mau dẫn Lục công chúa bọn hắn rời khỏi!”"),t("br"),t("br"),n._v("Vừa quay đầu, vội vàng phân phó."),t("br"),n._v("Hắn thân là Hoàng đế bệ hạ, tự nhiên không thể rời khỏi, nhưng Lục công chúa đám người, tuyệt đối không thể xảy ra chuyện gì!"),t("br"),t("br"),n._v("“Tốt!”"),t("br"),t("br"),n._v("Diệp Tiền cũng biết mức độ nghiêm trọng của sự việc, vội vàng đi tới Phi nhi công chúa trước mặt: “Lục công chúa, Hình Viễn học trưởng, Hóa Thanh trì hẳn là không kiên trì được bao lâu, liền muốn nổ tung, nơi này vô cùng nguy hiểm, ta mang các ngươi rời khỏi...”"),t("br"),t("br"),n._v("“Tốt!”"),t("br"),t("br"),n._v("Hình Viễn nhẹ gật đầu, đang muốn gọi Phi nhi công chúa rời khỏi, chỉ thấy nàng lông mày nhăn lại nhìn lại: “Tên kia hồn phách ly thể, linh hồn có phải hay không đã ném tại Hóa Thanh trì?”"),t("br"),t("br"),n._v("“Cái này...” Diệp Tiền sững sờ: “Hẳn là đi!”"),t("br"),t("br"),n._v("“Vậy làm sao bây giờ? Không nhanh chóng đem linh hồn tìm ra, đưa vào thân thể, người thật sẽ chết...” Phi nhi công chúa nói."),t("br"),t("br"),n._v("“Cái này ngươi yên tâm, chúng ta sẽ phái người đi tìm, học tỷ vẫn là nắm chặt thời gian rời đi được...” Diệp Tiền vội nói."),t("br"),t("br"),n._v("“Ừm!” Lên tiếng, Phi nhi công chúa nhìn về phía bạn tốt: “Chúng ta đi trước đi!”"),t("br"),t("br"),n._v("Nàng muốn cứu Trương Huyền, là bởi vì không muốn một mực làm đối phương tỳ nữ, bây giờ đối phương là linh hồn ly thể, nàng cũng hết cách rồi, coi như tiếp tục đợi ở chỗ này, cũng vô dụng, còn không bằng rời khỏi."),t("br"),t("br"),n._v("“Ngươi đi trước, Trương lão sư đối ta có ân, hắn hiện tại không rõ sống chết, nếu như cứ thế mà đi, thực sự có lỗi với thụ nghiệp chi ân!”"),t("br"),t("br"),n._v("Lạc Thất Thất lắc đầu."),t("br"),t("br"),n._v("Vị này Trương sư, từng chỉ điểm nàng luyện chế đan dược, để hắn luyện đan thuật tăng nhiều... Càng là hạ bái qua lão sư!"),t("br"),t("br"),n._v("Lúc này hôn mê bất tỉnh, như là tử vong, để nàng một mình rời khỏi, thực sự làm không được!"),t("br"),t("br"),n._v("“Ngươi không đi, vậy ta cũng không đi, ta cũng muốn nhìn một chút, cái này Hóa Thanh trì đến cùng chuyện gì xảy ra...”"),t("br"),t("br"),n._v("Chuyển biến tốt hữu không đi, Phi nhi công chúa cũng nhẹ gật đầu."),t("br"),t("br"),n._v("“A?”"),t("br"),t("br"),n._v("Nghe được hai người quyết định không đi, Diệp Tiền kém chút không điên rồi, chính không biết như thế nào khuyên can, liền nghe đến không trung một trận gió tiếng hô hô, một đầu cực lớn linh thú phi hành, đi tới trước mặt."),t("br"),t("br"),n._v("Hô hô hô!"),t("br"),t("br"),n._v("Ngay sau đó vài bóng người, từ không trung nhảy xuống tới."),t("br"),t("br"),n._v("“Là Vệ sư, cùng trận pháp sư công hội Ngô hội trưởng!”"),t("br"),t("br"),n._v("Nhìn thấy mấy người thân ảnh, Diệp Tiền ánh mắt sáng lên."),t("br"),t("br"),n._v("Hóa Thanh trì bí mật, hắn cũng biết một chút, trước mắt tình huống này, rất rõ ràng là ao nước xuất hiện không công bằng, dẫn đến trận pháp nứt toác, Ngô hội trưởng làm trận pháp sư công hội hội trưởng, ngũ tinh đỉnh phong trận pháp sư, tự mình tới, có lẽ sẽ có biện pháp ngăn lại, đỡ cao ốc chi tướng trút hết, xoay chuyển tình thế tại đã ngược lại."),t("br"),t("br"),n._v("“Làm sao lại biến thành như vậy?”"),t("br"),t("br"),n._v("Rơi xuống đất, nhìn thấy ao nước bốn phía trút xuống, nơi đó không ngừng chấn động, bất cứ lúc nào cũng sẽ sụp đổ, Ngô hội trưởng nhướng mày."),t("br"),t("br"),n._v("Huyễn Vũ đế quốc hoàng thất, hàng năm đều sẽ mời hắn tới gia cố trận pháp, đối với trận pháp biết đến rất kỹ càng, dựa theo đạo lý, chỉ là tại Trì Nhãn bên trong tu luyện, sẽ không xuất hiện loại sự tình này, làm sao... Trực tiếp sụp đổ?"),t("br"),t("br"),n._v("“Nguyên nhân cụ thể, ta cũng không rõ lắm, có điều, bây giờ không phải là nói những này thời điểm, Ngô hội trưởng mau nhìn xem, còn có hay không biện pháp tu bổ trận pháp, để phía dưới núi lửa không muốn phun trào!”"),t("br"),t("br"),n._v("Diệp Vấn Thiên vội nói."),t("br"),t("br"),n._v("Bây giờ nói nhiều hơn nữa đều không dùng."),t("br"),t("br"),n._v("Nhất định phải nắm chặt thời gian nhìn xem, có không có cách nào cứu vãn, nếu không, đợi ở chỗ này mọi người, đều không sống được."),t("br"),t("br"),n._v("“Ừm!”"),t("br"),t("br"),n._v("Biết mức độ nghiêm trọng của sự việc, lúc này không phải nói những này thời điểm, Ngô hội trưởng nhẹ gật đầu, hướng trước mắt ao nước nhìn sang."),t("br"),t("br"),n._v("“Lộc trưởng lão, ngươi đi bảo vệ khảm vị, Hàn trưởng lão, ngươi đi cấn vị...”"),t("br"),t("br"),n._v("Nhìn một hồi, quay đầu phân phó."),t("br"),t("br"),n._v("Hắn đi theo phía sau cùng một chỗ từ lưng thú bên trên xuống tới mấy cái lão giả gật đầu lên tiếng, đồng loạt đi tới đối ứng vị trí, bàn tay bỗng nhiên nhấn một cái, từng cái trận kỳ cắm vào trên mặt đất."),t("br"),t("br"),n._v("Ầm ầm!"),t("br"),t("br"),n._v("Nương theo trận kỳ xuất hiện, bốn phía lao nhanh ao nước, giống như là bị cái gì phong tỏa đồng dạng, dừng lại."),t("br"),t("br"),n._v("Bất quá, giống như là đè lại một đầu cuồng long, nơi đó lắc lư càng ngày càng kịch liệt, tựa như lúc nào cũng sẽ tránh thoát ràng buộc, lần nữa phun ra ngoài."),t("br"),t("br"),n._v("“Không đúng...” Ngô hội trưởng lông mày giương lên."),t("br"),t("br"),n._v("“Làm sao vậy?” Diệp Vấn Thiên bệ hạ tới đến trước mặt."),t("br"),t("br"),n._v("“Hóa Thanh trì vị trí, vừa lúc là Thanh Tắc sơn, Long Lân hà, cùng địa mạch chỗ giao hội, linh khí đầy đủ, trước đó bố trí trận pháp, chính là mượn nơi này hùng hậu linh khí khởi động, mới có thể ngăn chặn tam phương giao hội sinh ra cuồng bạo lực lượng... Làm sao hiện tại, một điểm linh khí cũng không có?”"),t("br"),t("br"),n._v("Ngô hội trưởng vẻ mặt ngạc nhiên."),t("br"),t("br"),n._v("“Linh khí không có?”"),t("br"),t("br"),n._v("Diệp Vấn Thiên cứng đờ, cẩn thận cảm thụ một cái, quả nhiên cũng cảm giác được không đúng."),t("br"),t("br"),n._v("Nơi này linh khí quá mức mạnh mẽ, liền Hóa Phàm bát trọng tiến vào bên trong, đều có thể bị tuỳ tiện giảo sát, nói thế nào không thấy đã không thấy tăm hơi?"),t("br"),t("br"),n._v("“Đúng vậy a...”"),t("br"),t("br"),n._v("Ngô hội trưởng gật gật đầu, chính muốn tiếp tục nói chuyện, lập tức nghe được có người sau lưng thét lên."),t("br"),t("br"),n._v("Nghe được thanh âm, Diệp Vấn Thiên cũng không đoái hoài tới hỏi nhiều, vội vàng quay đầu."),t("br"),t("br"),n._v("Ngay sau đó liền thấy Lạc Thất Thất cùng Lục công chúa tràn đầy nghi ngờ nhìn về phía cái kia gọi La Tuyền danh sư."),t("br"),t("br"),n._v("“Trương lão sư đâu?”"),t("br"),t("br"),n._v("Các nàng ngay phía trước, vốn là đặt ở trên bàn đá một cái Trương sư “Thi thể” chẳng biết lúc nào đã biến mất không thấy gì nữa, đã mất đi tung tích."),t("br"),t("br"),n._v("“Hắn, hắn...”"),t("br"),t("br"),n._v("Nghe được hỏi thăm, La Tuyền miệng không ngừng run rẩy, hai mắt trợn tròn xoe, vẻ mặt sắp khóc biểu lộ, một chỉ cách đó không xa ao nước: “Hắn nằm nằm, ta một không có chú ý, liền... Tự mình bò đi vào...”"),t("br"),t("br"),n._v("Convert by: ThấtDạ"),t("br"),t("br"),t("br"),t("br")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("a",{attrs:{href:"./chap-652"}},[n._v("Chương trước")]),n._v("  |  "),t("a",{attrs:{href:"./chap-654"}},[n._v("Chương sau")])])])}),[],!1,null,null,null);h.default=g.exports}}]);