(window.webpackJsonp=window.webpackJsonp||[]).push([[1917],{2272:function(n,h,t){"use strict";t.r(h);var c=t(44),g=Object(c.a)({},(function(){var n=this,h=n.$createElement,t=n._self._c||h;return t("ContentSlotsDistributor",{attrs:{"slot-key":n.$parent.slotKey}},[t("h1",{attrs:{id:"chuong-648-thanh-tac-son"}},[t("a",{staticClass:"header-anchor",attrs:{href:"#chuong-648-thanh-tac-son"}},[n._v("#")]),n._v(" Chương 648: Thanh Tắc sơn")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("br"),t("br"),n._v("Chương 648: Thanh Tắc sơn"),t("br"),t("br"),n._v("Mọi người buồn bực không dứt, mà để bọn hắn kính nể không thôi Dương sư, mới vừa bay ra ánh mắt, liền thân thể mềm nhũn, “Hô!” Một cái, từ không trung rơi xuống dưới."),t("br"),t("br"),n._v("Bành!"),t("br"),t("br"),n._v("Ngay sau đó một cái thống khổ rên rỉ, khí độ bất phàm Dương sư, dung mạo chậm rãi cải biến, trở thành Trương Huyền bộ dạng."),t("br"),t("br"),n._v("“Ngươi liền sẽ không phụ một tay, trơ mắt nhìn ta từ không trung ngã xuống?”"),t("br"),t("br"),n._v("Che bị ngã đến có chút choáng đầu đầu, nhìn về phía một bên phân thân, Trương Huyền tức đến sắp thổ huyết."),t("br"),t("br"),n._v("Cái này Dương sư, tự nhiên là hắn ngụy trang, còn vị kia “Trương Huyền”, thì là phân thân."),t("br"),t("br"),n._v("Vừa rồi tại Hóa Thanh trì đáy ao, lợi dụng mọi người chân khí cùng Địa Tâm hỏa, thành công để thân thể tăng cường không ít, rốt cục để tiếp cận mười mét to lớn hồn phách chui vào."),t("br"),t("br"),n._v("Mới vừa làm tốt đây hết thảy, mới phát hiện Hóa Thanh trì không thể kiên trì được nữa, bất cứ lúc nào cũng sẽ bạo tạc."),t("br"),t("br"),n._v("Vì bảo mệnh, lập tức để thư viện dò xét một cái, giờ mới hiểu được, là hắn cùng phân thân đem trong nước hồ linh khí hấp thu hết, dẫn đến ba cỗ lực lượng mất đi cân bằng, náo động lên động tĩnh lớn như vậy."),t("br"),t("br"),n._v("Biết những này, lập tức tràn đầy thật không tiện."),t("br"),t("br"),n._v("Nhân gia hảo tâm để hắn tới tu luyện, kết quả vừa đến liền làm ao nước bạo tạc..."),t("br"),t("br"),n._v("Quả thực không đủ phúc hậu."),t("br"),t("br"),n._v("Nhưng nói thật, hắn thật không phải cố ý!"),t("br"),t("br"),n._v("Muốn trách thì trách... Phân thân, nếu không phải cái này hai hàng, không biết tốt xấu liều mạng hấp thu, làm sao có thể dẫn đến linh khí khô kiệt, cân bằng đánh vỡ?"),t("br"),t("br"),n._v("Bất quá, bây giờ nói những này cũng vô ích, thật muốn chạy ra đi giải thích, sợ rằng sẽ cho tức giận Diệp Vấn Thiên, một bàn tay chụp chết."),t("br"),t("br"),n._v("Không có cách nào này mới khiến “Dương sư” lần nữa đi ra."),t("br"),t("br"),n._v("Vị này “Đại năng” tự mình xuất hiện, đồng thời giúp đối phương giải quyết hết tai hoạ ngầm, cũng coi như làm hỏng đối phương Hóa Thanh trì làm ra đền bù."),t("br"),t("br"),n._v("Vì để cho sự tình trở nên càng bức thật một chút, càng làm cho phân thân, ngụy trang thành bản thân, giả dạng làm vẫn như cũ hôn mê bộ dạng."),t("br"),t("br"),n._v("Cứ như vậy, trở về cũng thuận tiện giải thích, hoàn toàn có thể trốn tránh sạch sẽ, liền nói lão sư cứu hắn, đối những chuyện khác, hoàn toàn không biết gì cả."),t("br"),t("br"),n._v("Chắc hẳn mọi người tận mắt nhìn thấy, cũng sẽ không tiếp tục truy cứu."),t("br"),t("br"),n._v("Hắn phi hành mượn nhờ chính là Hồng Trần Đạp Thiên bộ, chân khí hao hết liền sẽ từ không trung ngã xuống, phân thân không giống, chủ đạo lực lượng là Vu hồn, thêm bên trên Cửu Thiên Liên Thai thuộc về thần vật, bản thân liền có thể bay."),t("br"),t("br"),n._v("Bởi vậy, hai người hợp tác, ngay cả Vệ sư loại này ngũ tinh đỉnh phong danh sư, đều không nhìn ra sơ hở."),t("br"),t("br"),n._v("Mới vừa bay đến nơi đây, tiêu hao chân khí không sai biệt lắm, lại thêm linh hồn cùng thân thể đều mới vừa đột phá, không thể thích ứng, mắt tối sầm lại, trực tiếp rớt xuống!"),t("br"),t("br"),n._v("Vốn cho rằng, một bên phân thân, bất kể nói thế nào, đều sẽ phụ một tay, để hắn bình ổn rơi xuống đất, kết quả, cái này hai hàng thế mà không nhúc nhích, tùy ý quẳng xuống đất..."),t("br"),t("br"),n._v("Đem hắn sắp tức đến bể phổi rồi!"),t("br"),t("br"),n._v("Mặc dù không phải rất cao, lấy hắn hiện tại thân thể cường độ tới nói, cũng ném không nghiêm trọng, có thể... Làm vì một cái phân thân, một điểm không thay bản tôn phân ưu, có chút quá gài bẫy đi!"),t("br"),t("br"),n._v("Cái gì ngoạn ý đây!"),t("br"),t("br"),n._v("Ngươi phải nhớ kỹ, ngươi chính là cái phân thân, bản tôn có thời điểm khó khăn, nên chủ động tiến lên..."),t("br"),t("br"),n._v("Đang định thật tốt cho cái tên này làm tư tưởng giáo dục, cho hắn biết chủ thứ phân chia, chỉ thấy cái này hai hàng, chậm rãi nhắm mắt lại... Vẻ mặt chính ngươi ngu xuẩn, không liên quan chuyện ta bộ dáng."),t("br"),t("br"),n._v("“...”"),t("br"),t("br"),n._v("Trương Huyền thổ huyết."),t("br"),t("br"),n._v("Nói ngươi không thể “Trơ mắt” nhìn ta ngã xuống, liền nhắm mắt lại, ta thế nào làm ra ngươi dạng này một cái hai hàng?"),t("br"),t("br"),n._v("Người khác phân thân, đều là hung mãnh cực kỳ, nhìn thấy bản tôn bị thương, lập tức xông lại ra mặt, cái tên này ngược lại tốt, tùy ý bản tôn rơi nửa chết nửa sống... Cũng là say!"),t("br"),t("br"),n._v("Quả thực chính là cái mười đủ mười lừa đảo."),t("br"),t("br"),n._v("“Xem trước một chút thực lực tăng lên bao nhiêu lại nói!”"),t("br"),t("br"),n._v("Lười phải tiếp tục cùng cái tên này tiếp tục tức giận, Trương Huyền nhìn lại."),t("br"),t("br"),n._v("Tại địa tâm lửa cùng linh ao nước trùng kích vào, thân thể có cực tiến bộ lớn, mỗi một tế bào, đều cứng rắn mềm dẻo, như là thép như sắt thép."),t("br"),t("br"),n._v("Cổ tay khẽ đảo, một quyền nện gõ mà đi."),t("br"),t("br"),n._v("Ầm ầm!"),t("br"),t("br"),n._v("Mười mấy mét bên ngoài đại thụ lắc lư một cái, xuất hiện lớn chừng quả đấm lỗ thủng, tựa như đao cắt."),t("br"),t("br"),n._v("“Chỉ bằng vào thân thể liền mạnh như vậy?”"),t("br"),t("br"),n._v("Ánh mắt sáng lên."),t("br"),t("br"),n._v("Một quyền này, hắn không dùng hồn lực, cũng không dùng chân khí, chỉ dựa vào nhục thể tiện tay phát ra, muốn nhìn một chút đến cùng có bao lớn lực lượng, không nghĩ tới ra quyền khí áp, liền đem mười mấy mét bên ngoài, ba người đều khó mà ôm hết đại thụ, đánh ra lỗ thủng!"),t("br"),t("br"),n._v("Sức công kích như thế này, như thế ngưng tụ, chỉ sợ Hóa Phàm ngũ trọng Hợp Linh cảnh cường giả, đều khó mà làm đến!"),t("br"),t("br"),n._v("“Chỉ bằng vào thân thể... 200 vạn đỉnh!”"),t("br"),t("br"),n._v("Nắm đấm xiết chặt, Trương Huyền mắt sáng rực lên."),t("br"),t("br"),n._v("Mới vừa lần này, hắn đã thử nghiệm ra lực lượng của thân thể, thế mà cùng chân khí đồng dạng, cũng đạt tới 200 vạn đỉnh nhiều."),t("br"),t("br"),n._v("Nói cách khác, coi như không cần chân khí cùng hồn lực, chỉ bằng vào cơ bắp, Hợp Linh cảnh lúc đầu cường giả, cũng có thể cứ thế mà đánh nổ."),t("br"),t("br"),n._v("Đáng sợ!"),t("br"),t("br"),n._v("“Hồn lực đâu?”"),t("br"),t("br"),n._v("Nhìn xong thân thể, tinh thần khẽ động, hồn lực thôi động, bàn tay hướng về phía trước duỗi ra."),t("br"),t("br"),n._v("Ầm ầm!"),t("br"),t("br"),n._v("Mấy chục mét bên ngoài một đỉnh núi nhỏ, nổ thành tro bụi."),t("br"),t("br"),n._v("“Mặc dù vẫn là Hợp Linh cảnh đỉnh phong, 320 vạn đỉnh lực lượng, nhưng khí lực càng thêm kéo dài, có thể nói, vô cùng vô tận...”"),t("br"),n._v(" Con mắt lần nữa sáng lên."),t("br"),t("br"),n._v("Mặc dù hồn thể kích cỡ từ cùng thân thể không sai biệt lắm một mét tám, dài đến tiếp cận mười mét, nhưng lực lượng cường độ đồng thời không có gia tăng, vẫn như cũ là 320 vạn đỉnh."),t("br"),t("br"),n._v("Bất quá, sức chịu đựng không đồng dạng."),t("br"),t("br"),n._v("Thật giống như, một người bình thường dùng hết lực lượng, có thể giơ lên hai trăm cân vật nặng, nhưng cử động một lần, đã là cực hạn, lần thứ hai, cần nghỉ ngơi hồi lâu mới có thể làm đến."),t("br"),t("br"),n._v("Trước đó có thể bộc phát 320 vạn đỉnh, cũng chỉ là cực hạn, nói đúng là, sức tấn công mạnh nhất, là như thế lớn, bình thường công kích là không thể nào mỗi một quyền, đều có thể đi đến."),t("br"),t("br"),n._v("Mà lúc này, hồn thể dài đến mười mét, mỗi một cái đều có 320 vạn đỉnh, tựa như động cơ vĩnh cửu, không biết mệt mỏi!"),t("br"),t("br"),n._v("Vậy thì mười phần đáng sợ, mỗi một quyền đều 320 vạn đỉnh, mặc dù không phải rất cường đại, nhưng một mực duy trì, Kiều Thiên cảnh lúc đầu, chỉ sợ đều có thể bị cứ thế mà đánh chết."),t("br"),t("br"),n._v("“Thân thể 200 vạn đỉnh, chân khí 200 vạn đỉnh, hồn lực 320 vạn đỉnh, cộng lại vượt qua bảy trăm vạn đỉnh, Hóa Phàm lục trọng Kiều Thiên cảnh đỉnh phong, đều có thể một trận chiến!”"),t("br"),t("br"),n._v("Hít sâu một hơi, Trương Huyền cảm thấy thân thể chiến ý hừng hực, như là bất bại kim cương."),t("br"),t("br"),n._v("Chỉ là Trọc Thanh cảnh đỉnh phong, liền nắm giữ liền Kiều Thiên cảnh đỉnh phong, đều không thể sánh ngang lực lượng, đổi lại ai, chỉ sợ đều sẽ sinh ra ý tưởng như vậy. (Kiều Thiên cảnh đỉnh phong nắm giữ 640 vạn đỉnh cự lực)"),t("br"),t("br"),n._v("“Đến, chúng ta đối đầu một quyền!”"),t("br"),t("br"),n._v("Nhẹ nhàng cười một tiếng, Trương Huyền nhìn về phía cách đó không xa phân thân."),t("br"),t("br"),n._v("Lần trước bởi vì thân thể không được, bị đối phương một quyền đánh bay, kém chút thổ huyết, hiện tại tinh khí thần bao quát hồn lực, đều có đột phá mới, đối phương muốn chiến thắng chỉ sợ đã không dễ dàng như vậy!"),t("br"),t("br"),n._v("Có lẽ, còn có thể đem đả thương, cho một chút giáo huấn, để cái này hai hàng rõ ràng chủ thứ phân chia!"),t("br"),t("br"),n._v("“Ừm!” Phân thân xoay đầu lại, một quyền duỗi ra."),t("br"),t("br"),n._v("Nhướng mày, Trương Huyền thể, hồn, tức giận, ba cỗ lực lượng ngưng tập hợp một chỗ, trong chốc lát, lực lượng toàn thân đi đến đỉnh phong nhất."),t("br"),t("br"),n._v("Hắn giờ phút này, cảm giác coi như hóa phản thất trọng lúc đầu cường giả tại cái này, cũng có thể một quyền đấm chết!"),t("br"),t("br"),n._v("Oanh!"),t("br"),t("br"),n._v("Hai quyền va chạm."),t("br"),t("br"),n._v("“Ngươi...”"),t("br"),t("br"),n._v("Một tiếng kêu thảm, phân thân đứng tại chỗ bất động, Trương Huyền thì thẳng tắp hướng (về) sau bay đi, sống lưng liên tục đụng gãy mười mấy khỏa đại thụ bay ra hơn mấy trăm mét, cái này mới dừng lại, từng ngụm từng ngụm thổ huyết."),t("br"),t("br"),n._v("Em gái ngươi ah!"),t("br"),t("br"),n._v("Muốn hay không ác như vậy?"),t("br"),t("br"),n._v("Vốn cho rằng lần này tiến lên như thế lớn, giáo huấn đối phương dễ như trở bàn tay, nằm mơ đều không nghĩ tới, chênh lệch hình như lớn hơn..."),t("br"),t("br"),n._v("Hắn đã dùng hết toàn lực, đi đánh đối phương, kết quả nhân gia lông tơ đều không có tổn thương, hắn nhưng kém chút treo..."),t("br"),t("br"),n._v("Không mang theo như thế hố! Đến cùng ngươi là phân thân, hay ta là?"),t("br"),t("br"),n._v("Giãy dụa lấy đứng dậy, Trương Huyền vẻ mặt u oán."),t("br"),t("br"),n._v("“Nhục thể của ngươi còn chưa đủ mạnh, không cách nào hoàn chỉnh phát huy ra linh hồn toàn bộ lực lượng!”"),t("br"),t("br"),n._v("Chính cảm thấy khó chịu, chỉ thấy phân thân đi tới, hai tay chắp sau lưng, mang trên mặt không cho khiêu khích uy nghiêm: “Muốn vượt qua ta, còn cần nhiều hơn ma luyện, đương nhiên, nếu như bái ta làm thầy, có thể dạy ngươi...”"),t("br"),t("br"),n._v("“Cút...”"),t("br"),t("br"),n._v("Mắt tối sầm lại, Trương Huyền một cước đạp bay, đem cái tên này thu vào nhẫn."),t("br"),t("br"),n._v("Một cái phân thân, thế mà trang bức muốn làm bản tôn lão sư, cũng không có người nào!"),t("br"),t("br"),n._v("Ngươi như thế có thể, thế nào không lên trời ơi?"),t("br"),t("br"),n._v("“Cái tên này mặc dù thật đáng giận, nói cũng không tệ!”"),t("br"),t("br"),n._v("Lấy đi cái tên này, Trương Huyền lông mày lần nữa nhăn lại."),t("br"),t("br"),n._v("Phân thân là một cái khác bản thân, mặc dù có đôi khi nói chuyện không đáng tin cậy, làm việc không theo sáo lộ, trên thực tế mới vừa nói hắn thân thể không đủ cường đại, không cách nào phát huy toàn bộ hồn lực, quả thực là thật."),t("br"),t("br"),n._v("Trải qua Địa Tâm hỏa cùng linh trì nước trợ giúp, thân thể cứ việc có rất tiến nhanh bước, có thể rèn luyện thời gian quá ngắn, chỉ là để khổng lồ linh hồn, có thể chui vào mà thôi."),t("br"),t("br"),n._v("Muốn hoàn toàn thích ứng, phát huy ra lực lượng, còn rõ ràng không đủ."),t("br"),t("br"),n._v("“Linh hồn không xứng đôi thân thể, sẽ đối với lực lượng khống chế không như vậy chính xác...”"),t("br"),t("br"),n._v("Linh hồn quá mạnh, thân thể không đủ, coi như cả hai hợp nhất, muốn đem lực lượng nắm giữ giống như trước đây, trong thời gian ngắn còn là rất khó làm được."),t("br"),t("br"),n._v("Cái này rất giống đại nhân chiếm cứ hài nhi thân thể, rất dễ dàng xuất hiện lòng có dư lực chưa đủ cục diện, thậm chí đối tự thân khí lực cũng không cách nào triệt để khống chế."),t("br"),t("br"),n._v("Hắn hiện tại chính là loại tình huống này, thân thể cứ việc có thể thả xuống được to lớn như vậy linh hồn, có thể đối lập lên, vẫn là hơi có vẻ yếu kém, đối thân thể khống chế không có trước đó như vậy chính xác."),t("br"),t("br"),n._v("Lúc trước tại Lục Trầm đại sư trong nhà, mới vừa tu luyện Thiên Đạo kim thân thời điểm, cũng xuất hiện qua tình huống tương tự."),t("br"),t("br"),n._v("Khi đó là rõ ràng cảm giác không dùng lực, trên thực tế lực lượng nhưng dùng rất lớn."),t("br"),t("br"),n._v("Lần này, lại có chút không quy luật, có đôi khi cảm thấy dùng sức rất lớn, trên thực tế rất nhỏ, có đôi khi cảm thấy rất nhỏ, trên thực tế lại rất lớn."),t("br"),t("br"),n._v("“Nhìn tới... Còn cần tiếp tục gia tăng thân thể lực lượng!”"),t("br"),t("br"),n._v("Xoa xoa mi tâm."),t("br"),t("br"),n._v("Trước đó vội vàng rèn luyện một cái nhục thể, rất rõ ràng còn chưa đủ, nhất định phải nhanh nghĩ biện pháp lần nữa gia tăng thân thể lực lượng, nếu không, một hồi lực lượng lớn, một hồi lực lượng nhỏ, người khác làm không cẩn thận còn biết cho là ngươi là bệnh tâm thần."),t("br"),t("br"),n._v("“Không được đi về hỏi Diệp Vấn Thiên bệ hạ, tìm kiếm tu luyện thân thể công pháp, ngưng tụ Thiên Đạo Kim Thân đệ tam trọng, chỉ cần ngưng tụ thành công, thân thể lực lượng khẳng định sẽ tăng lên trên diện rộng!”"),t("br"),t("br"),n._v("Chỉ cần có đầy đủ nhiều thân thể phương pháp tu luyện, thư viện liền có thể hình thành Thiên Đạo Kim Thân, hắn hoàn toàn có thể tu luyện tiến lên."),t("br"),t("br"),n._v("“Đây là đâu?”"),t("br"),t("br"),n._v("Rõ ràng những này, không tại nhiều muốn, lúc này mới chú ý hoàn cảnh chung quanh, nhìn thoáng qua, nhịn không được sững sờ."),t("br"),t("br"),n._v("“Đây là... Thanh Tắc sơn?”"),t("br"),t("br"),n._v("Trước đó vì không cho Diệp Vấn Thiên đám người nhìn thấy bản thân rời khỏi, bay thật nhanh, không biết đi bao xa, không nghĩ tới, nơi này đã cách xa Huyễn Vũ đế đô, đến Thanh Tắc sơn chỗ sâu."),t("br"),t("br"),n._v("Convert by: ThấtDạ"),t("br"),t("br"),t("br"),t("br")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("a",{attrs:{href:"./chap-656"}},[n._v("Chương trước")]),n._v("  |  "),t("a",{attrs:{href:"./chap-658"}},[n._v("Chương sau")])])])}),[],!1,null,null,null);h.default=g.exports}}]);