(window.webpackJsonp=window.webpackJsonp||[]).push([[1924],{2280:function(n,h,c){"use strict";c.r(h);var t=c(44),i=Object(t.a)({},(function(){var n=this,h=n.$createElement,c=n._self._c||h;return c("ContentSlotsDistributor",{attrs:{"slot-key":n.$parent.slotKey}},[c("h1",{attrs:{id:"chuong-654-ngo-duong-tu"}},[c("a",{staticClass:"header-anchor",attrs:{href:"#chuong-654-ngo-duong-tu"}},[n._v("#")]),n._v(" Chương 654: Ngô Dương Tử")]),n._v(" "),c("hr"),n._v(" "),c("p",[c("br"),c("br"),n._v("Chương 654: Ngô Dương Tử"),c("br"),c("br"),n._v("“Không thể đi?” Lạc Thất Thất đôi mi thanh tú nhăn lại, nhìn về phía khuê mật: “Vì cái gì?”"),c("br"),c("br"),n._v("Thí luyện, khẳng định sẽ có đủ loại nguy hiểm, lão sư cơ trí như vậy, tại cộng thêm thực lực mạnh như vậy, hắn đi, tất nhiên có thể bảo chứng mọi người an toàn, vì cái gì không chịu?"),c("br"),c("br"),n._v("“Hắn, hắn... Sẽ đùa nghịch lưu manh!”"),c("br"),c("br"),n._v("Ngọc Phi Nhi cắn răng."),c("br"),c("br"),n._v("“Đùa nghịch lưu manh? Ngươi suy nghĩ nhiều, hắn chướng mắt ngươi...”"),c("br"),c("br"),n._v("Còn tưởng rằng lý do gì, Lạc Thất Thất lắc đầu."),c("br"),c("br"),n._v("Lão sư nhân phẩm, nàng vẫn là mười phần tin tưởng."),c("br"),c("br"),n._v("Không nói cái khác, dung mạo của nàng, tuyệt đối được cho ngàn dặm mới tìm được một, cùng vị này Ngọc Phi Nhi công chúa so cũng không kém bao nhiêu, như thế xinh đẹp, lại đối hắn sùng bái có gia, cũng nhìn cũng không nhìn một cái."),c("br"),c("br"),n._v("Ngươi điêu ngoa như vậy... Liền càng không có thể!"),c("br"),c("br"),n._v("“...” Mắt tối sầm lại, Ngọc Phi Nhi thân thể nhoáng một cái."),c("br"),c("br"),n._v("Có ý tứ gì? Cái gì gọi là chướng mắt ta?"),c("br"),c("br"),n._v("Ta cũng không kém được không?"),c("br"),c("br"),n._v("Đường đường công chúa, còn chưa nói chướng mắt hắn, lại còn nói chướng mắt ta..."),c("br"),c("br"),n._v("“Thất Thất nói không sai, vị này Trương sư, ánh mắt cực cao, hắn cái kia gọi Vương Dĩnh học sinh, ta cũng gặp, so ngươi xinh đẹp hơn, cũng không nhúc nhích, hẳn là chướng mắt ngươi...” Hình Viễn gật đầu."),c("br"),c("br"),n._v("Hắn ưa thích Ngọc Phi Nhi, nhìn thấy Trương Huyền như thế ưu tú, lại cùng nàng có nhiều như vậy liên quan, nhịn không được tìm người hỏi thăm một chút."),c("br"),c("br"),n._v("Vị này Trương sư không chỉ có so Phi nhi xinh đẹp hơn học sinh, cũng không ít công chúa cấp bậc nữ tử truy cầu, nhưng một mực không hề bị lay động."),c("br"),c("br"),n._v("Phi nhi cứ việc cũng rất xinh đẹp, nhưng tính khí thực sự quá xấu rồi, cũng là mình có thể tiếp nhận, đổi lại người khác... Khẳng định không cần suy nghĩ."),c("br"),c("br"),n._v("“Ngươi nói cái gì?”"),c("br"),c("br"),n._v("Răng cắn “Khanh khách!” Rung động, Ngọc Phi Nhi con mắt trợn tròn, nhanh chằm chằm tới, mang theo trắng trợn sát ý: “Hình Viễn, có bản lĩnh lặp lại lần nữa...”"),c("br"),c("br"),n._v("“Ta...”"),c("br"),c("br"),n._v("Hình Viễn run lên."),c("br"),c("br"),n._v("Thấy hắn bị sợ đến như vậy, một bên Ngô Chấn hé miệng cười khẽ, có điều, nụ cười còn không có nở rộ, chỉ thấy Ngọc Phi Nhi lạnh lùng chằm chằm tới: “Cười cái gì cười? Có tin ta hay không lại đánh ngươi một chầu...”"),c("br"),c("br"),n._v("“...” Ngô Chấn nhoáng một cái."),c("br"),c("br"),n._v("Các ngươi ầm ĩ các ngươi, ta liền nhìn cái náo nhiệt, chuyện không ăn nhằm gì tới ta..."),c("br"),c("br"),n._v("“Tốt, chớ ồn ào, ta đi mời lão sư, Phi nhi, ngươi tốt nhất đừng nói chuyện, một khi chọc lão sư tức giận, ta sợ lại cho ngươi giao tiền, hoặc là để ngươi rót rượu đấm chân!”"),c("br"),c("br"),n._v("Thấy còn không tìm được Địa Cung cửa vào, bên này cũng đã cãi vã, Lạc Thất Thất lắc đầu."),c("br"),c("br"),n._v("“...”"),c("br"),c("br"),n._v("Nghe được giao tiền, nhớ tới tỳ nữ thân phận, Phi nhi công chúa răng cắn chặt, muốn nói điều gì rốt cuộc nói không nên lời."),c("br"),c("br"),n._v("Đình chỉ ồn ào, hướng về phía trước nhìn lại, lúc này Trương Huyền cùng linh thú chiến đấu không sai biệt lắm đã kết thúc."),c("br"),c("br"),n._v("Hơn ba mươi đầu linh thú nằm trên mặt đất, từng cái không ngừng rên rỉ, tràn đầy thê thảm."),c("br"),c("br"),n._v("Lần này thợ săn cũng không cùng lên đến, nếu không, đoán chừng cũng muốn giống như chúng nằm."),c("br"),c("br"),n._v("“Lão sư!”"),c("br"),c("br"),n._v("Đem bọn này đại gia hỏa toàn bộ đánh ngã, vỗ tay một cái, Trương Huyền chính muốn rời đi, liền nghe đến gọi tiếng vang lên, Lạc Thất Thất đi tới."),c("br"),c("br"),n._v("“Tiểu Thất!” Mỉm cười."),c("br"),c("br"),n._v("Đám người này vừa rồi tới, liền thấy."),c("br"),c("br"),n._v("Dù sao cũng là vì cứu bọn họ, thấy không nhìn thấy cũng không tính là gì."),c("br"),c("br"),n._v("“Lão sư, học sinh có cái yêu cầu quá đáng!”"),c("br"),c("br"),n._v("Đi tới trước mặt, Lạc Thất Thất khom người."),c("br"),c("br"),n._v("“Ừm?”"),c("br"),c("br"),n._v("“Chúng ta muốn mời lão sư cùng một chỗ tham gia học viện thí luyện...” Lạc Thất Thất đem ý nghĩ của mình nói một lần."),c("br"),c("br"),n._v("“Thí luyện?” Trương Huyền khoát tay áo: “Thôi được rồi, ta còn muốn trở về cùng Hồng sư bọn hắn tụ hợp, đi Hồng Viễn danh sư học viện...”"),c("br"),c("br"),n._v("“Hồng sư? Bọn hắn đã rời đi!”"),c("br"),c("br"),n._v("Sửng sốt một chút, Lạc Thất Thất nói."),c("br"),c("br"),n._v("“Rời khỏi?” Trương Huyền cau mày."),c("br"),c("br"),n._v("Bản thân vị này tên thứ nhất, còn ở nơi này không có trở về, làm sao người liền đi?"),c("br"),c("br"),n._v("“Đúng vậy a, vì không chậm trễ khai giảng, dùng Huyễn Vũ hoàng thất phi chu, đem tất cả mọi người mang đi, Hồng sư thấy ngươi không có trở về, chuyên môn xin chỉ thị Mạc đường chủ, lấy được khẳng định mới đi...”"),c("br"),c("br"),n._v("Dường như đối với chuyện này biết đến rất rõ ràng, Lạc Thất Thất hồi đáp."),c("br"),c("br"),n._v("“Phi chu?”"),c("br"),c("br"),n._v("Trương Huyền nghi hoặc."),c("br"),c("br"),n._v("“Ừm, là đế quốc mới có phương tiện giao thông, bình thường dùng cho đủ loại chiến sự, do mấy chục con linh thú, cùng khống chế, vài trăm người, hơn nghìn người đều có thể cưỡi trong đó!”"),c("br"),c("br"),n._v("Thấy mình vị này luyện đan lão sư, thế mà liền thứ này cũng chưa từng nghe qua, Lạc Thất Thất mang theo kỳ quái nhìn qua."),c("br"),c("br"),n._v("Phi chu, chỉ cần đế quốc danh sư, không có không biết, hắn như thế bác học nhiều biết, lại có lợi hại như vậy lão sư, làm sao có thể chưa từng nghe qua?"),c("br"),c("br"),n._v("Trương Huyền gật đầu."),c("br"),n._v("Trước đó hắn liền kỳ quái, một cái đế quốc phạm vi như thế lớn, một khi xuất hiện chiến sự, muốn tăng binh luôn không khả năng, để binh sĩ đi đến nhiều năm đi!"),c("br"),c("br"),n._v("Nguyên lai có loại vật này!"),c("br"),c("br"),n._v("Mấy chục con linh thú khống chế, đồng thời có thể dung nạp ngàn người... Chỉ sợ cũng chỉ có một cái đế quốc hoàng thất, mới có phách lực như thế."),c("br"),c("br"),n._v("“Đúng rồi, lão sư mấy cái học sinh, cũng bị Hồng sư bọn hắn mang đi, hình như nói, ngươi cùng Dương sư cùng một chỗ, an toàn không cần lo lắng, bọn hắn đi trước Hồng Viễn danh sư học viện chờ lấy...”"),c("br"),c("br"),n._v("Lạc Thất Thất tiếp tục nói."),c("br"),c("br"),n._v("Trương Huyền cười khổ."),c("br"),c("br"),n._v("Không nghĩ tới hôn mê ba ngày, xuất hiện nhiều chuyện như vậy."),c("br"),c("br"),n._v("Tất cả mọi người cảm thấy Dương sư mạnh mẽ, đi cùng với hắn, nhất định an toàn, nhưng bọn hắn nằm mộng cũng nghĩ không ra... Vị này lợi hại tới cực điểm Dương sư, là giả dối không có thật, căn bản không tồn tại!"),c("br"),c("br"),n._v("“Được rồi!” Lắc đầu."),c("br"),c("br"),n._v("Vốn còn nghĩ Hồng sư đám người rời khỏi, không có người chiếu cố, Tôn Cường đám người chỉ sợ sẽ có chút phiền phức."),c("br"),c("br"),n._v("Hiện tại đã đi danh sư học viện, mình tới thời điểm trực tiếp qua đi là được rồi."),c("br"),c("br"),n._v("Không cần tiếp tục lo lắng."),c("br"),c("br"),n._v("“Các ngươi đây là cái gì thí luyện?”"),c("br"),c("br"),n._v("Quay về Huyễn Vũ đế quốc đã không có ý nghĩa, Trương Huyền cũng không còn xoắn xuýt, nhịn không được nhìn qua."),c("br"),c("br"),n._v("“Là...” Lạc Thất Thất chần chờ một chút, đổi thành truyền âm: “Là... Lục tinh luyện khí sư, Ngô Dương Tử Địa Cung!”"),c("br"),c("br"),n._v("“Lục tinh luyện khí sư?” Trương Huyền lông mày nhíu một cái."),c("br"),c("br"),n._v("“Ừm, vị này Ngô Dương Tử, là ba ngàn năm trước, cực kỳ nổi danh một vị luyện khí đại tông sư, luyện khí năng lực không ai bằng! Mệnh danh Hồng Viễn đế quốc, vạn năm dĩ hàng luyện khí đệ nhất nhân. Hiện tại đế quốc sử dụng trấn quốc thần ấn, chính là hắn luyện chế thành công.”"),c("br"),c("br"),n._v("Lạc Thất Thất nói."),c("br"),c("br"),n._v("“Nếu là Hồng Viễn đế quốc luyện khí Tông sư... Làm sao Địa Cung lại ở chỗ này?” Trương Huyền nghi hoặc."),c("br"),c("br"),n._v("Hồng Viễn đế quốc cách nơi này ít nhất trăm vạn cây số, một cái luyện khí đại sư, làm sao lại chạy đến nơi đây?"),c("br"),c("br"),n._v("“Cái này...”"),c("br"),c("br"),n._v("Lạc Thất Thất vò đầu, có chút xấu hổ: “Hắn... Là bị người bắt đi!”"),c("br"),c("br"),n._v("“Bắt đi?”"),c("br"),c("br"),n._v("Đường đường lục tinh luyện khí đại tông sư, chỉ sợ thực lực, đã đi đến Thánh cấp, thế mà bị người bắt đi... Thật hay giả?"),c("br"),c("br"),n._v("Lại nói, luyện khí sư phía sau liên quan cái này luyện khí sư công hội, đây chính là toàn bộ đại lục cũng bài danh phía trên chức nghiệp liên minh, dám đối bọn hắn người ra tay... Ai lớn gan như vậy?"),c("br"),c("br"),n._v("“Ừm, hắn luyện khí năng lực năng lực mạnh đại... Bị người ngấp nghé, hẳn là nắm tới chuyên môn hỗ trợ luyện chế binh khí! Bởi vì việc này, Danh Sư đường, luyện khí sư công hội, từng phái ra đại lượng cao thủ, thậm chí đem trọn cái Hồng Viễn đế quốc đều lật một lần, cũng không tìm được...”"),c("br"),c("br"),n._v("Lạc Thất Thất nói: “Vẫn là mấy tháng gần đây, ta tìm đọc rất nhiều cổ tịch, tìm được một chút dấu vết để lại!”"),c("br"),c("br"),n._v("“Ngươi?”"),c("br"),c("br"),n._v("Trương Huyền kỳ quái."),c("br"),c("br"),n._v("Vô số cường giả cũng không tìm được, nàng một cái ngũ tinh sơ kỳ danh sư, danh sư học viện năm thứ hai học viện, làm sao có thể tìm tới? Mấu chốt nhất còn gian cách mấy ngàn năm thời gian!"),c("br"),c("br"),n._v("“Kỳ thật... Cũng chỉ là cái phỏng đoán, ta không chỉ am hiểu luyện đan, đối luyện khí cũng mười phần ưa thích... Đối vị này Ngô Dương Tử luyện khí sư, từ ở sâu trong nội tâm bội phục, chuyên môn học qua hắn sáng lập ra ... Chỉ là, truyền thừa đoạn tuyệt, không có học hết mà thôi!”"),c("br"),c("br"),n._v("Lạc Thất Thất giải thích: “Về sau, ta phát hiện, Huyễn Vũ đế quốc cảnh nội, lại có dùng loại này phương pháp luyện khí luyện chế vũ khí chảy ra, sau đó một đường truy xét, dưới cơ duyên xảo hợp, tìm đến Địa Cung địa đồ, lúc này mới hướng học viện xin, đưa ra cái này thí luyện cơ hội! Đương nhiên, cái này đến cùng phải hay không Ngô Dương Tử Tông sư Địa Cung, liền không được biết rồi.”"),c("br"),c("br"),n._v("Trương Huyền gật đầu."),c("br"),c("br"),n._v("Có thể từ phương pháp luyện khí, tìm tới dấu vết để lại, vị này Lạc Thất Thất, thật đúng là cái cố chấp người."),c("br"),c("br"),n._v("Cũng đúng, nếu như không có loại này cố chấp, đường đường danh sư học viện năm thứ hai cao tài sinh, Hóa Phàm lục trọng đỉnh phong thiên tài, cũng không có khả năng trực tiếp bái bản thân vi sư, không chút do dự!"),c("br"),c("br"),n._v("“Lão sư mặc dù còn chưa có đi danh sư học viện đưa tin, nhưng đã coi như là học viện người, hoàn toàn có tư cách tham dự... Hơn nữa, cái này thí luyện, chỉ là dò xét Địa Cung có tồn tại hay không, cùng Ngô Dương Tử đến cùng có quan hệ hay không... Nguy hiểm không lớn. Chỉ cần thành công, có có thể được hai cái học phần, cứ như vậy... Lão sư còn không có tiến vào học viện, chẳng khác nào có dựa dẫm!”"),c("br"),c("br"),n._v("Lạc Thất Thất cười nói."),c("br"),c("br"),n._v("“Học phần?” Trương Huyền nghi hoặc."),c("br"),c("br"),n._v("Làm trường cao đẳng sách báo nhân viên quản lý, cái từ này, hắn kiếp trước liền nghe qua, làm sao người danh sư này học viện cũng có loại vật này?"),c("br"),c("br"),n._v("“A, học phần là danh sư học viện đặc biệt có đồ vật, nghe nói là năm đó Khổng sư, tự mình định xuống! Có chuyên môn đánh giá tiêu chuẩn, học sinh có thể dùng thứ này mua sắm tài nguyên tu luyện tốt hơn, cao cấp hơn chương trình học... Thậm chí còn có thể trao đổi! Không có thứ này, tại học viện, sẽ nửa bước khó đi. Có thể nói, đến danh sư học viện, linh thạch, chương trình học ngọc phù, cũng không trân quý, học phần mới là trọng yếu nhất!”"),c("br"),c("br"),n._v("Lạc Thất Thất giải thích."),c("br"),c("br"),n._v("Danh sư học viện, đều là danh sư, chương trình học ngọc phù, linh thạch loại hình tất nhiên đều không thiếu khuyết, nắm giữ nhiều hơn nữa, cũng không có ý nghĩa quá lớn."),c("br"),c("br"),n._v("“Học phần không đủ... Có phải hay không không cho tốt nghiệp?”"),c("br"),c("br"),n._v("Trương Huyền vẻ mặt cổ quái nhìn qua."),c("br"),c("br"),n._v("“Đương nhiên sẽ không! Học phần chỉ là học viên giao lưu tài sản mà thôi, cũng không phải là tốt nghiệp dựa dẫm... Làm sao có thể học phần không đủ, không cho tốt nghiệp!” Lạc Thất Thất nói."),c("br"),c("br"),n._v("Trương Huyền lúc này mới thở phào nhẹ nhõm."),c("br"),c("br"),n._v("Còn tưởng rằng cùng kiếp trước trường cao đẳng đồng dạng, là học sinh tốt nghiệp nhất định phải có đồ vật, nhìn tới... Chỉ là tên tương tự mà thôi, cũng không phải là cùng một loại đồ vật."),c("br"),c("br"),n._v("“Lão sư... Muốn hay không cùng một chỗ? Nếu như nơi này thật là đã từng giam giữ Ngô Dương Tử Tông sư Địa Cung, vô cùng có khả năng có lưu lại truyền thừa của hắn!”"),c("br"),c("br"),n._v("Lạc Thất Thất nói tiếp."),c("br"),c("br"),n._v("“Truyền thừa?” Sửng sốt một chút, đột nhiên muốn được cái gì, Trương Huyền ánh mắt sáng lên: “Nghe nói luyện khí sư, đều am hiểu luyện thể, thật có truyền thừa của hắn, sẽ có hay không có cái gì luyện thể công pháp?”"),c("br"),c("br"),n._v("Mặc dù bây giờ linh nhục đã hoàn mỹ dung hợp, trên thực tế thân thể lực lượng vẫn còn có chút yếu kém, một khi hồn thể đột phá mười mét ràng buộc, khẳng định còn sẽ xuất hiện không phù hợp hiện tượng, nếu có luyện thể công pháp có thể sưu tập, tự nhiên tốt nhất."),c("br"),c("br"),n._v("Luyện khí sư, cả ngày cùng lò lửa, khoáng thạch, sắt thép tiếp xúc, thân thể nhất định phải cực mạnh mới được, bình thường đều sẽ có đặc biệt luyện thể pháp môn."),c("br"),c("br"),n._v("Convert by: ThấtDạ"),c("br"),c("br"),c("br"),c("br")]),n._v(" "),c("hr"),n._v(" "),c("p",[c("a",{attrs:{href:"./chap-662"}},[n._v("Chương trước")]),n._v("  |  "),c("a",{attrs:{href:"./chap-664"}},[n._v("Chương sau")])])])}),[],!1,null,null,null);h.default=i.exports}}]);