(window.webpackJsonp=window.webpackJsonp||[]).push([[1925],{2279:function(n,h,t){"use strict";t.r(h);var i=t(44),c=Object(i.a)({},(function(){var n=this,h=n.$createElement,t=n._self._c||h;return t("ContentSlotsDistributor",{attrs:{"slot-key":n.$parent.slotKey}},[t("h1",{attrs:{id:"chuong-655-pha-tran-ngan-cham"}},[t("a",{staticClass:"header-anchor",attrs:{href:"#chuong-655-pha-tran-ngan-cham"}},[n._v("#")]),n._v(" Chương 655: Phá trận ngân châm")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("br"),t("br"),n._v("Chương 655: Phá trận ngân châm"),t("br"),t("br"),n._v("“Hẳn là có đi! Ngô Dương Tử Tông sư, không riêng gì luyện khí sư, nghe nói cũng là một vị luyện thể đại sư!” Không nghĩ tới hắn lại như vậy hỏi, Lạc Thất Thất sửng sốt một chút, nói."),t("br"),t("br"),n._v("“Vậy thì tốt, ta đi!” Nhẹ nhàng cười một tiếng, Trương Huyền gật đầu đáp ứng."),t("br"),t("br"),n._v("Luyện khí sư, đối thân thể yêu cầu rất cao, lực lượng không đủ, rất nhiều vũ khí, liền sẽ rèn chế, bởi vậy, mỗi một vị luyện khí sư, cơ bản đều là luyện thể người trong nghề."),t("br"),t("br"),n._v("Nếu như có thể được đến cho rằng lục tinh luyện khí sư pháp quyết tu luyện, tuyệt đối so với tại Huyễn Vũ đế quốc tìm kiếm tốt hơn nhiều."),t("br"),t("br"),n._v("“Tốt!”"),t("br"),t("br"),n._v("Nghe lão sư đáp ứng, Lạc Thất Thất tràn đầy kích động."),t("br"),t("br"),n._v("Cứ việc cùng vị này luyện đan lão sư, tiếp xúc thời gian không nhiều, nhưng hắn cho người ta một loại sâu không thấy đáy cảm giác, có hắn đi qua, khẳng định sẽ thả tâm không ít."),t("br"),t("br"),n._v("Bên này thỏa đàm, gọi mọi người một tiếng, Phi nhi công chúa đám người đi tới trước mặt."),t("br"),t("br"),n._v("“Hóa Thanh trì chuyện đánh cược, ta là thua, có điều, đừng tưởng rằng bởi vậy liền có thể vân vê ta!”"),t("br"),t("br"),n._v("Vừa đến trước mặt, Phi nhi công chúa liền cắn răng nhìn qua: “Linh thạch ta sẽ chuẩn bị, có điều, có thể muốn trở lại Hồng Viễn đế quốc mới có thể cho!”"),t("br"),t("br"),n._v("“Hóa Thanh trì xuất hiện biến cố, ta cũng không có thắng, ngươi cũng không có thua... Chúng ta coi như cái ngang tay đi!”"),t("br"),t("br"),n._v("Gặp nàng bộ dáng này, đồng thời không có ý định chống chế, Trương Huyền cười một tiếng."),t("br"),t("br"),n._v("Hóa Thanh trì là hắn làm cho sập, mặc dù nhưng đã làm bồi thường, trên thực tế, vẫn là rất lúng túng."),t("br"),t("br"),n._v("Thời gian không tới, nửa đường xuất hiện biến cố, tự nhiên cũng không có gì gọi là thắng thua."),t("br"),t("br"),n._v("“Ngang tay?” Phi nhi công chúa sững sờ."),t("br"),t("br"),n._v("Vốn cho rằng đối phương, khẳng định sẽ đến thừa cơ vơ vét, để nàng tiếp tục làm cái gì tỳ nữ, lại hoặc là lời nói lạnh nhạt, để nàng khó xử, không nghĩ tới, nói thẳng tiếng ngang tay."),t("br"),t("br"),n._v("Thật... Hào phóng như vậy?"),t("br"),t("br"),n._v("“Không sai, đương nhiên, nếu là ngươi không phục, chúng ta có thể tiếp tục đánh cược, chỉ cần ngươi nguyện ý, ta tùy thời phụng bồi!”"),t("br"),t("br"),n._v("Trương Huyền nói."),t("br"),t("br"),n._v("Khóe miệng giật một cái, Phi nhi công chúa liền vội vàng lắc đầu: “Không cần...”"),t("br"),t("br"),n._v("Mới vừa cảm giác đối phương hào phóng, liền nói ra lời này, xem ra đều là giả."),t("br"),t("br"),n._v("Tùy thời phụng bồi... Ngươi coi ta khờ ah!"),t("br"),t("br"),n._v("Liên tục hai lần, người đều thua tiến vào, thật muốn tiếp tục đánh xuống, đến cuối cùng thua đoán chừng cọng lông đều không thừa một cái."),t("br"),t("br"),n._v("“Các ngươi có muốn cùng ta đánh cược sao?”"),t("br"),t("br"),n._v("Thấy nữ nhân này sợ đến như vậy, Trương Huyền nhịn không được lắc đầu, nhìn về phía Hình Viễn đám người."),t("br"),t("br"),n._v("Không có người đánh cược, chẳng khác nào thiếu đi nguồn kinh tế, nương theo tu luyện, linh thạch chung quy có sử dụng hết một ngày."),t("br"),t("br"),n._v("“Không đánh...”"),t("br"),t("br"),n._v("Hình Viễn đám người đồng thời giật nảy mình."),t("br"),t("br"),n._v("Cá với ngươi, cơ bản cũng thua, ngu nữa cũng sẽ không như thế không muốn sống ah."),t("br"),t("br"),n._v("“Ai, muốn thua cũng không có cơ hội!”"),t("br"),t("br"),n._v("Thấy ngay cả đánh cược đều không có người, Trương Huyền nhịn không được lắc đầu."),t("br"),t("br"),n._v("Những này thật đều là danh sư học viện cao tài sinh?"),t("br"),t("br"),n._v("Đánh cược cũng không dám, cũng không tránh khỏi quá yếu đi!"),t("br"),t("br"),n._v("Nếu như cho chúng người biết ý nghĩ của hắn, khẳng định lệ rơi đầy mặt, căn bản không phải bọn hắn yếu, mà là ngươi quá mạnh được không?"),t("br"),t("br"),n._v("Nhân gia phá giải bia đá, dùng năm cũng làm không được, ngươi là dùng giây; Hóa Thanh trì hấp thu linh khí, người khác dùng ngày, ngươi dùng chính là phút đồng hồ... Ai cũng sẽ không biết rõ tất thua, còn muốn đi tham gia náo nhiệt ah!"),t("br"),t("br"),n._v("..."),t("br"),t("br"),n._v("“Chúng ta mau mau đi Địa Cung đi!”"),t("br"),t("br"),n._v("Biết Trương Huyền phía sau có cái liền Mạc đường chủ cũng kính sợ lão sư, thực lực bản thân lại cường đại như thế, Hình Viễn đám người không dám tiếp tục đem hắn xem như bình thường tân sinh, mà là trở thành cùng thế hệ, không dám có chút khinh thị."),t("br"),t("br"),n._v("Việc cấp bách là tìm kiếm Địa Cung cửa vào, mọi người quen thuộc một cái, liền không nói chuyện, tăng thêm tốc độ, vòng qua trước mắt sơn mạch, lập tức đi vào một cái rộng sơn cốc."),t("br"),t("br"),n._v("“Dựa theo trên bản đồ miêu tả, Địa Cung hẳn là chính là ở đây!”"),t("br"),t("br"),n._v("Lạc Thất Thất ngừng lại."),t("br"),t("br"),n._v("“Cái này?”"),t("br"),t("br"),n._v("Hình Viễn đám người nhìn nhau, không hiểu ra sao."),t("br"),t("br"),n._v("Trước mắt sơn cốc này, không là rất lớn, thoạt nhìn cũng hết sức bình thường, đâu đâu cũng có cây cối, chim tước kêu to, tràn đầy sinh cơ, mảy may nhìn không ra có Địa Cung bộ dáng."),t("br"),t("br"),n._v("Có phải hay không là tin tức tính sai, hoặc là tìm sai địa phương?"),t("br"),t("br"),n._v("“Ừm, chuôi này dùng  luyện chế vũ khí, chính là ở đây bị một cái thợ săn phát hiện, ba ngàn năm phía trong, nơi này tổng cộng xuất hiện bảy chuôi đồng dạng thủ pháp vũ khí, lại thêm ta được đến tất cả tin tức tổng hợp, nơi đây tất nhiên có cái Địa Cung!”"),t("br"),t("br"),n._v("Lạc Thất Thất nói."),t("br"),t("br"),n._v("“Nhưng... Nơi này linh khí bình ổn, không có chút nào dị thường, không giống như là có trận pháp bộ dạng?”"),t("br"),t("br"),n._v("Hình Viễn đám người cau mày."),t("br"),t("br"),n._v("Nếu có Địa Cung không nhìn thấy, đã nói lên khẳng định là có trận pháp, ẩn giấu đi dấu vết, có thể... Trước mắt linh khí bình ổn, không có chút nào ba động, căn bản không giống có trận pháp bộ dáng."),t("br"),t("br"),n._v("“Ta đến dò xét đi, ta là ngũ tinh trận pháp sư, có hay không trận pháp, hẳn là có thể tìm tới, đồng thời phá giải!”"),t("br"),t("br"),n._v("Ngọc Phi Nhi công chúa đi ra."),t("br"),t("br"),n._v("Bọn hắn đám người này có thể tạo thành đội ngũ, cùng đi thí luyện, cũng không phải là quan hệ tốt liền có thể, còn có trên chức nghiệp bổ sung."),t("br"),t("br"),n._v("Bình thường khảo hạch ngũ tinh danh sư, cần phải có năm cái đi đến ngũ tinh phụ trợ chức nghiệp, Ngọc Phi Nhi công chúa, vừa vặn có một cái ngũ tinh trận pháp sư chức nghiệp."),t("br"),t("br"),n._v("“Ừm!” Mọi người gật đầu."),t("br"),t("br"),n._v("Phi nhi công chúa cổ tay khẽ đảo, lòng bàn tay thêm ra một cái la bàn, dọc theo sơn cốc dạo qua một vòng, một bên đi lại, một bên tìm kiếm, một lát sau, ngừng lại."),t("br"),t("br"),n._v("Tay ngọc khẽ đảo, lấy ra một cái trường mâu gai nhọn."),t("br"),t("br"),n._v("Không biết dùng cái gì luyện chế mà thành, phía trên điêu khắc cái này từng đạo đặc thù hoa văn, tản mát ra để cho người ta kính sợ hàn mang."),t("br"),n._v(" “Đây là phá trận ngân châm!”"),t("br"),t("br"),n._v("Thấy Diệp Tiền đám người vẻ mặt nghi hoặc, Lạc Thất Thất giải thích."),t("br"),t("br"),n._v("“Phá trận ngân châm?”"),t("br"),t("br"),n._v("“Ừm, một chút cấp bậc cao trận pháp, hấp thu thiên địa linh khí, y theo địa hình che giấu, rất khó coi đi ra, muốn để hắn hiện hình, liền cần thứ này. Phía trên điêu khắc có đặc thù trận văn, có thể đem vững chắc trận pháp linh khí phát tán ra, gây nên linh khí mất cân bằng, từ đó để trận pháp triển lộ hình thái!”"),t("br"),t("br"),n._v("Lạc Thất Thất nói."),t("br"),t("br"),n._v("Mọi người hiểu được, đồng thời gật đầu."),t("br"),t("br"),n._v("Trương Huyền cũng lên tiếng, tràn đầy kỳ quái nhìn qua."),t("br"),t("br"),n._v("Phá trận ngân châm, hắn từng tại trận pháp sư công hội trong thư tịch thấy qua giới thiệu, nghe nói nắm giữ loại bỏ trận pháp công hiệu, vốn cho rằng chỉ có cấp bậc cao hơn trận pháp sư công hội mới có thể nhìn thấy, không nghĩ tới vị này Ngọc Phi Nhi công chúa trên người liền có một cái."),t("br"),t("br"),n._v("Xem ra, bọn họ đích xác đến có chuẩn bị."),t("br"),t("br"),n._v("Đem phá trận ngân châm lấy ra, Ngọc Phi Nhi công chúa đồng thời không có gấp có hành động, mà là cầm la bàn lần nữa chuyển vài vòng, càng là xoay người trên mặt đất nhéo nhéo bùn đất, rút mấy cây cỏ dại."),t("br"),t("br"),n._v("Lợi hại trận pháp, có thể căn cứ hoàn cảnh chung quanh mà sửa đổi nhược điểm, tìm không thấy mấu chốt nhất vị trí, rút ra nhiều hơn nữa linh khí, cũng sẽ không đối đại trận có bất cứ tác dụng gì."),t("br"),t("br"),n._v("Thật giống như muốn lật úp một chiếc thuyền lớn, nhất định phải tại vị trí then chốt mở động đồng dạng, không có tìm đúng vị trí, coi như trên boong thuyền đánh động lại lớn, hiệu quả cũng không lớn."),t("br"),t("br"),n._v("“Các ngươi lui ra phía sau!”"),t("br"),t("br"),n._v("Lại nhìn một vòng, dường như tìm được thứ gì, Ngọc Phi Nhi quay đầu nhìn về phía mọi người, khoát tay áo."),t("br"),t("br"),n._v("Lạc Thất Thất mấy người người biết trận pháp nguy hiểm, cũng không nhiều lời, lui về phía sau, chỉ chốc lát đi tới bên ngoài sơn cốc."),t("br"),t("br"),n._v("Thấy các nàng rời khỏi, Ngọc Phi Nhi công chúa lại không trước đó bộ dáng, mà là đôi mi thanh tú nhíu lên, lộ ra vẻ mặt ngưng trọng, trong nháy mắt, khí độ sinh ra, khí khái anh hùng hừng hực, cho người ta một loại không dám khinh nhờn cảm giác."),t("br"),t("br"),n._v("“Hô!”"),t("br"),t("br"),n._v("Cổ tay rung lên, phá trận ngân châm rời khỏi tay, dọc theo cắm ở để ý đến nàng không xa một chỗ, ngay sau đó, ngón tay liên tục nắm cái mấy cái pháp quyết, bỗng nhiên một điểm."),t("br"),t("br"),n._v("Ông!"),t("br"),t("br"),n._v("Ngân châm trên mặt đất lập tức quay tròn xoay tròn."),t("br"),t("br"),n._v("Nương theo xoay tròn, ngân châm mặt ngoài trận văn, như là biến thành một cái hấp thu linh khí vòng xoáy, đem linh khí chung quanh, hướng ở giữa quét sạch, cuối cùng hình thành một đạo linh khí trụ, hướng lên bầu trời lan tràn."),t("br"),t("br"),n._v("“Phá trận ngân châm, có thể rút ra linh khí chung quanh, một khi linh khí xuất hiện kịch liệt ba động, che giấu lợi hại hơn nữa trận pháp, cũng sẽ hiển lộ hình thái!” Lạc Thất Thất giải thích."),t("br"),t("br"),n._v("Trương Huyền gật đầu."),t("br"),t("br"),n._v("Trận pháp lấy linh khí duy trì, linh khí bị rút lấy hết, tự nhiên sẽ mất đi cân bằng, hiển lộ hình thái, cùng tra ra manh mối một cái đạo lý."),t("br"),t("br"),n._v("Hóa Thanh trì sụp đổ, cũng là bởi vì cân bằng bị đánh phá."),t("br"),t("br"),n._v("Hô hô hô!"),t("br"),t("br"),n._v("Ngân châm càng chuyển càng nhanh, linh khí cũng càng rút ra càng nhiều, rất nhanh, Phi nhi công chúa trên trán toát ra mồ hôi."),t("br"),t("br"),n._v("“Mở!”"),t("br"),t("br"),n._v("Hét lên một tiếng, ngân châm chung quanh một hồi kịch liệt lắc lư, dường như có mê vụ, lúc ẩn lúc hiện."),t("br"),t("br"),n._v("“Quả nhiên có trận pháp!”"),t("br"),t("br"),n._v("Nhìn thấy mê vụ, chúng người ánh mắt sáng lên."),t("br"),t("br"),n._v("Mê vụ bốc lên là trận pháp tiêu chí, có thứ này hiện lên, nói rõ nơi này xác thực ẩn giấu đi một cái rất lợi hại trận pháp... Nói cách khác, Lạc Thất Thất suy đoán là chính xác, nơi này vô cùng có khả năng có một tòa Địa Cung!"),t("br"),t("br"),n._v("Ô ô ô ô!"),t("br"),t("br"),n._v("Giờ phút này, ngân châm chuyển càng nhanh, Phi nhi công chúa trên mặt đã tràn đầy mỏi mệt."),t("br"),t("br"),n._v("Có thể che giấu nhiều năm như vậy không được bất luận kẻ nào phát hiện, trận pháp này, tất nhiên không thấp, coi như nàng là ngũ tinh trận pháp sư, giờ phút này cũng có chút không kiên trì nổi."),t("br"),t("br"),n._v("Hô!"),t("br"),t("br"),n._v("Một ngụm trọc khí phun ra, nàng ngừng lại, cả người như là tê liệt, không để ý hình tượng ngồi dưới đất."),t("br"),t("br"),n._v("“Thế nào?”"),t("br"),t("br"),n._v("Gặp nàng cái dạng này, Lạc Thất Thất vội vàng vọt tới, cho ăn một cái hồi khí, vẻ mặt cái này mới khôi phục một chút."),t("br"),t("br"),n._v("“Nơi này có một cái cấp sáu đại trận, ta hiện tại tu vi còn chưa đủ, không cách nào triệt để đem nó dẫn ra!”"),t("br"),t("br"),n._v("Điều chỉnh một cái, Phi nhi công chúa đứng dậy, nhịn không được lắc đầu."),t("br"),t("br"),n._v("Vừa rồi nàng đã dùng hết toàn lực, cũng chỉ là để trận pháp như ẩn như hiện, muốn cho hắn triệt để biểu hiện ra ở trước mặt mọi người, rõ ràng lực có không bằng."),t("br"),t("br"),n._v("“Ngươi cũng dẫn không ra? Vậy làm sao đi vào?”"),t("br"),t("br"),n._v("Lạc Thất Thất cau mày."),t("br"),t("br"),n._v("Trận pháp cũng dẫn không ra, tự nhiên cũng là không có cách nào phá giải, càng không cần nói tiến vào Địa Cung."),t("br"),t("br"),n._v("Lời thề son sắt lại tới đây, muốn phải hoàn thành thí luyện, nằm mơ cũng không nghĩ tới, bước đầu tiên liền bị cản ở ngoài cửa."),t("br"),t("br"),n._v("Thật muốn như vậy liền trở về, đoán chừng sẽ không bị ít người sống chết cười."),t("br"),t("br"),n._v("“Ta cũng không biết...” Phi nhi công chúa lắc đầu."),t("br"),t("br"),n._v("Vốn cho là hắn ngũ tinh trận pháp sư năng lực, coi như không phá hết trận pháp, đem nó hiện hình, vẫn là rất đơn giản, không nghĩ tới suy nghĩ cả nửa ngày, không công mà lui, nhịn không được vẻ mặt mất tinh thần."),t("br"),t("br"),n._v("“Thực sự không được, ta nghỉ ngơi một chút, thử một lần nữa...” Một cắn răng, chính muốn nói tiếp, liền nghe đến một cái thanh âm nhàn nhạt vang lên."),t("br"),t("br"),n._v("“Nếu như vậy phá trận, là khẳng định không được!”"),t("br"),t("br"),n._v("Quay đầu nhìn lại, chỉ thấy Trương Huyền đi tới trước mặt."),t("br"),t("br"),n._v("“Ngươi cũng hiểu được phá trận?” Thấy là hắn nói chuyện, Phi nhi công chúa hừ một tiếng: “Vậy dạng này không được, không biết có gì cao kiến?”"),t("br"),t("br"),n._v("“Cao kiến ngược lại không đến nỗi, đã xác định cái này có trận pháp, trực tiếp đem trận pháp phá giải không liền xong rồi, tại sao phải dẫn ra để nó hiển lộ hình thái, nhiều một đạo thủ tục?”"),t("br"),t("br"),n._v("Trương Huyền nói."),t("br"),t("br"),n._v("Mục đích là tìm tới bị che giấu Địa Cung, trực tiếp phá vỡ trận pháp không được sao, vì sao nhất định phải đem nó lấy ra, tốn hao như thế bàn tay lớn chân?"),t("br"),t("br"),n._v("“Trực tiếp phá giải? Hừ, ngươi biết hay không trận pháp?”"),t("br"),t("br"),n._v("Phi nhi công chúa hất lên ống tay áo, lạnh hừ một tiếng."),t("br"),t("br"),n._v("Convert by: ThấtDạ"),t("br"),t("br"),t("br"),t("br")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("a",{attrs:{href:"./chap-663"}},[n._v("Chương trước")]),n._v("  |  "),t("a",{attrs:{href:"./chap-665"}},[n._v("Chương sau")])])])}),[],!1,null,null,null);h.default=c.exports}}]);