(window.webpackJsonp=window.webpackJsonp||[]).push([[1927],{2282:function(n,h,i){"use strict";i.r(h);var g=i(44),t=Object(g.a)({},(function(){var n=this,h=n.$createElement,i=n._self._c||h;return i("ContentSlotsDistributor",{attrs:{"slot-key":n.$parent.slotKey}},[i("h1",{attrs:{id:"chuong-657-hai-tam-bia-đa"}},[i("a",{staticClass:"header-anchor",attrs:{href:"#chuong-657-hai-tam-bia-đa"}},[n._v("#")]),n._v(" Chương 657: Hai tấm bia đá")]),n._v(" "),i("hr"),n._v(" "),i("p",[i("br"),i("br"),n._v("Chương 657: Hai tấm bia đá"),i("br"),i("br"),n._v("Càng hướng phía dưới càng cảm thấy âm lãnh, cho người ta một loại cảm giác rợn cả tóc gáy, phảng phất đi vào một cái tràn ngập âm hồn mộ địa."),i("br"),i("br"),n._v("May mắn, bốn phía chỉ là bình thường Địa Cung nên có bức tường, đồng thời không có cái gì cơ quan, trận pháp, đến để cho người ta nhẹ nhàng thở ra."),i("br"),i("br"),n._v("Đi xuống khoảng cách mấy trăm mét, dưới chân không còn, đi tới một cái rộng lớn cung điện dưới đất."),i("br"),i("br"),n._v("Hình Viễn lần nữa lấy ra Dạ Minh Châu, nhẹ nhàng gảy ra."),i("br"),i("br"),n._v("Phần phật!"),i("br"),i("br"),n._v("Minh châu lăn xuống bốn phía, cả phòng, xuất hiện mang theo mờ tối ánh sáng."),i("br"),i("br"),n._v("Tia sáng không mạnh, có điều, tất cả mọi người thực lực không yếu, có thể đủ thấy rõ tình cảnh trước mắt."),i("br"),i("br"),n._v("Thềm đá cách đó không xa là cái không lớn cầu nhỏ, dòng nước róc rách, thủy khí đập vào mặt, không biết phong bế bao nhiêu năm Địa Cung, không những không làm cho người ta buồn bực cảm giác, ngược lại cùng bên ngoài đồng dạng, không khí trong lành."),i("br"),i("br"),n._v("Loại tình cảnh này, coi như sớm làm đủ chuẩn bị, cũng không ngờ tới."),i("br"),i("br"),n._v("Bên dưới trước khi đến, tất cả mọi người cảm thấy đó là cái không biết phong bế bao lâu Địa Cung, trong đó tất nhiên có vô số khó ngửi khí tức, càng có trầm tích không tiêu tan chướng khí, ai có thể nghĩ tới sâu như vậy lòng đất, cùng phía trên không có gì khác nhau."),i("br"),i("br"),n._v("“Mọi người cẩn thận, cái này Địa Cung, có phải là vì rất nhiều người ở lại mà kiến tạo, nếu không không có khả năng có tốt như vậy để lộ tin tức!” Ngô Chấn nói."),i("br"),i("br"),n._v("“Ở lại?”"),i("br"),i("br"),n._v("Mọi người cau mày."),i("br"),i("br"),n._v("“Không sai, ít người lời nói, không cần thiết tốn hao đại tinh lực đưa vào dòng sông, càng không cần thiết để một cái huyệt động như thế thoải mái dễ chịu, tất nhiên là rất nhiều người ở lại, không làm tốt để lộ tin tức, gánh chịu không được nhiều người như vậy hô hấp!” Ngô Chấn giải thích."),i("br"),i("br"),n._v("Hắn tinh thông cơ quan, biết dưới đất dẫn tới dòng sông phiền phức, rõ ràng hơn, tốt như vậy để lộ tin tức, cần phải hao phí đại giới cỡ nào."),i("br"),i("br"),n._v("Nếu như chỉ là số ít người tạm cư địa phương, như thế mênh mông địa phương, đã không ngại buồn bực, không cần thiết lại lãng phí thời gian, chỉ có lại nhiều người, không làm tốt để lộ tin tức, rất dễ dàng ngạt thở."),i("br"),i("br"),n._v("Nghe được giải thích của hắn, Trương Huyền nhẹ gật đầu."),i("br"),i("br"),n._v("Lần trước đi Vu hồn mộ, rất rõ ràng cũng có chút buồn bực, không khí lưu thông bên trên, kém xa nơi này."),i("br"),i("br"),n._v("“Chẳng lẽ, trói đi Ngô Dương Tử đại tông sư người, không phải một cái, mà là rất nhiều?”"),i("br"),i("br"),n._v("Lạc Thất Thất nháy nháy mắt."),i("br"),i("br"),n._v("Căn cứ nàng điều tra, cái này Địa Cung cùng Ngô Dương Tử có quan hệ, thậm chí vô cùng có khả năng, lúc trước vị đại tông sư này, chính là bị trói tới đây... Rất nhiều người ở lại môi trường, mà không phải một hai cái..."),i("br"),i("br"),n._v("Chẳng lẽ vị đại tông sư này, cũng không phải là bị một người trói đi, mà là bị một đám người bắt giữ?"),i("br"),i("br"),n._v("“Vậy cũng không biết...”"),i("br"),i("br"),n._v("Ngô Chấn lắc đầu: “Bên kia có cái bia đá, đi qua nhìn một chút.”"),i("br"),i("br"),n._v("Cầu phía trước có một cái không lớn bia đá, mờ tối ánh sáng bên dưới, ngờ ngợ có thể nhìn thấy phía trên ghi chú cái gì."),i("br"),i("br"),n._v("Đi tới trước mặt, ba chữ to đập vào mi mắt."),i("br"),i("br"),n._v("“Vọng Hương cư?”"),i("br"),i("br"),n._v("Lạc Thất Thất cau mày."),i("br"),i("br"),n._v("Địa Cung, bình thường không phải mộ huyệt, chính là một cái thế lực trụ sở bí mật hoặc là ẩn cư chỗ, làm ra ba chữ này, có ý tứ gì?"),i("br"),i("br"),n._v("“Những chữ này thể có chút cổ quái!”"),i("br"),i("br"),n._v("Diệp Tiền cau mày."),i("br"),i("br"),n._v("“Ồ?” Nghe hắn chen vào nói, tất cả mọi người nhìn qua."),i("br"),i("br"),n._v("Vị này thái tử, mặc dù chỉ là nửa bước ngũ tinh danh sư, nhưng ở thư hoạ nhất đạo bên trên, nhưng đạt đến ngũ tinh cấp bậc, đối những vật này có tự nhiên mẫn cảm."),i("br"),i("br"),n._v("“Chữ có đôi khi có thể đại biểu một người trạng thái tinh thần cùng ý tưởng, ba chữ này, đạt đến tam cảnh , có thể rõ ràng cảm nhận được lưu chữ lòng người trạng thái! Có loại cùng nơi này không hợp nhau, muốn rời xa huyên náo ý vị!”"),i("br"),i("br"),n._v("Diệp Tiền nói."),i("br"),i("br"),n._v("Trương Huyền gật đầu, hắn nói những này, bản thân cũng cảm nhận được."),i("br"),i("br"),n._v("Vọng Hương cư, ba chữ, không riêng gì tự ý cho người ta một loại rời xa cố thổ, muốn muốn về nhà hương vị, chữ ý cảnh cũng có thể cảm thụ đi ra."),i("br"),i("br"),n._v("Hắn đối thư hoạ rất có nghiên cứu, coi như không cần thư viện, chỉ bằng vào mắt thường cũng nhìn ra."),i("br"),i("br"),n._v("“Nhớ nhà? Nhìn tới... Cái này đích xác là Ngô Dương Tử đại tông sư ở lại Địa Cung!”"),i("br"),i("br"),n._v("Lạc Thất Thất ánh mắt sáng lên: “Hẳn là hắn bị người bắt đi, tưởng niệm Hồng Viễn đế quốc, mới lưu lại chữ viết, chỉ bất quá, thẳng đến trước khi chết, đều không có trở về!”"),i("br"),i("br"),n._v("“Nói như vậy, ngược lại là có khả năng!” Hình Viễn đám người gật đầu."),i("br"),i("br"),n._v("Một cái Địa Cung gọi Vọng Hương cư, bản thân liền rất quái dị, nếu như là bị người bắt đến nơi đây, liền có thể nói thông."),i("br"),i("br"),n._v("Vô cùng có khả năng, mấy chữ này là vị kia Ngô Dương Tử lưu lại, dùng để diễn tả mình nhớ nhà chi tình."),i("br"),i("br"),n._v("“Ta cảm thấy không đúng, những chữ này, không chỉ có nhớ nhà chi ý, còn ẩn chứa sát phạt khí tức, cho người ta một loại kim qua thiết mã hương vị...” Diệp Tiền cau mày: “Ngô Tông sư, một đời cùng người cùng thiện, sẽ không có loại này cuồng bạo giết chóc chi ý đi!”"),i("br"),i("br"),n._v("Ngô Dương Tử luyện khí đại tông sư, một đời cùng vũ khí làm bạn, mặc dù rèn đúc chính là giết người chi nhận, mà bản thân hắn nhưng không có giết người chi tức giận."),i("br"),i("br"),n._v("Nhưng trước mắt này ba chữ, một luồng giết chóc chi ý đập vào mặt, để cho người ta không thể không hoài nghi."),i("br"),i("br"),n._v("“Ha ha, ta phản lại cảm thấy có sát lục chi khí, mới là hắn lưu lại!”"),i("br"),i("br"),n._v("Hình Viễn cười cười: “Đường đường lục tinh luyện khí sư, một đời đại tông sư, bị người bắt đi, nhốt tại loại này chỗ không thấy mặt trời, nếu như không có sát tâm, thật sự kì quái!”"),i("br"),i("br"),n._v("“Như thế...”"),i("br"),i("br"),n._v("Diệp Tiền sững sờ."),i("br"),i("br"),n._v("Cũng đúng, như thế người có thân phận địa vị, bị bắt được chỗ hắc ám, đi cũng đi không được, đổi lại ai, khẳng định cũng sẽ không cam tâm, sinh ra giết chóc chi ý cũng là chuyện đương nhiên."),i("br"),i("br"),n._v("“Ngô Dương Tử tiền bối nhưng mà lục tinh đỉnh phong luyện khí sư, thực lực sớm đã đi đến Thánh cấp, nhân vật như vậy, tâm không cam lòng, nhưng cũng không thể rời khỏi... Mọi người cẩn thận, ta sợ nơi này không có đơn giản như vậy!”"),i("br"),i("br"),n._v("Lạc Thất Thất khuôn mặt ngưng trọng."),i("br"),i("br"),n._v("“Ừm!”"),i("br"),i("br"),n._v("Mọi người đồng thời gật đầu, riêng phần mình tinh thần khẩn trương."),i("br"),i("br"),n._v("Có thể đem Thánh giả, quan tại trong cung điện dưới lòng đất, ra cũng ra không được, làm sao có thể đơn giản? Mặc dù qua ba ngàn năm, loại người này lưu hạ thủ đoạn, cũng chưa hẳn là bọn hắn bọn này chỉ có Kiều Thiên cảnh, Hợp Linh cảnh tiểu gia hỏa, có thể chống lại."),i("br"),i("br"),n._v("“Đi thôi!”"),i("br"),n._v(" Đem bia đá nhìn một vòng, lại không có phát hiện cái khác vật hữu dụng, mọi người đi thẳng về phía trước."),i("br"),i("br"),n._v("Cùng ở sau lưng mọi người, Trương Huyền lần nữa nhìn thoáng qua trên tấm bia đá kiểu chữ, mày nhăn lại, ngay sau đó ngón tay ở phía trên nhẹ nhàng vạch một cái, dường như cảm giác được cái gì, lông mày nhíu một cái."),i("br"),i("br"),n._v("Đi ngang qua bia đá, đi tới trên cầu."),i("br"),i("br"),n._v("Cầu không dài, phía dưới dòng nước ngược lại là rất nhanh, phát ra xao động thanh âm, để người tinh thần nhẹ nhàng khoan khoái."),i("br"),i("br"),n._v("Ngô Chấn đem Thiên Cơ tán để ở phía trước thăm dò đường, đối mọi người nhẹ gật đầu, làm trước đi tới, mọi người theo sát trên đó."),i("br"),i("br"),n._v("Cầu đằng sau là cái rộng lớn đại sảnh, bên trong vẫn như cũ súc lập một cái cự đại bia đá."),i("br"),i("br"),n._v("Hình Viễn cầm trong tay Dạ Minh Châu, đi tới trước mặt, nhạt hào quang màu vàng bên dưới, liền thấy phía trên ghi ba chữ to —— Vong Ưu cư."),i("br"),i("br"),n._v("“Cái này...”"),i("br"),i("br"),n._v("Nhìn thấy phía trên chữ, mọi người nháy con mắt."),i("br"),i("br"),n._v("Mặc kệ Địa Cung vẫn là cái gì, một chỗ luôn không khả năng có hai cái tên đi!"),i("br"),i("br"),n._v("Mới vừa rồi là Vọng Hương cư, hiện tại lại Vong Ưu cư, đến cùng cái nào là chỗ này tên?"),i("br"),i("br"),n._v("“Ba chữ này, ôn hoà công chính, cho người ta một loại thản nhiên cảm giác, vong ưu, vui mà vong ưu, nói rõ nơi này có vật hắn muốn, mới có thể yên vui...”"),i("br"),i("br"),n._v("Diệp Tiền hướng về phía trước nhìn một hồi, cho ra phân tích: “Nếu ta đoán không lầm, đây cũng là giam giữ Ngô Dương Tử đại tông sư những người kia lưu lại!”"),i("br"),i("br"),n._v("“Ngươi nói không sai, bọn hắn nhốt Ngô Tông sư, để nó vì chính mình luyện chế bảo vật, tự nhiên muốn vui mà vong ưu...”"),i("br"),i("br"),n._v("Hình Viễn gật đầu."),i("br"),i("br"),n._v("Lạc Thất Thất, Ngọc nhi công chúa chần chờ một chút, cũng đồng thời gật đầu."),i("br"),i("br"),n._v("Như vậy vừa vặn có thể giải thích thông."),i("br"),i("br"),n._v("Chỉ là có chút không rõ, vì cái gì một cái Địa Cung, bên này cầu lưu một cái tên, bên kia lưu một cái tên."),i("br"),i("br"),n._v("Đi qua bia đá, mọi người đi tới đại điện, gian phòng chân có mấy trăm mét vuông, mênh mông đến cực điểm, không có cái gì."),i("br"),i("br"),n._v("“Ta đi xem một chút!”"),i("br"),i("br"),n._v("Ngô Chấn dọc theo bức tường chung quanh gõ một hồi, dạo qua một vòng, lập tức lắc đầu: “Nơi này... Không có có cơ quan!”"),i("br"),i("br"),n._v("“Chẳng lẽ lại là che giấu trận pháp?”"),i("br"),i("br"),n._v("Phi nhi công chúa cầm trong tay trận bàn, cũng chầm chậm dạo qua một vòng, vẻ mặt khó coi."),i("br"),i("br"),n._v("“Ta xem, cũng không có trận pháp!”"),i("br"),i("br"),n._v("“Không biết cái này cái Địa Cung cứ như vậy đại chứ?”"),i("br"),i("br"),n._v("Mọi người lần nữa cau mày."),i("br"),i("br"),n._v("Bên ngoài bố trí lợi hại như vậy một cái che giấu đại trận, lại sửa chữa hướng phía dưới vài trăm mét cầu thang, càng làm cho cung điện không khí thông suốt, liền chỉ tu luyện một cái mênh mông như dã, chỉ có mấy trăm mét vuông đại điện?"),i("br"),i("br"),n._v("Nếu thực như thế, cũng không tránh khỏi quá hố!"),i("br"),i("br"),n._v("Nhưng nếu không phải như thế, chung quanh loại trừ bức tường, trận pháp cơ quan, cái gì đều không có, đến cùng chuyện gì xảy ra?"),i("br"),i("br"),n._v("“Chỉ là cái mênh mông đại điện lời nói, chúng ta không cách nào phán định có phải hay không Ngô Dương Tử đã từng vị trí, cũng là cho thấy nhiệm vụ chưa hoàn thành...”"),i("br"),i("br"),n._v("Lạc Thất Thất cau mày."),i("br"),i("br"),n._v("Đối với Ngô Chấn cùng Phi nhi công chúa nhãn lực, nàng là mười phần tin phục."),i("br"),i("br"),n._v("Hai người đã nói nơi này không có những vật này, hẳn là không có."),i("br"),i("br"),n._v("Nhưng... Cái gì đều không có, rỗng tuếch, cũng là không cách nào phán định, Ngô Dương Tử có hay không ở đây đợi qua, trở về bàn giao thế nào?"),i("br"),i("br"),n._v("Kỳ quái hơn chính là, đã như vậy mênh mông, cái gì đều không có, vì cái gì ba ngàn năm nay, cũng có vũ khí lưu lạc bên ngoài, hơn nữa còn là dùng Ngô Dương Tử Tông sư thủ pháp độc môn luyện chế?"),i("br"),i("br"),n._v("“Đúng vậy a, thiên tân vạn khổ chạy tới, còn tưởng rằng có thể được đến là cái gì, không nghĩ tới chỉ là cái đại điện trống trải!”"),i("br"),i("br"),n._v("Hình Viễn cũng lắc đầu."),i("br"),i("br"),n._v("Còn muốn lấy tiến vào Địa Cung, chí ít có thể tìm tới dấu vết gì, kết quả loại trừ hai cái bia đá, một cái cầu nhỏ, cái gì đều không có, chẳng lẽ lần này cần tới không?"),i("br"),i("br"),n._v("“Lão sư, ngươi... Có phải hay không nhìn ra cái gì?”"),i("br"),i("br"),n._v("Mọi người ngay tại buồn bực, liền nghe Lạc Thất Thất tiếng âm vang lên, đồng loạt nhìn lại, chỉ thấy Trương Huyền chẳng biết lúc nào lại đến cái thứ hai trước tấm bia đá, đưa tay sờ đi qua."),i("br"),i("br"),n._v("Cái này bia đá viết là Vong Ưu cư, hẳn là vây khốn Ngô Dương Tử những người kia lưu lại."),i("br"),i("br"),n._v("“A, nếu như ta đoán không lầm, hai cái này bia đá, có chút cổ quái!”"),i("br"),i("br"),n._v("Trương Huyền xoay đầu lại."),i("br"),i("br"),n._v("“Cổ quái?”"),i("br"),i("br"),n._v("Mọi người đi tới trước mặt."),i("br"),i("br"),n._v("“Ừm, một cái Vong Ưu cư, một cái Vọng Hương cư... Ta đoán không sai, hẳn là hai con đường, bia đá lưu lại chữ viết, là để kẻ đến sau, tiến hành lựa chọn!”"),i("br"),i("br"),n._v("Trương Huyền đem suy đoán của mình nói ra."),i("br"),i("br"),n._v("“Nói như vậy, ngược lại là có khả năng!”"),i("br"),i("br"),n._v("Mọi người sững sờ."),i("br"),i("br"),n._v("Toàn bộ Địa Cung, bắt mắt nhất chính là hai cái này bia đá, có lẽ thứ này phía dưới ẩn giấu đi một loại nào đó cơ quan."),i("br"),i("br"),n._v("“Ta đến xem!”"),i("br"),i("br"),n._v("Nghe được giải thích, Ngô Chấn chần chờ một chút, đi tới trước mặt."),i("br"),i("br"),n._v("Cũng xuất ra một cái Dạ Minh Châu, tử quan sát kỹ, đem trước tấm bia đá sau bên trái sau tường nhìn kỹ một lần, con mắt đột nhiên sáng lên."),i("br"),i("br"),n._v("“Hình như cái này... Quả thực có cơ quan!”"),i("br"),i("br"),n._v("“Có cơ quan?”"),i("br"),i("br"),n._v("“Có cơ quan liền có thông đạo, xem ra cái này căn bản cũng không phải là Địa Cung, chân chính Địa Cung còn không có xuất hiện!”"),i("br"),i("br"),n._v("Mọi người tất cả đều tinh thần chấn động, đi tới trước mặt."),i("br"),i("br"),n._v("Convert by: ThấtDạ"),i("br"),i("br"),i("br"),i("br")]),n._v(" "),i("hr"),n._v(" "),i("p",[i("a",{attrs:{href:"./chap-665"}},[n._v("Chương trước")]),n._v("  |  "),i("a",{attrs:{href:"./chap-667"}},[n._v("Chương sau")])])])}),[],!1,null,null,null);h.default=t.exports}}]);