(window.webpackJsonp=window.webpackJsonp||[]).push([[1931],{2286:function(n,h,t){"use strict";t.r(h);var i=t(44),c=Object(i.a)({},(function(){var n=this,h=n.$createElement,t=n._self._c||h;return t("ContentSlotsDistributor",{attrs:{"slot-key":n.$parent.slotKey}},[t("h1",{attrs:{id:"chuong-58-han-lam-sao-lam-đuoc"}},[t("a",{staticClass:"header-anchor",attrs:{href:"#chuong-58-han-lam-sao-lam-đuoc"}},[n._v("#")]),n._v(" Chương 58: Hắn làm sao làm được?")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("br"),t("br"),n._v(" Mới vừa rồi bị đối phương nói ra kim phiếu, hắn cũng kinh hoảng, nhưng vẫn còn có chút ngụy chứa ở bên trong, mà bây giờ là thực sự sợ hãi, chỉ cảm thấy một cái hàn khí từ trán xông thẳng mà xuống, khác nào phải đem hắn đông thành băng nhân."),t("br"),t("br"),n._v(" Một cái gọi ra tên của hắn không hiếm thấy, dù sao hắn cũng coi như được với kẻ tái phạm, bị nhân truy nã, lưu tâm điểm bị nhân phát hiện cũng có thể."),t("br"),t("br"),n._v(" Có thể... Tu luyện Hoặc Âm Thuật, liền lão nhị bọn họ đều không rõ ràng, tự mình càng là ai cũng không dám nói, làm sao có khả năng biết đến rõ rõ ràng ràng?"),t("br"),t("br"),n._v(" Đây chính là hắn một lần kỳ ngộ bên trong, thật vất vả lấy được bí tịch, một mực bị ẩn giấu ở đáy lòng, tự phụ thiên hạ không người thứ hai biết hiểu, đối phương một cái nói ra, mãnh liệt khiếp sợ, sắp đem hắn dọa chết tươi."),t("br"),t("br"),n._v(" Bí tịch chủng loại rất nhiều, có thân thể, võ kỹ, công pháp... Bất quá, những này đều rất dễ dàng có thể tìm tới, vương quốc cảnh nội, trân quý nhất e sợ phải kể tới này loại liên quan với tinh thần bí thuật!"),t("br"),t("br"),n._v(" Hoặc Âm Thuật, một loại cực kỳ cấp thấp tinh thần bí thuật, dù là thấp cấp, cũng là tấn công bằng tinh thần tuyệt chiêu, một khi triển khai, mạnh hơn nhân không chút phòng bị phía dưới, cũng sẽ trúng chiêu, lúc trước hắn từng bị một cái võ giả bảy tầng cường giả nắm lấy, chính là bởi vì dựa vào cái này tuyệt chiêu, miệng phun hoa sen, mới khiến cho đối phương tha cho hắn một mạng."),t("br"),t("br"),n._v(" Vừa nãy cố ý xin tha, trên thực tế định dùng loại thủ đoạn này, ma túy đối phương!"),t("br"),t("br"),n._v(" Nằm mơ đều không có nghĩ đến, đối phương không những không có bị ma túy, cũng còn tốt giống toàn trí toàn năng, đối với hắn biết tất cả mọi chuyện như thế, ở trước mặt đối phương, dường như bị dời đi ngụy trang, cả người trần trụi, không hề có một chút che giấu, làm sao không cảm thấy hoảng hốt?"),t("br"),t("br"),n._v(" Trước vẫn là sợ sệt, hiện tại đã là sợ hãi."),t("br"),t("br"),n._v(" “Không cần phải để ý đến ta làm sao mà biết được, không riêng biết ngươi những này, ngươi tu luyện Mệnh Môn loại hình, ta cũng rõ rõ ràng ràng! Ta có thể không giết ngươi, bất quá, từ ngày hôm nay bắt đầu, nếu như cho ta biết tiếp tục lừa người, làm xằng làm bậy, chờ chết là được rồi!” "),t("br"),t("br"),n._v(" Không ở phí lời, Trương Huyền một cước đá vào trên người đối phương."),t("br"),t("br"),n._v(" Răng rắc!"),t("br"),t("br"),n._v(" Một tiếng trứng gà phá toái âm thanh, tên lừa đảo Dương Mặc liền cảm thấy Đan Điền một trận nổ vang, nhiều năm tu luyện mà đến sức mạnh, hóa thành nước chảy."),t("br"),t("br"),n._v(" Không còn thực lực, coi như muốn lừa người, e sợ cũng không dám."),t("br"),t("br"),n._v(" “Tu vi của ta...” "),t("br"),t("br"),n._v(" Cảm nhận được thực lực biến mất, Dương Mặc lòng tràn đầy sự thù hận, cũng không dám chút nào phí lời."),t("br"),t("br"),n._v(" Đối phương quá kinh khủng, đã không phải là nhân, mà là yêu quái."),t("br"),t("br"),n._v(" Yêu quái phá của hắn Đan Điền, phế bỏ tu vi của hắn, làm sao dám phản kháng?"),t("br"),t("br"),n._v(" “Cút đi!” "),t("br"),t("br"),n._v(" Chỗ tốt nhận được, xử lý xong tên lừa đảo, Trương Huyền nhanh chân hướng học viện phương hướng đi đến."),t("br"),t("br"),n._v(" Một cái tên lừa đảo mà thôi, còn không đáng được hạ tử thủ."),t("br"),t("br"),n._v(" Trở lại nơi ở, gần như đã đến một giờ đêm."),t("br"),t("br"),n._v(" Bất quá, Trương Huyền không có một chút nào cơn buồn ngủ."),t("br"),t("br"),n._v(" Chuyện tối nay thực sự nhiều lắm, theo nhau mà tới, nếu không có Thiên Đạo Thư Viện cái này dối trá Thần khí, thật không biết ứng phó như thế nào."),t("br"),t("br"),n._v(" “Ở Lục Trầm đại sư nơi đó nhìn mười mấy bản võ giả sáu tầng công pháp, nhìn có thể hay không trước tiên sửa sang một chút!” "),t("br"),t("br"),n._v(" Trước chi tu luyện Thiên Đạo Kim Thân, không chút nhìn kỹ võ giả sáu tầng công pháp, hiện tại có thời gian, vừa vặn có thể tỉ mỉ nghiên cứu một chút."),t("br"),t("br"),n._v(" Rất nhanh, mười mấy bản bí tịch công chính xác thực phương thức tu luyện bị trích lục đi ra, bất quá liếc mắt nhìn, Trương Huyền đau cả đầu."),t("br"),t("br"),n._v(" “Này cái nào cái nào đều liên lạc không được a... Dựa theo cái này tu luyện, nhất định sẽ tẩu hỏa nhập ma, luyện chết tươi!” "),t("br"),t("br"),n._v(" Lắc đầu một cái, Trương Huyền bất đắc dĩ."),t("br"),t("br"),n._v(" Tuy rằng có mười mấy bản bí tịch, khuyết điểm vẫn là quá nhiều, coi như đem chính xác địa phương trích lục đi ra, cũng không cách nào tạo thành một bộ công pháp, trái lại càng thêm trăm ngàn chỗ hở, căn bản là không có cách tu luyện."),t("br"),t("br"),n._v(" “Được rồi, vẫn là nghĩ biện pháp lại nhìn càng nhiều thư tịch nói sau đi!” "),t("br"),t("br"),n._v(" Biết không có cách nào sử dụng, Trương Huyền cũng không vội vã, đem thư viện chỉnh lý xong tiền tam trọng công pháp, lại tu luyện từ đầu một lần, xác định cùng trong cơ thể tu vi hoàn mỹ dung hợp, lúc này mới từ từ thiếp đi."),t("br"),n._v("..."),t("br"),t("br"),n._v(" Trương Huyền không biết, hắn chạy đến thương trường mua đồ vật thời điểm, bên trong học viện, Thượng Thần trưởng lão sân đã sắp muốn nổ tung."),t("br"),t("br"),n._v(" “Gia gia, ngươi phải làm chủ cho ta a!” "),t("br"),t("br"),n._v(" Thượng Bân nước mắt tứ giàn giụa, khóc kinh thiên động địa."),t("br"),t("br"),n._v(" Làm như đường đường trưởng lão cháu trai ruột, từ nhỏ ngậm lấy chìa khóa vàng lớn lên, thêm vào thiên phú không tệ, một mực được người ta tôn trọng, ngày hôm nay lại đem cả đời đều không có bị qua ngăn trở đều chịu một lần, không tại chỗ điên rồi cũng rất không tệ."),t("br"),t("br"),n._v(" Đi tìm phiền phức, kết quả lao ra cái Phong Tử, đem hắn đánh thành đầu heo, buổi tối đi ăn cơm, kết quả nhìn thấy nữ thần cùng tiểu tử kia có ăn có uống, vừa nói vừa cười... Hãm hại không hữu hiệu, mất mặt xấu hổ ngược lại cũng thôi, mấu chốt là..."),t("br"),t("br"),n._v(" “Gia gia, ngươi không nói đầu này Bạo Thiên Sư rất nghe lời sao? Ta để hắn giáo huấn tên phế vật kia Trương Huyền, nó tại sao không những bất động, còn đem ta đánh cho một trận...” "),t("br"),t("br"),n._v(" Càng nghĩ càng giận, Thượng Bân chỉ cảm thấy trong lòng đều là nước mắt."),t("br"),t("br"),n._v(" Trước mắt vị này đầu Bạo Thiên Sư, ăn nhà mình, uống nhà mình, thời khắc mấu chốt không nghe lời ngược lại cũng thôi, phản trợ giúp người ngoài đánh tự mình... Ngẫm lại đều cảm thấy sắp điên rồi."),t("br"),t("br"),n._v(" “Nó bình thường là rất nghe lời...” Thượng Thần có chút lúng túng, nhìn về phía trước mắt Bạo Thiên Sư, cũng là đầy mặt kỳ quái."),t("br"),t("br"),n._v(" Hắn chỉ là phổ thông Tuần thú sư, cấp bậc không tính quá cao, coi như có thể thu phục Man Thú, cũng là bình đẳng giao lưu, cũng không phải là người hầu đồng dạng đối đãi!"),t("br"),t("br"),n._v(" Chính vì như thế, Bạo Thiên Sư muốn làm gì, có nó chính mình chủ ý, có thể không cần nhìn sắc mặt của chính mình, suy đoán ý nghĩ của chính mình."),t("br"),t("br"),n._v(" Có thể... Bất kể nói thế nào, hai người đều là khế ước quan hệ, để nó bảo vệ cháu, làm sao... Ngược lại nghe xong lời của người khác, đối với Thượng Bân ra tay đánh nhau?"),t("br"),t("br"),n._v(" “Rống!” "),t("br"),t("br"),n._v(" Thấy đối phương xem ra, Bạo Thiên Sư một mặt kiêu ngạo, đầu lâu ngang cao cao, một mặt không để ý chút nào."),t("br"),t("br"),n._v(" Nhìn thấy hắn cái bộ dáng này, Thượng Bân cảm thấy nội tâm lần thứ hai gặp 10 ngàn điểm thương tổn."),t("br"),t("br"),n._v(" Nhìn thấy Trương Huyền thời điểm, đầu lưỡi liếm tay của người ta, một mặt cung kính, chó bông như thế, bây giờ lại kiêu ngạo dường như Quốc vương... Giời ạ, đến cùng hắn là chủ nhân của ngươi, vẫn là ông nội ta là?"),t("br"),t("br"),n._v(" “Khụ khụ, Bạo Thiên Sư ta quay đầu lại sẽ cố gắng khuyên nhủ!” Nhìn thấy tự mình thú sủng dáng vẻ ấy, rõ ràng từ chối hợp tác, Thượng Thần trưởng lão sắc mặt một đỏ, khoát tay áo một cái."),t("br"),t("br"),n._v(" “Gia gia, lần này ngươi nhất định phải thay ta cố gắng giáo huấn một hồi cái này Trương Huyền, tốt nhất thủ tiêu của hắn giáo sư tư cách, đuổi ra học viện!” "),t("br"),t("br"),n._v(" Biết gia gia cùng Bạo Thiên Sư cũng chỉ được cho bình đẳng quan hệ, không thể giáo huấn đối phương, Thượng Bân không trong vấn đề này tiếp tục dây dưa, cắn răng nói."),t("br"),t("br"),n._v(" Một sư tư sát hạch được không điểm rác rưởi, để hắn làm mất đi lớn như vậy mặt mũi, còn thương tích khắp người, kém chút bỏ xuống, này loại cừu hận dốc hết nước sông lớn cũng khó có thể rửa sạch!"),t("br"),t("br"),n._v(" “Yên tâm đi, chuyện này ta sẽ xử lý! Bất quá, cái này Trương Huyền không biết đi rồi cái gì đại vận, dĩ nhiên chiêu thu đến năm cái học sinh, không có thích hợp cớ, coi như là ta, muốn khai trừ một vị đăng kí qua lão sư, cũng sẽ rất phiền phức!” "),t("br"),t("br"),n._v(" Thượng Thần trưởng lão nói."),t("br"),t("br"),n._v(" Trương Huyền là đại lục giáo sư công hội đăng kí qua giáo sư, thuộc về chính thức giáo sư Chức Nghiệp, coi như hắn là học Viện trưởng già, phòng Giáo Đạo chủ nhiệm, đối phương chỉ cần không đáng sai lầm lớn, cũng không tư cách khai trừ."),t("br"),t("br"),n._v(" Một bên lắc đầu, một bên ánh mắt lộ ra nghi hoặc."),t("br"),t("br"),n._v(" Một sư tư sát hạch là không phân gia hỏa, ai cũng cho rằng cũng lại chiêu thu không tới học sinh, nằm mơ đều không có nghĩ đến, không chỉ nhận được, còn chiêu ngã năm cái, trong đó còn không thiếu sát hạch đạt đến 100 người đứng đầu!"),t("br"),t("br"),n._v(" Không phải tận mắt thấy, thật cảm thấy khó mà tin nổi."),t("br"),t("br"),n._v(" Thậm chí... Coi như hắn nhìn phòng Giáo Đạo tập hợp kết quả, vẫn như cũ cảm thấy khó có thể tin."),t("br"),t("br"),n._v(" Cái tên này... Đến cùng làm sao làm được?"),t("br"),t("br"),n._v(" Hoàn toàn vượt ra khỏi thường thức có được hay không?"),t("br"),t("br"),n._v("Convert by: Hoang Chau"),t("br"),t("br"),t("br"),t("br")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("a",{attrs:{href:"./chap-66"}},[n._v("Chương trước")]),n._v("  |  "),t("a",{attrs:{href:"./chap-68"}},[n._v("Chương sau")])])])}),[],!1,null,null,null);h.default=c.exports}}]);