(window.webpackJsonp=window.webpackJsonp||[]).push([[1941],{2296:function(n,h,t){"use strict";t.r(h);var i=t(44),c=Object(i.a)({},(function(){var n=this,h=n.$createElement,t=n._self._c||h;return t("ContentSlotsDistributor",{attrs:{"slot-key":n.$parent.slotKey}},[t("h1",{attrs:{id:"chuong-670-diet-toc"}},[t("a",{staticClass:"header-anchor",attrs:{href:"#chuong-670-diet-toc"}},[n._v("#")]),n._v(" Chương 670: Diệt tộc")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("br"),t("br"),n._v("Chương 670: Diệt tộc"),t("br"),t("br"),n._v("“Lão sư!” Đi tới trước mặt, Lạc Thất Thất ôm quyền."),t("br"),t("br"),n._v("Vừa rồi rơi vào đốn ngộ, cũng không biết trước mắt vị này hoàn thành trời nhận danh sư, chỉ biết là đã trải qua lần này lĩnh ngộ, nàng đối luyện đan, luyện khí lý giải, có bay vọt về chất."),t("br"),t("br"),n._v("Trước kia luyện khí, nàng chỉ có thể coi là được có thiên phú, mà lần này trở về, coi như luyện khí học viện, mấy cái kia nổi danh thiên tài, cũng có tự tin, tại chỗ chọn dưới ngựa!"),t("br"),t("br"),n._v("“Không sai!”"),t("br"),t("br"),n._v("Gặp nàng ánh mắt doanh doanh, khẳng định lấy được chỗ tốt cực lớn, Trương Huyền hài lòng gật đầu."),t("br"),t("br"),n._v("Bất kể nói thế nào, vị này đều là học sinh của hắn, học sinh tiến bộ, làm lão sư cũng lòng có vinh yên."),t("br"),t("br"),n._v("“Đa tạ lão sư chỉ điểm!”"),t("br"),t("br"),n._v("Trịnh trọng quỳ mọp xuống đất, Lạc Thất Thất tràn đầy cảm kích."),t("br"),t("br"),n._v("Hô!"),t("br"),t("br"),n._v("Đang muốn đem hắn đỡ dậy, Trương Huyền đã cảm thấy thư viện nhoáng một cái, một bản kim sắc thư tịch xuất hiện."),t("br"),t("br"),n._v("Thiên Đạo chi sách!"),t("br"),t("br"),n._v("Trước đó muốn lấy được làm thế nào đều không được đến, không nghĩ tới, giờ phút này trực tiếp xuất hiện."),t("br"),t("br"),n._v("“Quá tốt rồi...”"),t("br"),t("br"),n._v("Có cái đồ chơi này, quản cái kia Khôi Lỗi Sư cái gì, một sách liền có thể đập thành thịt nát, cũng là lại không cần lo lắng."),t("br"),t("br"),n._v("“Xem ra, quả nhiên chỉ có thụ nghiệp cảm kích, mới có thể có đến cái này trang sách!” Trương Huyền gật đầu."),t("br"),t("br"),n._v("Trước đó bản thân cuốn lấy khôi lỗi, để nàng chạy trốn, ân tình cực lớn, đều không có hình thành, bây giờ lại lấy được, rất rõ ràng đối phương là cảm kích bản thân thụ nghiệp, để nàng học được tri thức tiến bộ."),t("br"),t("br"),n._v("Xem ra Thiên Đạo chi sách, là làm vì lão sư thân phận, thụ nghiệp mới có thể có đến, cái khác cảm kích vô hiệu."),t("br"),t("br"),n._v("“Đứng lên đi, đã luyện chế ra siêu việt Linh cấp thượng phẩm vũ khí, nhìn xem có thể hay không lấy được Ngô Dương Tử truyền thừa, tìm tới đường ra!”"),t("br"),t("br"),n._v("Có Thiên Đạo chi sách, Trương Huyền tâm cảnh tốt đẹp, đem mắt thấy nữ hài đỡ dậy, cổ tay khẽ đảo, đem Băng Vũ kiếm đặt ở Hồng Viêm đỉnh trước mặt rèn đúc trên đài."),t("br"),t("br"),n._v("Kẹt kẹt!"),t("br"),t("br"),n._v("Dường như đo lường ra chuôi kiếm này phẩm chất, đi đến quy cách, bệ đá một hồi lay động, cơ lò xo vận chuyển thanh âm vang lên lần nữa."),t("br"),t("br"),n._v('"Chúc mừng ngươi,'),t("br"),t("br"),n._v('Rèn đúc ra vượt qua Linh cấp thượng phẩm vũ khí, ngươi bây giờ có tư cách làm học trò ta, hiện tại liền đem bản thân suốt đời tâm huyết truyền thụ cho ngươi!"'),t("br"),t("br"),n._v("Hô!"),t("br"),t("br"),n._v("Nương theo lời của hắn, trước mắt khu vực, một hồi lay động, một cái bệ đá chậm rãi hiện lên."),t("br"),t("br"),n._v("Dưới mặt đất thế mà còn có cơ quan."),t("br"),t("br"),n._v("“Trên bệ đá, tổng cộng có ba món đồ, thứ nhất, ta cả đời luyện khí tổng kết, cùng một mình sáng tạo Phủ Cầm Luyện Khí pháp tường giải. Thứ hai, ta luyện thể công pháp, Ngũ Diệu Kim Thân. Thứ ba, ta tại cái này kinh nghiệm đến lối ra!”"),t("br"),t("br"),n._v("“Bị nhốt nơi này, không cách nào rời khỏi, nhưng cũng không có oán hận, phản mà suốt ngày cùng luyện khí làm bạn, vui mà vong ưu... Đáng tiếc, tuổi thọ có hạn, chuyên tâm muốn luyện chế ra Thánh khí, mà không thể được, đồ sinh bi thương.”"),t("br"),t("br"),n._v("“Hi vọng được ta truyền thừa hậu bối, có thể hoàn thành tâm nguyện của ta, dùng  luyện chế ra Thánh khí, cũng tốt an ủi ta trên trời có linh thiêng, như vậy... Cho dù chết cũng có thể nhắm mắt!”"),t("br"),t("br"),n._v("“Nếu như nguyện ý, phát ra lời thề, ta có thể đem biết đến một tin tức, truyền thụ cho ngươi. Tin tức này liên quan một cái Thượng Cổ để lại bảo tàng, lấy được về sau, tàng thai phá Thánh, chỉ sợ đều không phải là việc khó!”"),t("br"),t("br"),n._v("Thanh âm tiếp tục nói."),t("br"),t("br"),n._v("“Tàng thai phá Thánh?”"),t("br"),t("br"),n._v("Nhìn nhau, Trương Huyền, Lạc Thất Thất riêng phần mình hô hấp dồn dập."),t("br"),t("br"),n._v("Hóa Phàm cửu trọng, Tằm Phong cảnh cũng gọi Tàng Thai cảnh, Thai Tàng cảnh hoặc là Nguyên Anh cảnh."),t("br"),t("br"),n._v("Ý là tích súc tu vi, tại thể nội ngưng tụ nguyên thai (Nguyên Anh), một khi nguyên thai thành công, cùng bản thân dung hợp làm một, liền có thể đột phá ràng buộc, thành linh thành Thánh."),t("br"),t("br"),n._v("Tàng thai phá Thánh, là Linh cấp lớn nhất cửa ải, vô số người vây ở chỗ này, cả đời vô vọng."),t("br"),t("br"),n._v("Thật giống như Diệp Vấn Thiên bệ hạ, Vệ Giang đường chủ, không biết đi đến Hóa Phàm bát trọng thời gian dài bao lâu, cũng chỉ có thể vây ở đỉnh phong, không cách nào đột phá."),t("br"),t("br"),n._v("Nếu là có đồ vật, có thể khiến người ta đột phá loại cảnh giới này, cái này bảo tàng giá trị bao lớn, có thể nói kinh khủng."),t("br"),t("br"),n._v("“Ta đáp ứng!”"),t("br"),t("br"),n._v("Nhìn Trương sư một cái, lấy được hắn sau khi đồng ý, Lạc Thất Thất tiến về phía trước một bước, đi tới bồ đoàn bái sư hành lễ, kế thừa y bát."),t("br"),t("br"),n._v("Trương Huyền chỉ là nàng luyện đan lão sư, mặc dù cũng truyền thụ luyện khí, nhưng lại chưa thu đồ, coi như bái những người khác vi sư cũng không tính là phá hư quy củ."),t("br"),t("br"),n._v("Huống chi, còn liên quan cái gọi là bảo tàng cùng truyền thừa."),t("br"),t("br"),n._v("Phát ra lời thề về sau, cơ lò xo bên trong thanh âm lúc này mới tiếp tục vang lên."),t("br"),t("br"),n._v("“Ngươi đã phát ra lời thề, ta cũng không đang giấu giếm, cái này bảo tàng bí mật tại ta bị bắt trước, lưu tại trước kia nhà cũ lòng đất, tam tinh Địa Long chỗ, ngươi chỉ cần ở nơi đó dựa theo tam tinh bày trận, thả xuống ba cái trận kỳ, đồ vật tự nhiên sẽ nhảy ra!”"),t("br"),t("br"),n._v("Thanh âm chậm rãi kết thúc."),t("br"),t("br"),n._v("“Tam tinh Địa Long? Nhà cũ?”"),t("br"),t("br"),n._v("Trương Huyền hai người đồng thời sững sờ, thiếu chút nữa ngất đi."),t("br"),t("br"),n._v("Ngươi đùa bỡn ta chơi đâu?"),t("br"),t("br"),n._v("Ngươi cũng treo hơn hai nghìn năm, ai biết ngươi nhà cũ ở nơi nào? Tam tinh Địa Long lại là cái gì quỷ?"),t("br"),t("br"),n._v("Biết đây là cơ quan nhắn lại, coi như hỏi thăm, cũng hỏi không ra nguyên cớ, hai người không hiểu ra sao."),t("br"),t("br"),n._v("Nói có bảo tàng, kết quả lại nói không minh bạch, cũng là say."),t("br"),t("br"),n._v("“Ngô Dương Tử tiền bối nếu là được cầm đến, bảo tàng gì gì đó tự nhiên không dám lưu, càng không biết mình sau khi chết, đến cùng người nào lấy được truyền thừa, nhắn lại mơ hồ, xem như bình thường!”"),t("br"),t("br"),n._v("Mặc dù buồn bực, nhưng Trương Huyền cũng có thể hiểu được đối phương ý tứ."),t("br"),n._v(" Đã hắn là được cầm đến, lại không thể chạy trốn, khẳng định là thân bất do kỷ, lúc này, thật đem cái gì bảo tàng bí mật kéo ở trên người, tất nhiên bảo vệ không chứa được!"),t("br"),t("br"),n._v("“Chỉ là... Hắn nhà cũ ở nơi nào?”"),t("br"),t("br"),n._v("Trương Huyền nhịn không được nói."),t("br"),t("br"),n._v("“Ta không biết, bất quá... Ngô Dương Tử lão sư, năm đó là ở tại Hồng Viễn Đế đô, cũng chính là Hồng Viễn học viện vị trí thành thị.” Lạc Thất Thất nhớ lại một cái nói: “Thực sự không được, trở lại học viện lại cẩn thận thẩm tra, hai ngàn năm mà thôi, hẳn là có thể tìm được dấu vết để lại!”"),t("br"),t("br"),n._v("“Ừm!”"),t("br"),t("br"),n._v("Trương Huyền gật đầu."),t("br"),t("br"),n._v("Hai ngàn năm thời gian, đối Thiên Huyền vương quốc loại này tiểu vương quốc, thương hải tang điền, biến thiên cực lớn, nhưng đối với Hồng Viễn đế quốc loại này thế lực bá chủ, biến hóa liền không có rõ ràng như vậy."),t("br"),t("br"),n._v("Đế quốc nắm giữ cường giả Thánh vực, dựa theo bọn hắn một ngàn năm khoảng chừng tuổi thọ, hơn hai nghìn năm, cũng chỉ hai, ba bối mà thôi, thật muốn tìm, có lẽ còn là có thể làm được."),t("br"),t("br"),n._v("“Nhìn xem truyền thừa của hắn đi!”"),t("br"),t("br"),n._v("Biết bảo tàng tin tức, liền không lo tìm, Trương Huyền không còn xoắn xuýt, ra hiệu một cái."),t("br"),t("br"),n._v("Lạc Thất Thất lúc này mới gật đầu đi về phía bệ đá."),t("br"),t("br"),n._v("Phía trên có Ngô Dương Tử lưu cho truyền nhân ba kiện đồ vật, không thấy trước hai loại, mà là trực tiếp đem dạng thứ ba cầm lấy."),t("br"),t("br"),n._v("Cái này liên quan như thế nào rời khỏi, tự nhiên trọng yếu nhất."),t("br"),t("br"),n._v("“Lão sư!”"),t("br"),t("br"),n._v("Đem thư tịch đưa tới."),t("br"),t("br"),n._v("“Ừm!” Trương Huyền tiếp nhận, tiện tay lật một hồi, vẻ mặt tràn đầy cổ quái."),t("br"),t("br"),n._v("“Làm sao vậy, lão sư? Chẳng lẽ bên trong không hề rời đi phương pháp?”"),t("br"),t("br"),n._v("Thấy hắn bộ biểu tình này, Lạc Thất Thất tràn đầy khẩn trương."),t("br"),t("br"),n._v("Thật ra không được liền nguy rồi."),t("br"),t("br"),n._v("“Có thể ra ngoài, chỉ là thấy được một chút chuyện thú vị, vị này Ngô Dương Tử tiền bối, dường như cùng tưởng tượng không giống nhau lắm...”"),t("br"),t("br"),n._v("Trương Huyền mỉm cười."),t("br"),t("br"),n._v("“Không giống?” Lạc Thất Thất tràn đầy nghi hoặc."),t("br"),t("br"),n._v("“Chính ngươi nhìn!”"),t("br"),t("br"),n._v("Tiện tay đem thư tịch đưa cho Lạc Thất Thất."),t("br"),t("br"),n._v("Nghi ngờ tiếp nhận, nữ hài cúi đầu nhìn sang, sau khi xem xong cũng vẻ mặt cổ quái."),t("br"),t("br"),n._v("Sớm đoán được nàng sẽ có bộ dáng này, Trương Huyền nhẹ nhàng cười một tiếng."),t("br"),t("br"),n._v("Vị này Ngô Dương Tử, lại là bị Dị Linh tộc nhân bắt đi."),t("br"),t("br"),n._v("Nói cách khác, cái này Địa Cung, là Dị Linh tộc nhân sào huyệt!"),t("br"),t("br"),n._v("Dị Linh tộc, lúc trước thống trị nhân loại, cỡ nào phong quang, mà bây giờ còn lại chỉ có thể kéo dài hơi tàn sống tại cung điện dưới đất, tự nhiên cảm thấy bên ngoài mới là quê hương của bọn hắn."),t("br"),t("br"),n._v("Bởi vậy, cái thứ nhất trên tấm bia đá “Vọng Hương cư”, mang theo nhớ nhà chi ý, cũng không phải là chỉ Ngô Dương Tử, mà là bọn hắn."),t("br"),t("br"),n._v("Cũng là nguyên nhân này, ba chữ to bên trong, mang theo đặc hữu sát lục chi khí."),t("br"),t("br"),n._v("Cái này Địa Cung, là Dị Linh tộc vì tránh né truy sát xây dựng, ẩn giấu mấy chục đầu tộc nhân, bởi vậy xây dựng cực kỳ kỹ xảo, coi như rất nhiều người ở lại trong đó, cũng sẽ không cảm thấy buồn bực."),t("br"),t("br"),n._v("Còn vì sao phải trảo Ngô Dương Tử, liền rất đơn giản, bọn này tránh né Danh Sư đường Dị Linh tộc nhân, không biết kéo dài mấy vạn năm, bởi vì tộc nhân quá ít, họ hàng gần kết hôn, sinh dục tỉ lệ dần dần hạ thấp, nhân khẩu càng ngày càng ít, mắt thấy là đến, triệt để diệt vong tình trạng."),t("br"),t("br"),n._v("Biết hắn am hiểu luyện khí cùng cơ quan chi thuật, cho nên bắt đến, muốn cho hắn hỗ trợ rèn luyện thân thể, để kéo dài tuổi thọ."),t("br"),t("br"),n._v("Ý tưởng tuy tốt, thế nhưng... Dựa vào rèn luyện thân thể, gia tăng tuổi thọ sao mà khó khăn!"),t("br"),t("br"),n._v("Đừng nói Ngô Dương Tử chỉ là cái lục tinh luyện khí sư, liền xem như bát tinh, cửu tinh, chỉ sợ cũng làm không được!"),t("br"),t("br"),n._v("Nếu không, tin tức này nhất định truyền vang toàn bộ danh sư đại lục, để vô số người đổ xô tới."),t("br"),t("br"),n._v("Có thể còn sống... Ai cũng không muốn chết ah!"),t("br"),t("br"),n._v("Ngô Dương Tử bị bắt tới, liền nhốt vào cái này thạch thất. Thoạt nhìn mặc dù rộng lớn, trên thực tế lại là cái lồng giam, muốn phải thoát đi, chỉ có thể thông qua cái kia chật hẹp cửa đá, chỉ cần có người trông coi, căn bản là ra không được."),t("br"),t("br"),n._v("Có thể nói, hắn bị giam giữ nhốt, đã mất đi tự do, cho dù có luyện khí tương bồi, cũng tất nhiên cảm thấy buồn tẻ, “Vong ưu” để cho người ta khó có thể lý giải được."),t("br"),t("br"),n._v("Nhưng biết chuyện về sau, liền hiểu biết cái tên này vì sao như thế mệnh danh."),t("br"),t("br"),n._v("Bắt hắn tới Dị Linh tộc nhân, mục đích là để hắn hỗ trợ rèn luyện thân thể, đi đến kéo dài tuổi thọ công hiệu, kết quả... Cái tên này cũng điên rồi, dùng hết đủ loại thủ đoạn, là đem thân thể của bọn họ luyện chế càng thêm mạnh mẽ, hơn nữa sẽ không hư hại..."),t("br"),t("br"),n._v("Nhưng... Cũng mất bản thân ý thức, biến thành từng đầu chỉ có thể thi hành mệnh lệnh khôi lỗi."),t("br"),t("br"),n._v("Nói cách khác... Phía ngoài đầu kia khôi lỗi, trên thực tế chính là bắt Ngô Dương Tử tiền bối Dị Linh tộc nhân một trong, kết quả bị hắn cứ thế mà luyện chế thành cái đồ chơi này!"),t("br"),t("br"),n._v("Còn về sau, đối phương khẳng định cũng phát hiện vấn đề, lại thêm vị này Ngô Dương Tử cũng tuổi thọ đến cuối cùng, đến cuối cùng, đều không có rời khỏi."),t("br"),t("br"),n._v("Trước đó bên ngoài lưu truyền ra vũ khí, thì là hắn thiết kế cơ quan, lặng lẽ thả ra ngoài, vốn nghĩ, có người có thể phát hiện đem hắn cứu ra, không nghĩ tới, vừa chờ... Chính là hơn hai nghìn năm!"),t("br"),t("br"),n._v("Hai ngàn năm, thương hải tang điền, nơi này Dị Linh tộc, trừ bỏ bị hắn luyện chế thành khôi lỗi những tên kia, còn lại hầu như đều chết hết, nguyên nhân chính là như thế, mọi người đi tới, mới rỗng tuếch."),t("br"),t("br"),n._v("Biết tuổi thọ sắp hết, Ngô Dương Tử đem truyền thừa loại hình lưu lại, mới có bọn hắn nhìn thấy hết thảy, còn phía ngoài đại gia hỏa, một mực thủ hộ nơi này, phòng ngừa hắn rời khỏi, coi như trước khi chết, cũng không có cách nào diệt trừ, liền chỉ có thể mặc cho hắn, tiếp tục “Còn sống”."),t("br"),t("br"),n._v("“Cái này...”"),t("br"),t("br"),n._v("Lạc Thất Thất đến bây giờ đều không thể tin được."),t("br"),t("br"),n._v("Ngô Dương Tử tại toàn bộ Hồng Viễn đế quốc, đều có lưu lại nổi danh, làm người chính trực, cương trực công chính, nằm mơ đều không nghĩ tới, nha như thế âm hiểm."),t("br"),t("br"),n._v("Đem bắt hắn Dị Linh tộc nhân toàn bộ luyện chế thành khôi lỗi... Thật mẹ nó điên rồi!"),t("br"),t("br"),n._v("Đây cũng không phải là đồng dạng hung ác, mà là đoạn người cơ sở, diệt tộc!"),t("br"),t("br"),n._v("Convert by: ThấtDạ"),t("br"),t("br"),t("br"),t("br")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("a",{attrs:{href:"./chap-678"}},[n._v("Chương trước")]),n._v("  |  "),t("a",{attrs:{href:"./chap-680"}},[n._v("Chương sau")])])])}),[],!1,null,null,null);h.default=c.exports}}]);