(window.webpackJsonp=window.webpackJsonp||[]).push([[1942],{2297:function(n,h,t){"use strict";t.r(h);var c=t(44),i=Object(c.a)({},(function(){var n=this,h=n.$createElement,t=n._self._c||h;return t("ContentSlotsDistributor",{attrs:{"slot-key":n.$parent.slotKey}},[t("h1",{attrs:{id:"chuong-59-phat-ro-cac-hoc-vien"}},[t("a",{staticClass:"header-anchor",attrs:{href:"#chuong-59-phat-ro-cac-hoc-vien"}},[n._v("#")]),n._v(" Chương 59: Phát rồ các học viên")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("br"),t("br"),n._v(" “Cớ ta đã giúp gia gia tìm kĩ, Tào Hùng lão sư có một cái học sinh gọi Lưu Dương, gặp phải Trương Huyền cưỡng bức, mới làm học sinh của hắn, Tào Hùng lão sư đã xin ngày mai học tâm tra hỏi, đến thời điểm, chỉ cần gia gia đứng ra, khai trừ của hắn tư cách, đơn giản đến cực điểm!” "),t("br"),t("br"),n._v(" Thượng Bân cắn răng nói."),t("br"),t("br"),n._v(" Tào Hùng cùng hắn nói là dụ dỗ, mà hắn nhưng đổi thành cưỡng bức, hai chữ chi kém, nhưng lại có khác nhau một trời một vực."),t("br"),t("br"),n._v(" Dụ dỗ, chỉ có thể nói vị lão sư này tính cách có vấn đề, nhưng biến thành cưỡng bức, cái kia chính là đạo đức trên đồ vật."),t("br"),t("br"),n._v(" “Cưỡng bức? Ngươi chắc chắn chứ?” Thượng Thần trưởng lão sững sờ."),t("br"),t("br"),n._v(" “Ta xác định!” Thượng Bân gật đầu."),t("br"),t("br"),n._v(" “Nếu như là như vậy... Như vậy đi, ngày mai ta đi đem giáo sư công hội Mạc trưởng lão mời lại đây, để hắn làm chứng, nếu như học tâm tra hỏi thật có thể góp ý cái này Lưu Dương cũng không phải là chân tâm thật ý làm học sinh của hắn, khai trừ của hắn giáo sư tư cách, đúng là có thể!” Do dự một chút, Thượng Thần trưởng lão nói."),t("br"),t("br"),n._v(" “Đa tạ gia gia!” Thượng Bân ánh mắt hưng phấn lóe lên."),t("br"),t("br"),n._v(" Có giáo sư, tự nhiên cũng là có sát hạch, bảo vệ giáo sư thân phận tổ chức, chính là giáo sư công hội."),t("br"),t("br"),n._v(" Nơi này có thể xét duyệt giáo sư cử động, cũng có thể huỷ bỏ tư cách của bọn hắn, dùng Trương Huyền kiếp trước ánh mắt đến xem, cùng Giáo Dục Cục công năng gần như."),t("br"),t("br"),n._v(" Giáo sư, vi nhân sư biểu, truyền thụ học sinh, muốn cho đối phương cam tâm tình nguyện mới được, nếu như là cưỡng bức, để học sinh bất mãn, có thể khai trừ tư cách."),t("br"),t("br"),n._v(" Làm sao phán định học sinh có hay không chân tâm thật ý làm học sinh của hắn, học tâm tra hỏi chính là nhất công chính phương pháp."),t("br"),t("br"),n._v(" “Tiểu tử thúi, hiện tại để ngươi đắc ý, ngày mai chờ ngươi đã mất đi giáo sư tư cách, ta nhìn còn thế nào hung hăng!” "),t("br"),t("br"),n._v(" Nghĩ đến ngày mai sẽ có thể báo một mũi tên mối thù, Thượng Bân nắm đấm không tự chủ được xiết chặt, một loại thoải mái từ đầu tới đuôi."),t("br"),t("br"),n._v("..."),t("br"),t("br"),n._v(" Trương Huyền không biết đã có người định ra kế sách, đi qua một đêm nghỉ ngơi, tinh lực dồi dào, đối tấm gương nhìn lại, đi qua Thiên Đạo Kim Thân rèn luyện qua thân thể, da dẻ bóng loáng trắng nõn, dường như trẻ con, không có một chút nào vết tích loại hình lưu lại, cả người xem ra cũng trẻ lại không ít, không biết thân phận, làm không cẩn thận còn cho là hắn là học sinh."),t("br"),t("br"),n._v(" “Không sai!” Cảm khái một tiếng, nhanh chân hướng về lớp đi đến, vừa tới đến, liền thấy năm cái học sinh đã đem bên trong gian phòng bên ngoài toàn bộ quét dọn sạch sành sanh, thậm chí ngay cả uống nước sôi đều đốt tốt."),t("br"),t("br"),n._v(" Vương Dĩnh, Lưu Dương, Trịnh Dương, Triệu Nhã, Viên Đào đám người, càng là mỗi người tinh thần run tẩu, lần thứ hai nhìn về phía Trương Huyền tràn đầy sùng bái cùng hưng phấn."),t("br"),t("br"),n._v(" Này mấy cái học sinh, vừa mới bắt đầu đều cảm thấy bị lừa gạt, nhận một sư tư sát hạch thứ nhất đếm ngược gia hỏa vi sư, nhưng đi qua ngày hôm qua giảng bài, biết tất cả người trước mắt này tiếng tăm tuy rằng không được, nhưng là cái thực sự nhân vật lợi hại!"),t("br"),t("br"),n._v(" Đặc biệt là Vương Dĩnh, nàng đem lão sư nói một ít lý luận nói cho phụ thân và gia tộc trưởng lão nghe, mặc dù là bọn họ, đều khen không dứt miệng, có chút lý giải không được, thậm chí nhất định phải mang theo Vương Đào đến bái sư! Bởi vậy có thể thấy được trình độ!"),t("br"),t("br"),n._v(" Bái người như vậy vi sư, có thể nào không hưng phấn?"),t("br"),t("br"),n._v(" “Hừm, hôm nay tới rất chỉnh tề, Triệu Nhã, đem bụi dược liệu này, ép thành bụi phấn cùng nước dùng, mặt khác, đây là ta vừa tự viết công pháp tu luyện, ngươi chiếu tu luyện, ba ngày tả hữu, liền có thể khỏi hẳn!” "),t("br"),t("br"),n._v(" Nhìn thấy mọi người ánh mắt, Trương Huyền nơi nào không biết bọn họ nghĩ cái gì, gật gật đầu, đem tối hôm qua mua Hàn Dương Mẫu Thảo cùng một quyển sách, đưa tới."),t("br"),t("br"),n._v(" Triệu Nhã bệnh tình là bởi vì tu luyện Thuần Âm thuộc tính công pháp đưa đến, hôm qua Thiên Tướng giáo sư Tàng Thư Các sách toàn bộ lật xem xong, thông qua Thiên Đạo Thư Viện đã tìm ra phương pháp giải quyết, bộ công pháp này tuy rằng cùng hắn Thiên Đạo Thần Công so với, kém xa lắm, nhưng là hắn sửa sang lại mấy chục bản bí tịch có được, quý giá dị thường."),t("br"),t("br"),n._v(" Trong đó khuyết điểm vẫn có, bất quá, đã không ảnh hưởng đại cục."),t("br"),t("br"),n._v(" “Rõ!” Triệu Nhã đem dược liệu cất vào trong lòng tiện tay mở sách tịch, chỉ thấy trong đó nét mực mới mẻ, rất rõ ràng là vừa vặn viết ra, nhìn một hồi, con ngươi co rụt lại, tràn đầy không thể tin được nhìn sang: “Trương lão sư, tuy rằng ta mới đến học viện, nhưng trong học viện có tiếng bí tịch ta đều nghe nói qua, bản này... So với ta tu luyện Bạch Ngọc Tố Nữ Công còn lợi hại hơn gấp mấy lần, làm sao... Ta chưa từng nghe qua? Hơn nữa... Làm sao trong sách vở cũng không có tên!” "),t("br"),t("br"),n._v(" Triệu Nhã thân là Thành chủ con gái, trong nhà tàng thư vô số, thấy qua bí tịch không ít, nhãn lực làm sao có khả năng kém, trong tay bản này, so với trước đây tu luyện Bạch Ngọc Tố Nữ Công mạnh không biết bao nhiêu lần, có thể... Một mực tự mình làm sao chưa từng nghe nói tên?"),t("br"),t("br"),n._v(" Chỉ cần là công pháp, bất kể thế nào sao chép, ngươi tổng nên đem người sáng lập tên viết đến đi, có thể quyển sách này, trọc lốc, cái gì đều không có, để nhân không nhịn được nghi hoặc."),t("br"),n._v(" “Há, quyển sách này là ta ngày hôm qua căn cứ ngươi thể chất, tổng kết một hồi, vừa viết ra, vốn là vô danh tự, nếu như ngươi cảm thấy không tốt xưng hô, có thể tùy tiện lấy một cái!” Trương Huyền nói."),t("br"),t("br"),n._v(" Thiên Đạo Thư Viện tổng kết bí tịch chính xác chỗ, hình thành thư tịch, vốn cũng không có tên, hắn cũng lười lấy, tiện tay viết ra liền lấy đến rồi, không nghĩ tới Triệu Nhã sẽ hỏi, rất tùy ý giải thích."),t("br"),t("br"),n._v(" “Cái gì? Lão sư... Tự mình viết? Đây là... Lão sư sáng tạo công pháp?” "),t("br"),t("br"),n._v(" Không riêng Triệu Nhã sợ hết hồn, mọi người khác cũng tất cả đều con ngươi co rụt lại, thiếu chút nữa ngất đi."),t("br"),t("br"),n._v(" Có thể sáng tạo công pháp, người nào không phải đại sư cấp bậc nhân vật, lưu danh sử sách cường giả tuyệt thế! Tự mình vị này không có danh tiếng gì lão sư lại có thể sáng chế một bộ công pháp... Hơn nữa còn cao cấp như vậy biệt, thật hay giả?"),t("br"),t("br"),n._v(" “Có thể nói như vậy!” "),t("br"),t("br"),n._v(" Thiên Đạo Thư Viện không cách nào giải thích, những sách vở này lại là bỗng dưng mà ra, nói là tự mình sáng chế, cũng là có thể, Trương Huyền không hề cảm thấy có cái gì, tùy ý gật đầu."),t("br"),t("br"),n._v(" “Quá lợi hại...” "),t("br"),t("br"),n._v(" “Bạch Ngọc Tố Nữ Công, là lúc trước gia tộc chúng ta một vị võ giả tám tầng Tông Sư cảnh cường giả sáng lập ra, lão sư dĩ nhiên có thể tiện tay sáng chế so với cấp bậc này còn cao, đến cùng đạt đến thực lực ra sao?” "),t("br"),t("br"),n._v("..."),t("br"),t("br"),n._v(" Thấy hắn thừa nhận, năm cái học sinh đều sắp điên rồi."),t("br"),t("br"),n._v(" Sáng chế công pháp, Hồng Thiên học viện xây trường trăm năm qua, có thể làm được điểm này, ít ỏi, mỗi một cái đều là thành danh đại nhân vật, sư phụ của mình lại có thể làm được, quả thực sùng bái sắp ngất đi."),t("br"),t("br"),n._v(" Hơn nữa... Quan trọng nhất chính là, Trương lão sư mới vừa nói cái gì?"),t("br"),t("br"),n._v(" Hắn nói “Ngươi cảm thấy không tốt xưng hô, tùy tiện lấy một cái”..."),t("br"),t("br"),n._v(" Mịa nó a!"),t("br"),t("br"),n._v(" Có thể sáng chế công pháp đại sư, người nào không dốc hết tâm huyết, đem coi như trân bảo, không phải đệ tử thân truyền, cũng không thể truyền thụ!"),t("br"),t("br"),n._v(" Vị này Trương lão sư ngược lại tốt, sáng chế một bộ công pháp, tiện tay biếu tặng không nói, liền tên đều chẳng muốn lên, để học sinh tùy ý lấy..."),t("br"),t("br"),n._v(" Phải biết ai lấy tên, vô cùng có khả năng hậu thế chỉ nhớ kỹ người này, người sáng lập ngược lại không có tên tuổi."),t("br"),t("br"),n._v(" Này loại danh truyền thiên cổ, truyền lưu hậu thế sự tình, đều không cần thiết chút nào..."),t("br"),t("br"),n._v(" Cũng quá không màng danh lợi, quá đạo đức tốt đi!"),t("br"),t("br"),n._v(" Chẳng trách Trương lão sư lợi hại như vậy, ở trường học không những yên lặng Vô Danh, còn lưng đeo một cái kém cỏi nhất lão sư danh tiếng xấu, nguyên lai hắn là cao thượng như vậy, như vậy coi danh lợi như cặn bã..."),t("br"),t("br"),n._v(" “Trương lão sư...” "),t("br"),t("br"),n._v(" Nghĩ tới những thứ này, năm vị học viên viền mắt đồng thời một đỏ, từng cái từng cái cảm động sắp khóc."),t("br"),t("br"),n._v(" “Các ngươi thế nào?” "),t("br"),t("br"),n._v(" Nhìn thấy vừa nãy còn rất tốt mấy cái học sinh, giờ khắc này từng cái từng cái con mắt đỏ lên, Trương Huyền đầu óc mơ hồ."),t("br"),t("br"),n._v(" Này mấy cái học sinh, lẽ nào... Đồng thời điên rồi?"),t("br"),t("br"),n._v("Convert by: Hoang Chau"),t("br"),t("br"),t("br"),t("br")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("a",{attrs:{href:"./chap-67"}},[n._v("Chương trước")]),n._v("  |  "),t("a",{attrs:{href:"./chap-69"}},[n._v("Chương sau")])])])}),[],!1,null,null,null);h.default=i.exports}}]);