(window.webpackJsonp=window.webpackJsonp||[]).push([[1945],{2300:function(n,h,t){"use strict";t.r(h);var i=t(44),c=Object(i.a)({},(function(){var n=this,h=n.$createElement,t=n._self._c||h;return t("ContentSlotsDistributor",{attrs:{"slot-key":n.$parent.slotKey}},[t("h1",{attrs:{id:"chuong-673-du-nhieu-lanh-it"}},[t("a",{staticClass:"header-anchor",attrs:{href:"#chuong-673-du-nhieu-lanh-it"}},[n._v("#")]),n._v(" Chương 673: Dữ nhiều lành ít")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("br"),t("br"),n._v("Chương 673: Dữ nhiều lành ít"),t("br"),t("br"),n._v("Vừa rồi, chủ động đem quyển bí tịch này, đưa cho Trương sư, hắn nói là truyền thừa, thật không tiện quan sát, - đang vì loại này đức hạnh thuyết phục, kết quả là thi triển đi ra..."),t("br"),t("br"),n._v("Ngươi đến cùng là nhìn vẫn là không thấy?"),t("br"),t("br"),n._v("Còn có... Không nói thiên tài, nhanh nhất cũng phải hai năm mới có thể luyện thành ư?"),t("br"),t("br"),n._v("Cái này mới luyện bao lâu?"),t("br"),t("br"),n._v("Mới vừa đem nội dung nhớ kỹ, liền luyện qua, hơn nữa cường thế như vậy..."),t("br"),t("br"),n._v("Lão sư, ngươi đến cùng là Trương sư, vẫn là Ngô Dương Tử sống lại?"),t("br"),t("br"),n._v("Lạc Thất Thất vẻ mặt mộng bức, cả người đều trở nên không tốt lắm."),t("br"),t("br"),n._v("Cảm giác bản thân tam quan đều bị phá vỡ."),t("br"),t("br"),n._v("“Đi a?”"),t("br"),t("br"),n._v("Một bước nhảy ra Đoạn Long Tỏa, thấy nữ hài đang ngẩn người, Trương Huyền hô một tiếng."),t("br"),t("br"),n._v("“Lão sư... Ngươi vừa rồi dùng chính là Ngũ Diệu Kim Thân?”"),t("br"),t("br"),n._v("Đi ra thềm đá, thực sự nhịn không được, Lạc Thất Thất hỏi."),t("br"),t("br"),n._v("“Cũng như thế, cũng coi như không phải đâu!”"),t("br"),t("br"),n._v("Trương Huyền lắc đầu, tràn đầy khó chịu: “Chuyện gấp tòng quyền, vốn là ta không muốn học, nhưng không học mở không ra cái này Đoạn Long Tỏa, cho nên liền học được một cái. Bất quá, bộ công pháp này tu luyện khá là phiền toái, liền tùy ý sửa đổi một cái, dù vậy, cũng có rất lớn thiếu hụt, chỉ tu luyện đệ nhất trọng!”"),t("br"),t("br"),n._v("Hắn thật sự rất buồn bực, vốn là nhìn thấy chỉ có bốn cái thiếu hụt, hết sức cao hứng, vừa tu luyện, mới biết được... Con mẹ nó bị lừa rồi."),t("br"),t("br"),n._v("Cái này bốn cái thiếu hụt, phân biệt tại đệ nhị trọng, đệ tam trọng, đệ tứ trọng cùng đệ ngũ trọng, muốn tu luyện, cần chuẩn bị đủ loại trân quý vật phẩm."),t("br"),t("br"),n._v("Tỷ như, muốn tu luyện đệ nhị trọng, cần chuẩn bị địa tâm Hồng nham; Tu luyện đệ tam trọng, cần chuẩn bị Nhất Nguyên Trọng Thủy..."),t("br"),t("br"),n._v("Trước đó Thiên Đạo Kim Thân, chỉ cần Thiên Đạo chân khí là được, lúc nào chuẩn bị qua những đồ chơi này?"),t("br"),t("br"),n._v("Dung hợp mấy trăm bản luyện thể công pháp, thế mà còn muốn dùng những vật này, bộ này pháp quyết, cũng không tránh khỏi quá hố!"),t("br"),t("br"),n._v("May mắn đệ nhất trọng, không cần những dược vật khác, Thiên Đạo chân khí liền có thể luyện thành, nếu không, khẳng định sẽ làm trận buồn bực chết."),t("br"),t("br"),n._v("Hắn tràn đầy khó chịu, một bên Lạc Thất Thất nhanh muốn điên rồi."),t("br"),t("br"),n._v("Tùy ý sửa chữa một cái?"),t("br"),t("br"),n._v("Còn có... Rất lớn thiếu hụt?"),t("br"),t("br"),n._v("Đây chính là Linh cấp đỉnh phong cấp bậc công pháp, thậm chí... Còn có thể cao hơn! Ta bên này mới xem xong, ngươi không chỉ lĩnh ngộ, còn sửa chữa xong, không chỉ sửa chữa, còn luyện qua..."),t("br"),t("br"),n._v("Thậm chí... Còn cảm giác đến người ta có thiếu hụt!"),t("br"),t("br"),n._v("Ngươi là yêu nghiệt vẫn là đến đùa ta chơi?"),t("br"),t("br"),n._v("Gặp qua không ít thiên tài, không ít nghịch thiên cường giả, có thể... Chưa bao giờ thấy qua, như thế nghịch thiên!"),t("br"),t("br"),n._v("Trước mắt choáng váng, Lạc Thất Thất khóe miệng quất loạn."),t("br"),t("br"),n._v("Chỉ cảm thấy vị này Trương lão sư, cái gì ngoạn ý không thể nào sự tình, cũng có thể làm đến thuận theo tự nhiên, đương nhiên."),t("br"),t("br"),n._v("“Ừm? Bọn hắn là không có đi ra, vẫn là đã rời đi?”"),t("br"),t("br"),n._v("Đang khiếp sợ không biết nói cái gì cho phải, liền nghe đến thanh niên thanh âm tiếp tục vang lên, gấp vội ngẩng đầu, chỉ thấy Trương sư đã qua cầu nhỏ, đi tới một cái khác trước cửa đá, mày nhăn lại."),t("br"),t("br"),n._v("Ngọc Phi Nhi, Hình Viễn đám người, vào cái này cửa đá, mà bọn hắn vào một cái khác, chơi đùa chí ít bảy, tám canh giờ mới ra ngoài, vốn cho rằng đối phương khẳng định chờ đến đến, không nghĩ tới, lớn như vậy trong thạch thất, chỉ có mấy viên dạ minh châu lấp lánh tỏa ánh sáng, không nhìn thấy nửa cái bóng người."),t("br"),t("br"),n._v("Bọn hắn là rời đi vẫn là không có đi ra?"),t("br"),t("br"),n._v("Chấn động trong lòng, lại không để ý tới suy nghĩ lung tung, vội vàng đi tới gần."),t("br"),t("br"),n._v("Nhìn chung quanh một hồi, Lạc Thất Thất sắc mặt tái xanh: “Hẳn là không có đi ra, nếu như về tới đây, khẳng định sẽ tới tìm chúng ta, Dạ Minh Châu trưng bày vị trí không nhúc nhích, cũng không có bất kỳ cái gì dấu vết, nói rõ... Bọn hắn vô cùng có khả năng còn tại trong cửa đá, không có đi ra khỏi!”"),t("br"),t("br"),n._v("Lấy nàng đối Ngọc Phi Nhi hiểu rõ, một khi đi ra, khẳng định sẽ đi tìm bọn hắn, hiện tại Địa Cung cái gì dấu vết đều không có lưu lại, cũng đã nói lên... Hình Viễn, Ngọc Phi Nhi, Ngô Chấn, Diệp Tiền bốn người, đã lâu như vậy, vô cùng có khả năng cùng nhóm người mình đồng dạng, còn vây ở trong cửa đá, không cách nào đi ra."),t("br"),t("br"),n._v("“Ừm!”"),t("br"),t("br"),n._v("Nàng có thể đoán được, nắm giữ Minh Lý Chi Nhãn Trương Huyền, tự nhiên cũng đã xác nhận, chỉ là nhìn về phía cửa đá ánh mắt, tràn đầy nghi hoặc: “Cánh cửa này đồng thời không có Đoạn Long Tỏa, vì sao không đi ra?”"),t("br"),t("br"),n._v("“Không có?”"),t("br"),t("br"),n._v("Lạc Thất Thất lúc này mới chú ý tới sau cửa đá mặt, là một loạt hướng phía dưới thềm đá, đồng thời không có Đoạn Long Tỏa phong tỏa cửa."),t("br"),t("br"),n._v("Nói cách khác... Cửa không đóng!"),t("br"),t("br"),n._v("Đã như vậy, cái kia vì cái gì bọn hắn không có đi ra?"),t("br"),t("br"),n._v("“Ngô Dương Tử lão sư nói, chúng ta vừa rồi đi cái kia Địa Cung, là chuyên môn vì giam giữ hắn nhà tù, mà bên này là Dị Linh tộc nhân chỗ ở... Còn nói, những này Dị Linh tộc nhân đều bị hắn biến thành khôi lỗi, cái kia không sẽ...”"),t("br"),t("br"),n._v("Nghĩ đến cái gì, Lạc Thất Thất gương mặt xinh đẹp tái đi."),t("br"),t("br"),n._v("Ngô Dương Tử giết chết nhiều như vậy Dị Linh tộc nhân, vừa rồi cái kia Địa Cung, chỉ thấy được một cái, nếu như toàn bộ tại nơi này..."),t("br"),t("br"),n._v("“Nhanh đi qua nhìn một chút!”"),t("br"),t("br"),n._v("Trương Huyền cũng biến sắc."),t("br"),t("br"),n._v("Mặc dù đối Ngọc Phi Nhi đám người, không có hảo cảm quá lớn, nhưng cũng không có bất kỳ cái gì ác cảm, không thể trơ mắt nhìn thấy đối phương, bị những này Dị Linh tộc nhân giết chết."),t("br"),t("br"),n._v("“Ừm!”"),t("br"),t("br"),n._v("Biết tỷ muội, học chung khả năng gặp được nguy hiểm, Lạc Thất Thất không dám dừng lại nghỉ, vội vàng theo sau lưng, hướng phía dưới đi đến."),t("br"),t("br"),n._v("Thềm đá cùng vừa rồi đồng dạng, có chút băng lãnh cùng âm hàn."),t("br"),t("br"),n._v("Trước đó trên cầu đá, nói một người chỉ có thể tiến vào một cánh cửa, hiện tại xem ra, chỉ cần vào bên này, nhiều như vậy Dị Linh tộc khôi lỗi vây quét, có đi không về, cái thứ hai khẳng định cũng vào không được."),t("br"),t("br"),n._v("Còn đi vào vừa rồi bọn hắn đi cái kia, không chiếm được truyền thừa, đồng dạng tử vong, lấy được truyền thừa, biết nơi này có thể là Dị Linh tộc nhân hang ổ, khẳng định cũng sẽ không khi tiến vào."),t("br"),t("br"),n._v("Cả hai tuyển một, kỳ thật chính là tại lựa chọn Sinh Tử!"),t("br"),t("br"),n._v("“Chỉ mong không có việc gì!”"),t("br"),t("br"),n._v("Đem Băng Vũ kiếm giữ tại lòng bàn tay, Trương Huyền nắm đấm xiết chặt."),t("br"),n._v(" Sớm biết nơi này nguy hiểm như vậy, bất luận thế nào cũng phải khuyên ngăn trở đối phương, vạn nhất bốn người chết thật ở bên trong, hắn cũng sẽ cảm thấy có chút áy náy."),t("br"),t("br"),n._v("Đi một hồi, ngay lúc sắp tiến vào Địa Cung, Trương Huyền ngừng lại: “Ngươi chớ đi vào, lưu ở phía trên chờ lấy, ta một người đi vào!”"),t("br"),t("br"),n._v("“Lão sư...”"),t("br"),t("br"),n._v("Lạc Thất Thất sững sờ."),t("br"),t("br"),n._v("“Thực lực ngươi quá yếu, lại không am hiểu chạy trốn, nếu như bọn hắn thật xảy ra chuyện, coi như đi vào cũng vô dụng, ngược lại sẽ góp đi vào càng nhiều người! Nếu là không có việc gì... Ta sẽ đem bọn hắn mang đi ra!”"),t("br"),t("br"),n._v("Trương Huyền nói."),t("br"),t("br"),n._v("Có người đi theo, hắn không ít thủ đoạn cũng không thể dùng, không nếu như để cho vị này Lạc Thất Thất đi lên trước."),t("br"),t("br"),n._v("“Cái này... Tốt!”"),t("br"),t("br"),n._v("Chần chờ một chút, Lạc Thất Thất một cắn răng, nhẹ gật đầu."),t("br"),t("br"),n._v("Thực lực của nàng, quả thực không được, nhất là đối mặt những khôi lỗi kia, thật muốn đi vào, chỉ sợ còn có thể cùng trước đó đồng dạng, rơi vào khổ chiến."),t("br"),t("br"),n._v("Lần này không phải một cái, có thể là rất nhiều, một khi bị cuốn lấy, cho dù có Băng Vũ kiếm chuôi này Bán Thánh chi khí, chỉ sợ cũng rất khó chạy trốn."),t("br"),t("br"),n._v("Dù sao, Trương sư sở dĩ có thể đem cái kia đầu gia hỏa chém giết, cũng là lợi dụng kỹ xảo, dùng Hồng Viêm đỉnh đem cái kia hắn vây khốn, nếu không, chính diện chống chọi, đối phương căn bản không có khả năng cho ngươi chém giết cơ hội."),t("br"),t("br"),n._v("“Lão sư, ngươi phải cẩn thận... Mặc kệ bọn hắn như thế nào, ngươi đều phải trước cam đoan bản thân an toàn!” Lạc Thất Thất tràn đầy lo lắng nhìn qua."),t("br"),t("br"),n._v("“Yên tâm đi, ta sẽ bay, đánh không lại, chạy trốn còn có thể làm được!” Trương Huyền nói."),t("br"),t("br"),n._v("“Ừm, ta chờ ngươi ở ngoài bọn họ!”"),t("br"),t("br"),n._v("Nghe được hắn nói như vậy, Lạc Thất Thất nhớ tới đối phương đủ loại chỗ thần kỳ, trấn an không ít, nhẹ gật đầu, xoay người đi ra ngoài, một lát sau biến mất trong bóng đêm."),t("br"),t("br"),n._v("Gặp nàng rời khỏi, Trương Huyền cũng không tiến vào Địa Cung, mà là khoanh chân ngồi xuống, hít sâu một hơi."),t("br"),t("br"),n._v("Hô!"),t("br"),t("br"),n._v("Tinh thần khẽ động, Vu hồn ly thể."),t("br"),t("br"),n._v("Nếu như bên trong thật có một, hai mươi đầu trước đó gặp phải loại kia Dị Linh tộc khôi lỗi, coi như hắn biết bay cũng khó có thể đào thoát."),t("br"),t("br"),n._v("Dù sao nơi này là Địa Cung, không phải bầu trời, nhận hạn chế độ cao, một khi đồng thời vọt tới, căn bản trốn không tránh nổi."),t("br"),t("br"),n._v("Cho nên, Vu hồn đi quan sát địa hình, tìm kiếm Ngọc Phi Nhi đám người, là biện pháp tốt nhất."),t("br"),t("br"),n._v("Vu hồn vô hình vật chất, coi như Dị Linh tộc nhân, cũng khó có thể phát hiện."),t("br"),t("br"),n._v("Hô!"),t("br"),t("br"),n._v("Vu hồn rời khỏi thân thể, lập tức bay lên, trực tiếp hướng Địa Cung đi đến."),t("br"),t("br"),n._v("Trong cung điện dưới lòng đất, cũng có trước Hình Viễn đám người thả xuống Dạ Minh Châu, ánh sáng mặc dù ảm đạm, lại có thể thấy rõ chung quanh tình huống."),t("br"),t("br"),n._v("Mang mắt nhìn đi, nơi này so Ngô Dương Tử vị trí địa lao lớn rất nhiều, không nhìn thấy cuối cùng."),t("br"),t("br"),n._v("Không hổ là vô số Dị Linh tộc nhân chỗ ở, đủ loại kiến trúc tầng tầng lớp lớp, nếu như không phải âm u, chỉ sợ đều sẽ cho rằng đi vào một tòa thành thị."),t("br"),t("br"),n._v("“Có thể để cho Dị Linh tộc nhân sinh hoạt vài vạn năm, khó trách...”"),t("br"),t("br"),n._v("Trương Huyền âm thầm gật đầu."),t("br"),t("br"),n._v("Nơi này có thể là sinh hoạt nhất tộc Dị Linh tộc nhân vài vạn năm, nếu như xây lại bố trí cùng nhà tù đồng dạng, chỉ là một cái thẳng tính, coi như hắn cũng sẽ không tin tưởng."),t("br"),t("br"),n._v("Dọc theo ánh sáng bay về phía trước."),t("br"),t("br"),n._v("Đã có Dạ Minh Châu, khẳng định là Ngọc Phi Nhi đám người lưu lại, chỉ cần dọc theo ánh sáng đi, khẳng định liền có thể tìm tới đối phương."),t("br"),t("br"),n._v("“Có chiến đấu dấu vết...”"),t("br"),t("br"),n._v("Đi về phía trước một hồi, sầm mặt lại."),t("br"),t("br"),n._v("Dạ Minh Châu chiếu xuống, trước mắt không ít phòng ốc sụp đổ, trên mặt đất còn có vết máu, rất hiển nhiên có người ở đây chiến đấu qua."),t("br"),t("br"),n._v("“Minh Lý Chi Nhãn!”"),t("br"),t("br"),n._v("Hai mắt hoa văn nhúc nhích, nhìn sang."),t("br"),t("br"),n._v("“Bảy canh giờ trước... Xem ra là bọn hắn lưu lại!”"),t("br"),t("br"),n._v("Minh Lý Chi Nhãn là trời nhận danh sư ban thưởng, coi như hiện tại là Vu hồn, đồng dạng có thể sử dụng, tuỳ tiện xem thấu những này dấu vết, là lúc nào lưu lại."),t("br"),t("br"),n._v("Đi qua quan sát, có thể xác nhận, đây là hơn bảy canh giờ trước lưu lại."),t("br"),t("br"),n._v("Hẳn là Ngọc Phi Nhi đám người vừa tiến vào nơi này không lâu, gặp được lớn cái gì, tiến hành chiến đấu."),t("br"),t("br"),n._v("“Làm sao không có động tĩnh? Chẳng lẽ... Người đã bị giết?”"),t("br"),t("br"),n._v("Trong lòng kỳ quái."),t("br"),t("br"),n._v("Đã xảy ra chiến đấu, khẳng định là gặp phải nguy hiểm, nếu như còn sống, hẳn là tại chiến đấu, làm sao cái này Địa Cung yên tĩnh một điểm thanh âm đều không có?"),t("br"),t("br"),n._v("Hai mắt tỏa ánh sáng, tiếp tục hướng phía trước nhìn lại, khu vực lập tức giống như là nổi lên một loạt xốc xếch dấu chân."),t("br"),t("br"),n._v("Những này dấu chân, kích thước không lớn, vừa nhìn liền biết là nhân loại lưu lại."),t("br"),t("br"),n._v("“Là vết chân của bọn họ!”"),t("br"),t("br"),n._v("Đi qua lần thứ ba Thiên Nhân danh sư, Minh Lý Chi Nhãn năng lực càng thêm cường đại, những này không có che lấp qua dấu chân, rất nhẹ nhàng liền nhìn ra, Trương Huyền theo sát trên đó, mới vừa bay không xa, vội vàng ngừng lại."),t("br"),t("br"),n._v("Kẽo kẹt! Kẽo kẹt!"),t("br"),t("br"),n._v("Một cái tiếng bước chân từ nơi không xa đi tới, mượn nhờ trên đường phố Dạ Minh Châu ánh sáng, lập tức nhìn thấy một đầu to lớn khôi lỗi, đi nhanh tới."),t("br"),t("br"),n._v("Đầu này đại gia hỏa cùng trước đó chém giết đầu kia không kém nhiều, toàn thân xám xanh, như là sắt thép rèn đúc."),t("br"),t("br"),n._v("Trương Huyền Vu hồn, tu luyện chính là Thiên Đạo công pháp, trong cơ thể không có âm lãnh chi khí, đối phương đồng thời chưa phát hiện, mới vừa từ đỉnh đầu bay qua, ngay sau đó lại nhìn thấy vài đầu, đang song song đi đến."),t("br"),t("br"),n._v("“Quả nhiên thật nhiều...”"),t("br"),t("br"),n._v("Còn không tìm được người, liền thấy chí ít bảy, tám đầu Dị Linh tộc nhân luyện chế khôi lỗi, Trương Huyền trái tim co lại."),t("br"),t("br"),n._v("Gặp được nhiều như vậy, hắn đều hẳn phải chết không nghi ngờ, chỉ sợ Ngọc Phi Nhi, Hình Viễn đám người... Dữ nhiều lành ít!"),t("br"),t("br"),n._v("Convert by: ThấtDạ"),t("br"),t("br"),t("br"),t("br")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("a",{attrs:{href:"./chap-681"}},[n._v("Chương trước")]),n._v("  |  "),t("a",{attrs:{href:"./chap-683"}},[n._v("Chương sau")])])])}),[],!1,null,null,null);h.default=c.exports}}]);