(window.webpackJsonp=window.webpackJsonp||[]).push([[1951],{2306:function(n,h,t){"use strict";t.r(h);var i=t(44),g=Object(i.a)({},(function(){var n=this,h=n.$createElement,t=n._self._c||h;return t("ContentSlotsDistributor",{attrs:{"slot-key":n.$parent.slotKey}},[t("h1",{attrs:{id:"chuong-679-ta-goi-ngoan-nhan-phan-2"}},[t("a",{staticClass:"header-anchor",attrs:{href:"#chuong-679-ta-goi-ngoan-nhan-phan-2"}},[n._v("#")]),n._v(" Chương 679: Ta gọi Ngoan Nhân (phần 2)")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("br"),t("br"),n._v("Chương 679: Ta gọi Ngoan Nhân (phần 2)"),t("br"),t("br"),n._v("Thời gian trở lại cùng Hình Viễn đám người phân biệt."),t("br"),t("br"),n._v("Trong cung điện dưới lòng đất."),t("br"),t("br"),n._v("Một cái ôm đầu khôi lỗi chân phát chạy như điên, đằng sau hơn hai mươi đầu Dị Linh tộc khôi lỗi theo đuổi không bỏ, từng cái điên cuồng gào thét, tựa như muốn ăn thịt người."),t("br"),t("br"),n._v("Thấy hắn một lúc toát ra ý tưởng, để bọn này khôi lỗi điên cuồng như vậy, Trương Huyền lòng hiếu kỳ càng ngày càng thịnh."),t("br"),t("br"),n._v("Cái này khôi lỗi thân thể nói ném liền ném đi, phân thân lại đánh không chết, Vu hồn vô hình vô chất, thật muốn chạy trốn, ai cũng không ngăn cản nổi, lại nói, trong đầu còn có kim sắc trang sách, coi như thật bị nhốt lại, cũng có năng lực chạy trốn, đồng thời không sợ."),t("br"),t("br"),n._v("Cho nên mới để mọi người trước trốn, đem bọn này khôi lỗi hấp dẫn tới."),t("br"),t("br"),n._v("Vốn cho rằng hành động này, có thể để những khôi lỗi này theo tới là tốt lắm rồi, nằm mơ đều không nghĩ tới, bọn gia hỏa này cùng gặp cừu nhân giết cha, theo đuổi không bỏ."),t("br"),t("br"),n._v("Có thể để cho một đám không có trí tuệ gia hỏa điên cuồng như vậy, cái này trái tim đến cùng là cái gì?"),t("br"),t("br"),n._v("“Quản hắn là cái gì, chỉ cần sờ một chút, liền biết...”"),t("br"),t("br"),n._v("Muốn cho mọi người thuận lợi chạy đi, chỉ có thể để khôi lỗi không đuổi theo đánh, Trương Huyền cũng không do dự, ôm đầu, rất nhanh chạy như điên, bảy, tám cái hô hấp qua đi, liền đi tới trước đó dòng sông gần."),t("br"),t("br"),n._v("Cỡ nhỏ tế đàn cùng cái kia trái tim vẫn còn ở đó."),t("br"),t("br"),n._v("“Đi qua!”"),t("br"),t("br"),n._v("Thấy sau lưng khôi lỗi đã đi tới gần, Trương Huyền không có có mơ tưởng chỗ trống, thân thể nhảy lên, rơi vào dòng sông."),t("br"),t("br"),n._v("Rống! Rống! Rống!"),t("br"),t("br"),n._v("Thấy hắn nhảy vào đi, tất cả khôi lỗi tràn đầy sốt ruột, nhưng cũng không dám xông lại, từng cái trợn mắt nhìn, dường như muốn đem hắn xé thành bụi phấn."),t("br"),t("br"),n._v("“Thật đúng là...”"),t("br"),t("br"),n._v("Nhìn quả nhiên không có đuổi tới, Trương Huyền nhẹ nhàng thở ra."),t("br"),t("br"),n._v("Trước đó nhìn thấy những khôi lỗi này đều là từng cái đi vào dòng sông, tiếp nhận trái tim linh khí truyền vào, đã cảm thấy nghi hoặc, hiện tại xem ra, suy đoán là đúng."),t("br"),t("br"),n._v("Sông này chảy mỗi lần chỉ có thể để một đầu khôi lỗi tới, cái khác coi như muốn, cũng không dám."),t("br"),t("br"),n._v("“Nhìn xem trái tim này là cái gì...”"),t("br"),t("br"),n._v("Rơi vào dòng sông khoảng cách trái tim không xa, Trương Huyền quay đầu nhìn thoáng qua, nhấc chân liền đi thẳng về phía trước."),t("br"),t("br"),n._v("Soạt!"),t("br"),t("br"),n._v("Ánh mắt mới một tập trung, lập tức cảm thấy một luồng nồng đậm sát lục chi khí, trực tiếp đâm vào trong óc, tựa như chủy thủ."),t("br"),t("br"),n._v("“Thiên Đạo công pháp vận chuyển!”"),t("br"),t("br"),n._v("Sớm biết sẽ có loại tình huống này, nhướng mày, hồn lực vận chuyển."),t("br"),t("br"),n._v("Quả nhiên cảm thấy sát lục chi khí yếu không ít, có điều, vẫn như cũ cảm thấy đau đầu muốn nứt, có chút không kiên trì nổi."),t("br"),t("br"),n._v("“Vu hồn không có thiên đạo chân khí, không đối kháng được...”"),t("br"),t("br"),n._v("Ngừng lại."),t("br"),t("br"),n._v("Cái này sát lục chi khí so với lúc trước Hồng sư xuất ra đầu kia Dị Linh tộc nhân mạnh lớn hơn nhiều lắm, Vu hồn mặc dù cũng tu luyện Thiên Đạo công pháp, nhưng không có thiên đạo chân khí duy trì, không thể nào chống lại, đi đến vậy thì cố hết sức, một khi tiếp tục đi tới, chỉ sợ cũng sẽ bị sát lục chi khí ăn mòn trong óc, tạo thành tổn thương cực lớn."),t("br"),t("br"),n._v("Quay đầu nhìn thoáng qua, chung quanh khôi lỗi mặc dù tức giận, nhưng không dám tới gần, thậm chí cũng không dám đánh xa, an toàn tạm thời không cần lo lắng quá mức, Trương Huyền một cắn răng, tinh thần khẽ động."),t("br"),t("br"),n._v("Phần phật!"),t("br"),t("br"),n._v("Trữ vật giới chỉ bên trong thân thể xuất hiện ở trước mắt."),t("br"),t("br"),n._v("Vu hồn khẽ động từ khôi lỗi trên người thoát ly, trở lại bản tôn."),t("br"),t("br"),n._v("Vừa tiến vào thân thể, lúc này mới cảm thấy dễ chịu rất nhiều, thật dài phun ra một hơi."),t("br"),t("br"),n._v("Vừa rồi khôi lỗi mặc dù so với hắn hiện tại thân thể mạnh lớn hơn nhiều lắm, nhưng vẫn là thân thể của mình tốt, khống chế lại thuận buồm xuôi gió, thư thái nói không nên lời."),t("br"),t("br"),n._v("“Thiên Đạo chân khí!”"),t("br"),t("br"),n._v("Vu hồn cùng thân thể hoàn mỹ phù hợp, chân khí lập tức vận chuyển lên đến, trước đó còn xâm nhập trong óc sát lục chi khí, lập tức biến mất không thấy gì nữa, tựa như không tồn tại đồng dạng."),t("br"),t("br"),n._v("Thiên Đạo chân khí đối Dị Linh tộc loại này quỷ dị khí tức, có tác dụng khắc chế, trước kia liền chuyên môn thí nghiệm qua, lúc này dùng, thuận buồm xuôi gió."),t("br"),t("br"),n._v("Rống!"),t("br"),t("br"),n._v("Thấy trong ao khôi lỗi biến mất, thêm ra một cái nhân loại, đông đảo khôi lỗi càng thêm tức giận, không ngừng gào thét, nhưng thật giống như tại kiêng kị cái gì, không dám tới gần."),t("br"),t("br"),n._v("Gặp bọn họ không đến, Trương Huyền cũng không cần lo lắng, nhanh chân hướng trước mắt trái tim đi tới."),t("br"),t("br"),n._v("Phản chính là muốn mượn nhờ thư viện, cũng cần chạm đến, không có gì kiêng kỵ."),t("br"),t("br"),n._v("Mấy bước đi tới gần, bàn tay nhẹ nhàng hướng tâm tạng đáp tới."),t("br"),t("br"),n._v("“Ha ha!”"),t("br"),t("br"),n._v("Ngón tay tới tiếp xúc, mới vừa hô hô một tiếng “Thiếu hụt”, còn chưa kịp mở ra thư tịch quan sát, liền nghe đến một cái điên cuồng cười tiếng vang lên, ngay sau đó mắt tối sầm lại, một cái to lớn thân ảnh xuất hiện ở trước mắt."),t("br"),t("br"),n._v("“Cái này thân thể, quá tốt rồi, quá tốt rồi... Thật là trời cũng giúp ta!”"),t("br"),t("br"),n._v("Thân ảnh điên cuồng gào thét, to lớn trái tim không ngừng nhúc nhích, giống như là lấy được vật đại bổ, không ngừng run rẩy."),t("br"),t("br"),n._v("“Có cái này thân thể, ngủ say mấy vạn năm, rốt cục có thể sống lại!”"),t("br"),t("br"),n._v("To lớn thân ảnh tràn đầy kích động, rống lên một tiếng, hai mắt mở ra, bắn ra sắc bén điện mang: “Khổng sư, ngươi không phải muốn giết ta ư? Chờ ta trở về, xem ta như thế nào hủy diệt ngươi Danh Sư đường, diệt đi truyền thừa của ngươi...”"),t("br"),t("br"),n._v("Tiếng gầm gừ bên trong, to lớn thân ảnh, trực tiếp hướng Trương Huyền vồ tới."),t("br"),t("br"),n._v("Trên tế đài trái tim, cũng nhoáng một cái liền hướng lồng ngực của hắn bay tới."),t("br"),t("br"),n._v("Xem ra muốn khoan nhập thể nội, thay thế trái tim của hắn."),t("br"),t("br"),n._v("Một khi thành công, ý thức chẳng khác nào bị thôn phệ, triệt để mất đi bản thân."),t("br"),t("br"),n._v("“Ừm?”"),t("br"),t("br"),n._v("Trương Huyền khuôn mặt xiết chặt."),t("br"),t("br"),n._v("Ở trước mặt đối phương, cảm giác khác cảm giác bản thân hình như là cái cự sơn trước mặt sâu kiến, hoàn toàn không cách nào chống cự."),t("br"),t("br"),n._v("“Thật mạnh...”"),t("br"),t("br"),n._v("Hiện nay gặp qua thực lực mạnh nhất là Hồng Viễn đế quốc Mạc đường chủ, liền xem như hắn, tại trái tim này trước mặt, tựa hồ cũng cùng sâu bọ đồng dạng buồn cười!"),t("br"),t("br"),n._v("Loại cảm giác này, thật giống như lúc trước gặp Khổng sư, bất kể tâm cảnh vẫn là tinh thần, đều thân bất do kỷ, tùy ý đối phương khống chế."),t("br"),n._v(" “Chẳng lẽ muốn bị cái tên này đoạt xá?”"),t("br"),t("br"),n._v("Tràn đầy sốt ruột."),t("br"),t("br"),n._v("Vốn là muốn nhìn một chút trái tim này là cái gì, nằm mơ đều không nghĩ tới, cái đồ chơi này lại là sống, hơn nữa còn có thể đoạt xá!"),t("br"),t("br"),n._v("Một khi bị đoạt xá rơi, ý thức liền không có, đến lúc đó cùng một bộ thân thể, không có khác nhau chút nào."),t("br"),t("br"),n._v("“Thiên Đạo chân khí!”"),t("br"),t("br"),n._v("Răng cắn chặt, tinh thần câu thông chân khí, lại phát hiện thân thể như là không phải là của mình, cũng cùng tinh thần đã mất đi liên hệ."),t("br"),t("br"),n._v("Loại cảm giác này, cùng ác mộng đồng dạng, bất kể cỡ nào sốt ruột, chính là không cách nào vận dụng chút nào."),t("br"),t("br"),n._v("“Không cần phản kháng!”"),t("br"),t("br"),n._v("Trái tim bên trong xuất hiện thân ảnh, giống như là nhìn ra hắn tình huống, cười lạnh: “Lúc trước Khổng sư đều tại tay ta bên trên bị thua thiệt, ngươi mặc dù là trời nhận danh sư, tu vi thực sự quá yếu, còn chưa có tư cách phản kháng! Ngoan ngoãn để cho ta đoạt xá, ta sẽ dẫn lấy thân thể của ngươi, rong ruổi thiên địa, lần nữa để thế giới vì đó run rẩy...”"),t("br"),t("br"),n._v("“Khổng sư tại ngươi tay bên trên bị thua thiệt? Ngươi là ai?”"),t("br"),t("br"),n._v("Trương Huyền nhịn không được nói."),t("br"),t("br"),n._v("Khổng sư, là mấy vạn năm trước nhân vật tuyệt thế, nhờ vào sức một mình, sáng chế Danh Sư đường, đối kháng Dị Linh tộc nhân, đồng thời đem nó triệt để khu trục, cống hiến bao lớn, không ai bằng, tu vi cực cao, không người sóng vai."),t("br"),t("br"),n._v("Cái tên này lại còn nói để Khổng sư đều thua thiệt qua, đến cùng là ai?"),t("br"),t("br"),n._v("“Bản tọa danh hào là cái gì, đã không nhớ rõ!”"),t("br"),t("br"),n._v("Tim đập, huyễn hóa ra người tới ảnh, trên người để lộ ra xa xăm khí tức: “Bất quá, lúc ấy tất cả mọi người, đều gọi hô bản tọa vì ‘Ngoan Nhân’!”"),t("br"),t("br"),n._v("“Ngoan Nhân?”"),t("br"),t("br"),n._v("“Không sai! Sợ hãi?” Ngoan Nhân nói."),t("br"),t("br"),n._v("“Chưa từng nghe qua!” Trương Huyền nói."),t("br"),t("br"),n._v("“...” Trái tim."),t("br"),t("br"),n._v("Thượng Cổ lịch sử, đều ghi chép tại cao cấp hơn Danh Sư đường phía trong, Trương Huyền hiện tại chẳng qua tứ tinh danh sư, còn không có tư cách quan sát."),t("br"),t("br"),n._v("Đến tại cái gì là “Ngoan Nhân” vẫn là “Người sói”... Thật đúng là chưa từng nghe qua."),t("br"),t("br"),n._v("“Hôm nay ta liền để ngươi mở mang kiến thức một chút, bản tọa lợi hại!” Không nghĩ tới gặp được như vậy một cái cái gì cũng không biết trời nhận danh sư, trái tim tức giận không ngừng nhảy loạn, một tiếng nổi giận, thân ảnh bàn tay lớn, trực tiếp vồ tới."),t("br"),t("br"),n._v("Kẽo kẹt!"),t("br"),t("br"),n._v("Cổ họng bị nắm, Trương Huyền lập tức cảm thấy hô hấp dồn dập, trước mắt biến thành màu đen."),t("br"),t("br"),n._v("Tại trong tay đối phương, hắn giống như là cái hài nhi, một điểm sức phản kháng đều không có."),t("br"),t("br"),n._v("“Ngươi muốn đoạt xá ta?” Dưới tình thế cấp bách, nhịn không được nói."),t("br"),t("br"),n._v("“Không sai, trời nhận danh sư, thân thể đi qua thiên địa công nhận, ta liền xem như Dị Linh tộc, cũng có thể đoạt xá, không bị ảnh hưởng! Thậm chí, còn có thể ngụy trang thành nhân loại, bất kỳ người nào cũng nhìn không ra...”"),t("br"),t("br"),n._v("Trái tim khẽ nói."),t("br"),t("br"),n._v("“Ngươi nhìn ra ta là trời nhận danh sư?”"),t("br"),t("br"),n._v("Trương Huyền nói."),t("br"),t("br"),n._v("Hắn là trời nhận danh sư sự tình, cho đến trước mắt, cái khác danh sư đều không thể nhận ra, không nghĩ tới bị đầu này trái tim một cái liền nhận ra được."),t("br"),t("br"),n._v("Chẳng lẽ đây thật là cùng Khổng sư cùng thế hệ siêu cường nhân vật?"),t("br"),t("br"),n._v("Có thể cùng Khổng sư đối kháng... Đây rốt cuộc là cái nhân vật cấp bậc nào?"),t("br"),t("br"),n._v("Làm sao có thể sống đến bây giờ, còn chưa có chết vong?"),t("br"),t("br"),n._v("“Khổng sư tên kia chính là, ta cùng hắn chinh chiến cả một đời, như thế nào nhận không ra?” Trái tim hừ lạnh."),t("br"),t("br"),n._v("Đổi lại người khác, hắn khả năng nhận không ra, nhưng hắn một đời đều tại cùng Khổng sư chiến đấu, đối phương là trời nhận danh sư, nếu như ngay cả cái này đều nhận không ra, liền nguy rồi."),t("br"),t("br"),n._v("“Ngươi là cùng Khổng sư cùng thế hệ nhân vật, ta chỉ là cái tiểu nhân vật, như vậy, ngươi trước thả ta xuống, ta khẳng định cũng không phản kháng được...”"),t("br"),t("br"),n._v("Trương Huyền vội vàng nói."),t("br"),t("br"),n._v("“Thế nào, muốn giở trò gian?” Trái tim nói."),t("br"),t("br"),n._v("“Ngươi cường đại như vậy, ta coi như muốn giở trò gian, cũng đùa nghịch không được ah!”"),t("br"),t("br"),n._v("Trương Huyền nói: “Ta lập tức liền bị ngươi đoạt xá, cũng không cách nào chống chọi, chỉ là muốn tại trước khi chết, biết một ít chuyện, cũng coi như chết nhắm mắt!”"),t("br"),t("br"),n._v("“Hừ, ở trước mặt ta giở trò gian không có một chút tác dụng nào!” Trái tim nói."),t("br"),t("br"),n._v("“Làm sao? Ngươi không tin? Chẳng lẽ ngươi cái này kẻ hung hãn là khoác lác, ngay cả ta một cái tu vi như thế tiểu gia hỏa, đều không yên lòng?” Trương Huyền nói."),t("br"),t("br"),n._v("“Làm càn, bản tọa thỏa sức hoành thiên địa thời điểm, Khổng sư đều kiêng kị ba phần, cửu tinh danh sư tới, đều là tiện tay bóp chết, sẽ sợ ngươi cái này tu vi như thế gia hỏa?”"),t("br"),t("br"),n._v("Nghe được Trương Huyền bán tín bán nghi lời nói, trái tim giận dữ."),t("br"),t("br"),n._v("“Không sợ, liền buông ra đối ta ràng buộc, ta loại năng lực này coi như thủ đoạn nhiều hơn nữa, cũng không gây thương tổn được ngươi...”"),t("br"),t("br"),n._v("Trương Huyền tiếp tục nói."),t("br"),t("br"),n._v("Trái tim lên tiếng, dường như cũng cảm thấy đối phương thủ đoạn nhiều hơn nữa, cũng không chống lại được, trầm tĩnh lại."),t("br"),t("br"),n._v("Bị đoạt xá người nếu như không cam tâm, liền xem như hắn, cũng rất khó loại bỏ tai hoạ ngầm, đã cái tên này, muốn chết nhắm mắt, thỏa mãn hắn cũng không tính là gì."),t("br"),t("br"),n._v("Cảm thấy ràng buộc lực lượng của thân thể biến mất, Trương Huyền nhẹ nhàng thở ra."),t("br"),t("br"),n._v("“Được rồi, ngươi có cái gì cứ hỏi đi!”"),t("br"),t("br"),n._v("Trái tim thản nhiên nói."),t("br"),t("br"),n._v("“Được...”"),t("br"),t("br"),n._v("Trương Huyền gật gật đầu, cảm thấy tinh thần lần nữa có thể cùng thân thể liên hệ, đột nhiên cười một tiếng: “Ta muốn hỏi một chút, thứ này ngươi biết không?”"),t("br"),t("br"),n._v("Nói xong, một bản thư tịch màu vàng cao ngất xuất hiện ở trước mắt."),t("br"),t("br"),n._v("“Cái này? Đây là cái gì?”"),t("br"),t("br"),n._v("Trái tim sững sờ, đang đang kỳ quái, chỉ thấy thư tịch bỗng nhiên đè ép xuống, trực tiếp đem nó bao phủ."),t("br"),t("br"),n._v("Ngay sau đó một cái điên cuồng tiếng gầm gừ vang vọng Địa Cung."),t("br"),t("br"),n._v("“Ta cái cỏ, gia gia ngươi, ngươi đến cùng là ai...”"),t("br"),t("br"),n._v("Convert by: ThấtDạ"),t("br"),t("br"),t("br"),t("br")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("a",{attrs:{href:"./chap-687"}},[n._v("Chương trước")]),n._v("  |  "),t("a",{attrs:{href:"./chap-689"}},[n._v("Chương sau")])])])}),[],!1,null,null,null);h.default=g.exports}}]);