(window.webpackJsonp=window.webpackJsonp||[]).push([[1954],{2310:function(n,h,t){"use strict";t.r(h);var i=t(44),c=Object(i.a)({},(function(){var n=this,h=n.$createElement,t=n._self._c||h;return t("ContentSlotsDistributor",{attrs:{"slot-key":n.$parent.slotKey}},[t("h1",{attrs:{id:"chuong-681-tan-chung"}},[t("a",{staticClass:"header-anchor",attrs:{href:"#chuong-681-tan-chung"}},[n._v("#")]),n._v(" Chương 681: Tần Chung")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("br"),t("br"),n._v("Chương 681: Tần Chung"),t("br"),t("br"),n._v("Hỏi không biết bao lâu, Trương Huyền lúc này mới khép sách lại."),t("br"),t("br"),n._v("Khổng sư có hay không Tiên Thiên thai độc sự tình, cái tên này quả thực không rõ ràng, coi như giết chết, cũng nói không nên lời như thế về sau."),t("br"),t("br"),n._v("Trúng độc là chuyện bí ẩn, bằng hữu cũng sẽ không để hắn biết, huống chi kẻ địch."),t("br"),t("br"),n._v("Tựa như hắn, cùng nhau đi tới, bất kể là ai, cũng không biết trúng độc sự tình, chắc hẳn lúc trước Khổng sư cũng là như vậy."),t("br"),t("br"),n._v("Thông qua hỏi thăm, cũng rõ ràng cái tên này tại sao lại biến thành như vậy, cái này Địa Cung mục đích thực sự."),t("br"),t("br"),n._v("Trái tim chủ nhân, khi còn sống rong ruổi tung hoành, hủy thiên diệt địa, không biết bao nhiêu nhân loại chết trên tay."),t("br"),t("br"),n._v("Khổng sư tự mình ra tay, cùng hắn chiến đấu qua nhiều lần, đều để hắn thuận lợi chạy trốn, thẳng đến một lần cuối cùng, hai người chiến trời đất mù mịt, hắn bị cứ thế mà lột đi cốt nhục, tại chỗ đánh thần hồn câu diệt."),t("br"),t("br"),n._v("Thật sự là hắn là chết, có điều, làm vì một thời đại siêu cấp cường giả, làm sao có thể không có biện pháp dự phòng, cùng Khổng sư chiến đấu trước, liền đem một giọt tâm đầu huyết, lưu tại cái này Địa Cung."),t("br"),t("br"),n._v("Đồng thời lưu lại một nhánh tộc nhân thủ hộ."),t("br"),t("br"),n._v("Vài vạn năm đi qua, thương hải tang điền, tâm đầu huyết đi qua nhiều năm diễn biến, rốt cục hội tụ thành một khoả trái tim, ý thức của hắn cũng tỉnh táo lại."),t("br"),t("br"),n._v("Vốn cho rằng, còn có thể một lần nữa tung hoành thiên hạ, kết quả còn chưa kịp ra ngoài, liền gặp bản thân, bị phong ấn ở sách bên trong, biến thành một bộ tương tự tranh vẽ đồ vật."),t("br"),t("br"),n._v("Còn những khôi lỗi này, mặc dù nhưng đã đã mất đi ý thức, nhưng đều là hắn hậu bối, càng là vì phục sinh hắn, hao phí vô số tâm huyết, bởi vậy, ý niệm của hắn, có thể nhẹ nhõm khống chế, cũng có thể mượn nhờ linh trận, để hắn khôi phục thể lực."),t("br"),t("br"),n._v("Hỏi đến nơi này, một cái ý nghĩ đột nhiên xông ra."),t("br"),t("br"),n._v("Ngô Dương Tử tự cho là đắc kế, đem chi này Dị Linh tộc nhân toàn bộ luyện chế thành khôi lỗi, nhưng... Sẽ có hay không có khả năng, bọn này khôi lỗi biết rõ mục đích của hắn, tương kế tựu kế?"),t("br"),t("br"),n._v("Chi này Dị Linh tộc nhân, chỉ có hai mươi đầu, hơn nữa, cơ bản đều là nam đinh, tiếp tục lan tràn đi xuống rõ ràng không thể nào, biết hay không biết bọn hắn biết sắp diệt tộc, vì có thể tiếp tục thủ hộ cái này “Ngoan Nhân”, cố ý để hắn luyện chế thành khôi lỗi?"),t("br"),t("br"),n._v("Như vậy trải qua, tu là còn tại, phòng ngự mạnh hơn, có thể tốt hơn nghe theo “Ngoan Nhân” sai khiến, bốn phía chinh chiến, thậm chí “Ngoan Nhân” phục sinh, làm không cẩn thận, còn có thể một lần nữa để bọn hắn thanh tỉnh..."),t("br"),t("br"),n._v("Bằng không, Dị Linh tộc trời sinh mạnh mẽ, lại là thống trị nhân loại một thời đại siêu tuyệt sinh mệnh, làm sao có thể bị trêu đùa diệt tộc, không còn một mống?"),t("br"),t("br"),n._v("Đương nhiên... Ý nghĩ này mặc kệ đúng đắn còn là sai lầm, đều không thể nào khảo cứu."),t("br"),t("br"),n._v("Trái tim thức tỉnh trước, Ngô Dương Tử liền đã chết, những này Dị Linh tộc nhân, đã biến thành khôi lỗi, liền xem như hắn, đều nói không rõ ràng."),t("br"),t("br"),n._v("  Lại hỏi thăm không ít, cái tên này ý niệm mặc dù thanh tỉnh, trí nhớ lại không triệt để khôi phục, rất nhiều chuyện chỉ biết là cái đại khái, muốn nói rõ chi tiết, lại ngôn ngữ bất tường."),t("br"),t("br"),n._v("Nhất là công pháp, hỏi thăm một chút, chỉ biết là một chút Dị Linh tộc nhân phương pháp tu luyện, nhân loại hầu như một chữ cũng không biết."),t("br"),t("br"),n._v("Cái này khiến Trương Huyền rất là buồn bực."),t("br"),t("br"),n._v("Vốn cho rằng bắt cái Thượng Cổ sống sót lão quái vật, có thể từ đối phương trong miệng làm nhiều chút công pháp, tốt thừa thế xông lên, nhanh chóng tấn cấp, nằm mơ đều không nghĩ tới, cùng nhặt được một cái phế vật không có gì khác nhau."),t("br"),t("br"),n._v("Nhịn không được, lại đánh đối phương tốt mấy trận, lúc này mới thở phào nhẹ nhõm."),t("br"),t("br"),n._v("Đem muốn hỏi hỏi xong, thấy khôi lỗi khoảng cách đào ra đi còn có thời gian nhất định, Trương Huyền cũng không vội vã, khoanh chân ngồi dưới đất, bắt đầu tu luyện."),t("br"),t("br"),n._v("Ngũ Diệu Kim Thân, mặc dù chỉ tu luyện đệ nhất trọng, lại làm cho nhục thể của hắn lực lượng tròn tăng lên gấp đôi, từ hai trăm vạn đỉnh, đạt đến bốn trăm vạn đỉnh!"),t("br"),t("br"),n._v("Cộng thêm chân khí 400 vạn đỉnh, hồn lực 320 vạn đỉnh lời nói, toàn bộ lực lượng đã đạt đến 11 20 vạn đỉnh, có thể so với Hóa Phàm thất trọng Quy Nhất cảnh cường giả đỉnh phong!"),t("br"),t("br"),n._v("(Quy Nhất cảnh sơ kỳ 800 vạn đỉnh, trung kỳ 900 vạn đỉnh, hậu kỳ 1000 vạn đỉnh, đỉnh phong 1100 vạn đỉnh)"),t("br"),t("br"),n._v("Hóa Phàm ngũ trọng Hợp Linh cảnh đỉnh phong, nắm giữ Quy Nhất cảnh lực lượng, vượt ngang hai cái đại cấp bậc, coi như danh sư học viện siêu cấp thiên tài, khẳng định đều làm không được."),t("br"),t("br"),n._v("Cái khác tứ trọng có thiếu hụt, nhất định phải có đủ loại bảo vật mới có thể tu luyện, Trương Huyền đành phải thôi, tiếp tục rèn luyện chân khí, gia tăng đan điền đối chân khí số lượng dự trữ."),t("br"),t("br"),n._v("“Năm ngày!”"),t("br"),t("br"),n._v("Nhìn lên trời hố đồng dạng khu vực, Lạc Thất Thất lắc đầu, trong mắt khó mà kìm lại thất lạc."),t("br"),t("br"),n._v("Năm ngày không nghỉ không ngủ, chính là chờ mong vị kia xả thân cứu người Trương sư, có thể lần nữa sáng tạo kỳ tích, kết quả... Nhìn bộ dáng bây giờ, kỳ tích rất khó xuất hiện."),t("br"),t("br"),n._v("“Lâu như vậy, bị chôn dưới đất, liền xem như Hóa Phàm bát trọng cường giả, đều khó mà sống sót!”"),t("br"),t("br"),n._v("Ngọc Phi Nhi cũng có chút ngẩn người."),t("br"),t("br"),n._v("Đối phương khi còn sống, nàng chọc tức hận không thể đem nó cắn rơi mấy khối thịt, một khi chết rồi, hơn nữa còn là vì cứu các nàng mà chết, trong lòng sinh ra khó mà dứt bỏ chi tình."),t("br"),t("br"),n._v("Chôn dưới đất vài trăm mét, bị nham thạch bao vây, coi như Hóa Phàm cường giả lực lượng siêu quần, sống lâu một, hai canh giờ, thì cũng thôi đi, mấy ngày... Tuyệt đối không thể!"),t("br"),t("br"),n._v("Nói cách khác... Trương sư, lần này là thật đã chết rồi!"),t("br"),t("br"),n._v("Hơn nữa hài cốt không còn, muốn xác chết vùng dậy... Cũng lừa dối không được nữa!"),t("br"),t("br"),n._v("“Đi Thú đường, mượn một đầu linh thú phi hành, mau chóng trở lại Hồng Viễn thành, ta muốn đem chuyện này bẩm báo Phụ Hoàng, để hắn phái người tới! Hình Viễn bọn hắn mặc dù có thể xin động học viện cao thủ, nhưng ta sợ bọn họ sẽ không tận tâm tìm kiếm Trương sư.”"),t("br"),t("br"),n._v("Một cắn răng, đứng dậy, Ngọc Phi Nhi nói."),t("br"),t("br"),n._v("Một mực ở chỗ này chờ, cũng không phải biện pháp, kế trước mắt, chỉ có tìm Thánh vực cấp bậc cường giả, tự mình ra tay đào móc, sống thì gặp người, chết phải thấy xác."),t("br"),t("br"),n._v("Học viện cao thủ, có thể sẽ vì Dị Linh tộc khôi lỗi cùng Ngô Dương Tử đến đây, nhưng muốn cho kỳ hoa phí công phu tìm người, khẳng định là không thể nào."),t("br"),n._v(" Trương sư mệnh danh học viện học viên, vẫn còn không có chính thức báo danh, không có thông qua nhập môn khảo hạch, không tính là tại tịch trong danh sách học sinh, không đáng cường giả Thánh vực, vì đó hao phí công phu."),t("br"),t("br"),n._v("Huống chi, khả năng đã chết."),t("br"),t("br"),n._v("“Tốt!”"),t("br"),t("br"),n._v("Lạc Thất Thất cũng gật gật đầu."),t("br"),t("br"),n._v("Chờ năm ngày, đều không có đi ra, Trương sư đã dữ nhiều lành ít, làm học sinh cần phải làm là đem hắn thi thể tìm ra, thật tốt hậu táng."),t("br"),t("br"),n._v("“Huyễn Vũ đế quốc Thú đường, cách nơi này không xa, chỉ cần toàn lực, nửa ngày liền không sai biệt lắm có thể tới...”"),t("br"),t("br"),n._v("Ngọc Phi Nhi tính toán một chút vị trí, nói."),t("br"),t("br"),n._v("“Nửa ngày? Không cần, nơi này là Thú đường cùng Huyễn Vũ đế đô khu vực cần phải đi qua, thường xuyên có Thú đường linh thú phi hành bay qua, chúng ta có thể nghĩ biện pháp ngồi!”"),t("br"),t("br"),n._v("Lạc Thất Thất ngẩng đầu hướng lên bầu trời nhìn lại."),t("br"),t("br"),n._v("Nơi này khoảng cách Thú đường cứ việc không xa, trèo đèo lội suối, rất dễ dàng gặp được cái khác linh thú, một khi chiến đấu, làm không cẩn thận ba ngày đều không qua được, chậm trễ không ít công phu."),t("br"),t("br"),n._v("Không nếu muốn biện pháp ngồi."),t("br"),t("br"),n._v("Ngồi chính là cho cùng nhất định thù lao, tiện đường cưỡi người khác linh thú."),t("br"),t("br"),n._v("Nơi này là Thú đường cùng Huyễn Vũ đế quốc phải qua chỗ, mỗi ngày đều có thể nhìn thấy không ít linh thú bay qua, có lẽ liền có thể tìm tới một đầu, để các nàng ngồi, như vậy trải qua, mấy chục phút liền có thể bay đến, miễn đi không ít phiền phức."),t("br"),t("br"),n._v("“Ừm!” Ngọc Phi Nhi cũng gật gật đầu, ngẩng đầu nhìn lại, mới vừa nhìn về phía không trung, liền nghe đến một tiếng cánh gào thét, một đầu to lớn Thanh Diệp Long Dực thú từ không trung bay qua."),t("br"),t("br"),n._v("“Thanh Diệp Long Dực thú? Đây là ẩn chứa Long tộc huyết mạch Hợp Linh cảnh linh thú, tại Thú đường địa vị hẳn là cũng không thấp, chính là nó...”"),t("br"),t("br"),n._v("Thấy rõ ràng đầu này linh thú bộ dáng, Lạc Thất Thất ngẩng đầu nhìn về phía bầu trời, dồn khí đan điền, thanh âm lập tức truyền đi lên."),t("br"),t("br"),n._v("“Tại hạ danh sư Lạc Thất Thất, muốn ngồi, mong rằng tạo thuận lợi!”"),t("br"),t("br"),n._v("Danh sư thân phận tại bất kỳ địa phương nào đều dùng rất tốt, trực tiếp báo ra đến, đối phương cho phép ngồi khả năng tương đối lớn."),t("br"),t("br"),n._v("Quả nhiên, nghe được lời của nàng, không trung Thanh Diệp Long Dực thú, xoay một chút, trực tiếp hướng phía dưới hạ xuống, rất mau tới đến hai người gần."),t("br"),t("br"),n._v("Đầu này linh thú trên lưng đồng thời không có nói cung cấp cưỡi phòng ốc, chỉ là trụi lủi một cây dây cương, vừa hạ xuống, một thanh niên liền từ trên lưng nhảy xuống tới."),t("br"),t("br"),n._v("Người này chừng ba mươi tuổi, quần áo lộng lẫy, cặp mắt tinh khí bức người, vậy mà cùng Lạc Thất Thất đám người đồng dạng, là một vị Kiều Thiên cảnh cường giả!"),t("br"),t("br"),n._v("“Thú đường Tần Chung, gặp qua Lạc sư!”"),t("br"),t("br"),n._v("Nhìn thấy muốn ngồi, lại là hai cái như thế cô nương xinh đẹp, Tần Chung ánh mắt sáng lên, tràn đầy kích động."),t("br"),t("br"),n._v("Mỹ nữ loại này cấp bậc, coi như hắn duyệt nữ vô số, cũng chưa bao giờ thấy qua."),t("br"),t("br"),n._v("“Hai người chúng ta muốn đi Thú đường, hy vọng có thể ngồi một chút ngươi linh thú, không biết có thể!” Lạc Thất Thất thản nhiên nói."),t("br"),t("br"),n._v("“Hai vị có thể ngồi, là vinh hạnh của ta!” Tần Chung tràn đầy hưng phấn: “Chỉ là... Ta linh thú không có phòng ốc, có thể muốn oan ức các ngươi...”"),t("br"),t("br"),n._v("“Không sao cả!” Lạc Thất Thất lắc đầu."),t("br"),t("br"),n._v("Các nàng hiện tại mục đích là mau chóng đuổi tới Thú đường, tìm tới thích hợp linh thú lại nói, có hay không phòng ốc, phi hành dễ chịu không thoải mái, râu ria."),t("br"),t("br"),n._v("“Đi Thú đường không tính là quá xa, không biết như thế nào thu phí?” Ngọc Phi Nhi hỏi."),t("br"),t("br"),n._v("Ngồi, tương đương với đáp mượn người khác linh thú, là có thù lao, bình thường đều là sớm nói ra giá cả, nếu không không trung thêm tiền, ngay tại chỗ lên giá, coi như nàng là công chúa, cũng ngồi không dậy nổi."),t("br"),t("br"),n._v("“Vị cô nương này nói đùa, hai vị xinh đẹp như vậy, có thể ngồi ta linh thú, là vinh hạnh của ta, làm sao có ý tứ muốn tiền của các ngươi!”"),t("br"),t("br"),n._v("Tần Chung hai tay một lưng, làm ra một cái tiêu sái biểu lộ, muốn hấp dẫn đối phương chú ý, kết quả phát hiện đối phương không thấy được đồng dạng."),t("br"),t("br"),n._v("“Vẫn là nói giá cả đi, như vậy chúng ta cưỡi cũng yên tâm một chút!”"),t("br"),t("br"),n._v("Thấy cái tên này vẻ mặt lỗ mãng, rất rõ ràng đối động tâm tư khác, Lạc Thất Thất đôi mi thanh tú nhăn lại."),t("br"),t("br"),n._v("Nếu không phải trong lòng sốt ruột, căn bản không thèm để ý như vậy tự cho là đúng gia hỏa."),t("br"),t("br"),n._v("“Cái này...”"),t("br"),t("br"),n._v("Gặp nàng kiên trì, Tần Chung chần chờ một chút, nhãn châu xoay động: “Tại hạ cũng không có yêu cầu gì, như vậy đi, nhìn hai vị phong trần ngủ ngoài trời bộ dạng, hẳn là trong núi chờ đợi thời gian không ngắn đi! Đến Thú đường, nếu như có thể để cho tại hạ cùng ăn một bữa cơm, thu phí loại hình, liền thôi!”"),t("br"),t("br"),n._v("“Ăn cơm?”"),t("br"),t("br"),n._v("Thấy hắn bộ dáng này, hai người chỗ nào không biết nghĩ cái gì, tất cả đều sầm mặt lại."),t("br"),t("br"),n._v("Một cái Huyễn Vũ đế quốc Thú đường gia hỏa, lại dám đùa giỡn hai cái ngũ tinh danh sư, quả thực lá gan quá lớn!"),t("br"),t("br"),n._v("“Vâng, tại hạ không dám có cái gì yêu cầu xa vời, chỉ là muốn tận tình địa chủ hữu nghị. Lại nói, hai vị gấp gáp như vậy đi Thú đường, tất nhiên cũng là muốn tìm kiếm một đầu lợi hại linh thú, ta thân là tứ tinh thuần thú sư, gia phụ càng là Thú đường đường chủ, có lẽ có thể giúp được hai vị!”"),t("br"),t("br"),n._v("Tần Chung cười nói, vẻ mặt tự hào."),t("br"),t("br"),n._v("Hắn Thú đường Thiếu đường chủ thân phận, chỉ phải bày ra đến, người bình thường đều sẽ kính trọng mấy phần, đối phương liền xem như danh sư, nhìn tuổi tác, hẳn là sẽ không vượt qua tam tinh, biết những này, tất nhiên sẽ lau mắt mà nhìn."),t("br"),t("br"),n._v("Đến lúc đó, cách ôm mỹ nhân về liền rất gần."),t("br"),t("br"),n._v("Convert by: ThấtDạ"),t("br"),t("br"),t("br"),t("br")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("a",{attrs:{href:"./chap-689"}},[n._v("Chương trước")]),n._v("  |  "),t("a",{attrs:{href:"./chap-691"}},[n._v("Chương sau")])])])}),[],!1,null,null,null);h.default=c.exports}}]);