(window.webpackJsonp=window.webpackJsonp||[]).push([[1958],{2314:function(n,h,t){"use strict";t.r(h);var g=t(44),i=Object(g.a)({},(function(){var n=this,h=n.$createElement,t=n._self._c||h;return t("ContentSlotsDistributor",{attrs:{"slot-key":n.$parent.slotKey}},[t("h1",{attrs:{id:"chuong-685-nguoi-biet-thu-ngu"}},[t("a",{staticClass:"header-anchor",attrs:{href:"#chuong-685-nguoi-biet-thu-ngu"}},[n._v("#")]),n._v(" Chương 685: Ngươi biết thú ngữ?")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("br"),t("br"),n._v("Chương 685: Ngươi biết thú ngữ?"),t("br"),t("br"),n._v("Huyễn Vũ đế quốc Thú đường thú khu, xây dựng tại một cái cự đại trong núi rừng, vô số linh thú sinh hoạt trong đó, khoan thai tự đắc, rất nhiều thậm chí là thiên địch, tại thuần thú sư thuần phục bên dưới, cũng có thể ở chung cùng một chỗ, không phát sinh mâu thuẫn."),t("br"),t("br"),n._v("“Hồng Viễn đế quốc cách nơi này vượt qua trăm vạn cây số, không biết các ngươi định bao nhiêu ngày chạy tới?”"),t("br"),t("br"),n._v("Bên cạnh đi về phía trước, Hàn Xung vừa nói."),t("br"),t("br"),n._v("“Đương nhiên là càng nhanh càng tốt, tốt nhất có thể tại hai mươi ngày khoảng chừng!” Trương Huyền tính toán một chút, nói."),t("br"),t("br"),n._v("Hồng sư đám người đã đi chín ngày, hơn nữa cưỡi chính là Huyễn Vũ đế quốc hoàng thất phi chu, muốn muốn đuổi kịp, chỉ có thể đem một tháng hành trình rút ngắn đến hai mươi ngày."),t("br"),t("br"),n._v("Vượt qua trăm vạn cây số khoảng cách, coi như linh thú một ngày có thể phi hành hai mươi vạn, ba mươi vạn, cũng không phải mười ngày liền có thể đến tới, nửa đường còn cần nghỉ ngơi, điều chỉnh."),t("br"),t("br"),n._v("“Hai mươi ngày?” Hàn Xung ngừng lại, mày nhăn lại: “Muốn ngắn như vậy thời gian đến Hồng Viễn đế quốc, chỉ sợ ít nhất phải Hóa Phàm thất, bát trọng linh thú mới có thể làm đến!”"),t("br"),t("br"),n._v("Linh thú chủng loại khác biệt, tốc độ cũng không giống, có chút Hóa Phàm thất trọng, liền có bát trọng thậm chí cửu trọng tốc độ, cũng có chút, thực lực mạnh, tốc độ thong thả bên trên không ít."),t("br"),t("br"),n._v("Bất quá, mặc kệ loại kia, muốn hai mươi ngày liền đuổi tới Hồng Viễn đế quốc, không có tan phàm thất, bát trọng, là không thể nào làm được."),t("br"),t("br"),n._v("Trương Huyền gật đầu."),t("br"),t("br"),n._v("Hắn là thuần thú sư, đối linh thú giải rất nhiều, cũng có thể suy tính ra thời gian."),t("br"),t("br"),n._v("“Loại cấp bậc này linh thú, chỉ có ngũ tinh hậu kỳ thậm chí đỉnh phong thuần thú sư mới có thể thuần phục, như vậy, ta mang các ngươi đi trưởng lão viện nhìn xem...”"),t("br"),t("br"),n._v("Hàn Xung lông mày nhăn lại."),t("br"),t("br"),n._v("Nếu là Hóa Phàm tứ trọng, ngũ trọng linh thú, nhờ vào hắn mặt mũi có thể tùy tiện đi mượn, thất trọng, bát trọng, coi như ngũ tinh thuần thú sư đều rất khó thuần phục, hơn nữa đếm lượng không nhiều, chỉ sợ chỉ có thể hướng Thú đường trưởng lão viện nhờ giúp đỡ."),t("br"),t("br"),n._v("“Trưởng lão viện? Có thể hay không quá phiền phức?”"),t("br"),t("br"),n._v("Trương Huyền nhìn qua."),t("br"),t("br"),n._v("“Phiền phức ngược lại không đến nỗi, lão sư ta chính là một vị ngũ tinh đỉnh phong thuần thú sư... Nếu như cho hắn biết, ngươi trẻ tuổi như vậy chính là tứ tinh thuần thú sư, hơn nữa càng là một vị y đạo đại tông sư, khẳng định sẽ cao hứng phi thường!”"),t("br"),t("br"),n._v("Hàn Xung mỉm cười."),t("br"),t("br"),n._v("Bốn người nói chuyện công phu, vòng qua một mảnh núi rừng, đi tới một cái không lớn củi viện."),t("br"),t("br"),n._v("Sân nhỏ là dùng cây cối, bùn đất dựng mà thành, thoạt nhìn mười phần mộc mạc, không có bên ngoài những kiến trúc kia xa hoa."),t("br"),t("br"),n._v("Đẩy cửa đi vào, rộng rãi trong sân, bày đầy đủ loại chiếc lồng, mỗi một cái đều giam giữ một đầu không giống động vật, có linh thú cũng có man thú, lớn nhỏ không đều, lít nha lít nhít chừng bảy, tám mươi cái."),t("br"),t("br"),n._v("“Những thứ này...”"),t("br"),t("br"),n._v("Nhìn thấy những này chiếc lồng, Trương Huyền tràn đầy kỳ quái."),t("br"),t("br"),n._v("Nơi này hẳn là Hàn Xung trong miệng vị lão sư kia chỗ ở, trong lồng động vật, cấp bậc đều không cao, thậm chí còn có không ít cảnh giới tông sư trở xuống man thú."),t("br"),t("br"),n._v("Loại người này, một ngón tay liền có thể đạn chết, đường đường ngũ tinh đỉnh phong thuần thú sư, Hóa Phàm bát trọng cường giả, nuôi dưỡng những vật này, muốn làm gì?"),t("br"),t("br"),n._v("đọc truyện với http://ｔruyencuatui."),t("br"),n._v("net/ “A, lão sư ta đang...”"),t("br"),t("br"),n._v("Hàn Xung biết hắn nghi hoặc cái gì, đang muốn giải thích, liền nghe đến phía trước cửa phòng “Kẹt kẹt!” Mở ra, một cái thần thái sáng láng lão giả đi ra."),t("br"),t("br"),n._v("Lão giả râu bạc trắng tóc trắng, trong tay cầm một quyển sách, dường như không thấy được mọi người, mà là vừa đi vừa nói thầm, không biết nói cái gì, nương theo nói thầm càng nhiều, trên mặt càng ngày càng hưng phấn, dường như tìm được cái gì chân lý, hai mắt tỏa ánh sáng."),t("br"),t("br"),n._v("“Lão sư...”"),t("br"),t("br"),n._v("Hàn Xung đi lên phía trước."),t("br"),t("br"),n._v("“Đừng quấy rầy ta, chờ ta làm xong lại nói!” Nhìn cũng không nhìn đối phương một cái, lão giả khoát tay áo, cầm sách vỡ đi tới một cái chiếc lồng gần."),t("br"),t("br"),n._v("Cái này lồng bên trong đóng bảy, tám đầu Chí Tôn cấp bậc lông đỏ hamster."),t("br"),t("br"),n._v("Loại này hamster, kích thước không lớn, đào đất nhưng cực mạnh, có ít người chuyên môn nuôi nhốt, dùng để đào móc tầng hầm, xây dựng Địa Cung."),t("br"),t("br"),n._v("Đương nhiên, vị lão giả này đem nó nhốt ở trong lồng, hẳn không phải là mục đích này."),t("br"),t("br"),n._v("“Lão sư cứ như vậy, một khi rơi vào nghiên cứu, liền cái gì đều không quan tâm...”"),t("br"),t("br"),n._v("Thấy lão sư không để ý tới mình, Hàn Xung đối ba người cười khổ một tiếng."),t("br"),t("br"),n._v("“Không sao cả!”"),t("br"),t("br"),n._v("Trương Huyền cười nhạt một tiếng."),t("br"),t("br"),n._v("Mặc dù gấp đi Hồng Viễn đế quốc, nhưng cũng không vội vã tại đây trong thời gian ngắn, lão giả này tay cầm thư tịch, lại lấy nhiều như vậy chiếc lồng, coi như hắn, cũng nhịn không được hiếu kỳ."),t("br"),t("br"),n._v("Không để ý tới mọi người vây xem, lão giả ở trong sách liên tục lật mấy trang, rốt cuộc tìm được cái gì, hai mắt tỏa ánh sáng, nhìn trừng trừng lấy trong lồng vài đầu lông đỏ hamster, hít sâu một hơi, hàm răng cắn chặt, hô lên."),t("br"),t("br"),n._v("“Ba 渃 luộc Lạc mật Tiên...”"),t("br"),t("br"),n._v("Nương theo tiếng âm vang lên, vài đầu lông đỏ hamster giống như là nghe được cực kỳ chán ghét lời nói, con mắt đồng thời trừng lớn, há miệng liền nôn."),t("br"),t("br"),n._v("“Ói ra? Không đúng?”"),t("br"),t("br"),n._v("Lão giả gãi gãi đầu, lại liên tục lật vài tờ: “Đoán chừng là ta phát âm phương thức không đúng, một lần nữa!”"),t("br"),t("br"),n._v("“Ba 渃 luộc Lạc mật Tiên...”"),t("br"),t("br"),n._v("Hô hô hô!"),t("br"),t("br"),n._v("Vài đầu lông đỏ hamster, con mắt đảo một vòng, nằm trên mặt đất, hai chân không ngừng run rẩy, tròng mắt trắng bệch, còn có hai cái muốn chạy trốn, kết quả đụng đầu vào lồng giam bên trên, nhất thời ngất đi."),t("br"),t("br"),n._v("“Cái này...”"),t("br"),n._v(" Mọi người đồng thời trừng to mắt."),t("br"),t("br"),n._v("Ngay cả Trương Huyền cũng con mắt xoay loạn."),t("br"),t("br"),n._v("Cái tên này mới vừa rồi là nói cái gì, buồn nôn như vậy, để lông đỏ hamster, sợ đến như vậy? Tình nguyện tại chỗ đụng choáng, cũng không nguyện ý thêm một khắc?"),t("br"),t("br"),n._v("“Đây là... Thú ngữ?”"),t("br"),t("br"),n._v("Nhớ tới cái gì, Lạc Thất Thất nhìn về phía Hàn Xung."),t("br"),t("br"),n._v("Mặc dù không phải thuần thú sư, nhưng thân là danh sư, biết đến rất nhiều, Thượng Cổ thú ngữ vẫn là nghe qua một chút."),t("br"),t("br"),n._v("Nghe nói là Khổng sư tự mình biên soạn, chỉ bất quá niên đại xa xưa bị mất, cho dù có người biết, cũng nắm giữ tại số ít người trong tay, khái không truyền ra ngoài."),t("br"),t("br"),n._v("Chẳng lẽ, Hàn Xung vị lão sư này, đã học tập, đang tiến hành thí nghiệm?"),t("br"),t("br"),n._v("Chỉ là, thú thí nghiệm ngữ cũng không tính là gì, đến cùng nói thứ gì, có thể để cái này lông đỏ hamster, nôn thành như vậy?"),t("br"),t("br"),n._v("“Lão sư tại học tập thú ngữ, hơn nữa đã có hiệu quả nhất định...”"),t("br"),t("br"),n._v("Hàn Xung gật gật đầu."),t("br"),t("br"),n._v("“Hiệu quả?” Trương Huyền các loại người không lời."),t("br"),t("br"),n._v("Nói động vật nôn, là hiệu quả?"),t("br"),t("br"),n._v("Ta còn có thể đánh bọn chúng nôn đây!"),t("br"),t("br"),n._v("“Khụ khụ!” Nghe được đối thoại, nhìn thấy vẻ mặt của mọi người, lão giả mặt mũi xấu hổ, đem thư thu về, cả sửa lại một chút y quan, xoay người lại: “Hàn Xung, ta không phải để chính ngươi học tập ư? Tìm ta có chuyện gì?”"),t("br"),t("br"),n._v("“Bẩm báo lão sư, vị này Trương sư, là ân nhân của ta, muốn tới đây mượn một đầu Hóa Phàm bát trọng cấp bậc linh thú, đi Hồng Viễn đế quốc!” Hàn Xung vội vàng nói."),t("br"),t("br"),n._v("“Hóa Phàm bát trọng?”"),t("br"),t("br"),n._v("Lão giả lông mày nhíu một cái: “Loại cấp bậc này linh thú phi hành, toàn bộ Thú đường chỉ có ba đầu, trong đó hai cái, lần trước bị Huyễn Vũ hoàng thất mượn đi! Cuối cùng một đầu, vừa mới đường dài trở về, mỏi mệt không chịu nổi, chỉ sợ không có cách nào lên đường! Hơn nữa, muốn mượn loại cấp bậc này linh thú, ta liền xem như trưởng lão, cũng nói không tính, cần muốn tiến hành trưởng lão hội, thậm chí đường chủ đồng ý mới được!”"),t("br"),t("br"),n._v("“Cái này...”"),t("br"),t("br"),n._v("Hàn Xung vẻ mặt có chút xấu hổ."),t("br"),t("br"),n._v("Huyễn Vũ đế quốc Thú đường, quản lý bên trên rõ ràng muốn so Vạn Quốc thành muốn nghiêm khắc, Hóa Phàm bát trọng linh thú, tại toàn bộ đế quốc phạm vi, đều thuộc về đỉnh phong nhất, bình thường là sẽ không đảm nhiệm vụ, coi như ra, cũng cần trưởng lão hội đồng ý."),t("br"),t("br"),n._v("Lúc trước hắn tại Vạn Quốc thành Thú đường, thân là đường chủ, một câu nói liền có thể lên đường bất luận cái gì linh thú, lấy vì lão sư thân là đỉnh phong nhất trưởng lão một trong, cũng có cái quyền lợi này, hiện tại xem ra, không dễ dàng như vậy."),t("br"),t("br"),n._v("“Cái kia... Hóa Phàm thất trọng linh thú nhưng còn có?”"),t("br"),t("br"),n._v("Đã vận dụng bát trọng phiền toái như vậy, thất trọng mặc dù chậm chút, cũng còn có thể."),t("br"),t("br"),n._v("“Thất trọng linh thú chúng ta công hội tổng cộng mười bốn đầu, toàn bộ đều thuộc về trưởng lão tư nhân thú sủng, chỉ có năm đầu có thể phi hành, muốn mượn dùng, còn cần trưởng lão tự mình khống chế, độ khó cũng là cực lớn!”"),t("br"),t("br"),n._v("Lão giả lắc đầu: “Hơn nữa, những linh thú này, đại bộ phận đều không thích hợp đường dài phi hành, đi Hồng Viễn đế quốc... Chỉ sợ sẽ không so một chút Hóa Phàm lục trọng linh thú mạnh quá nhiều!”"),t("br"),t("br"),n._v("Thú đường linh thú, có thuê cùng tư nhân hai loại, thuê linh thú, bình thường đều là thuần thú sư tại tập thể thuần phục về sau, từ học đồ hoặc là cấp thấp thuần thú sư chăn nuôi, dùng để thuê cho người khác cưỡi."),t("br"),t("br"),n._v("Tư nhân, thì là thú sủng, bình thường là chiến đấu đồng bọn, loại này linh thú, cự ly ngắn tốc độ cực nhanh, thích hợp chiến đấu, muốn viễn trình phi hành liền khó khăn."),t("br"),t("br"),n._v("Thú đường Hóa Phàm thất trọng thực lực linh thú mặc dù rất nhiều, nhưng hầu như đều là lục địa, cho dù có thể phi hành, cũng đều là cự ly ngắn, đi Hồng Viễn đế quốc khoảng cách như vậy, lặn lội đường xa, có rất ít linh thú có thể làm đến."),t("br"),t("br"),n._v("“Vậy liền không có biện pháp?” Hàn Xung vẻ mặt đỏ lên."),t("br"),t("br"),n._v("Trương sư giúp hắn như thế đại ân, đối phương thật vất vả nâng cái yêu cầu, bản thân thế mà không có hoàn thành, thực sự quá mất mặt."),t("br"),t("br"),n._v("“Không có cách nào...”"),t("br"),t("br"),n._v("Lão giả lắc đầu, đột nhiên nhớ tới cái gì nói: “Trừ phi... Có thể cùng đầu kia câu thông, đưa nó thuyết phục! Nếu như có thể làm đến, đi Hồng Viễn đế quốc, chỉ sợ liền mười ngày đều không dùng đến!”"),t("br"),t("br"),n._v("“Đầu kia?”"),t("br"),t("br"),n._v("Hàn Xung nghĩ đến cái gì, cười khổ lắc đầu, chính muốn nói chuyện, đột nhiên nhớ tới bên người vị thanh niên này thần kỳ, ánh mắt sáng lên: “Đúng rồi, lão sư, chúng ta chỉ sợ không cách nào thuyết phục, nhưng Trương sư... Có khả năng làm đến!”"),t("br"),t("br"),n._v("Thấy học sinh kích động như thế, lão giả mày nhăn lại, nhìn Trương Huyền một cái, vẻ mặt hoài nghi: “Chỉ bằng hắn?”"),t("br"),t("br"),n._v("Không phải hắn không coi trọng, đối phương thoạt nhìn cũng chỉ chừng hai mươi tuổi, từng tuổi này, coi như thiên tư không sai, cũng nhiều nhất là cái nhất tinh, nhị tinh thuần thú sư, loại thực lực này, có thể làm gì?"),t("br"),t("br"),n._v("Đây chính là liền đường chủ đều không có cách nào gia hỏa."),t("br"),t("br"),n._v("“Đừng nhìn Trương sư tuổi không lớn lắm, lại là thực sự tứ tinh thuần thú sư, lúc trước Yêu Thần thú, ta không cách nào thuần phục, chính là hắn thuần phục!”"),t("br"),t("br"),n._v("Hàn Xung vội vàng giải thích, nhớ tới cái gì, nói tiếp: “Hơn nữa... Trương sư hình như cũng biết thú ngữ, có vẻ như... So lão sư còn đều hữu hiệu hơn một chút!”"),t("br"),t("br"),n._v("“Thú ngữ? Ngươi biết Thượng Cổ thú ngữ?”"),t("br"),t("br"),n._v("Lão giả sững sờ, trong mắt tràn đầy hoài nghi."),t("br"),t("br"),n._v("Thượng Cổ thú ngữ, hàng vạn năm trước liền thất truyền, có thể nắm giữ, cũng đều là đỉnh phong nhất thuần thú sư, của mình mình quý, không nguyện ý rò rỉ, một cái trẻ tuổi như vậy gia hỏa, làm sao có thể?"),t("br"),t("br"),n._v("“Ta... Học qua một điểm, nhìn qua vài cuốn sách!” Trương Huyền gật đầu."),t("br"),t("br"),n._v("“Một điểm? Ta nghiên cứu hơn năm mươi năm, đều chỉ hiểu da lông, học qua một điểm có chỗ lợi gì!”"),t("br"),t("br"),n._v("Nghe đối phương chỉ học được một điểm, lão giả lộ ra vẻ thất vọng, khoát tay áo, chính muốn nói tiếp, chỉ thấy thanh niên trước mắt, hít sâu một hơi, miệng đột nhiên mở ra."),t("br"),t("br"),n._v("“Hồng!”"),t("br"),t("br"),n._v("Soạt!"),t("br"),t("br"),n._v("Trong viện tất cả linh thú, man thú, tất cả đều trong nháy mắt nằm rạp trên mặt đất run lẩy bẩy, lão giả cũng cảm thấy toàn thân run lên, mắt tối sầm lại, một cái lảo đảo té ngã trên đất, không ngừng run rẩy."),t("br"),t("br"),n._v("Convert by: ThấtDạ"),t("br"),t("br"),t("br"),t("br")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("a",{attrs:{href:"./chap-693"}},[n._v("Chương trước")]),n._v("  |  "),t("a",{attrs:{href:"./chap-695"}},[n._v("Chương sau")])])])}),[],!1,null,null,null);h.default=i.exports}}]);