(window.webpackJsonp=window.webpackJsonp||[]).push([[1959],{2313:function(n,h,t){"use strict";t.r(h);var g=t(44),i=Object(g.a)({},(function(){var n=this,h=n.$createElement,t=n._self._c||h;return t("ContentSlotsDistributor",{attrs:{"slot-key":n.$parent.slotKey}},[t("h1",{attrs:{id:"chuong-686-thien-long-bat-am"}},[t("a",{staticClass:"header-anchor",attrs:{href:"#chuong-686-thien-long-bat-am"}},[n._v("#")]),n._v(" Chương 686: Thiên Long Bát Âm")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("br"),t("br"),n._v("Chương 686: Thiên Long Bát Âm"),t("br"),t("br"),n._v("Trương Huyền Long ngữ, mặc dù chỉ đối Long tộc hiệu quả lớn nhất, nhưng trong sân man thú, linh thú cấp bậc quá thấp, đồng dạng có cường đại chấn nhiếp tác dụng."),t("br"),t("br"),n._v("Đột nhiên nghe được, không có tại chỗ ngất đi là tốt lắm rồi."),t("br"),t("br"),n._v("Những linh thú này, man thú cái dạng này, hắn có thể lý giải, chỉ là..."),t("br"),t("br"),n._v("Trương Huyền mặt mũi nghi ngờ nhìn qua: “Vị trưởng lão này, ngươi... Không có sao chứ?”"),t("br"),t("br"),n._v("Linh thú nằm xuống, bởi vì bị quản chế Long tộc mạnh mẽ, không dám phản kháng, một mình ngươi loại, nằm trên mặt đất làm gì?"),t("br"),t("br"),n._v("Hơn nữa, như thế mất hồn run rẩy, không ngừng nước miếng..."),t("br"),t("br"),n._v("Chẳng lẽ ta Long ngữ nói cũng làm cho ngươi buồn nôn?"),t("br"),t("br"),n._v("“Lão sư...” Hàn Xung cũng giật nảy mình, vội vàng ấn huyệt nhân trung, độ chân khí, mới làm cho đối phương khôi phục lại."),t("br"),t("br"),n._v("“Ngươi mới vừa mới nói chính là... Chân Long thanh âm?”"),t("br"),t("br"),n._v("Một tỉnh táo lại, lão giả vội vàng đứng lên, nhìn về phía trước mắt Trương Huyền, tràn đầy kích động cùng không thể tin được."),t("br"),t("br"),n._v("“Ta cũng không biết, tùy tiện học được mấy cái âm tiết...”"),t("br"),t("br"),n._v("Trương Huyền nói."),t("br"),t("br"),n._v("Thật sự là hắn không biết học đến cùng là cái gì, lúc trước tưởng rằng Thượng Cổ thú ngữ, học được ba cái âm tiết, liền cái này dùng tốt nhất, cho nên mỗi lần gặp được linh thú, đều lấy ra dùng một lần."),t("br"),t("br"),n._v("Kết quả, chỉ cần vừa hô, mang Long tộc huyết mạch, cơ bản đều sẽ lập tức cứng ngắc, dọa đến gần chết."),t("br"),t("br"),n._v("“Tùy tiện học? Chân Long thanh âm... Nhân loại cũng có thể học?” Lão giả tràn đầy không thể tin được, lời nói nói phân nửa, đột nhiên nói: “Ngươi chờ ta một chút!”"),t("br"),t("br"),n._v("Nói xong vội vã đi về phòng, một lát sau, lấy ra một bản cổ xưa thư tịch, tiện tay mở ra: “Ngươi nhìn!”"),t("br"),t("br"),n._v("Trương Huyền cúi đầu nhìn lại, vừa rồi hắn nói cái kia âm tiết, quả nhiên xuất hiện tại thư tịch bên trên."),t("br"),t("br"),n._v("“Cái này là Chân Long thanh âm?”"),t("br"),t("br"),n._v("Đem thư tịch tiếp nhận, tùy tiện lật vài tờ, nhìn thấy phía trên trang bìa, nhịn không được hỏi."),t("br"),t("br"),n._v("“Ừm, đây là Thượng Cổ một vị thuần thú sư tổng kết, cũng được xưng là !”"),t("br"),t("br"),n._v("“Thiên Long Bát Âm?” Trương Huyền sững sờ."),t("br"),t("br"),n._v("Lão giả gật đầu: “Truyền thuyết, Long tộc bởi vì quá mức mạnh mẽ, bị giới hạn thiên địa tự nhiên quy luật, không cách nào phát ra rất nhiều âm tiết, chỉ có tám cái âm tiết, dùng để bày tỏ bản thân mừng, giận, buồn, vui, hồi tưởng, ưu sầu! Nhưng, không nên xem thường cái này tám cái âm tiết, nhưng có thể biểu đạt ra Long tộc toàn bộ ngôn ngữ, thậm chí so với nhân loại lời nói đều muốn phong phú.”"),t("br"),t("br"),n._v("Trương Huyền gật đầu."),t("br"),t("br"),n._v("Hắn là thuần thú sư, nhìn qua liên quan tới thú ghi chép, một chút thú loại quá mức mạnh mẽ, vì công bằng, liền sẽ để hắn có chỗ thiếu thốn, thanh âm chính là một cái trong số đó."),t("br"),t("br"),n._v("Bất quá, Long tộc cứ việc chỉ có tám cái âm tiết, nhưng không chỉ tám cái ý tứ, liên hợp lại cùng nhau, nắm giữ hơn một nghìn vạn loại tổ hợp, hoàn toàn có thể diễn tả bất luận cái gì lời muốn nói, thậm chí so ngôn ngữ của nhân loại còn muốn phong phú. (Tám cái âm tiết, đủ loại tổ hợp biến hóa, vì tám tám lần phương, 16777216 loại)"),t("br"),t("br"),n._v("Cái gọi là đại âm hi thanh, đại tượng vô hình chính là cái đạo lý này."),t("br"),t("br"),n._v("“Long tộc ngôn ngữ, biến hóa quá nhiều, mỗi một loại tổ hợp đều đại biểu một ý nghĩ, không có Chân Long tới giao lưu, ai cũng không biết bày tỏ cái gì! Càng quan trọng hơn là...”"),t("br"),t("br"),n._v("Lão giả nói đến đây, con mắt trừng lớn, tràn đầy không thể tin được nhìn qua: “Thiên Long Bát Âm, cần nắm giữ Chân Long huyết mạch có thể phát ra, bình thường người thân thể căn bản không chịu nổi, ngươi... Ngươi làm sao làm được? Chẳng lẽ... Ngươi là Chân Long chi thân?”"),t("br"),t("br"),n._v("“Chân Long chi thân?”"),t("br"),t("br"),n._v("Trương Huyền nghi ngờ nhìn qua."),t("br"),t("br"),n._v("“Đúng vậy a, Chân Long chi thân, là thể chất đặc thù bên trong một loại, so Thuần Dương, Thuần Âm còn cường đại hơn, một khi kích hoạt, huyết mạch trong cơ thể sẽ đến gần vô hạn Chân Long, tu vi cùng lực lượng, đều sẽ có trên phạm vi lớn tiến bộ... Nhất là tu luyện bình cảnh, hầu như không có...”"),t("br"),t("br"),n._v("Càng nói càng kích động, lão giả vẻ mặt dần dần ửng hồng."),t("br"),t("br"),n._v("Chân Long chi thân, nhưng mà thể chất đặc thù bên trong, đều bài danh phía trên tồn tại, trước mắt vị này, có thể phát ra nhân loại không cách nào phát ra Long ngữ, chẳng lẽ là loại thể chất này?"),t("br"),t("br"),n._v("Nếu thật là, cái kia liền đáng sợ!"),t("br"),t("br"),n._v("“Lão sư, ngươi thật chẳng lẽ là?”"),t("br"),t("br"),n._v("Nghe đến lão giả lời nói, Lạc Thất Thất, Ngọc Phi Nhi liếc mắt nhìn nhau, đồng thời tràn đầy kỳ quái nhìn qua."),t("br"),t("br"),n._v("Căn cứ vị này vị này Trương sư truyền kỳ kinh nghiệm đến xem, tốc độ tu luyện của hắn quả thực quá nhanh"),t("br"),t("br"),n._v("Vốn cho rằng là có cái hảo lão sư nguyên nhân, chẳng lẽ thật là loại này Chân Long chi thân?"),t("br"),t("br"),n._v("Muốn là như thế này, rất nhiều chuyện cũng là có thể giải thích thông."),t("br"),t("br"),n._v("“Ta...”"),t("br"),t("br"),n._v("Trương Huyền vò đầu."),t("br"),t("br"),n._v("Chỗ của hắn là cái gì Chân Long chi thân, sở dĩ có thể hô lên Long ngữ, là bởi vì có Thiên Đạo chân khí cùng Thiên Đạo kim thân duyên cớ."),t("br"),t("br"),n._v("Đương nhiên, cái này lời nói không thể nói ra được, đành phải nói sang chuyện khác: “Quyển sách này, ghi chép toàn bộ tám cái âm tiết?”"),t("br"),t("br"),n._v("“Tám cái âm tiết? Đương nhiên không thể nào!”"),t("br"),t("br"),n._v("Thấy đối phương không nguyện ý bao nhiêu những này, lão giả cũng không truy vấn, lắc đầu: “Thiên Long Bát Âm, là Long tộc ngôn ngữ, nhân loại là không có tư cách đánh cắp, quyển sách này chính là ghi chép việc này, phỏng đoán phát ra tiếng phương thức, cụ thể có ý tứ gì, như thế nào phát âm, cũng lời chi không rõ!”"),t("br"),t("br"),n._v("“Ah!” Trương Huyền tiện tay đọc qua thư tịch, tinh thần lại rơi vào trong thư viện."),t("br"),t("br"),n._v("Thư tịch đặt ở thư viện thoạt nhìn tốc độ nhanh không nói, càng có thể phân biệt thật giả, tìm tới thiếu hụt."),t("br"),t("br"),n._v("Hô!"),t("br"),t("br"),n._v("Thời gian nháy mắt, đem thư tịch quét một lần, lập tức hiểu được."),t("br"),t("br"),n._v("Quyển sách này cùng đối phương nói đồng dạng, chỉ ghi chép Thiên Long Bát Âm nguồn gốc, cùng cùng Long tộc quan hệ, còn cái khác, liền lời chi không rõ."),t("br"),n._v(" Phía trên viết ra cái này tám cái âm tiết, trong đó có hắn thường xuyên nói cái kia “Hồng”, bất quá nếu như phát âm, ghi tròn hơn ba mươi đầu, không có một cái đúng đắn."),t("br"),t("br"),n._v("Còn trước đó học mặt khác hai cái âm tiết, thì vắng mặt cái này tám cái âm tiết bên trong, xem ra hẳn không phải là Long ngữ."),t("br"),t("br"),n._v("Cũng khó trách cái kia hai cái không có gì tác dụng, cho tới nay, đều dùng cái này thuần phục linh thú, nguyên lai căn bản cũng không phải là Long ngữ."),t("br"),t("br"),n._v("Thư tịch bên trên kỹ càng ghi Long ngữ độ khó, cùng lão giả nói đồng dạng, sinh mệnh cấu tạo khác biệt, nhân loại coi như biết phát âm, cũng là không cách nào bắt chước cùng phát ra."),t("br"),t("br"),n._v("Cho nên, hơn ba mươi đầu, đều chỉ là phán đoán, không có chuyện thực căn cứ."),t("br"),t("br"),n._v("“Ừm? Lại có một cái âm phát ra tiếng phương pháp là đúng đắn?”"),t("br"),t("br"),n._v("Đem thư tịch nhìn xong, đột nhiên con mắt rơi vào cuối cùng."),t("br"),t("br"),n._v("Quyển sách này ghi đến cuối cùng, bát âm bên trong, thế mà còn có một cái phát âm phương pháp, là đúng đắn."),t("br"),t("br"),n._v("Chính là Thiên Long Bát Âm bên trong thứ hai âm “Bò... Ò...!”."),t("br"),t("br"),n._v("Đem phát âm phương pháp, ghi lại ở trong lòng, định dành thời gian thử một chút, đột nhiên nhớ tới cái gì, nhịn không được nhìn về phía lão giả trước mắt."),t("br"),t("br"),n._v("“Phía trên này ghi, Thiên Long Bát Âm, chỉ đối nắm giữ Long tộc huyết mạch sinh mệnh, có áp chế tác dụng, thuộc về Chân Long áp chế, ngươi... Vừa rồi làm sao cũng nằm xuống?”"),t("br"),t("br"),n._v("Kỳ thật không chỉ hắn kỳ quái, Lạc Thất Thất, Hàn Xung mấy người cũng tràn đầy nghi hoặc."),t("br"),t("br"),n._v("Vừa rồi Trương sư phát ra thanh âm mặc dù to lớn, nhưng đối bọn hắn tới nói, chỉ là có chút ầm ĩ mà thôi, cũng không chịu ảnh hưởng, làm sao ngược lại Hóa Phàm bát trọng đỉnh phong trưởng lão, trực tiếp nằm trên mặt đất? Còn không ngừng run rẩy, miệng sùi bọt mép, thoạt nhìn như thế chướng tai gai mắt?"),t("br"),t("br"),n._v("“Cái này...”"),t("br"),t("br"),n._v("Nghe được tra hỏi, lão giả không có trước đó kinh ngạc, vẻ mặt lúc thì trắng lúc thì đỏ, qua nửa ngày than thở một tiếng: “Ngươi đi theo ta!”"),t("br"),t("br"),n._v("Nói xong hướng nhà cỏ đằng sau đi đến."),t("br"),t("br"),n._v("“Lão sư...”"),t("br"),t("br"),n._v("Nhìn lão sư vậy mà đợi bọn hắn cùng đi, Hàn Xung tràn đầy nghi hoặc."),t("br"),t("br"),n._v("Hắn đi theo lão sư học tập, cũng có một đoạn thời gian, nhưng lão sư chưa từng để hắn đi nhà cỏ đằng sau, bản thân có một lần, hiếu kỳ muốn qua, thậm chí bị trách mắng một trận, đồng thời cảnh cáo, không có chuyện gì khác, không được qua đây."),t("br"),t("br"),n._v("Những ngày này một mực nghi hoặc, nhà cỏ đằng sau đến cùng là cái gì, không nghĩ tới lão sư thế mà chủ động muốn mang bọn họ tới."),t("br"),t("br"),n._v("Trương Huyền đám người không biết những này điển cố, cùng ở sau lưng lão ta, vòng qua không lớn nhà tranh, hướng (về) sau đi đến."),t("br"),t("br"),n._v("Nhà cỏ về sau, là cái không lớn dốc núi, sườn núi trước thì súc lập một cái cự đại bia đá."),t("br"),t("br"),n._v("Ngẩng đầu hướng bia đá nhìn lại, phía trên ghi năm chữ to: “Long Nham thú chi mộ!”"),t("br"),t("br"),n._v("“Hóa Phàm bát trọng lục địa bá vương cấp bậc... Long Nham thú?”"),t("br"),t("br"),n._v("Mày nhăn lại, Trương Huyền nhịn không được nhìn qua."),t("br"),t("br"),n._v("“Không sai!” Lão giả nhẹ gật đầu, nhìn về phía trước mắt dốc núi, trong mắt mang theo lưu luyến không rời: “Nó... Là ta linh thú!”"),t("br"),t("br"),n._v("“Của ngươi?” Hàn Xung sững sờ, con mắt trợn tròn: “Lão sư linh thú... Chết rồi?”"),t("br"),t("br"),n._v("Hắn biết mình lão sư, là ngũ tinh đỉnh phong thuần thú sư, nhưng chưa bao giờ thấy qua lão sư linh thú là cái gì, vốn cho rằng, đi làm cái gì sự tình, không có ở bên người, nằm mộng cũng nghĩ không ra, đã chết."),t("br"),t("br"),n._v("Thuần thú sư mặc dù có thể có rất nhiều thú sủng, nhưng thu được đối phương tín nhiệm, tất nhiên cũng phải bỏ ra rất nhiều."),t("br"),t("br"),n._v("Đại bộ phận thuần thú sư, đều sẽ đem thuần phục thú sủng xem như bằng hữu tốt nhất, thậm chí thân nhân."),t("br"),t("br"),n._v("Thú chết, đối thuần thú sư đả kích, là vô cùng, không ít người đều lại bởi vậy không gượng dậy nổi."),t("br"),t("br"),n._v("Lão sư chưa từng cùng các trưởng lão khác tiếp xúc, một người ở tại như thế chỗ thật xa, chẳng lẽ, liền bởi vì chính mình thú sủng chết rồi?"),t("br"),t("br"),n._v("“Ừm, ta thú sủng chết rồi, cẩn thận nói đến, là bởi vì ta mà chết!”"),t("br"),t("br"),n._v("Lão giả lắc đầu."),t("br"),t("br"),n._v("“Bởi vì ngươi?”"),t("br"),t("br"),n._v("“Đầu này Long Nham thú, là ta trở thành ngũ tinh thuần thú sư thuần phục linh thú...” Lão giả than thở một tiếng, đem chuyện của hắn nói ra."),t("br"),t("br"),n._v("Hắn thuần phục Long Nham thú, thuận lợi trở thành ngũ tinh thuần thú sư, hắn lúc đó, ý khí phong phát, không sợ trời không sợ đất, kết quả tại một lần trong giao chiến... Gặp phải nguy hiểm."),t("br"),t("br"),n._v("Lần kia hắn bản thân bị trọng thương, bất cứ lúc nào cũng sẽ tử vong, là đầu này Long Nham thú, dùng tinh huyết của mình cứu hắn, sau đó, không trị bỏ mình."),t("br"),t("br"),n._v("Chính vì vậy, hắn một mực áy náy, đem nó chôn ở nơi này, lập xuống mộ bia, thề làm bạn cuối đời."),t("br"),t("br"),n._v("“Bởi vì ta trong cơ thể dung hợp Long Nham thú tinh huyết, mới đối Chân Long thanh âm mẫn cảm, có thể chịu đến áp bách...”"),t("br"),t("br"),n._v("Lão giả nói."),t("br"),t("br"),n._v("Trương Huyền gật đầu."),t("br"),t("br"),n._v("Khó trách."),t("br"),t("br"),n._v("Long Nham thú hình thể mặc dù rất lớn, nhưng số lượng máu tươi có hạn, dùng tới cứu người, yêu cầu càng thêm tinh thuần! Dung nhập huyết mạch, mặc dù không là Chân Long thể chất, cũng sẽ nhận Long ngữ áp bách, thân bất do kỷ."),t("br"),t("br"),n._v("“Ngươi cố gắng học tập Thượng Cổ thú ngữ, thậm chí học tập Thiên Long Bát Âm... Cũng bởi vì nên cùng cái này có quan hệ chứ?”"),t("br"),t("br"),n._v("Trương Huyền nhìn qua."),t("br"),t("br"),n._v("Đối phương như thế chăm chỉ không ngừng nghiên cứu Thượng Cổ thú ngữ, cái gì đều không quan tâm, có lẽ cũng cùng cái này kinh nghiệm có quan hệ."),t("br"),t("br"),n._v("“Đúng... Lúc ấy ta trọng thương hôn mê, là Long Nham thú đã cứu ta, ở đây còn có một đầu khác linh thú... Chính là ta mới vừa nói mười ngày liền có thể đến Hồng Viễn đế quốc cái kia —— Tử Dực Thiên Hùng thú!”"),t("br"),t("br"),n._v("Lão giả nắm đấm xiết chặt: “Long Nham thú trước khi chết, nói với ta rất nhiều, nhưng mà khi đó hôn mê bất tỉnh, cũng không biết cái gì, những năm này một mực cố gắng học tập Thượng Cổ thú ngữ, chính là muốn từ trong miệng nó biết... Nói thứ gì, lại có cái gì nguyện vọng, để cho ta hoàn thành...”"),t("br"),t("br"),n._v("Nói đến đây, lão giả hốc mắt ửng hồng."),t("br"),t("br"),n._v("Convert by: ThấtDạ"),t("br"),t("br"),t("br"),t("br")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("a",{attrs:{href:"./chap-694"}},[n._v("Chương trước")]),n._v("  |  "),t("a",{attrs:{href:"./chap-696"}},[n._v("Chương sau")])])])}),[],!1,null,null,null);h.default=i.exports}}]);