(window.webpackJsonp=window.webpackJsonp||[]).push([[196],{547:function(n,h,t){"use strict";t.r(h);var g=t(44),c=Object(g.a)({},(function(){var n=this,h=n.$createElement,t=n._self._c||h;return t("ContentSlotsDistributor",{attrs:{"slot-key":n.$parent.slotKey}},[t("h1",{attrs:{id:"chuong-1153-gap-lai-hua-truong-lao"}},[t("a",{staticClass:"header-anchor",attrs:{href:"#chuong-1153-gap-lai-hua-truong-lao"}},[n._v("#")]),n._v(" Chương 1153: Gặp lại Hứa trưởng lão")]),n._v(" "),t("hr"),n._v(" "),t("p",[n._v("Chương 1153: Gặp lại Hứa trưởng lão"),t("br"),t("br"),n._v("Độc điện người thật là bởi vì hắn, mới bị liên lụy, một khi giao cho Danh Sư đường, tất nhiên sẽ chịu đến rất nhiều chính nghĩa chi sĩ gạt bỏ, kết quả cuối cùng, ai cũng không biết."),t("br"),t("br"),n._v("Nhưng mà nếu như có thể chiêu tiến Chiến sư đường, liền không đồng dạng."),t("br"),t("br"),n._v("Không chỉ cấp cho tẩy trắng cơ hội, càng ban cho tốt hơn thân phận... Chắc hẳn tất cả độc sư sẽ không cự tuyệt."),t("br"),t("br"),n._v("Hơn nữa, mấu chốt nhất là, nếu như có thể đem độc sư dùng tốt, Chiến sư đường thực lực tổng hợp, tất nhiên sẽ lập tức bạo tăng!"),t("br"),t("br"),n._v("“Đây nhất định không được...”"),t("br"),t("br"),n._v("Hình đường chủ da mặt co lại, vội vàng xua tay."),t("br"),t("br"),n._v("“Vì cái gì?” Trương Huyền sững sờ."),t("br"),t("br"),n._v("“Độc sư, ngươi cũng biết, từ trước tới nay tản mạn quen rồi, ta sợ gia nhập Chiến sư đường, rất khó khống chế. Chuyện trọng yếu nhất, bọn họ tu luyện công pháp, rất nhiều đều mười phần kỳ lạ, không phải đứng đắn đường tắt, danh sư sẽ không làm...”"),t("br"),t("br"),n._v("Hình đường chủ lắc đầu."),t("br"),t("br"),n._v("Độc sư, vì tăng cao tu vi, nuôi dưỡng độc trùng, nuốt ăn độc vật, thậm chí dùng sinh mệnh dưỡng cổ... Không ít thủ đoạn, đều chạm đến danh sư ranh giới cuối cùng."),t("br"),t("br"),n._v("Chiến sư, là danh sư một cái phân loại, thật muốn tiếp nhận, về sau còn làm sao phục người?"),t("br"),t("br"),n._v("Chiến sư có thể tăng thực lực lên, có thể tăng lên sức chiến đấu, nhưng cũng có hắn chuẩn tắc, cũng không phải là người nào đều có thể thu."),t("br"),t("br"),n._v("“Cái này đơn giản, ta sẽ cho bọn hắn lần nữa thiết kế độc công phương pháp tu luyện, đường đường chính chính, phù hợp đại đạo! Hơn nữa, ta sẽ để hắn nghe lời, để đường chủ có thể hoàn toàn khống chế!”"),t("br"),t("br"),n._v("Trương Huyền nói."),t("br"),t("br"),n._v("Độc công bên trong, quả thực có rất nhiều càng nhân luân, lễ pháp, có điều, chuyện này muốn xử lý cũng rất đơn giản."),t("br"),t("br"),n._v("Chỉ cần tu luyện hắn bản rút gọn độc công, không chỉ tu vi tiến bộ cực nhanh, cũng không cần đi cái gì tà ma ngoại đạo!"),t("br"),t("br"),n._v("“Cái này...” Hình đường chủ chần chừ."),t("br"),t("br"),n._v("Trước mắt vị thanh niên này năng lực hắn biết đến rất rõ ràng."),t("br"),t("br"),n._v("Có thể xem một lần công pháp, mười một cái hô hấp, liền tu luyện tới viên mãn như ý siêu cường nhân vật, sửa chữa độc công, ngược lại cũng thật có khả năng."),t("br"),t("br"),n._v("Nếu như hai điểm này có thể giải quyết, đem độc sư hợp nhất, đối bọn hắn Chiến sư đường quả thực có trợ giúp rất lớn."),t("br"),t("br"),n._v("Không nói cái khác, liền nói vị này Trung Thanh Vương, năm đó nếu không phải dựa vào độc, làm sao có thể một bước lên trời, ngắn ngủi hơn hai mươi năm, liền đi tới hiện tại tình trạng."),t("br"),t("br"),n._v("“Danh sư, hữu giáo vô loại, độc sư mặc dù danh tiếng không tốt, nhưng tổng bộ một mực không hạ lệnh diệt trừ, nói rõ tại cấp cho cơ hội... Nếu như Trương sư có thể có biện pháp, để cái này độc sư, cam tâm tình nguyện vì ta nhân tộc hiệu lực, ta sẽ báo lên tổng bộ, tuyệt đối là một công lao lớn!”"),t("br"),t("br"),n._v("Đem đối thoại nghe được trong tai, Ngô sư xen vào nói."),t("br"),t("br"),n._v("Độc sư là để cho người ta e ngại, có thể dùng được rồi, sức chiến đấu đồng dạng đáng sợ."),t("br"),t("br"),n._v("Nhất là đối mặt quần công thời điểm."),t("br"),t("br"),n._v("Độc điện tổng bộ, ẩn tàng mặc dù rất sâu, nhưng Danh Sư đường tổng bộ tất nhiên biết, cho tới nay, đều không có giống lúc trước đối phó vu hồn sư đồng dạng, ra tay tiêu diệt, nói rõ cũng không phải là tội ác tày trời, không được tha thứ."),t("br"),t("br"),n._v("Huống chi, những này độc sư, rất nhiều đều sinh trưởng ở địa phương tại Tĩnh Viễn thành bên cạnh hòn đảo nhỏ kia bên trên, chưa hề đi ra qua, cho dù học độc, nhưng chưa bao giờ hại qua người."),t("br"),t("br"),n._v("Không hại qua người, chưa làm qua ác, chỉ bằng vào chức nghiệp không tốt, liền muốn một cái búa gạt bỏ, thật muốn làm như thế, danh sư cùng những nghề nghiệp khác có cái gì khác nhau?"),t("br"),t("br"),n._v("“Tốt!”"),t("br"),t("br"),n._v("Nghe được Ngô sư đồng ý, Trương Huyền thở phào nhẹ nhõm."),t("br"),t("br"),n._v("Hình đường chủ thực lực mặc dù vượt qua Ngô sư, nhưng muốn nói địa vị, đương nhiên vẫn là người sau cao hơn một cấp, hắn đã nói như vậy, nhất định là nghĩ kỹ đến tiếp sau phương pháp."),t("br"),t("br"),n._v("“Hai vị kia ở đây làm sơ đợi chờ, ta đi địa lao nhìn một chút Độc điện mọi người, thuận tiện nhìn một chút có thể hay không thuyết phục, thực sự không được, lại thương nghị làm sao quyết định đi!”"),t("br"),t("br"),n._v("Trương Huyền nói."),t("br"),t("br"),n._v("“Cái này... Cũng tốt, có điều, Trương sư trên người ngươi tổn thương...” Ngô sư có chút lo lắng."),t("br"),t("br"),n._v("“Phục thuốc chữa thương, lại thêm thể chất của ta, tạm thời đã không có quá đáng lo!” Trương Huyền cười khoát tay áo."),t("br"),t("br"),n._v("“Không ngại là tốt rồi, có điều, độc sư tính tình xảo quyệt, không được, ta cùng ngươi cùng đi đi!”"),t("br"),t("br"),n._v("Nhớ tới trước mắt vị này tất cả thủ đoạn, biết điểm ấy tổn thương hẳn là không ngại, Ngô sư thở phào nhẹ nhõm, nói tiếp."),t("br"),t("br"),n._v("“Quên đi, ngươi bây giờ địa vị cùng thân phận, ta sợ sẽ cho bọn họ mang đến áp lực, ta tự mình đi qua là đủ...”"),t("br"),t("br"),n._v("Trương Huyền cự tuyệt."),t("br"),t("br"),n._v("Muốn thuyết phục Hứa Du đám người gia nhập Chiến sư đường, hơn nữa ngoan ngoãn nghe lời, khẳng định muốn tế ra Tôn Cường thân phận, Ngô sư đi qua, chẳng khác nào ngồi vững hắn độc sư thân phận, vẫn là cẩn thận chút tốt."),t("br"),t("br"),n._v("“Cái này...”"),t("br"),t("br"),n._v("Dừng lại một chút, Ngô sư đành phải nhẹ gật đầu: “Cũng tốt, chúng ta có thể không đi địa lao, có điều, phải ở bên ngoài đợi chờ, một khi phát giác vấn đề, liền lập tức ra tay!”"),t("br"),t("br"),n._v("“Làm phiền hai vị!”"),t("br"),t("br"),n._v("Biết đối phương là thật quan tâm bản thân, Trương Huyền nhẹ gật đầu."),t("br"),t("br"),n._v("Tại vừa rồi vị danh sư kia dẫn đầu xuống, mọi người rất mau tới đến địa lao trước mặt."),t("br"),t("br"),n._v("Để Ngô sư, Hình đường chủ hai người ở bên ngoài chờ đợi, Trương Huyền một thân một mình đi vào."),t("br"),t("br"),n._v("Vương phủ địa lao có chút âm trầm, bốn phía che kín đủ loại đại trận, để phòng ngừa bị giam giữ tu luyện giả chạy ra."),t("br"),t("br"),n._v("Đi vào trong đó, Trương Huyền toàn thân xương cốt “Cục cục!” Vang vọng, lần nữa biến thành lúc trước “Tôn Cường” bộ dáng."),t("br"),t("br"),n._v("“Hứa trưởng lão...”"),t("br"),t("br"),n._v("Rất nhanh, thấy được Hứa trưởng lão, quả nhiên cùng trước đó vị danh sư kia nói đồng dạng, nhận lấy cực nặng cực hình, so hơn hai tháng trước gầy gò không ít, khí tức cũng có chút uể oải."),t("br"),t("br"),n._v("Vừa nhìn liền biết, thương thế không nhẹ."),t("br"),t("br"),n._v("Đồng dạng bị giam giữ, chỉ có có lẽ chừng một trăm."),t("br"),n._v(" Còn Độc điện những người khác, sống hay chết, cũng không biết."),t("br"),t("br"),n._v("“Tổ sư...”"),t("br"),t("br"),n._v("Nhìn thấy Trương Huyền, Hứa trưởng lão đám người đầu tiên là sững sờ, lập tức nguyên một đám tràn đầy kích động."),t("br"),t("br"),n._v("Trước mắt vị này Tôn Cường, Hải độc sư, Bạch độc sư hai vị đều chứng minh thân phận, bọn họ không có chút nào hoài nghi."),t("br"),t("br"),n._v("“Ta chỉ là hộ tống tổ sư người, không phải tổ sư, gọi ta Tôn Cường là được...” Lắc đầu, Trương Huyền nói: “Các ngươi chịu khổ, đây là khôi phục thể lực đan dược, mau mau dùng, tranh thủ khôi phục chút thể lực...”"),t("br"),t("br"),n._v("Cổ tay khẽ đảo, lấy ra mấy cái bình ngọc, đem bên trong đan dược đưa tới."),t("br"),t("br"),n._v("Săn giết Thanh Điền thập đại vương giả, hắn loại này bổ dưỡng đan dược rất nhiều, chỉ cần lặng lẽ truyền vào vài ngày đạo chân tức giận, chữa thương, khôi phục thể lực, liền nắm giữ kỳ hiệu."),t("br"),t("br"),n._v("“Vâng!”"),t("br"),t("br"),n._v("Hứa trưởng lão mấy người cũng không nói nhiều, tất cả đều nhận lấy đan dược, ăn vào."),t("br"),t("br"),n._v("Dược vật cửa vào tức tan, cất giấu trong đó Thiên Đạo chân khí, theo huyết dịch chảy vào toàn thân, trước đó chịu đến tổn thương, lập tức mắt trần có thể thấy khôi phục."),t("br"),t("br"),n._v("“Cái này...”"),t("br"),t("br"),n._v("Cảm nhận được biến hóa trên người, Hứa Du đám người nguyên một đám trợn mắt hốc mồm, khó tin."),t("br"),t("br"),n._v("Cái này so với bọn hắn phối trí dược vật, hiệu quả mạnh mẽ quá nhiều... Không hổ hộ tống tổ sư người, nhất định nhận được tổ sư truyền thừa."),t("br"),t("br"),n._v("“Độc điện thế nào?”"),t("br"),t("br"),n._v("Thấy bọn họ khôi phục thể lực, Trương Huyền hỏi."),t("br"),t("br"),n._v("“Độc điện...” Hứa Du hốc mắt một đỏ: “Xem như hoàn toàn hủy, chỉ sống sót chúng ta những này, cái khác cơ bản đều bị tàn sát sạch sẽ...”"),t("br"),t("br"),n._v("Minh Chân thân là Độc điện phó điện chủ, biết toàn bộ Độc điện tất cả bí mật, nhẹ nhõm dẫn người lẻn vào trong đó, này mới khiến tất cả mọi người không có phòng bị, cuối cùng bị trước nay chưa có hạo kiếp."),t("br"),t("br"),n._v("“Đồ sát? Cái kia Trung Thanh Vương, là trực tiếp đồ sát?”"),t("br"),t("br"),n._v("Trương Huyền cau mày."),t("br"),t("br"),n._v("Vốn, hắn tưởng rằng gặp cực mạnh phản kháng, hai bên đều có tổn hại, nghe đối phương hơi thở, dường như không phải chuyện như vậy."),t("br"),t("br"),n._v("“Vâng! Bọn họ là ban đêm tiến công, chúng ta không có chút nào phòng bị phía dưới, liền bị bao vây, trong vòng một đêm, máu chảy thành sông, Độc điện trên vạn năm cơ nghiệp, cứ như vậy hủy hoại chỉ trong chốc lát...” Nắm đấm xiết chặt, Hứa Du vẻ mặt tăng thấu đỏ."),t("br"),t("br"),n._v("“Cái này...” Trương Huyền híp mắt lại."),t("br"),t("br"),n._v("Độc điện, hắn đi qua, không chỉ có độc sư, còn có rất nhiều bình dân bách tính, những người này không có luyện độc thiên phú, cũng không thế nào tu luyện... Không nghĩ tới thế mà cũng không lưu tình!"),t("br"),t("br"),n._v("Độc sư chức nghiệp mặc dù làm người ta hoảng sợ, nhưng những này độc sư, một mực co đầu rút cổ tại bản thân hòn đảo bên trong, tự ngu tự nhạc, chưa hề ra ngoài hại người, như vậy đều hạ thủ được, vị này Trung Thanh Vương, cũng quá tàn nhẫn đi!"),t("br"),t("br"),n._v("Vốn còn nghĩ, thiết kế hắn một hồi, có thể hay không quá mức, hiện tại xem ra, quả thực quá nhẹ!"),t("br"),t("br"),n._v("Loại người này, giết chết một trăm lần, cũng không tính là quá mức."),t("br"),t("br"),n._v("“Vị này Trung Thanh Vương, đem trọn cái Độc điện tàn sát sạch sẽ, sau đó đem chúng ta những này đạt tới ngũ tinh cấp bậc trở lên độc sư, toàn bộ tập trung lại, khóa lại tu vi, giam giữ ở đây, mỗi ngày cực hình... Liền là muốn cho chúng ta vì hắn đại lượng phối chế độc dược!”"),t("br"),t("br"),n._v("Nói đến vị này đệ nhất vương gia, Hứa Du cắn răng, trong mắt nồng đậm hận ý, tựa như muốn đem người xé rách."),t("br"),t("br"),n._v("“Đại lượng?”"),t("br"),t("br"),n._v("Trương Huyền cau mày: “Thuốc gì?”"),t("br"),t("br"),n._v("Vị này Trung Thanh Vương, tại Thanh Nguyên đế quốc, dưới một người, trên vạn người, thực lực mạnh mẽ không nói, quyền lợi cũng lớn không có giới hạn... Loại người này, nếu như chỉ là đơn thuần độc chết chính đàn đối thủ, không đến mức đại lượng phối chế dược vật a?"),t("br"),t("br"),n._v("“Là Thanh Phong tán!”"),t("br"),t("br"),n._v("Hứa Du nói."),t("br"),t("br"),n._v("“Cấp năm độc dược, Thanh Phong tán?” Trương Huyền sững sờ: “Loại độc dược này, vô sắc vô vị, có thể nương theo gió mát bốn phía phất phới, chỉ đối Thánh vực trở xuống người hữu dụng, hấp thụ về sau, trong thời gian ngắn, sẽ gân cốt rã rời không có khí lực... Vị này Trung Thanh Vương, phải phối chế bao nhiêu?”"),t("br"),t("br"),n._v("Thanh Phong tán không tính là lợi hại kịch độc, nhưng có thể rộng rãi vì phổ biến, chỉ cần ngửi được liền sẽ trúng độc, khó lòng phòng bị."),t("br"),t("br"),n._v("“Mười tấn!”"),t("br"),t("br"),n._v("Hứa Du nói."),t("br"),t("br"),n._v("“Mười tấn?” Trương Huyền lông mày nhảy một cái."),t("br"),t("br"),n._v("Bình thường Thanh Phong tán, một cái bình để vào trong gió, liền có thể tuỳ tiện độc choáng mấy trăm người, mười tấn... Chỉ sợ có thể độc chết mấy chục triệu người đi!"),t("br"),t("br"),n._v("Cái tên này muốn nhiều như vậy làm gì?"),t("br"),t("br"),n._v("“Hắn là chúng ta Độc điện cừu nhân, cùng hắn không đội trời chung, làm sao có thể vì hắn làm việc... Cho nên, một mực không đáp ứng...” Nhìn ra Trương Huyền lo lắng, Hứa Du giải thích một câu, đồng thời có chút kỳ quái nhìn qua: “Cháu... Mạnh tiền bối, làm sao biết chúng ta tại đây? Ngươi cứ như vậy đi vào, sẽ không bị phát hiện đi!”"),t("br"),t("br"),n._v("Bọn họ như bây giờ, chết không có gì đáng tiếc, nhưng mà nguyên nhân quan trọng này dính líu trước mắt vị này, liền thẹn với Tiên Tổ."),t("br"),t("br"),n._v("“Trung Thanh Vương đã bị ta đánh thành trọng thương, hơn nữa bị Danh Sư đường người chộp tới tra hỏi, ta hiện tại tới, liền là chuyên môn cứu các ngươi ra ngoài!”"),t("br"),t("br"),n._v("Trương Huyền nói."),t("br"),t("br"),n._v("“Đánh thành trọng thương?”"),t("br"),t("br"),n._v("“Cứu chúng ta ra ngoài?”"),t("br"),t("br"),n._v("Mọi người nguyên một đám hai mặt nhìn nhau, khó tin."),t("br"),t("br"),n._v("Vốn cho rằng bị bắt được nơi này, hẳn phải chết không nghi ngờ, nằm mơ đều không có nghĩ đến, vị này hộ tống tổ sư tiền bối, lại trở về, còn thay bọn họ báo thù, tuyết hận."),t("br"),t("br"),n._v("“Ừm, có điều, các ngươi thân phận bây giờ, cho dù ra ngoài, cũng tương đối xấu hổ... Ta muốn cho các ngươi đổi lại thân phận, không biết, có nguyện ý hay không!” Trương Huyền nói."),t("br"),t("br"),n._v("“Đổi thân phận?” Hứa Du cau mày: “Cái gì?”"),t("br"),t("br"),n._v("Những người khác cũng đồng loạt nhìn qua."),t("br"),t("br"),n._v("“Chiến sư!” Trương Huyền nói."),t("br"),t("br"),t("br"),t("br")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("a",{attrs:{href:"./chap-1161"}},[n._v("Chương trước")]),n._v("  |  "),t("a",{attrs:{href:"./chap-1163"}},[n._v("Chương sau")])])])}),[],!1,null,null,null);h.default=c.exports}}]);