(window.webpackJsonp=window.webpackJsonp||[]).push([[1960],{2316:function(n,h,t){"use strict";t.r(h);var g=t(44),c=Object(g.a)({},(function(){var n=this,h=n.$createElement,t=n._self._c||h;return t("ContentSlotsDistributor",{attrs:{"slot-key":n.$parent.slotKey}},[t("h1",{attrs:{id:"chuong-687-ta-hoc-xong"}},[t("a",{staticClass:"header-anchor",attrs:{href:"#chuong-687-ta-hoc-xong"}},[n._v("#")]),n._v(" Chương 687: Ta học xong")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("br"),t("br"),n._v("Chương 687: Ta học xong"),t("br"),t("br"),n._v("Bình thường thuần thú sư thuần phục thú sủng, đều cần tốn hao thời gian mấy năm, tới thổ lộ tâm tình, vô tư trả giá, không có khả năng giống như Trương Huyền như thế, đánh một trận là được."),t("br"),t("br"),n._v("Do đó, thuần thú sư cùng thú sủng tình cảm mười phần thâm hậu, thậm chí thời gian lâu dài, còn muốn cùng tu luyện, huyết mạch cùng dung."),t("br"),t("br"),n._v("Đầu này Long Nham thú, bồi vị trưởng lão này không biết dài bao nhiêu thời gian, tại cộng thêm vì hắn mà chết, trong lòng rất hiển nhiên đã sớm đem hắn trở thành thân mật nhất đồng bọn, coi như cực khổ nữa, cũng muốn xong thành đối phương nguyện vọng."),t("br"),t("br"),n._v("“Đầu kia... Tử Dực Thiên Hùng thú, không phải Thú đường?”"),t("br"),t("br"),n._v("Trương Huyền lông mày nhăn lại."),t("br"),t("br"),n._v("Là Thú đường linh thú, cơ bản đều có người thuần phục, coi như không biết thú ngữ, chủ nhân cùng thú sủng tầm đó, cũng có biện pháp câu thông cùng giao lưu."),t("br"),t("br"),n._v("Hiện tại hắn muốn học thú ngữ, từ đầu này đại gia hỏa miệng bên trong hiểu được Long Nham thú trước khi chết di ngôn, người sau tự nhiên là không có bị người thuần phục, nếu không trực tiếp hỏi thuần thú sư là được, không cần thiết phiền toái như vậy."),t("br"),t("br"),n._v("“Thành niên Tử Dực Thiên Hùng thú, mặc dù cũng là Hóa Phàm bát trọng, nhưng đầu này, nhưng đạt đến nửa bước cửu trọng cảnh giới, chúng ta Thú đường căn bản không có người có thể thuần phục...” Lão giả lắc đầu."),t("br"),t("br"),n._v("“Nửa bước cửu trọng?”"),t("br"),t("br"),n._v("“Ừm, mặc dù chỉ là nửa bước, nhưng bằng mượn huyết mạch cùng lực lượng, coi như một chút chân chính Hóa Phàm cửu trọng cường giả, cũng không phải là đối thủ, hơn nữa, bởi vì Long Nham thú chết đi, để nó đối với nhân loại ôm lấy cực lớn thành kiến, căn bản là không có biện pháp thuần phục...”"),t("br"),t("br"),n._v("Lão giả nói."),t("br"),t("br"),n._v("“Nó cùng Long Nham thú là bạn?” Trương Huyền nghi hoặc."),t("br"),t("br"),n._v("“Đúng... Tình lữ!” Lão giả chần chờ một chút, nói."),t("br"),t("br"),n._v("“Tình lữ?” Trương Huyền giật mình."),t("br"),t("br"),n._v("Một chút cường đại linh thú, bình thường số lượng so sánh một loại, muốn tìm được cùng chủng tộc rất khó, loại tình huống này, sẽ cùng một loại khác cường đại sinh mệnh kết thành bạn đời, đương nhiên, loại này bạn đời, có thể hay không kéo dài huyết mạch, liền không nhất định."),t("br"),t("br"),n._v("Chỉ là trên tinh thần làm bạn, cùng một chỗ chiến đấu, cùng một chỗ sinh tồn."),t("br"),t("br"),n._v("Thậm chí trong nhân loại cũng có một chút đạo lữ, cũng là như vậy."),t("br"),t("br"),n._v("Tu vi đến cảnh giới cao thâm, giao lưu tinh thần mới là trọng yếu nhất, phồn - diễn - hậu - đại, nhục - thể - hoan - du, đã không được coi trọng."),t("br"),t("br"),n._v("Tử Dực Thiên Hùng thú đã cùng Long Nham thú là tình lữ, tự nhiên đối với nó mười phần chú trọng."),t("br"),t("br"),n._v("Tận mắt thấy bạn đời chết, tự nhiên đối lão giả vị chủ nhân này, ghi hận trong lòng, có thể nói cho hắn biết mới là lạ."),t("br"),t("br"),n._v("Mà lão giả một lòng học tập thú ngữ, chính là muốn cùng chi giao lưu, hóa giải khúc mắc."),t("br"),t("br"),n._v("“Lão sư, đã Tử Dực Thiên Hùng thú là lão sư thú sủng bạn đời, cái kia vì cái gì... Nó ba ngày hai đầu tới làm loạn, đánh lén Thú đường?” Hàn Xung tựa hồ đối với lấy đầu linh thú cũng có hiểu biết, nhịn không được nói."),t("br"),t("br"),n._v("“Đánh lén?” Lạc Thất Thất nhìn sang."),t("br"),t("br"),n._v("“Đúng vậy a, đầu này đại gia hỏa, mỗi tháng đều muốn đến mấy lần, đánh lén Thú đường, ăn vào một lạng đầu linh thú, man thú. Làm tiếng oán hờn khắp nơi, mỗi lần Thú đường đều tốn hao cực lớn đánh đổi muốn bắt lấy, nhưng thực lực của nó cùng tốc độ thực sự quá mạnh quá nhanh, căn bản bắt không được...”"),t("br"),t("br"),n._v("Hàn Xung giải thích."),t("br"),t("br"),n._v("Hắn đến Huyễn Vũ đế quốc Thú đường thời gian mặc dù không dài, nhưng rất nhiều chuyện vẫn là nhìn ở trong mắt."),t("br"),t("br"),n._v("Một tháng, đầu này Tử Dực Thiên Hùng thú, đều muốn đánh lén Thú đường mấy lần, ăn vụng linh thú, man thú, để không ít người tràn đầy buồn bực, có thể nhận hạn chế thực lực của đối phương, coi như Thú đường lại bất đắc dĩ, cũng không có cách nào."),t("br"),t("br"),n._v("Vốn nghĩ cái tên này là vô chủ linh thú, không ưa linh thú bị nhân loại chi phối, hiện tại xem ra, chỉ sợ là có ý định trả thù."),t("br"),t("br"),n._v("“Nó là không cam lòng Long Nham thú bị giết, cho nên cố ý đến Thú đường làm loạn, những năm này ta học thú ngữ, cũng là muốn hướng nó hỏi một chút, đến cùng muốn làm gì!”"),t("br"),t("br"),n._v("Trưởng lão lắc đầu, nói xong nhịn không được nhìn về phía trước mắt Trương Huyền: “Ngươi đã liền Chân Long thanh âm đều biết, Thượng Cổ thú ngữ có phải hay không cũng biết một ít?”"),t("br"),t("br"),n._v("Chân Long thanh âm, liền xem như Chân Long chi thân, cũng cần cùng người đặc biệt học tập, đối phương liền Thiên Long Bát Âm đều có thể giáo, có thể hay không liền Thượng Cổ thú ngữ một khối dạy?"),t("br"),t("br"),n._v("Mặc dù mới vừa mới đối phương nói chỉ nhìn qua một điểm, có phải hay không là khiêm tốn ngữ điệu?"),t("br"),t("br"),n._v("Trương Huyền gãi gãi đầu: “Thượng Cổ thú ngữ... Ta xác thực không biết!”"),t("br"),t("br"),n._v("Lúc trước hắn là học qua vài câu, có điều, nói linh thú gào thét không dứt, suýt chút nữa nổi điên, bản thân cũng không biết ý gì, cùng không biết, không có gì khác nhau."),t("br"),t("br"),n._v("“Không biết?” Nghe đối phương xác nhận, trưởng lão tràn đầy ánh mắt mong đợi, ảm đạm xuống, hỏi lần nữa: “Cái kia... Thiên Long Bát Âm đâu?”"),t("br"),t("br"),n._v("Coi như Tử Dực Thiên Hùng thú không chứa Long tộc huyết mạch, nếu như trước mắt vị này thật có thể nói ra Long ngữ, cũng có khả năng tới câu thông, để hắn ngoan ngoãn đem chuyện năm đó nói ra, thậm chí còn có thể có thể để cho nói ra, vì sao như thế căm thù nhân loại, nhất định phải cố ý tại Thú đường làm loạn."),t("br"),t("br"),n._v("“Ta... Chỉ biết một cái âm tiết!” Trương Huyền càng thêm thật không tiện."),t("br"),t("br"),n._v("Đối phương còn tưởng rằng hắn thực biết Long ngữ, trên thực tế, hắn liền chỉ học được một cái âm tiết, bình thường nhàn rỗi không chuyện gì, hù dọa một chút ẩn chứa Long tộc huyết mạch linh thú ngược lại cũng thôi."),t("br"),t("br"),n._v("Để hắn nói ra hoàn chỉnh Long ngữ, khẳng định là không thể nào."),t("br"),t("br"),n._v("“Một cái âm tiết?” Trưởng lão nhoáng một cái."),t("br"),t("br"),n._v("Nguyên bản nghe được thanh niên nói ra Long ngữ còn tưởng rằng tìm được hi vọng, hiện tại xem ra, cùng trước kia không có gì khác biệt."),t("br"),t("br"),n._v("“Vậy quên đi...”"),t("br"),t("br"),n._v("Lắc đầu, trưởng lão bất đắc dĩ."),t("br"),t("br"),n._v("Những năm này sưu tập không ít sách vở, chuyên môn học tập Thượng Cổ thú ngữ, nhưng loại ngôn ngữ này thực sự quá khó khăn, liền xem như hắn, không có người giáo dục, không có lão sư tình huống dưới, thật giả khó phân biệt, cũng không có nghiên cứu ra vài câu."),t("br"),t("br"),n._v("Còn tưởng rằng Hàn Xung như thế đề cử, có thể từ đối phương miệng bên trong học đến một chút, không nghĩ tới, đồng dạng không biết."),t("br"),t("br"),n._v("“Kỳ thật... Cũng không cần nản chí, trưởng lão đã nghiên cứu nhiều năm như vậy Thượng Cổ thú ngữ, nhất định sưu tập không ít sách vở, ta có thể hay không mượn đọc một phen? Vạn nhất có thể có chỗ tâm đắc, cũng chưa biết chừng!”"),t("br"),t("br"),n._v("Trương Huyền nhìn qua."),t("br"),t("br"),n._v("“Mượn đọc? Tâm đắc?” Trưởng lão cười khổ: “Ngươi ý tứ, hiện tại học tập?”"),t("br"),n._v("“Ừm!” Trương Huyền gật đầu."),t("br"),t("br"),n._v("“Ngươi cũng đã biết Thượng Cổ thú ngữ độ khó?”"),t("br"),t("br"),n._v("Thấy đối phương quả nhiên có quyết định này, lão giả nhịn không được lắc đầu: “Thú ngữ thất truyền, hiện tại lưu truyền xuống ghi chép, rất nhiều đều là lời chi không rõ, cũng không ít chỉ tốt ở bề ngoài, không nói trước có thể hay không phát âm, coi như có thể phát ra tới, ngươi biết có ý tứ gì? Ta nghiên cứu không dưới năm mười năm, đến bây giờ cũng không có chút nào tiến triển, bây giờ nhìn...”"),t("br"),t("br"),n._v("Hắn lời nói mặc dù chưa nói xong, ý tứ nhưng hết sức rõ ràng, cảm giác đối phương quả thực chính là trò đùa."),t("br"),t("br"),n._v("Hắn nghiên cứu cả một đời, đều cái gì cũng đều không hiểu, ngươi xem một chút thư liền nghĩ học tập, Thượng Cổ thú ngữ nếu như đơn giản như vậy, cũng không trở thành thất truyền, để vô số thuần thú sư muốn khôi phục mà không thể được."),t("br"),t("br"),n._v("“Ta đối Thượng Cổ thú ngữ cũng rất có hứng thú, chỉ là muốn tùy tiện nhìn xem, nếu như có thể học tập, đương nhiên tốt nhất, học tập không được, cũng không có biện pháp...”"),t("br"),t("br"),n._v("Biết đối phương lo lắng cùng ý nghĩ, Trương Huyền nói."),t("br"),t("br"),n._v("“Vậy thì tốt, ngươi đi vào đi, ta cái này nhà tranh bên trong, tất cả đều là liên quan tới Thượng Cổ thú ngữ thư tịch, những năm này, Huyễn Vũ đế quốc chỉ cần có quan hệ thú ngữ ghi lại đồ vật, hầu như đều bị ta sưu tập...”"),t("br"),t("br"),n._v("Trưởng lão có chút mất hết cả hứng, khoát tay áo."),t("br"),t("br"),n._v("Những sách vở này mặc dù là hắn tốn hao tâm huyết sưu tập, nhưng cũng không phải cái gì cực kỳ trọng yếu đồ vật, đối phương đã muốn nhìn, cũng không có gì ghê gớm."),t("br"),t("br"),n._v("“Đa tạ!”"),t("br"),t("br"),n._v("Trương Huyền liền ôm quyền, đẩy cửa phòng ra đi vào."),t("br"),t("br"),n._v("Thấy thân ảnh của hắn biến mất ở trước mắt, trưởng lão nhịn không được lắc đầu, khắp khuôn mặt là thất vọng: “Thượng Cổ thú ngữ cho dù có người truyền thụ, không có mấy năm cũng khó có thể học được, đơn giản như vậy có thể học được, cái này năm mươi năm, ta chẳng phải sống vô dụng rồi?”"),t("br"),t("br"),n._v("Vốn cho rằng đối phương có thể nói ra Chân Long thanh âm, tâm tính tất nhiên trầm ổn, không nghĩ tới còn như thế táo bạo."),t("br"),t("br"),n._v("Thượng Cổ thú ngữ, liên quan Thú đường một đại cơ mật, dễ dàng như vậy học được, bọn hắn những này thuần thú sư, cũng không trở thành thuần phục linh thú, bị động như thế."),t("br"),t("br"),n._v("“Lão sư... Luôn luôn trầm ổn, đã đi xem sách, khẳng định có ý nghĩ của mình, có lẽ có thể học tập một hai!”"),t("br"),t("br"),n._v("Nghe đến lão giả trong lời nói mang theo chất vấn hương vị, Lạc Thất Thất nhịn không được nói."),t("br"),t("br"),n._v("“Lão sư? Ngươi là học sinh của hắn?”"),t("br"),t("br"),n._v("Nghe được nữ hài xưng hô, trưởng lão sửng sốt một chút, nhịn không được nhìn qua."),t("br"),t("br"),n._v("Trước mắt cô bé này vô luận tu vi hay là khí chất, đều tài năng xuất chúng, cùng thế hệ bên trong được cho người nổi bật, làm sao lại nhận một cái so với chính mình tuổi nhỏ nhân vi sư?"),t("br"),t("br"),n._v("“Vâng!” Lạc Thất Thất gật đầu."),t("br"),t("br"),n._v("“Vừa rồi ta nghe ngươi xưng hô Trương sư, mới vừa đi vào vị này chẳng lẽ là vị danh sư?” Gặp nàng thừa nhận, lão giả lúc này mới nhớ tới Hàn Xung."),t("br"),t("br"),n._v("“Trương sư là tứ tinh danh sư, vạn quốc liên minh danh sư thi đấu quán quân, lần này đi Hồng Viễn đế quốc, hẳn là đi danh sư học viện học tập!” Hàn Xung suy nghĩ một chút, nói."),t("br"),t("br"),n._v("Danh sư thi đấu còn không có tiến hành, hắn liền rời đi Vạn Quốc thành, rất nhiều chuyện biết đến không nhiều, nhưng Trương Huyền lấy được quán quân, muốn đi học viện sự tình, vẫn là đã nghe qua một chút tin tức."),t("br"),t("br"),n._v("“Danh sư thi đấu quán quân?”"),t("br"),t("br"),n._v("Trưởng lão sững sờ."),t("br"),t("br"),n._v("“Không chỉ có như thế, Trương sư còn là một vị tứ tinh thuần thú sư, cùng tứ tinh y đạo đại sư!” Hàn Xung vội vàng nói."),t("br"),t("br"),n._v("“Tứ tinh thuần thú sư?” Trưởng lão nhướng mày: “Nếu là tứ tinh thuần thú sư, hẳn phải biết Thượng Cổ thú ngữ độ khó, không thể như thế lỗ mãng đi...”"),t("br"),t("br"),n._v("Nếu như đối phương chỉ là danh sư, không rõ ràng thuần thú, ngược lại cũng thôi."),t("br"),t("br"),n._v("Tứ tinh thuần thú sư, đã coi như là thuần thú bên trong người nổi bật, làm sao có thể không biết Thượng Cổ thú ngữ độ khó?"),t("br"),t("br"),n._v("Biết, lại làm sao có thể, nói ra muốn học tập một chút, như thế không có trình độ?"),t("br"),t("br"),n._v("“Chờ hắn đi ra, cho ta biết một tiếng, ta mang các ngươi đi tìm đường chủ!”"),t("br"),t("br"),n._v("Lắc đầu, không đang xoắn xuýt vấn đề này, trưởng lão khoát tay áo."),t("br"),t("br"),n._v("“Tìm đường chủ? Lão sư là muốn giúp đỡ tìm linh thú phi hành?” Hàn Xung ánh mắt sáng lên."),t("br"),t("br"),n._v("“Ừm, nếu là tứ tinh danh sư, lại là thuần thú sư, có thể trợ giúp tự nhiên không thể chối từ, ta nhớ được đường chủ còn có một đầu Hóa Phàm thất trọng linh thú phi hành, mặc dù chưa từng cho người mượn, nhưng ta tự mình đi nói lời, có lẽ có thể cho chút mặt mũi!”"),t("br"),t("br"),n._v("Trưởng lão nói."),t("br"),t("br"),n._v("“Đa tạ lão sư...”"),t("br"),t("br"),n._v("Thấy lão sư đáp ứng hỗ trợ, Hàn Xung gấp vội vàng gật đầu, chính muốn tiếp tục nói chuyện, liền nghe cửa phòng “Kẹt kẹt!” Một tiếng, thanh niên đi ra."),t("br"),t("br"),n._v("“Ách?”"),t("br"),t("br"),n._v("Mọi người tất cả đều sững sờ, trưởng lão cũng nhíu mày."),t("br"),t("br"),n._v("Không phải muốn đi vào đọc sách, học tập Thượng Cổ thú ngữ ư? Lúc này mới đi vào không đến mười phút đồng hồ, làm sao lại đi ra?"),t("br"),t("br"),n._v("“Có phải hay không biết Thượng Cổ thú ngữ không có đơn giản như vậy? Không nhớ lại học?”"),t("br"),t("br"),n._v("Lão giả nhịn không được nói."),t("br"),t("br"),n._v("Khẳng định là cái tên này sau khi đi vào, nhìn thấy hạo như biển khói thư tịch, sinh lòng rút lui, nếu không, làm sao ngắn như vậy thời gian, liền đi ra?"),t("br"),t("br"),n._v("“Quả thực rất khó, có điều, không phải là không muốn lại học, mà là...”"),t("br"),t("br"),n._v("Trương Huyền xoắn xuýt một chút, cuối cùng nói."),t("br"),t("br"),n._v("“... Ta học xong!”"),t("br"),t("br"),n._v("Convert by: ThấtDạ"),t("br"),t("br"),t("br"),t("br")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("a",{attrs:{href:"./chap-695"}},[n._v("Chương trước")]),n._v("  |  "),t("a",{attrs:{href:"./chap-697"}},[n._v("Chương sau")])])])}),[],!1,null,null,null);h.default=c.exports}}]);