(window.webpackJsonp=window.webpackJsonp||[]).push([[1961],{2315:function(n,h,t){"use strict";t.r(h);var g=t(44),i=Object(g.a)({},(function(){var n=this,h=n.$createElement,t=n._self._c||h;return t("ContentSlotsDistributor",{attrs:{"slot-key":n.$parent.slotKey}},[t("h1",{attrs:{id:"chuong-688-tan-đuong-chu"}},[t("a",{staticClass:"header-anchor",attrs:{href:"#chuong-688-tan-đuong-chu"}},[n._v("#")]),n._v(" Chương 688: Tần đường chủ")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("br"),t("br"),n._v("Chương 688: Tần đường chủ"),t("br"),t("br"),n._v("“Học xong?”"),t("br"),t("br"),n._v("Mọi người tất cả đều ngẩn ngơ."),t("br"),t("br"),n._v("Nhất là trưởng lão, sắc mặt tái xanh, có chút khó coi."),t("br"),t("br"),n._v("Người khác không biết trong phòng có bao nhiêu thư tịch, hắn nhưng là nhất thanh nhị sở."),t("br"),t("br"),n._v("Năm mươi năm đến mượn nhờ công hội lực lượng khắp nơi sưu tập, liên quan tới thú ngữ ghi chép, bản độc nhất, cổ tịch... Không dưới mấy chục vạn bản, liền xem như hắn, cũng là hao tốn tròn mười năm mới xem xong một lần, đối phương đi vào không đến mười phút đồng hồ liền đi ra, thế mà nói thẳng... Học xong!"),t("br"),t("br"),n._v("Coi như nói dối cũng đáng tin hơn một điểm đi!"),t("br"),t("br"),n._v("“Ngươi cái gọi là học xong là có ý gì?” Thực sự nhịn không được, lão giả khẽ nói."),t("br"),t("br"),n._v("“A, chính là... Không sai biệt lắm học được, cơ bản giao lưu không có vấn đề quá lớn...”"),t("br"),t("br"),n._v("Trương Huyền nói."),t("br"),t("br"),n._v("Vừa rồi đi vào phòng liền bắt đầu chạy băng băng sưu tập thư tịch, vài phút công phu liền đem mấy chục vạn bản thư tịch thu vào trong óc, sau đó thông qua Thiên Đạo thư viện, tìm ra đúng đắn nội dung."),t("br"),t("br"),n._v("Một tìm phía dưới, lại vẫn thật tìm được không ít."),t("br"),t("br"),n._v("Những sách vở này, mặc dù có không ít ước đoán, càng có thật nhiều lung tung viết, nhưng số lượng nhiều, tích cát thành tháp, góp ít thành nhiều, đúng đắn câu nói, cũng có thể hội tụ không ít."),t("br"),t("br"),n._v("Tròn một cái thư tịch, phía trên hàm cái Thượng Cổ thú ngữ bên trong tiếng thông dụng câu, cùng phát ra tiếng phương pháp, học được những này, ứng phó khó khăn đối thoại rất khó, nhưng đồng dạng giao lưu, đã không thành vấn đề."),t("br"),t("br"),n._v("“Cơ bản giao lưu... Còn không có vấn đề quá lớn?” Nghe đối phương càng nói càng mơ hồ, lão giả sắc mặt tái xanh: “Ăn nói bừa bãi!”"),t("br"),t("br"),n._v("Hắn học được năm mươi năm đều không có học biết cái gì, mới mở miệng, để lông đỏ hamster nôn đến chết đi sống lại, ngươi nói ngươi đi vào một hồi liền học được?"),t("br"),t("br"),n._v("Thật lời như vậy, ta nhiều năm như vậy nghiên cứu học tập, là lấy tới trên thân chó, vẫn là lấy tới heo trên thân?"),t("br"),t("br"),n._v("Coi như nói dối, cũng phải vung đáng tin cậy một chút đi!"),t("br"),t("br"),n._v("“Ta...”"),t("br"),t("br"),n._v("Biết nói thật, khẳng định sẽ bị nghi ngờ, Trương Huyền đang muốn giải thích một chút, chỉ thấy một thanh niên vội vã chạy tới."),t("br"),t("br"),n._v("“Trưởng lão, không xong, đầu kia... Tử Dực Thiên Hùng thú lại tới!”"),t("br"),t("br"),n._v("“Lại tới?”"),t("br"),t("br"),n._v("Lão giả sầm mặt lại: “Phía trước dẫn đường!”"),t("br"),t("br"),n._v("“Vâng!” Thanh niên vội vàng đi ra ngoài, lão giả theo sát mà lên."),t("br"),t("br"),n._v("“Chúng ta cũng qua xem một chút đi!” Thấy hắn vội vã rời khỏi, Trương Huyền nhìn Lạc Thất Thất đám người một cái, cũng đi theo."),t("br"),t("br"),n._v("Đến chính là tìm linh thú phi hành, đã đầu này Tử Dực Thiên Hùng thú là nửa bước cửu trọng, có thể thuần phục lời nói, tuyệt đối là một sự giúp đỡ lớn, lại đi Hồng Viễn đế quốc, liền sẽ mau hơn không ít."),t("br"),t("br"),n._v("“Ừm!” Hàn Xung cũng biết chuyện nghiêm trọng, theo sát đi qua."),t("br"),t("br"),n._v("Đi trên đường, cùng hắn trò chuyện, Trương Huyền cũng biết, hắn vị lão sư này, họ Tiền, là Thú đường tư cách tương đối sâu trưởng lão một trong, địa vị tôn sùng."),t("br"),t("br"),n._v("Bởi vì Tử Dực Thiên Hùng thú cùng hắn có quan hệ, mỗi lần xuất hiện, đều sẽ chạy tới xử lý, Hàn Xung đến Thú đường thời gian mặc dù không dài, lại đều kinh nghiệm nhiều lần."),t("br"),t("br"),n._v("Cho nên đối đầu này đại gia hỏa, trí nhớ càng sâu."),t("br"),t("br"),n._v("“Cái tên này, vừa đến không phải kiếm phá hư chính là giết chết cái khác linh thú, làm không ít khách hàng đều tiếng oán hờn khắp nơi!” Nói đến đây đầu Tử Dực Thiên Hùng thú, ngay cả Hàn Xung, đều tràn đầy buồn bực."),t("br"),t("br"),n._v(" Đả thương người ngược lại cũng thôi, mấu chốt nhất là, đem không ít linh thú làm bị thương, có ít người xếp hàng thật nhiều ngày mới không dễ dàng xếp tới linh thú, bởi vì tổn thương, không cách nào đi lại, làm cho cả Thú đường tín dự đều thấp xuống không ít."),t("br"),t("br"),n._v("Thú đường cũng không ít bố bẫy rập, nhưng cái tên này mỗi lần cũng giống như sớm biết đồng dạng, lại thêm thực lực mạnh mẽ, coi như chân chính đã rơi vào bao vây, đồng dạng có thể thuận lợi đào thoát, thậm chí lần nữa trở về, càng thêm làm tầm trọng thêm."),t("br"),t("br"),n._v("Đã từng báo cáo qua tổng bộ, Hồng Viễn đế quốc Thú đường cũng phái lục tinh thuần thú sư đến đây, kết quả cái tên này lại biết nguy hiểm, liên tiếp mấy tháng không có xuất hiện, đợi vị này thuần thú sư vừa rời đi, lập tức lại lao đến."),t("br"),t("br"),n._v("Liên tục mấy lần, Thú đường tất cả mọi người coi như bất đắc dĩ, cũng không có biện pháp, đành phải tùy ý đối phương làm loạn, chỉ là mỗi lần, đều để vị này Tiền trưởng lão đi xử lý."),t("br"),t("br"),n._v("Bất quá, nhắc tới cũng kỳ quái, chỉ cần Tiền trưởng lão đi qua, coi như cái tên này hung ác hơn nữa, cũng biết xoay người rời đi, không dám đả thương hắn chút nào."),t("br"),t("br"),n._v("Trước đó một mực không có hiểu rõ, còn tưởng rằng là bản thân vị lão sư này, thuần thú năng lực mạnh mẽ, làm cho đối phương không dám động thủ, hiện tại xem ra, chỉ sợ cùng chết đi con rồng kia nham thú có quan hệ."),t("br"),t("br"),n._v("Đoán không sai, hẳn là cái này Tử Dực Thiên Hùng thú đáp ứng Long Nham thú cái gì, mặc dù đối lão sư bất mãn, nhưng cũng không dám làm ra tổn thương hại hắn sự tình."),t("br"),t("br"),n._v("“Ngay ở phía trước!”"),t("br"),t("br"),n._v("Hàn huyên một hồi, Hàn Xung lông mày giương lên, vội vàng xông về phía trước, Trương Huyền đám người theo sát trên đó, lập tức liền thấy một cái cự đại màu tím linh thú, chính xoay quanh trên không trung, thô to móng vuốt bên trên nắm lấy một đầu Hóa Phàm tam trọng tiểu gia hỏa."),t("br"),t("br"),n._v("Mà trước đó tới Tiền trưởng lão đang đứng tại cách đó không xa, vẻ mặt sốt ruột."),t("br"),t("br"),n._v("Tiền trưởng lão cách đó không xa, thì là bảy, tám cái lão giả râu bạc trắng, ở giữa nhất một cái, cùng lúc trước đã gặp Tần Chung dung mạo có chút tương tự, có lẽ chính là vị kia Tần đường chủ."),t("br"),t("br"),n._v("“Không có nghĩ tới tên này nhanh như vậy liền trở lại...”"),t("br"),t("br"),n._v("Rất nhiều lão giả râu bạc trắng sau lưng, Trương Huyền lần nữa nhìn thấy một người quen."),t("br"),t("br"),n._v("Chính là cái kia “Không biết bay” Tần Chung."),t("br"),t("br"),n._v("Vốn cho rằng cái tên này không có linh thú, sẽ qua một đoạn thời gian mới có thể trở về, không nghĩ tới, đã đến Thú đường."),t("br"),t("br"),n._v("“Hẳn là đầu kia Long Dực thú đi đón tới...”"),t("br"),t("br"),n._v("Lạc Thất Thất thấp giọng."),t("br"),t("br"),n._v("Trương Huyền gật đầu."),t("br"),t("br"),n._v("Bọn hắn đến Thú đường liền đem đầu kia Long Dực thú thả, cái tên này biết chủ nhân còn ngã tại trong hố hôn mê bất tỉnh, tự nhiên muốn đi cứu người."),t("br"),t("br"),n._v("Thú đường khoảng cách cái này không xa, một cái qua lại, tự nhiên cũng là tốn hao không được bao dài thời gian."),t("br"),t("br"),n._v("Lúc này Tần Chung toàn thân u cùng bánh chưng giống như, mặc dù bị thương nặng, nhưng quả thực không có sinh mệnh trở ngại."),t("br"),t("br"),n._v("Cặp mắt, trừng trừng nhìn về phía không trung Thiên Hùng thú, cũng không chú ý tới thù người tới gần."),t("br"),t("br"),n._v("..."),t("br"),t("br"),n._v("“Nhanh lên đưa nó buông ra, giữa chúng ta cho dù có mâu thuẫn, còn hi vọng không cần liên luỵ Thú đường, nếu không, coi như Long Nham thú sống lại, cũng khẳng định sẽ mất hứng...”"),t("br"),n._v("Phía trước, Tiền trưởng lão nhìn xem không bên trong phi hành Tử Dực Thiên Hùng thú, nắm đấm xiết chặt, nói."),t("br"),t("br"),n._v("“Rống!”"),t("br"),t("br"),n._v("Nghe hắn nhấc lên Long Nham thú, không trung đại gia hỏa nhe răng trợn mắt, không những không có buông tay ra bên trong linh thú, ngược lại móng vuốt nắm càng chặt, xem ra bất cứ lúc nào cũng sẽ đem nó bóp chết."),t("br"),t("br"),n._v("“Tiền trưởng lão, đường chủ, còn xin mau cứu ta linh thú...”"),t("br"),t("br"),n._v("Một cái tứ tinh thuần thú sư tràn đầy gấp gáp nhìn về phía không trung, sắp khóc ra thành tiếng."),t("br"),t("br"),n._v("Rất hiển nhiên, không trung bị bắt đầu này linh thú là hắn thú sủng."),t("br"),t("br"),n._v("Phí hết tâm huyết, thật vất vả thuần phục, một khi bị bóp chết, một phen vất vả đều đem phó mặc."),t("br"),t("br"),n._v("“Lý thú sư, đừng có gấp, ta nhất định sẽ cứu nó...”"),t("br"),t("br"),n._v("Tiền trưởng lão an ủi một câu."),t("br"),t("br"),n._v("Thú sủng bị giết, hắn biết đau khổ, không muốn để cho hậu bối cũng dẫm vào vết xe đổ của mình."),t("br"),t("br"),n._v("“Thiên Hùng thú, chỉ cần ngươi thả nó, thú huyết thạch vẫn là linh thạch, chỉ cần Tiền mỗ người có thể làm đến, sẽ không tiếc!”"),t("br"),t("br"),n._v("Dỗ dành xong đối phương, Tiền trưởng lão lần nữa đối không trung đạo."),t("br"),t("br"),n._v("“Tiền trưởng lão, không thể đáp ứng, những năm này, ngươi tất cả tích súc hầu như đều cho ra ngoài, tiếp tục như vậy nữa, ta sợ chậm trễ tu luyện của ngươi...”"),t("br"),t("br"),n._v("Tần đường chủ nghe nói như thế, nhịn không được cau mày, vội vàng khuyên can."),t("br"),t("br"),n._v("Dựa theo thiên phú, Tiền trưởng lão có lẽ sớm liền có thể đột phá đến Hóa Phàm cửu trọng, đáng tiếc tất cả tích súc đều cho cái tên này, dẫn đến tài nguyên tu luyện thiếu thốn."),t("br"),t("br"),n._v("Ngược lại cái tên này, thường cách một đoạn thời gian, vơ vét một lần, mượn nhờ những tư nguyên này, trước đột phá đến nửa bước cửu trọng."),t("br"),t("br"),n._v("Lại không khuyên can, tiếp tục tẩm bổ đi xuống, cái tên này sẽ càng ngày càng mạnh, đối Thú đường uy hiếp cũng biết lớn hơn."),t("br"),t("br"),n._v("“Là ta thua thiệt Long Nham thú...”"),t("br"),t("br"),n._v("Tiền trưởng lão lắc đầu."),t("br"),t("br"),n._v("“Nhưng mà...” Hôn đường chủ sắc mặt tái xanh."),t("br"),t("br"),n._v("“Không có nhưng mà, nếu như không có Long Nham thú, ta đã sớm chết, có thể kéo dài hơi tàn lâu như vậy, đã là thiên đại ân huệ, đừng nói tích súc, coi như để cho ta lập tức chết, chỉ cần có thể để nó tâm lý dễ chịu, ta cũng nguyện ý!”"),t("br"),t("br"),n._v("Ánh mắt mang theo kiên định, Tiền trưởng lão cổ tay khẽ đảo, trước mắt xuất hiện một đống thú huyết thạch, linh thạch trung phẩm cùng một chút trân quý dược liệu."),t("br"),t("br"),n._v("Số lượng cũng không phải là rất nhiều, xem ra những năm này, vị trưởng lão này, quả thực bị vơ vét không sai biệt lắm."),t("br"),t("br"),n._v("“Ngươi là thua thiệt Long Nham thú, nhưng cũng không thua thiệt đầu này Tử Y Thiên Hùng thú, Tiền trưởng lão, ngươi làm như vậy, thật có chút không ổn!”"),t("br"),t("br"),n._v("Ngay tại Tiền trưởng lão định đem vẻn vẹn có đồ vật cho không trung đầu này đại gia hỏa thời điểm, một cái thanh âm nhàn nhạt vang lên."),t("br"),t("br"),n._v("Nghe được thanh âm, mọi người quay đầu nhìn lại, chỉ thấy một thanh niên đi tới."),t("br"),t("br"),n._v("“Cha, chính là hắn đem ta làm hại thảm như vậy... Ngươi nhất định không thể bỏ qua!”"),t("br"),t("br"),n._v("Không có nghĩ tới tên này lại dám nghênh ngang đi tới gần, Tần Chung tức giận con mắt đều tái rồi."),t("br"),t("br"),n._v("“Hắn?” Tần đường chủ sầm mặt lại, quay đầu nhìn về phía Tiền trưởng lão: “Trưởng lão, vị này ngươi có thể nhận biết?”"),t("br"),t("br"),n._v("“Hắn là Hàn Xung ân nhân, một vị tứ tinh danh sư cùng thuần thú sư...”"),t("br"),t("br"),n._v("Tiền trưởng lão nhướng mày, nhìn về phía đi tới thanh niên, khoát tay áo: “Chuyện này không liên quan gì đến ngươi, nếu như muốn tiếp tục mượn linh thú lời nói, xin lập tức rời đi!”"),t("br"),t("br"),n._v("Đi tới chính là Trương Huyền."),t("br"),t("br"),n._v("Hắn thấy vị này Tiền trưởng lão lấy một mực thỏa hiệp, tràn đầy bất đắc dĩ, liền đi ra."),t("br"),t("br"),n._v("Nghe nói như thế, mới vừa muốn nói gì, sau lưng Ngọc Phi Nhi tiếng âm vang lên."),t("br"),t("br"),n._v("“Nếu như chúng ta không đi đâu?”"),t("br"),t("br"),n._v("“Không đi, linh thú vậy cũng đừng nghĩ lại mượn...”"),t("br"),t("br"),n._v("Hơi vung tay, Tiền trưởng lão đang muốn nói hết lời, chỉ thấy một bên Tần đường chủ, sắc mặt trắng nhợt, bờ môi không ngừng phát run."),t("br"),t("br"),n._v("Đồng thời không ngừng đối với hắn nháy mắt: “Khụ khụ, Tiền trưởng lão, ngươi trước không vội nói...”"),t("br"),t("br"),n._v("“Làm sao vậy?”"),t("br"),t("br"),n._v("Thấy đường chủ kỳ quái như thế, Tiền trưởng lão tràn đầy nghi hoặc, còn không hỏi ra đến, chỉ thấy đi theo Trương sư sau lưng tùy tùng đồng dạng nữ hài, đã đi tới gần, nhìn về phía Tần đường chủ, cười nhạt một tiếng."),t("br"),t("br"),n._v("“Nếu như ta nhớ không lầm, ngươi gọi Tần Chiêu đúng không? Nguyên lai ngươi là nơi này Thú đường đường chủ!”"),t("br"),t("br"),n._v("“Tần Chiêu gặp qua Ngọc Phi Nhi công chúa!”"),t("br"),t("br"),n._v("Tần đường chủ sắc mặt trắng nhợt, vội vàng ôm quyền."),t("br"),t("br"),n._v("“Ngươi còn nhận ra ta?” Hừ một tiếng, Ngọc Phi Nhi hai tay chắp sau lưng, không giận tự uy, mang theo một cái công chúa uy nghiêm."),t("br"),t("br"),n._v("“Tại hạ... Cùng ân sư đi hoàng cung, may mắn gặp qua công chúa một mặt...”"),t("br"),t("br"),n._v("Tần Chiêu đường chủ vội vàng ôm quyền, nói còn chưa dứt lời, liền nghe đến nơi xa trọng thương con trai tiếng gầm gừ phẫn nộ vang lên lần nữa."),t("br"),t("br"),n._v("“Cha, chính là cái này nữ nhân, còn có người thanh niên kia, bọn hắn cùng một giuộc, đều không là đồ tốt, ngươi nhất định phải giúp ta hả giận...”"),t("br"),t("br"),n._v("Tần Chung bị bầy người chặn lại, cũng không thấy rõ cha thái độ, còn tưởng rằng định muốn giúp chính mình hả giận, hưng phấn con mắt đỏ bừng."),t("br"),t("br"),n._v("Hắn bên này cao hứng, trước mặt Tần đường chủ nghe nói như thế, thân thể nhoáng một cái, suýt chút nữa không có tại chỗ ngất đi."),t("br"),t("br"),n._v("Đừng gọi ta..."),t("br"),t("br"),n._v("Ta không là cha ngươi, ngươi là cha ta còn không được nha..."),t("br"),t("br"),n._v("Người khác đều là hiếu tử, ngươi đây là hố cha ah!"),t("br"),t("br"),n._v("(Trước đó làm cái phiên ngoại hoạt động, mọi người tham gia không nô nức tấp nập chỉ có mấy cái, lão nhai rất không vui, dưới cơn nóng giận, bản thân ghi. Công chúng số phát bỏ phiếu có Mạc Vũ, Lạc Thất Thất đám người bỏ phiếu, mọi người chú ý bỏ phiếu, bỏ phiếu nhiều lão nhai đơn độc ghi phiên ngoại. Wechat chú ý Hoành Tảo Thiên Nhai, kiểm tra lịch sử tin tức là được!)"),t("br"),t("br"),n._v("Convert by: ThấtDạ"),t("br"),t("br"),t("br"),t("br")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("a",{attrs:{href:"./chap-696"}},[n._v("Chương trước")]),n._v("  |  "),t("a",{attrs:{href:"./chap-698"}},[n._v("Chương sau")])])])}),[],!1,null,null,null);h.default=i.exports}}]);