(window.webpackJsonp=window.webpackJsonp||[]).push([[1963],{2318:function(n,h,t){"use strict";t.r(h);var g=t(44),c=Object(g.a)({},(function(){var n=this,h=n.$createElement,t=n._self._c||h;return t("ContentSlotsDistributor",{attrs:{"slot-key":n.$parent.slotKey}},[t("h1",{attrs:{id:"chuong-690-truong-huyen-cuu-thu"}},[t("a",{staticClass:"header-anchor",attrs:{href:"#chuong-690-truong-huyen-cuu-thu"}},[n._v("#")]),n._v(" Chương 690: Trương Huyền cứu thú")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("br"),t("br"),n._v("Chương 690: Trương Huyền cứu thú"),t("br"),t("br"),n._v("Cứu đầu kia linh thú, đoàn người vội vã chạy tới nhà tranh."),t("br"),t("br"),n._v("Rất nhanh, lần nữa thấy được cái kia bia đá cùng dốc núi đồng dạng phần mộ."),t("br"),t("br"),n._v("“Mở ra!”"),t("br"),t("br"),n._v("Biết sự tình khẩn cấp, Trương Huyền cũng không nhiều lời, bàn tay lớn vẫy một cái."),t("br"),t("br"),n._v("Tiền trưởng lão nhẹ gật đầu, nhướng mày, bỗng nhiên bắt tới."),t("br"),t("br"),n._v("Ầm ầm!"),t("br"),t("br"),n._v("Hùng hậu chân khí xao động, lực lượng cuồng bạo khắp nơi chạy, dốc núi tại hắn thôi thúc dưới, cây cối dần dần thanh trừ, lộ ra một cái cự đại mộ thất."),t("br"),t("br"),n._v("“Ngươi xây dựng mộ thất? Xem ra còn có hi vọng...”"),t("br"),t("br"),n._v("Nhìn thấy Tiền trưởng lão cũng không phải là cứng rắn chôn, Trương Huyền thở phào nhẹ nhõm."),t("br"),t("br"),n._v("Mặc kệ mộ thất là cực kỳ nhỏ, xem như có nhất định không gian, tại cộng thêm bùn đất bản thân liền lúc rảnh rỗi khe hở, không khí có thể đi vào, còn không đến mức không chết bị cứ thế mà chôn chết."),t("br"),t("br"),n._v("Cũng coi là vạn hạnh trong bất hạnh."),t("br"),t("br"),n._v("Đẩy ra mộ thất đại môn, mọi người nối đuôi nhau mà vào, lập tức nhìn thấy một cái rộng lớn trong phòng, một đầu to lớn Long Nham thú thi thể, nằm thẳng dưới đất."),t("br"),t("br"),n._v("Đầu này đại gia hỏa, toàn thân như là tảng đá, mặc dù qua năm mươi năm, nhưng cũng chưa từng xuất hiện hư thối loại hình hiện tượng."),t("br"),t("br"),n._v("“Không hổ là Long Nham thú...”"),t("br"),t("br"),n._v("Nhìn thoáng qua, Trương Huyền gật gật đầu."),t("br"),t("br"),n._v("Đổi lại đồng dạng linh thú, chôn năm mươi năm, không có người quản không có người hỏi, đoán chừng bất tử cũng gần như rữa nát hết."),t("br"),t("br"),n._v("Long Nham thú, trong cơ thể nắm giữ Long tộc huyết mạch, bên ngoài thân càng là như là nham thạch đồng dạng cứng rắn, cứ việc cách nhau mấy chục năm, nhưng không có vấn đề quá lớn."),t("br"),t("br"),n._v("“Nhanh đi mời y sư...”"),t("br"),t("br"),n._v("Thấy “Thi thể” không có biến hóa, Tiền trưởng lão tràn đầy kích động, xoay người nhìn về phía Hàn Xung."),t("br"),t("br"),n._v("“Lão sư, Trương sư chính là... Rất lợi hại y sư!”"),t("br"),t("br"),n._v("Hàn Xung vội nói."),t("br"),t("br"),n._v("“A, đúng, Trương sư, còn cầu ngươi mau cứu Long Nham thú...”"),t("br"),t("br"),n._v("Tiền trưởng lão nhịn không được nói, đầu gối mềm nhũn liền muốn quỳ xuống."),t("br"),t("br"),n._v("Long Nham thú, là đồng bọn của hắn, những năm này mỗi một ngày đều sống ở tự trách bên trong, vốn cho rằng kiếp này lại không có cơ hội gặp nhau, nằm mơ đều không nghĩ tới, thế mà còn chưa có chết!"),t("br"),t("br"),n._v("Nếu quả thật có thể cứu sống, đừng nói quỳ xuống, coi như lập tức muốn hắn đi chết, cũng tuyệt đối sẽ không do dự."),t("br"),t("br"),n._v("Đương nhiên, càng nhiều còn hổ thẹn."),t("br"),t("br"),n._v("Đối phương vì cứu hắn, suýt chút nữa chết, kết quả bản thân lại bởi vì nghe không hiểu đối phương, cứ thế mà đem nó chôn đến dưới đất năm mươi năm lâu..."),t("br"),t("br"),n._v("Thật muốn không cứu sống, hắn khả năng thật sống không nổi, áy náy tự sát."),t("br"),t("br"),n._v("“Tiền trưởng lão không cần khách khí, nếu như có thể cứu, ta sẽ ra tay!”"),t("br"),t("br"),n._v("Biết tâm tình của đối phương, Trương Huyền cũng không nhiều lời, mấy bước đi tới trước mắt to lớn Long Nham thú gần, ngón tay nhẹ nhàng đụng vào."),t("br"),t("br"),n._v("Ngay sau đó làm bộ dạo qua một vòng, cúi đầu trầm tư."),t("br"),t("br"),n._v("Một lát sau, đi tới, thấy hắn vẻ mặt nghiêm túc, Tiền trưởng lão khẩn trương toàn thân cứng ngắc, nhịn không được hỏi: “Nó... Còn có thể cứu ư?”"),t("br"),t("br"),n._v("“Nó thật sự không chết, bất quá...” Trương Huyền chần chờ một chút: “Bị chôn dưới đất năm mươi năm, trong cơ thể sinh cơ cắt đứt, muốn cứu sống, cần kích hoạt sinh cơ đan dược, cùng linh thạch!”"),t("br"),t("br"),n._v("“Kích hoạt sinh cơ đan dược? Ta chỗ này có ba cái , coi như Hóa Phàm bát trọng Đạp Hư cảnh cường giả, đều có cực mạnh hiệu quả!”"),t("br"),t("br"),n._v("Tần đường chủ tiến về phía trước một bước, đưa đến một cái bình ngọc."),t("br"),t("br"),n._v("“Ta chỗ này cũng có hai cái , xem như kích hoạt sinh cơ tuyệt cao đan dược!” Lại có một vị trưởng lão đi tới."),t("br"),t("br"),n._v("“Ta không có đan dược, nhưng linh thạch có không ít, đây là năm trăm viên linh thạch trung phẩm...”"),t("br"),t("br"),n._v("Bọn hắn đều cùng Tiền trưởng lão mấy chục năm hảo hữu, nếu quả thật có thể cứu sống đầu này Long Nham thú, không chỉ đối Tiền trưởng lão là một bình phục an ủi, đối toàn bộ Thú đường thực lực, cũng đều là một đại tăng lên."),t("br"),t("br"),n._v("“Không sai!”"),t("br"),t("br"),n._v("Nhìn thấy chất đống tới đồ vật càng ngày càng nhiều, Trương Huyền hài lòng gật đầu."),t("br"),t("br"),n._v("“Ta còn cần Ngũ Hành trận kỳ, Tử Trúc mực, ba thùng lớn thú huyết, không cần cái khác, bên ngoài đầu kia Tử Dực Thiên Hùng thú là đủ... Còn có Đạm Thanh hương, Tử Lăng yên...”"),t("br"),t("br"),n._v("Thấy khôi phục sinh cơ đan dược và linh thạch, Tần đường chủ cùng mấy vị trưởng lão liền gom góp, âm thầm cảm khái một tiếng Thú đường giàu có, Trương Huyền lại tiếp tục nói mấy chục loại đồ vật."),t("br"),t("br"),n._v("“Những thứ này...”"),t("br"),t("br"),n._v("Nghe được những vật phẩm này, mọi người tất cả đều cau mày, từng cái tràn đầy nghi hoặc."),t("br"),t("br"),n._v("Trước đó sinh cơ đan dược, linh thạch, bọn hắn còn có thể lý giải, dù sao cái này sinh mệnh có quan hệ, Đạm Thanh hương, Tử Lăng yên... Những vật này có cái gì dùng?"),t("br"),t("br"),n._v("“Đạm Thanh hương ta nghe qua, có định hồn tác dụng, nghe nói là vu hồn sư thường dùng đồ vật...”"),t("br"),t("br"),n._v("Một trưởng lão đột nhiên nhớ tới cái gì, nhịn không được nói."),t("br"),t("br"),n._v("“Vu hồn sư?” Mọi người sững sờ."),t("br"),t("br"),n._v("Loại nghề nghiệp này đã biến mất tại dòng sông lịch sử, vài vạn năm, chẳng lẽ... Trước mắt người thanh niên này biết?"),t("br"),t("br"),n._v("“Chỉ là một chút thủ đoạn đặc thù mà thôi, cùng vu hồn sư không có liên quan quá nhiều!” Trương Huyền khoát tay áo: “Đúng rồi, lại chuẩn bị cho ta dài ba thước ngân châm, 108 cây, Lục Vĩ xà xà tiên, một chén!”"),t("br"),t("br"),n._v("Vu hồn sư là lúc trước danh sư diệt đi, hắn coi như biết, cũng không thể thừa nhận, dù sao đối phương cũng không rõ ràng tình huống cụ thể."),t("br"),t("br"),n._v("“Lục Vĩ xà xà tiên? Đây chính là kịch độc...”"),t("br"),t("br"),n._v("Tần đường chủ nhịn không được nói."),t("br"),n._v(" Đạm Thanh hương, Tử Lăng yên hắn không biết dùng tới làm cái gì, nhưng Lục Vĩ xà xà tiên nhưng mà rõ rõ ràng ràng."),t("br"),t("br"),n._v("Chỉ cần một giọt, Hóa Phàm bát trọng cường giả đều có thể tại chỗ hạ độc chết, muốn thứ này làm gì?"),t("br"),t("br"),n._v("“Độc, không chỉ có thể giết người, có đôi khi cũng có thể cứu người!” Trương Huyền khoát tay áo."),t("br"),t("br"),n._v("“Dùng độc cứu người? Đây là... Độc sư mới có thủ đoạn...”"),t("br"),t("br"),n._v("Mọi người lần nữa co lại."),t("br"),t("br"),n._v("Mặc dù thanh niên trước mắt không nói, nhưng độc không phải dùng linh tinh, muốn dùng cái này cứu người, không phải độc sư, căn bản làm không được."),t("br"),t("br"),n._v("Hắn không phải y sư ư? Làm sao biết Vu hồn thủ đoạn? Lại biết độc sư thủ pháp?"),t("br"),t("br"),n._v("Còn có thuần thú sư, danh sư, Thượng Cổ thú ngữ..."),t("br"),t("br"),n._v("Ngươi còn có cái gì không biết?"),t("br"),t("br"),n._v("“Tốt rồi, liền những vật này, nhanh đi chuẩn bị đi!” Đem đồ vật toàn bộ giao phó xong, Trương Huyền khoát tay áo."),t("br"),t("br"),n._v("“Là, ta cái này cũng làm người ta chuẩn bị!”"),t("br"),t("br"),n._v("Tiền trưởng lão gật gật đầu, gấp vội vàng lui ra ngoài."),t("br"),t("br"),n._v("Không thể không nói, Thú đường tài lực quả thực kinh người, Trương Huyền nói tới đồ vật, chẳng những thưa thớt, càng liên quan mấy cái chức nghiệp, lộn xộn không chịu nổi, đổi lại đồng dạng chức nghiệp công hội, căn bản không có khả năng tìm tới, nhưng Thú đường chỉ dùng không đến ba canh giờ, liền toàn bộ tìm đủ."),t("br"),t("br"),n._v("Thậm chí còn có ít thứ chuẩn bị thêm mấy phần, sợ thời khắc mấu chốt không đủ."),t("br"),t("br"),n._v("“Tốt rồi, đồ vật chuẩn bị đầy đủ, chư vị đều ra ngoài đi, ta trị liệu thời điểm, cần tuyệt đối yên tĩnh, không có phân phó, mong rằng chư vị có thể thay ta bảo vệ bên ngoài, không cho bất luận kẻ nào qua tới quấy rầy!”"),t("br"),t("br"),n._v("Vốn cho rằng những vật phẩm này, ít nhất phải ba, bốn thiên tài có thể tìm đủ, không nghĩ tới ngắn như vậy thời gian liền hoàn thành, Trương Huyền cũng âm thầm cảm khái, nhìn quanh một vòng khoát tay áo."),t("br"),t("br"),n._v("Phía sau hắn trị liệu, liên quan đồ vật quá nhiều, hơn nữa còn phải không ngừng mượn nhờ Thiên Đạo thư viện, ngoại nhân quan sát, khó tránh khỏi sẽ xuất hiện không ít phiền phức."),t("br"),t("br"),n._v("“Tốt!”"),t("br"),t("br"),n._v("Tiền trưởng lão nhẹ gật đầu, cùng Tần đường chủ đám người lui ra ngoài, Lạc Thất Thất cùng Ngọc Phi Nhi liếc mắt nhìn nhau, cũng đi ra ngoài, rất nhanh, mộ thất bên trong chỉ còn lại có hắn một cái."),t("br"),t("br"),n._v("Nhìn trước mắt chuẩn bị xong đồ vật, Trương Huyền cổ tay khẽ đảo, thu vào trữ vật giới chỉ."),t("br"),t("br"),n._v("Ngay sau đó lông mày nhăn lại, cổ tay rung lên, ngũ tinh Tụ Linh trận trận bàn ném đi đi ra."),t("br"),t("br"),n._v("Muốn những vật này, không phải giữ lại bản thân dùng, mà là thực sự muốn dùng tại đây đầu Long Nham thú trên người."),t("br"),t("br"),n._v("Lúc trước cái tên này vì cứu Tiền trưởng lão, tinh huyết hao hết, dựa theo tình huống bình thường, chỉ cần cho đối phương bổ sung huyết dịch, hoặc là dùng một chút đại bổ vật phẩm, lại hoặc là dùng linh khí tẩm bổ, mấy năm công phu liền có thể phục sinh."),t("br"),t("br"),n._v("Có thể Tiền trưởng lão cái gì đều không có làm..."),t("br"),t("br"),n._v("Địa Cung linh khí cực kì thưa thớt, cái tên này liền duy trì sinh cơ đều làm không được, năm mươi năm xuống, coi như không chết, sinh mệnh cũng biến thành tràn ngập nguy hiểm, như là trong gió ngọn nến, bất cứ lúc nào cũng sẽ dập tắt."),t("br"),t("br"),n._v("Đổi lại cái khác y sư, dù là thất tinh, bát tinh, thậm chí cửu tinh, chỉ sợ đều hết cách xoay chuyển, nhưng Trương Huyền khác biệt."),t("br"),t("br"),n._v("Hắn nắm giữ Thiên Đạo chân khí, tinh thuần như nước, chỉ cần còn có sinh cơ, liền có thể duy trì bất tử."),t("br"),t("br"),n._v("Bất quá, quang Thiên Đạo chân khí còn chưa đủ, nó tổn thất tinh huyết, lại bị chôn dưới đất mấy chục năm, còn cần gia tăng sinh cơ dược vật cùng hùng hậu linh khí chống đỡ."),t("br"),t("br"),n._v("Càng quan trọng hơn là... Vu hồn thủ đoạn!"),t("br"),t("br"),n._v("Cùng vị trưởng lão kia đoán đồng dạng, Đạm Thanh hương, Tử Lăng yên quả thực là vu hồn sư mới dùng đồ vật, đối phương linh hồn, mặc dù không giống lúc trước Lộ Xung đồng dạng, rơi vào trạng thái ngủ say, năm mươi năm không có tỉnh lại, cũng không kém nhiều."),t("br"),t("br"),n._v("Cần những này triệu hoán Vu hồn vật phẩm để hắn khôi phục, mới có thể cứu sống."),t("br"),t("br"),n._v("Còn Lục Vĩ xà xà tiên, Tần đường chủ nói không sai, quả thực là kịch độc chi vật, có điều, không phải dùng để độc chết, mà là kích thích huyệt đạo, lấy gai độc kích thân thể, để hắn thức tỉnh."),t("br"),t("br"),n._v("Có thể nói, lần này trị liệu, thuần thú sư, vu hồn sư, y sư, độc sư, danh sư... Rất nhiều thủ đoạn đều dùng."),t("br"),t("br"),n._v("Đổi lại người khác thật đúng là khó mà hoàn thành, cũng là Trương Huyền quái thai này, mới có thể làm đến."),t("br"),t("br"),n._v("Trận bàn rơi xuống đất, đem vừa rồi vị trưởng lão kia đưa ra linh thạch ném vào trong đó, lập tức linh khí vây quanh, đem Long Nham thú “Thi thể” bao phủ ở bên trong."),t("br"),t("br"),n._v("Ngón tay búng một cái, chuẩn bị xong ngân châm xuất hiện tại lòng bàn tay, tại Lục Vĩ xà xà tiên hơi dính, thân ảnh hóa thành thiểm điện, đồng thời năm ngón tay gảy nhẹ, thời gian nháy mắt, 108 cây ngân châm liền bay ra ngoài, phong bế Long Nham thú một trăm linh tám chỗ huyệt vị."),t("br"),t("br"),n._v("Long Nham thú, thuộc về cực kỳ hiếm có linh thú, vài vạn năm đến, đem thuần phục thuần thú sư đều không cao hơn bản lĩnh số lượng, do đó, chết giả sự tình, không người có thể biết, cũng không có lưu truyền tới nay."),t("br"),t("br"),n._v("Liền những này cũng không biết, huyệt vị của nó càng không người biết được, Trương Huyền cũng là nhìn qua trong thư viện hình thành thư tịch, mới hiểu được huyệt vị phân bố."),t("br"),t("br"),n._v("Phong bế huyệt đạo, Trương Huyền thân ảnh càng lúc càng nhanh, đem Đạm Thanh hương, Tử Lăng yên những vật này dựa theo đặc thù phương vị nhen lửa dọn xong, lúc này mới khoanh chân ngồi xuống, tinh thần khẽ động."),t("br"),t("br"),n._v("Ầm ầm!"),t("br"),t("br"),n._v("Vu hồn ly thể."),t("br"),t("br"),n._v("Hồn phách trên không trung, Minh Lý Chi Nhãn vận chuyển, nhìn một vòng, đột nhiên bàn tay đối với một nơi nào đó nhẹ nhàng dẫn ra."),t("br"),t("br"),n._v("Động tác nhẹ nhàng, giống như tình nhân ở giữa triệu hoán."),t("br"),t("br"),n._v("Ông!"),t("br"),t("br"),n._v("Dường như cảm nhận được hắn triệu hoán, nơi này ngân châm lập tức phát ra thanh minh, hình như trong gió Phong Linh."),t("br"),t("br"),n._v("Vu hồn thủ pháp đặc biệt Chiêu Hồn thủ!"),t("br"),t("br"),n._v("Lúc trước từ Mặc Hồn Sinh nơi đó, nhận được chơi hoàn chỉnh Vu hồn truyền thừa, Chiêu Hồn thủ chính là một cái trong số đó, một khi thi triển, coi như rơi vào trầm luân hồn phách, đều có thể bị cứ thế mà lôi kéo đi ra."),t("br"),t("br"),n._v("Thậm chí người sống đều có thể bị nhẹ nhõm tước đoạt."),t("br"),t("br"),n._v("Đối phương linh hồn ngủ say năm mươi năm, bình thường thủ đoạn muốn cho hắn tỉnh lại quá khó khăn, chỉ có thể sử dụng loại này cực đoan thủ pháp."),t("br"),t("br"),n._v("Không thành công thì thành nhân!"),t("br"),t("br"),n._v("Cô cô cô cục cục!"),t("br"),t("br"),n._v("Ngân châm tiếng động một lát, nguyên bản nằm Long Nham thú đột nhiên lắc lư một cái, chậm rãi mang khởi thân thể, tựa như sắp sống."),t("br"),t("br"),n._v("“Không đúng...” Nhìn đến nơi này, Trương Huyền không những không có cao hứng, ngược lại sầm mặt lại."),t("br"),t("br"),n._v("Convert by: ThấtDạ"),t("br"),t("br"),t("br"),t("br")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("a",{attrs:{href:"./chap-698"}},[n._v("Chương trước")]),n._v("  |  "),t("a",{attrs:{href:"./chap-700"}},[n._v("Chương sau")])])])}),[],!1,null,null,null);h.default=c.exports}}]);