(window.webpackJsonp=window.webpackJsonp||[]).push([[1967],{2322:function(n,h,i){"use strict";i.r(h);var t=i(44),c=Object(t.a)({},(function(){var n=this,h=n.$createElement,i=n._self._c||h;return i("ContentSlotsDistributor",{attrs:{"slot-key":n.$parent.slotKey}},[i("h1",{attrs:{id:"chuong-692-danh-su-hoc-vien-quy-cu"}},[i("a",{staticClass:"header-anchor",attrs:{href:"#chuong-692-danh-su-hoc-vien-quy-cu"}},[n._v("#")]),n._v(" Chương 692: Danh sư học viện quy củ")]),n._v(" "),i("hr"),n._v(" "),i("p",[i("br"),i("br"),n._v("Chương 692: Danh sư học viện quy củ"),i("br"),i("br"),n._v("Trương Huyền tại Vạn Quốc thành, Huyễn Vũ đế quốc làm ra sự tình, mấy ngày nay hắn cũng nghe đến một chút, chỗ đi qua, một mảnh hỗn độn..."),i("br"),i("br"),n._v("Danh sư học viện, đem hắn tuyển nhận vì học viên, đến cùng là chuyện tốt là xấu, thật đúng là có thể nói là không biết chi trời!"),i("br"),i("br"),n._v("Xem ra chỉ có thể để cái này thành lập không biết bao nhiêu năm học viện, tự cầu phúc đi..."),i("br"),i("br"),n._v("..."),i("br"),i("br"),n._v("Trên bầu trời, Tử Dực Thiên Hùng thú gào thét phi hành."),i("br"),i("br"),n._v("Phía sau của nó, là cái bộ dáng tương tự thuyền đồng dạng đồ vật, dài bảy, tám mét, phía trên lầu các san sát, phân chia ra ba cái gian phòng, Trương Huyền đám người, mới vừa dễ dàng mỗi người một gian, tách ra ở lại."),i("br"),i("br"),n._v("Phi chu!"),i("br"),i("br"),n._v("Đây là Thú đường vì cảm tạ hắn thu phục Thiên Hùng thú tên sát tinh này, chuyên môn chế tạo riêng, phía trên ăn mặc ở lại, đầy đủ mọi thứ."),i("br"),i("br"),n._v("Nghe được cái này phi chu ít nhất giá trị mấy ngàn linh thạch trung phẩm, Trương Huyền lúc này mới cảm thấy dễ chịu không ít."),i("br"),i("br"),n._v("Xem ra lần này tới, cũng không phải giúp không vội vàng, có chút thu hoạch..."),i("br"),i("br"),n._v("Nếu như cho Thú đường những người khác biết ý nghĩ này, khẳng định sẽ toàn bộ thổ huyết, tới Thú đường mấy ngày, đem mạnh nhất linh thú thuần phục không nói, chế tạo trân quý như thế phi chu, vậy mà mới phát giác được không chịu thiệt..."),i("br"),i("br"),n._v("Cái này tâm cũng quá lớn đi!"),i("br"),i("br"),n._v("Có phải hay không muốn đem toàn bộ Thú đường vơ vét không có tiền, mới phát giác được hài lòng?"),i("br"),i("br"),n._v("Kỳ thật Trương Huyền còn thật không có vơ vét quang Thú đường ý tứ, chỉ nghĩ đối mới có thể tiễn cái trăm tám mươi cái linh thạch thượng phẩm, chỉ tiếc... Tiền trưởng lão những năm này bị vơ vét nghèo, liền một trăm cái linh thạch trung phẩm, đều không bỏ ra nổi đến, chớ nói chi là thượng phẩm."),i("br"),i("br"),n._v("“Lão sư!”"),i("br"),i("br"),n._v("Phi chu boong thuyền, đối diện đón bầu trời cương phong, Lạc Thất Thất đi tới gần."),i("br"),i("br"),n._v("“Ngươi tới thật đúng lúc, ta trước đó nghe ngươi nói, danh sư học viện, có Đan viện, luyện khí học viện, đến cùng chuyện gì xảy ra?”"),i("br"),i("br"),n._v("Trương Huyền nhìn qua."),i("br"),i("br"),n._v("Lập tức liền muốn đi danh sư học viện, hắn đối với nơi này không có chút nào biết, khẳng định không được."),i("br"),i("br"),n._v("“Danh sư cần có đầy đủ phụ tu chức nghiệp, mới có thể khảo hạch thành công, do đó học viện không riêng gì học tập danh sư năng lực địa phương, còn có thật nhiều phụ tu chức nghiệp có thể trực tiếp tại nội bộ học tập.”"),i("br"),i("br"),n._v("Lạc Thất Thất giới thiệu nói: “Đi qua nhiều năm phát triển, những này phụ tu chức nghiệp, đã chia làm mười cái học viện! Theo thứ tự là Đan viện, Khí viện, Thiên Công viện, Trận Pháp viện, Thư Họa viện, Vũ Kỹ viện, Thuần Thú viện, Y Đạo viện, Ma âm viện, Kinh Hồng viện.”"),i("br"),i("br"),n._v("“Đan viện, cũng chính là học tập luyện đan thuật địa phương, ta chính là ở nơi đó học tập luyện đan thuật. Khí viện, thì là học tập luyện khí địa phương, trước đó ta luyện khí thuật ở nơi đó, liền một trăm người đứng đầu đều chưa có xếp hạng, được đến lão sư chỉ điểm, tất nhiên có thể mở mày mở mặt!”"),i("br"),i("br"),n._v("“Còn Thiên Công viện, Trận Pháp viện, Thư Họa viện, Y Đạo viện, chắc hẳn ta không nói nhiều lão sư cũng biết có ý tứ gì, hiện tại liền nói một chút Vũ Kỹ viện, Ma âm viện cùng Kinh Hồng viện!”"),i("br"),i("br"),n._v("Trương Huyền gật đầu."),i("br"),i("br"),n._v("Thiên Công viện khẳng định là bồi dưỡng Thiên Công sư địa phương, liên quan đến cơ quan kỹ xảo, điểm ấy từ Ngô Chấn cũng có thể thấy được, còn trận pháp, y đạo, thư hoạ, lại càng không cần phải nói."),i("br"),i("br"),n._v("Những nghề nghiệp này, hắn cũng học tập một chút, xem như cực kỳ lý giải."),i("br"),i("br"),n._v("Chỉ có còn lại cái này ba cái, kiến thức nửa vời, có chút không hiểu rõ lắm."),i("br"),i("br"),n._v("“Vũ Kỹ viện kỳ thật không thuộc về đặc thù chức nghiệp, mà là chuyên môn rèn luyện danh sư năng lực chiến đấu địa phương, có thể thông qua khảo hạch học tập võ kỹ, để thực lực càng ngày càng mạnh. Ma âm viện, thì là bồi dưỡng Ma âm sư địa phương, Ma âm sư lão sư có thể từng nghe nói qua?”"),i("br"),i("br"),n._v("Lạc Thất Thất nhìn qua."),i("br"),i("br"),n._v("“Ừm, nghe qua một chút, nghe nói là hạ cửu lưu chức nghiệp, có điều, nhưng có thể sử dụng âm nhạc mê hoặc nhân tâm, khiến tâm linh người ta chịu đến xung kích, cường giả đều khó mà chống chọi!” Trương Huyền gật đầu."),i("br"),i("br"),n._v("Cái này Ma âm sư hắn đương nhiên biết, thậm chí đều gặp hai vị, chỉ là một cái bị bản thân làm cho nổi điên, một cái khác trực tiếp từ không trung rơi xuống, hiện tại cũng không rõ sống chết."),i("br"),i("br"),n._v("“A, nghe nói qua liền đơn giản, còn cái cuối cùng Kinh Hồng viện, thì là bồi dưỡng kinh hồng sư địa phương!” Lạc Thất Thất cười nói."),i("br"),i("br"),n._v("“Kinh hồng sư? Ngươi nói là vũ sư?” Trương Huyền sững sờ."),i("br"),i("br"),n._v("“Không sai, chính là vũ sư!” Lạc Thất Thất gật đầu: “Vũ sư cùng Ma âm sư đồng dạng, lấy múa diễn dịch đại đạo, để cho người ta trầm mê trong đó, cường giả, có thể kinh hồng, để vô số chim bay đã tới... Vì vậy mệnh danh.”"),i("br"),i("br"),n._v("Trương Huyền gật đầu."),i("br"),i("br"),n._v("Thiên hạ có vũ cơ, cũng là có vũ sư cái nghề nghiệp này, sáng chế cái nghề nghiệp này người, họ kép Công Tôn, một khúc múa ra, Thánh giả cũng vì đó trầm mê, khó mà tự kềm chế."),i("br"),i("br"),n._v("Truyền thuyết, vị này Công Tôn thị, lúc tuổi còn trẻ bị diệt cả nhà, thân mang huyết hải thâm cừu, không cách nào đi báo, một ngày, xem hồng phi thiên mà tự ngộ, sáng chế vũ sư chức nghiệp, khổ tu ba mươi năm, kinh hồng vừa ra thiên hạ sợ hãi, bị chiêu đến phong hào đế quốc trình diễn tài nghệ."),i("br"),i("br"),n._v("Vị này Công Tôn thị cừu nhân chính là đế quốc này Hoàng đế bệ hạ, một vị siêu cường Thánh giả."),i("br"),i("br"),n._v("Kết quả, Công Tôn múa ra, thiên hoa loạn trụy, chúng màu xuất hiện, Thánh giả vì đó mất phương hướng, từ đó để hắn thong dong chém giết."),i("br"),i("br"),n._v("Liền tại sắp chạy ra hoàng thành thời điểm, bị trăm vạn người vòng vây, cùng làm kinh hồng múa, cuối cùng trăm vạn quan binh, thả xuống vũ khí, tùy ý rời khỏi, từ đó truyền vì giai thoại."),i("br"),i("br"),n._v("Khổng sư biết tin tức này về sau, từng chuyên môn tìm tới vị này Công Tôn thị, quan sát dáng múa, tán thưởng không dứt, nói ra “Chỉ buồn bắt không được, bay đi đuổi kinh hồng.” Loại hình lời nói, cũng tự mình xác định cái nghề nghiệp này, từ đây “Kinh hồng” tên truyền vang thiên hạ."),i("br"),i("br"),n._v("Cái nghề nghiệp này, đồng dạng vì hạ cửu lưu một trong, cơ bản đều là nữ tử làm chủ, so Ma âm sư còn ít ỏi hơn, do đó không muốn người biết."),i("br"),i("br"),n._v("Hồng Viễn danh sư học viện, trong lúc vô tình nhận được môn này nghề nghiệp truyền thừa, mới thành lập cái này học viện, chẳng qua nhân viên thưa thớt, xem như mười đại học viện bên trong, yếu nhất một cái."),i("br"),i("br"),n._v("“Ngọc Phi Nhi, từ nhỏ học tập, đoạn thời gian trước mới vừa thông qua được khảo hạch, là một vị ngũ tinh kinh hồng sư.”"),i("br"),i("br"),n._v("Lạc Thất Thất cười cười."),i("br"),n._v("Trương Huyền gật đầu."),i("br"),i("br"),n._v("Người danh sư này học viện, kỳ thật nói đến cùng kiếp trước đại học có chút tương tự, mặc dù chỉ là một trường học, nhưng phân ra không biết bao nhiêu nhỏ bé viện hệ."),i("br"),i("br"),n._v("Ở đây đi học danh sư, có thể tùy ý lựa chọn chức nghiệp bồi dưỡng, xem như phụ trợ chức nghiệp, khảo hạch đẳng cấp cao hơn."),i("br"),i("br"),n._v("Không thể không nói, cái này đích xác là bồi dưỡng danh sư nơi tốt, khó trách tại nhất đẳng đế quốc, đều tiếng tăm lừng lẫy."),i("br"),i("br"),n._v("“Danh sư học viện, mặc dù là rất nhiều danh sư hướng tới Thánh địa, trên thực tế nhưng cũng dị thường tàn khốc, bất kể học tập vẫn là nghe giảng bài, đều cần có đầy đủ học phần mới được, không có học phần nửa bước khó đi! Mà muốn kiếm lấy học phần, liền phải hoàn thành đủ loại nhiệm vụ, hoặc là thông qua một ít lợi hại khảo hạch... Rất nhiều danh sư, bởi vì không chịu nổi áp lực, chủ động lựa chọn thôi học, cũng không ít bởi vì năm khảo hạch cuối cùng thành tích quá kém, mà bị đuổi!”"),i("br"),i("br"),n._v("Nói đến đây, Lạc Thất Thất thần sắc tràn đầy ngưng trọng."),i("br"),i("br"),n._v("“Nghỉ học? Bị đuổi?”"),i("br"),i("br"),n._v("Vốn cho rằng tiến vào học viện chẳng khác nào bước vào thành công bậc thang, về sau một bước lên mây, hiện đang nghe tới, cũng không phải là chuyện như vậy."),i("br"),i("br"),n._v("“Đúng vậy a, Khổng sư lúc trước thiết lập danh sư học viện thời điểm, đã từng nói, danh sư, là nhân loại tiến bộ bậc thang, sư mạnh, nhân loại mới mạnh! Do đó, danh sư học viện, không cần tiêu cực biếng nhác học viên, hoặc là tiến bộ, hoặc là rời khỏi, chỉ đơn giản như vậy!”"),i("br"),i("br"),n._v("Lạc Thất Thất cười khổ một tiếng, nói."),i("br"),i("br"),n._v("“Sư mạnh, nhân loại mới mạnh?” Trương Huyền chấn động toàn thân."),i("br"),i("br"),n._v("Đối phương nói không sai, nhân loại muốn tiến bộ, lão sư cực kỳ trọng yếu."),i("br"),i("br"),n._v("Nếu như không thể cho cùng đúng đắn nhân sinh quan giá trị quan, học sinh thành liền sẽ có hạn."),i("br"),i("br"),n._v("Lão sư đều giậm chân tại chỗ, lại làm sao có thể giáo dục ra tích cực tiến bộ học viên? Từ đó để cả nhân loại, đều tiến bộ?"),i("br"),i("br"),n._v("Không thể không nói, cái này lý luận vô cùng tốt."),i("br"),i("br"),n._v("Khôn sống mống chết."),i("br"),i("br"),n._v("Liền xem như thiên hạ đệ nhất chức nghiệp, cũng không có có thể buông lỏng vốn liếng."),i("br"),i("br"),n._v("“Lão sư kỳ thật không cần lo lắng, nhờ vào thực lực của ngươi, coi như muốn đào thải, cũng khẳng định làm không được...”"),i("br"),i("br"),n._v("Nhớ tới vị lão sư này tại rất nhiều chức nghiệp bên trong biến thái, Lạc Thất Thất cười khổ một tiếng, nhớ tới cái gì ngay sau đó nói: “Bất quá, muốn trổ hết tài năng, tiến vào học viện danh sư bảng, chỉ sợ không có mấy năm công phu, rất khó làm đến!”"),i("br"),i("br"),n._v("“Danh sư bảng?”"),i("br"),i("br"),n._v("“Ừm, đây là danh sư học viện làm ra một cái bảng danh sách, có thể tự động tạo ra họ tên, biểu hiện học viện kiệt xuất nhất một trăm vị danh sư, có thể đi vào bảng danh sách, không có chỗ nào mà không phải là học viện người nổi bật, để cho người ta hâm mộ!”"),i("br"),i("br"),n._v("Lạc Thất Thất nói."),i("br"),i("br"),n._v("“Học viện một trăm người đứng đầu? Học viện tổng cộng bao nhiêu người?”"),i("br"),i("br"),n._v("Trương Huyền nhịn không được hỏi."),i("br"),i("br"),n._v("“Hồng Viễn danh sư học viện, không chỉ tuyển nhận Đế quốc cảnh nội học sinh, cái khác nhất đẳng đế quốc học sinh đồng dạng tuyển nhận, toàn bộ học viện không dưới mười vạn người, do đó có thể đi vào bảng danh sách, hầu như đều là thiên tài trong thiên tài!”"),i("br"),i("br"),n._v("Lạc Thất Thất giải thích."),i("br"),i("br"),n._v("Có thể đi vào danh sư học viện, đều là ngàn dặm mới tìm được một thiên tài, lại ở bên trong tinh tuyển, một ngàn cái lấy ra một cái, độ khó bao lớn, có thể tưởng tượng được."),i("br"),i("br"),n._v("Trương sư mặc dù rất yêu nghiệt, dưới cái nhìn của nàng, muốn đi vào bảng danh sách này, cũng khó có thể làm đến."),i("br"),i("br"),n._v("“Ngươi không có ở bảng danh sách này?”"),i("br"),i("br"),n._v("Trương Huyền hỏi."),i("br"),i("br"),n._v("Đi qua tiếp xúc, cái này Lạc Thất Thất, bất kể luyện đan, luyện khí hay là tu vi, tại đồng niên cấp, thời gian ngắn so sánh chói mắt, chẳng lẽ... Vẫn như cũ chưa đi đến vào bảng danh sách?"),i("br"),i("br"),n._v("“Ta? Cái này sao có thể! Bảng danh sách này cùng học phần, tu vi, hiểu biết có quan hệ, có thể tiến vào bên trong, bình thường đều là đệ tử cấp cao, ta chỉ là cái năm thứ hai tiểu nhân vật...” Lạc Thất Thất cười khổ lắc đầu."),i("br"),i("br"),n._v("Nếu là bảng danh sách này tuỳ tiện liền có thể vào, những cái kia đệ tử cấp cao, cũng không cần sống."),i("br"),i("br"),n._v("Bọn hắn tại học viện học tập không biết bao nhiêu năm, hoàn thành không biết bao nhiêu nhiệm vụ, điểm tích lũy chất đống như núi, một cái vừa tiến vào học viên mấy năm người, liền muốn siêu việt, hầu như là không thể nào."),i("br"),i("br"),n._v("“Được rồi!”"),i("br"),i("br"),n._v("Nghe đối phương giảng xong, Trương Huyền đối danh sư học viện xem như có hiểu rõ nhất định."),i("br"),i("br"),n._v("Vốn cho rằng là cái có thể tùy tiện học tập tri thức địa phương, không nghĩ tới độ khó bao lớn vượt ra khỏi tưởng tượng ra."),i("br"),i("br"),n._v("Khó trách Ngọc Phi Nhi thân là công chúa, đều phải đi ra thí luyện, không sợ nguy hiểm, xem ra cái này học viện cũng sẽ không bởi vì quyền quý mà thay đổi."),i("br"),i("br"),n._v("Kỳ thật như vậy càng tốt hơn, đối với hắn mà nói, cũng càng có khiêu chiến."),i("br"),i("br"),n._v("Nếu không, cũng là không dễ chơi."),i("br"),i("br"),n._v("Trong nháy mắt, Trương Huyền trong lòng sinh ra vô cùng đấu chí, đối cái này cái gọi là danh sư học viện, càng thêm tò mò."),i("br"),i("br"),n._v("Phía sau mấy ngày, Lạc Thất Thất không làm gì liền kỹ càng giảng giải danh sư học viện chư nhiều chuyện, Trương Huyền cũng chỉ điểm nàng luyện đan, luyện khí, ngắn ngủi trong mười ngày, nàng luyện đan, luyện khí lần nữa tăng lên không ít."),i("br"),i("br"),n._v("“Lão sư, Hồng Viễn thành đến!”"),i("br"),i("br"),n._v("Hôm nay, Trương Huyền chính tại giảng giải, liền nghe đến Lạc Thất Thất tiếng âm vang lên, cúi đầu nhìn lại, quả nhiên thấy một cái so Huyễn Vũ đế đô bao la vô số lần cự đại thành thị xuất hiện tại ánh mắt."),i("br"),i("br"),n._v("Convert by: ThấtDạ"),i("br"),i("br"),i("br"),i("br")]),n._v(" "),i("hr"),n._v(" "),i("p",[i("a",{attrs:{href:"./chap-700"}},[n._v("Chương trước")]),n._v("  |  "),i("a",{attrs:{href:"./chap-702"}},[n._v("Chương sau")])])])}),[],!1,null,null,null);h.default=c.exports}}]);