(window.webpackJsonp=window.webpackJsonp||[]).push([[1968],{2323:function(n,h,t){"use strict";t.r(h);var i=t(44),g=Object(i.a)({},(function(){var n=this,h=n.$createElement,t=n._self._c||h;return t("ContentSlotsDistributor",{attrs:{"slot-key":n.$parent.slotKey}},[t("h1",{attrs:{id:"chuong-693-cuu-giup-mot-cai"}},[t("a",{staticClass:"header-anchor",attrs:{href:"#chuong-693-cuu-giup-mot-cai"}},[n._v("#")]),n._v(" Chương 693: Cứu giúp một cái?")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("br"),t("br"),n._v("Chương 693: Cứu giúp một cái?"),t("br"),t("br"),n._v("Cái thành phố này rộng lớn cực kỳ, liền xem như Minh Lý Chi Nhãn, đều không nhìn thấy cuối cùng, khảm nạm tại to lớn bình nguyên bên trên, như là đất cát bên trong một khỏa chói mắt trân châu."),t("br"),t("br"),n._v("“Nhất đẳng đế quốc Đế đô... Quả nhiên hùng vĩ!”"),t("br"),t("br"),n._v("Trương Huyền ánh mắt giương lên."),t("br"),t("br"),n._v("Không hổ là nhất đẳng đế quốc Đế đô, hùng vĩ khí phách, Thiên Huyền vương quốc tới so sánh, liền cùng nông thôn cục đất đồng dạng, không có ý nghĩa."),t("br"),t("br"),n._v("Thậm chí cái này một tòa thành thị lớn nhỏ, liền tiếp cận người sau cả quốc gia diện tích."),t("br"),t("br"),n._v("Cả tòa thành thị linh khí vây quanh, dường như có to lớn trận pháp, khống chế nội thành thời tiết, khí hậu, để ở lại trong đó người, lại càng dễ tu luyện, tốt hơn tiến bộ."),t("br"),t("br"),n._v("“Nghịch thiên cải biến tiết khí, đây là Thánh giả mới có thủ đoạn!”"),t("br"),t("br"),n._v("Ngọc Phi Nhi đi tới, vẻ mặt kiêu ngạo."),t("br"),t("br"),n._v("Cho tới nay, kẻ trước mắt này đều ngưu khí hống hống, để nàng cảm thấy mình cùng nông thôn đến nghèo cô nương đồng dạng, hiện tại rốt cục nhìn thấy đối phương khiếp sợ, lập tức đắc chí vừa lòng."),t("br"),t("br"),n._v("“Ừm!”"),t("br"),t("br"),n._v("Mặc dù hắn cũng có thể bố trí trận pháp, tại trận pháp nhất đạo, cũng không yếu tại ngũ tinh trận pháp sư, nhưng muốn bố trí ra một cái khống chế phương viên mấy ngàn dặm phạm vi đại trận, vẫn là khó mà làm đến."),t("br"),t("br"),n._v("Loại cấp bậc này trận pháp, không chỉ muốn khống chế tinh chuẩn, trọng yếu nhất chính là, đối lực lượng tuyệt đối khống chế, một khi xử lý không tốt, liền sẽ sụp đổ, tạo thành to lớn phá hư."),t("br"),t("br"),n._v("“Chúng ta hơn hai mươi ngày liền trở lại, khoảng cách khai giảng còn có mấy ngày, Hồng sư bọn hắn hẳn là cũng vừa tới, ta muốn trước cùng bọn hắn tụ hợp!” Từ trong lúc khiếp sợ khôi phục lại, Trương Huyền nói."),t("br"),t("br"),n._v("Dung hợp linh hồn, làm trễ nải ba ngày, Dị Linh tộc nhân Địa Cung làm trễ nải ba, bốn trời, về sau Thú đường, phi hành hai mươi ngày, khoảng cách thời hạn một tháng, còn có ba ngày thời gian, dựa theo tình huống bình thường, Hồng sư đám người, hẳn là cũng mới vừa đến nơi đây không lâu."),t("br"),t("br"),n._v("“Huyễn Vũ đế quốc tới học viên mới, đều sẽ ở tại sĩ tử hải, ta mang ngươi tới, sau đó chúng ta cũng phải về học viện phục mệnh!”"),t("br"),t("br"),n._v("Ngọc Phi Nhi nói."),t("br"),t("br"),n._v("Bọn hắn đi ra không chỉ có hoàn thành thí luyện, còn phát hiện như thế lớn bí mật, cần muốn trở về bẩm báo."),t("br"),t("br"),n._v("Cứ việc Hình Viễn, Ngô Chấn đám người đã sớm trở về, nhưng Ngô Dương Tử lưu hạ thủ thư, đến lô đỉnh còn tại Lạc Thất Thất trong tay, lại thêm Trương Huyền trở về, còn cần ở trước mặt bẩm báo, mới có thể đem sự tình nói rõ ràng."),t("br"),t("br"),n._v("“Ừm!” Trương Huyền gật đầu."),t("br"),t("br"),n._v("“Lão sư, sĩ tử hải là học viện tân sinh chỗ ở, trong đó không chỉ có Huyễn Vũ đế quốc học viên mới, cũng có cái khác nhị đẳng đế quốc, thậm chí Hồng Viễn đế quốc thiên tài, ngươi phải cẩn thận, tuyệt đối không nên tới náo ra mâu thuẫn!”"),t("br"),t("br"),n._v("Lạc Thất Thất bàn giao một câu."),t("br"),t("br"),n._v("Có thể xưng là thiên tài, khẳng định đều là kiêu căng khó thuần hạng người, sĩ tử hải là Hồng Viễn đế quốc tiếp đãi tân sinh địa phương, trong đó thiên tài nhiều vô số, cùng bọn hắn náo ra mâu thuẫn, liền xem như Ngọc Phi Nhi loại này công chúa, đến lúc đó đều không tiện ra mặt."),t("br"),t("br"),n._v("“Yên tâm đi, ta là người thành thật, không sẽ chọc cho phiền phức!” Trương Huyền cười cười."),t("br"),t("br"),n._v("Lạc Thất Thất, Ngọc Phi Nhi nhìn nhau, đồng thời lắc đầu."),t("br"),t("br"),n._v("Ngươi nếu là người thành thật, chúng ta đám người này, còn không phải ngoan ngoãn thầy giáo?"),t("br"),t("br"),n._v("Đến Huyễn Vũ đế đô ngày đầu tiên, trước đem Phùng sư rơi hoàn toàn thay đổi, sau đó đánh cái khác các nước chư hầu rất nhiều ba hạng đầu, trạng thái như đầu heo, đi vào Hóa Thanh trì, lại biến thành thi thể..."),t("br"),t("br"),n._v("Dù sao, ngươi đi đến cái kia, đều là gà bay chó chạy, Thần quỷ lui tránh, thế mà còn dám nói mình là người thành thật!"),t("br"),t("br"),n._v("Sĩ tử hải, là chỉ bốn phương tám hướng đến đây tân sinh nhiều như đại dương mênh mông, lại gọi sĩ tử cư, tân sinh cư."),t("br"),t("br"),n._v("Tại Hồng Viễn thành một bên, khoảng cách danh sư học viện không tính là quá xa, Tử Dực Thiên Hùng thú tốc độ cực nhanh, không lâu sau, liền đến đến gần."),t("br"),t("br"),n._v("Từ phi chu bên trên xuống tới, đem Thú đường cho cái này đồ vật, thu vào trữ vật giới chỉ, Trương Huyền lúc này mới cùng Lạc Thất Thất đám người cáo từ."),t("br"),t("br"),n._v("Đi vào sĩ tử hải đại môn, vô số kiến trúc san sát, từng cái tuổi trẻ danh sư, đi tới đi lui, cho người ta một loại tinh khí thần sung mãn cảm giác."),t("br"),t("br"),n._v("Những người này thoạt nhìn đều hai mươi tuổi, cấp bậc lại đều đạt đến tứ tinh, thậm chí không ít tu vi đều là Hợp Linh cảnh, so với hắn, đều không kém chút nào."),t("br"),t("br"),n._v("Không hổ là rất nhiều tông môn, vương quốc, đế quốc thiên tài, không tầm thường."),t("br"),t("br"),n._v("Dựa theo Lạc Thất Thất đám người nói tới phương vị, tìm một hồi, rất mau tới đến một cái sân."),t("br"),t("br"),n._v("“Ừm? Tại sao không ai thủ vệ?”"),t("br"),t("br"),n._v("Nhìn một chút, xác nhận đây là Huyễn Vũ đế quốc tới tân sinh chỗ ở, Trương Huyền tràn đầy kỳ quái."),t("br"),t("br"),n._v("Đế quốc khác tân sinh chỗ ở, tất cả đều đóng chặt cửa nẻo, hoặc là có người trấn giữ, mà ở trong đó, đại môn mở ra, một cái Quỷ Ảnh đều không có, chẳng lẽ, bản thân đoán sai, Hồng sư đám người còn chưa tới đến?"),t("br"),t("br"),n._v("Trong lòng kỳ quái nhấc chân đi vào."),t("br"),t("br"),n._v("Trong sân sạch sẽ gọn gàng, nói rõ thường xuyên có người quét dọn, chung quanh lương đình hành lang bên trong, có gần nhất mới vừa ở lại người dấu vết."),t("br"),t("br"),n._v("“Xem ra người đã đi tới, làm sao đều vắng mặt?”"),t("br"),t("br"),n._v("Hắn thân là nửa bước ngũ tinh danh sư, càng có Minh Lý Chi Nhãn, nhìn ra nơi này có hay không có người ở vẫn là rất đơn giản, đang đang kỳ quái, liền nghe đến phía trước một hồi tiếng rít vang lên, dường như có đồ vật gì đâm vào trước mặt trên vách tường, chấn bụi đất tung bay."),t("br"),t("br"),n._v(" “Có người chiến đấu!”"),t("br"),t("br"),n._v("Sầm mặt lại, vội vàng đi thẳng về phía trước, vòng qua một cái hành lang, lập tức nhìn thấy rộng lớn trong sân, một cái không lớn lôi đài, phía trên đang có người tỷ thí."),t("br"),t("br"),n._v("Dưới đài đống một đống người, Hồng sư, Phùng sư đám người thình lình xuất hiện."),t("br"),t("br"),n._v("Cùng hắn cùng đi Nhược Hoan công tử, Tất Giang Hải, La Tuyền đám người, cũng ngồi ở phía dưới, chỉ bất quá, từng cái khuôn mặt hồng nhuận, mặt mũi bầm dập, hình như mới vừa bị người đánh cho một trận."),t("br"),n._v("Trên đài đang tỷ thí, đúng lúc cũng nhận biết, là Phùng sư dẫn đầu một vị thiên tài, gọi Hàn lăng, tu vi sớm đã đạt đến Hợp Linh cảnh trung kỳ, tiếp gần một tháng không thấy, rõ ràng mạnh hơn, đã sau khi đột phá kỳ."),t("br"),t("br"),n._v("Còn không có ra tay, liền cho người ta một loại mạnh mẽ đến cực điểm cảm giác."),t("br"),t("br"),n._v("Hắn đối diện, là cái thanh niên áo xám, thực lực của người này, đồng dạng là Hợp Linh cảnh hậu kỳ, nhưng sức chiến đấu dường như mạnh hơn, Hàn lăng tới giao thủ liên tục bại lui, đã thối lui đến lôi đài biên giới."),t("br"),t("br"),n._v("“Hàn sư cố lên!”"),t("br"),t("br"),n._v("“Không thể thua nữa...”"),t("br"),t("br"),n._v("“Đúng vậy a, lại thua, chúng ta liền thật chỉ có thể nghe bọn họ...”"),t("br"),t("br"),n._v("..."),t("br"),t("br"),n._v("Phía dưới La Tuyền đám người từng cái tất cả đều xiết chặt, vẻ mặt đỏ lên, ánh mắt bên trong tràn ngập sự không cam lòng."),t("br"),t("br"),n._v("“Làm cái gì vậy?”"),t("br"),t("br"),n._v("Thấy mọi người quá mức chú ý chiến đấu, liền hắn đi tới cũng không phát hiện, Trương Huyền gãi gãi đầu."),t("br"),t("br"),n._v("Làm sao đánh nhau?"),t("br"),t("br"),n._v("“Ha ha, đừng vùng vẫy, nhanh lên nhận thua đi! Ta tại Phong sư huynh trong tay liền ba chiêu đều đi không đến, ngay cả ta đều đánh không lại, còn muốn khiêu chiến sư huynh, nằm mơ đi!”"),t("br"),t("br"),n._v("Chiêu chiêu tới gần, Hàn lăng đối diện thanh niên vẻ mặt đắc ý, cất tiếng cười to: “Yên tâm, theo chúng ta, có chỗ tốt của các ngươi, nếu như lại ngu xuẩn mất khôn, cũng đừng trách ta không khách khí!”"),t("br"),t("br"),n._v("Tiếng nói chuyện bên trong, công kích một chiêu tiếp lấy một chiêu, lực lượng cuồng mãnh, Hàn lăng vẻ mặt trắng bệch, cưỡng ép chống cự."),t("br"),t("br"),n._v("“Hồng sư, đây là có chuyện gì?”"),t("br"),t("br"),n._v("Nhìn một hồi, biết Hàn lăng bại cục đã định, nên lại không kiên trì được mấy chiêu, Trương Huyền mấy bước đi tới dưới đài, nhịn không được nhìn về phía cách đó không xa Hồng sư."),t("br"),t("br"),n._v("“Ah... Trương sư, ngươi không có việc gì? Ngươi trở về, quá tốt rồi!”"),t("br"),t("br"),n._v("Nhìn thấy Trương sư xuất hiện, Hồng sư hưng phấn mà suýt chút nữa nhảy lên, dưới đài những người khác, bao quát La Tuyền ở bên trong mọi người, cũng đều hưng phấn mà đầy đỏ mặt lên."),t("br"),t("br"),n._v("Thậm chí trước đó bị hắn đánh thành đầu heo tú liên công tử, cũng từng cái hốc mắt đỏ lên, giống như là thấy được thân nhân, không nói ra được kích động."),t("br"),t("br"),n._v("“Làm sao vậy?”"),t("br"),t("br"),n._v("Thấy mọi người bộ dáng này, Trương Huyền tràn đầy nghi hoặc, liền không đến một tháng không thấy, cần thiết hay không?"),t("br"),t("br"),n._v("“Là tân sinh khảo hạch!” Biết hắn không hiểu, Hồng sư vội vàng giải thích: “Khảo hạch hạng mục đã định xuống, là tại Hồng Viễn sơn mạch đi săn...”"),t("br"),t("br"),n._v("Rất nhanh, Hồng sư đem sự tình nói một lần."),t("br"),t("br"),n._v("Nghe xong Trương Huyền cũng coi như hiểu được."),t("br"),t("br"),n._v("Cũng không phải là tham gia danh sư thi đấu, đi vào mười vị trí đầu, liền có thể trực tiếp đi vào danh sư học viện, còn cần thông qua nhập môn khảo hạch, khảo hạch nội dung mỗi lần đều không giống nhau, từng có quan, có xông trận, có văn thí... Mà năm nay, là đi săn."),t("br"),t("br"),n._v("Đi săn địa điểm, tại Hồng Viễn sơn mạch Lôi Viễn phong."),t("br"),t("br"),n._v("Lôi Viễn phong là dựa vào gần danh sư học viện một chỗ sơn mạch to lớn, dốc đứng như đao, khó mà trèo đi, coi như Hợp Linh cảnh cường giả, muốn đi lên đều phi thường khó khăn, huống chi còn muốn ở trong đó đi săn."),t("br"),t("br"),n._v("Săn giết linh thú cấp bậc khác biệt, lấy được thành tích khác biệt, lấy một ngày làm giới hạn, săn giết một đầu Trọc Thanh cảnh đỉnh phong linh thú, liền coi như thông qua."),t("br"),t("br"),n._v("Mặc dù linh thú có thể vượt cấp chiến đấu, nhưng bằng mượn mọi người danh sư nhãn lực cùng Hợp Linh cảnh thực lực, săn giết loại này cấp bậc vẫn là rất nhẹ nhàng, nhưng... Toàn bộ sơn mạch linh thú số lượng có hạn, hơn nữa linh thú biết phải bị săn giết, khẳng định sẽ chạy trốn, hoặc là ẩn nấp, không phải dễ dàng như vậy tìm kiếm."),t("br"),t("br"),n._v("Lại thêm, săn giết số lượng càng nhiều, vượt qua hợp cách giá trị, có thể hối đoái học phần... Rất dễ dàng gây nên tân sinh ở giữa cướp đoạt."),t("br"),t("br"),n._v("Càng quan trọng hơn là, bên trong dãy núi linh thú, không riêng gì Trọc Thanh cảnh, còn có Hợp Linh cảnh, Kiều Thiên cảnh, thậm chí cấp bậc cao hơn, tiến vào bên trong, không chỉ muốn đi săn, còn muốn phòng ngừa bị linh thú giết ngược lại!"),t("br"),t("br"),n._v("Mỗi lần khảo hạch, thương vong tân sinh đều có không ít."),t("br"),t("br"),n._v("Có thể nói, chỉ muốn khảo hạch bắt đầu, chính là một trận chiến đấu, đối danh sư tâm tính, thực lực, ứng biến, nhãn lực... Rất nhiều phương diện tiến hành khảo nghiệm."),t("br"),t("br"),n._v("Chỉ có cường giả chân chính, mới có thể trổ hết tài năng, một tiếng hót lên làm kinh người, thực lực yếu hoặc là tâm tính không tốt, chỉ sợ đi vào không bao lâu, liền sẽ tâm tính sụp đổ, cuối cùng thảm bại mà quay về."),t("br"),t("br"),n._v("Lần khảo hạch này, chính là một trận khôn sống mống chết khảo nghiệm, không được cũng chỉ có thể rời khỏi!"),t("br"),t("br"),n._v("Vì tốt hơn thông qua, tránh khỏi nguy hiểm, không ít đế quốc, vừa đến liền tạo thành liên minh."),t("br"),t("br"),n._v("Người trong liên minh nhiều, coi như gặp được thực lực mạnh tân sinh cướp đoạt, cùng cường đại linh thú, cũng có thể chống chọi."),t("br"),t("br"),n._v("Bất quá, kết minh có lợi cũng có hại, chỗ tốt tự nhiên không cần phải nói, có thể để cho tham gia khảo hạch người càng thêm an toàn, chỗ xấu chính là nhiều người, linh thú rất dễ dàng phát hiện, trực tiếp chạy trốn, coi như may mắn bắt lấy, phân phối lên cũng có cái chủ thứ, tuần tự vấn đề."),t("br"),t("br"),n._v("Làm chủ, trước hưởng thụ phân phối, có khả năng kết thúc mỗi ngày, đến khảo hạch kết thúc, làm phụ một đầu đều không được đến, cuối cùng toàn bộ thảm tao đào thải!"),t("br"),t("br"),n._v("Có thể nói, có thể hay không thông qua, liền muốn nhìn đi săn thành quả như thế nào, cùng làm chủ thế lực phân chia như thế nào, do đó, vì lấy được phải chủ động quyền, chỉ cần liên minh, đều muốn lấy được làm chủ địa vị."),t("br"),t("br"),n._v("Cùng mọi người đối chiến là đến từ kim hải nhị đẳng đế quốc danh sư, hai bên quyết định liên minh, đang tại tranh đoạt liên minh chủ phụ quyền, lấy thực lực định thắng thua."),t("br"),t("br"),n._v("“Tổng cộng so mấy trận? Chúng ta bên này đã so mấy trận?”"),t("br"),t("br"),n._v("Nghe xong giải thích, Trương Huyền xem như hiểu được, nhịn không được hỏi."),t("br"),t("br"),n._v("Nếu như chỉ là thua một nửa, hắn hiện tại tới, có lẽ còn có thể cứu giúp cứu giúp."),t("br"),t("br"),n._v("Convert by: ThấtDạ"),t("br"),t("br"),t("br"),t("br")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("a",{attrs:{href:"./chap-701"}},[n._v("Chương trước")]),n._v("  |  "),t("a",{attrs:{href:"./chap-703"}},[n._v("Chương sau")])])])}),[],!1,null,null,null);h.default=g.exports}}]);