(window.webpackJsonp=window.webpackJsonp||[]).push([[1969],{2325:function(n,h,t){"use strict";t.r(h);var i=t(44),g=Object(i.a)({},(function(){var n=this,h=n.$createElement,t=n._self._c||h;return t("ContentSlotsDistributor",{attrs:{"slot-key":n.$parent.slotKey}},[t("h1",{attrs:{id:"chuong-694-mot-ngon-tay"}},[t("a",{staticClass:"header-anchor",attrs:{href:"#chuong-694-mot-ngon-tay"}},[n._v("#")]),n._v(" Chương 694: Một ngón tay")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("br"),t("br"),n._v("Chương 694: Một ngón tay"),t("br"),t("br"),n._v("“Dựa theo bình thường yêu cầu là, so mười trận, thắng sáu trận người làm chủ...” Hồng sư nói: “Trên đài Hàn Lăng so chính là thứ mười trận...”"),t("br"),t("br"),n._v("“Có thể so sánh đến thứ mười trận, nói rõ đều có thắng bại...” Nghe được đây là thứ mười trận, Trương Huyền thở phào nhẹ nhõm."),t("br"),t("br"),n._v("Nếu như đối phương một mực lấy được nếu thắng, sáu trận liền kết thúc, bây giờ có thể so đến cuối cùng một trận, nói rõ mọi người thế lực ngang nhau."),t("br"),t("br"),n._v("“Đều có thắng bại... Không phải!”"),t("br"),t("br"),n._v("Hồng sư hơi đỏ mặt, mặt mũi xấu hổ: “Chúng ta là so mười trận, nhưng một mực thua, hơn nữa... Cũng đều bại bởi cùng là một người!”"),t("br"),t("br"),n._v("“Bại bởi cùng là một người? Ngươi nói... Trên đài cái tên này ngay cả đánh mười trận, một lần đều không có thua?”"),t("br"),t("br"),n._v("Trương Huyền con mắt trợn tròn."),t("br"),t("br"),n._v("“Đúng vậy a...”"),t("br"),t("br"),n._v("Hồng sư che cái trán, những người khác càng là xấu hổ không ngẩng đầu được lên."),t("br"),t("br"),n._v("Liên tục mười người khiêu chiến một cái, xa luân chiến đều bị đối phương đánh rơi lôi đài, mấu chốt nhất là... Trên đài cái này, rõ ràng còn không phải đối phương mạnh nhất, thật có chút mất mặt xấu hổ."),t("br"),t("br"),n._v("Bành!"),t("br"),t("br"),n._v("Vẻ mặt im lặng, Trương Huyền đang muốn nói chuyện, liền nghe đến trên đài rên lên một tiếng, Hàn Lăng liền phản ứng đều không có kịp phản ứng, bay ngược mà ra, rơi vào trong sân."),t("br"),t("br"),n._v("“Mười trận đều thua... Cái này còn thế nào cứu giúp?”"),t("br"),t("br"),n._v("Nhìn xem nằm trên mặt đất, mặt mũi thống khổ gia hỏa, Trương Huyền mí mắt xoay loạn."),t("br"),t("br"),n._v("Vốn nghĩ còn có thể cứu giúp một cái, hiện tại mười trận đều bại bởi cùng là một người, còn thế nào cứu?"),t("br"),t("br"),n._v("Coi như muốn tìm lý do, cũng tìm không thấy ah!"),t("br"),t("br"),n._v("“Tốt rồi, mười trận kết thúc, các ngươi một lần cũng không thắng, có lời gì nói?”"),t("br"),t("br"),n._v("Đem Hàn Lăng đánh bay, trên đài thanh niên, mỉm cười, nhìn quanh một vòng."),t("br"),t("br"),n._v("“Chúng ta...”"),t("br"),t("br"),n._v("Hồng sư đám người sắc mặt tăng thấu đỏ."),t("br"),t("br"),n._v("Nếu là mười trận thắng hai, ba trận, cũng không cảm thấy ngại nói chúng ta mạnh nhất không đến, hiện tại cũng thua ở cùng là một người, làm sao lối ra?"),t("br"),t("br"),n._v("Hơn nữa, Trương sư vạn nhất cũng không phải là đối thủ, chẳng phải mất mặt càng lớn? Thua càng triệt để hơn?"),t("br"),t("br"),n._v("Trương sư mặc dù thiên phú tuyệt cao, danh sư rất nhiều năng lực bên trong được cho đỉnh tiêm, nhưng dù sao chỉ có Trọc Thanh cảnh đỉnh phong, muốn vượt qua trước mắt cái này một chọi mười, còn lớn hơn hơi thở không gấp gia hỏa, chỉ sợ rất khó, huống chi, cái tên này còn không phải mạnh nhất."),t("br"),t("br"),n._v("(Trương Huyền đột phá đến Hợp Linh cảnh, là biến thành “Thi thể” chuyện sau đó, những người khác cũng không biết rõ tình hình.)"),t("br"),t("br"),n._v("“Không lời nói, cái kia cứ dựa theo ước định, nghe theo Phong sư huynh an bài!” Xanh trẻ măng cười một tiếng, quay đầu ôm quyền: “Phong sư huynh!”"),t("br"),t("br"),n._v("Tiếng nói kết thúc, một thanh niên sải bước đi đi lên."),t("br"),t("br"),n._v("Người này khí tức càng nặng, thực lực mạnh hơn, đã đi đến nửa bước Kiều Thiên cảnh, tùy thời đều có thể đột phá."),t("br"),t("br"),n._v("Vừa rồi thắng liên tiếp mười người thanh niên, tại Nhược Hoan công tử trong mắt coi như rất cường đại, nhưng cùng vị này so sánh, vẫn là kém thật lớn một đoạn."),t("br"),t("br"),n._v("Khó trách Huyễn Vũ đế quốc rất nhiều thiên tài khó mà chống chọi, người này thật muốn xuất thủ, đừng nói mười người xa luân chiến không cách nào chiến thắng, coi như đồng thời ra tay, chỉ sợ cũng phải bị tuỳ tiện đánh tan, ngăn cản không nổi."),t("br"),t("br"),n._v("“Đã chúng ta liên minh, trước hết đem quy củ nói rõ ràng!”"),t("br"),t("br"),n._v("Đứng trên đài, Phong sư huynh nhìn quanh một vòng, thanh âm như là sắt thép va chạm: “Sau ba ngày, đi săn bắt đầu, chư vị muốn nghe từ ta thống nhất an bài, thú đến con mồi, trước có chúng ta kim hải đế quốc lựa chọn! Yên tâm đi, chỉ muốn mọi người lục lực đồng tâm, toàn bộ thông qua cũng không là vấn đề!”"),t("br"),t("br"),n._v("“Trước do bọn hắn lựa chọn?”"),t("br"),t("br"),n._v("“Nếu như đi săn số lượng không đủ, chúng ta há không toàn bộ muốn bị đào thải?”"),t("br"),t("br"),n._v("..."),t("br"),t("br"),n._v("Mặc dù rõ ràng, liên minh làm chủ, muốn hưởng thụ đặc quyền, nhưng đối phương trực tiếp đem lời nói rõ ràng ra, vẫn là để mọi người từng cái nắm đấm xiết chặt."),t("br"),t("br"),n._v("Liên minh có chỗ tốt, chính là gặp được lợi hại linh thú, có thể đủ ứng đối, không đến mức nguy hiểm, có thể chỗ xấu chính là, con mồi phân phối không khỏi bản thân, một khi cùng ngày đi săn số lượng không đủ, bọn hắn Huyễn Vũ đế quốc liền vô cùng có khả năng toàn bộ bị đào thải rơi."),t("br"),t("br"),n._v("“Thế nào, còn không nguyện ý? Chúng ta Phong sư huynh, loại thực lực này, nguyện ý cùng các ngươi liên minh, là vận may của các ngươi, đừng không biết tốt xấu!” Thấy mọi người dưới đài từng cái tràn đầy buồn bực, vừa rồi người thanh niên kia, lạnh hừ một tiếng."),t("br"),t("br"),n._v("Dựa theo thực lực của bọn hắn, kỳ thật không cùng Huyễn Vũ đế quốc những người này liên minh cũng được, nhưng liên hợp nhiều, đối bọn hắn cũng có ích lợi rất lớn."),t("br"),t("br"),n._v("Lần khảo hạch này, học viện không ít lão sư đều nhìn, nếu như biểu hiện nổi bật, rất dễ dàng bị ưu ái, từ đó lau mắt mà nhìn, một bước lên mây."),t("br"),t("br"),n._v("“Ngươi...”"),t("br"),t("br"),n._v("Nghe được đối phương, mọi người từng cái vẻ mặt đỏ lên, muốn phản bác, nhưng phản bác không ra."),t("br"),t("br"),n._v("Ai bảo tài nghệ không bằng người!"),t("br"),t("br"),n._v("“Là có chút không nguyện ý...”"),t("br"),t("br"),n._v("Mọi người ở đây khó chịu thời điểm, Trương Huyền thanh âm nhàn nhạt vang lên."),t("br"),t("br"),n._v("“Ừm? Có ý tứ gì? Đường đường Huyễn Vũ đế quốc danh sư, quyết định liên minh, còn muốn đổi ý?” Thấy có người chen vào nói, thanh niên nhướng mày."),t("br"),t("br"),n._v("Tất cả mọi người là danh sư, nhân vật có mặt mũi, đã đáp ứng liên minh, lại tiến hành tỷ thí, chẳng lẽ, bởi vì đánh không lại, muốn nuốt lời?"),t("br"),t("br"),n._v("Thật muốn như vậy, còn không có tham gia khảo hạch, liền muốn làm trò hề cho thiên hạ."),t("br"),t("br"),n._v("“Nuốt lời ngược lại không đến nỗi, chỉ là có một ý tưởng...” Trương Huyền nói."),t("br"),t("br"),n._v("“Ý nghĩ?”"),t("br"),t("br"),n._v("Trương Huyền nói: “Không sai, ta cũng là Huyễn Vũ đế quốc lần này tới danh sư, vừa rồi các ngươi tỷ thí thời điểm, cũng không biết rõ tình hình, có điều, đã đều so thử qua, ta cũng nói không nên lời cái gì, chỉ là muốn công bằng một chút, thú đến linh thú, mọi người cùng nhau chia đều!”"),t("br"),t("br"),n._v("“Chia đều? Đã liên minh liền muốn có chủ phụ phân chia, có chủ phụ, liền không khả năng công bằng!” Thanh niên cười lạnh."),t("br"),t("br"),n._v("“Ta cũng biết, vừa rồi tỷ thí thua, phân ra chủ phụ, muốn có được công bằng rất khó, như vậy đi!”"),t("br"),t("br"),n._v("Trương Huyền cười cười, hai tay chắp sau lưng: “Để ngươi cái này Phong sư huynh ra tay, nếu như hắn có thể trong tay ta đi ba chiêu... Công bằng sự tình, ta liền không lại đi nâng, các ngươi muốn làm sao phân phối liền làm sao phân phối! Thậm chí, đi săn đến linh thú, chúng ta có thể một cái cũng không được!”"),t("br"),t("br"),n._v("“Ngươi nói cái gì?”"),t("br"),t("br"),n._v("Thanh niên con mắt một cái nheo lại."),t("br"),t("br"),n._v("Cái tên này thế mà để Phong sư huynh ra tay, còn có thể trong tay hắn đi ba chiêu?"),t("br"),n._v("Gặp qua cuồng, chưa thấy qua như thế cuồng!"),t("br"),t("br"),n._v("Ngươi biết chúng ta Phong sư huynh thực lực gì? Ngay ở chỗ này nói khoác không biết ngượng?"),t("br"),t("br"),n._v("Còn ba chiêu, liền ngươi cái bộ dáng này, có thể hay không chống Phong sư huynh một chiêu, đều vẫn là không biết chi trời!"),t("br"),t("br"),n._v("“Làm sao? Không có tự tin? Nếu không như vậy đi, một chiêu, chỉ cần các ngươi Phong sư huynh có thể trong tay ta chống một chiêu, đi săn đến linh thú, chúng ta Huyễn Vũ đế quốc một cái không muốn!”"),t("br"),t("br"),n._v("Thấy đối phương chần chờ, Trương Huyền còn tưởng rằng điều kiện nâng quá cao, nhịn không được đổi giọng."),t("br"),t("br"),n._v("“Mẹ nó!”"),t("br"),t("br"),n._v("Nhìn thấy hắn nghiêm túc bộ dạng, thanh niên suýt chút nữa không có tại chỗ tức điên!"),t("br"),t("br"),n._v("Vị kia Phong sư huynh cũng mắt tối sầm lại, thiếu chút nữa ngất đi."),t("br"),t("br"),n._v("Thực lực của hắn, đi đến nửa bước Kiều Thiên cảnh, coi như so với một chút năm thứ hai lão sinh, đều không kém nhiều lắm, mấy cái này từ Huyễn Vũ đế quốc xuất hiện gia hỏa, lại muốn một chiêu liền đánh bại hắn?"),t("br"),t("br"),n._v("Nói đùa cái gì?"),t("br"),t("br"),n._v("Hai người tức giận sắp hôn mê, Nhược Hoan công tử, La Tuyền đám người, thì suýt chút nữa không có thổ huyết."),t("br"),t("br"),n._v("Bọn hắn đã sớm đoán ra, Trương sư đi tới, khẳng định sẽ kiếm chuyện, có thể làm giấc mơ đều không nghĩ tới, nói ra lời này!"),t("br"),t("br"),n._v("Vừa rồi người thanh niên kia, bọn hắn nhiều người như vậy đều đánh không lại, vị này Phong sư huynh tự nhiên lợi hại hơn, ngươi muốn một chiêu đánh bại... Làm sao có thể?"),t("br"),t("br"),n._v("Một khi thật thua, chẳng lẽ, chúng ta thật trắng cho người khác hỗ trợ, một đầu con mồi cũng đừng?"),t("br"),t("br"),n._v("Thật muốn như thế, tất cả mọi người sẽ thảm tao đào thải..."),t("br"),t("br"),n._v("“Thế nào, một chiêu còn nhiều thêm? Nếu không như vậy đi, ta dùng một ngón tay... Như vậy cũng có thể đi! Thật sự nếu không dám, ta cũng không có biện pháp...”"),t("br"),t("br"),n._v("Một chiêu đều ngại nhiều lời nói, bản thân cũng không thể không cần chiêu số liền đem nó đánh bại đi!"),t("br"),t("br"),n._v("Mặc dù như thế cũng có thể làm được, nhưng khẳng định sẽ bại lộ Vu hồn cùng thủ đoạn khác, ừm, vẫn là khiêm tốn một chút tốt."),t("br"),t("br"),n._v("Vừa tới đến Hồng Viễn đế quốc, vẫn là đừng bại lộ nhiều lắm."),t("br"),t("br"),n._v("Trương Huyền trên mặt khó xử."),t("br"),t("br"),n._v("“Phốc!”"),t("br"),t("br"),n._v("Thanh niên cùng Phong sư huynh, suýt chút nữa không có máu tươi phun ra."),t("br"),t("br"),n._v("Nhiều em gái ngươi ah!"),t("br"),t("br"),n._v("Hơn nữa, ngươi vẻ mặt khó xử có ý tứ gì?"),t("br"),t("br"),n._v("Chẳng lẽ ta cứ như vậy không đỡ nổi một đòn, để ngươi buồn thành như vậy?"),t("br"),t("br"),n._v("“Tốt, ta ngược lại muốn xem xem, ngươi đến cùng làm sao một ngón tay đem ta đánh bại...”"),t("br"),t("br"),n._v("Thực sự nhịn không được, Phong sư huynh hét dài một tiếng."),t("br"),t("br"),n._v("“Sư huynh... Cái này không biết trời cao đất rộng gia hỏa, để cho ta tới giáo huấn chính là...” Thấy sư huynh nổi giận, thanh niên hướng về phía trước."),t("br"),t("br"),n._v("“Không cần, ta muốn đích thân nhìn xem, như thế khẩu xuất cuồng ngôn, đến cùng có thủ đoạn gì!” Phong sư huynh ánh mắt như điện, lạnh lùng nhìn qua."),t("br"),t("br"),n._v("Cái tên này như thế nói lớn không ngượng, khẳng định có chỗ ỷ lại, hắn cũng muốn nhìn một chút, đến cùng là nói càn, vẫn là thật có thực lực."),t("br"),t("br"),n._v("“Vị bằng hữu này, lên đây đi, nếu như ngươi thật có thể thắng được ta, liên minh sự tình, như vậy coi như không có gì, nếu như thua, nhớ kỹ ngươi lời nói, đi săn lấy được linh thú, các ngươi một đầu không muốn!”"),t("br"),t("br"),n._v("Hai mắt nheo lại, Phong sư huynh hất lên ống tay áo."),t("br"),t("br"),n._v("“Tốt!”"),t("br"),t("br"),n._v("Nghe được đối phương đáp ứng, Trương Huyền đi lên lôi đài."),t("br"),t("br"),n._v("“Cái này...”"),t("br"),t("br"),n._v("“Trương sư có thể thắng sao?”"),t("br"),t("br"),n._v("“Ta cũng không biết...”"),t("br"),t("br"),n._v("Nhìn thấy Trương sư thế mà cùng đối phương đánh xuống dạng này cá cược, tất cả mọi người từng cái hai mặt nhìn nhau."),t("br"),t("br"),n._v("Một ngón tay đánh bại vị này Phong sư huynh?"),t("br"),t("br"),n._v("Trương sư ngươi là nghiêm túc?"),t("br"),t("br"),n._v("Mặc dù ta biết chúng ta thua, ngươi rất gấp, nhưng vụ cá cược này... Cũng quá lớn!"),t("br"),t("br"),n._v("“Phong sư huynh, vẫn là ta đến giáo huấn cái tên này đi...”"),t("br"),t("br"),n._v("Thấy Phong sư huynh tức giận như vậy, thanh niên cũng có chút nhịn không được, lần nữa hướng về phía trước: “Nếu như ta thua, sư huynh lại đến cũng không muộn!”"),t("br"),t("br"),n._v("Cái tên này nói lớn lối như thế, chỉ sợ thật có chút thực lực, không bằng hắn trước thăm dò một cái, nếu như yếu, trước hết đánh một trận trút giận, mạnh, sư huynh cũng tốt có chuẩn bị tâm lý."),t("br"),t("br"),n._v("“Cái này...” Phong sư huynh chần chờ, đang muốn nói chuyện, liền nghe đến đối diện thanh niên hơi có vẻ không nhịn được thanh âm vang lên: “Nếu như cảm thấy sợ hãi, các ngươi hai cái cùng lên đi, ta ra... Hai ngón tay!”"),t("br"),t("br"),n._v("Không nghe lời này còn tốt, nghe được về sau, Phong sư huynh cùng thanh niên, trước mắt đồng thời một đen."),t("br"),t("br"),n._v("“Không cần, liền để ta xem một chút, ngươi đến cùng loại thực lực nào!”"),t("br"),t("br"),n._v("Hừ lạnh một tiếng, Phong sư huynh bỗng nhiên xông về phía trước."),t("br"),t("br"),n._v("Kẻ trước mắt này nói chuyện quá khinh người, hắn sợ nói tiếp, sẽ tươi sống tức chết."),t("br"),t("br"),n._v("Bàn chân trên mặt đất đạp mạnh, cả người như là băng băng mà tới mãnh thú, còn chưa tới đến gần, liền cho người ta một loại Long Hổ xu thế."),t("br"),t("br"),n._v("Nửa bước Kiều Thiên cảnh thực lực hoàn toàn phát huy, tiếng gió rít gào, cho người ta một loại, Hắc Vân tiếp cận, khó mà chống chọi cảm giác."),t("br"),t("br"),n._v("“Thật mạnh!”"),t("br"),t("br"),n._v("Hồng sư đám người lông mày nhịn không được nhảy lên."),t("br"),t("br"),n._v("Loại thực lực này, liền coi như bọn họ là Kiều Thiên cảnh sơ kỳ, chỉ sợ đều khó mà chống chọi, chỉ có thể tạm tránh mũi nhọn."),t("br"),t("br"),n._v("Vội vàng quay đầu hướng cách đó không xa thanh niên nhìn lại, muốn nhìn một chút hắn đến cùng ứng đối ra sao, chỉ thấy hắn lắc đầu, duỗi ra một ngón tay, lảo đảo hướng về phía trước điểm đi qua."),t("br"),t("br"),n._v("Convert by: ThấtDạ"),t("br"),t("br"),t("br"),t("br")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("a",{attrs:{href:"./chap-702"}},[n._v("Chương trước")]),n._v("  |  "),t("a",{attrs:{href:"./chap-704"}},[n._v("Chương sau")])])])}),[],!1,null,null,null);h.default=g.exports}}]);