(window.webpackJsonp=window.webpackJsonp||[]).push([[197],{549:function(n,h,g){"use strict";g.r(h);var t=g(44),c=Object(t.a)({},(function(){var n=this,h=n.$createElement,g=n._self._c||h;return g("ContentSlotsDistributor",{attrs:{"slot-key":n.$parent.slotKey}},[g("h1",{attrs:{id:"chuong-1154-ngo-su-phan-tich"}},[g("a",{staticClass:"header-anchor",attrs:{href:"#chuong-1154-ngo-su-phan-tich"}},[n._v("#")]),n._v(" Chương 1154: Ngô sư phân tích")]),n._v(" "),g("hr"),n._v(" "),g("p",[n._v("Chương 1154: Ngô sư phân tích"),g("br"),g("br"),n._v("“Chiến sư?”"),g("br"),g("br"),n._v("Đông đảo độc sư, hai mặt nhìn nhau, không rõ ràng cho lắm."),g("br"),g("br"),n._v("“Không sai, chỉ cần các ngươi trở thành Chiến sư, liền có thể quang minh chính đại đi tại Thanh Nguyên thành, mà không cần lo lắng, đủ loại chỉ trích cùng phiền phức! Liền rốt cuộc không cần e ngại, có nhân sự hậu báo lại! Càng không cần lo lắng, Danh Sư đường hỏi ý!”"),g("br"),g("br"),n._v("Trương Huyền nhẹ nhàng cười một tiếng."),g("br"),g("br"),n._v("“Cái này...” Đông đảo độc sư hơi đỏ mặt, nguyên một đám xiết chặt nắm đấm."),g("br"),g("br"),n._v("Thân là độc sư, nguyện vọng lớn nhất liền là có thể không bị mang theo mặt khác ánh mắt, đi lại tại ánh sáng bên trong... Nếu quả thật có thể thực hiện, trước mắt vị này Tôn Cường tiền bối, thật tương đương cứu mạng của bọn hắn, cho lần thứ hai cơ hội sống lại."),g("br"),g("br"),n._v("“Nhưng mà... Chúng ta không phải danh sư, muốn trở thành Chiến sư, gần như không có khả năng đi...” Cho phép du tràn đầy lo lắng nhìn lại."),g("br"),g("br"),n._v("Những người khác cũng mang theo tương đồng nghi vấn."),g("br"),g("br"),n._v("Chiến sư, là danh sư một cái chi nhánh, dưới tình huống bình thường, theo danh sư chi bên trong chọn lựa, bọn họ chỉ là một đám độc sư..."),g("br"),g("br"),n._v("“Chiến sư, tám chín phần mười là danh sư, bất quá... Cũng sẽ đưa vào một chút đặc thù nhân tài, chúng ta độc sư, mặc dù danh khí không tốt lắm, nhưng chân chính chiến đấu, độc vật rơi, sức chiến đấu cũng hết sức kinh người! Nếu như có thể đối chiến sư đường thu hút, trở thành một thành viên trong đó, dù là chỉ thuộc về quân dự bị, vắng mặt bện, cũng chờ với có chính thức thân phận, không cần khổ cực như vậy!”"),g("br"),g("br"),n._v("Trương Huyền cười gật đầu."),g("br"),g("br"),n._v("Chiến sư đường là có thu hút nhân tài đặc thù quy định, có điều, đại bộ phận đều là Trịnh Dương loại trình độ này, Hứa trưởng lão đám người, nói thật, vẫn là kém chút."),g("br"),g("br"),n._v("Bất quá, trở thành quân dự bị, không quy thuộc Chiến sư đường tổng bộ đan, cũng không phải là không thể được, một khi về sau lập công, lại trình báo chính thức, cũng không muộn."),g("br"),g("br"),n._v("“Nếu như có thể như vậy tự nhiên tốt nhất, nhưng mà... Công pháp của chúng ta, cùng tu luyện, sợ rằng sẽ bị danh sư kháng cự, ta sợ... Ở chung lâu, sớm muộn gì đều sẽ bộc phát mâu thuẫn!” Hứa trưởng lão nói tiếp."),g("br"),g("br"),n._v("“Ta biết lo lắng của các ngươi, có điều, chuyện này, tổ sư đã sớm an bài, đây là tổ sư lưu lại tân tu luyện công pháp, các ngươi chỉ cần dựa theo cái này tu luyện, tấn cấp tốc độ chẳng những càng nhanh, còn không cần sử dụng một chút làm trái đạo đức thủ đoạn...”"),g("br"),g("br"),n._v("Cổ tay khẽ đảo, Trương Huyền đưa qua mấy quyển sách."),g("br"),g("br"),n._v("Chính là Thiên Đạo độc công tinh giản bản."),g("br"),g("br"),n._v("Lúc trước tại Độc điện sưu tập hết, liền viết lưu lại, mục đích là vì về sau có cơ hội truyền cho người khác."),g("br"),g("br"),n._v("Cho phép du đám người đầy mang nghi ngờ tiếp nhận, chỉ nhìn thoáng qua, nguyên một đám kích động vẻ mặt đỏ lên, hô hấp đều kìm lòng không được dồn dập lên."),g("br"),g("br"),n._v("Bọn họ những người này ở đây dùng độc bên trên, ít nhất đều đắm chìm trên trăm năm, biết độc công ưu khuyết, trước mắt mấy bản này, nội dung mặc dù không nhiều, nhưng từng chữ châu ngọc, mỗi một câu nói đều có khắc sâu ý nghĩa, nhắm thẳng vào đại đạo hạch tâm."),g("br"),g("br"),n._v("Dựa theo cái này tu luyện, có thể đoán được, bọn họ đối độc nắm giữ càng ngày càng mạnh, thực lực cũng càng thêm tinh thuần mạnh mẽ."),g("br"),g("br"),n._v("Cùng cấp bậc, sức chiến đấu bạo tăng hai, gấp ba, đều là hết sức dễ dàng!"),g("br"),g("br"),n._v("“Đa tạ tổ sư ban cho công pháp...”"),g("br"),g("br"),n._v("Tràn đầy kích động, rất nhiều độc sư đồng loạt quỳ rạp xuống đất."),g("br"),g("br"),n._v("Có bộ công pháp này, bọn họ cũng không cần tại khắp nơi vất vả, tìm kiếm đủ loại cổ trùng, đủ loại kịch độc, thậm chí làm ra một chút vi phạm đạo đức sự tình."),g("br"),g("br"),n._v("“Có thể gia nhập Chiến sư đường, là Hình đường chủ nhân ái, ta hi vọng các ngươi có thể nghe theo phân phó, không cần hành động theo cảm tính, nếu không, liền xem như ta, cũng không thể nào cứu được các ngươi!”"),g("br"),g("br"),n._v("Đem nên nói nói xong, Trương Huyền vẻ mặt nghiêm túc, nói."),g("br"),g("br"),n._v("“Vâng!”"),g("br"),g("br"),n._v("Mọi người gật đầu."),g("br"),g("br"),n._v("Độc sư cứ việc dùng độc đáng sợ, coi thường quần công, nhưng đối mặt Chiến sư, vẫn là lực có không bằng."),g("br"),g("br"),n._v("Chiến sư không chỉ chiến lực mạnh mẽ, càng quan trọng hơn là, bọn họ tu luyện cực kỳ khắc khổ, rất nhiều người đều có rất mạnh kháng độc tính... Cùng cấp bậc độc sư, cùng Chiến sư đối chiến, khả năng liền độc cũng không kịp lấy ra, liền sẽ bị trực tiếp giết chết!"),g("br"),g("br"),n._v("Coi như sớm bố trí, đối phương cũng có khả năng tại lúc sắp chết, đem hắn giết chết."),g("br"),g("br"),n._v("“Được rồi, đem thư tịch bên trên nội dung nhớ kỹ, sau đó thiêu hủy... Trừ bọn ngươi ra những người này, ta không hy vọng bộ công pháp này lại truyền đi!” Trương Huyền nói tiếp."),g("br"),g("br"),n._v("“Vâng!” Mọi người đồng ý."),g("br"),g("br"),n._v("Cứ việc nhưng mà bản rút gọn độc công, cũng dị thường đáng sợ, tin tức rò rỉ, tất nhiên sẽ gây nên vô số người ngấp nghé."),g("br"),g("br"),n._v("..."),g("br"),g("br"),n._v("Trương Huyền bên này hợp nhất mọi người, Hình đường chủ cùng Ngô sư đứng tại địa lao bên ngoài."),g("br"),g("br"),n._v("“Ngô sư, Chiến sư đường nhận lấy độc sư... Truyền đi như thế nào cùng Danh Sư đường bàn giao?” Hình đường chủ cau mày."),g("br"),g("br"),n._v("Hắn vốn là không có ý định đáp ứng, kết quả Ngô sư mở miệng, liền không tốt phản bác."),g("br"),g("br"),n._v("Mặc dù lực chiến đấu của hắn hơn xa đối phương, nhưng muốn nói đối chuyện nắm chắc cùng cái nhìn, tuyệt đối xa kém xa."),g("br"),g("br"),n._v("Danh sư, không chỉ giảng bài mạnh mẽ, càng quan trọng hơn là, tại thấy xa bên trên cũng phải vượt qua người bình thường rất nhiều."),g("br"),g("br"),n._v("“Ngươi có phải hay không cảm thấy, Trương sư cho các ngươi Chiến sư đường, mang đến phiền phức?”"),g("br"),g("br"),n._v("Nghe được hắn hỏi thăm, Ngô sư lắc đầu, nhìn qua."),g("br"),g("br"),n._v("“Cái này...” Hình đường chủ mặt mũi xấu hổ."),g("br"),g("br"),n._v("Mặc dù cũng không nói ra miệng, nhưng mà trong lòng thật là nghĩ như vậy."),g("br"),g("br"),n._v("Độc sư nguyên một đám kiêu căng khó thuần, phiền phức cực kỳ, hơn nữa hạ độc thủ đoạn khó lòng phòng bị, nếu quả thật làm cho đối phương lưu tại Chiến sư đường, còn không gà bay chó chạy, lộn xộn?"),g("br"),g("br"),n._v("“Kỳ thật ngươi không rõ ràng Trương sư khổ tâm! Hắn làm như vậy cũng là vì Chiến sư đường, vì chúng ta nhân tộc!”"),g("br"),g("br"),n._v("Thấy hắn quả nhiên nghĩ như vậy, Ngô sư cười khổ."),g("br"),g("br"),n._v("“Vì Chiến sư đường cùng nhân tộc?” Hình đường chủ tràn đầy không hiểu."),g("br"),g("br"),n._v("Đám người kia, là một đống đại phiền toái, làm sao có thể là vì bọn họ?"),g("br"),n._v("“Liền biết ngươi không hiểu!” Thấy hắn bộ biểu tình này, Ngô sư lắc đầu: “Cái kia trước tiên ta hỏi ngươi... Đem bọn này độc sư thả ra, ngươi là đem bọn hắn giam lỏng vẫn là chém giết?”"),g("br"),g("br"),n._v("“Cái này... Bọn họ lại không có lầm lỗi, càng không có độc chết nhân tộc... Tại sao phải giam lỏng chém giết?” Hình đường chủ sững sờ."),g("br"),g("br"),n._v("Danh Sư đường, Chiến sư đường mặc dù quản lý toàn bộ đại lục, nhưng cũng là coi trọng quy tắc, không phải cường đạo."),g("br"),g("br"),n._v("Bọn này độc sư, bản thân liền là người bị hại, lại không làm ra thương thiên hại lí sự tình, liền coi là thực lực bọn hắn mạnh mẽ, chưởng khống quyền lợi, cũng không thể tùy tiện giam lỏng cùng giết chết đi!"),g("br"),g("br"),n._v("“Đúng vậy a, không thể giam lỏng, không thể chém giết... Độc của bọn họ điện lại hoàn toàn bại lộ, bị hủy diệt, lại không cách nào trở về... Nhiều như vậy độc sư, rải rác tại toàn bộ Thanh Nguyên đế quốc, thật muốn mang đến tai hoạ làm sao bây giờ?” Ngô sư nói."),g("br"),g("br"),n._v("“Cái này...”"),g("br"),g("br"),n._v("Hình đường chủ nói không ra lời."),g("br"),g("br"),n._v("Nhiều như vậy độc sư, tích tụ cùng một chỗ, thật vây quét cũng tốt chém giết... Nhưng mà một khi phân tán, dung nhập toàn bộ đế quốc, còn muốn tìm liền gần như không có khả năng."),g("br"),g("br"),n._v("Những người này, thật muốn lặng lẽ hạ độc, đối phó người bình thường, đến lúc đó, coi như tìm tới hung thủ, cũng đã chậm."),g("br"),g("br"),n._v("“Đem bọn hắn hợp nhất đến Chiến sư đường, cầm giữ phạm vi hoạt động của bọn họ, tránh khỏi xuất hiện càng lớn tai hoạ, Trương sư làm như vậy, kỳ thật tương đương biến tướng giam lỏng... Bất quá, trên danh nghĩa liền dễ nghe nhiều, không chỉ không tổn hại hại thanh danh của chúng ta, hơn nữa còn có thể lộ ra Danh Sư đường dung người độ lượng, nhất cử lưỡng tiện!”"),g("br"),g("br"),n._v("“Cái này...”"),g("br"),g("br"),n._v("Hình đường chủ miệng há mở, nói không ra lời."),g("br"),g("br"),n._v("Đối phương như vậy vừa phân tích, đúng là như thế."),g("br"),g("br"),n._v("Bọn này độc sư một khi gia nhập Chiến sư đường, liền phải bị Chiến sư đường quy định ước thúc, lại không có thể tự do tản mạn, quả thực tương đương giam lỏng ở đây, lại không cách nào ra ngoài làm ác."),g("br"),g("br"),n._v("“Đương nhiên đây chỉ là Trương sư cái mục đích thứ nhất... Hắn mục đích thứ hai, thì là chân tâm thật ý cho các ngươi Chiến sư đường suy nghĩ!”"),g("br"),g("br"),n._v("Ngô sư nói tiếp: “Hơn hai mươi năm trước lần kia cùng Dị Linh tộc nhân chiến đấu, Chiến sư đường tổn thất nghiêm trọng đến mức nào, ngươi bây giờ cũng là ký ức vẫn còn mới mẻ đi!”"),g("br"),g("br"),n._v("“Vâng!”"),g("br"),g("br"),n._v("Hình đường chủ thanh âm trầm thấp."),g("br"),g("br"),n._v("Năm đó thanh điền nhất mạch dốc toàn bộ lực lượng, Chiến sư đường bị vây công, trọng thương vô số, ngay cả hắn, đều suýt chút nữa vẫn lạc."),g("br"),g("br"),n._v("Nếu không phải ngang lão ra tay, khẳng định đã sớm chết."),g("br"),g("br"),n._v("“Dị Linh tộc nhân mặc dù có chuẩn bị mà đến, có thể nghĩ muốn vượt qua chúng ta Chiến sư đường nhiều như vậy Chiến sư, cũng không dễ dàng như vậy, nhưng mà, lần kia thảm liệt như vậy, ngươi có lẽ rõ ràng nhất, đã xảy ra biến cố gì!”"),g("br"),g("br"),n._v("Ngô sư vẻ mặt nghiêm túc."),g("br"),g("br"),n._v("Dị Linh tộc nhân có chuẩn bị mà đến, súc thế mà ra, có thể chiến sư đường chưa hề thả lỏng qua, một mực chuẩn bị chiến đấu, dựa theo tình huống bình thường, coi như hai bên giao thủ, chỉ bằng vào Chiến sư đường liền có thể toàn bộ tiêu diệt, không cần Danh Sư đường, tính cả Thanh Nguyên đế quốc toàn bộ ra tay..."),g("br"),g("br"),n._v("Đáng tiếc, ba phe thế lực đều xuất thủ, kết quả... Vẫn như cũ là thắng thảm!"),g("br"),g("br"),n._v("Nguyên nhân chính là như thế, Thanh Nguyên phong hào đế quốc, tại bát đại phong hào đế quốc bên trong, biến thành yếu nhất một cái, hơn hai mươi năm, đều không có khôi phục nguyên khí."),g("br"),g("br"),n._v("“Là... Chí Tôn Thanh Phong tán!”"),g("br"),g("br"),n._v("Hình đường chủ nắm đấm xiết chặt."),g("br"),g("br"),n._v("Lúc trước bọn họ Chiến sư đường người, chẳng biết lúc nào bị xuống một loại gọi là Chí Tôn Thanh Phong tán kịch độc, tất cả Chiến sư, nguyên một đám tất cả đều toàn thân rã rời, sức chiến đấu giảm lớn... Lúc này mới bị đối phương thừa cơ chiến thắng!"),g("br"),g("br"),n._v("Ngay cả hắn, đều không có may mắn thoát khỏi."),g("br"),g("br"),n._v("Chuyện này, như cùng một căn đâm, một mực đâm ở trong lòng, để hắn không cách nào tiêu tan."),g("br"),g("br"),n._v("“Đúng vậy a, chính là loại độc dược này, theo gió mà bay, vô sắc vô vị... Ai cũng không có cách nào phát giác! Liền xem như ta lúc đầu cũng không phát giác được... Vẫn là ruộng Thanh phó đường chủ phát hiện, mới để cho Danh Sư đường tổn thất không như thế lớn... Bởi vì việc này, hắn cũng hoàn toàn nắm trong tay toàn bộ Danh Sư đường, quyền lợi càng lúc càng lớn!”"),g("br"),g("br"),n._v("Ngô sư cảm khái một câu, nói tiếp: “Trương sư, để nhiều như vậy độc sư gia nhập Chiến sư đường, thứ nhất, là muốn cho chúng ta có một nhánh có thể dùng độc đoàn thể chiến đấu, gặp lại loại sự tình này lời nói, liền có thể nhẹ nhõm ứng đối! Thứ hai, có độc sư gia nhập, Chiến sư đường mọi người, cũng có cơ hội tôi luyện bản thân kháng độc tính, về sau Dị Linh tộc nhân thật muốn dùng độc, cũng không trở thành trở tay không kịp...”"),g("br"),g("br"),n._v("“Cái này...”"),g("br"),g("br"),n._v("Hình đường chủ sửng sốt."),g("br"),g("br"),n._v("Quả thực."),g("br"),g("br"),n._v("Có độc sư gia nhập, gặp lại hai mươi năm trước sự tình, tất nhiên có thể sớm phòng bị, không đến mức bị động."),g("br"),g("br"),n._v("“Những thứ này... Thật là Trương sư tính toán tốt?”"),g("br"),g("br"),n._v("Buồn bực một lát, càng nghĩ càng thấy đến, để độc sư gia nhập Chiến sư đường, trăm lợi mà không có một hại, Hình đường chủ nuốt ngụm nước bọt, nhịn không được nhìn qua."),g("br"),g("br"),n._v("Việc này, ngay cả hắn đều không nghĩ tới, đối phương bản thân bị trọng thương, còn như thế ngắn ngủi thời gian, liền nghĩ thấu triệt... Thật hay giả?"),g("br"),g("br"),n._v("“Trương sư trí tuệ, không phải chúng ta những người này có thể đoán, ta cũng là nghe yêu cầu của hắn về sau, suy tư rất lâu, mới bừng tỉnh đại ngộ!”"),g("br"),g("br"),n._v("Ngô sư cảm khái."),g("br"),g("br"),n._v("Không nghĩ còn khá, càng nghĩ càng thấy đến chuyện này làm rất khá, chỉ có đại trí tuệ người, mới có thể làm ra."),g("br"),g("br"),n._v("“Cái này...”"),g("br"),g("br"),n._v("Lần nữa cân nhắc một hồi, càng ngày càng bội phục, Hình đường chủ cũng từ đáy lòng cảm khái: “Trương sư thật là thần nhân vậy!”"),g("br"),g("br"),n._v("PS: Gửi cho bạn bè quyển sách. 《 tới từ địa ngục nam nhân 》 tác giả: Gió thu 123, giới thiệu vắn tắt: Tinh không Ma Đế, trùng sinh trở về!"),g("br"),g("br"),n._v("Kẻ địch, phải quỳ lấy!"),g("br"),g("br"),n._v("Mỹ nữ, muốn nằm!"),g("br"),g("br"),n._v("“Ta là Ma, lấy Thần làm nô, lấy Tiên là bộc! Không phục? Vậy liền giết tới ngươi phục!” —— gió đêm!"),g("br"),g("br"),n._v("Mặt khác: QQ thư thành, hình như có cái gì hoạt động, mọi người nô nức tấp nập phát bình luận sách, trả lời lão nhai nhắn lại, liền có thể kiếm lấy điểm tích lũy. Bài danh liền sẽ càng ngày càng cao. Mọi người có thể đi nhìn xem. Hơn nữa, lão nhai tại QQ đọc chỗ bình luận truyện, đưa đỉnh một cái tin tức nặng ký, liên quan tới quyển sách, mọi người có thể đi qua nô nức tấp nập nhắn lại ha."),g("br"),g("br"),g("br"),g("br")]),n._v(" "),g("hr"),n._v(" "),g("p",[g("a",{attrs:{href:"./chap-1162"}},[n._v("Chương trước")]),n._v("  |  "),g("a",{attrs:{href:"./chap-1164"}},[n._v("Chương sau")])])])}),[],!1,null,null,null);h.default=c.exports}}]);