(window.webpackJsonp=window.webpackJsonp||[]).push([[1971],{2326:function(n,h,g){"use strict";g.r(h);var i=g(44),t=Object(i.a)({},(function(){var n=this,h=n.$createElement,g=n._self._c||h;return g("ContentSlotsDistributor",{attrs:{"slot-key":n.$parent.slotKey}},[g("h1",{attrs:{id:"chuong-696-hoai-vuong-phu"}},[g("a",{staticClass:"header-anchor",attrs:{href:"#chuong-696-hoai-vuong-phu"}},[n._v("#")]),n._v(" Chương 696: Hoài Vương phủ")]),n._v(" "),g("hr"),n._v(" "),g("p",[g("br"),g("br"),n._v("Chương 696: Hoài Vương phủ"),g("br"),g("br"),n._v("Trịnh Dương đám người là hắn học sinh thân truyền, từ Thiên Huyền vương quốc một đường theo tới, đã trải qua vô số sống chết, đã sớm đem hắn xem như người thân nhất, nếu quả thật đã xảy ra chuyện gì, không ngại đem trọn cái Hồng Viễn đế quốc, đều đâm cho đáy rơi!"),g("br"),g("br"),n._v("Liền xem như ông trời, cũng phải đâm cái lỗ thủng!"),g("br"),g("br"),n._v("Dám gây bất lợi cho bọn họ, liền phải thừa nhận bản thân vô cùng lửa giận."),g("br"),g("br"),n._v("“Trương sư yên tâm, an toàn của bọn hắn không có vấn đề!”"),g("br"),g("br"),n._v("Thấy sắc mặt hắn khó coi, Hồng sư cái này mới biết mình nói quanh co, khả năng đưa tới hiểu lầm, vội vàng giải thích: “Là Hoài Vương phủ, hôm qua tới người đem bọn hắn đón đi, chúng ta cũng không dám ngăn cản...”"),g("br"),g("br"),n._v("Mấy người này, hắn phụ trách chiếu cố, kết quả, Trương sư trở về, đã không thấy tăm hơi, đổi lại ai chịu định đô sẽ giận dữ."),g("br"),g("br"),n._v("Không có trực tiếp tức giận, đã rất tốt."),g("br"),g("br"),n._v("“Hoài Vương phủ?” Trương Huyền lông mày nhíu một cái."),g("br"),g("br"),n._v("Cái tên này nghe đều chưa từng nghe qua, đón hắn bọn họ làm gì?"),g("br"),g("br"),n._v("“Ta cũng không biết!” Hồng sư cũng không rõ ràng cho lắm, chần chờ một chút, nói: “Hoài Vương Ngọc Long Hiên là Hồng Viễn đế quốc Hoàng đế bệ hạ chú ruột, cũng chính là đương triều hoàng thúc, có thể nói dưới một người, trên vạn người, địa vị tôn sùng. Coi như trước đó chúng ta nhìn thấy vị kia Ngọc Phi Nhi công chúa, đều muốn chính miệng xưng hô một tiếng, Hiên gia gia!”"),g("br"),g("br"),n._v("“Hôm qua đột nhiên phái người tới, nhất định phải đón hắn bọn họ đi qua ở mấy ngày, ta thấy không có ác ý gì, lại là đương triều quyền quý, liền không có ngăn cản...”"),g("br"),g("br"),n._v("“Tiểu ở vài ngày?”"),g("br"),g("br"),n._v("Trương Huyền càng nghe càng kỳ quái."),g("br"),g("br"),n._v("Bản thân cùng vị này Hoài Vương Ngọc Long Hiên, khẳng định là không có giao tập, còn bốn cái học sinh, Trịnh Dương, Vương Dĩnh, Viên Đào, Lưu Dương, cũng là Vương Dĩnh gia thế tốt một chút, có điều, cũng là Thiên Huyền vương quốc đại gia tộc một trong mà thôi, Viên Đào càng là cô nhi, từ nhỏ đã lang thang khắp nơi, tại Địa Hoàng làm thành tán tu, liền cái tu luyện công pháp cũng không tìm tới."),g("br"),g("br"),n._v("Trịnh Dương, Lưu Dương, cũng không có gì đặc biệt, khẳng định không có khả năng cùng nhất đẳng đế quốc đường đường vương gia làm thân thích, đã như vậy, vì sao phải đón hắn bọn họ ở?"),g("br"),g("br"),n._v("Có phần quá kì quái đi!"),g("br"),g("br"),n._v("“Các ngươi ngày nào đến? Mấy người bọn hắn có phải hay không xông cái gì họa?” Trong lòng hơi động, Trương Huyền nghĩ đến một việc."),g("br"),g("br"),n._v("Hắn mấy cái này đồ đệ, cộng thêm quản gia Tôn Cường, mỗi một cái đều là không biết sợ chủ, nhất là người sau, có “Dương sư” làm chỗ dựa, có thể nói vô pháp vô thiên, cái gì đều không để ý, có phải hay không là chọc xảy ra chuyện gì, bị người ta Hoài Vương phủ để mắt tới, lúc này mới mượn cớ nắm tới?"),g("br"),g("br"),n._v("“Chúng ta là hôm qua buổi sáng đến... Vừa đến liền đến nơi này, không gây sự ah...”"),g("br"),g("br"),n._v("Hồng sư mày nhăn lại, cẩn thận suy tư chuyện ngày hôm qua."),g("br"),g("br"),n._v("“Làm sao không gây sự, ngươi quên hôm qua chúng ta vừa tới, còn không có tìm tới nơi này, Trương sư cái kia mập mạp học sinh...” Thấy hắn có chút nghĩ không ra, Phùng sư xen vào nói."),g("br"),g("br"),n._v("“Mập mạp học sinh? A, đúng, hôm qua Viên Đào là cùng người khác giao thủ qua ấy nhỉ, có điều, vậy cũng là việc nhỏ, lại nói, đối phương chỉ là người bình thường, phải cùng Hoài Vương phủ không có quan hệ gì đi!” Hồng sư sững sờ, cũng nghĩ tới, nhịn không được nói."),g("br"),g("br"),n._v("“Chuyện gì xảy ra?” Nghe được Viên Đào gây chuyện, Trương Huyền sắc mặt tái xanh nhìn qua."),g("br"),g("br"),n._v("“Chuyện này, kỳ thật không trách Viên Đào, hôm qua chúng ta mới vừa đến nơi đây, liền cùng một cái tán tu bộ dáng người, phát sinh xung đột, người kia cố ý gây chuyện, phải cứ cùng hắn tỷ thí, Viên Đào ra tay dạy dỗ đối phương dừng lại, do đó còn bị thương, chảy chút máu. Bất quá, đối phương cũng cho cùng chữa thương đan dược đền bù, nhìn người kia trang phục, giống như là cái thương nhân, thật chẳng lẽ cùng chuyện này có quan hệ?”"),g("br"),g("br"),n._v("Nhớ tới chuyện ngày hôm qua, Hồng sư cau mày."),g("br"),g("br"),n._v("Cùng Viên Đào đối chiến người kia, giống như là cái bình thường vân du bốn phương thương nhân, thực lực cũng rất bình thường, phải cùng Hoài Vương phủ xé không lên quan hệ, do đó một mực không nhớ ra được."),g("br"),g("br"),n._v("“Luận võ? Còn tổn thương?” Trương Huyền chân mày nhíu càng chặt."),g("br"),g("br"),n._v("Viên Đào, Trịnh Dương đám người mặc dù không cách nào vô thiên, nhưng mình không ở bên cạnh, vẫn là biết thu liễm, lại nói, cái trước thể chất kích hoạt, phòng ngự vô địch, muốn cho hắn tổn thương đổ máu, cũng không phải một chuyện dễ dàng."),g("br"),g("br"),n._v("Chẳng lẽ, cái gọi là Hoài Vương thật cùng chuyện này có quan hệ?"),g("br"),g("br"),n._v("Nếu như không phải, vì sao bản thân cũng không nhận ra cái gì vương gia, muốn tìm bọn hắn đi qua?"),g("br"),g("br"),n._v("“Hoài Vương phủ ở đâu?” Nghĩ một lát cũng nghĩ không thông, lông mày càng nhăn càng chặt, Trương Huyền nhịn không được nhìn qua."),g("br"),g("br"),n._v("“Trương sư muốn đi Hoài Vương phủ tìm người? Một mình ngươi khẳng định không được...” Hồng sư giật nảy mình, vội vàng vẫy tay."),g("br"),g("br"),n._v("Hoài Vương làm hoàng thúc, phủ đệ tường đồng vách sắt, cao thủ nhiều như mây, Thánh cấp cường giả khẳng định đều có không ít, cứ như thế trôi qua, tất nhiên mười phần nguy hiểm."),g("br"),g("br"),n._v("“Nói cho ta biết ở đâu!” Cắt ngang đối phương, Trương Huyền hừ một tiếng."),g("br"),g("br"),n._v("Tu vi của hắn cùng cấp bậc mặc dù không bằng Hồng sư, nhưng vừa rồi chiến thắng vị kia Phong sư huynh, lại thêm đủ loại truyền kỳ, trên tâm cảnh siêu nhiên, trên người đã mang theo khí tức cường đại, để cho người ta không dám chống lại."),g("br"),g("br"),n._v("“Đúng...”"),g("br"),g("br"),n._v("Biết đối phương quả thực muốn đi, liền hắn cũng không cách nào phản bác, sắc mặt trắng nhợt, Hồng sư không thể làm gì khác hơn nói: “Hoài Vương phủ, cách nơi này không xa, từ sĩ tử hải ra ngoài, đi mấy chục cây số liền có thể nhìn thấy...”"),g("br"),g("br"),n._v("Nói chỉ một cái phương hướng."),g("br"),g("br"),n._v("Nói còn chưa dứt lời, liền nghe đến không trung một tiếng chói tai vang lên, một đầu to lớn Tử Dực Thiên Hùng thú rơi xuống, Trương Huyền bàn chân đạp lên mặt đất, rơi vào lưng thú, thẳng tắp hướng hắn chỉ phương hướng bay đi."),g("br"),g("br"),n._v("“Trương sư...”"),g("br"),g("br"),n._v("đọc Ｔruyện cùng http://ngantruyen.com/"),g("br"),n._v("Không nghĩ tới vị này Trương sư tốc độ nhanh như vậy, thậm chí cái gì đều không có thương lượng, trực tiếp hướng Hoài Vương phủ phóng đi, Hồng sư dọa đến sắc mặt trắng bệch."),g("br"),g("br"),n._v("Hoài Vương là ai?"),g("br"),g("br"),n._v("Đương triều hoàng thúc, địa vị cực cao, coi như Hoàng đế bệ hạ, nhìn thấy đều muốn cúi người chào, không dám vượt qua, ngươi trực tiếp cưỡi linh thú tiến lên, không nói hai lời, một khi chọc giận, tai họa không nhỏ..."),g("br"),g("br"),n._v("“Nhược Hoan, mau theo ta cùng đi hoàng cung tìm Ngọc Phi Nhi công chúa, có lẽ chỉ có nàng mới có thể miễn trừ tai hoạ...”"),g("br"),g("br"),n._v("Một cắn răng, nói một tiếng, mang theo Nhược Hoan công tử liền đi ra ngoài."),g("br"),g("br"),n._v("Hiện tại bọn hắn duy nhất nhận biết hoàng thân quốc thích, chỉ có Ngọc Phi Nhi, chỉ hy vọng vị này có thể niệm cùng một chỗ tại Hóa Thanh trì tu luyện phân thượng, giúp Trương sư một lần..."),g("br"),g("br"),n._v("..."),g("br"),n._v("Tử Dực Thiên Hùng thú tốc độ cực nhanh, Trương Huyền đứng tại lưng thú, cúi đầu nhìn xuống dưới, không lâu sau, quả nhiên thấy một cái cự đại phủ đệ."),g("br"),g("br"),n._v("Phủ đệ trang trí vàng son lộng lẫy, rất nhiều hộ vệ người mặc kim giáp, còn chưa tới đến gần, liền để lộ ra một cỗ vô địch khí thế."),g("br"),g("br"),n._v("“Nơi này hẳn là...”"),g("br"),g("br"),n._v("Để Tử Dực Thiên Hùng thú ở chung quanh dạo chơi, vừa nghe đến mệnh lệnh liền đến, Trương Huyền thì nhẹ nhàng nhoáng một cái, từ phía trên mà hạ xuống."),g("br"),g("br"),n._v("Hắn cũng không vọt thẳng vào đối phương phủ đệ, dù sao đối phương là làm hướng hoàng thúc, tại không xác thực cắt Viên Đào đám người tình huống dưới, không tốt tới trở mặt."),g("br"),g("br"),n._v("Rơi vào khoảng cách phủ đệ mấy trăm mét bên ngoài một cái không người trên đường phố, đổi một thân mới tinh danh sư trang phục, cái này mới đi tới."),g("br"),g("br"),n._v("Đi tới gần, lập tức nhìn thấy phủ đệ ngoài cửa lớn, “Hoài Vương phủ” ba chữ to, lóng lánh kim quang, không biết là mấy tinh thư họa sư làm ra, tựa như đang sống, không ngừng chạy, tựa như lúc nào cũng sẽ phá không mà đi."),g("br"),g("br"),n._v("“Chỉ sợ là lục tinh thư họa sư tác phẩm!”"),g("br"),g("br"),n._v("Ánh mắt ngưng lại."),g("br"),g("br"),n._v("Có thể để cho kiểu chữ như là du long, phong mang tất hiện, ghi ba chữ này người, chỉ sợ ít nhất đạt đến lục tinh thư họa sư cấp bậc."),g("br"),g("br"),n._v("Trước cửa bốn tên hộ vệ, người mặc xanh xám sắc khôi giáp, mỗi một cái đều khí tức mơ hồ, cho người ta một loại trên linh hồn nghiền ép cảm giác."),g("br"),g("br"),n._v("“Hóa Phàm lục trọng Kiều Thiên cảnh cường giả?”"),g("br"),g("br"),n._v("Con ngươi co rụt lại."),g("br"),g("br"),n._v("Không nghĩ tới trước cửa cái này bốn tên hộ vệ, rõ ràng đều là Kiều Thiên cảnh cường giả, thực lực so với hắn cao hơn một cấp."),g("br"),g("br"),n._v("Loại này cường giả, tại Huyễn Vũ đế quốc, tuyệt đối là một phương đại quan, theo liền đi tới chỗ nào, đều có thể thống lĩnh vạn quân, ở đây thế mà chỉ là tên hộ vệ, cái này Hoài Vương phủ, không phải bình thường mạnh mẽ!"),g("br"),g("br"),n._v("Khó trách vừa rồi Hồng sư như thế lo lắng, hiện tại xem ra, nơi này coi như không phải đầm rồng hang hổ cũng không xê xích gì nhiều."),g("br"),g("br"),n._v("Bất quá, liền xem như đầm rồng hang hổ lại như thế nào?"),g("br"),g("br"),n._v("Nếu như đối phương thực có can đảm đối Viên Đào, Trịnh Dương đám người bất lợi, cho dù là hoàng thất, hắn còn lớn hơn náo một trận!"),g("br"),g("br"),n._v("Cùng lắm thì, đem hơn hai mươi đầu khôi lỗi ném ra, hơn hai mươi cái phòng ngự vô địch, không biết mệt mỏi cường giả Thánh vực, lại thêm cái kia không biết sâu cạn “Ngoan Nhân”, lại chỗ lợi hại, cũng có tự tin xông vào một lần!"),g("br"),g("br"),n._v("Hít sâu một hơi, Trương Huyền đi tới hộ vệ gần."),g("br"),g("br"),n._v("“Hoài Vương phủ, người không có phận sự, không được phụ cận!”"),g("br"),g("br"),n._v("Thấy hắn đi tới, một tên hộ vệ trường thương trong tay vẫy một cái, khẽ nói."),g("br"),g("br"),n._v("“Danh sư Trương Huyền, đến đây bái kiến Hoài Vương, mong rằng thông báo một tiếng!”"),g("br"),g("br"),n._v("Mỉm cười, Trương Huyền cổ tay khẽ đảo tay lấy ra danh thiếp, đưa tới."),g("br"),g("br"),n._v("Tại không có xác định đối phương mục đích trước, vẫn là không nên nháo cương."),g("br"),g("br"),n._v("“Danh sư?”"),g("br"),g("br"),n._v("Hộ vệ nghi ngờ nhìn thoáng qua, tiện tay tiếp nhận danh thiếp."),g("br"),g("br"),n._v("Phía trên rất đơn giản, chỉ có danh sư cùng tên, cũng không có lưu lại cấp bậc."),g("br"),g("br"),n._v("Tứ tinh danh sư tại Vạn Quốc thành là rất đỉnh phong cấp bậc, nhưng cùng loại này nhất đẳng đế quốc, còn chênh lệch nhị đẳng đế quốc, tròn hai cái đại cấp bậc, cũng có chút không đáng chú ý."),g("br"),g("br"),n._v("Thật muốn viết ra, muốn thấy Hoài Vương, chỉ sợ không dễ dàng như vậy."),g("br"),g("br"),n._v("Thậm chí đối phương liền danh thiếp cũng không cho tiễn."),g("br"),g("br"),n._v("“Cái này...”"),g("br"),g("br"),n._v("Đem danh thiếp đưa cho cái khác mấy tên hộ vệ nhìn một chút, cái này tên hộ vệ tràn đầy chần chờ: “Không biết Trương sư, là loại nào cấp bậc? Cùng chúng ta gia vương gia, trước đó nhận biết? Có thể có ước định?”"),g("br"),g("br"),n._v("Đối phương không có ghi cụ thể cấp bậc, bọn họ chỉ là hộ vệ, đành phải tuân hỏi một chút."),g("br"),g("br"),n._v("Bọn họ vương gia, tự nhiên không phải ai cầm cái danh thiếp liền có thể nhìn thấy."),g("br"),g("br"),n._v("“Tại hạ cũng chưa gặp qua Hoài Vương, chỉ là cùng Ngọc Phi Nhi công chúa là bạn, a, cùng Mạc Cao Viễn Mạc sư, cũng là bạn cũ!” Trương Huyền cười cười."),g("br"),g("br"),n._v("Nói ra bản thân là tứ tinh danh sư, đối phương khẳng định không cho vào, liền liền đánh liếc mắt đại khái."),g("br"),g("br"),n._v("Bất quá, nói cũng đúng sự thật, Ngọc Phi Nhi công chúa không cần phải nói, đồng sinh cộng tử, xưng hô bằng hữu không tính là quá trớn, Mạc Cao Viễn trước đó cũng coi như nhận biết, được xưng tụng “Bạn cũ”."),g("br"),g("br"),n._v("“Mạc sư bạn cũ?”"),g("br"),g("br"),n._v("Quả nhiên, nghe nói như thế, mấy tên hộ vệ lập tức sắc mặt nghiêm túc."),g("br"),g("br"),n._v("Mạc Cao Viễn, Hồng Viễn đế quốc Danh Sư đường đường chủ, lục tinh đỉnh phong danh sư, dám xưng hô loại người này vì bạn cũ, chỉ sợ kém cỏi nhất cũng là một vị lục tinh danh sư!"),g("br"),g("br"),n._v("Chỉ là... Lục tinh danh sư vì sao trẻ tuổi như vậy?"),g("br"),g("br"),n._v("“Trương sư mời tới bên này, chúng ta vậy thì bẩm báo vương gia!”"),g("br"),g("br"),n._v("Trong lòng nghi hoặc, nhưng không dám thất lễ, vội vàng gọi Trương Huyền tiến sân nhỏ nghỉ ngơi."),g("br"),g("br"),n._v("Danh sư thủ đoạn vô cùng, mặc dù trước mắt vị này thoạt nhìn tuổi trẻ, không thể bảo đảm tuổi tác không nhỏ, dù sao mặc kệ là thật là giả, thân là danh sư, cũng không phải là bọn họ có thể đắc tội."),g("br"),g("br"),n._v("“Ừm!”"),g("br"),g("br"),n._v("Thấy đối phương muốn đi bẩm báo vương gia, Trương Huyền thở phào nhẹ nhõm, gật gật đầu, đi theo hộ vệ sau lưng đi vào vương phủ."),g("br"),g("br"),n._v("Convert by: ThấtDạ"),g("br"),g("br"),g("br"),g("br")]),n._v(" "),g("hr"),n._v(" "),g("p",[g("a",{attrs:{href:"./chap-704"}},[n._v("Chương trước")]),n._v("  |  "),g("a",{attrs:{href:"./chap-706"}},[n._v("Chương sau")])])])}),[],!1,null,null,null);h.default=t.exports}}]);