(window.webpackJsonp=window.webpackJsonp||[]).push([[1976],{2331:function(n,h,t){"use strict";t.r(h);var i=t(44),g=Object(i.a)({},(function(){var n=this,h=n.$createElement,t=n._self._c||h;return t("ContentSlotsDistributor",{attrs:{"slot-key":n.$parent.slotKey}},[t("h1",{attrs:{id:"chuong-62-co-chut-tan-vo-thuong-ban"}},[t("a",{staticClass:"header-anchor",attrs:{href:"#chuong-62-co-chut-tan-vo-thuong-ban"}},[n._v("#")]),n._v(" Chương 62: Có chút tan vỡ Thượng Bân")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("br"),t("br"),n._v(" Mỹ nữ đi tới chỗ nào đều sẽ gây nên nam nhân chú ý, không ít người vì nàng, cố ý làm ra đủ loại thủ đoạn, chỉ vì làm cho nàng nhìn nhiều, này loại anh hùng cứu mỹ nhân, đã coi như là nhất dung tục sáo lộ!"),t("br"),t("br"),n._v(" Này loại kiều đoạn, hoặc là đại triển thần uy, hoặc là chính là bị đối phương cuồng đánh một trận, sau đó để cho mình lòng sinh thương hại, bởi vì thương sinh thích..."),t("br"),t("br"),n._v(" Ngược lại, bất kể thế nào, đều là làm bộ làm tịch, làm người ta sinh chán ghét."),t("br"),t("br"),n._v(" Rõ ràng điểm ấy, Trầm Bích Như lui ra vòng chiến."),t("br"),t("br"),n._v(" Nếu là cố ý hành động, vậy thì mặc kệ ngươi, muốn chơi thế nào thì chơi thế đó..."),t("br"),t("br"),n._v(" Bành bành bành oành!"),t("br"),t("br"),n._v(" Nhìn thấy kẻ thù đặc biệt đỏ mắt, Diêu Hàn giờ khắc này toàn lực ứng phó, căn bản không có lưu tình chút nào, thời gian ngắn ngủi, dũng mãnh quả cảm Thượng Bân lần thứ hai bị đánh da tróc thịt bong, đầy mặt máu tươi."),t("br"),t("br"),n._v(" Diêu Hàn cũng không dễ chịu, xác ướp giống như trên mặt, bị dòng máu thẩm thấu."),t("br"),t("br"),n._v(" “Còn trang rất giống... Tiếp tục...” Trầm Bích Như hai tay ôm ở trước ngực."),t("br"),t("br"),n._v(" “Trầm lão sư, nhanh đi trợ giúp Thượng thiếu, hắn sắp không chịu được nữa...” "),t("br"),t("br"),n._v(" Nhìn thấy Thượng Bân bị đánh, Tào Hùng cũng không nhịn được nữa vội vã tiến lên."),t("br"),t("br"),n._v(" Hắn chỉ có võ giả bốn tầng Bì Cốt cảnh, cùng hai người chênh lệch quá lớn, đi lên cũng là chịu chết, chỉ có thể cầu viện trước mắt Trầm lão sư."),t("br"),t("br"),n._v(" “Ta trợ giúp là có thể sao?” Thấy đối phương “Nắm” đi tới, Trầm Bích Như cười gằn."),t("br"),t("br"),n._v(" “Đúng vậy a, ngươi không cứu hắn, Thượng thiếu nhất định sẽ bị đánh chết...” "),t("br"),t("br"),n._v(" Không nhìn ra nàng ý lạnh, Tào Hùng vội hỏi."),t("br"),t("br"),n._v(" “Vậy thì tốt, ta đi lên!” Trầm Bích Như bước lên trước."),t("br"),t("br"),n._v(" Hô!"),t("br"),t("br"),n._v(" Thấy được nàng lần thứ hai lại đây, Diêu Hàn song chưởng đẩy một cái, đem Thượng Bân đẩy lui hai bước, nhảy ra vòng chiến: “Rất tốt, hôm nay thù ta nhớ rồi, núi cao sông dài, hai vị cho ân tình của ta, ta sau đó nhất định sẽ báo!” "),t("br"),t("br"),n._v(" Hắn hiện tại bị thương, một cái Trầm Bích Như đánh nhau cũng có chút vất vả, hai người gộp lại, coi như có thể vượt qua, nhưng nơi này là Hồng Thiên học viện, đợi thời gian càng dài, phiền phức càng nhiều, vạn nhất đối phương có âm mưu gì, một lúc nữa muốn chạy trốn cũng trốn không thoát."),t("br"),t("br"),n._v(" Vì lẽ đó, do dự một chút, vẫn là quả đoán rời đi."),t("br"),t("br"),n._v(" Tuy rằng không báo thù, đối phương cũng bị tự mình đánh rất thảm, nhưng cái này mối thù kết liễu, sau đó sớm muộn báo trở về chính là..."),t("br"),t("br"),n._v(" Nói xong, Diêu Hàn xoay người rời đi, thời gian ngắn ngủi liền biến mất ở tầm mắt của mọi người."),t("br"),t("br"),n._v(" Võ giả sáu tầng Ích Huyệt cảnh cường giả muốn chạy trốn, coi như Trầm Bích Như cũng khẳng định không đuổi kịp."),t("br"),t("br"),n._v(" Lại nói, nàng cũng căn bản không muốn đuổi theo, trên mặt ý lạnh càng sâu."),t("br"),t("br"),n._v(" Tự mình vừa lên, đối phương bỏ chạy, nói rõ cái gì? Rất đơn giản a, trình diễn xong, đương nhiên phải đi..."),t("br"),t("br"),n._v(" “Trầm lão sư, không nghĩ tới ngươi đối với ta như vậy tốt, ta đã rõ ràng tâm ý của ngươi...” Nhìn thấy Trầm Bích Như lần thứ hai xông lên giúp hắn giải vây, Thượng Bân lòng tràn đầy cao hứng, chuyển qua tràn đầy sưng đỏ mặt, khẽ mỉm cười, làm ra một cái tự nhận là rất tuấn tú động tác: “Kỳ thực, ta cũng yêu ngươi...” "),t("br"),t("br"),n._v(" Chính đang tức giận, muốn nhìn một chút cái tên này lấy cái gì bài cũ thủ đoạn dẫn tự mình chú ý, liền nghe nói như thế, Trầm Bích Như kém chút không tươi sống tức điên."),t("br"),t("br"),n._v(" Tâm ý của ta?"),t("br"),t("br"),n._v(" Tâm ta ý cái đại đầu quỷ!"),t("br"),t("br"),n._v(" “Lưu manh!” Trầm Bích Như nghiến răng nghiến lợi."),t("br"),t("br"),n._v(" Đùng!"),t("br"),t("br"),n._v(" Một cái tát quất tới."),t("br"),t("br"),n._v(" “Dát?” "),t("br"),t("br"),n._v(" Lấy dũng khí biểu lộ xong, chính cho rằng mỹ nữ sẽ không kìm lòng được nhào tới trong lòng, liền cảm thấy trên mặt một trận kịch liệt đau đớn, thân thể không nhịn được một cái lảo đảo, va đầu vào trên cửa sắt, Thượng Bân hai mắt say xe, kém chút không tại chỗ ngất đi."),t("br"),t("br"),n._v(" Ngươi quất ta làm chi?"),t("br"),t("br"),n._v(" Ngươi không phải là vì ta cùng người khác chiến đấu sao?"),t("br"),t("br"),n._v(" Hắn đều sắp điên rồi."),t("br"),n._v("  Này giời ạ đến cùng xảy ra chuyện gì? Một hồi tốt, một hồi không được, một hồi mùa xuân, một hồi mùa đông... Ta trêu ai ghẹo ai..."),t("br"),t("br"),n._v(" Kẹt kẹt!"),t("br"),t("br"),n._v(" Chính đang Thượng Bân không làm rõ được đến cùng xảy ra chuyện gì, lòng tràn đầy tan vỡ thời điểm, lớp học cửa từ từ mở ra, Trương Huyền đi ra, nhìn thấy mọi người hơi nhướng mày: “Ta đang dạy, cãi nhau còn thể thống gì?” "),t("br"),t("br"),n._v(" “Trương Huyền, ngươi tới thật đúng lúc!” "),t("br"),t("br"),n._v(" Chính đang lòng tràn đầy tan vỡ, nhìn thấy hắn đi ra, Thượng Bân phẫn nộ lập tức xông ra: “Tào Hùng lão sư đã đối với học viện đưa ra khiếu nại, cho rằng ngươi lợi dụng lừa gạt hành vi, bức bách học viên của hắn Lưu Dương trở thành học sinh của ngươi! Hiện tại học viện đã phê chuẩn tiến hành học tâm tra hỏi, khuyên ngươi nhanh lên một chút đem Lưu Dương mang tới đi!” "),t("br"),t("br"),n._v(" “Học tâm tra hỏi?” "),t("br"),t("br"),n._v(" Trương Huyền sững sờ, kết hợp trí nhớ của đời trước, nhất thời hiểu được, hơi nhướng mày."),t("br"),t("br"),n._v(" “Không sai, ta hiện tại đại biểu phòng giáo vụ đối với ngươi đưa tin, không đi nữa, chính là vi phạm trường học quy củ, tương tự sẽ bị khai trừ!” Thượng Bân liên tục cười lạnh."),t("br"),t("br"),n._v(" Diêu Hàn hắn đánh không lại, trầm nữ thần hắn lại không dám nói thêm cái gì, chỉ có thể đem một bồn lửa giận phát đến tên rác rưởi này lão sư trên thân."),t("br"),t("br"),n._v(" “Trương Huyền lão sư, đừng đi...” Trầm Bích Như đôi mi thanh tú nhăn lại."),t("br"),t("br"),n._v(" Tuy rằng không biết Thượng Bân muốn như vậy làm đến cùng có cái gì nắm chắc, nhưng khẳng định là nhằm vào Trương Huyền triển khai mưu kế, một khi đáp ứng, học tâm tra hỏi xảy ra vấn đề, làm không cẩn thận giáo sư tư cách thất lạc chuyện nhỏ, còn bị khai trừ!"),t("br"),t("br"),n._v(" Bất quá nàng lời còn chưa nói hết, liền bị Trương Huyền xua tay đánh gãy: “Vậy thì tốt, ta đi!” "),t("br"),t("br"),n._v(" Muốn nói trước, Lưu Dương đích thật là cảm thấy làm tự mình học sinh có chút oan ức, mà bây giờ, từ lâu triệt để tín phục, hiện tại coi như để hắn nhảy lầu luyện tập khinh công, e sợ đều sẽ không chậm trễ chút nào đáp ứng!"),t("br"),t("br"),n._v(" Này loại tín nhiệm, còn sợ gì học tâm tra hỏi?"),t("br"),t("br"),n._v(" Nhìn cái tên này tự tin tràn đầy, đến thời điểm ai mất mặt còn chưa chắc chắn!"),t("br"),t("br"),n._v(" “Ngươi không dám đi, ta hiện tại liền thông báo học viện bảo vệ nơi, đưa ngươi bắt đi...” Còn tưởng rằng Trương Huyền từ chối, Thượng Bân chính đang căm phẫn sục sôi lý lẽ hùng hồn hét lớn, đột nhiên phản ứng lại: “A? Ngươi nói cái gì? Ngươi muốn đi?” "),t("br"),t("br"),n._v(" Trương Huyền không còn trả lời, cười nhạt, xoay người đi tới cửa trước: “Lưu Dương, đi theo ta!” "),t("br"),t("br"),n._v(" “Rõ!” Lưu Dương từ gian phòng đi ra."),t("br"),t("br"),n._v(" “Chuyện này...” "),t("br"),t("br"),n._v(" Không nghĩ tới đối phương như vậy phối hợp, Thượng Bân nguyên bản tràn đầy tự tin trên mặt có chút do dự."),t("br"),t("br"),n._v(" “Thượng thiếu yên tâm đi, cái này Lưu Dương là ban đầu ta chuyên môn khai ra, khẳng định muốn làm học sinh của ta, ta chắc chắn!” Tào Hùng hạ thấp giọng."),t("br"),t("br"),n._v(" “Vậy thì tốt!” "),t("br"),t("br"),n._v(" Nghe được này loại bảo đảm, Thượng Bân lúc này mới gật gật đầu, nhanh chân hướng về phía trước: “Đi thôi!” "),t("br"),t("br"),n._v(" “Ngươi...” "),t("br"),t("br"),n._v(" Thiếu niên như vậy bất cẩn liền đáp ứng, Trầm Bích Như tràn đầy lo lắng."),t("br"),t("br"),n._v(" Học tâm tra hỏi, đây là lão sư không nể mặt mũi, nhưng không có cách giải quyết phương thức xử lý, một khi thất bại, làm như lừa dối lão sư, sẽ phải chịu rất lớn trừng phạt!"),t("br"),t("br"),n._v(" Chuyện như vậy làm sao có thể đáp ứng chứ, làm sao cũng phải chối từ một hồi, tiện dụng nhất nhu hòa phương thức giải quyết, bằng không... Họa liền lớn hơn!"),t("br"),t("br"),n._v(" “Không có gì!” "),t("br"),t("br"),n._v(" Biết đối phương lo lắng cái gì, Trương Huyền nhẹ nhàng nở nụ cười, cũng không nhiều lời, nhấc chân hãy cùng Thượng Bân hướng học viện Học Tâm Tháp đi đến."),t("br"),t("br"),n._v(" “Cái tên này...” "),t("br"),t("br"),n._v(" Thấy thiếu niên căn bản không đem khuyến cáo của nàng nghe được trong tai, Trầm Bích Như vừa vội vừa tức."),t("br"),t("br"),n._v(" Làm như đại mỹ nữ, lời của mình, người khác đều sẽ hưng phấn tiếp thu, cái tên này ngược lại tốt, không những không nghe, trái lại trực tiếp đi qua, đây không phải muốn chết sao?"),t("br"),t("br"),n._v(" Phải biết Thượng Bân gia gia là phòng Giáo Đạo chủ nhiệm, hắn dám làm như vậy, khẳng định là nhằm vào ngươi bày cái tròng..."),t("br"),t("br"),n._v(" “Muốn tự mình tìm xui xẻo, đáng đời!” "),t("br"),t("br"),n._v(" Tức giận giậm chân một cái, bất quá, do dự chốc lát, Trầm Bích Như vẫn là nhấc chân đi theo sát tới."),t("br"),t("br"),n._v(" Đối với Trương Huyền, nàng chỉ là hiếu kỳ, còn nói không lên yêu thích, nhưng Thượng Bân như vậy đối xử hắn, khẳng định vẫn là bởi vì nàng mà xảy ra, bất kể nói thế nào, cũng không thể khoanh tay đứng nhìn!"),t("br"),t("br"),n._v("Convert by: Hoang Chau"),t("br"),t("br"),t("br"),t("br")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("a",{attrs:{href:"./chap-70"}},[n._v("Chương trước")]),n._v("  |  "),t("a",{attrs:{href:"./chap-72"}},[n._v("Chương sau")])])])}),[],!1,null,null,null);h.default=g.exports}}]);