(window.webpackJsonp=window.webpackJsonp||[]).push([[1977],{2332:function(n,h,t){"use strict";t.r(h);var i=t(44),c=Object(i.a)({},(function(){var n=this,h=n.$createElement,t=n._self._c||h;return t("ContentSlotsDistributor",{attrs:{"slot-key":n.$parent.slotKey}},[t("h1",{attrs:{id:"chuong-701-giao-dich-hoi"}},[t("a",{staticClass:"header-anchor",attrs:{href:"#chuong-701-giao-dich-hoi"}},[n._v("#")]),n._v(" Chương 701: Giao dịch hội")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("br"),t("br"),n._v("Chương 701: Giao dịch hội"),t("br"),t("br"),n._v("“Tôn Cường, hai ngày này tìm chỗ ở, muốn hoàn cảnh tốt, cách học viện không xa!”"),t("br"),t("br"),n._v("Đi ra Hoài Vương phủ, Trương Huyền quay đầu nhìn về phía Tôn Cường."),t("br"),t("br"),n._v("Hắn là danh sư học viện học viên, có thể đi vào sĩ tử hải ở lại, Trịnh Dương đám người không giống, dựa theo bình thường đạo lý, là không có tư cách ở lại, nguyên nhân chính là như thế, Hoài Vương đến mời, Hồng sư bọn người mới không chần chờ bao lâu liền đáp ứng."),t("br"),t("br"),n._v("Ngắn ngủi cư ở vài ngày, không thành vấn đề, lớn, khẳng định muốn gặp phải chỉ trích, cùng hắn đến lúc đó phiền phức, còn không bằng sớm chuẩn bị, như vậy cũng thuận tiện giảng bài, người càng an nhàn chút."),t("br"),t("br"),n._v("“Vâng!” Tôn Cường gật đầu."),t("br"),t("br"),n._v("Hắn là quản gia, đến một chỗ ăn ở, đều thuộc về chỗ hắn lý, mọi người chỉ cần an tâm tu luyện là tốt rồi."),t("br"),t("br"),n._v("“Lão sư, ta cảm giác lập tức liền muốn đột phá!”"),t("br"),t("br"),n._v("Giao phó xong, đang muốn về sĩ tử hải, liền nghe đến Vương Dĩnh tiếng âm vang lên, quay đầu nhìn lại, chỉ thấy nàng toàn thân khí tức chảy xuôi, chân khí từ huyệt đạo xao động, tùy thời đều muốn đột phá!"),t("br"),t("br"),n._v("Nàng là Âm Dương cảnh đỉnh phong, một khi đột phá, chính là Hóa Phàm tứ trọng Trọc Thanh cảnh."),t("br"),t("br"),n._v("Một tháng kế tiếp đến, nàng tu luyện khắc khổ, đã sớm đạt đến đỉnh phong, một mực không tìm được thời cơ, giờ phút này biết lão sư trong lòng khó chịu, tinh thần xao động phía dưới, thế mà tùy thời đều muốn xông ra cái này cửa ải."),t("br"),t("br"),n._v("“Thiên Hùng thú!”"),t("br"),t("br"),n._v("Biết đột phá coi trọng cơ duyên, không thể bị dở dang, Trương Huyền tinh thần khẽ động, câu thông không trung linh thú, lập tức chân khí một trảo, mọi người lăng không mà lên, rơi vào lưng thú."),t("br"),t("br"),n._v("Mấy chục cái hô hấp liền trở lại sĩ tử hải, an bài Trịnh Dương đám người trước ở tại gian phòng của mình, lúc này mới đứng tại Vương Dĩnh bên người, thủ hộ đột phá."),t("br"),t("br"),n._v("Trọc Thanh cảnh, linh hồn, thân thể Trọc Thanh tách rời, mặc dù có phiên bản đơn giản hóa Thiên Đạo công pháp, đột phá không thành vấn đề, nhưng muốn làm đến chân khí mượt mà như một, linh nhục giao hòa mà bất tương dung, vẫn còn có chút khó khăn."),t("br"),t("br"),n._v("Biết chỉ có tiếp nhận áp lực đột phá, mới có thể có đến chỗ tốt lớn nhất, Trương Huyền cũng không ra tay, chỉ thay đối phương thủ hộ."),t("br"),t("br"),n._v("Cô cô cô cục cục!"),t("br"),t("br"),n._v("Chân khí tại thể nội chạy, toàn thân huyệt đạo mở ra, không ngừng thôn phệ linh khí, Vương Dĩnh đột nhiên hừ lạnh một tiếng, thô to khí tức, dọc theo đầu lâu phía trên huyệt Bách Hội, phun ra ngoài."),t("br"),t("br"),n._v("Ầm ầm!"),t("br"),t("br"),n._v("Trọc Thanh tách rời, Hóa Phàm tứ trọng Trọc Thanh cảnh đến!"),t("br"),t("br"),n._v("Đi đến loại cảnh giới này, giống như là thoát thai hoán cốt, Vương Dĩnh cả người tựa như biến thành ra khỏi vỏ trường kiếm, tản mát ra khí tức kinh người."),t("br"),t("br"),n._v("“Đây là... Trên tâm cảnh đột phá?”"),t("br"),t("br"),n._v("Trương Huyền lông mày nhảy một cái."),t("br"),t("br"),n._v("Võ giả cùng danh sư đồng dạng, cũng có ý cảnh đột phá, người sau tâm cảnh, là minh lý phương hướng, mà võ giả, thì là thiên hướng về chiến đấu."),t("br"),t("br"),n._v("Bình thường đột phá Trọc Thanh cảnh, coi như thực lực tu vi gia tăng, cũng không có khả năng có bén nhọn như vậy chi khí, rất rõ ràng, đây là trên tâm cảnh đột phá, đạt đến cảnh giới cao hơn!"),t("br"),t("br"),n._v("Thanh minh như nước, xích tử như kim!"),t("br"),t("br"),n._v("Xích Tâm cảnh!"),t("br"),t("br"),n._v("“Thật là lớn cơ duyên!”"),t("br"),t("br"),n._v("Ánh mắt sáng lên."),t("br"),t("br"),n._v("Võ giả cảnh giới, thân thể chân khí rất trọng yếu, tu luyện đến Trọc Thanh cảnh về sau, tâm cảnh liền rất trọng yếu."),t("br"),t("br"),n._v("Cái này liên quan linh hồn độ tinh khiết, tâm cảnh bạo tăng, về sau tu luyện, liền như là rồng vào biển rộng, sẽ càng ngày càng trôi chảy, càng nhanh càng nhanh."),t("br"),t("br"),n._v("Người là có suy nghĩ động vật, bất kể tu luyện còn là sinh hoạt, có việc vặt vãnh hỗn loạn, liền vĩnh viễn không thể nào làm được trong lòng không suy nghĩ bất cứ chuyện gì khác, hết sức chuyên chú."),t("br"),t("br"),n._v("Xích Tâm cảnh, chính là đem tất cả tạp niệm ném trừ, tâm thần như một, chuyên tâm tu luyện, loại tình huống này, tốc độ tu luyện, đâu chỉ nhanh mấy lần!"),t("br"),t("br"),n._v("Có thể nói, Vương Dĩnh đi đến loại cảnh giới này, về sau tốc độ tu luyện sẽ càng lúc càng nhanh, vượt xa Trịnh Dương đám người."),t("br"),t("br"),n._v("“Nha đầu này mặc dù không muốn nói, nhưng cũng có một cây đầu óc bảo thủ...”"),t("br"),t("br"),n._v("Lắc đầu, Trương Huyền cười khổ."),t("br"),t("br"),n._v("Rất nhiều học sinh, Vương Dĩnh thiên phú không kịp Triệu Nhã, kiên trì không bằng Lộ Xung, tư duy nhún nhảy không bằng Trịnh Dương, giảo hoạt không bằng Viên Đào, đạo lí đối nhân xử thế, không bằng Lưu Dương... Có thể cho tới nay, nàng tiến bộ nhanh nhất, hiện tại càng là lĩnh ngộ Xích Tâm cảnh!"),t("br"),t("br"),n._v("Xem ra tâm tư tỉ mỉ, trầm ổn như nước, mới là tu luyện tiến bộ lớn nhất thiên phú!"),t("br"),t("br"),n._v("“Nàng tu luyện càng lúc càng nhanh, có lẽ rất nhanh sẽ dùng đến Hợp Linh cảnh công pháp, ta còn không có...”"),t("br"),t("br"),n._v("Biết đối phương tu luyện đi vào quỹ đạo, không cần tiếp tục thủ hộ, Trương Huyền xoa xoa mi tâm."),t("br"),t("br"),n._v("Hắn hiện tại mặc dù là Hợp Linh cảnh đỉnh phong, nhưng cái này Hợp Linh cảnh, là bởi vì nữ tử áo trắng, lại thêm Vu hồn thủ đoạn mới thành công đột phá, mà trong đầu, cũng không hình thành Thiên Đạo bí tịch."),t("br"),t("br"),n._v("Nhất định phải nắm chặt thời gian tìm kiếm, nếu không, Vương Dĩnh lần nữa đột phá, cái này làm lão sư, sẽ đối mặt với vô công có thể thụ cục diện."),t("br"),t("br"),n._v("Không tiếp tục để ý tu luyện mấy cái học sinh, nhấc chân ra khỏi phòng."),t("br"),t("br"),n._v("“Hồng sư, Hợp Linh cảnh cùng Kiều Thiên cảnh công pháp bí tịch, có thể có thể tìm tới?” Tìm tới Hồng sư, Trương Huyền hỏi."),t("br"),t("br"),n._v("“Công pháp bí tịch? Có thể tìm tới một chút... Ngươi muốn bao nhiêu?” Hồng sư sững sờ."),t("br"),t("br"),n._v("“Càng nhiều càng tốt!” Trương Huyền gật đầu."),t("br"),t("br"),n._v("“Ngươi chờ ta một chút, ta xem một chút...”"),t("br"),t("br"),n._v("Hồng sư đi ra ngoài, không lâu sau, một lần nữa trở về, cầm mấy chục bản thư tịch."),t("br"),t("br"),n._v("“Tổng cộng bốn mươi bộ Hợp Linh cảnh bí tịch, Kiều Thiên cảnh, chỉ có ba quyển!” Mặc dù không biết đối phương muốn những bí tịch này là dụng ý gì, hắn vẫn là tìm một chút tới."),t("br"),t("br"),n._v("Huyễn Vũ đế quốc thông qua khảo hạch tới chỗ này năm mươi vị thiên tài, cơ bản đều là Hợp Linh cảnh, loại cấp bậc này công pháp, tìm ra được không khó, nhưng Kiều Thiên cảnh liền không quá dễ dàng."),t("br"),t("br"),n._v("Cũng chỉ tại Phùng sư, La sư bọn người trên thân tìm tới mấy quyển."),t("br"),n._v("“Bốn mươi bộ...” Đưa tay xẹt qua, Trương Huyền lắc đầu."),t("br"),t("br"),n._v("Đối với những người khác tới nói, bốn mươi bộ tuyệt đối được cho phong phú, đối với hắn mà nói, muốn hình thành Thiên Đạo công pháp, còn còn thiếu rất nhiều."),t("br"),t("br"),n._v("“Đi nơi nào có thể tìm tới càng nhiều, càng nhiều càng tốt!” Đem thư tịch một lần nữa còn cho Hồng sư, Trương Huyền nhịn không được hỏi."),t("br"),t("br"),n._v("“Ngươi... Đến cùng cần bao nhiêu bản?”"),t("br"),t("br"),n._v("Thấy bốn mươi bản đều không đủ, Hồng sư giật nảy mình."),t("br"),t("br"),n._v("Nhiều như vậy công pháp, liền xem như hắn, muốn xem xong cũng lý giải, không có mấy tháng công phu đều làm không được, thế mà còn chưa đủ, rốt cuộc muốn bao nhiêu?"),t("br"),t("br"),n._v("“Một ngàn bản trở lên!” Trương Huyền nói."),t("br"),t("br"),n._v("“Một ngàn bản?”"),t("br"),t("br"),n._v("Một cái lảo đảo, Hồng sư suýt chút nữa không có ngã sấp xuống."),t("br"),t("br"),n._v("Mặc dù Khổng sư sáng lập Danh Sư đường về sau, đủ loại công pháp tầng tầng lớp lớp, như măng mọc sau mưa, có thể... Muốn tìm đến hơn ngàn bản khác biệt, cũng là rất khó!"),t("br"),t("br"),n._v("Dù sao, mỗi một bộ công pháp đều ngưng tụ một vị người tu luyện toàn bộ tâm huyết, không phải rau cải trắng, một dài một đầu, chỉ cần hạt giống nhiều, có người chăm sóc, muốn bao nhiêu thì có bấy nhiêu."),t("br"),t("br"),n._v("“Không sai, ngươi cũng đã biết chỗ nào có thể tìm tới?” Không để ý tới đối phương khiếp sợ, Trương Huyền nói."),t("br"),t("br"),n._v("“Cái này...”"),t("br"),t("br"),n._v("Thấy đối phương không có nói đùa, Hồng sư xoa xoa mi tâm: “Danh sư học viện khẳng định có, đừng nói mấy ngàn bản, hơn vạn bản đều có thể tìm tới...”"),t("br"),t("br"),n._v("Danh sư học viện, hội tụ thiên hạ danh sư, đủ loại công pháp, bí tịch, hạo như biển khói, đừng nói mấy ngàn, hơn vạn đều có thể tìm tới, chỉ là Trương sư hiện tại còn không phải học viện chính thức học sinh, càng không có học phần, muốn xem thư, khẳng định là làm không được."),t("br"),t("br"),n._v("“Đúng rồi!”"),t("br"),t("br"),n._v("Thì thầm một hồi, Hồng sư đột nhiên ánh mắt sáng lên: “Tân sinh mấy ngày nay có giao dịch hội, cũng có bán ra công pháp bí tịch, có thể đi qua nhìn một chút, sĩ tử hải nhiều như vậy tân sinh, tìm chừng một ngàn bản, không khó lắm!”"),t("br"),t("br"),n._v("“Giao dịch hội?” Trương Huyền sững sờ."),t("br"),t("br"),n._v("“Ừm! Các đại đế quốc đến đây tân sinh, cơ bản đều có thuộc về mình tu luyện công pháp, cũng mang theo thuộc về mình quốc gia đặc sản, khảo hạch trước mấy ngày, sẽ tổ chức loại này giao dịch hội, vừa đến, để mọi người quen biết một chút, thứ hai, cũng có thể giao lưu vật tư, kinh nghiệm tu luyện, làm cho tất cả mọi người đều tiến bộ, tốt hơn thông qua khảo hạch!”"),t("br"),t("br"),n._v("Hồng sư nói."),t("br"),t("br"),n._v("“Tốt, giao dịch này sẽ ở nơi nào? Đi qua nhìn một chút!”"),t("br"),t("br"),n._v("Nghe được còn có như thế thịnh hội, Trương Huyền ánh mắt sáng lên."),t("br"),t("br"),n._v("“Ngay tại sĩ tử hải ở giữa giao dịch trên thuyền!”"),t("br"),t("br"),n._v("Hồng sư một chỉ."),t("br"),t("br"),n._v("Sĩ tử hải ở giữa là cái cự hồ nước lớn, vốn cho rằng chỉ là thưởng thức dùng, không nghĩ tới giao dịch chỗ, thế mà bố trí ở phía trên."),t("br"),t("br"),n._v("Đi ra ở lại sân nhỏ, hướng bên hồ đi đến, chỉ chốc lát liền đến đến gần, mặc dù đã vào xuân, mùa đông hàn ý còn không có triệt để tiêu tán, gió nhẹ dọc theo mặt hồ thổi tới, mang theo thấu xương ý lạnh."),t("br"),t("br"),n._v("Dọc theo mặt hồ hướng phía trong nhìn tới, mấy ngàn mét bên ngoài quả nhiên thấy một cái cự đại lâu thuyền, vắt ngang trong đó, từ xa nhìn lại, dòng người như thoi đưa, nhiều vô số."),t("br"),t("br"),n._v("“Nơi đó có cái đưa đò thuyền nhỏ...”"),t("br"),t("br"),n._v("Mấy ngàn mét khoảng cách, không có con đường, Hồng sư nhìn quanh hai bên một vòng, lập tức thấy được một cái đưa đò thuyền nhỏ, hai người đi tới."),t("br"),t("br"),n._v("“Đi giao dịch hội, bao nhiêu linh thạch?” Đi tới gần, Hồng sư hỏi."),t("br"),t("br"),n._v("“Một cái linh thạch trung phẩm!”"),t("br"),t("br"),n._v("Đưa đò chính là một vị lão giả, mí mắt vừa nhấc."),t("br"),t("br"),n._v("“Một cái trung phẩm? Ngươi đoạt ah!” Trương Huyền con mắt trợn tròn."),t("br"),t("br"),n._v("Liền điểm ấy khoảng cách, một cái hạ phẩm linh thạch, đều cảm thấy quý, lại muốn một cái trung phẩm, cái tên này có phần quá tối đi!"),t("br"),t("br"),n._v("“Đoạt? Nếu như không cảm thấy quý, có thể đạp nước đi qua ah! Đây là sĩ tử hồ, bên trong Hóa Phàm thất, bát trọng linh thú chỗ nào cũng có, chết đừng trách ta không có nhắc nhở ngươi!”"),t("br"),t("br"),n._v("Dường như đã sớm xem quen rồi loại tràng diện này, lão giả cười nhạt một tiếng."),t("br"),t("br"),n._v("Trên lục địa có linh thú, trong nước càng nhiều, thậm chí so trên lục địa chủng loại còn muốn phức tạp."),t("br"),t("br"),n._v("Khổng lồ như vậy hồ nước, nắm giữ rất nhiều cấp cao linh thú, Hóa Phàm thất, bát trọng, không tính là gì."),t("br"),t("br"),n._v("Tu vi đi đến Hợp Linh cảnh, coi như không có thể phi hành, đạp nước mà đi cũng không khó khăn, chỉ là... Bên trong có linh thú lời nói, liền không dám làm như thế, hơi không cẩn thận liền vô cùng có khả năng bị nuốt vào yết hầu, Thần Tiên khó cứu."),t("br"),t("br"),n._v("“Cái này...” Hồng sư chần chờ một chút, chính nghĩ nên hay không ngồi, chỉ thấy một cái linh thạch trung phẩm rơi vào lão giả trong tay, ngay sau đó hai bóng người nhoáng một cái, rơi vào trên thuyền."),t("br"),t("br"),n._v("“Nhà đò, lái thuyền!”"),t("br"),t("br"),n._v("Định thần nhìn lại, không là người khác, chính là trước kia bị Trương Huyền một chỉ đánh tan Phong sư huynh cùng người thanh niên kia."),t("br"),t("br"),n._v("Đứng trên thuyền, nhìn xem lão giả trong tay thuyền mái chèo, đẩy ra mặt nước, chậm rãi tiến lên, thanh niên vẻ mặt cười nhạo nhìn lại: “Một cái linh thạch đều luyến tiếc ra, còn muốn đi giao dịch hội...”"),t("br"),t("br"),n._v("Vị này Trương sư ngay trước mặt mọi người, một ngón tay đánh bại hắn Phong sư huynh, mặc dù hạ thủ lưu tình, nhưng cũng để hắn rất mất thể diện, do đó, trong lòng hắn vẫn là có rất lớn ý kiến."),t("br"),t("br"),n._v("Lúc này gặp vị này thực lực như thế Trương sư, thế mà liền một cái linh thạch trung phẩm đều không bỏ được, lập tức mở miệng chế giễu."),t("br"),t("br"),n._v("Bất quá, lời còn chưa nói hết, liền thấy trên bờ hai người lăng không nhảy một cái."),t("br"),t("br"),n._v("Hô!"),t("br"),t("br"),n._v("Rơi vào một đầu to lớn Thiên Hùng lưng thú bên trên."),t("br"),t("br"),n._v("To lớn linh thú, màu tím cánh khẽ vỗ, mặt nước dập dờn ra tầng tầng gợn sóng, thổi đến thuyền nhỏ một hồi run rẩy, suýt chút nữa lật rơi, thời gian nháy mắt biến mất ở trước mắt, hướng bên hồ lâu thuyền bay đi."),t("br"),t("br"),n._v("Convert by: ThấtDạ"),t("br"),t("br"),t("br"),t("br")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("a",{attrs:{href:"./chap-709"}},[n._v("Chương trước")]),n._v("  |  "),t("a",{attrs:{href:"./chap-711"}},[n._v("Chương sau")])])])}),[],!1,null,null,null);h.default=c.exports}}]);