(window.webpackJsonp=window.webpackJsonp||[]).push([[198],{552:function(n,h,t){"use strict";t.r(h);var g=t(44),i=Object(g.a)({},(function(){var n=this,h=n.$createElement,t=n._self._c||h;return t("ContentSlotsDistributor",{attrs:{"slot-key":n.$parent.slotKey}},[t("h1",{attrs:{id:"chuong-1155-ta-thich-nguoi"}},[t("a",{staticClass:"header-anchor",attrs:{href:"#chuong-1155-ta-thich-nguoi"}},[n._v("#")]),n._v(" Chương 1155: Ta thích ngươi")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("br"),t("br"),n._v("“Tôn Cường tiền bối, chúng ta đã đem công pháp toàn bộ nhớ kỹ!”"),t("br"),t("br"),n._v("Chừng nửa canh giờ, Hứa Du trưởng lão ôm quyền nói."),t("br"),t("br"),n._v("“Hủy đi đi!” Trương Huyền khoát tay áo."),t("br"),t("br"),n._v("Hứa trưởng lão đám người gật đầu, bàn tay lăng không một trảo, trước mắt mấy quyển bí tịch liền hóa thành tro bụi hoàn toàn biến mất, hình như chưa hề xuất hiện qua đồng dạng."),t("br"),t("br"),n._v("“Tiền bối, sau khi ra ngoài, chúng ta nên như thế nào tìm ngươi?”"),t("br"),t("br"),n._v("Biết vị này “Tổ sư sứ giả”, sớm muộn gì đều sẽ rời khỏi, Hứa Du trưởng lão nhịn không được nói."),t("br"),t("br"),n._v("Vị này cho bọn hắn lần thứ hai sinh mệnh, trong lòng cảm kích, vô luận như thế nào biểu đạt, đều không đủ."),t("br"),t("br"),n._v("“Tìm ta?” Trương Huyền lắc đầu: “Các ngươi không cần tìm ta, cũng không cần tìm ta, nếu như có chuyện, ta sẽ tìm các ngươi!”"),t("br"),t("br"),n._v("“Cái này...” Hứa Du trưởng lão dừng lại một chút: “Cái kia... Chúng ta có thể hay không có tư cách biết Tôn tiền bối chân thực thân phận? Về sau có chút phân phó, dù là bản thân tới không được, phái người đưa tin, cũng sẽ xông pha khói lửa, không chối từ.”"),t("br"),t("br"),n._v("“Cái này...” Nghe được đối phương yêu cầu, nhìn thấy bọn họ ánh mắt mong đợi, Trương Huyền dừng lại một chút, nói: “Thôi được, thật ra thì thân phận của ta, là Hồng Viễn đế quốc danh sư học viện viện trưởng Trương Huyền... Quản gia! Bất quá, ta người này từ trước tới nay khiêm tốn, hi vọng các ngươi có thể giữ bí mật, đừng nói đi ra.”"),t("br"),t("br"),n._v("“Trương viện trưởng quản gia?”"),t("br"),t("br"),n._v("“Vị này Trương viện trưởng, ta nghe nói qua, là ngàn năm không gặp siêu cấp thiên tài, tuổi còn trẻ liền trở thành danh sư học viện viện trưởng!”"),t("br"),t("br"),n._v("“Nguyên lai là quản gia của hắn, chúng ta biết!”"),t("br"),t("br"),n._v("“Tiền bối yên tâm, chúng ta tuyệt đối sẽ không nói...”"),t("br"),t("br"),n._v("..."),t("br"),t("br"),n._v("Biết Trương Huyền “Chân thực” thân phận, mọi người nguyên một đám tràn đầy hưng phấn nhẹ gật đầu."),t("br"),t("br"),n._v("“Tốt, ra ngoài đi!”"),t("br"),t("br"),n._v("Khoát tay áo."),t("br"),t("br"),n._v("Hứa trưởng lão đám người đồng loạt đi ra ngoài."),t("br"),t("br"),n._v("Thấy bọn họ lộ hàng, Trương Huyền lúc này mới thở phào nhẹ nhõm, khôi phục vốn dung mạo, cũng chầm chậm đi ra."),t("br"),t("br"),n._v("Đợi hắn đi ra địa lao, Độc điện mọi người đã bị Chiến sư đường người đón đi, phía trên chỉ còn lại có Hình đường chủ, Ngô sư cùng Lạc Thất Thất đám người."),t("br"),t("br"),n._v("“Trương sư...”"),t("br"),t("br"),n._v("Thấy hắn không ngại, mọi người lúc này mới thở phào nhẹ nhõm, tiến lên đón."),t("br"),t("br"),n._v("Trương Huyền gật đầu: “Đám này độc sư, ta đã thuyết phục kết thúc, về sau sẽ nghe theo phân phó!”"),t("br"),t("br"),n._v("“Ừm! Đa tạ Trương sư cao thượng...” Hình đường chủ ôm quyền."),t("br"),t("br"),n._v("“Cao thượng?” Thấy đối phương một mặt hưng phấn, hơn nữa tràn đầy cảm kích, Trương Huyền không hiểu ra sao."),t("br"),t("br"),n._v("Thật ra thì, hắn còn có chút lo lắng đối phương không tình nguyện, làm sao lại đi một chuyến địa lao, biến thành bộ dáng này?"),t("br"),t("br"),n._v("Biến hóa này, có phần quá nhanh đi!"),t("br"),t("br"),n._v("“Khách khí, ta thân là danh sư, đây là phải làm...” Mặc dù không biết đối phương thái độ tại sao lại như vậy biến hóa, nhưng mà khiêm tốn luôn luôn là bổn phận của hắn, Trương Huyền vội vàng nhẹ gật đầu."),t("br"),t("br"),n._v("Nhìn thấy hắn bộ dáng này, Hình đường chủ càng thêm bội phục."),t("br"),t("br"),n._v("Như thế vì bọn họ Chiến sư đường suy nghĩ, lại không giành công... Đây mới thật sự là cao nhân phong phạm."),t("br"),t("br"),n._v("“Trương sư, không biết, một lát nữa có gì an bài?”"),t("br"),t("br"),n._v("Ngô sư nhìn qua."),t("br"),t("br"),n._v("“Ta ý định đi thư họa sư công hội hoặc là giám bảo sư công hội một chuyến, thi đậu thất tinh phụ tu chức nghiệp, tốt tấn thăng thất tinh danh sư!” Cũng không có gì có thể giấu diếm, Trương Huyền nói thẳng."),t("br"),t("br"),n._v("Vốn là phải đánh tính toán đi giám bảo sư công hội, kết quả, bị thét lên nơi này, đại chiến một hồi, diễn một tuồng kịch... Suy nghĩ một chút đều cảm thấy làm trễ nải không ít thời gian."),t("br"),t("br"),n._v("“Làm sao? Ngô sư nhưng có sự tình?”"),t("br"),t("br"),n._v("Giải thích xong, Trương Huyền nhìn qua."),t("br"),t("br"),n._v("“Là như vậy, ta nghĩ đến, nếu như Trương sư không có việc gì, có thể cùng ta cùng đi Danh Sư đường, nhìn một chút ta trong mấy ngày qua điều tra kết quả...” Ngô sư nói."),t("br"),t("br"),n._v("“Điều tra kết quả?”"),t("br"),t("br"),n._v("“Ừm!” Ngô sư gật đầu: “Căn cứ Chương Dẫn Khâu thuyết pháp, tin tức của hắn là bị một vị kêu Trần Triết danh sư mang đi... Mà vị này Trần Triết, chính là Điền Thanh phó điện chủ học sinh thân truyền, cùng hắn quan hệ vô cùng mật thiết! Người này ta đã giam giữ xuống, bây giờ đang ở Danh Sư đường, nếu như Trương sư lúc rảnh rỗi, có thể cùng một chỗ nhìn một chút...”"),t("br"),t("br"),n._v("“Cái này...” Trương Huyền nhíu nhíu mày."),t("br"),t("br"),n._v("Thật ra thì, đối với những này, hắn không tính quá cảm thấy hứng thú."),t("br"),t("br"),n._v("Dù sao không chỉ đối phương học sinh, liền vị này phó đường chủ cũng bị nhốt áp, Danh Sư đường từ từ thẩm vấn chính là, tham dự không tham dự, đều không quan trọng."),t("br"),t("br"),n._v("Đang nghĩ cự tuyệt, liền nghe đến Ngô sư cười cười: “Thật ra thì, khảo hạch thư họa sư cùng giám bảo sư, Trương sư cũng không cần gấp, ta hôm nay ban đêm muốn tham gia một lần phẩm giám hội, nếu như Trương sư lúc rảnh rỗi, có thể mang ngươi cùng đi, đến lúc đó, thư họa sư công hội, giám bảo sư công hội hội trưởng đều sẽ có mặt, giới thiệu cho các ngươi một chút, khảo hạch lên tất nhiên cũng sẽ thuận lợi không ít!”"),t("br"),t("br"),n._v("“Cái gì phẩm giám hội?” Trương Huyền nhìn qua."),t("br"),t("br"),n._v("“Là một nơi khai quật ra mấy món bảo vật, Thanh Nguyên thành nhân vật có mặt mũi, đều sẽ tham gia, đánh giá bảo vật đẳng cấp, thuận tiện có thể hay không chữa trị một chút!”"),t("br"),n._v("Ngô sư gật đầu."),t("br"),t("br"),n._v("“Cũng tốt!” Dừng lại một chút, Trương Huyền gật đầu."),t("br"),t("br"),n._v("Trước sáng sớm đi ra ngoài, đi kinh hồng sư công hội, lại tại cái này trung phủ thân vương chơi đùa một. Hai canh giờ, hiện tại đã đến buổi chiều, khoảng cách ban đêm thời gian đến cũng không nhiều, cùng hắn đến lúc đó hai mắt đen thui đi khảo hạch, còn không bằng trước gặp đến hai đại công hội hội trưởng, trước thời hạn hỏi thăm một chút, cũng có thể càng có dựa dẫm."),t("br"),t("br"),n._v("“Vậy thì tốt, ta tại Danh Sư đường chờ ngươi!”"),t("br"),t("br"),n._v("Nghe hắn đáp ứng, Ngô sư ý cười đầy mặt, xoay người trước hết đi rời khỏi."),t("br"),t("br"),n._v("Cùng Hình đường chủ đám người cáo từ, Trương Huyền nhìn về phía Tôn Cường đám người."),t("br"),t("br"),n._v("“Mấy người các ngươi đi về trước đi!”"),t("br"),t("br"),n._v("“Vâng!” Tôn Cường biết mình gây đại họa, không dám nói nhảm, gật đầu đáp ứng."),t("br"),t("br"),n._v("“Lão sư, ta có việc muốn hỏi ngươi... Không biết ngươi có tiện hay không?” Cắn môi một cái, Lạc Thất Thất chần chờ một chút, nhìn qua."),t("br"),t("br"),n._v("“Đương nhiên!” Trương Huyền cười cười."),t("br"),t("br"),n._v("Thấy nàng có chuyện xảy ra hỏi thăm, Tôn Cường cùng Ngọc Phi Nhi không tốt tiếp tục đợi, đi đầu rời khỏi."),t("br"),t("br"),n._v("Thấy bốn phía lại không có người, bố trí một cái cách âm trận pháp, Lạc Thất Thất nhìn qua: “Lão sư, thật ra thì ta là trong lòng có chút nghi ngờ, muốn cho ngươi hỗ trợ giải đáp. Chuyện ngày hôm nay, ta toàn bộ nhìn ở trong mắt... Điền phó đường chủ cùng Trung Thanh Vương, muốn cố ý cho ngươi cộng thêm tội danh, dùng một chút thủ đoạn, không gì đáng trách... Nhưng, nếu như, đối phương không có sai, cũng làm như vậy, có phải hay không cũng quá đáng rồi, không phù hợp danh sư đạo nghĩa?”"),t("br"),t("br"),n._v("Nàng theo Huyễn Vũ đế quốc nhận biết Trương sư, cùng nhau đi tới, vị lão sư này, đều lấy quên mình vì người cao thượng hình thái xuất hiện tại trước mặt."),t("br"),t("br"),n._v("Theo Địa Cung cứu nàng, đi cứu Ngọc Phi Nhi, lại đến về sau mạo hiểm thu phục Vân Vụ lĩnh tất cả Thánh thú..."),t("br"),t("br"),n._v("Nàng nhìn thấy chuyện lầm lượt từng món, đều cho thấy vị này Trương sư cao thượng cùng vĩ đại."),t("br"),t("br"),n._v("Nguyên nhân chính là như vậy, theo ở chung đến bây giờ, càng ngày càng bội phục, thậm chí vẫn cảm thấy, trước mắt vị này, cùng năm đó Khổng sư đồng dạng, là một vị chân chính danh sư... Mặc dù hơi nhỏ thủ đoạn, nhưng cũng không thương tổn phong nhã."),t("br"),t("br"),n._v("Có thể... Ngày hôm nay nhìn thấy tất cả, có chút phá vỡ trước đó tư duy."),t("br"),t("br"),n._v("Bất kể nói thế nào, Điền phó đường chủ, đều là thất tinh đỉnh phong danh sư, Danh Sư đường phó đường chủ, như vậy thân phận, trực tiếp đối kháng... Đã vi phạm với lễ nghi, chạm đến danh sư, đáng giá nhất tuân thủ đồ vật."),t("br"),t("br"),n._v("Nguyên nhân chính là như vậy, tín niệm của nàng có chút dao động."),t("br"),t("br"),n._v("“Danh sư, là cái gì?”"),t("br"),t("br"),n._v("Nhìn ra nàng lo nghĩ, Trương Huyền không có trực tiếp trả lời, mà là mở miệng hỏi."),t("br"),t("br"),n._v("“Danh sư... Là lão sư, làm gương sáng cho người khác, truyền đạo học nghề giải thích nghi hoặc, để nhân tộc càng ngày càng cường đại, lấy thật vì đức, lấy tin làm gốc, trồng người trước làm người...” Lạc Thất Thất dừng lại một chút, nói."),t("br"),t("br"),n._v("“Sai, đều sai!” Trương Huyền lắc đầu."),t("br"),t("br"),n._v("“Sai? Đây là trở thành danh sư liền muốn đọc thuộc lòng quy tắc, chỉ cần là danh sư, đều biết...” Lạc Thất Thất không hiểu."),t("br"),t("br"),n._v("Nàng mới vừa nói, chính là danh sư chuẩn tắc, là cái danh sư, đều sẽ đọc thuộc lòng."),t("br"),t("br"),n._v("“Danh sư, là sư không giả, nhưng cũng là người!” Trương Huyền nói."),t("br"),t("br"),n._v("“Người?”"),t("br"),t("br"),n._v("“Ừm, là người, liền có hỉ nộ ái ố, liền có tư tưởng, liền có yêu hận!” Trương Huyền nhìn qua: “Đây là... Nhân tính! Khổng sư năm đó, cũng không yêu cầu, tích trữ thiên lý, diệt nhân dục! Đã như vậy, danh sư cũng phải có tư tưởng của mình, bản thân hỉ nộ ái ố, không cần chuyện gì, đều muốn tuân theo lễ nghi, gặp được chân chính trái lương tâm, cũng có thể đi chống lại. Danh sư, là sư, lại không phải đà điểu, nhẫn nhục chịu đựng, mà là phải có tư tưởng của mình, mới có thể tốt hơn vận dụng kiến thức, càng đi càng xa!”"),t("br"),t("br"),n._v("“Cái này...”"),t("br"),t("br"),n._v("Lạc Thất Thất trầm tư."),t("br"),t("br"),n._v("“Ta đã từng thấy qua một vị tu luyện giả, phụ thân hắn là danh sư, từ nhỏ đã nghe theo đối phương, dốc lòng tu luyện, cảm thấy hắn tất cả đều là chính xác... Đáng tiếc, phụ thân hắn trước khi chết, đều không có đột phá! Cứ như vậy, trong lòng hắn lưu lại bóng ma... Cha đều không đột phá nổi, hắn khẳng định cũng không cách nào đột phá... Bóng mờ phía dưới, từ đầu đến cuối đi không ra, thậm chí chân khí cùng lực lượng đều gom góp tích luỹ đủ rồi, vẫn như cũ không cách nào thành công!”"),t("br"),t("br"),n._v("Trương Huyền nói: “Đây chính là cố định tư duy, chúng ta là danh sư đồng thời, cũng là học sinh, làm học sinh, cần phát tán tư duy, mà không phải chỉ nghe lão sư, không dám phản bác, thật muốn như vậy... Thật giống như, ngày hôm nay ta đối mặt vị này Điền Thanh phó điện chủ, biết rất rõ ràng sai, vì cái gì không dám nói?”"),t("br"),t("br"),n._v("“Hắn thực lực so với ta mạnh hơn, ta không cách nào phản kháng, vậy dĩ nhiên phải vận dụng một chút thủ đoạn, đây là vì bảo vệ mình, càng là vì tốt hơn tiến bộ!”"),t("br"),t("br"),n._v("“Cái kia... Nếu có rất lợi hại danh sư, lấy quy tắc cùng đại nghĩa tới rêu rao, để cho ta nhất định phải tiếp nhận đâu?” Lạc Thất Thất nhìn qua."),t("br"),t("br"),n._v("“Tuân theo nội tâm!” Trương Huyền cười cười: “Lợi hại hơn nữa danh sư, còn có thể lợi hại qua Khổng sư? Khổng sư để cho ta giết người, chẳng lẽ ta liền muốn giết người? Tất cả tuân theo nội tâm của ngươi... Chỉ cần không nguyện ý, hoàn toàn có thể không đáp ứng! Không tôn trọng nội tâm của ngươi, lại muốn ngươi tuân thủ quy tắc cùng đại nghĩa... Đối phương đã vi phạm với danh sư đạo lý, hắn đều vi phạm với, dùng chút mưu kế cùng thủ đoạn, lại có cái gì?”"),t("br"),t("br"),n._v("“Cái này...” Lạc Thất Thất ánh mắt càng ngày càng sáng rực, dường như đã hiểu tới."),t("br"),t("br"),n._v("“Được rồi, thủ đoạn loại hình chính là phụ trợ, là vì cam đoan chính ngươi không bị thương tổn phương pháp, cùng danh sư lý luận cũng không xung đột, không cần buồn lo vô cớ, muốn nhiều như vậy...”"),t("br"),t("br"),n._v("Trương Huyền lắc đầu."),t("br"),t("br"),n._v("Đối phương rõ ràng là rơi vào ngõ cụt."),t("br"),t("br"),n._v("Đọc sách không phải xem chết sách, học vẹt, danh sư, cũng không phải chỉ biết là tuân thủ quy củ đồ ngốc, cái kia biến báo thời điểm liền muốn biến báo."),t("br"),t("br"),n._v("Vị này Lạc Thất Thất, bất kể thiên phú vẫn là cái gì đều rất tốt, chính là không có Hồ Yêu Yêu biến báo."),t("br"),t("br"),n._v("“Ta hiểu được!”"),t("br"),t("br"),n._v("Nghe xong giải thích, Lạc Thất Thất hưng phấn con mắt tỏa ánh sáng, lần nữa nhìn qua: “Đã lão sư như vậy biến báo, không câu nệ lễ phép, ta liền nói thật là nói đi, lão sư...”"),t("br"),t("br"),n._v("“Ta thích ngươi!”"),t("br"),t("br"),n._v("“...” Trương Huyền."),t("br"),t("br"),t("br"),t("br")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("a",{attrs:{href:"./chap-1163"}},[n._v("Chương trước")]),n._v("  |  "),t("a",{attrs:{href:"./chap-1165"}},[n._v("Chương sau")])])])}),[],!1,null,null,null);h.default=i.exports}}]);