(window.webpackJsonp=window.webpackJsonp||[]).push([[1981],{2336:function(n,h,t){"use strict";t.r(h);var i=t(44),c=Object(i.a)({},(function(){var n=this,h=n.$createElement,t=n._self._c||h;return t("ContentSlotsDistributor",{attrs:{"slot-key":n.$parent.slotKey}},[t("h1",{attrs:{id:"chuong-705-co-nguoi-muon-đanh-cuoc"}},[t("a",{staticClass:"header-anchor",attrs:{href:"#chuong-705-co-nguoi-muon-đanh-cuoc"}},[n._v("#")]),n._v(" Chương 705: Có người muốn đánh cược")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("br"),t("br"),n._v("Chương 705: Có người muốn đánh cược"),t("br"),t("br"),n._v("Lúc trước tại Vạn Quốc thành, lần thứ nhất nhìn thấy nhiều vị danh sư, nhưng cộng lại cũng chỉ hơn ngàn người, danh sư học viện vừa mở học, đi đến nửa bước ngũ tinh, tứ tinh đỉnh phong liền có ba vạn..."),t("br"),t("br"),n._v("Suy nghĩ một chút đều cảm thấy kinh khủng!"),t("br"),t("br"),n._v("Bất quá, biết Hồng Viễn danh sư học viện hoàn cảnh địa lý, cũng là có thể nghĩ thông suốt."),t("br"),t("br"),n._v("Cái này học viện là phong hào vương quốc phía dưới, một cái duy nhất danh sư học phủ, tuyển nhận không riêng gì Hồng Viễn đế quốc học viên, còn có cái khác nhất đẳng đế quốc."),t("br"),t("br"),n._v("Mấy chục cái nhất đẳng đế quốc, cộng thêm vô số tông môn, Danh Sư đường, thiên tài danh sư, nhiều vô số kể."),t("br"),t("br"),n._v("Khó trách Thiên Huyền vương quốc loại địa phương này được xưng là Thượng Cổ chỗ, liền cái danh sư, đều không có, quả thực rớt lại phía sau."),t("br"),t("br"),n._v("“Chúng ta không cùng thế lực khác liên minh, những học sinh này, có thể thông qua khảo hạch, liền dựa vào Trương sư ngươi...”"),t("br"),t("br"),n._v("Phùng sư, La sư đám người đi tới gần."),t("br"),t("br"),n._v("Trước đó mặc dù cùng vị này Trương sư có mâu thuẫn, nhưng tất cả mọi người là danh sư, cùng thuộc một cái thế lực, đối phương càng cường đại, Huyễn Vũ đế quốc cũng là càng cường đại, đi qua những ngày gần đây, mấy người cũng coi như nghĩ thông suốt."),t("br"),t("br"),n._v("“Yên tâm đi!”"),t("br"),t("br"),t("br"),n._v("Nhìn thoáng qua chung quanh rất nhiều học sinh, Trương Huyền gật đầu."),t("br"),t("br"),n._v("Lần này Huyễn Vũ đế quốc cộng thêm hắn tổng cộng tới năm mươi vị danh sư, đại bộ phận đều là tứ tinh đỉnh phong, Trọc Thanh cảnh trái phải thực lực."),t("br"),t("br"),n._v("Loại thực lực này, tại ba vạn tân sinh bên trong, quả thực không tính là xuất chúng, muốn trổ hết tài năng, toàn bộ thông qua, chỉ có thể nói... Rất khó!"),t("br"),t("br"),n._v("Nguyên nhân chính là như thế, lúc trước mọi người thương nghị qua đi mới muốn cùng kim hải nhị đẳng liên minh đế quốc, vốn nghĩ, coi như không thắng, cũng không kém nhiều, có thể không phân chủ phụ, công bằng cạnh tranh, không nghĩ tới, thực lực đối phương mạnh như vậy, bị đánh thất bại thảm hại, suýt chút nữa biến thành khiên thịt của bọn họ."),t("br"),t("br"),n._v("May mắn Trương sư ngăn cơn sóng dữ."),t("br"),t("br"),n._v("Không có liên minh, hiện tại cũng chỉ có thể đem hi vọng ký thác vào Trương sư trên người, hi vọng hắn có thể dẫn đầu mọi người, thông qua khảo hạch, không nên trúng đường bị đào thải."),t("br"),t("br"),n._v("“Đa tạ!”"),t("br"),t("br"),n._v("La sư, Phùng sư đám người ôm quyền."),t("br"),t("br"),n._v("Đổi lại người khác, lúc trước như thế đủ kiểu cản trở, khẳng định sẽ cho bọn hắn gây khó dễ, vị này Trương sư nhưng không có chút nào loại kia ý nghĩ, bất kể phẩm hạnh vẫn là phong độ, đều không phải là bọn họ có thể so sánh."),t("br"),t("br"),n._v("Hồi tưởng trước đó làm ra đủ loại,"),t("br"),t("br"),n._v("Chỉ cảm thấy tràn đầy áy náy, xấu hổ vô cùng."),t("br"),t("br"),n._v("“Khách khí!” Thấy nét mặt của bọn hắn, Trương Huyền cười cười, đang muốn nói mấy câu khách sáo, liền nghe đến một cái thanh âm nhàn nhạt từ nơi không xa vang lên."),t("br"),t("br"),n._v("“Đây chính là đem ngươi đánh bại người?”"),t("br"),t("br"),n._v("Quay đầu nhìn lại, chỉ thấy trước đó kim hải đế quốc cái kia bị đánh bại Phong sư huynh, đi theo một thanh niên sau lưng, đi tới."),t("br"),t("br"),n._v("Nói chuyện chính là người thanh niên này, kích cỡ cao lớn, tay chân rất dài, làn da ngăm đen, trong ánh mắt mang theo lạnh lùng, tựa như có điện mang lưu chuyển."),t("br"),t("br"),n._v("“Kiều Thiên cảnh?”"),t("br"),t("br"),n._v("Trương Huyền lông mày nhíu một cái."),t("br"),t("br"),n._v("Đối phương không có che lấp hơi thở, Kiều Thiên cảnh lực lượng tản ra, như là chói chang mặt trời, cho người ta áp lực thực lớn."),t("br"),t("br"),n._v("Kiều Thiên cảnh, tinh thần cùng tự nhiên ở giữa hình thành thiên địa chi kiều, càng thân cận tự nhiên, kết hợp tự nhiên, Hợp Linh cảnh mặc dù cũng không yếu, nhưng ở loại này cường giả trước mặt, liền không có ý nghĩa."),t("br"),t("br"),n._v("Mặc dù đạt đến Kiều Thiên cảnh, cùng Lạc Thất Thất đám người thực lực tương đương, nhưng nhìn huy chương trước ngực, vẫn như cũ là tứ tinh."),t("br"),t("br"),n._v("Khảo hạch ngũ tinh, Kiều Thiên cảnh chỉ là cơ sở nhất điều kiện, còn cần có năm cái đi đến ngũ tinh phụ tu chức nghiệp, cùng đi đến 15. 0 tâm cảnh khắc độ."),t("br"),t("br"),n._v("Do đó, danh sư học viện năm nhất, không ít học viên, thực lực sớm là đủ rồi, những điều kiện khác không cách nào đi đến, mà chỉ có thể khốn tại nguyên chỗ, không cách nào đột phá."),t("br"),t("br"),n._v("Trước mắt cái này, rất rõ ràng chính là như vậy."),t("br"),t("br"),n._v("Dù vậy, cũng rất đáng sợ."),t("br"),t("br"),n._v("Không đến ba mươi, hơn nữa chưa đi đến vào danh sư học viện liền đi đến loại thực lực này, coi như tại lần khảo hạch này rất nhiều tân sinh bên trong, cũng được tính là thiên tài, có thể đủ trổ hết tài năng."),t("br"),t("br"),n._v("“Vâng!” Phong sư huynh gật đầu."),t("br"),t("br"),n._v("“Cái này...”"),t("br"),t("br"),n._v("Nghe hắn xác nhận, thanh niên mày nhăn lại."),t("br"),t("br"),n._v("Hắn cùng vị này “Phong sư huynh” đã sớm nhận biết, vốn cho rằng đem người bạn thân này, luân phiên chà đạp, lại kém cũng đi đến Kiều Thiên cảnh, thực lực siêu quần, khí chất phi phàm, có thể... Vị này, cái quỷ gì?"),t("br"),t("br"),n._v("Toàn thân dơ dáy bẩn thỉu, thật nhiều ngày không tắm rửa bình thường; Con mắt vằn vện tia máu, vừa nhìn liền biết không nghỉ ngơi tốt; Trên đầu, trên mặt mặc dù khôi phục như lúc ban đầu, vẫn còn có thương thế lưu lại máu ứ đọng..."),t("br"),t("br"),n._v("Nếu như đoán không sai, trước đó không lâu có lẽ mới vừa bị đánh hoàn toàn thay đổi..."),t("br"),t("br"),n._v("Mấu chốt nhất là, thực lực mặc dù là Hợp Linh cảnh đỉnh phong, nhưng cho người ta một loại cũng không cường đại cảm giác... Đổi lại địa phương khác, cái bộ dáng này người, nhìn cũng không nhìn một cái, thật có thể đem bạn tốt đả kích uể oải suy sụp? Lòng tin đều đánh mất?"),t("br"),t("br"),n._v("“Dư huynh, đừng coi thường, Trương sư... Thực lực phi phàm, một ngón tay liền đem ta đánh bại!” Sợ đối phương không tin, Phong sư huynh, giải thích một câu, chẳng qua nói xong lời cuối cùng, chính mình cũng có chút hoảng hốt."),t("br"),t("br"),n._v("Thật là con hàng này đem ta đả thương?"),t("br"),t("br"),n._v("Làm sao cảm giác bại bởi một tên ăn mày..."),t("br"),t("br"),n._v("“Một ngón tay?”"),t("br"),t("br"),n._v("“Dư huynh” lắc đầu, ánh mắt như điện, nhìn lại: “Ngươi chính là Trương Huyền Trương sư? Ngươi đánh bại Phong Ngô, có dám so với ta bên trên một trận?”"),t("br"),t("br"),n._v("“So một trận?” Lắc đầu, Trương Huyền đang muốn nói mình rất bận, không thời gian, nghĩ đến cái gì, hai mắt sáng lên nhìn qua: “Có tiền đặt cược ư?”"),t("br"),t("br"),n._v("Đúng lúc tiền bạc hơi thiếu, thuê phòng đều không có tiền, nếu có người có thể đánh cược đưa chút tiền đến, liền quá tốt rồi."),t("br"),n._v(" Lúc trước tại Kỳ Trân lâu lấy được bảo vật, Tôn Cường còn chưa kịp bán ra liền đến nơi này, hiện tại Hồng Viễn thành lại chưa quen cuộc sống nơi đây, trong thời gian ngắn, khẳng định cũng bán ra không được, cẩn thận tính toán ra, trong tay đã không có gì tiền tiết kiệm."),t("br"),t("br"),n._v("“Tiền đặt cược?” “Dư huynh” sững sờ."),t("br"),t("br"),n._v("“Không sai, muốn so thử, không lấy ra tiền đặt cược, sao có thể đi?” Trương Huyền vẫy tay."),t("br"),t("br"),n._v("Hắn cũng không có thời gian khô bổ tình cảm sâu đậm, không lấy ra chút tính thực chất chỗ tốt, liền nghĩ tỷ thí, cái nào có nhiều thời gian như vậy lãng phí?"),t("br"),t("br"),n._v("“Ngươi... Nhưng biết ta muốn cùng ngươi so cái gì?”"),t("br"),t("br"),n._v("Lông mày nhíu một cái, “Dư huynh” khuôn mặt khó coi."),t("br"),t("br"),n._v("Bất kể nói thế nào, tất cả mọi người là danh sư, thuộc về nhân vật có mặt mũi, mới mở miệng liền tiền đặt cược, cũng không tránh khỏi quá là lạ đi!"),t("br"),t("br"),n._v("Hơn nữa, ngươi một cái Hợp Linh cảnh, không sợ ta ngược lại cũng thôi, liền so cái gì cũng không biết, liền muốn tiền đặt cược... Ngươi liền xác định nhất định sẽ thắng?"),t("br"),t("br"),n._v("Cuồng vọng!"),t("br"),t("br"),n._v("Phách lối!"),t("br"),t("br"),n._v("Vốn là đối cái tên này ấn tượng không tốt, thời gian nháy mắt, hạ xuống điểm thấp nhất."),t("br"),t("br"),n._v("“Quản ngươi đánh cược gì, chỉ cần có tiền đặt cược, tùy tiện!”"),t("br"),t("br"),n._v("Không để ý tới đối phương thái độ, Trương Huyền khoát tay áo."),t("br"),t("br"),n._v("“Đã ngươi tự tin như vậy, ta liền thành toàn ngươi!”"),t("br"),t("br"),n._v("Lạnh hừ một tiếng, “Dư huynh” cổ tay khẽ đảo, một đống linh thạch xuất hiện ở trước mắt: “Đây là 1000 linh thạch trung phẩm, chỉ cần ngươi thắng, trực tiếp lấy đi!”"),t("br"),t("br"),n._v("“1000 linh thạch?” Trương Huyền nhìn qua."),t("br"),t("br"),n._v("“Không sai! Lấy thực lực của ngươi, chắc hẳn chưa thấy qua nhiều linh thạch như vậy đi, có điều, yên tâm, ngươi thua... Ta đừng ngươi linh thạch, chỉ cần ngươi khi tất cả tân sinh trước mặt, bởi vì vừa rồi không lễ phép, hướng ta xin lỗi là đủ...”"),t("br"),t("br"),n._v("“Dư huynh” cười lạnh, hai tay chắp sau lưng, vẻ mặt ngạo nghễ."),t("br"),t("br"),n._v("Huyễn Vũ đế quốc tại nhị đẳng trong đế quốc, bản thân liền không khá cao, hơn nữa đối với vừa mới mặt keo kiệt lẫn nhau, sửa đổi miệng ngậm miệng tiền đặt cược... Vừa nhìn liền biết là cái quỷ nghèo, một ngàn linh thạch trung phẩm, đối loại người này tới nói, tuyệt đối là siêu cấp số lượng lớn, cả một đời đều chưa thấy qua."),t("br"),t("br"),n._v("“Xin lỗi? Ngươi suy nghĩ nhiều...”"),t("br"),t("br"),n._v("Thấy đối phương càng nói càng nhiều, Trương Huyền khoát tay áo cắt ngang: “Một ngàn linh thạch trung phẩm quá phiền phức, không bằng cược một khối!”"),t("br"),t("br"),n._v("“Một khối? Một khối linh thạch trung phẩm, cũng gọi đánh cược? Ta tùy tiện cho hạ nhân tiêu phí, cũng không chỉ cái này...”"),t("br"),t("br"),n._v("Nghe được đối phương tiền đặt cược chỉ là một khối, “Dư huynh” cười lạnh, còn không có nói hết lời, đã cảm thấy bên người “Phong sư huynh” Phong Ngô đẩy hắn một chút, nghi ngờ nhìn sang, con mắt rơi vào vị kia Trương sư trên tay, tròng mắt suýt chút nữa không rơi ra hốc mắt."),t("br"),t("br"),n._v("Chỉ thấy trong tay đối phương chẳng biết lúc nào nhiều hơn một khối linh thạch, óng ánh lóa mắt, lóng lánh để cho người ta choáng váng ánh sáng."),t("br"),t("br"),n._v("“Thượng, thượng,.. Linh thạch thượng phẩm?”"),t("br"),t("br"),n._v("Bờ môi run rẩy, “Dư huynh” thân thể nhoáng một cái."),t("br"),t("br"),n._v("“Đúng vậy a, một khối linh thạch thượng phẩm!” Trương Huyền nhàn nhạt nhìn qua."),t("br"),t("br"),n._v("“...” “Dư huynh” mắt tối sầm lại, thiếu chút nữa ngất đi."),t("br"),t("br"),n._v("Hắn mặc dù giá trị bản thân không ít, cũng chỉ có linh thạch trung phẩm. Thượng phẩm, nhưng mà Thánh giả mới có thể giao dịch vật phẩm, tiện tay xuất ra làm làm tiền đặt cược... Ngươi thật là Hợp Linh cảnh, thật là từ Huyễn Vũ đế quốc tới?"),t("br"),t("br"),n._v("Mới vừa thấy cái tên này, toàn thân dơ dáy bẩn thỉu, cùng ăn mày đồng dạng, còn tưởng rằng là người nghèo rớt mồng tơi, nằm mơ đều không nghĩ tới, vừa ra tay chính là linh thạch thượng phẩm... Ngươi đây là muốn điên ah!"),t("br"),t("br"),n._v("“Làm sao? Hay không? Không có tiền còn đánh cái gì cược, đi một bên chơi, không có rảnh cùng các ngươi nói mò...”"),t("br"),t("br"),n._v("Thấy cái tên này mặt mũi run rẩy, Trương Huyền nhếch miệng, vẻ mặt khinh thường."),t("br"),t("br"),n._v("Liền linh thạch thượng phẩm đều không có, ở đây giả trang cái gì lão sói vẫy đuôi."),t("br"),t("br"),n._v("Nhìn thấy hắn bộ dáng này, “Dư huynh”, Phong Ngô tất cả đều tức giận hai mắt biến thành màu đen."),t("br"),t("br"),n._v("Chuyện này là sao, bọn họ là đến tìm phiền phức, làm cho đối phương khó chịu, làm sao náo loạn nửa ngày, khó chịu thành bản thân?"),t("br"),t("br"),n._v("Nhất là vẻ mặt đó, hình như là các ngươi là quỷ nghèo, ta khinh thường cùng các ngươi tỷ thí đồng dạng..."),t("br"),t("br"),n._v("“Tốt, cược thì cược!” Tức giận cắn răng, “Dư huynh” hất lên ống tay áo, khẽ nói."),t("br"),t("br"),n._v("“Vậy thì tốt, đem ngươi linh thạch thượng phẩm ở đâu? Lấy ra ta xem một chút, nếu không thật muốn quỵt nợ, ta cũng không có cách nào! Thời đại này, không có tiền còn làm bộ người có nhiều lắm!” Trương Huyền nói."),t("br"),t("br"),n._v("“Ngươi...”"),t("br"),t("br"),n._v("“Dư huynh” nắm đấm xiết chặt, biết cùng đối phương tức giận, là bản thân tìm phiền toái, vừa quay đầu nhìn về phía Phong Ngô: “Đưa ngươi  lấy ra!”"),t("br"),t("br"),n._v("“Dư huynh, thứ này...”"),t("br"),t("br"),n._v("Nghe được muốn hắn lấy cái này, Phong Ngô lông mày nhảy lên, biến sắc, vội vàng vẫy tay."),t("br"),t("br"),n._v("“Yên tâm, ngươi cảm thấy hắn có thể thắng ta?” “Dư huynh” hừ một tiếng, hai tay chắp sau lưng, trên mặt lộ ra sự tự tin mạnh mẽ."),t("br"),t("br"),n._v("“Ây... Vậy được rồi!”"),t("br"),t("br"),n._v("Vừa nghĩ tới Phùng huynh thân phận, cùng lần khảo hạch này nội dung, Phong Ngô chần chờ một chút, nhẹ gật đầu, lật bàn tay một cái, một cái ngọc phù xuất hiện tại lòng bàn tay đưa tới."),t("br"),t("br"),n._v("“Dư huynh” tiếp nhận ngọc phù, nắm tại lòng bàn tay: “Cái này viên Kích Không phù, là Thánh giả luyện chế, có thể thừa nhận được Thánh giả nhất trọng cường giả một chiêu công kích, mà không bị thương tổn! Giá trị tuyệt đối bù đắp được một cái linh thạch thượng phẩm!”"),t("br"),t("br"),n._v("“Kích Không phù?”"),t("br"),t("br"),n._v("Trương Huyền ngón tay đưa tới, đụng một cái, lập tức hài lòng nhẹ gật đầu: “Thứ này quả thực giá trị một cái linh thạch thượng phẩm, tốt, ta cược!”"),t("br"),t("br"),n._v("“Vậy thì tốt, chúng ta liền cược... Một lát nữa thí luyện, ai thành tích cao hơn!”"),t("br"),t("br"),n._v("Thấy hắn đáp ứng, “Dư huynh” khóe miệng vung lên."),t("br"),t("br"),n._v("Convert by: ThấtDạ"),t("br"),t("br"),t("br"),t("br")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("a",{attrs:{href:"./chap-713"}},[n._v("Chương trước")]),n._v("  |  "),t("a",{attrs:{href:"./chap-715"}},[n._v("Chương sau")])])])}),[],!1,null,null,null);h.default=c.exports}}]);