(window.webpackJsonp=window.webpackJsonp||[]).push([[1982],{2338:function(n,h,t){"use strict";t.r(h);var i=t(44),g=Object(i.a)({},(function(){var n=this,h=n.$createElement,t=n._self._c||h;return t("ContentSlotsDistributor",{attrs:{"slot-key":n.$parent.slotKey}},[t("h1",{attrs:{id:"chuong-706-mi-truong-lao"}},[t("a",{staticClass:"header-anchor",attrs:{href:"#chuong-706-mi-truong-lao"}},[n._v("#")]),n._v(" Chương 706: Mi trưởng lão")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("br"),t("br"),n._v("Chương 706: Mi trưởng lão"),t("br"),t("br"),n._v("“Thành tích cao?”"),t("br"),t("br"),n._v("Trương Huyền lông mày nhíu một cái."),t("br"),t("br"),n._v("Không phải nói muốn khảo hạch săn giết linh thú ư? Thành tích cao là cái gì?"),t("br"),t("br"),n._v("Đang muốn hỏi thăm, liền nghe đến phía trước một cái hùng vĩ tiếng âm vang lên."),t("br"),t("br"),n._v("“Yên tĩnh!”"),t("br"),t("br"),n._v("Mọi người tất cả đều kìm lòng không được dừng lại giao lưu, ngẩng đầu nhìn về phía trước, lập tức nhìn thấy một cái lão giả đứng lơ lửng trên không, lơ lửng giữa không trung."),t("br"),t("br"),n._v("“Thánh giả...”"),t("br"),t("br"),n._v("Trương Huyền lông mày ngưng lại."),t("br"),t("br"),n._v("Đồng dạng là phi hành, hắn có thể rõ ràng cảm nhận được, đối phương phi hành là đối với tự nhiên khống chế, thiên địa đối công nhận của hắn, mà không phải chân khí thôi thúc."),t("br"),t("br"),n._v("Liền cùng chim bay cùng khí cầu, người sau là không khí nâng, coi như bất động, cũng có thể ở trên trời phi hành, cái trước một khi cánh đình chỉ vỗ, liền sẽ trực tiếp rơi xuống."),t("br"),t("br"),n._v("Hồng Trần Đạp Thiên bộ giống như chim bay, cần phải không ngừng dùng chân khí thôi thúc, mới có thể vẫy vùng, trước mắt vị này rõ ràng không phải, thực lực tất nhiên đạt đến Thánh cấp."),t("br"),t("br"),n._v("“Tại hạ Mi Trúc, là học viện trưởng lão, chịu trách nhiệm lần này tân sinh nhập môn khảo hạch!” Người trên không trung, ánh mắt vung rơi xuống dưới, mang theo nhàn nhạt ấm áp, lão giả nói."),t("br"),t("br"),n._v("“Hắn chính là Mi trưởng lão?” Nghe được giới thiệu, Trương Huyền phía trước một cái tân sinh lên tiếng kinh hô."),t("br"),t("br"),n._v("“Mi trưởng lão rất nổi danh?” Một cái tân sinh không hiểu."),t("br"),t("br"),n._v("“Hồng Viễn danh sư học viện, có thập đại danh sư, đều đạt đến lục tinh đỉnh phong, cũng được phong làm mạnh nhất thập đại trường lão, chịu trách nhiệm quản lý học viện tất cả mọi chuyện... Vị này Mi trưởng lão chính là một cái trong số đó, ngươi nói nổi danh không có tên?” Cái thứ nhất tân sinh hạ giọng nói."),t("br"),t("br"),n._v("“Thập đại danh sư?” Cái thứ hai tân sinh con mắt trợn tròn."),t("br"),t("br"),n._v("Nghe qua danh sư học viện, liền nghe qua thập đại danh sư, là Hồng Viễn đế quốc, cùng vô số nhất đẳng đế quốc, liên hợp lại cùng nhau, mỗi năm mươi năm cử hành một lần thịnh hội bình chọn đi ra, do phong hào đế quốc Danh Sư đường, tự mình sắc phong."),t("br"),t("br"),n._v("Thập đại danh sư, nắm giữ cực cao địa vị, coi như Hồng Viễn đế quốc Hoàng đế bệ hạ gặp được đều muốn lấy học sinh chi lễ đối đãi, hưởng thụ Danh Sư đường trân quý nhất tài nguyên cùng điều kiện."),t("br"),t("br"),n._v("Tất cả học sinh, đều lấy có thể đi vào bọn họ môn hạ làm vinh..."),t("br"),t("br"),n._v("Vốn cho rằng lợi hại như vậy danh sư, cách mình đám người rất xa, không nghĩ tới còn không có tiến vào học viện liền gặp được một vị!"),t("br"),t("br"),n._v("  “Đúng vậy a, Mi trưởng lão là thập đại danh sư bên trong, phụ tu thuần thú, không nghĩ tới lần này tiến hành đi săn tỷ thí, hắn thế mà tự mình ra mặt...”"),t("br"),t("br"),n._v("Cái thứ nhất tân sinh, cảm khái một tiếng, hưng phấn mà hai mắt tỏa ánh sáng."),t("br"),t("br"),n._v("Thập đại danh sư, là nhân vật trong truyền thuyết, có thể tận mắt nhìn đến, kích động trong lòng, khó mà kìm lại."),t("br"),t("br"),n._v("Một bên “Dư huynh” cũng vẻ mặt trắng bệch, hai mắt hưng phấn mà tỏa ánh sáng."),t("br"),t("br"),n._v("“Lần này khảo hạch, chắc hẳn mọi người đã sớm biết, chư vị trước mắt cái này Lôi Viễn phong, ta để vào vô số linh thú!”"),t("br"),t("br"),n._v("Cắt ngang mọi người hưng phấn, không trung Mi trưởng lão cười cười: “Những này, đều là hung tàn thành tính hạng người, nhiều lần đả thương người, chết chưa hết tội. Ta đem hắn cầm tù tại ngọn núi bên trên, chư vị có thể lên đi săn giết. Chỉ cần đánh giết một đầu Hóa Phàm tứ trọng đỉnh phong cấp bậc linh thú, nhận được nội đan, coi như thông qua!”"),t("br"),t("br"),n._v("“Hóa Phàm tứ trọng đỉnh phong linh thú?”"),t("br"),t("br"),n._v("“Hung tàn thành tính, nhiều lần đả thương người?”"),t("br"),t("br"),n._v("“Lần khảo hạch này, rất khó...”"),t("br"),t("br"),n._v("Mi trưởng lão mặc dù chỉ là ngắn ngủi hai câu, nhưng mọi người đều là danh sư, trong nháy mắt liền hiểu chỗ mấu chốt, lại không vừa rồi hưng phấn, thay vào đó là ngưng trọng."),t("br"),t("br"),n._v("Linh thú hình thể to lớn, sức chiến đấu bản thân liền mạnh, Hóa Phàm tứ trọng đỉnh phong linh thú, coi như đối chiến Hợp Linh cảnh danh sư, đều không rơi vào thế hạ phong, thậm chí còn có thể thong dong chạy trốn."),t("br"),t("br"),n._v("Trước tới tham gia khảo hạch tân sinh, đại bộ phận đều tại Trọc Thanh cảnh đỉnh phong, muốn vượt qua cùng cấp bậc linh thú, khó!"),t("br"),t("br"),n._v("Huống chi, đối phương hung tàn thành tính, nắm giữ giết người, đả thương người kinh nghiệm, tại bất kể tại ý thức vẫn là kinh nghiệm thượng, đều so không trải qua chém giết linh thú cường đại hơn rất nhiều."),t("br"),t("br"),n._v("“Khó trách muốn liên minh...”"),t("br"),t("br"),n._v("Trương Huyền cũng nhẹ gật đầu."),t("br"),t("br"),n._v("Trước đó hắn một mực kỳ quái, đi săn mà thôi, vì sao phải liên minh, nghe được giờ mới hiểu được, không làm như vậy, một, hai người đơn độc hành động, rất dễ dàng bị linh thú vây công, làm không cẩn thận tại chỗ chết."),t("br"),t("br"),n._v("Tân sinh nhập môn khảo hạch, có chết nguy hiểm... Xem ra người danh sư này học viện, quả nhiên cùng Lạc Thất Thất nói đồng dạng, kẻ phù hợp mới có thể sinh tồn, không phải dễ dàng như vậy ở lại."),t("br"),t("br"),n._v("“Thời gian là một ngày, nói cách khác mặt trời lặn trước, nhất định phải đạt được nội đan, đồng thời chạy về nơi này! Chỉ có làm đến những này, mới coi như thông qua, làm không được... Coi như thất bại! Cũng là cho thấy, cùng danh sư học viện vô duyên.”"),t("br"),t("br"),n._v("Mi trưởng lão tiếp tục nói: “Mặt khác, ta nhắc nhở mọi người một câu, trên núi không chỉ có Hóa Phàm tứ trọng linh thú, còn có ngũ trọng, thậm chí lục trọng, ngươi muốn đi săn giết linh thú đồng thời, cũng phải phòng ngừa bị linh thú săn giết!”"),t("br"),t("br"),n._v("Tin tức này, mọi người rất rõ ràng cũng sớm biết được, không quá nhiều ngoài ý muốn."),t("br"),t("br"),n._v("Danh sư, không chỉ muốn sức chiến đấu mạnh, các phương diện đều muốn xuất chúng mới được, ngươi đi săn giết linh thú, còn muốn phòng ngừa bị linh thú giết chết, vậy thì cho thấy một người tổng hợp năng lực."),t("br"),t("br"),n._v("“Mi trưởng lão, nếu như có thể săn giết Hóa Phàm ngũ trọng, hoặc là lục trọng linh thú, thành tích nên như thế nào tính toán?”"),t("br"),t("br"),n._v("Buồn bực một lát, một cái tân sinh nhịn không được nói."),t("br"),t("br"),n._v("“Ha ha, hỏi rất hay!” Mi trưởng lão vuốt râu một cái: “Vậy thì liên quan tân sinh nhập môn thành tích đánh giá. Thành tích cao, sẽ bị phân đến tốt hơn trong lớp, tiếp nhận cấp bậc cao hơn lão sư chỉ điểm, thậm chí, quá mức xuất chúng lời nói, có thể được đến trưởng lão ưu ái!”"),t("br"),t("br"),n._v("“Trưởng lão ưu ái?”"),t("br"),t("br"),n._v("Mọi người hô hấp dồn dập."),t("br"),t("br"),n._v("Có thể tại danh sư học viện trở thành trưởng lão, ít nhất đều là lục tinh danh sư, nhận được loại người này chỉ điểm, về sau tuyệt đối một bước lên mây, thực lực tăng vọt."),t("br"),n._v(" Bất kể địa vị vẫn là tài nguyên, chỉ sợ đều có thể hưởng thụ cấp bậc cao nhất, thành tựu vô hạn."),t("br"),t("br"),n._v("“Còn như thế nào đánh giá, nói đến mười phần đơn giản, chính là điểm tích lũy hạn chế!” Mi trưởng lão nói."),t("br"),t("br"),n._v("“Điểm tích lũy?” Mọi người cảm thấy rất ngờ vực."),t("br"),t("br"),n._v("“Không sai, chính là căn cứ săn giết linh thú đẳng cấp, tiến hành điểm tích lũy. Một đầu Hóa Phàm tứ trọng đỉnh phong linh thú, tích một điểm; Một đầu Hợp Linh cảnh sơ kỳ linh thú, tích 10 điểm, trung kỳ 20 điểm, hậu kỳ 30 điểm, đỉnh phong 40 điểm! Còn Kiều Thiên cảnh sơ kỳ linh thú thì là 100 điểm, trung kỳ 200 điểm... Cứ thế mà suy ra!”"),t("br"),t("br"),n._v("Mi trưởng lão nói: “Điểm tích lũy tích tụ tại cá nhân trên người, có thể được đến tốt hơn thành tích, trổ hết tài năng, đương nhiên, cũng có thể tiến hành chia đều, dùng để thông qua khảo hạch!”"),t("br"),t("br"),n._v("“Tỷ như, mười người liên hợp săn giết một đầu Hợp Linh cảnh sơ kỳ linh thú, hoàn toàn có thể đem cái này mười cái điểm tích lũy, bình quân phân phối, cũng là cho thấy, mười người toàn bộ đều có thể thông qua khảo hạch!”"),t("br"),t("br"),n._v("“Mười người săn giết một đầu Hợp Linh cảnh sơ kỳ liền đều có thể thông qua khảo hạch?”"),t("br"),t("br"),n._v("“Quá tốt rồi...”"),t("br"),t("br"),n._v("Không ít thực lực hơi yếu tân sinh, nghe nói như thế, tất cả đều ánh mắt sáng lên, nắm đấm xiết chặt."),t("br"),t("br"),n._v("Bằng cho bọn hắn mượn đơn độc thực lực, muốn muốn chém giết một đầu Hóa Phàm tứ trọng đỉnh phong linh thú, chỉ sợ rất khó, nhưng nhiều người liên hợp, liền không nhất định."),t("br"),t("br"),n._v("“Danh sư, là đối kháng Dị Linh tộc nhân mà sinh, lấy thiên hạ làm nhiệm vụ của mình, không chỉ muốn đơn độc thực lực mạnh, đoàn kết hợp tác, tín nhiệm lẫn nhau, trợ giúp lẫn nhau, cũng là mấu chốt!” Hiểu biết đạo tâm tư của mọi người, Mi trưởng lão giải thích một câu."),t("br"),t("br"),n._v("Nghe nói như thế, mọi người nhẫn gật đầu không ngừng."),t("br"),t("br"),n._v("Danh sư không là một người, mà là một cái chỉnh thể, liên hợp đối kháng, đem sinh mệnh giao cho đồng bạn, mới có thể tốt hơn phối hợp, đối đồng bạn nắm giữ càng lớn tín nhiệm."),t("br"),t("br"),n._v("“Được rồi, nội dung nói xong, cùng mọi người nói một chút quy định!”"),t("br"),t("br"),n._v("Mi trưởng lão vẻ mặt nghiêm túc, mang theo nghiêm túc: “Lần khảo hạch này, không cho phép danh sư tầm đó lẫn nhau cướp đoạt, chém giết! Nói cách khác, không thể vì nhận được càng nhiều điểm tích lũy, đi đánh cái khác danh sư biện pháp, một khi phát hiện, trực tiếp hủy bỏ tư cách, học viện vĩnh không mướn người!”"),t("br"),t("br"),n._v("Thanh âm đến cuối cùng, như là đá tảng vẫn lạc, để không khí chung quanh đều có chút ngưng kết."),t("br"),t("br"),n._v("Thánh giả giận dữ, thiên băng địa liệt."),t("br"),t("br"),n._v("Dù chỉ là vẻ mặt trầm thấp, cũng làm cho Hóa Phàm cảnh người, không dám phản bác, sinh không ra bất kỳ ý niệm phản kháng."),t("br"),t("br"),n._v("“Không cho phép danh sư cướp đoạt chém giết?”"),t("br"),t("br"),n._v("“Trước kia không phải có thể ư?”"),t("br"),t("br"),n._v("“Hình như là làm như vậy, rất dễ dàng gây nên danh sư ở giữa mâu thuẫn, lần khảo hạch này cố ý hủy bỏ!”"),t("br"),t("br"),n._v("“Ai, còn muốn lấy có thể đánh lén người khác cướp đoạt nội đan, hiện tại xem ra... Không được!”"),t("br"),t("br"),n._v("..."),t("br"),t("br"),n._v("Linh thú là có trí tuệ động vật, biết nhiều người như vậy vây giết, khẳng định đã sớm trốn đi, làm sao có thể dễ dàng tìm tới?"),t("br"),t("br"),n._v("Không ít thực lực mạnh tân sinh, sớm liền định được rồi, đến lúc đó ra tay cướp đoạt, nghe được không cho phép, toàn bộ đều có chút thất vọng."),t("br"),t("br"),n._v("Bất quá, càng nhiều người là thở phào nhẹ nhõm."),t("br"),t("br"),n._v("Thật vất vả săn giết một đầu linh thú, vô cùng có khả năng bản thân bị trọng thương, đến lúc đó, lại bị người đoạt đi, muốn khóc cũng không kịp."),t("br"),t("br"),n._v("“Mi trưởng lão, danh sư ở giữa không cho phép cướp đoạt, cái kia... Có thú sủng danh sư, có thể hay không mượn nhờ thú sủng, khắp nơi săn giết?”"),t("br"),t("br"),n._v("“Phong sư huynh” Phong Ngô hỏi lên."),t("br"),t("br"),n._v("Nghe được hắn, ánh mắt mọi người đồng loạt tập trung ở Trương Huyền trên người."),t("br"),t("br"),n._v("Vị này Trương sư, liên tục tại giao dịch hội dùng linh thú nội đan đổi lấy công pháp, không ít tân sinh đều biết, biết tất cả hắn có một đầu nửa bước cửu trọng cấp bậc Thiên Hùng thú."),t("br"),t("br"),n._v("Nếu như cái tên này dùng đầu này đại gia hỏa đi săn, ai là đối thủ?"),t("br"),t("br"),n._v("Bay được, thực lực lại mạnh, đoán chừng thứ nhất, không cần người khác, khẳng định là hắn."),t("br"),t("br"),n._v("“Có thú sủng danh sư, cũng coi như thực lực bản thân, bất quá... Thú sủng chỉ có thể dùng một lần, vượt qua một lần, nhận được lại nhiều nội đan, đều không cho tính toán thành tích!” Mi trưởng lão bàn tay lớn vẫy một cái."),t("br"),t("br"),n._v("Có thể thuần phục thú sủng, cũng là năng lực cá nhân, có điều, hoàn toàn dựa dẫm thú sủng, liền đã mất đi khảo hạch ý nghĩa."),t("br"),t("br"),n._v("“Chỉ có thể sử dụng một lần? Tốt!”"),t("br"),t("br"),n._v("Nghe nói như thế, Phong Ngô thở phào nhẹ nhõm."),t("br"),t("br"),n._v("Chỉ có thể sử dụng một lần, vị này Trương Huyền muốn thắng “Dư huynh” cơ bản không thể nào."),t("br"),t("br"),n._v("“Được rồi, còn có vấn đề gì không? Sớm hỏi ra, đừng trái với quy định, đã mất đi danh ngạch!”"),t("br"),t("br"),n._v("Giải thích xong thú sủng vấn đề, Mi trưởng lão lần nữa nhìn quanh một vòng."),t("br"),t("br"),n._v("“Trưởng lão, cái kia... Nếu có người có cấp năm trận pháp, có thể hay không sử dụng?” Lại một cái danh sư nói."),t("br"),t("br"),n._v("“Cấp năm trận pháp, cấp bậc cao vũ khí, đủ loại vượt qua bản thân tu vi đồ vật, cùng vừa rồi thú sủng đồng dạng, chỉ có thể dùng một lần!” Mi trưởng lão nói."),t("br"),t("br"),n._v("Mọi người tiếp lấy lại hỏi một vài vấn đề, Mi trưởng lão từng cái trả lời."),t("br"),t("br"),n._v("Rất nhanh, tất cả mọi người đối lần khảo hạch này đều triệt để rõ ràng, cũng đều hiểu học viện thiết kế cái khảo hạch này khổ tâm."),t("br"),t("br"),n._v("“Được rồi, nếu như không có vấn đề gì, khảo hạch chính thức bắt đầu, chư vị có thể lên núi!”"),t("br"),t("br"),n._v("Thấy mọi người lại không vấn đề, Mi trưởng lão vung tay lên: “Xuất phát!”"),t("br"),t("br"),n._v("“Tốt!”"),t("br"),t("br"),n._v("Nghe được mệnh lệnh, ba vạn tham gia khảo hạch tân sinh, lập tức như châu chấu, hướng trước mắt Lôi Viễn phong phóng đi."),t("br"),t("br"),n._v("Đi càng sớm, săn giết linh thú cơ hội càng lớn hơn, mọi người tất cả đều không chút nào dừng lại."),t("br"),t("br"),n._v("Convert by: ThấtDạ"),t("br"),t("br"),t("br"),t("br")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("a",{attrs:{href:"./chap-714"}},[n._v("Chương trước")]),n._v("  |  "),t("a",{attrs:{href:"./chap-716"}},[n._v("Chương sau")])])])}),[],!1,null,null,null);h.default=g.exports}}]);