(window.webpackJsonp=window.webpackJsonp||[]).push([[1990],{2346:function(n,h,t){"use strict";t.r(h);var g=t(44),i=Object(g.a)({},(function(){var n=this,h=n.$createElement,t=n._self._c||h;return t("ContentSlotsDistributor",{attrs:{"slot-key":n.$parent.slotKey}},[t("h1",{attrs:{id:"chuong-713-thuan-phuc-ong-mau"}},[t("a",{staticClass:"header-anchor",attrs:{href:"#chuong-713-thuan-phuc-ong-mau"}},[n._v("#")]),n._v(" Chương 713: Thuần phục ong mẫu")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("br"),t("br"),n._v("Chương 713: Thuần phục ong mẫu"),t("br"),t("br"),n._v("“Thú Lân hương mặc dù có để linh thú mê muội hiệu quả, nhưng muốn hôn mê Thiên Nghĩ Phong Mẫu... Vẫn là làm không được!”"),t("br"),t("br"),n._v("Đem một màn này nhìn ở trong mắt, Trương Huyền lắc đầu."),t("br"),t("br"),n._v("Thú Lân hương, mang theo đặc thù mê huyễn tác dụng, bình thường linh thú, dù là Hóa Phàm thất trọng đỉnh phong, ngửi bên trên một cái đều khó mà may mắn thoát khỏi, nhưng Thiên Nghĩ Phong Mẫu khác biệt."),t("br"),t("br"),n._v("Loại sinh mạng này, có thể phân ra khống chế tinh thần ngàn vạn đầu Thiên Nghĩ Phong, linh hồn cường đại dường nào!"),t("br"),t("br"),n._v("Muốn cho hắn hôn mê, nào có đơn giản như vậy."),t("br"),t("br"),n._v("Tiếp tục tiếp tục chờ đợi, một khi Thiên Nghĩ Phong Mẫu đi ra, khẳng định phiền phức không nhỏ."),t("br"),t("br"),n._v("“Thiên Nghĩ Phong sào huyệt ta khẳng định muốn cầm tới, một khi bị bọn họ phát hiện ra trước đây là ong mẫu vị trí, lại muốn lấy được tổ ong liền không quá dễ dàng...”"),t("br"),t("br"),n._v("Hai người an nguy, Trương Huyền cũng không ngại, dù sao liền Thú Lân hương thứ đồ tốt này đều có thể lấy ra, khó giữ nổi trong tay không có lợi hại hơn bảo vật."),t("br"),t("br"),n._v("Hắn bây giờ nghĩ càng nhiều hơn chính là, không thể để cho đối mới biết, đồ vật bên trong là cái gì!"),t("br"),t("br"),n._v("Loại này linh thú, thưa thớt cực kỳ, toàn bộ Hồng Viễn đế quốc, đều chưa hẳn có thể tìm tới con thứ hai đến, có thể gặp gỡ, đã mười phần may mắn, một khi rơi vào trong tay đối phương, lại muốn lấy được, liền hầu như không thể nào!"),t("br"),t("br"),n._v("Loại này có thể chứa đựng sinh mệnh không gian bảo vật, là không thể nào có người nguyện ý bán ra."),t("br"),t("br"),n._v("“Có muốn hay không ra tay đem bọn hắn đánh ngất xỉu?”"),t("br"),t("br"),n._v("Vịn cái cằm."),t("br"),t("br"),n._v("Muốn có được Thiên Nghĩ Phong sào huyệt, biện pháp tốt nhất chính là vọt thẳng đi vào, mà hai người này ngăn ở cửa động, thực sự không được, chỉ có thể trước đem bọn hắn đánh ngất xỉu."),t("br"),t("br"),n._v("“Được rồi, nếu là khảo hạch, làm không cẩn thận Mi trưởng lão khẳng định biết, vạn nhất bị định tính cướp đoạt, liền khảo hạch đều chẳng qua quan, liền được không bù mất...”"),t("br"),t("br"),n._v("Loại ý nghĩ này tại trong óc xoay một cái, liền lắc đầu."),t("br"),t("br"),n._v("Thánh giả năng lực, là hắn không có thể hiểu được, đã nhấn mạnh không thể cướp đoạt, chạy tới đem người đánh bất tỉnh, đem linh thú lấy đi, một khi truyền đi, phiền phức không ít, có chút được không bù mất."),t("br"),t("br"),n._v("“Phân thân!”"),t("br"),t("br"),n._v("Tinh thần khẽ động, phân thân xuất hiện ở trước mắt."),t("br"),t("br"),n._v("Ý niệm câu thông, đem ý nghĩ nói cho đối phương biết, bản thân thì Hồng Trần Đạp Thiên bộ vận chuyển, mượn nhờ nồng đậm cỏ cây che chở, nhanh chóng hướng sơn động phương hướng bay đi."),t("br"),t("br"),n._v("Khinh thân công pháp, duy trì khá hơn nữa, một khi đụng vào khu vực, cành lá, khó tránh khỏi sẽ có âm thanh, phi hành liền không loại này lo lắng."),t("br"),t("br"),n._v("Chỉ nếu không muốn bị phát hiện, coi như đối phương đi đến Quy Nhất cảnh, cũng chưa chắc biết."),t("br"),t("br"),n._v("Không lâu sau, đi tới hai người không xa, cách cách sơn động chỉ có vài chục mét địa phương, trốn đi."),t("br"),t("br"),n._v("..."),t("br"),t("br"),n._v("“Thú Lân hương khói mù đã đi vào nhiều như vậy, hẳn là có thể đem đầu kia linh thú mê choáng đi, nếu không chúng ta vào xem?”"),t("br"),t("br"),n._v("Nghe được Dư huynh luân phiên xác nhận, Phong Ngô lúc này mới yên tâm không ít, vừa nghĩ tới có thể có được Quy Nhất cảnh linh thú nội đan, liền kích động vẻ mặt đỏ lên."),t("br"),t("br"),n._v("Trước đó Mi trưởng lão nói, Trọc Thanh cảnh đỉnh phong nội đan, tích một điểm; Hợp Linh cảnh sơ kỳ, tích 10 điểm, trung kỳ 20 điểm, hậu kỳ 30 điểm, đỉnh phong 40 điểm! Kiều Thiên cảnh sơ kỳ linh thú 100 điểm, trung kỳ 200 điểm... Cứ thế mà suy ra, Quy Nhất cảnh linh thú nội đan, một đầu có thể đủ hối đoái 1000 điểm!"),t("br"),t("br"),n._v("Nhận được nhiều như vậy điểm tích lũy, tương đương với chém giết mười đầu Kiều Thiên cảnh sơ kỳ linh thú, coi như rất nhiều học sinh đại bộ phận Đô Thiên phú vô song, cũng đầy đủ thu hoạch được cực tốt thứ tự."),t("br"),t("br"),n._v("“Trước đừng có gấp, Quy Nhất cảnh linh thú, vạn pháp quy nhất, linh hồn mạnh hơn, tốt nhất nhiều chờ một lát, tuyệt đối không nên chủ quan!”"),t("br"),t("br"),n._v("Dư huynh cũng đầy là kích động, có điều, khắc chế muốn so với đối phương rất nhiều."),t("br"),t("br"),n._v("Hắn mặc dù đối Thú Lân hương rất có tự tin, nhưng dù sao cũng là Quy Nhất cảnh linh thú, hết thảy vẫn là cẩn thận cho thỏa đáng."),t("br"),t("br"),n._v("“Được!”"),t("br"),t("br"),n._v("Phong Ngô gật gật đầu, con mắt tỏa ánh sáng: “Chỉ cần săn giết đầu này, Dư huynh điểm tích lũy liền tiếp cận hai ngàn, vị kia Trương Huyền, coi như có thể mượn dùng nửa bước cửu trọng linh thú một lần, cũng khẳng định không nhiều như vậy!”"),t("br"),t("br"),n._v("Nhớ tới vị kia Trương Huyền, hắn đã cảm thấy buồn bực."),t("br"),t("br"),n._v("Bị đối phương một chỉ đánh bại, lại luân phiên làm nhục, đả kích đều không có lòng tin gì, nếu như lần này Dư huynh có thể đại hoạch toàn thắng, để hắn thất bại, đối lòng tự tin của hắn đều có tăng lên cực lớn."),t("br"),t("br"),n._v("“Cường đại tới đâu thú sủng, cũng phải tìm được trước linh thú lại nói! Linh thú cũng không tìm tới, như thế nào nhận được điểm tích lũy?”"),t("br"),t("br"),n._v("Dư huynh cười lạnh, trên mặt lộ ra tràn đầy tự tin."),t("br"),t("br"),n._v("Nửa bước cửu trọng thú sủng lại như thế nào?"),t("br"),t("br"),n._v("Nhiều nhất chỉ có thể tìm tới Kiều Thiên cảnh linh thú, có thể tìm tới Quy Nhất cảnh sao?"),t("br"),t("br"),n._v("Tìm không thấy, điểm tích lũy liền không khả năng so với chính mình nhiều!"),t("br"),t("br"),n._v("“Đúng vậy a...” Phong Ngô lên tiếng, chính muốn tiếp tục nói chuyện, đột nhiên nghe được ngoài sơn cốc có vang lên tiếng gió."),t("br"),t("br"),n._v("“Có người!”"),t("br"),t("br"),n._v("Dư huynh sầm mặt lại."),t("br"),t("br"),n._v("Phong Ngô cũng vội vàng nhìn sang."),t("br"),t("br"),n._v("Thật vất vả tìm tới lợi hại như thế linh thú, bọn họ cũng không muốn khiến người khác nhúng chàm."),t("br"),t("br"),n._v("“Là Quy Nhất cảnh linh thú, dấu vết lưu lại!”"),t("br"),t("br"),n._v("Đang tại âm thầm phòng bị, liền nghe phía ngoài tiếng gió ngừng lại, ngay sau đó một cái mang theo thanh âm trầm thấp vang lên."),t("br"),t("br"),n._v("“Quy Nhất cảnh linh thú?”"),t("br"),t("br"),n._v("Nghe được thanh âm, hai người đồng thời lông mày nhảy một cái."),t("br"),t("br"),n._v("Bọn họ mới vừa nói tìm được linh thú, liền có người phát hiện, không phải là đến cùng bọn hắn cướp đoạt a?"),t("br"),t("br"),n._v("Vù vù!"),t("br"),t("br"),n._v("Chính đang kỳ quái, liền nghe đến tiếng gió càng đi càng xa, hình như vị danh sư này, đuổi theo đầu kia Quy Nhất cảnh linh thú."),t("br"),t("br"),n._v("“Ngươi chăm sóc Thú Lân hương, ta đi qua nhìn một chút!”"),t("br"),t("br"),n._v("Nắm đấm xiết chặt, Dư huynh cũng nhịn không được nữa, bàn giao một tiếng, bàn chân đạp mạnh, thẳng tắp hướng cốc bên ngoài phóng đi."),t("br"),t("br"),n._v("Chẳng lẽ địa phương khác còn có Quy Nhất cảnh linh thú? Người này đuổi theo? Sẽ không như thế đúng dịp đi!"),t("br"),n._v("Đi tới cốc bên ngoài, quả nhiên thấy một bóng người thẳng tắp hướng về trên núi chạy như điên, có điều, đã đi xa, có chút thấy không rõ!"),t("br"),t("br"),n._v("Còn cái gọi là linh thú, dấu vết, hắn tìm nửa ngày, cái gì cũng không thấy."),t("br"),t("br"),n._v("“Cơ hội tốt...”"),t("br"),t("br"),n._v("Thấy Dư huynh đến lối vào thung lũng, bên này vị này Phong Ngô, cũng đem ánh mắt tập trung ở ngoài sơn cốc, giấu ở một bên Trương Huyền nhẹ nhàng cười một tiếng, “Hô!” Một chút, thẳng tắp hướng sơn động chui vào."),t("br"),t("br"),n._v("Câu nói mới vừa rồi kia cùng chạy trốn bóng lưng tự nhiên là phân thân."),t("br"),t("br"),n._v("Mục đích của hắn là thu hút sự chú ý của đối phương, sau đó nghĩ biện pháp vào sơn động, lần này đã hoàn thành."),t("br"),t("br"),n._v("Còn vì cái gì không cần thanh âm của mình, vạn nhất đối phương phát hiện trúng kế, cũng có thể phủi sạch quan hệ."),t("br"),t("br"),n._v("Hô!"),t("br"),t("br"),n._v("Không tiếp tục để ý phía ngoài phân thân, cùng Dư huynh, Phong Ngô hai người, Trương Huyền một vào sơn động, lập tức vận chuyển Minh Lý Chi Nhãn, chung quanh nhìn sang."),t("br"),t("br"),n._v("Sơn động tối tăm, âm hàn, không biết sâu bao nhiêu, trên mặt đất nằm một tầng Thiên Nghĩ Phong, xem ra đều là bị Thú Lân hương hun choáng."),t("br"),t("br"),n._v("Thú Lân hương liền Hóa Phàm lục thất trọng linh thú đều có thể hun choáng, một chút man thú tự nhiên ngăn cản không nổi."),t("br"),t("br"),n._v("Người trên không trung chậm rãi vào bên trong phi hành, đi một hồi, con mắt lập tức sáng lên."),t("br"),t("br"),n._v("Cách đó không xa quả nhiên có cái bóng đá lớn nhỏ tổ ong, treo ở trên vách đá, cho người ta một loại ngọc thạch đúc thành cảm giác, vừa nhìn liền biết không là phàm phẩm."),t("br"),t("br"),n._v("Tổ ong bên cạnh, là cái tràn đầy lông tơ, tuyết màu trắng linh thú, một người lớn nhỏ, trắng trắng mập mập, xa xa nhìn tới, như cùng một đầu người tuyết."),t("br"),t("br"),n._v("“Đây chính là Thiên Nghĩ Phong Mẫu?”"),t("br"),t("br"),n._v("Trương Huyền sững sờ."),t("br"),t("br"),n._v("Loại sinh mạng này, hắn cũng là ở trong sách nhìn qua giới thiệu, chưa bao giờ thấy qua, chưa từng có kỹ càng miêu tả, vốn cho rằng Thiên Nghĩ Phong đồng dạng, thật nhiều móng vuốt, tràn đầy dữ tợn, không nghĩ tới, thế mà bộ dáng này."),t("br"),t("br"),n._v("Hô hô hô!"),t("br"),t("br"),n._v("Trong lòng kỳ quái, chân khí trong cơ thể khống chế không nổi, tiết lộ một tia, trước mắt trắng như tuyết Thiên Nghĩ Phong Mẫu, giống như là phát hiện cái gì, con mắt một chút chằm chằm đi qua."),t("br"),t("br"),n._v("Sưu!"),t("br"),t("br"),n._v("Sau một khắc, liền đến đến gần, móng vuốt đối Trương Huyền liền bổ tới."),t("br"),t("br"),n._v("Không hổ là Quy Nhất cảnh linh thú, lực công kích kinh người, móng vuốt không đi tới gần, liền cho người ta một loại kịch liệt áp bách, phảng phất muốn đem không khí chấn vỡ."),t("br"),t("br"),n._v("Quy Nhất cảnh, vạn pháp quy nhất, lực lượng càng thêm chất phác, tinh khiết, càng thêm phù hợp tự nhiên, hồn lực cũng sẽ trở nên càng thêm cường đại."),t("br"),t("br"),n._v("Một trảo này thoạt nhìn đơn giản, nhưng đem tinh khí thần dung hợp tại một chỗ, cho người ta một loại khó mà chống chọi cảm giác."),t("br"),t("br"),n._v("“A!”"),t("br"),t("br"),n._v("Biết che giấu có điều, Trương Huyền cũng không tiếp tục ẩn giấu, thân thể giãn ra, người trên không trung, liền một quyền tiến lên đón."),t("br"),t("br"),n._v("Kiều Thiên cảnh đỉnh phong thực lực, điên cuồng vận chuyển, phối hợp thân thể, linh hồn lực lượng, có thể so với Hóa Phàm bát trọng."),t("br"),t("br"),n._v("Răng rắc!"),t("br"),t("br"),n._v("Quyền trảo va chạm, Thiên Nghĩ Phong Mẫu còn không có kịp phản ứng, trắng như tuyết thân thể liền bay ngược mà ra, thẳng tắp đụng ở trên vách tường, đập ra một cái hố to."),t("br"),t("br"),n._v("Đơn thuần lực lượng đối đầu, nó kém lớn hơn nhiều lắm."),t("br"),t("br"),n._v("Nếu không phải linh thú thể chất đặc thù, khẳng định lần này cũng đã bị tại chỗ đánh chết."),t("br"),t("br"),n._v("Ông!"),t("br"),t("br"),n._v("Bị một quyền đánh bay, Thiên Nghĩ Phong Mẫu dường như cũng cảm giác được trước mắt người này mạnh mẽ, thân thể chấn động, từ trên vách tường xông ra, thẳng tắp hướng tổ ong bay đi, dường như biết người trước mắt này nguy hiểm, muốn muốn lấy đi tổ ong, trốn đi đâm ra."),t("br"),t("br"),n._v("“Muốn đi?”"),t("br"),t("br"),n._v("Biết một khi bị nó cướp đi tổ ong, lại muốn tóm lấy liền khó khăn, bàn chân trên mặt đất đạp mạnh, Trương Huyền Thiên Đạo thân pháp thi triển, “Hô!” Một chút, đi tới tổ ong gần, bàn tay một phong chặn lại rồi đường đi của đối phương."),t("br"),t("br"),n._v("Lòng bàn tay chân khí nhúc nhích, đem không khí chung quanh đều phong tỏa."),t("br"),t("br"),n._v("Tu vi đi đến Kiều Thiên cảnh đỉnh phong, khí tức như cầu vồng, đối phương liền xem như Quy Nhất cảnh cường giả, muốn phá vỡ, cũng rất khó."),t("br"),t("br"),n._v("“Ong ong!”"),t("br"),t("br"),n._v("Thấy cái này nhân loại, không cho nó lấy đi tổ ong, Thiên Nghĩ Phong Mẫu tràn đầy lo lắng, miệng há ra, lộ ra sắc bén răng."),t("br"),t("br"),n._v("“Không thể tới triền đấu...”"),t("br"),t("br"),n._v("Biết cùng cái tên này quấn đấu nữa, phiền phức không nhỏ, nhất là đầy đất Thiên Nghĩ Phong, một khi tỉnh táo lại, mình coi như tu vi cao thâm, cũng khó có thể chống chọi, lúc này lông mày giương lên, trên người lực lượng toàn lực vận chuyển, một chưởng đánh xuống."),t("br"),t("br"),n._v("Bành!"),t("br"),t("br"),n._v("Ngàn phong kiến chịu đến trọng kích, lần nữa đụng như bức tường, từng ngụm từng ngụm hộc máu."),t("br"),t("br"),n._v("Cái này Trương Huyền dùng lực lượng cực lớn, coi như nó là linh thú chi thân, phòng ngự cực mạnh, cũng bị trọng thương."),t("br"),t("br"),n._v("“Thần phục ta, làm ta thú sủng, ta có thể giúp ngươi tăng thực lực lên, khôi phục thương thế, nếu không... Chết!”"),t("br"),t("br"),n._v("Một chiêu đem hắn đánh trọng thương, Trương Huyền hai tay chắp sau lưng, yết hầu nhúc nhích, Thượng Cổ thú ngữ nói ra."),t("br"),t("br"),n._v("Nói xong, một đạo chân khí phun ra ngoài, tinh thuần Thiên Đạo chân khí, tựa như khe suối, cho người ta một loại toàn thân lỗ chân lông mở rộng cảm giác."),t("br"),t("br"),n._v("“Ông! Ông! Ông!”"),t("br"),t("br"),n._v("Nhìn thấy cỗ này chân khí tinh thuần mức độ, cảm nhận được thương thế trên người, Thiên Nghĩ Phong Mẫu biết còn dám chần chờ, đối phương tuyệt đối dám đem hắn chém giết, chần chờ một chút, nói tiếp thứ gì."),t("br"),t("br"),n._v("“Ừm, khế ước đi!”"),t("br"),t("br"),n._v("Biết đối mới hiểu quyết tâm của mình, chủ động thần phục, Trương Huyền hài lòng nhẹ gật đầu."),t("br"),t("br"),n._v("Có Thiên Đạo chân khí cùng thú ngữ phối hợp, thuần phục linh thú so trước kia càng thêm dễ dàng, căn bản không cần nói nhảm quá nhiều."),t("br"),t("br"),n._v("“Cái này...”"),t("br"),t("br"),n._v("Không lâu sau, khế ước hoàn thành, Trương Huyền một ngụm trọc khí phun ra, ngẩng đầu nhìn về phía trước mắt tổ ong, vẻ mặt tràn đầy cổ quái."),t("br"),t("br"),n._v("Convert by: ThấtDạ"),t("br"),t("br"),t("br"),t("br")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("a",{attrs:{href:"./chap-721"}},[n._v("Chương trước")]),n._v("  |  "),t("a",{attrs:{href:"./chap-723"}},[n._v("Chương sau")])])])}),[],!1,null,null,null);h.default=i.exports}}]);