(window.webpackJsonp=window.webpackJsonp||[]).push([[1994],{2348:function(n,h,t){"use strict";t.r(h);var i=t(44),g=Object(i.a)({},(function(){var n=this,h=n.$createElement,t=n._self._c||h;return t("ContentSlotsDistributor",{attrs:{"slot-key":n.$parent.slotKey}},[t("h1",{attrs:{id:"chuong-717-sup-đo-tu-duong-thu"}},[t("a",{staticClass:"header-anchor",attrs:{href:"#chuong-717-sup-đo-tu-duong-thu"}},[n._v("#")]),n._v(" Chương 717: Sụp đổ Tử Dương thú")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("br"),t("br"),n._v("Chương 717: Sụp đổ Tử Dương thú"),t("br"),t("br"),n._v("Lôi Viễn phong chân núi."),t("br"),t("br"),n._v("Lưu quang phóng tới, một bóng người cùng một đầu linh thú phi hành, thẳng tắp rơi xuống."),t("br"),t("br"),n._v("“Mi trưởng lão!”"),t("br"),t("br"),n._v("Bóng người đi tới gần, ôm quyền mỉm cười."),t("br"),t("br"),n._v("“Mạc đường chủ!” Mi trưởng lão gấp vội hoàn lễ."),t("br"),t("br"),n._v("Không phải người khác, chính là Hồng Viễn đế quốc Danh Sư đường đường chủ, Mạc Cao Viễn!"),t("br"),t("br"),n._v("Mặc dù hắn không thuộc về thập đại trường lão một trong, địa vị cùng thực lực, nhưng so với bọn hắn không hề yếu."),t("br"),t("br"),n._v("“Lang Gia Thánh thú!” Mi trưởng lão lần nữa nhìn về phía cách đó không xa linh thú phi hành, cũng ôm quyền."),t("br"),t("br"),n._v("Mạc Cao Viễn thú sủng, Thánh cấp nhất trọng sơ kỳ Lang Gia thú!"),t("br"),t("br"),n._v("Cùng là Thánh cấp, lễ tiết bên trên không dám chậm trễ chút nào."),t("br"),t("br"),n._v("“Gặp qua Mi trưởng lão!” Thánh thú mở miệng nói tiếng người."),t("br"),t("br"),n._v("“Không biết Mạc đường chủ cùng Thánh thú tới, cần làm chuyện gì?” Làm lễ ra mắt xong xuôi, Mi trưởng lão nghi ngờ nhìn qua."),t("br"),t("br"),n._v("Danh sư học viện cùng Hồng Viễn Danh Sư đường, thuộc về cùng cấp quan hệ, cùng thuộc phong hào đế quốc khống chế, tuyển nhận tân sinh, đối phương là không tư cách chen vào nói."),t("br"),t("br"),n._v("“Nghe nói đến vô số thiên tài, nhưng mà tùy tiện tới xem một chút...” Mạc Cao Viễn cười cười."),t("br"),t("br"),n._v("Đã nhập môn khảo hạch bắt đầu, vị kia Trương sư khẳng định cũng tới, nhìn xem có thể hay không giữ gìn mối quan hệ, thừa cơ hỏi thăm một chút Dương sư tình huống."),t("br"),t("br"),t("br"),n._v("“Quả thực đến không ít thiên tài, có điều, có thể hay không thông qua khảo hạch, còn chưa biết được!” Nghe đối phương không có việc gì, Mi trưởng lão thở phào nhẹ nhõm, mỉm cười vuốt râu."),t("br"),t("br"),n._v("“Ồ? Mi trưởng lão cớ gì nói ra lời ấy?” Mạc Cao Viễn kỳ quái."),t("br"),t("br"),n._v("Nếu là khảo hạch, tự nhiên phần lớn người đều có thể thông qua, nếu không, thiết lập hạng mục cũng liền không có ý nghĩa."),t("br"),t("br"),n._v("“Lần khảo hạch này không phải đi săn Hóa Phàm tứ trọng đỉnh phong linh thú ư? Đối với những thiên tài này danh sư tới nói, nhiệm vụ này, muốn phải hoàn thành, không khó lắm đi!”"),t("br"),t("br"),n._v("Danh sư học viện khảo hạch, là Hồng Viễn đế quốc thịnh sự, làm Danh Sư đường đường chủ, làm sao không hiểu biết."),t("br"),t("br"),n._v("Tứ tinh đỉnh phong danh sư, đối mặt Hóa Phàm tứ trọng linh thú, mặc dù trên thực lực còn hơi có không bằng, nhưng muốn phải hoàn thành nhiệm vụ, vẫn là không khó."),t("br"),t("br"),n._v("“Danh sư học viện, là tuyển nhận danh sư địa phương, cũng không phải là ai thực lực mạnh, ai man lực đại liền có thể trúng tuyển, nếu như nhưng mà đi săn, có phần quá dễ dàng chút!” Mi trưởng lão cười một tiếng: “Lần khảo hạch này, không đơn giản như vậy!”"),t("br"),t("br"),n._v("“Ồ?” Mạc đường chủ sững sờ, cùng Lang Gia Thánh thú nhìn nhau, đều tràn đầy kỳ quái nhìn qua."),t("br"),t("br"),n._v("Danh sư không phải đồ tể, lấy giáo hóa làm chủ, thực đang giáo hóa không được, mới muốn tiêu diệt."),t("br"),t("br"),n._v("Lôi Viễn phong bắt đến rất nhiều linh thú, coi như làm nhiều việc ác, cũng có sửa đổi cơ hội, không cần thiết đuổi tận giết tuyệt, săn bắt nội đan đi!"),t("br"),t("br"),n._v("Trước đó, bọn họ nghe được khảo hạch chuẩn tắc cũng có chút kỳ quái, nghe được Mi trưởng lão lời này, chỉ sợ cũng không phải là nghe nói như vậy dễ dàng, có lẽ có ẩn tình khác."),t("br"),t("br"),n._v("“Lần khảo hạch này bên ngoài là đi săn, ai lấy được nội đan nhiều, ai liền chiến thắng, trên thực tế là khảo hạch danh sư ứng biến cùng phản ứng, cùng đối mặt nguy hiểm xử lý phương pháp!”"),t("br"),t("br"),n._v("Đối với Mạc Cao Viễn cũng không có gì có thể giấu diếm, Mi trưởng lão mặt mũi mỉm cười: “Kỳ thật trên núi rất nhiều linh thú, sớm đã chỉnh hợp được rồi, chỉ cần những học sinh mới này đi vào sơn mạch, liền sẽ rơi vào bọn chúng bố trí tốt bẫy rập, gặp phải vây công!”"),t("br"),t("br"),n._v("“Vây công?” Mạc Cao Viễn lông mày nhíu một cái, thoáng lo lắng: “Đây đều là Danh Sư đường tương lai...”"),t("br"),t("br"),n._v("“Yên tâm đi, linh thú vây công, chỉ làm cho hắn không thể chạy trốn, bị thương, sẽ không đả thương hắn tính mạng!” Biết đối phương lo lắng cái gì, Mi trưởng lão nói."),t("br"),t("br"),n._v("Đây đều là Danh Sư đường tương lai, nhân tộc xà nhà trụ cột, coi như hắn đúng đúng thập đại trường lão một trong, cũng không dám lạm sát."),t("br"),t("br"),n._v("Tất cả linh thú, nhưng mà vây mà không giết mà thôi, cũng không phải là diệt tuyệt."),t("br"),t("br"),n._v("Không phải như vậy, Đinh sư đám người bị mấy trăm đầu linh thú vây công, làm sao có thể kiên trì đến Trương Huyền tới cứu, còn một cái không chết?"),t("br"),t("br"),n._v("Chỉ bất quá tình cảnh nguy cấp, không có người nghĩ đến mà thôi, ngay cả Trương Huyền, cũng không quá chú ý."),t("br"),t("br"),n._v("“Thì ra là thế!”"),t("br"),t("br"),n._v("Mạc Cao Viễn thở phào nhẹ nhõm, con mắt càng ngày càng sáng: “Linh thú kết minh, bố trí hạ bẫy rập, cùng danh sư đấu trí đấu dũng, mặc kệ có thể hay không nhìn ra, đối những học sinh mới này tới nói, đều là một lần rất tốt rèn luyện, để bọn hắn rõ ràng, liền xem như thú, cũng không phải có thể tùy ý chém giết, tùy ý cướp đoạt! Đồng thời còn có thể bồi dưỡng đoàn bọn hắn kết hợp tác năng lực, nhận thức đến năng lực cá nhân tại khảo hạch bên trong, không có ý nghĩa... Không hổ là Mi trưởng lão, lợi hại!”"),t("br"),t("br"),n._v("Trúng linh thú mà tính, rất nhiều tân sinh, tự nhiên cũng là rõ ràng, danh sư cũng không phải vạn năng, không kiêu không ngạo, còn không có tiến vào học viện, chẳng khác nào lên bài học."),t("br"),t("br"),n._v("Nghe được khen ngợi, Mi trưởng lão mỉm cười gật đầu."),t("br"),t("br"),n._v("“Biện pháp là tốt, có điều, người chấp hành là linh thú, có chút khó làm, cũng là Mi trưởng lão loại này lục tinh đỉnh phong thuần thú sư, mới có thể làm đến đi!”"),t("br"),t("br"),n._v("Cảm khái một câu, Mạc Cao Viễn tràn đầy bội phục."),t("br"),t("br"),n._v("Đã khảo hạch ba vạn danh sư, trên núi linh thú, tuyệt đối không dưới hai, ba vạn, để nhiều như vậy kiêu căng khó thuần gia hỏa ngoan ngoãn nghe lời, đối danh sư vây mà không giết, cần mạnh cỡ nào lực khống chế?"),t("br"),t("br"),n._v("“Ta? Ta nào có như thế bản lĩnh, tự nhiên là mượn Thánh thú lực lượng!”"),t("br"),t("br"),n._v("Mi trưởng lão cười khổ lắc đầu."),t("br"),t("br"),n._v("Hắn mặc dù là lục tinh đỉnh phong thuần thú sư, nhưng muốn muốn thuần phục mấy vạn con linh thú, để hắn ngoan ngoãn nghe lời, vẫn là làm không được."),t("br"),t("br"),n._v("“Thánh thú? Mi trưởng lão Hỏa Long Thánh thú?” Mạc Cao Viễn sững sờ."),t("br"),t("br"),n._v("Người người đều biết, Mi trưởng lão có một đầu thú sủng, Thánh giả nhất trọng trung kỳ Hỏa Long Thánh thú, dâng trào hỏa diễm, thực lực phi phàm."),t("br"),t("br"),n._v("“Hỏa Long? Cái tên này tính cách táo bạo, không gây họa là tốt lắm rồi, cái nào có thể làm được loại sự tình này, là ta hao tốn vô số đại giới, xin Tử Dương Thánh thú ra tay!” Mi trưởng lão cười khổ nói."),t("br"),t("br"),n._v("“Tử Dương? Học viện lão viện trưởng thú sủng?” Mạc Cao Viễn con ngươi co rụt lại."),t("br"),t("br"),n._v("Mi trưởng lão gật đầu."),t("br"),n._v("“Là nó ra tay, cái kia an tâm, lão viện trưởng lần kia thí luyện lại không trở về, học viện sự tình mặc dù tạm thời do thập đại trường lão liên hợp xử lý, nhưng trên thực tế nó tại học viện địa vị, cùng viện trưởng tương đương, không ai dám vi phạm. Do nó thống lĩnh rất nhiều linh thú, những người này, khẳng định không dám phản kháng!”"),t("br"),t("br"),n._v("Mạc Cao Viễn nở nụ cười."),t("br"),t("br"),n._v("“Tử Dương Thánh thú, tinh thông Thượng Cổ thú ngữ, do nó ra mặt, thí luyện không lo!” Mi trưởng lão lên tiếng."),t("br"),t("br"),n._v("Lão viện trưởng sau khi mất tích, Tử Dương Thánh thú một mực uể oải suy sụp, hắn là hao tốn không biết giá lớn bao nhiêu, mới để cho hắn đáp ứng, suy nghĩ một chút trả ra đại giới, đến bây giờ, đều cảm thấy nhức nhối."),t("br"),t("br"),n._v("Bất quá, dù vậy, hiệu quả cũng thật là tốt."),t("br"),t("br"),n._v("Do tinh thông Thượng Cổ thú ngữ Tử Dương Thánh thú ra mặt, thí luyện tất nhiên sẽ không có sơ hở nào, không xuất hiện bất kỳ vấn đề."),t("br"),t("br"),n._v("Thánh thú có thể miệng nói tiếng người, dựa theo đạo lý, có thể đem Thượng Cổ thú phiên dịch ra đến, truyền cho thuần thú sư, nhưng... Biết âm tiết, đối ứng ý tứ cùng dùng ngôn ngữ nói ra, là hai chuyện khác nhau!"),t("br"),t("br"),n._v("Thượng Cổ thú ngữ, là phối hợp linh thú thể chất mà thiết trí, coi như biết âm tiết, không biết phát âm kỹ xảo, không có đối ứng công pháp phương thức vận chuyển, cũng là không thể nào nói ra được tới."),t("br"),t("br"),n._v("Do đó, muốn cùng Thánh thú học tập Thượng Cổ thú ngữ, liền xem như thuần thú sư, cũng rất khó làm đến."),t("br"),t("br"),n._v("Lại nói, Thánh thú đã có thể nói ra ngôn ngữ nhân loại, câu thông không ngại, rất nhiều đến đến lục tinh trở lên thuần thú sư, cũng cũng không cần phải chuyên môn học tập thú ngữ."),t("br"),t("br"),n._v("“Tử Dương Thánh thú ra tay, khẳng định không có vấn đề, chỉ hy vọng nó đừng như vậy nghiêm khắc, cho rất nhiều học viên một cái cơ hội thông qua, thật muốn công chính nghiêm minh, một cái quá quan đều không có, liền nguy rồi...”"),t("br"),t("br"),n._v("Mạc Cao Viễn nói."),t("br"),t("br"),n._v("Tử Dương Thánh thú tại học viện địa vị, cùng cấp viện trưởng, luôn luôn làm việc nghiêm cẩn, nếu là thật quá chăm chỉ, thật đúng là sợ những học sinh mới, khó mà thông qua."),t("br"),t("br"),n._v("“Yên tâm đi, điểm ấy ta đã sớm cùng Thánh thú thương nghị qua, trước vây khốn, chờ đến thời gian không sai biệt lắm, sẽ cho cùng cho đi...” Mi trưởng lão gật gật đầu, nói được nửa câu, lập tức cười chỉ về phía trước: “Ngươi nhìn, đã có người thành công xuống núi!”"),t("br"),t("br"),n._v("Mạc Cao Viễn thuận ngón tay hắn nhìn lại, quả nhiên thấy một đám người từ trên ngọn núi đi xuống, hướng bên này đi tới."),t("br"),t("br"),n._v("Hiện tại mới vừa đến giữa trưa, khoảng cách buổi chiều còn có một đoạn thời gian, lúc này trở về, tất nhiên là hoàn thành nhiệm vụ, thông qua được khảo hạch, tới phục mệnh."),t("br"),t("br"),n._v("“Cái kia, cái kia... Là cái gì?”"),t("br"),t("br"),n._v("Nhìn thấy bóng người, Mi trưởng lão chính muốn đi qua kiểm nghiệm điểm tích lũy, con mắt đột nhiên một chút trợn tròn."),t("br"),t("br"),n._v("Chỉ thấy đám người này sau lưng, mấy trăm con linh thú theo ở phía sau, nguyên một đám đầu rũ cụp lấy, như là đấu bại gà trống."),t("br"),t("br"),n._v("“Cái này... Hình như là bị thuần phục?”"),t("br"),t("br"),n._v("Nuốt ngụm nước bọt, Mạc Cao Viễn cũng quai hàm đều rơi địa phương."),t("br"),t("br"),n._v("Thân là danh sư kiêm thuần thú sư, linh thú bộ dáng có thể nhìn ra, rất rõ ràng là bị người thuần phục, nếu không không sẽ như thế bộ dạng phục tùng ghé mắt, dịu dàng như nước."),t("br"),t("br"),n._v("Gần hai trăm người, thuần phục bốn, năm trăm đầu linh thú?"),t("br"),t("br"),n._v("Không phải nói, linh thú liên minh muốn đem học viên vây khốn, để bọn hắn biết khó khăn ư? Không phải nói Tử Dương Thánh thú tự mình tổ chức ư?"),t("br"),t("br"),n._v("Đây là... Chuyện gì xảy ra?"),t("br"),t("br"),n._v("..."),t("br"),t("br"),n._v("Một bên khác."),t("br"),t("br"),n._v("“Để cho ta nhận chủ, nằm mơ...”"),t("br"),t("br"),n._v("Tử Dương Thánh thú răng cắn chặt."),t("br"),t("br"),n._v("Nó là lão viện trưởng thú sủng, mặc dù viện trưởng tám chín phần mười chết rồi, nhưng muốn cho nó thần phục một cái Dị Linh tộc nhân, không có khả năng!"),t("br"),t("br"),n._v("Thú cũng có chuẩn tắc!"),t("br"),t("br"),n._v("Đi theo lão viện trưởng nhiều năm như vậy, đã sớm đem nhân loại lợi ích đặt ở vị thứ nhất, cho dù chết, cũng mà không có khả năng hướng kẻ địch hàng phục!"),t("br"),t("br"),n._v("“Nằm mơ?”"),t("br"),t("br"),n._v("Không nghĩ đến cái này cùng Dị Linh tộc nhân cấu kết Thánh thú, như thế có cốt khí, Trương Huyền lắc đầu: “Cho ta tiếp tục đánh, đánh đến nó khuất phục mới thôi!”"),t("br"),t("br"),n._v("Nghe được phân phó, rất nhiều khôi lỗi lại là một hồi quyền đấm cước đá."),t("br"),t("br"),n._v("Sau năm phút."),t("br"),t("br"),n._v("Có thể so với viện trưởng đồng dạng địa vị, mệnh danh học viện mạnh nhất sức chiến đấu Tử Dương Thánh thú, đã bị đánh hơi thở nhiều hít vào thì ít, bất cứ lúc nào cũng sẽ cúp máy."),t("br"),t("br"),n._v("“Được rồi, không thần phục ta cũng không sao, ta hiện đang hỏi ngươi, ngươi muốn thành thật trả lời, nếu không, coi như thân thể chết, ta cũng có thể đem linh hồn ngươi rút ra luyện chế, để ngươi muốn sống không được, muốn chết không xong!”"),t("br"),t("br"),n._v("Thấy đầu này Tử Dương thú vẫn là xương cứng, nhanh bị đánh chết, đều không nhận chủ, Trương Huyền lắc đầu nhìn qua, ánh mắt ngưng lại: “Ngươi có phải hay không bị Dị Linh tộc sai khiến, tới tàn sát rất nhiều danh sư? Chỉ cần nói ra những cái kia Dị Linh tộc nhân chuẩn xác vị trí, có thể cho ngươi thống khoái!”"),t("br"),t("br"),n._v("“Dị Linh tộc nhân... Sai khiến?”"),t("br"),t("br"),n._v("Nâng lên sưng đỏ mắt, Tử Dương Thánh thú suýt chút nữa không đánh tới."),t("br"),t("br"),n._v("Len sợi sai khiến ah!"),t("br"),t("br"),n._v("Là Mi trưởng lão tại chúng ta trước cầu tròn hai tháng, tuyên dương đủ loại đại nghĩa, lại hứa hẹn đủ loại chỗ tốt, ta mới tới, mục đích là vì tôi luyện rất nhiều tân sinh, thay chủ nhân bồi dưỡng càng nhiều, cường đại hơn danh sư..."),t("br"),t("br"),n._v("Cùng Dị Linh tộc có nửa xu quan hệ?"),t("br"),t("br"),n._v("Ngược lại là ngươi!"),t("br"),t("br"),n._v("Dùng Dị Linh tộc trái tim của cao thủ đối phó ta, sau đó lại dùng Dị Linh tộc khôi lỗi cuồng ẩu..."),t("br"),t("br"),n._v("Cuối cùng..."),t("br"),t("br"),n._v("Nhưng hỏi ta có phải hay không bị Dị Linh tộc nhân sai khiến, có xấu hổ hay không?"),t("br"),t("br"),n._v("Ta không mang theo như thế trả đũa!"),t("br"),t("br"),n._v("Convert by: ThấtDạ"),t("br"),t("br"),t("br"),t("br")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("a",{attrs:{href:"./chap-725"}},[n._v("Chương trước")]),n._v("  |  "),t("a",{attrs:{href:"./chap-727"}},[n._v("Chương sau")])])])}),[],!1,null,null,null);h.default=g.exports}}]);