(window.webpackJsonp=window.webpackJsonp||[]).push([[20],{376:function(n,h,c){"use strict";c.r(h);var t=c(44),i=Object(t.a)({},(function(){var n=this,h=n.$createElement,c=n._self._c||h;return c("ContentSlotsDistributor",{attrs:{"slot-key":n.$parent.slotKey}},[c("h1",{attrs:{id:"chuong-994-khao-hach-đoc-su-phuong-phap"}},[c("a",{staticClass:"header-anchor",attrs:{href:"#chuong-994-khao-hach-đoc-su-phuong-phap"}},[n._v("#")]),n._v(" Chương 994: Khảo hạch độc sư phương pháp")]),n._v(" "),c("hr"),n._v(" "),c("p",[n._v("Chương 994: Khảo hạch độc sư phương pháp"),c("br"),c("br"),n._v("“Nếu như không tiện, không nhìn cũng được...”"),c("br"),c("br"),n._v("Thấy hắn mặt lộ vẻ khó khăn, Hứa trưởng lão lắc đầu."),c("br"),c("br"),n._v("“Cũng không phải không tiện, mà là truyền thừa không tiện rò rỉ... Như vậy đi, ta có thể đem trong đó nhị tinh trở xuống kiến thức căn bản, nói rõ chi tiết một chút, có lẽ Hứa trưởng lão có thể thông qua cái này, đoán ra phần này bí tịch lai lịch!”"),c("br"),c("br"),n._v("Thấy đối phương chỉ là thuận miệng hỏi một chút, cũng không phải là truy xét, Trương Huyền thở phào nhẹ nhõm."),c("br"),c("br"),n._v("Bí tịch hắn căn bản không có, đối độc tri thức, cũng chỉ dừng lại ở nhị tinh trái phải, sở dĩ có thể làm cho đối phương cảm thấy mười phần đáng sợ, là bởi vì Thiên Đạo chân khí, bách độc bất xâm."),c("br"),c("br"),n._v("Bởi vậy, muốn cho hắn giảng giải cái gì độc, có thể nói hoàn toàn là thường dân, nhưng muốn giảng giải nhị tinh trở xuống kiến thức căn bản, có thể cam đoan, không có người có thể đủ thắng quá."),c("br"),c("br"),n._v("“Tốt!”"),c("br"),c("br"),n._v("Hứa trưởng lão ánh mắt sáng lên."),c("br"),c("br"),n._v("Rất nhiều truyền thừa, quả thực là không tiện tiết lộ, nếu không, sư đạo cũng sẽ không như vậy chịu đến kính trọng."),c("br"),c("br"),n._v("Đối phương không muốn nói, có thể lý giải, còn nhị tinh trở xuống, thuộc về cơ sở, nói lên một chút, đổ không ảnh hưởng toàn cục."),c("br"),c("br"),n._v("“Căn cứ quyển sách kia giải thích, độc là thuốc đến một loại, nói trắng ra là, thật ra thì cũng là vì điều cùng nhân thể âm dương cùng ngũ hành, ngũ hành thuận, bách độc bất xâm...”"),c("br"),c("br"),n._v("Đem nhị tinh Thiên Đạo độc công theo trong óc lật ra đến, Trương Huyền giải thích."),c("br"),c("br"),n._v("“Cái này, cái này...”"),c("br"),c("br"),n._v("Vừa mới bắt đầu cảm thấy nhị tinh trở xuống cơ sở không có gì, nghe một hồi, Hứa trưởng lão vẻ mặt thay đổi, kích động thân thể run rẩy, hô hấp dồn dập."),c("br"),c("br"),n._v("Đối phương tri thức, là đem thân thể người lấy Âm Dương Ngũ Hành tới lần nữa cấu thành khung, nguyên nhân chính là như vậy, mới có thể dùng kịch độc mà không thương tổn."),c("br"),c("br"),n._v("Nghe nói qua hạ độc chết người, có nghe nói qua, đem ông trời hạ độc chết sao?"),c("br"),c("br"),n._v("Đối phương thuận miệng giảng giải kiến thức căn bản, nhắm thẳng vào độc sư thâm ảo nhất nghĩa, chỉ cần nghiêm túc tu luyện, trở thành cái gọi là Tiên Thiên độc thể, đều là chắc chắn."),c("br"),c("br"),n._v("Mặc dù hắn hiện tại đã không thể sửa đổi công pháp, nhưng có thể đoán được, cái này tri thức một khi rò rỉ, tất nhiên gây nên cực lớn rung chuyển, làm không cẩn thận, toàn bộ Độc điện đều sẽ vì thế đổ xuống."),c("br"),c("br"),n._v("“Độc... Có thể cứu người, có thể trị bệnh, có thể làm thành thuốc bổ, chỉ cần tồn hồ nhất tâm, vận dụng thoả đáng, cũng có thể trở thành một sự giúp đỡ lớn...”"),c("br"),c("br"),n._v("Trương Huyền thanh âm tiếp tục vang lên."),c("br"),c("br"),n._v("Càng nghe càng cảm giác say mê, Hứa trưởng lão nắm đấm không tự chủ được xiết chặt."),c("br"),c("br"),n._v("Độc sư sở dĩ bị người sở kinh sợ, cũng là bởi vì, thủ đoạn kỳ lạ, khó lòng phòng bị, chẳng biết lúc nào sẽ bị độc chết, nếu quả thật có thể cùng đối phương giảng giải đồng dạng, đem hắn vận dụng tại chính đạo, chưa hẳn không thể để cho cái nghề nghiệp này lại thấy ánh mặt trời, cùng những nghề nghiệp khác đồng dạng, thản nhiên đi lại tại Danh Sư đường, chịu hắn che chở, mà không phải hiện tại chuột chạy qua đường, mọi người tránh chi xà hạt."),c("br"),c("br"),n._v("Muốn đến nơi này, lần nữa nhìn về phía cách đó không xa Tôn Cường, Hứa trưởng lão giống như là nhìn xem một cái bảo tàng khổng lồ."),c("br"),c("br"),n._v("“Được rồi, ta liền hiểu nhiều như vậy...”"),c("br"),c("br"),n._v("Đem nhị tinh trở xuống tri thức, tùy tiện giảng giải một phen, Trương Huyền mới vừa dừng lại, liền thấy Hứa trưởng lão tràn đầy lửa nóng ánh mắt, nhịn không được cúi đầu nhìn một chút trước ngực quần áo, có vẻ như... Không có lộ!"),c("br"),c("br"),n._v("Không có lộ... Ngươi kích động cái gì!"),c("br"),c("br"),n._v("Nghe cái khóa, chẳng lẽ, còn đem lấy hướng đều nghe thay đổi?"),c("br"),c("br"),n._v("Coi như danh sư... Cũng không có chức năng này ah!"),c("br"),c("br"),n._v("“Khụ khụ...”"),c("br"),c("br"),n._v("Nhìn thấy động tác của hắn, Hứa trưởng lão biết mình thất thố, bị nước bọt sặc một cái, vẻ mặt nghiêm túc: “Tôn độc sư, ngươi phần này bí tịch, thực sự quá mức kinh thế hãi tục, tốt nhất đừng nói cho những người khác, nếu không, ta sợ... Gặp được cực lớn nguy hiểm!”"),c("br"),c("br"),n._v("Chỉ cần là độc sư, ai không muốn bách độc bất xâm? Ai không muốn, chế biến ra càng cường đại hơn độc dược?"),c("br"),c("br"),n._v("Nếu là cho những người khác biết, cái tên này cầm giữ có lợi hại như thế bí tịch, thất phu vô tội mang ngọc có tội, sợ rằng sẽ bị đủ loại ám toán, nguy hiểm vô cùng."),c("br"),c("br"),n._v("Trương Huyền gật đầu."),c("br"),c("br"),c("span",[c("br"),n._v(" http://tr"),c("br"),n._v("uＹencuatui.net/ ")]),n._v(" Độc sư không giống danh sư, chú thích thân phận, coi trọng quy củ, một khi thật cho người khác biết, bản thân nắm giữ lợi hại như thế công pháp, đánh lén, hạ độc đều là nhẹ, làm không cẩn thận, còn có thể quang minh chính đại ra tay cướp đoạt."),c("br"),c("br"),n._v("Bất quá, mục đích của hắn chỉ là vì nhìn nhiều thư tịch, cứu chữa Ngụy Như Yên, Độc điện người, thật muốn thức thời không tìm phiền toái lời nói, cố gắng ở chung, không tính là gì."),c("br"),c("br"),n._v("Thật muốn không biết tốt xấu, cùng lắm thì toàn bộ hạ độc chết, cũng coi như vì dân trừ hại."),c("br"),c("br"),n._v("“Còn có, ngươi bách độc bất xâm thể chất, cũng phải ngụy trang một chút, không nên tùy tiện mở ra cho những người khác xem!”"),c("br"),c("br"),n._v("Chần chờ một chút, Hứa trưởng lão nói tiếp: “Nếu không, khẳng định sẽ dẫn tới người khác ngấp nghé!”"),c("br"),c("br"),n._v("“Ừm!” Trương Huyền đáp một tiếng."),c("br"),c("br"),n._v("Cũng đúng, hắn không phải Tiên Thiên độc thể, nhưng bách độc bất xâm, đổi lại ai cũng sẽ hoài nghi, xem ra chính mình đi Độc điện, phải khiêm tốn một chút."),c("br"),c("br"),n._v("Bất quá, làm như vậy, ngược lại cũng phù hợp tính cách của hắn, hắn bản thân liền là cái khiêm tốn mà không thích khoe khoang người."),c("br"),c("br"),n._v("“Ta đối khảo hạch độc sư, không hiểu rõ lắm, không biết làm sao kiểm tra? Còn có, chỉ muốn khảo hạch thông qua, liền có thể tùy ý quan sát Độc điện tất cả thư tịch ư?”"),c("br"),c("br"),n._v("Lại trò chuyện một hồi, Trương Huyền hỏi."),c("br"),c("br"),n._v("Lần này đi qua, mục đích lớn nhất là tìm thư xem, chỉ cần có thể cho đọc sách, cái khác đều khỏe nói."),c("br"),n._v("“Khảo hạch độc sư, tương đối phức tạp, cũng không phải là đơn giản có thể chế biến ra đối ứng đẳng cấp độc dược, mà là... Hạ độc!”"),c("br"),c("br"),n._v("Hứa trưởng lão nói."),c("br"),c("br"),n._v("“Hạ độc?” Trương Huyền nghi ngờ."),c("br"),c("br"),n._v("“Ừm, tựa như ngươi, khảo hạch lục tinh độc sư, chỉ riêng có thể chế biến ra cấp sáu độc dược không tính là gì, chủ yếu nhất là, có thể cho cùng cấp bậc, thậm chí cao một cấp bậc người hạ độc, mà không bị phát giác! Cái này mới tính được là bên trên chân chính độc sư.”"),c("br"),c("br"),n._v("Hứa trưởng lão giải thích."),c("br"),c("br"),n._v("“Ngươi ý tứ, chẳng lẽ còn có hạ độc so đấu?”"),c("br"),c("br"),n._v("“Không sai, nếu như khảo hạch lục tinh độc sư, liền muốn khiêu chiến lục tinh độc sư, hai bên thi triển giương bản lĩnh hạ độc, nếu như ngươi độc liền đối phương một chút cũng không có làm bị thương, không có ý tứ, không cách nào thông qua! Nếu như đem đối phương làm bị thương, mới coi như khảo hạch thành công...” Hứa trưởng lão nói."),c("br"),c("br"),n._v("“Độc... Thứ này không dễ dàng như vậy khống chế đi, ngộ nhỡ bị độc chết... Làm sao bây giờ?”"),c("br"),c("br"),n._v("Trương Huyền kỳ quái."),c("br"),c("br"),n._v("Độc thứ này, thuộc về không có khống chế, lượng dùng lớn, rất dễ dàng hạ độc chết, dùng đến ít, khả năng chẳng có chuyện gì, tỷ thí cái này... Có phải hay không quá nguy hiểm?"),c("br"),c("br"),n._v("“Bị độc chết, chỉ có thể nói bản thân học nghệ không tinh, không trách được người khác!”"),c("br"),c("br"),n._v("Lắc đầu, Hứa trưởng lão than thở một tiếng: “Độc sư khảo hạch, chính là như thế tàn khốc!”"),c("br"),c("br"),n._v("Không nghĩ tới độc sư khảo hạch một lần, liền trải qua một lần sống chết, Trương Huyền bất đắc dĩ lắc đầu, tiếp tục xem tới: “Không biết Hứa trưởng lão có thể hay không giới thiệu cho ta một chút, chúng ta Hồng Viễn đế quốc Độc điện tình huống!”"),c("br"),c("br"),n._v("Đối với cái này Độc điện, hắn hoàn toàn không biết gì cả, nếu như có thể trước thời hạn biết một chút tin tức, cũng có thể có chút ứng đối, không đến mức luống cuống tay chân."),c("br"),c("br"),n._v("“Cái này đơn giản!”"),c("br"),c("br"),n._v("Hứa trưởng lão cười cười: “Hồng Viễn Độc điện phân bộ, nhưng thật ra là cái cấp bảy phân bộ, không chỉ nắm giữ lục tinh độc sư, điện chủ càng là nắm giữ thất tinh thực lực, nói cách khác, nếu như ngươi có thể thắng được qua điện chủ, đem hắn độc trở mình, liền có thể trực tiếp khảo hạch thất tinh độc sư!”"),c("br"),c("br"),n._v("“Loại trừ điện chủ bên ngoài, toàn bộ Độc điện còn có ba vị thất tinh cấp bậc phó điện chủ, còn lại thì là hai mươi hai vị lục tinh đỉnh phong trưởng lão, ta cùng Tiết trưởng lão liền ở trong đó...”"),c("br"),c("br"),n._v("“Còn lục tinh trở xuống độc sư, chừng mấy vạn nhiều.”"),c("br"),c("br"),n._v("“Mấy vạn?” Trương Huyền ngẩn ngơ."),c("br"),c("br"),n._v("Vốn cho rằng loại này dùng độc gia hỏa, số lượng không sẽ rất lớn, không nghĩ tới thế mà có thể so với một cái danh sư học viện."),c("br"),c("br"),n._v("May mắn bọn họ đều ở tại một chỗ không ra, nếu không, đừng nói Tĩnh Viễn thành, toàn bộ Hồng Viễn đế quốc đều sẽ lộn xộn."),c("br"),c("br"),n._v("“Loại trừ độc sư bên ngoài, còn lại chính là tam đại điện, một là tàng thư điện. Bên trong nắm giữ Độc điện trên vạn năm tới tất cả bí tịch, số lượng kinh người. Muốn trở thành độc sư, không ngừng tiến bộ, chỉ có từ đó không ngừng học tập.”"),c("br"),c("br"),n._v("“Hai là thử độc điện. Phối chế độc dược cần vô số dược liệu, nếu như toàn bộ sử dụng thật, cho dù có cái Dược sơn, cũng không đủ tiêu xài, nơi này có trận pháp đặc biệt, có thể chuẩn xác mô phỏng mỗi một gốc dược liệu đặc tính, ở đây thử độc, có thể giảm bớt thuốc hao tổn!”"),c("br"),c("br"),n._v("“Cái cuối cùng, thì là tỷ thí điện! Độc sư cùng tu luyện giả đồng dạng, đồng dạng sẽ xuất hiện mâu thuẫn, muốn phải giải quyết, liền muốn tới đây, một khi tiến hành sinh tử đấu, hoàn toàn có thể dùng dùng độc dược tiến hành quyết đấu, mặc dù mệnh danh tỷ thí, thật ra thì... Cũng là độc sư chết nhiều nhất chỗ!”"),c("br"),c("br"),n._v("Hứa trưởng lão lắc đầu."),c("br"),c("br"),n._v("“Tam đại điện bên ngoài, thì là hai cái cấm địa, thứ nhất, điện chủ lăng. Là chứa đựng lịch đại điện chủ di thể chỗ, chỉ có điện chủ mới có thể đi vào, những người khác đi vào chính là phá hư quy củ, sẽ bị tươi sống độc chết. Đệ nhị, dược điền. Nơi này trồng trọt cần thiết dùng tất cả dược liệu, muốn sử dụng, cần trước thời hạn xin, không có thể tùy ý đi vào, nếu không, đồng dạng sẽ bị đánh chết!”"),c("br"),c("br"),n._v("“Cái khác cũng không có cái gì, lấy thực lực của ngươi, cùng bách độc bất xâm năng lực, đổ cũng không cần lo lắng...”"),c("br"),c("br"),n._v("“Ừm!”"),c("br"),c("br"),n._v("Trương Huyền nhẹ gật đầu."),c("br"),c("br"),n._v("Thất tinh độc sư, tam đại điện, hai đại cấm địa..."),c("br"),c("br"),n._v("Hắn liền đọc sách mà thôi, loại trừ tàng thư điện, cái khác có lẽ cũng sẽ không đi."),c("br"),c("br"),n._v("“Được rồi, Độc điện thật ra thì cũng không như trong tưởng tượng đáng sợ, chỉ cần là độc sư, mọi người vẫn là rất hữu hảo... Huống chi ngươi đối ta còn có ân cứu mạng!”"),c("br"),c("br"),n._v("Hứa trưởng lão cười cười."),c("br"),c("br"),n._v("Độc điện, độc sư ở trong mắt những người khác vô cùng kinh khủng, trên thực tế mọi người ở chung cũng đều là rất vui vẻ."),c("br"),c("br"),n._v("Mặc dù giữa bằng hữu, cũng sẽ thỉnh thoảng hạ cái độc chơi đùa, bình thường đều sẽ sớm chuẩn bị giải dược, không ảnh hưởng toàn cục."),c("br"),c("br"),n._v("Lại hỏi thăm một chút liên quan tới Độc điện sự tình, Hứa trưởng lão xác nhận hắn độc sư thân phận, cũng không có gì có thể giấu diếm, tất cả đều một năm một mười trả lời."),c("br"),c("br"),n._v("Cả buổi về sau, Trương Huyền đối Hồng Viễn đế quốc cái này Độc điện, đã hiểu rõ rất nhiều."),c("br"),c("br"),n._v("Nói là Hồng Viễn đế quốc phạm vi, thật ra thì nơi này được cho Thanh Nguyên đế quốc trong vòng lớn nhất, lợi hại nhất Độc điện, nguyên nhân chính là như vậy, mới có thất tinh cấp bậc độc sư tọa trấn."),c("br"),c("br"),n._v("Toàn bộ Độc điện, không chỉ có độc sư, độc dược, còn có rất nhiều độc trùng, độc thảo, thậm chí nước uống bên trong, đều vô cùng có khả năng có độc... Đổi lại người khác, Hứa trưởng lão khẳng định sẽ thêm thêm nhắc nhở, nhưng mà trước mắt vị này bách độc bất xâm, ăn cấp sáu đỉnh phong độc dược, đều cùng ăn điểm tâm đồng dạng, nhắc nhở không nhắc nhở cũng không sao cả."),c("br"),c("br"),n._v("Dù sao lại độc không chết."),c("br"),c("br"),n._v("“Được rồi, Độc điện đến...”"),c("br"),c("br"),n._v("Bay ròng rã hai ngày, đột nhiên Hứa trưởng lão đứng dậy, chỉ về phía trước, Trương Huyền lập tức nhìn lại chỉ thấy mênh mông hải dương, một vòng mặt trời chiếu rọi, sáng rõ con mắt có chút choáng váng."),c("br"),c("br"),n._v("Lập tức phi hành Thánh thú xoay một cái, trước mắt rộng rãi sáng sủa, một cái cực lớn hòn đảo xuất hiện ở trước mắt."),c("br"),c("br"),n._v("Hòn đảo này thế mà bị một cái ẩn nấp đại trận bao phủ, nếu như không phải có người dẫn đầu, căn bản không có khả năng tìm tới."),c("br"),c("br"),c("br"),c("br")]),n._v(" "),c("hr"),n._v(" "),c("p",[c("a",{attrs:{href:"./chap-1002"}},[n._v("Chương trước")]),n._v("  |  "),c("a",{attrs:{href:"./chap-1004"}},[n._v("Chương sau")])])])}),[],!1,null,null,null);h.default=i.exports}}]);