(window.webpackJsonp=window.webpackJsonp||[]).push([[200],{554:function(n,h,t){"use strict";t.r(h);var c=t(44),i=Object(c.a)({},(function(){var n=this,h=n.$createElement,t=n._self._c||h;return t("ContentSlotsDistributor",{attrs:{"slot-key":n.$parent.slotKey}},[t("h1",{attrs:{id:"chuong-1157-lac-nhuoc-hi-tin-tuc"}},[t("a",{staticClass:"header-anchor",attrs:{href:"#chuong-1157-lac-nhuoc-hi-tin-tuc"}},[n._v("#")]),n._v(" Chương 1157: Lạc Nhược Hi tin tức")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("br"),t("br"),n._v("Nữ nhân xinh đẹp gặp qua không ít, hơn nữa vừa mới còn có hai cái thổ lộ... Chỉ là, chưa thấy qua như thế cối xay khổng lồ... Quả thực có thể làm xe lu dùng."),t("br"),t("br"),n._v("“Được rồi!”"),t("br"),t("br"),n._v("Trương Huyền không thèm để ý, cũng không có gì có thể đưa tức giận, tìm người hỏi thăm một chút Ngô sư vị trí, nhấc chân đi tới."),t("br"),t("br"),n._v("Đi không xa, con mắt đột nhiên sáng lên, vội vàng hướng về phía trước."),t("br"),t("br"),n._v("“Mộc sư!”"),t("br"),t("br"),n._v("Một bóng người xuất hiện ở trước mắt, không phải người khác, chính là Mộc Viên Mộc sư!"),t("br"),t("br"),n._v("Rời khỏi Khưu Ngô cung về sau, hắn liền hồi phong hào đế quốc, đáp ứng Ngô sư tới Danh Sư đường, liền định thuận tiện nhìn một chút vị này, không nghĩ tới vừa vặn đụng phải."),t("br"),t("br"),n._v("“Sư thúc...”"),t("br"),t("br"),n._v("Thấy là hắn, Mộc sư ôm quyền."),t("br"),t("br"),n._v("“Ừm!” Nhẹ gật đầu, Trương Huyền nhìn qua: “Ngươi đột phá đến Thánh vực tứ trọng?”"),t("br"),t("br"),n._v("Mộc sư trước đó chỉ là Thánh vực tam trọng thực lực, trong cơ thể có kim thuộc tính lực lượng áp chế, một mực không cách nào đột phá, chỉ có thể duy trì tại thất tinh trung phẩm trái phải, không nghĩ tới hơn một tháng không thấy, vậy mà đột phá ràng buộc, đạt đến Nguyên Thần cảnh."),t("br"),t("br"),n._v("“Đúng vậy a, nhờ có Dương sư chỉ điểm, mới có ngày hôm nay...” Mộc sư lộ ra nồng đậm vẻ cảm kích."),t("br"),t("br"),n._v("Lúc trước tại Hồng Viễn thành, gặp được Dương sư, nhận được chỉ điểm, lúc này mới có ngày hôm nay, nếu không, cả một đời khả năng đều bị vây ở tam trọng, không cách nào đi ra."),t("br"),t("br"),n._v("“Không biết... Dương sư có thể tới Đế đô? Ta cũng tốt thăm hỏi một chút, ở trước mặt cảm kích!”"),t("br"),t("br"),n._v("Trò chuyện hai câu, Mộc sư hỏi."),t("br"),t("br"),n._v("Vị này ân nhân, để hắn đi ra bóng ma tâm lý, càng hóa giải trong cơ thể kim thuộc tính lực lượng, có chút đột phá, vẫn muốn tìm cơ hội nói một tiếng cảm ơn, chỉ bất quá, đối phương thần long thấy đầu mà không thấy đuôi, từ cái này từ biệt, hầu như không chút gặp qua."),t("br"),t("br"),n._v("“Lão sư hành tung phiêu hốt, đều là hắn tìm ta, liền xem như ta, cũng không liên lạc được...”"),t("br"),t("br"),n._v("Trương Huyền lắc đầu."),t("br"),t("br"),n._v("Dương Huyền cái thân phận này, chỉ là một cái để cho người ta kiêng kỵ bối cảnh, nếu không, hắn vô duyên vô cớ tiến bộ nhanh như vậy, chắc là phải bị người ngấp nghé, thậm chí làm không cẩn thận, còn có thể bị bắt lại nghiên cứu... Nhưng có vị này tọa trấn, liền không đồng dạng, bất kể người nào muốn động thủ, đều muốn cân nhắc một chút, đắc tội một vị có khả năng vượt qua bát tinh danh sư, có đáng giá hay không."),t("br"),t("br"),n._v("Dù sao, loại cấp bậc này danh sư, không chỉ thực lực mạnh, càng quan trọng hơn là, sau lưng còn có đệ nhất chức nghiệp làm chỗ dựa."),t("br"),t("br"),n._v("Dám đắc tội, liền có khả năng đối mặt bị tiêu diệt khả năng."),t("br"),t("br"),n._v("Vu hồn sư đủ cường đại, đáng tiếc, tại Danh Sư đường trước mặt, vẫn như cũ tuỳ tiện nghiền nát."),t("br"),t("br"),n._v("Nếu là bối cảnh, tự nhiên không có khả năng bình thường lấy ra gặp người, nếu không, loại cấp bậc này danh sư cũng liền không đáng tiền, bị người phát hiện sơ hở."),t("br"),t("br"),n._v("“Ta còn muốn có thể tại trước khi đi, thấy Dương sư một mặt, tự mình nói lời cảm tạ, nhìn tới... Không có cơ hội!”"),t("br"),t("br"),n._v("Mộc sư cười khổ lắc đầu."),t("br"),t("br"),n._v("“Trước khi đi? Mộc sư muốn đi đâu?” Trương Huyền khó hiểu."),t("br"),t("br"),n._v("Hắn liền là Thanh Nguyên phong hào đế quốc danh sư, muốn đi đâu?"),t("br"),t("br"),n._v("“Thực lực của ta khôi phục, trong cơ thể kim thuộc tính loại bỏ, nhớ tới Dương sư lúc trước nói với ta lời nói, cho nên, quyết định... Trở lại Mộc gia, mặc kệ đối phương xử trí như thế nào, cũng thản nhiên tiếp nhận!” Mộc sư ánh mắt tràn đầy kiên định."),t("br"),t("br"),n._v("Trương Huyền giật mình."),t("br"),t("br"),n._v("Vị này Mộc Viên, là Thánh Nhân quý tộc Mộc gia người, bởi vì ưa thích một nữ tử, rò rỉ công pháp, bị trục xuất gia tộc, lưu lạc đến nơi này."),t("br"),t("br"),n._v("“Đúng vậy a! Lá rụng về cội, ta cũng muốn trở về, không thể một mực phiêu bạt! Dù là không được tha thứ, cuối cùng chết ở nơi đó, cũng so một mực lưu lạc ở bên ngoài muốn tốt!” Mộc sư gật đầu."),t("br"),t("br"),n._v("Trương Huyền trầm mặc."),t("br"),t("br"),n._v("Hắn chưa từng có gia tộc, cũng không có trải qua gia tộc, cũng không biết loại cảm tình này."),t("br"),t("br"),n._v("Bất quá, có thể theo trước mắt vị này trong mắt, nhìn ra quyết nhiên hương vị, dường như cho dù Mộc gia đem hắn xử tử, cũng cam tâm tình nguyện."),t("br"),t("br"),n._v("“Ta không muốn một mực làm... Đào binh!” Mộc sư cười khổ."),t("br"),t("br"),n._v("“Đào binh?”"),t("br"),t("br"),n._v("“Đúng vậy a! Vi phạm gia tộc quy định, tự tiện hành động, liền là đào binh! Mặc dù lúc ấy không cảm thấy, nhưng từ khi rời khỏi gia tộc về sau, không có một ngày không hối hận...” Mộc sư lắc đầu."),t("br"),t("br"),n._v("Nhìn đối phương biểu lộ, Trương Huyền không biết làm sao mở miệng khuyên can."),t("br"),t("br"),n._v("“Trương sư, ta biết ngươi ưa thích Lạc sư, nhưng... Còn là trước thời hạn khuyên ngươi vài câu!” Mộc sư nhìn qua."),t("br"),t("br"),n._v("Trước mắt vị này Trương sư, cùng Lạc sư quan hệ, hắn nhìn ở trong mắt, biết đến rõ rõ ràng ràng."),t("br"),t("br"),n._v("“Mời nói!” Trương Huyền thần thái cung kính."),t("br"),t("br"),n._v("“Lạc sư chuẩn xác thân phận, ta cũng không rõ ràng, có điều, có lẽ cùng Thánh Nhân Lạc gia có cực lớn liên quan... Nếu thật là vị tiểu công chúa kia, trừ phi để Dương sư tự mình ra tay, nếu không, muốn lấy nàng, khẳng định không dễ dàng như vậy!”"),t("br"),t("br"),n._v("Mộc sư nói."),t("br"),t("br"),n._v("“Ngươi cũng không rõ ràng thân phận của nàng?” Trương Huyền sững sờ."),t("br"),t("br"),n._v("Lạc sư, là trước mắt vị này đưa đến Hồng Viễn học viện, vốn cho rằng, đối nàng biết rất nhiều, không nghĩ tới, thế mà cũng nói như vậy."),t("br"),t("br"),n._v("“Đúng vậy a, ta đã rất nhiều năm không có rời khỏi Thanh Nguyên thành, càng rất nhiều năm không có về Thánh Nhân quý tộc, không ít chuyện đều không rõ ràng. Bất quá, Lạc sư trẻ tuổi như vậy, thực lực liền vượt xa tại ta, hẳn là Lạc gia vị kia tiểu thiên tài! Không chỉ khống chế Lạc gia trọng yếu nhất bảo vật, còn có tinh thuần nhất huyết mạch... Loại người này, là không thể nào tùy tiện gả cho ngoại nhân!”"),t("br"),t("br"),n._v("Mộc sư lắc đầu."),t("br"),t("br"),n._v("“Ta biết!” Trương Huyền than thở."),t("br"),t("br"),n._v("Hắn nghe Hàn hội trưởng cùng Ngô sư, chuyên môn đã thông báo."),t("br"),t("br"),n._v("Biết muốn cưới vị này Lạc sư độ khó."),t("br"),t("br"),n._v("Bất quá, hắn không quan tâm."),t("br"),n._v("Thân là trời nhận danh sư, cùng trời nhận Thánh giả, có cái này tự tin và quyết đoán."),t("br"),t("br"),n._v("Quản hắn là ai, dám ngăn trở... Nghiền ép chính là!"),t("br"),t("br"),n._v("“Biết liền tốt, ta khuyên ngươi lời nói, chỉ có một câu: Tuyệt đối đừng để nàng... Hối hận!” Mộc sư nói."),t("br"),t("br"),n._v("“Hối hận?” Trương Huyền trong lòng run lên."),t("br"),t("br"),n._v("Trước đó, hắn còn muốn lấy, nếu như Lạc gia thật không đồng ý, đợi thực lực mạnh, hoàn toàn có thể đem người cướp đi, dám phản kháng, trực tiếp nghiền ép... Nghe được đối phương hơi thở, cùng nhìn thấy kinh nghiệm của hắn, biết làm như vậy, mặc dù có thể để cho Lạc Nhược Hi cùng với chính mình, nhưng chỉ sợ cũng phải để phía sau hối hận cả đời."),t("br"),t("br"),n._v("Cũng đúng."),t("br"),t("br"),n._v("Nàng từ lúc vừa ra đời liền hưởng thụ toàn cả gia tộc chiếu cố cùng yêu thương, sớm đã đối với gia tộc có cảm tình sâu đậm, chỉ vì ưa thích bản thân, liền để hắn cùng gia tộc náo nói lời nói, chỉ sợ thật sẽ để cho phía sau hối hận một đời."),t("br"),t("br"),n._v("“Yên tâm đi, ta sẽ quang minh chính đại cưới nàng, không cho nàng làm khó...” Nắm đấm xiết chặt, Trương Huyền trong lòng hạ quyết định."),t("br"),t("br"),n._v("Muốn cho đối phương, không cùng gia tộc xảy ra vấn đề, chỉ có một cái phương pháp, cái kia chính là... Cố gắng để cái này Thánh Nhân Lạc gia thừa nhận bản thân!"),t("br"),t("br"),n._v("Chỉ có như vậy, mới không còn để Nhược Hi làm khó."),t("br"),t("br"),n._v("“Ngươi biết liền tốt...” Thấy trước mắt vị này có quyết định, Mộc sư nhẹ gật đầu, không nói thêm lời."),t("br"),t("br"),n._v("“Đúng rồi, Mộc sư, Nhược Hi nàng... Nói có chuyện xảy ra muốn rời khỏi, ngươi cũng đã biết nàng có khả năng đi cái nào? Cái này Thánh Nhân Lạc gia, đến cùng ở nơi nào?”"),t("br"),t("br"),n._v("Trương Huyền nhịn không được hỏi."),t("br"),t("br"),n._v("Lạc Nhược Hi đi rất vội vàng, cũng không nói đi đâu, càng không nói để đi nơi nào tìm, Mộc sư đã cũng là Thánh Nhân quý tộc xuất thân, hẳn phải biết một chút."),t("br"),t("br"),n._v("“Thánh Nhân Lạc gia tại thánh thành Khúc Phụ... Bất quá, ta cảm thấy nàng sẽ không có về gia tộc, mà là đi !”"),t("br"),t("br"),n._v("Chần chờ một chút, Mộc sư nói."),t("br"),t("br"),n._v("“Thánh tử điện?” Trương Huyền cau mày."),t("br"),t("br"),n._v("“Ừm, Thánh tử điện, là tập hợp thiên hạ có thiên phú nhất người trẻ tuổi, mà sáng lập, có thể đi vào trong đó, hầu như đều là Thánh Nhân quý tộc hậu nhân, cùng đại lục ở bên trên đứng đầu nhất tuyệt thế thiên tài! Ta nghe qua tin tức, Lạc gia vị tiểu công chúa kia, thật ra thì liền là Thánh tử điện học sinh, bởi vì... Chuyện gì, chọc không vui, mới trốn thoát, không biết tung tích!”"),t("br"),t("br"),n._v("Suy nghĩ một chút, Mộc sư nói."),t("br"),t("br"),n._v("“Thánh tử điện học sinh?” Trương Huyền khiếp sợ."),t("br"),t("br"),n._v("Mặc dù không biết Lạc Nhược Hi chân chính thực lực, nhưng nàng nhẹ nhõm liền đem đột phá đến Xuất Khiếu cảnh Thiên Diệp Vương giam cầm, đã vượt ra khỏi tưởng tượng ra."),t("br"),t("br"),n._v("Thực lực như thế, chỉ là học sinh..."),t("br"),t("br"),n._v("Nơi này có phần thật là đáng sợ đi!"),t("br"),t("br"),n._v("“Thánh tử điện, là bồi dưỡng Thánh tử địa phương, kỳ trước Danh Sư đường đường chủ, cùng các đại gia tộc gia chủ, hầu như đều xuất từ nơi này! Trương sư, nếu quả thật muốn cho Lạc gia thừa nhận ngươi, hơn nữa thuận lý thành chương cưới đi Lạc sư, tốt nhất có thể đi vào nơi này, hơn nữa theo trong mọi người trổ hết tài năng... Như vậy, cho dù không có gia tộc bối cảnh, cũng không ai dám coi thường!”"),t("br"),t("br"),n._v("Mộc sư nói tiếp."),t("br"),t("br"),n._v("Trương Huyền gật đầu."),t("br"),t("br"),n._v("Mặc dù người người đều biết, phía sau hắn có cái “Dương sư”, thân thế không đơn giản, nhưng mà... Chính hắn biết chuyện gì xảy ra."),t("br"),t("br"),n._v("“Dương sư” là giả, nếu quả thật muốn quang minh chính đại cưới Lạc Nhược Hi, khẳng định không lấy ra được."),t("br"),t("br"),n._v("Tại địa phương nhỏ trò đùa trẻ con, giả vờ giả vịt, người khác nhìn không ra, gặp được chân chính đứng đầu nhất danh sư, khẳng định một chút liền sẽ để lộ, làm không cẩn thận, chết cũng không biết chết như thế nào."),t("br"),t("br"),n._v("Nếu quả thật có cái này Thánh tử điện, hơn nữa có thể tiến vào lời nói, tuyệt đối là lựa chọn tốt nhất."),t("br"),t("br"),n._v("Đương nhiên, để hắn tâm động nhất chính là, có thể sớm đi tìm Lạc Nhược Hi..."),t("br"),t("br"),n._v("Mới vừa nói yêu thương người, đừng nói lâu như vậy không gặp, cho dù một ngày không thấy, đều sẽ suốt đêm nhớ."),t("br"),t("br"),n._v("“Không biết... Cái này cái gọi là Thánh tử điện, như thế nào mới có thể đi vào?”"),t("br"),t("br"),n._v("Trương Huyền hỏi."),t("br"),t("br"),n._v("“Thánh tử điện, hàng năm trung tuần tháng chín khai giảng, đều sẽ tuyển nhận một nhóm mới học viên, phong hào đế quốc Danh Sư đường, có một cái báo đưa danh ngạch, nếu như có thể được đến... Tự nhiên là có thể đi vào!” Mộc sư gật đầu."),t("br"),t("br"),n._v("“Trung tuần tháng chín... Còn không đến hai tháng?” Trương Huyền lông mày nhảy một cái."),t("br"),t("br"),n._v("Hắn tới này cái thế giới, đã sắp một năm."),t("br"),t("br"),n._v("Tới thời điểm, vừa mới khai giảng, nói cách khác... Khoảng cách khai giảng, đã không đủ gần hai tháng."),t("br"),t("br"),n._v("“Đúng vậy a, không đủ hai tháng, chuẩn xác mà nói, là một tháng linh mười bảy ngày!” Mộc sư lên tiếng."),t("br"),t("br"),n._v("“Cái này...” Trương Huyền cau mày: “Cái kia không biết... Muốn có được cái này danh ngạch, đối thực lực cùng danh sư cấp bậc, có hay không yêu cầu?”"),t("br"),t("br"),n._v("Nếu là toàn bộ đại lục, đứng đầu nhất thiên tài đất tập trung, tự nhiên không có khả năng tùy tiện người nào đều có thể đi vào, khẳng định có tiền đề cùng yêu cầu."),t("br"),t("br"),n._v("“Tự nhiên có!” Mộc sư nhẹ gật đầu: “Ba mươi tuổi phía trước, thấp nhất trở thành thất tinh trung phẩm cấp bậc danh sư, tu vi kém cỏi nhất cũng muốn đạt tới Thai Anh cảnh!” Mộc sư nói."),t("br"),t("br"),n._v("“Thất tinh trung phẩm cấp bậc...” Trương Huyền gật đầu."),t("br"),t("br"),n._v("Hắn bây giờ cách Thai Anh cảnh, chỉ kém một tia, chỉ cần tìm được Thiên Đạo công pháp và tinh nguyên linh thạch thượng phẩm, hẳn là rất dễ dàng liền có thể đột phá."),t("br"),t("br"),n._v("Vấn đề không lớn."),t("br"),t("br"),n._v("“Bất quá... Chúng ta Danh Sư đường danh ngạch, gần như định, là Cẩu đường chủ tự mình làm được... Ta sợ, Trương sư ngươi chưa hẳn có thể cầm tới!” Dừng lại một chút, Mộc sư cau mày."),t("br"),t("br"),n._v("“Gần như định? Ai?”"),t("br"),t("br"),n._v("Còn muốn lấy đem danh ngạch nắm bắt tới tay, không nghĩ tới đã định, Trương Huyền sầm mặt lại, đột nhiên một bóng người theo trong đầu xông ra, nhịn không được mở miệng."),t("br"),t("br"),n._v("“Ngươi chẳng lẽ nói chính là vị kia... Trương Cửu Tiêu?”"),t("br"),t("br"),n._v("Mộc sư gật đầu: “Không sai, đúng là hắn, đoán không sai, hẳn là Thánh Nhân Trương gia đời sau!”"),t("br"),t("br"),t("br"),t("br")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("a",{attrs:{href:"./chap-1165"}},[n._v("Chương trước")]),n._v("  |  "),t("a",{attrs:{href:"./chap-1167"}},[n._v("Chương sau")])])])}),[],!1,null,null,null);h.default=i.exports}}]);