(window.webpackJsonp=window.webpackJsonp||[]).push([[2001],{2356:function(n,h,t){"use strict";t.r(h);var g=t(44),i=Object(g.a)({},(function(){var n=this,h=n.$createElement,t=n._self._c||h;return t("ContentSlotsDistributor",{attrs:{"slot-key":n.$parent.slotKey}},[t("h1",{attrs:{id:"chuong-723-mi-truong-lao-muon-thu-đo"}},[t("a",{staticClass:"header-anchor",attrs:{href:"#chuong-723-mi-truong-lao-muon-thu-đo"}},[n._v("#")]),n._v(" Chương 723: Mi trưởng lão muốn thu đồ")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("br"),t("br"),n._v("Chương 723: Mi trưởng lão muốn thu đồ"),t("br"),t("br"),n._v("Trọng yếu nhất chính là..."),t("br"),t("br"),n._v("Nghe cái khác danh sư nói, vị này Trương sư, vì cứu người, sử dụng bí pháp, mạnh mẽ từ Kiều Thiên cảnh sơ kỳ, đi đến đỉnh phong!"),t("br"),t("br"),n._v("Đổi lại người khác, loại thực lực này khẳng định phách lối không được, ước gì khoe khoang, mà hắn, nhưng cố ý đè thấp tu vi, để cho người ta thoạt nhìn cùng Hợp Linh cảnh tương đương..."),t("br"),t("br"),n._v("Nếu không phải cái này Dư Thừa báo ra họ tên, nhóm người mình cũng chưa nhận ra được!"),t("br"),t("br"),n._v("Loại này lạnh nhạt tâm cảnh cùng thái độ, chính là vô số danh sư, không học được!"),t("br"),t("br"),n._v("Liền ngươi... Cũng muốn cùng hắn so?"),t("br"),t("br"),n._v("Lấy cái gì so?"),t("br"),t("br"),n._v("Bằng vào miệng ư?"),t("br"),t("br"),n._v("Hừ một tiếng, rất nhiều lão sinh, tất cả đều khinh bỉ nhìn vị này Dư Thừa một cái."),t("br"),t("br"),n._v("Đổi lại trước kia, nhận được hơn hai ngàn điểm, bọn họ khẳng định sẽ mười phần tôn trọng, cảm thấy tương lai không yếu, nhưng cùng Trương sư so, suýt chút nữa nhiều lắm."),t("br"),t("br"),n._v("Không riêng gì thực lực còn có phẩm hạnh."),t("br"),t("br"),n._v("Nhân gia Trương sư, có thể vì cái khác danh sư, cam tâm tặng, không cầu hồi báo, ngươi đây?"),t("br"),t("br"),n._v("Nhìn nhân gia thú sủng bắt đến linh thú, cố ý đánh chạy, sau đó lại như thế loay hoay điểm tích lũy, đủ loại vô sỉ cùng bỉ ổi..."),t("br"),t("br"),n._v("Đồng dạng là thiên tài, khác biệt làm sao lại lớn như vậy chứ!"),t("br"),t("br"),n._v("“...”"),t("br"),t("br"),n._v("Nhìn thấy rất nhiều lão sinh ánh mắt khinh bỉ, Dư Thừa thân thể nhoáng một cái, quay đầu nhìn về phía những người khác, phát hiện cái khác danh sư cũng đều lộ ra khinh thường chi ý."),t("br"),t("br"),n._v("Mắt tối sầm lại, lần nữa một ngụm máu tươi phun ra."),t("br"),t("br"),n._v("Ma đản... Chẳng phải đánh cược ư?"),t("br"),t("br"),n._v("Bị một đầu linh thú lột sạch sành sanh, tất cả bảo vật đều mất đi không nói, còn chịu đến như thế xem thường, thành vạn người chế giễu, châm chọc đối tượng... Chuyện này là sao!"),t("br"),t("br"),n._v("Dư Thừa cùng Phùng Vũ nhìn nhau, tất cả đều khóc."),t("br"),t("br"),n._v("..."),t("br"),t("br"),n._v("“Cái này... Bạch sư, không biết Mi trưởng lão tìm ta chuyện gì?”"),t("br"),t("br"),n._v("Lúc này Trương Huyền, vẫn như cũ một mặt choáng váng, hoàn toàn không làm rõ ràng được chuyện gì xảy ra."),t("br"),t("br"),n._v("Hắn lúc trước vì lo lắng đuổi Thiên Nghĩ Phong Mẫu, không để ý những cái kia linh thú, càng không biết, bị hắn cứu rất nhiều danh sư, đem linh thú tất cả điểm tích lũy đều đặt vào tên của hắn xuống."),t("br"),t("br"),n._v("Do đó, còn tưởng rằng hắn điểm tích lũy không sánh bằng vị kia Dư Thừa."),t("br"),t("br"),n._v("“Ngươi không biết?”"),t("br"),t("br"),n._v("Thấy hắn bộ dáng này cùng hỏi ra loại lời này, Bạch sư sửng sốt một chút, cũng đầy là bội phục."),t("br"),t("br"),n._v("Thấy không?"),t("br"),t("br"),n._v("Đây mới là cao nhân lòng dạ!"),t("br"),t("br"),n._v("Người khác làm việc tốt, không cầu hồi báo là tốt lắm rồi, hắn làm việc tốt, chẳng những không muốn hồi báo, còn quên mất không còn một mảnh, lòng dạ rộng rãi, khí lượng bao lớn, không hơn được nữa."),t("br"),t("br"),n._v("Đã thấy người bên trong, loại trừ lão viện trưởng, lại không người có thể đụng."),t("br"),t("br"),n._v("“Ta làm sao biết?”"),t("br"),t("br"),n._v("Trương Huyền lắc đầu."),t("br"),t("br"),n._v("Mi trưởng lão hắn liền gặp qua một lần, hơn nữa còn là cách xa không biết bao xa, làm sao biết tìm bản thân làm gì?"),t("br"),t("br"),n._v("“Yên tâm đi, là chuyện tốt, một lát nữa liền biết!”"),t("br"),t("br"),n._v("Cười khổ lắc đầu, Bạch sư cũng không giải thích, đi đầu dẫn đường."),t("br"),t("br"),n._v("Đi một hồi, một cái nhà tranh xuất hiện ở trước mắt."),t("br"),t("br"),n._v("Nói là nhà tranh, trên thực tế là lương đình, xem ra mới vừa dựng lên đến không lâu, hai cái ông lão ngồi ngay ngắn ở trong, dường như chính đang nói chuyện."),t("br"),t("br"),n._v("“Mạc đường chủ?”"),t("br"),t("br"),n._v("Nhìn thoáng qua, Trương Huyền nhận ra, một cái là Mi trưởng lão, mà một cái khác cũng là người quen biết cũ, chính là đem viên kia linh thạch thượng phẩm xem như phần thưởng ban thưởng cho mình Mạc đường chủ, Mạc Cao Viễn."),t("br"),t("br"),n._v("“Lão sư, Trương sư đến rồi!”"),t("br"),t("br"),n._v("Không biết tâm lý của hắn ba động, Bạch sư tiến về phía trước một bước."),t("br"),t("br"),n._v("“Ồ? Ngươi chính là Trương Huyền?”"),t("br"),t("br"),n._v("Mi trưởng lão đứng dậy, nhìn về phía Trương Huyền, càng xem càng cảm thấy hài lòng."),t("br"),t("br"),n._v("Khí tức nội liễm, mộc mạc tối tăm, xem xét liền có rất cao tu dưỡng."),t("br"),t("br"),n._v("“Trương Huyền gặp qua Mi trưởng lão, Mạc đường chủ!” Trương Huyền ôm quyền."),t("br"),t("br"),n._v("“Ồ? Trương sư, chúng ta hình như còn là lần đầu tiên gặp mặt đi!” Mạc Cao Viễn nhìn qua."),t("br"),t("br"),n._v("“Ây... Đúng a!”"),t("br"),t("br"),n._v("Trương Huyền sững sờ, lúc này mới nhớ tới, lần trước nhìn thấy vị này Mạc đường chủ chính là “Dương sư”, mà hắn cũng không cùng đối phương từng có gặp nhau, cẩn thận nói đến, còn là lần đầu tiên gặp mặt."),t("br"),t("br"),n._v("“Đường chủ uy danh truyền xa, ta cũng nghe Hồng sư kỹ càng giới thiệu qua, lúc này thấy một lần, tự nhiên nhận ra!”"),t("br"),t("br"),n._v("Mặt không đỏ tim không đập, Trương Huyền nói."),t("br"),t("br"),n._v("“Không sai, chỉ bằng vào giới thiệu liền có thể phán đoán là ta, không hổ là thiên tài!” Mạc Cao Viễn gật đầu, cũng không hoài nghi."),t("br"),t("br"),n._v("Hắn cũng coi là thành danh thật lâu nhân vật, bản thân lại lấy ra linh thạch thượng phẩm làm ban thưởng, đối phương nhìn thấy lại không đoán ra được, cũng thẹn với thiên tài tên."),t("br"),t("br"),n._v("“Ồ? Mạc đường chủ, ngươi có ý tứ gì? Chẳng lẽ các ngươi đã sớm nhận biết?” Nghe được cái giọng nói này, Mi trưởng lão nghi ngờ nhìn qua."),t("br"),t("br"),n._v("Vị này không phải năm nay tới tân sinh ư?"),t("br"),t("br"),n._v("Làm sao Mạc đường chủ hình như từng có giao tình đồng dạng?"),t("br"),t("br"),n._v("“Cũng không tính được quen biết đã lâu, nhưng mà nghe qua Trương sư tên mà thôi! Ngươi cũng thấy đấy, trên thực tế chúng ta mới lần thứ nhất gặp mặt.” Mạc Cao Viễn nói."),t("br"),t("br"),n._v("Dương sư sự tình, không được đến đối phương cho phép, hắn không dám tự mình truyền ra ngoài, chỉ có thể mập mờ suy đoán."),t("br"),n._v(" “Nghe qua tên?” Mi trưởng lão càng thêm kỳ quái."),t("br"),t("br"),n._v("Trước mắt vị này là ai? Lục tinh đỉnh phong danh sư, khống chế một phương Danh Sư đường đường chủ, tại Huyễn Vũ đế quốc, đều được cho tiếng tăm lừng lẫy nhất đẳng nhân vật, đối diện vị này Trương sư, liền xem như tuyệt đỉnh thiên tài, cũng chỉ là Huyễn Vũ đế quốc một tiểu nhân vật mà thôi, làm sao còn có gặp nhau, hơn nữa còn nghe qua tên?"),t("br"),t("br"),n._v("“Mạc đường chủ cùng gia sư từng có sự giao hảo, do đó biết tên của ta!” Trương Huyền thấy hắn không tốt giải thích, mở miệng nói."),t("br"),t("br"),n._v("“Từng có sự giao hảo?” Mi trưởng lão sửng sốt, lập tức nhìn qua: “Không biết quý sư là vị nào, ta không biết phải chăng là quen biết?”"),t("br"),t("br"),n._v("Biết vị này Trương Huyền, trong vòng một canh giờ, thuần phục hơn bốn trăm đầu linh thú, để hắn động thu đồ chi tâm."),t("br"),t("br"),n._v("Hắn thấy, vị này Trương Huyền đã từ Huyễn Vũ đế quốc loại địa phương nhỏ này mà đến, lão sư tất nhiên không cao minh lắm, thu hắn làm đồ, không tính là gì, giờ phút này thấy Mạc Cao Viễn biết hắn lão sư, hơn nữa nghe ngữ khí quan hệ không ít, nhịn không được hỏi lên."),t("br"),t("br"),n._v("Trước đó lão sư cấp bậc thấp, thu làm học sinh, không có gì, nhưng muốn là đồng cấp, cũng có chút không thật thích hợp."),t("br"),t("br"),n._v("Nhất là thân truyền, chú trọng nhất mặt mũi cùng môn phong, không điều tra rõ ràng, liền tùy tiện thu đồ, liền cùng lúc trước Tào Hùng tranh đoạt Lưu Dương đồng dạng, thuộc về đánh mặt hành vi, vô cùng có khả năng làm tan rã trong không vui."),t("br"),t("br"),n._v("“Gia sư Dương Huyền!” Trương Huyền liền vội vàng gật đầu."),t("br"),t("br"),n._v("“Dương Huyền?”"),t("br"),t("br"),n._v("Mi trưởng lão mày nhíu lại thành mụn nhọt, cẩn thận suy tư cả buổi, cũng chưa từng nghe qua cái tên này, nhịn không được nhìn về phía cách đó không xa Mạc Cao Viễn: “Vị này Dương sư là...”"),t("br"),t("br"),n._v("“Cái này...”"),t("br"),t("br"),n._v("Mạc Cao Viễn mặt mũi chần chờ, không biết nên trả lời như thế nào."),t("br"),t("br"),n._v("“Gia sư, nhàn vân dã hạc, không có danh tiếng gì, Mi trưởng lão không biết cũng rất bình thường!” Nhìn ra Mạc đường chủ khó xử, Trương Huyền mở miệng nói."),t("br"),t("br"),n._v("“Không có danh khí gì?” Mi trưởng lão điểm khả nghi tỏa ra."),t("br"),t("br"),n._v("Mạc Cao Viễn làm Danh Sư đường đường chủ, địa vị tôn sùng, nếu như chỉ là bình thường cùng cấp danh sư, có cái gì không thể nói?"),t("br"),t("br"),n._v("“Ta liền nói thật đi, Dương sư, đối ta có chỉ điểm chi ân...” Nhìn hắn không hỏi ra đến, dường như không cam tâm, Mạc Cao Viễn sợ mình vị lão hữu này chọc giận không nên dây vào giận tồn tại, nhịn không được nói."),t("br"),t("br"),n._v("“Chỉ điểm? Chẳng lẽ...”"),t("br"),t("br"),n._v("Con ngươi co rụt lại, Mi trưởng lão vội vàng quay đầu nhìn về phía thanh niên trước mắt."),t("br"),t("br"),n._v("Cùng cấp bậc danh sư, bình thường đều dùng chỉ ra chỗ sai, nghiên cứu thảo luận, giao lưu, lẫn nhau học tập các loại từ ngữ, chỉ có cấp bậc cao danh sư, mới dùng tới được chỉ điểm."),t("br"),t("br"),n._v("Mạc Cao Viễn cẩn thận như vậy, chẳng lẽ... Vị này Trương Huyền lão sư, là một vị vượt qua hắn danh sư?"),t("br"),t("br"),n._v("Hắn cùng Mạc đường chủ đều đã là lục tinh đỉnh phong, vượt qua... Há không cho thấy, đạt đến thất tinh, thậm chí cao hơn?"),t("br"),t("br"),n._v("“Đúng!” Biết hắn nghĩ cái gì, Mạc Cao Viễn liền vội vàng gật đầu."),t("br"),t("br"),n._v("“Cái này...” Sắc mặt trắng nhợt, Mi trưởng lão có chút xấu hổ."),t("br"),t("br"),n._v("Hắn nhưng mà lục tinh đỉnh phong, muốn thu một vị thất tinh danh sư học sinh làm đồ đệ, rõ ràng đi quá giới hạn."),t("br"),t("br"),n._v("“Không biết... Trương sư thuần thú phương pháp, có phải hay không Dương sư chỗ dạy?”"),t("br"),t("br"),n._v("Mặc dù xấu hổ, vẫn còn có chút không cam tâm."),t("br"),t("br"),n._v("Một cái học sinh, có thể có rất nhiều lão sư, tu vi, thuần thú, thư hoạ, y đạo... Nếu như thuần thú không biết vị này Dương sư truyền lại, hắn thu đối phương làm thuần thú học sinh, không tính là thất lễ."),t("br"),t("br"),n._v("Thật giống như Trương Huyền thu Lạc Thất Thất là luyện đan học sinh đồng dạng."),t("br"),t("br"),n._v("Người thành đạt vi sư, coi như vị này Dương Huyền là thất tinh danh sư, chỉ cần hắn không dạy thuần thú, không coi là xúc phạm."),t("br"),t("br"),n._v("“Chính là gia sư chỗ dạy!”"),t("br"),t("br"),n._v("Lúc này cũng nhìn ra đối phương ý tứ, Trương Huyền vội vàng giải thích."),t("br"),t("br"),n._v("Mặc dù nhưng cái này Mi trưởng lão thực lực mạnh, lại là thập đại danh sư một trong, nhưng hắn cũng không muốn bái sư."),t("br"),t("br"),n._v("Đầu tiên, “Dương sư” cái thân phận này liền không tốt lắm ăn nói."),t("br"),t("br"),n._v("“Dương sư” cho tới nay bị hắn biểu hiện ra mạnh mẽ vô địch, ít nhất đạt đến thất tinh, bát tinh cấp bậc, nếu như bỏ qua lợi hại như vậy danh sư, đi bái một cái lục tinh, há không cho thấy, liền Mi trưởng lão cũng không bằng?"),t("br"),t("br"),n._v("Thứ yếu, hắn liền Khổng sư thu đồ đều không có đáp ứng, thật muốn đáp ứng cái tên này, Khổng sư có thể hay không tại chỗ tức giận sống lại?"),t("br"),t("br"),n._v("Mặc dù toàn bộ đại lục đều biết Khổng sư đã tiên thăng, nhưng đi qua lần trước “Thu đồ”, hắn cảm thấy không đơn giản như vậy."),t("br"),t("br"),n._v("Lại nói, hắn là thiên nhận danh sư, ông trời đều thừa nhận tồn tại, coi như muốn bái sư, cái tên này chỉ sợ cũng không chịu nổi đi!"),t("br"),t("br"),n._v("“Cái kia... Luyện khí đâu? Ta không chỉ thuần thú có thể, luyện khí cũng coi như không sai!” Thấy thuần thú thật là Dương sư chỗ dạy, Mi trưởng lão tiếp tục xem tới."),t("br"),t("br"),n._v("Ngoại nhân đều biết hắn thuần thú mạnh mẽ, nhưng lại không biết hắn luyện khí cũng là nhất đẳng người có nghề."),t("br"),t("br"),n._v("“Luyện khí... Cũng là gia sư truyền thụ!” Trương Huyền tiếp tục nói."),t("br"),t("br"),n._v("“Cái này... Cái kia, cái kia y đạo đâu?” Mi trưởng lão không cam tâm."),t("br"),t("br"),n._v("“Khụ khụ, được rồi, Dương sư học cứu Thiên Nhân, không phải ngươi ta có thể phỏng đoán...”"),t("br"),t("br"),n._v("Thấy bạn cũ chưa từ bỏ ý định, từ thuần thú kéo tới luyện khí, y đạo, Mạc Cao Viễn nhịn không được mở miệng cắt ngang."),t("br"),t("br"),n._v("“Ai, ngươi cũng biết, ta nhiều năm như vậy một mực tịch thu chân truyền học sinh, cũng không phải là không muốn, mà là không tìm được chân chính lương chất mỹ ngọc...”"),t("br"),t("br"),n._v("Biết Mạc đường chủ là hảo ý, Mi trưởng lão cười khổ lắc đầu."),t("br"),t("br"),n._v("Nhiều năm như vậy, không phải chưa từng gặp qua thiên tài, nhưng đều không có đi đến tâm lý của hắn tiêu chuẩn."),t("br"),t("br"),n._v("Hắn thu đồ yêu cầu, không cần thiên phú như thế nào, đầu tiên muốn tâm tính tốt, làm danh sư, muốn có thiên hạ là công trách nhiệm."),t("br"),t("br"),n._v("Vị này Trương Huyền, vì cứu người cưỡng ép tăng thực lực lên, còn bất kể hồi báo, chỉ bằng vào hai điểm này, nhân phẩm tâm tính liền phù hợp yêu cầu."),t("br"),t("br"),n._v("Tốn hao to lớn đánh đổi kính xin Tử Dương thú khảo hạch rất nhiều tân sinh, chính là muốn tuyển một vị phù hợp bản thân, thật vất vả tìm tới, tự nhiên không nguyện ý từ bỏ."),t("br"),t("br"),n._v("“Ta biết tâm tư của ngươi, nhưng mà...”"),t("br"),t("br"),n._v("Mạc Cao Viễn chần chờ một chút: “Dương sư thực lực...”"),t("br"),t("br"),n._v("Chính xoắn xuýt, có muốn hay không hơi tiết lộ một chút, để bạn cũ dẹp ý niệm này, liền thấy một cái chịu trách nhiệm khảo hạch lão sinh, vội vã đi tới."),t("br"),t("br"),n._v("“Bẩm báo Mi trưởng lão, tân sinh điểm tích lũy chúng ta đã toàn bộ xét duyệt xong rồi!”"),t("br"),t("br"),n._v("“Xét duyệt xong?”"),t("br"),t("br"),n._v("Mi trưởng lão ánh mắt sáng lên: “Tổng cộng có bao nhiêu tân sinh thông qua?”"),t("br"),t("br"),n._v("“Bẩm báo trưởng lão, tổng cộng 6,487 người!”"),t("br"),t("br"),n._v("“Sáu, sáu... Mới sáu ngàn?” Mi trưởng lão ngẩn ngơ."),t("br"),t("br"),n._v("Convert by: ThấtDạ"),t("br"),t("br"),t("br"),t("br")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("a",{attrs:{href:"./chap-731"}},[n._v("Chương trước")]),n._v("  |  "),t("a",{attrs:{href:"./chap-733"}},[n._v("Chương sau")])])])}),[],!1,null,null,null);h.default=i.exports}}]);